INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(431, 'Introduction to Computer Science: Building a Search Engine', 'In this course you will learn key concepts in computer science and learn how to write your own computer programs in the context of building&hellip;', 'Class Summary 					 					In this course you will learn key concepts in computer science and learn how to write your own computer programs in the context of building a web crawler.   				 				 					 						What Should I Know? 					 					There is no prior programming knowledge needed for this course. Beginners welcome!   				 				 					 						What Will I Learn? 					 					At the end of this course, you will have learned key concepts in computer science and  enough Python programming to be able to write programs to solve problems on your own. This course will prepare you to move on to more intermediate-level courses in CS.', 'https://www.udacity.com/course/cs101', 'http://www.youtube.com/watch?v=Pm_WAWZNbdA', '0000-00-00', 0, 'https://lh5.ggpht.com/yhPNCRsKl23saHN_vqPZS5cAvFDLdXZ9OK3QPv9nBRZCXIWMYng0mHo0tm3p_uz-xzJyGlsec9h_pKkdIQ=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(432, 'Introduction to Physics: Landmarks in Physics', 'Study physics abroad in Europe &mdash; virtually! Learn the basics of physics on location in Italy, the Netherlands and the UK, by answering some&hellip;', 'Class Summary 					 					Study physics abroad in Europe -- virtually! Learn the basics of physics on location in Italy, the Netherlands and the UK, by answering some of the discipline''s major questions from over the last 2000 years.    				 				 					 						What Should I Know? 					 					This course is suitable for anyone; a basic understanding of algebra is suggested.   				 				 					 						What Will I Learn? 					 					This unique class gives you the chance to see the sites where physics history was made and learn some of the subject''s most captivating concepts.', 'https://www.udacity.com/course/ph100', 'http://www.youtube.com/watch?v=cuKmcq6pu9s', '0000-00-00', 0, 'https://lh6.ggpht.com/Z8iDehj4KHicCdptW7FnnzVSQCksoN6cTt58vUvcLeUVRf5qJIRcs6LsPuG5X7srxtl4jMajBAudSyw_pQ=s0', 'PHYSICS', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(433, 'Introduction to Statistics: Making Decisions Based on Data', 'Statistics is about extracting meaning from data. In this class, we will introduce techniques for visualizing relationships in data and&hellip;', 'Class Summary 					 					Statistics is about extracting meaning from data. In this class, we will introduce techniques for visualizing relationships in data and systematic techniques for understanding the relationships using mathematics.   				 				 					 						What Should I Know? 					 					This course does not require any previous knowledge of statistics. Basic familiarity with algebra such as knowing how to compute the mean, median and mode of a set of numbers will be helpful.    				 				 					 						What Will I Learn? 					 					This course will cover visualization, probability, regression and other topics that will help you learn the basic methods of understanding data with statistics.', 'https://www.udacity.com/course/st101', 'http://www.youtube.com/watch?v=bAkFZtE6XVQ', '0000-00-00', 0, 'https://lh4.ggpht.com/DeRzxnpEirE2B_f59aeWo1dTDDc0IBovKn0kJwvFwStyEjA2hy6Otsbh1hvdjLx2IZkqHjilQbzTrCNv1HE=s0', 'MATHEMATICS', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(434, 'Visualizing Algebra: Patterns and Problems', 'Throughout this Intermediate Algebra course, we will use algebra to quantify and describe the world around us. Have you ever wondered&hellip; San Jose State University', 'Class Summary 					 					Throughout this course, we will use algebra to quantify and describe the world around us. Have you ever wondered how many songs can fit onto your flash drive? By the end of the course, youâ€™ll have stronger skills for modeling problems, analyzing patterns, and using algebra to arrive at conclusions.   				 				 					 						What Should I Know? 					 					 Almost nothing! Students should know their multiplication tables (1-12) and be proficient with adding and subtracting integers (for example, -7 - 16). A pencil and paper will be a studentâ€™s best aid rather than a calculator.    \r\n \r\n   See our Udacity / SJSU Plus FAQ for more details on how this course works and how to get college credit.    				 				 					 						What Will I Learn? 					 					Understand and develop the language, models, and tools for math reasoning.    Reason with numbers and units to solve problems.    Identify, analyze, extend, and create patterns and functions.    Make connections about relations and functions through multiple representations: graphs, charts, tables, equations.    Apply mathematical reasoning and logic to solve equations and write equivalent forms.', 'https://www.udacity.com/course/ma006', 'http://www.youtube.com/watch?v=JDlu4CM9bDg', '0000-00-00', 0, 'https://lh6.ggpht.com/tnQsrjUouppKZxLidvKl9tziOsRszEBO2LIHRwP5wgiiBogMJS8pvdB8t_qDydEL3H81HX86IrOb4uEOdNc=s0', 'MATHEMATICS', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(435, 'College Algebra: Animals, Architecture, and Innovation', 'How on earth are animals, architecture, and business related? You''ve heard it before, but we''ll say it again: math is everywhere. In this class&hellip; San Jose State University', 'Class Summary 					 					How on earth are animals and business related? Youâ€™ve heard it before, but weâ€™ll say it again: math is everywhere. In this class, youâ€™ll gain an in-depth understanding of algebraic principles, many of which you may have seen before, and learn how to use them to solve problems that we encounter in everyday life.  The online version of College Algebra will cover all of the topics that you would see in more traditional class formats, but it will present the material in a way that we hope youâ€™ll find fresh and interesting. You will learn about functions, polynomials, graphing, complex numbers, exponential and logarithmic equations, and much more, all through exploring real-world scenarios.   				 				 					 						What Should I Know? 					 					Basic arithmetic - addition, subtraction, multiplication and division of positive and negative numbers. \r\n \r\n See our Udacity / SJSU Plus FAQ for more details on how this course works and how to get college credit.   				 				 					 						What Will I Learn? 					 					In this course, you will learn...     To translate word problems into mathematical expressions or equations To manipulate algebraic expressions and solve equations To graph different types of functions and to understand the relationship between a functionâ€™s graph and its equation To be able to model growth and decay', 'https://www.udacity.com/course/ma008', 'http://www.youtube.com/watch?v=NBijVTxYUlk', '0000-00-00', 0, 'https://lh4.ggpht.com/4c7MDphGVFfo8EvhNOzxAs2OMKHthAgXkmni03muDj2j5Ex6dUYWuu6GSX27G62kkEzFp1P6_-ot7GwNYUY=s0', 'MATHEMATICS', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(436, 'Introduction to Programming in Java NEW : Learning Java', 'In this class, you will learn basic skills and concepts of computer programming in an object-oriented approach using Java. You will learn&hellip; 							 San Jose State University', 'Class Summary 					 					In this class, you will learn basic skills and concepts of computer programming in an object-oriented approach using Java.    You will learn concepts like classes, methods and argument passing, loops, and general problem solving ability that will become building blocks to your programming skills.   				 				 					 						What Should I Know? 					 					No prior programming experience and knowledge is assumed or needed.   				 				 					 						What Will I Learn? 					 					Upon successful completion of this course, students should be able to:   Analyze and explain the behavior of programs involving the fundamental program constructs. Write short programs that use the fundamental program constructs including standard conditional and iterative control structures. Identify and correct syntax and logic errors in short programs. Write short programs that use arrays or array lists. Design and implement a class based on attributes and behaviors of objects. Construct objects using a class and activate methods on them. Use static and instance members of a class properly. Identify and describe the properties of a variable such as its associated value, scope and lifetime. Describe the parameter passing mechanisms in terms of formal parameters, actual parameters, non-object parameters and object parameters. Write a graphics program that draws simple shapes. Identify super- and subclasses in a class hierarchy. Recognize and trace overrridden and inherited methods in a class hierarchy. Write javadoc comments for classes and methods. To be able to use an integrated development environment and a debugger.', 'https://www.udacity.com/course/cs046', 'http://www.youtube.com/watch?v=Wsp5Rrenoq4', '0000-00-00', 0, 'https://www.udacity.com/media/img/temp/temp_intro_java.jpg', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(437, 'Introduction to Psychology NEW : The Science of Thought and Behavior', 'Introduction to Psychology is a journey through all of the major psychological concepts and principles. The knowledge gained from this course will&hellip; 							 San Jose State University', 'Class Summary 					 					Introduction to Psychology is a journey through all of the major psychological concepts and principles. The knowledge gained from this course will allow students to critically evaluate psychological research and have a more in-depth understanding of human thought and behavior.   				 				 					 						What Should I Know? 					 					There are no prerequisites, but we encourage curiosity about human nature.   				 				 					 						What Will I Learn? 					 					Students will be able to:   identify and analyze the social dimension of society as a context for human life, the processes of social change and social continuity, the role of human agency in those processes, and the forces that engender social cohesion and fragmentation. place contemporary developments in cultural, historical, environmental, and spatial contexts. identify the dynamics of ethnic, cultural, gender/sexual, age-based, class, regional, national, transnational, and global identities and the similarities, differences, linkages, and interactions between them. evaluate social science information, draw on different points of view, and formulate applications appropriate to contemporary social issues. recognize the interactions of social institutions, culture, and environment with the behavior of individuals.', 'https://www.udacity.com/course/ps001', 'http://www.youtube.com/watch?v=IhOHdV_2AFA', '0000-00-00', 0, 'https://www.udacity.com/media/img/temp/temp_intro_psych.jpg', 'PSYCHOLOGY', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(438, 'Statistics: The Science of Decisions', 'We live in a time of unprecedented access to information...data. Whether researching the best school, job, or relationship, the &hellip; San Jose State University', 'Class Summary 					 					 We live in a time of unprecedented access to information...data.  Whether researching the best school, job, or relationship, the Internet has thrown open the doors to vast pools of data.  Statistics are simply objective and systematic methods for describing and interpreting information so that you may make the most informed decisions about life. \r\n \r\n \r\n     				 				 					 						What Should I Know? 					 					It sounds strange to say, but math is not the focus of this class.  To do well, however, it is necessary to have a basic understanding of proportions (fractions, decimals, and percentages), negative numbers, basic algebra (solving equations), and exponents and square roots.   \r\n \r\n       See our Udacity / SJSU Plus FAQ for more details on how this course works and how to get college credit.   				 				 					 						What Will I Learn? 					 					   The applications of statistics to everyday life Methods for acquiring data through observation and experimentation To organize and describe quantitative and categorical forms of data Anticipating patterns using basic probability and sampling     Statistical inference through estimation and hypothesis testing Correlation and simple regression Ways of describing the strength of relationships between variables', 'https://www.udacity.com/course/st095', 'http://www.youtube.com/watch?v=ZF62KhgmNxs', '0000-00-00', 0, 'https://lh3.ggpht.com/C-0I_oyTp_TnIHrT8YU7dC_P7b1udOZ0-sZg_qXkxFSp9ozKhAh-H04y8iW0ByfIaxgTsJ9VQBhDziYO4cc=s0', 'MATHEMATICS', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(439, 'Algorithms: Crunching Social Networks', 'Ever played the Kevin Bacon game? This class will show you how it works by giving you an introduction to the design and analysis of algorithms,&hellip;', 'Class Summary 					 					Ever played the Kevin Bacon game? This class will show you how it works by giving you an introduction to the design and analysis of algorithms, enabling you to discover how individuals are connected.    				 				 					 						What Should I Know? 					 					This class assumes an understanding of programming at the level of CS101, including the ability to read and write short programs in Python; it also assumes a comfort level with mathematical notation at the level of high school Algebra II or the SATs.   				 				 					 						What Will I Learn? 					 					By the end of this class you will understand key concepts needed to devise new algorithms for graphs and other important data structures and to evaluate the efficiency of these algorithms.', 'https://www.udacity.com/course/cs215', 'http://www.youtube.com/watch?v=-jTyn7xuyvE', '0000-00-00', 0, 'https://lh5.ggpht.com/ubai6WgDHlCzU7PjRSBHKwOnrNeXt4sAlCgYKrJommqU7clHdTA7lDbObnLLzOxJFXIg-21rn7m9bfU_7594=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(440, 'Differential Equations in Action: Making Math Matter', 'In this course you will examine real world problems &mdash; rescue the Apollo 13 astronauts, stop the spread of epidemics, and fight forest fires&hellip;', 'Class Summary 					 					In this course you will examine real world problems -- rescue the Apollo 13 astronauts, stop the spread of epidemics, and fight forest fires -- involving differential equations and figure out how to solve them using numerical methods.    				 				 					 						What Should I Know? 					 					You''ll need a basic knowledge of programming for this course, around the level of CS 101 or equivalent. You''ll also need to understand trigonometry at the high school level, as well as basic vector algebra. This class will primarily involve solving equations numerically rather than analytically, but some exposure to calculus and physics at the level of PH 100 wouldn''t hurt.   				 				 					 						What Will I Learn? 					 					By the end of this course, you''ll develop an intuition for the use of differential equations in the applied sciences. You''ll also learn how to build mathematical models for systems of differential equations. Along the way, you''ll learn how to translate mathematical expressions into Python code, and solve some really cool problems!', 'https://www.udacity.com/course/cs222', 'http://www.youtube.com/watch?v=CWyJDfkP8T4', '0000-00-00', 0, 'https://lh3.ggpht.com/mF9CgSX0RRrMDoMXNIqnhIZd5nPUNZ-bX8YfB8e9kkMCYSWD_B3eSHbLFFyPyeOR28iqrFaqzpmjEE3aQKo=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(441, 'Web Development: How to Build a Blog', 'Starting from the basics of how the web works, this class will walk you through everything you need to know to build your own blog&hellip;', 'Class Summary 					 					Starting from the basics of how the web works, this class will walk you through everything you need to know to build your own blog application and scale it to support large numbers of users.    				 				 					 						What Should I Know? 					 					A moderate amount of programming and computer science experience is necessary for this course.   				 				 					 						What Will I Learn? 					 					In this project-based course your knowledge will be evaluated as you learn to build your own blog application! Learn everything Steve Huffman wished he would have known when he broke into the startup world. Read more about Huffman and the course here.', 'https://www.udacity.com/course/cs253', 'http://www.youtube.com/watch?v=CRYn30--PPk', '0000-00-00', 0, 'https://lh6.ggpht.com/So4yjQXrjvuSV8_b_wuQCOhsZ_cTzjaGGWWWIhWXzpP9Ovw93nBXF3NaTekUvpyTHjtM-_FKM_60GdWl-g=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(442, 'HTML5 Game Development NEW : Building High Performance Web Applications', 'This course will walk you through the major components of building GRITS, an HTML5 game. We''ll talk about how to take standard game&hellip;', 'Class Summary 					 					This course will walk you through the major components of building GRITS, an HTML5 game. We''ll talk about how to take standard game development techniques, and use them to create high performance HTML5 applications.   				 				 					 						What Should I Know? 					 					Knowledge of HTML, Javascript, and how the web works is necessary for this course. There is an optional lesson on the specific parts of Javascript and the DOM that you''ll need for this class as a refresher, but this is not an introductory course on Javascript.   				 				 					 						What Will I Learn? 					 					At the end of this course, you''ll understand how to develop an HTML5 game.  You will gain familiarity with HTML5 features such as 2D canvas and techniques for improving performance.', 'https://www.udacity.com/course/cs255', 'http://www.youtube.com/watch?v=F3JeV756B7w', '0000-00-00', 0, 'https://lh5.ggpht.com/JlHcrEXAixygvcN2J4vYcCOEByEnm6dwZnPbq0w3bkn2FdY1jcCKiw9pOgUHPKqvA1afqJNjc47g5hbM6g=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(443, 'Software Testing: How to Make Software Fail', 'When writing software, destruction can be just as valuable as creation. Learn how to catch bugs and break software as you discover&hellip;', 'Class Summary 					 					When writing software, destruction can be just as valuable as creation. Learn how to catch bugs and break software as you discover different testing methods that will help you build better software.   				 				 					 						What Should I Know? 					 					Programming experience is required.   				 				 					 						What Will I Learn? 					 					This course will teach you how to think like a software tester, how to find bugs in code earlier, and write better code.', 'https://www.udacity.com/course/cs258', 'http://www.youtube.com/watch?v=e06Vo9rba3k', '0000-00-00', 0, 'https://lh6.ggpht.com/iLxXBhU-im80Yztrzgmr39z9qvvfgKr-sKG8T2GeTpP5YngXakOH365L5Be9IKgjoC15qrHhOCEhcSMsexo=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(444, 'Software Debugging: Automating the Boring Tasks', 'In this class you will learn how to debug programs systematically, how to automate the debugging process and build several automated&hellip;', 'Class Summary 					 					In this class you will learn how to debug programs systematically, how to automate the debugging process and build several automated debugging tools in Python.   				 				 					 						What Should I Know? 					 					Basic knowledge of programming and Python at the level of Udacity CS101 or better is required. Basic understanding of Object-oriented programming is helpful.   				 				 					 						What Will I Learn? 					 					At the end of this course you will have a solid understanding about systematic debugging, will know how to automate debugging and will have built several functional debugging tools in Python.', 'https://www.udacity.com/course/cs259', 'http://www.youtube.com/watch?v=pklED4UfODg', '0000-00-00', 0, 'https://lh6.ggpht.com/qKxI8m0dbWZmtzlldyCOhtF-4d7MmACIXd1Io-E_kM5pwpywrkm7FZyW_Q_w74rP4MOVKjFzg_G7SOwMVaO7=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(445, 'Programming Languages: Building a Web Browser', 'This class will give you an introduction to the fundamentals of programming languages. Key concepts include how to specify&hellip;', 'Class Summary 					 					This class will give you an introduction to the fundamentals of programming languages. Key concepts include how to specify and process valid strings, sentences and program structures.    				 				 					 						What Should I Know? 					 					This course assumes previous programming experience, comparable to what is covered by the Udacity CS101 course.   				 				 					 						What Will I Learn? 					 					Take this class and learn how to build your own simple web browser complete with the ability to parse and understand HTML and JavaScript.', 'https://www.udacity.com/course/cs262', 'http://www.youtube.com/watch?v=cn83Tm1jNSo', '0000-00-00', 0, 'https://lh4.ggpht.com/kQ8a4uxiTD1oEQNLM6EsD6Iepgfb6swcii7fXVaezuU22RjxIMrX_No0F2rSBzC4tF2Hi50vNONGa2VL-q0=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(446, 'Introduction to Artificial Intelligence: AI-Class', 'The objective of this class is to teach you modern AI. You will learn about the basic techniques and tricks of the trade. We also aspire to excite you&hellip;', 'Class Summary 					 					The objective of this class is to teach you modern AI. You will learn about the basic techniques and tricks of the trade. We also aspire to excite you about the field of AI.   				 				 					 						What Should I Know? 					 					Some of the topics in Introduction to Artificial Intelligence will build on probability theory and linear algebra. You should have understanding of probability theory comparable to that at our ST101: Introduction to Statistics class   				 				 					 						What Will I Learn? 					 					This class introduces students to the basics of Artificial Intelligence, which includes machine learning, probabilistic reasoning, robotics, and natural language processing.', 'https://www.udacity.com/course/cs271', 'http://www.youtube.com/watch?v=VfnXzwduq6U', '0000-00-00', 0, 'https://lh6.ggpht.com/_7fhSDYTKVnuTntvjXW5h7k4kIJnd8ae_KbyzlYWrBUcY2JMnavA4tF4ee4lhZ2Q-2zEDzdAl-0eH3Fzerg=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(447, 'Interactive 3D Graphics NEW : Creating Virtual Worlds', 'This class will teach you about the basic principles of 3D computer graphics: meshes, transforms, cameras, materials, lighting, and animation.', 'Class Summary 					 					This class will teach you about the basic principles of 3D computer graphics: meshes, transforms, cameras, materials, lighting, and animation.   Course content is brought to you in partnership with Autodesk, a worldwide leader in 3D design, engineering, and entertainment software.  http://www.autodesk.com   				 				 					 						What Should I Know? 					 					Knowing how to program in some language is all youâ€™ll need to get started. Some knowledge of JavaScript is useful later on if you wish to make your projects more interactive and engaging. Familiarity with vectors and matrices will ease your way but is not critical.  To be able to see the interactive demos and do programming assignments you need a browser that supports WebGL technology. An up to date version of Chrome or Firefox will work (see our FAQ for more information). You can test if your browser supports this technology by visiting the WebGL project homepage.   Almost all programming will be done in JavaScript using three.js library/API.   				 				 					 						What Will I Learn? 					 					By the end of this class you will know enough to make an animated 3D demo program that runs in a web browser, while also understanding the basic principles of how realistic computer images are generated.  You will also have a portfolio of small interactive programs that run in any web browser that supports WebGL.', 'https://www.udacity.com/course/cs291', 'http://www.youtube.com/watch?v=Vl0hmKg1pVw', '0000-00-00', 0, 'https://lh6.ggpht.com/Eed_-OEi12uGGzHQbty1lIh3MQpzDLgCaM2W3wxsy1oc51ophYGk4Kv909f9ltHwcJXsH8IPJZD-dCshNJg=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(448, 'Intro to Theoretical Computer Science: Solving Hard Problems', 'This class teaches you about basic concepts in theoretical computer science&mdash;such as NP-completeness&mdash;and what they imply for&hellip;', 'Class Summary 					 					This class teaches you about basic concepts in theoretical computer science -- such as NP-completeness -- and what they imply for solving tough algorithmic problems.   				 				 					 						What Should I Know? 					 					You should have a basic understanding of algorithms (such as CS215) and programming (such as CS101). No prior knowledge about theoretical computer science required!   				 				 					 						What Will I Learn? 					 					At the end of this course, you will have a solid understanding of theoretical computer science. This will not only allow you to recognize some of the most challenging algorithmic problems out there, but also give you powerful tools to deal with them in practice.', 'https://www.udacity.com/course/cs313', 'http://www.youtube.com/watch?v=AZ9B1kTW5FA', '0000-00-00', 0, 'https://lh6.ggpht.com/yoELB7M_PvSkKyEVKckK6r66jLez3QPkn5LTA-GfdYfNHb4-OpPVvBkNTMl-fzdYSmdRnO5kcqTWBLY3LHQ=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(449, 'How to Build a Startup: The Lean LaunchPad', 'Learn the key tools and steps to build a successful startup (or at least reduce the risk of failure). An introduction to the basics of Steve&hellip;', 'Class Summary 					 					Learn the key tools and steps to build a successful startup (or at least reduce the risk of failure).  An introduction to the basics of Steve Blank''s famous Customer Development process, where entrepreneurs "get out of the building" to gather massive amounts of customer and marketplace feedback, and then use that feedback to continuously iterate and evolve their startup business models, improving the chances of success at every step.   				 				 					 						What Should I Know? 					 					Prior knowledge or experience are not required. Passion, tenacity, and a willingness to work hard are essential. Ideally, you''ll join the class with at least a rough idea of the business model of the startup you''ll work on step-by-step through the course of the class.   				 				 					 						What Will I Learn? 					 					You''ll learn the key steps of the Customer Development process: how to identify and engage the first customers for your product, and how to gather, evaluate and use their feedback to make your product, marketing and business model far stronger.', 'https://www.udacity.com/course/ep245', 'http://www.youtube.com/watch?v=VZvgj6B2JZs', '0000-00-00', 0, 'https://lh4.ggpht.com/hhkKJAsmCxGiQCjVhMh6v-syfufwXw0OKynBzC2Wa7iXVi5sgzdZVBoCNYSjB-E5ku2YQvNlqL0LaYUEj3oe=s0', 'BUSINESS', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(450, 'Design of Computer Programs: Programming Principles', 'Learn new concepts, patterns, and methods that will expand your programming abilities, helping move you from a novice to an&hellip;', 'Class Summary 					 					Learn new concepts, patterns, and methods that will expand your programming abilities, helping move you from a novice to an expert programmer.   				 				 					 						What Should I Know? 					 					This course assumes previous programming experience, comparable to what is covered by the Udacity CS101 course.   				 				 					 						What Will I Learn? 					 					Move along the path towards becoming an expert programmer! In this class you will practice going from a problem description to a solution, using a series of assignments.', 'https://www.udacity.com/course/cs212', 'http://www.youtube.com/watch?v=6LSVPBgEl6w', '0000-00-00', 0, 'https://lh4.ggpht.com/gXOFHD4aSeq-XUcuvvmmhDLXDVD5SuG1xWVmHK6yLsYEnlZieh-GBO9S1QGK1woj3clmwmuClyHc3XLzGLI=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(451, 'Intro to Parallel Programming NEW : Using CUDA to Harness the Power of GPUs', 'Learn the fundamentals of parallel computing with the GPU and the CUDA programming environment! In this class, you''ll learn about parallel&hellip;', 'Class Summary 					 					Learn the fundamentals of parallel computing with the GPU and the CUDA programming environment! In this class, you''ll learn about parallel programming by coding a series of image processing algorithms, such as you might find in Photoshop or Instagram. You''ll be able to program and run your assignments on high-end GPUs, even if you don''t own one yourself.    Why Itâ€™s Important to Think Parallel    Third Pillar of Science \r\n Learn how scientific discovery can be accelerated by combining theory and experimentation with computing to fight cancer, prevent heart attacks, and spur new advances in robotic surgery.   				 				 					 						What Should I Know? 					 					We expect students to have a solid experience with the C programming language and basic knowledge of data structures and algorithms.   				 				 					 						What Will I Learn? 					 					You''ll master the fundamentals of massively parallel computing by using CUDA C/C++ to program modern GPUs. You''ll learn the GPU programming model and architecture, key algorithms and parallel programming patterns, and optimization techniques. Your assignments will illustrate these concepts through image processing applications, but this is a parallel computing course and what you learn will translate to any application domain. Most of all we hope you''ll learn how to think in parallel.', 'https://www.udacity.com/course/cs344', 'http://www.youtube.com/watch?v=zb49vDrOxgA', '0000-00-00', 0, 'https://lh4.ggpht.com/mCW0QvL1IWdDeSjP3yuUgMxDS5MSZh1J4o3fioCXRD6yvK1n0-jIASgX6NxGCZjpqPkqjBcaaziR9yKeTH4=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(452, 'Functional Hardware Verification NEW : How to Verify Chips and Eliminate Bugs', 'When developing chips it is essential that they get verified thoroughly because it is very hard or impossible to fix them once they have&hellip;', 'Class Summary 					 					When developing chips it is essential that they get verified thoroughly because it is very hard or impossible to fix them once they have been manufactured.  In this class, you will learn how to program verification environments that verify chip functionality efficiently, as well as understand and leverage automation such as constrained random test generation and improve code reuse leveraging a standardized methodology.   				 				 					 						What Should I Know? 					 					Programming experience including object-oriented programming. Data & Control structure.  \r\n \r\n \r\n This course is developed by Cadence Design Systems, a global leader in electronic design automation. CadenceÂ® software,  hardware, IP, and services help customers around the world to overcome a range of technical and economic hurdles.      				 				 					 						What Will I Learn? 					 					This course will teach you how to think like a verification engineer. It will show the software development aspects you need to know to  ensure chips are working as expected. You will learn how to implement verification environments.', 'https://www.udacity.com/course/cs348', 'http://www.youtube.com/watch?v=PZHh2O6iqj8', '0000-00-00', 0, 'https://lh3.ggpht.com/4hvBCVFKELjqTmILZ09pbXzFU1VeTSvqpsj1Qt-9wev7EYb7hStUVHsocmGQ5WNKxbCKbrs0D2bS7casqKxE=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(453, 'Artificial Intelligence for Robotics: Programming a Robotic Car', 'Learn how to program all the major systems of a robotic car from the leader of Google and Stanford''s autonomous driving teams. This class will teach&hellip;', 'Class Summary 					 					Learn how to program all the major systems of a robotic car from the leader of Google and Stanford''s autonomous driving teams. This class will teach you basic methods in Artificial Intelligence, including: probabilistic inference, planning and search, localization, tracking and control, all with a focus on robotics. Extensive programming examples and assignments will apply these methods in the context of building self-driving cars.   				 				 					 						What Should I Know? 					 					You should either already know Python, or have enough experience with another language to be confident you can pick up what you need on your own.Â Fortunately, Python was built to be easy to learn, read, and use. If you already know another programming language, you''ll be coding in Python in less than an hour. Additionally, knowledge of probability and linear algebra will be helpful.  Python Review  Python for Programmers\r\n Introduction to Programs Data Types and Variables\r\n Python Lists\r\n For Loops in Python\r\n While Loops in Python\r\n Writing a Simple Factorial Program\r\n Fun with Strings    Probability  Basic Probability\r\n Probability (Part 6) [Conditional Probability]\r\n Probability (Part 7) [Bayes'' Rule]\r\n Probability (Part 8) [More Bayes'' Rule]\r\n Introduction to Random Variables\r\n Probability Density Functions\r\n Expected Value: E(X)      Linear Algebra          Introduction to Matrices\r\n     Matrix Multiplication (Part 1)\r\n     Matrix Multiplication (Part 2)\r\n     Inverse Matrix (Part 1)\r\n     Inverting Matrices (Part 2)\r\n     Inverting Matrices (Part 3)\r\n     Matrices to Solve a System of Equations\r\n     Singular Matrices\r\n     Introduction to Vectors\r\n     Vector Dot Product and Vector Length\r\n     Defining the Angle Between Vectors\r\n     Cross Product Introduction\r\n     Matrix Vector Products\r\n     Linear Transformations as Matrix Vector Products\r\n     Linear Transformation Examples: Scaling and Reflections\r\n     Linear Transformation Examples: Rotations in R2\r\n     Introduction to Projections\r\n     Exploring the Solution Set of Ax = b\r\n     Transpose of a Matrix\r\n     3x3 Determinant\r\n     Introduction to Eigenvalues and Eigenvectors     </p> </span></p> 				 				 					 						What Will I Learn? 					 					This course will cover probabilistic inference, planning and search, localization, tracking and control, all with a focus on robotics.', 'https://www.udacity.com/course/cs373', 'http://www.youtube.com/watch?v=bdCnb0EFAzk', '0000-00-00', 0, 'https://lh5.ggpht.com/PzzMwjfXRwAihWzCTIE-8mpZc_9PLgX4DSeSrc9G0V_MHxSDaxRMNtmxG4bWRzWMZal6cKhBY55-OiKrrQ=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(454, 'Applied Cryptography: Science of Secrets', 'Cryptography is present in everyday life, from paying with a credit card to using the telephone. Learn all about making and breaking puzzles&hellip;', 'Class Summary 					 					Cryptography is present in everyday life, from paying with a credit card to using the telephone. Learn all about making and breaking puzzles in computing.   				 				 					 						What Should I Know? 					 					This course assumes previous programming experience, comparable to what is covered by the Udacity CS101 course, as well as some understanding of probability and theory of computation and algorithm analysis.   				 				 					 						What Will I Learn? 					 					Explore how secrets are written and shared, as well as what can go wrong when cryptography is misused or implemented badly.', 'https://www.udacity.com/course/cs387', 'http://www.youtube.com/watch?v=HlNDXiVnhqI', '0000-00-00', 0, 'https://lh3.ggpht.com/tdgifWkxGaIpQ_2HhQCAmL4HDUFoHqBKCocr2YYmM0hnArPH_YWB0EN4PSb2Idw-LiILDdAWsb-0J7HQjyg=s0', 'COMPUTER SCIENCE', 'Udacity');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(455, 'CityScope: New Orleans', 'Do you want to think about ways to help solve New Orleans'' problems? CityScope is a project-based introduction to the contemporary city.', 'Do you want to think about ways to help solve New Orleans'' problems? CityScope is a project-based introduction to the contemporary city. ''Problem solving in complex (urban) environments'' is different than ''solving complex problems.'' As a member of a team, you will learn to assess scenarios for the purpose of formulating social, economic and design strategies to provide humane and sustainable solutions. A visit to New Orleans is planned for spring break 2007.', 'http://ocw.mit.edu/courses/architecture/4-001j-cityscope-new-orleans-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-001j-cityscope-new-orleans-spring-2007/4-001js07.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(456, 'Experiencing Architecture Studio', 'This course uses scale models to design environments that orchestrate contrasting material properties and conventional constructional systems to create places that foster specific ways of inhabiting space.', 'This course uses scale models to design environments that orchestrate contrasting material properties and conventional constructional systems to create places that foster specific ways of inhabiting space. It also demonstrates how architecture differs from other forms of design. Intended for students to test aptitude for architectural design and to experience an unfamiliar mode of thought, it''s conducted in a studio format, with lectures on architectural theory and history, and structured for students with no previous experience in design. Required of Architecture majors.', 'http://ocw.mit.edu/courses/architecture/4-101-experiencing-architecture-studio-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-101-experiencing-architecture-studio-spring-2003/4-101s03.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(457, 'Architecture Studio: Intentions', 'This is the second undergraduate design studio.', 'This is the second undergraduate design studio. It introduces a full range of architectural ideas and issues through drawing exercises, analyses of precedents, and explored design methods. Students will develop design skills by conceptualizing and representing architectural ideas and making aesthetic judgments about building design. Discussions regarding architecture''s role in mediating culture, nature and technology will help develop the students'' architectural vocabulary.', 'http://ocw.mit.edu/courses/architecture/4-104-architecture-studio-intentions-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-104-architecture-studio-intentions-spring-2005/4-104s05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(458, 'Architecture Studio: Building in Landscapes', '4.', '4.125 is the third undergraduate design studio. This subject introduces skills needed to build within a landscape establishing continuities between the built and natural world. Students learn to build appropriately through analysis of landscape and climate for a chosen site, and to conceptualize design decisions through drawings and models.', 'http://ocw.mit.edu/courses/architecture/4-125-architecture-studio-building-in-landscapes-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-125-architecture-studio-building-in-landscapes-fall-2002/4-125f02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(459, 'Architecture Studio: Building in Landscapes', 'This subject introduces skills needed to build within a landscape establishing continuities between the built and natural world.', 'This subject introduces skills needed to build within a landscape establishing continuities between the built and natural world. Students learn to build appropriately through analysis of landscape and climate for a chosen site, and to conceptualize design decisions through drawings and models. This class was taught concurrently with 4.125B. Some of the assignments are the same, some are different, and the sites for the final project are different. But since they were taught in tandem, it would be useful to look at both together.', 'http://ocw.mit.edu/courses/architecture/4-125a-architecture-studio-building-in-landscapes-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-125a-architecture-studio-building-in-landscapes-fall-2005/4-125af05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(460, 'Architecture Studio: Building in Landscapes', 'This subject introduces skills needed to build within a landscape establishing continuities between the built and natural world.', 'This subject introduces skills needed to build within a landscape establishing continuities between the built and natural world. Students learn to build appropriately through analysis of landscape and climate for a chosen site, and to conceptualize design decisions through drawings and models. This class was taught concurrently with course 4.125A. Some of the assignments are the same, some are different, and the sites for the final project are different. But since they were taught in tandem, it would be useful to look at both together.', 'http://ocw.mit.edu/courses/architecture/4-125b-architecture-studio-building-in-landscapes-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-125b-architecture-studio-building-in-landscapes-fall-2005/4-125bf05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(461, 'Introduction to Integrated Design', 'During this course, we will be exploring basic questions of architecture through several short design exercises.', 'During this course, we will be exploring basic questions of architecture through several short design exercises. Working with many different media, students will discover the interrelationship of architecture and its related disciplines, such as structures, sustainability, architectural history and the visual arts. Each problem will focus on one of these disciplines and one exploration and presentation technique.', 'http://ocw.mit.edu/courses/architecture/4-191-introduction-to-integrated-design-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-191-introduction-to-integrated-design-fall-2006/4-191f06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(462, 'Analysis of Contemporary Architecture', 'The goal of this course is to investigate with students backgrounds on some of the pivotal events that have shaped our understanding and approach to architecture.', 'The goal of this course is to investigate with students backgrounds on some of the pivotal events that have shaped our understanding and approach to architecture. Emphasis of discussion will be primarily on buildings and works of individual architects. Canonical architects, buildings and movements that have exerted significant influences on the development of architecture will be studied in detail. We will visit some of these buildings for a first-hand look and to evaluate for ourselves their significance or lack thereof. As a final project, each student will analyze a building through drawings, text, bibliography and a physical model in a format ready for documentation and exhibition.', 'http://ocw.mit.edu/courses/architecture/4-205-analysis-of-contemporary-architecture-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-205-analysis-of-contemporary-architecture-fall-2009/4-205f09.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(463, 'The Once and Future City', 'What is a city? What shapes it? How does its history influence future development? How do physical form and institutions vary from city to city and how are these differences significant? How are cities changing and what is their future? This course will explore these and other questions, with emphasis upon twentieth-century American cities.', 'What is a city? What shapes it? How does its history influence future development? How do physical form and institutions vary from city to city and how are these differences significant? How are cities changing and what is their future? This course will explore these and other questions, with emphasis upon twentieth-century American cities. A major focus will be on the physical form of cities - from downtown and inner-city to suburb and edge city - and the processes that shape them. The class Web site can be found here: The City.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-016j-the-once-and-future-city-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-016j-the-once-and-future-city-spring-2006/11-016js06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(464, 'Introduction to Urban Design and Development', 'This course examines the evolving structure of cities and the way that cities, suburbs, and metropolitan areas can be designed and developed.', 'This course examines the evolving structure of cities and the way that cities, suburbs, and metropolitan areas can be designed and developed. Boston and other American cities are studied to see how physical, social, political and economic forces interact to shape and reshape cities over time.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-001j-introduction-to-urban-design-and-development-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-001j-introduction-to-urban-design-and-development-spring-2006/11-001js06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(465, 'Introduction to the Visual Arts', 'This class will introduce students to a variety of contemporary art practices and ideas.', 'This class will introduce students to a variety of contemporary art practices and ideas. The class will begin with a brief overview of ''visual language'' by looking at a variety of artworks and discussing basic concepts revolving around artistic practice. The rest of the class will focus on notions of the real/unreal as explored with various mediums and practices. The class will work in video, sculpture and in public space.', 'http://ocw.mit.edu/courses/architecture/4-301-introduction-to-the-visual-arts-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-301-introduction-to-the-visual-arts-spring-2007/4-301s07.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(466, 'BSAD Foundations in the Visual Arts', 'This class offers a foundation in the visual art practice and its critical analysis for beginning architecture students.', 'This class offers a foundation in the visual art practice and its critical analysis for beginning architecture students. Emphasis is on long-range artistic development and its analogies to architectural thinking and practice. Students will learn to communicate ideas and experiences through various two-dimensional, and three-dimensional, and time-based media, including installations, performance and video. Lectures, visiting artist presentations, field trips, and readings supplement studio practice.', 'http://ocw.mit.edu/courses/architecture/4-302-bsad-foundations-in-the-visual-arts-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-302-bsad-foundations-in-the-visual-arts-fall-2003/4-302f03.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(467, 'Introduction to Photography and Related Media', 'This course provides practical instruction in the fundamentals of analog and digital SLR and medium/large format camera operation, film exposure and development, black and white darkroom techniques, digital imaging, and studio lighting.', 'This course provides practical instruction in the fundamentals of analog and digital SLR and medium/large format camera operation, film exposure and development, black and white darkroom techniques, digital imaging, and studio lighting. This semester we will explore the MIT Department of Brain and Cognitive Sciences for our theme- and site-specific term project, which provides opportunities to develop technical skills and experimental photographic techniques, and for personal artistic exploration. Final projects will be presented on site in exhibition format. Work in progress is continuously presented and discussed in a critical forum. Lectures, readings, visiting professionals, group discussions, and site visits encourage aesthetic appreciation of the medium and a deeper understanding of our semester theme, as well as a critical awareness of how images in our culture are produced and constructed.', 'http://ocw.mit.edu/courses/architecture/4-341-introduction-to-photography-and-related-media-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-341-introduction-to-photography-and-related-media-fall-2007/4-341f07.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(468, 'Introduction to Photography', 'This course combines practical instruction, field trips, group discussions, and individual reviews intended to foster a critical awareness of how images in our culture are produced and constructed.', 'This course combines practical instruction, field trips, group discussions, and individual reviews intended to foster a critical awareness of how images in our culture are produced and constructed. Student-initiated term projects are at the core of this exploration of the relationship of image to language and issues of interpretation and personal history. Besides, this course also offers practical instruction in basic black and white techniques, digital imaging, fundamentals of camera operation, lighting, film exposure, development and printing. Course provides opportunity for continued exploration.', 'http://ocw.mit.edu/courses/architecture/4-341-introduction-to-photography-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-341-introduction-to-photography-fall-2002/4-341f02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(469, 'Introduction to Video', 'This class serves as an introduction to video recording and editing, presenting video as a tool of personal apprehension and expression, with an emphasis on self-exploration, performance, social critique, and the organization of raw experience into aesthetic form (narrative, abstract, documentary, essay).', 'This class serves as an introduction to video recording and editing, presenting video as a tool of personal apprehension and expression, with an emphasis on self-exploration, performance, social critique, and the organization of raw experience into aesthetic form (narrative, abstract, documentary, essay).&nbsp;Students are required to complete a variety of assignments to learn the basics of video capture and editing, culminating in a final assignment that has to do with personal storytelling.', 'http://ocw.mit.edu/courses/architecture/4-351-introduction-to-video-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-351-introduction-to-video-spring-2004/4-351s04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(470, 'Introduction to Building Technology', 'The course aims at providing a fundamental understanding of the physics related to buildings and to propose an overview of the various issues that have to be adequately combined to offer the occupants a physical, functional and psychological well-being.', 'The course aims at providing a fundamental understanding of the physics related to buildings and to propose an overview of the various issues that have to be adequately combined to offer the occupants a physical, functional and psychological well-being. Students will be guided through the different components, constraints and systems of a work of architecture. These will be examined both independently and in the manner in which they interact and affect one another.', 'http://ocw.mit.edu/courses/architecture/4-401-introduction-to-building-technology-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-401-introduction-to-building-technology-spring-2006/chp_street.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(471, 'Building Technology Laboratory', 'In this class, concepts of building technology and experimental methods are studied, in class and in lab assignments.', 'In this class, concepts of building technology and experimental methods are studied, in class and in lab assignments. Projects vary yearly and have included design and testing of strategies for daylighting, passive heating and cooling, and improved indoor air quality via natural ventilation. Experimental methods focus on measurement and analysis of thermally driven and wind-driven airflows, lighting intensity and glare, and heat flow and thermal storage. Experiments are conducted at model and full scale and are often motivated by ongoing field work in developing countries.', 'http://ocw.mit.edu/courses/architecture/4-411-building-technology-laboratory-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-411-building-technology-laboratory-spring-2004/4-411s04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(472, 'Fundamentals of Energy in Buildings', 'This design-based subject provides a first course in energy and thermo-sciences with applications to sustainable energy-efficient architecture and building technology.', 'This design-based subject provides a first course in energy and thermo-sciences with applications to sustainable energy-efficient architecture and building technology. No previous experience with subject matter is assumed. After taking this subject, students will understand introductory thermodynamics and heat transfer, know the leading order factors in building energy use, and have creatively employed their understanding of energy fundamentals and knowledge of building energy use in innovative building design projects. This year, the focus will be on design projects that will complement the new NSTAR/MIT campus efficiency program.', 'http://ocw.mit.edu/courses/architecture/4-42j-fundamentals-of-energy-in-buildings-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-42j-fundamentals-of-energy-in-buildings-fall-2010/4-42jf10.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(473, 'Introduction to Design Computing', 'This course will introduce students to architectural design and computation through the use of computer modeling, rendering and digital fabrication.', 'This course will introduce students to architectural design and computation through the use of computer modeling, rendering and digital fabrication. The course focuses on teaching architectural design with CAD drawing, 3-D modeling, rendering and rapid prototyping. Students will be required to build computer models that will lead to a full package of architectural explorations with computers. Each semester we will explore the design process of a particular building type and building material. The course also investigates a few design processes of selected architects. The course is critical of design principles and building production methods. Student assignments are graded based on the quality of design, representation and constructability. Great design input is always encouraged.', 'http://ocw.mit.edu/courses/architecture/4-500-introduction-to-design-computing-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-500-introduction-to-design-computing-fall-2008/4-500f08.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(474, 'Architectural Construction and Computation', 'This class investigates the use of computers in architectural design and construction.', 'This class investigates the use of computers in architectural design and construction. It begins with a pre-prepared design computer model, which is used for testing and process investigation in construction. It then explores the process of construction from all sides of the practice: detail design, structural design, and both legal and computational issues.', 'http://ocw.mit.edu/courses/architecture/4-501-architectural-construction-and-computation-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-501-architectural-construction-and-computation-fall-2005/4-501f05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(475, 'Computational Design I: Theory and Applications', 'This class introduces design as a computational enterprise in which rules are developed to compose and describe architectural and other designs.', 'This class introduces design as a computational enterprise in which rules are developed to compose and describe architectural and other designs. The class covers topics such as shapes, shape arithmetic, symmetry, spatial relations, shape computations, and shape grammars. It focuses on the application of shape grammars in creative design, and teaches shape grammar fundamentals through in-class, hands-on exercises with abstract shape grammars. The class discusses issues related to practical applications of shape grammars.', 'http://ocw.mit.edu/courses/architecture/4-520-computational-design-i-theory-and-applications-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-520-computational-design-i-theory-and-applications-fall-2005/4-520f05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(476, 'Introduction to the History and Theory of Architecture', 'This course is a global-oriented survey of the history of architecture, from the prehistoric to the sixteenth century.', 'This course is a global-oriented survey of the history of architecture, from the prehistoric to the sixteenth century. It treats buildings and environments, including cities, in the context of the cultural and civilizational history. It offers an introduction to design principles and analysis. Being global, it aims to give the student perspective on the larger pushes and pulls that influence architecture and its meanings, whether these be economic, political, religious or climatic.', 'http://ocw.mit.edu/courses/architecture/4-605-introduction-to-the-history-and-theory-of-architecture-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-605-introduction-to-the-history-and-theory-of-architecture-spring-2012/4-605s12.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(477, 'Topics in the Avant-Garde in Literature and Cinema', '21F.', '21F.031 examines the terms ''avant garde'' and ''Kulturindustrie'' in French and German culture of the early twentieth century. Considering the origins of these concepts in surrealist and dadaist literature, art, and cinema, the course then expands to engage parallel formations across Europe, particularly in the former Soviet Union. Emphasis on the specific historical conditions that enabled these interventions. Guiding questions are these: What was original about the historical avant-garde?&#160;What connections between art and revolution did avant-garde writers and artists imagine? What strategies did they deploy to meet their modernist imperatives? To what extent did their projects maintain a critical stance towards the culture industry? Surveying key interventions in the fields of poetry, painting, sculpture, photography, film, and music, the readings also include signal moments in critical thought of the last century. Figures to be considered are: Adorno, Aragon, Bataille, Beckett, Brecht, Breton, B&#252;rger, Duchamp, Eisenstein, Ernst, J&#252;nger, Greenberg, Kandinsky, Malevich, Mayakovsky, and Tzara. Taught in English, but students are encouraged to consult original sources when possible.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-031j-topics-in-the-avant-garde-in-literature-and-cinema-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-031j-topics-in-the-avant-garde-in-literature-and-cinema-spring-2003/21f-031js03.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(478, 'Religious Architecture and Islamic Cultures', 'This course introduces the history of Islamic cultures through their most vibrant material signs: the religious architecture that spans fourteen centuries and three continents &mdash; Asia, Africa, and Europe.', 'This course introduces the history of Islamic cultures through their most vibrant material signs: the religious architecture that spans fourteen centuries and three continents &mdash; Asia, Africa, and Europe. The course presents Islamic architecture both as a historical tradition and as a cultural catalyst that influenced and was influenced by the civilizations with which it came in contact.', 'http://ocw.mit.edu/courses/architecture/4-614-religious-architecture-and-islamic-cultures-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-614-religious-architecture-and-islamic-cultures-fall-2002/4-614f02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(479, 'Art Since 1940', 'This subject focuses on the objects, history, context, and critical discussion surrounding art since World War II.', 'This subject focuses on the objects, history, context, and critical discussion surrounding art since World War II. Because of the burgeoning increase in art production, the course is necessarily selective. We will trace major developments and movements in art up to the present, primarily from the US; but we will also be looking at art from Europe, Asia, Africa, Latin America, and the Middle East, as well as art &quot;on the margins&quot; &mdash; art that has been overlooked by the mainstream critical press, but may have a broad cultural base in its own community. We will ask what function art serves in its various cultures of origin, and why art has been such a lightning rod for political issues around the world.', 'http://ocw.mit.edu/courses/architecture/4-651-art-since-1940-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-651-art-since-1940-fall-2010/4-651f10.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(480, 'Stories Without Words: Photographing the First Year', 'The transition from high school and home to college and a new living environment can be a fascinating and interesting time, made all the more challenging and interesting by being at MIT.', 'The transition from high school and home to college and a new living environment can be a fascinating and interesting time, made all the more challenging and interesting by being at MIT. More than recording the first semester through a series of snapshots, this freshman seminar will attempt to teach photography as a method of seeing and a tool for better understanding new surroundings. Over the course of the semester, students will develop a body of work through a series of assignments, and then attempt to describe the conditions and emotions of their new environment in a cohesive final presentation.', 'http://ocw.mit.edu/courses/architecture/4-a21-stories-without-words-photographing-the-first-year-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-a21-stories-without-words-photographing-the-first-year-fall-2006/4-a21f06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(481, 'MArch Portfolio Seminar', 'The aim of the Portfolio Seminar is to assist in developing a critical position in relationship to their design work.', 'The aim of the Portfolio Seminar is to assist in developing a critical position in relationship to their design work. By engaging multiple forms of representation, written and visual, students will explore methods that facilitate describing and representing their design work. Through a critical assessment of their existing portfolios, students will first be challenged to articulate design theses and interests in their past projects. Different mediums of representation will then be studied in order to hone an understanding of the relationship between form and content, and more specifically, the understanding of particular modes of representation as different filters through which their work can be read. Some of the questions that will be addressed are: How does one go about describing an image? How does one theorize representation? How does one articulate a design thesis in writing verses visual media? How can the two interact to enhance each other? How do different media, printed verses web publishing, affect the representation of work? How is your work best communicated?', 'http://ocw.mit.edu/courses/architecture/4-107-march-portfolio-seminar-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-107-march-portfolio-seminar-fall-2003/4-107f03.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(482, 'Architectural Design, Level II: Material Essence: The Glass House', 'The theme that unites the Level II studios in the fall semester is a focus upon the ''making of architecture and built form'' as a tectonic, technical and materially driven endeavor.', 'The theme that unites the Level II studios in the fall semester is a focus upon the ''making of architecture and built form'' as a tectonic, technical and materially driven endeavor. It is a design investigation that is rooted in a larger culture of materiality and the associated phenomena, but a study of the language and production of built form as an integrated response to the conceptual proposition of the project. The studio will look to works of architecture where the material tectonic and its resultant technology or fabrication become instrumental to the realization of the ideas, in whatever form they may take. This becomes the ''art of technology'' -- suggesting a level of innovation and creative manipulation as part of the design process to transform material into a composition of beauty and poetry as well as environmental control. In this regard the studio will look to the works and design processes of a number of architects including Shigeru Ban, Peter Zumthor, Herzog and deMeuron, Kazuyo Sejima, Richard Horden, Rick Joy and Glenn Murcutt among others.', 'http://ocw.mit.edu/courses/architecture/4-131-architectural-design-level-ii-material-essence-the-glass-house-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-131-architectural-design-level-ii-material-essence-the-glass-house-fall-2003/4-131f03.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(483, 'Architectural Design, Level II: Material and Tectonic Transformations: The Herreshoff Museum', 'This semester students are asked to transform the Hereshoff Museum in Bristol, Rhode Island, through processes of erasure and addition.', 'This semester students are asked to transform the Hereshoff Museum in Bristol, Rhode Island, through processes of erasure and addition.&#160;Hereshoff Manufacturing was recognized as one of the premier builders of America''s Cup racing boats between 1890''s and 1930''s. The studio, however, is about more than the program.&#160;It is about land, water, and wind and the search for expressing materially and tectonically the relationships between these principle conditions. That is, where the land is primarily about stasis (docking, anchoring and referencing our locus), water''s fluidity holds the latent promise of movement and freedom.&#160;Movement is activated by wind, allowing for negotiating the relationship between water and land.', 'http://ocw.mit.edu/courses/architecture/4-131b-architectural-design-level-ii-material-and-tectonic-transformations-the-herreshoff-museum-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-131b-architectural-design-level-ii-material-and-tectonic-transformations-the-herreshoff-museum-fall-2003/4-131bf03.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(484, 'Architecture Design, Level II: Cuba Studio', 'This architectural studio will have one main project for the semester:&nbsp;to explore the issues surrounding the redesign of an area in Havana, Cuba.', 'This architectural studio will have one main project for the semester:&nbsp;to explore the issues surrounding the redesign of an area in Havana, Cuba. It is a typical area about the size of a Law of Indies block that presently has a mix of housing, work, and shopping, in buildings that need to be replaced and others that need to be rehabilitated. There is also vacant land, and buildings that are unused. Part of the blocks front on the Malecon, the street next to the water. The other edge fronts onto a typical neighborhood. The intention is to study the culture through an understanding of one area of Havana and then design an ''echo'' in architectural form. The design will include public space as well as a mix of buildings: some new, some rehabilitated.', 'http://ocw.mit.edu/courses/architecture/4-196-architecture-design-level-ii-cuba-studio-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-196-architecture-design-level-ii-cuba-studio-spring-2004/4-196s04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(485, 'Architectural Design, Level II: New Orleans Studio', 'The project for this studio is to design a demonstration project for a site near the French Quarter in New Orleans.', 'The project for this studio is to design a demonstration project for a site near the French Quarter in New Orleans. The objectives of the project are the following: To design more intense housing, community, educational and commercial facilities in&nbsp;four to&nbsp;six story buildings. To explore the ''space between'' buildings as a way of designing and shaping objects. To design at three scales - dwelling, cluster and overall. To design dwellings where the owners may be able to help build and gain a skill for employment. To provide/design facilities that can help the residents to gain education and skills.', 'http://ocw.mit.edu/courses/architecture/4-144-architectural-design-level-ii-new-orleans-studio-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-144-architectural-design-level-ii-new-orleans-studio-spring-2006/4-144s06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(486, 'Architectural Design, Level III: A Student Center for MIT', 'This studio will investigate the social, programmatic, tectonic and phenomenological performance and character of a student gathering place on the MIT campus.', 'This studio will investigate the social, programmatic, tectonic and phenomenological performance and character of a student gathering place on the MIT campus. Whether it is simply for socializing or for more specific events, the student gathering place will serve as a refuge from the vigorous educational environment of the Institute, and it will reinforce a critical sense of ''place'' through the almost logical organization of its program. The place will foster a casual discovery of ''being'': a reflection upon the student''s own existence based upon participation in group events and an intellectual attitude toward acting. To create a space that inspires, rather than imposes: such a discovery is the foremost challenge of this studio.', 'http://ocw.mit.edu/courses/architecture/4-155b-architectural-design-level-iii-a-student-center-for-mit-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-155b-architectural-design-level-iii-a-student-center-for-mit-fall-2004/4-155bf04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(487, 'Urban Design Studio: Providence', 'This studio discusses in great detail the design of urban environments, specifically in Providence, RI.', 'This studio discusses in great detail the design of urban environments, specifically in Providence, RI. It will propose strategies for change in large areas of cities, to be developed over time, involving different actors. Fitting forms into natural, man-made, historical, and cultural contexts; enabling desirable activity patterns; conceptualizing built form; providing infrastructure and service systems; guiding the sensory character of development: all are topics covered in the studio.&nbsp;The course&nbsp;integrates architecture and planning students in joint work and requires individual designs and planning guidelines as a final product.', 'http://ocw.mit.edu/courses/architecture/4-163j-urban-design-studio-providence-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-163j-urban-design-studio-providence-spring-2005/4-163js05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(488, 'Beijing Urban Design Studio', 'In 2008, the Beijing Urban Design Studio will focus on the issue of Beijing''s urban transformation under the theme of de-industrialization, by preparing an urban design and development plan for the Shougang (Capital Steel Factory) site.', 'In 2008, the Beijing Urban Design Studio will focus on the issue of Beijing''s urban transformation under the theme of de-industrialization, by preparing an urban design and development plan for the Shougang (Capital Steel Factory) site. This studio will address whether portions of the old massive factory infrastructure can be preserved as a national industrial heritage site embedded into future new development; how to balance the cultural and recreational value of the site with environmental challenges; as well as how to use the site for urban development. A special focus of the studio will be to consider development approaches that minimize energy utilization. To research these questions, students will be asked to interact with clients from the factory, local residents, city officials and experts on transportation, environment, energy and real estate. They will assess strategic options for the steel factory and propose comprehensive plans for the design and development of the brownfield site.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-307-beijing-urban-design-studio-summer-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-307-beijing-urban-design-studio-summer-2008/11-307su08.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(489, 'Beijing Urban Design Studio', 'This is the 20th anniversary of the Beijing Urban Design Studio, which is a joint program between the MIT and Tsinghua University Schools of Architecture and Planning.', 'This is the 20th anniversary of the Beijing Urban Design Studio, which is a joint program between the MIT and Tsinghua University Schools of Architecture and Planning. The goal of the studio is to foster international cooperation through the undertaking of a joint urban design and planning initiative in the city of Beijing involving important, often controversial, sites and projects. Since 1995, almost 250 MIT and Tsinghua University students and faculty have participated in this annual studio, making it one of the most successful and enduring international academic programs between China and the U.S. It has received the Irwin Sizer Award from MIT for outstanding innovation in education. The studio takes place over five weeks in June and July including several weeks in residence at Tsinghua University and two brief study tours to locations and projects that inform the work. It will include 18-20 MIT and 10-15 Tsinghua Architecture and Planning students. The Beijing City Planning Institute, responsible for strategic planning in the city, participates in the studio as the client.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-307-beijing-urban-design-studio-summer-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-307-beijing-urban-design-studio-summer-2006/11-307su06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(490, 'Ecuador Workshop', 'This is a project to assist in the design, drawing, modeling and hopefully constructing of a small Community Children''s Center near Guayaquil, Ecuador.', 'This is a project to assist in the design, drawing, modeling and hopefully constructing of a small Community Children''s Center near Guayaquil, Ecuador. For the last year, Nicki Lehrer, from MIT''s Aero/Astro Department, has been organizing efforts to build the project. The goal of the workshop is to provide her with a full fleshed out design for the community center so it can be built in the summer of 2007.', 'http://ocw.mit.edu/courses/architecture/4-170-ecuador-workshop-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-170-ecuador-workshop-fall-2006/4-170f06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(491, 'Case Studies in City Form', 'This course serves as an introduction to urban form and design, focusing on the physical, historical, and social form of cities.', 'This course serves as an introduction to urban form and design, focusing on the physical, historical, and social form of cities. Selected cities are analyzed, drawn, and compared, to develop a working understanding of urban and architectural form. The development of map making and urban representation is discussed, and use of the computer is required. A special focus is placed on the historical development of the selected cities, especially mid-nineteenth and mid-twentieth century periods of expansion. Readings focus on urban design theory in the twentieth century and will be discussed during a weekly seminar on them. This is a methods class for S.M.Arch.S. students in Architecture and Urbanism.', 'http://ocw.mit.edu/courses/architecture/4-175-case-studies-in-city-form-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-175-case-studies-in-city-form-fall-2005/4-175f05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(492, 'Sustainable Design and Technology Research Workshop', 'This workshop investigates the current state of sustainability in regards to architecture, from the level of the tectonic detail to the urban environment.', 'This workshop investigates the current state of sustainability in regards to architecture, from the level of the tectonic detail to the urban environment. Current research and case studies will be investigated, and students will propose their own solutions as part of the final project.', 'http://ocw.mit.edu/courses/architecture/4-183-sustainable-design-and-technology-research-workshop-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-183-sustainable-design-and-technology-research-workshop-spring-2004/4-183s04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(493, 'Architectural Design Workshop: Collage - Method and Form', 'This class investigates the theory, method, and form of collage.', 'This class investigates the theory, method, and form of collage. It studies not only the historical precedents for collage and their physical attributes, but the psychology and process that plays a part in the making of them. The class was broken into three parts, changing scales and methods each time, to introduce and study the rigor by which decisions were made in relation to the collage. The class was less about the making of art than the study of the processes by which art is made.', 'http://ocw.mit.edu/courses/architecture/4-184-architectural-design-workshop-collage-method-and-form-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-184-architectural-design-workshop-collage-method-and-form-spring-2004/4-184s04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(494, 'Beijing Urban Design Studio', 'The Beijing Urban Design Studio is a joint program between the MIT and Tsinghua University Schools of Architecture and Planning.', 'The Beijing Urban Design Studio is a joint program between the MIT and Tsinghua University Schools of Architecture and Planning.&#160;The goal of the studio is to foster international cooperation through the undertaking of a joint urban design and planning initiative in the city of Beijing involving important, often controversial, sites and projects.&#160;Since 1995, almost 250 MIT and Tsinghua University students and faculty have participated in this annual studio, making it one of the most successful and enduring international academic programs between China and the US.&#160;It has received the Irwin Sizer Award from MIT for outstanding innovation in education. The studio takes place over five weeks in June and July including several weeks in residence at Tsinghua University and two brief study tours to locations and projects that inform the work.&#160;It will include 18-20 MIT and 10-15 Tsinghua Architecture and Planning students. The Beijing City Planning Institute, responsible for strategic planning in the city, participates in the studio as the client.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-946j-beijing-urban-design-studio-summer-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-946j-beijing-urban-design-studio-summer-2004/11-946jsu04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(495, 'Special Problems in Architectural Design', 'This class focuses on representation tools used by architects during the design process and attempts to discuss the relationship they develop with the object of design.', 'This class focuses on representation tools used by architects during the design process and attempts to discuss the relationship they develop with the object of design. Representation plays a key role in architectural design, not only as a medium of conveying and narrating a determined meaning or a preconceived idea, but also as a code of creating new meaning, while the medium seeks to establish a relationship with itself. In this sense, mediums of representation, as external parameters to the design process, are not neutral tools of translating an idea into its concrete form. They are neither authentic means of creativity, nor vapid carriers of an idea. Therefore, an important aspect in issues of meaning is how the architect manipulates the play of translating a concept to its concrete version, through the use of a medium of representation. The course is a continuation of the equivalent course taught in the fall semester and specifically focuses on digital media. The course is intended to establish a reciprocal relationship with the design studio, feeding from and contributing to its content.', 'http://ocw.mit.edu/courses/architecture/4-195-special-problems-in-architectural-design-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-195-special-problems-in-architectural-design-spring-2005/4-195s05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(496, 'Architecture Design, Level II: Cuba Studio', 'This architectural studio will have one main project for the semester:&nbsp;to explore the issues surrounding the redesign of an area in Havana, Cuba.', 'This architectural studio will have one main project for the semester:&nbsp;to explore the issues surrounding the redesign of an area in Havana, Cuba. It is a typical area about the size of a Law of Indies block that presently has a mix of housing, work, and shopping, in buildings that need to be replaced and others that need to be rehabilitated. There is also vacant land, and buildings that are unused. Part of the blocks front on the Malecon, the street next to the water. The other edge fronts onto a typical neighborhood. The intention is to study the culture through an understanding of one area of Havana and then design an ''echo'' in architectural form. The design will include public space as well as a mix of buildings: some new, some rehabilitated.', 'http://ocw.mit.edu/courses/architecture/4-196-architecture-design-level-ii-cuba-studio-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-196-architecture-design-level-ii-cuba-studio-spring-2004/4-196s04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(497, 'Advanced Seminar: Urban Nature and City Design', 'This course explores the urban environment as a natural phenomenon, human habitat, medium of expression, and forum for action.', 'This course explores the urban environment as a natural phenomenon, human habitat, medium of expression, and forum for action. The course has several major themes: how ideas of nature influence the way cities are perceived, designed, built, and managed; how natural processes and urban form interact and the consequences for human health and welfare; how planners and designers can shape the urban natural environment. Each student researches and presents a case, either historical or an example of contemporary theory and practice.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-308j-advanced-seminar-urban-nature-and-city-design-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-308j-advanced-seminar-urban-nature-and-city-design-fall-2005/11-308jf05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(498, 'Sensing Place: Photography as Inquiry', 'This course explores photography as a disciplined way of seeing, of investigating landscapes and expressing ideas.', 'This course explores photography as a disciplined way of seeing, of investigating landscapes and expressing ideas. Readings, observations, and photographs form the basis of discussions on landscape, light, significant detail, place, poetics, narrative, and how photography can inform design and planning, among other issues. The class Web site can be found here: Sensing Place: Photography as Inquiry.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-309j-sensing-place-photography-as-inquiry-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-309j-sensing-place-photography-as-inquiry-spring-2006/11-309js06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(499, 'Sites in Sight: Photography as Inquiry', 'This course explores photography as a disciplined way of seeing, of investigating landscapes and expressing ideas.', 'This course explores photography as a disciplined way of seeing, of investigating landscapes and expressing ideas. Readings, observations, and photographs form the basis of discussions on landscape, light, significant detail, place, poetics, narrative, and how photography can inform design and planning, among other issues. The class website can be found here: Sites in Sight: Photography as Inquiry.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-309j-sites-in-sight-photography-as-inquiry-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-309j-sites-in-sight-photography-as-inquiry-fall-2003/11-309jf03.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(500, 'Urban Design Skills: Observing, Interpreting, and Representing the City', 'The course is designed to be an introduction to methods of analyzing, evaluating, and recording the urban environment first hand.', 'The course is designed to be an introduction to methods of analyzing, evaluating, and recording the urban environment first hand. Its aim is to supplement existing courses that cover theory and history of city design and planning and to better prepare students without prior design background for the studio sequence.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-328j-urban-design-skills-observing-interpreting-and-representing-the-city-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-328j-urban-design-skills-observing-interpreting-and-representing-the-city-fall-2004/11-328jf04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(501, 'Theory of City Form', 'Theories about cities and the form that settlements should take will be discussed.', 'Theories about cities and the form that settlements should take will be discussed. Attempts will be made at a distinction between descriptive and normative theory, by examining examples of various theories of city form over time. The class will concentrate on the origins of the modern city and theories about its emerging form, including the transformation of the nineteenth-century city and its organization. It analyzes current issues of city form in relation to city making, social structure, and physical design. Case studies of several cities will be presented as examples of the theories discussed in the class.', 'http://ocw.mit.edu/courses/architecture/4-241j-theory-of-city-form-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-241j-theory-of-city-form-spring-2004/4-241js04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(502, 'Urban Design Policy and Action', 'In this course we examine the relationship between public policy and urban design through readings, discussions, presentations, and papers.', 'In this course we examine the relationship between public policy and urban design through readings, discussions, presentations, and papers. We also analyze the ways in which policies shape cities, and investigate how governments implement urban design. Students gain a critical understanding of both the complex system of governance within which urban design occurs and the effective tools available for creative intervention.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-337j-urban-design-policy-and-action-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-337j-urban-design-policy-and-action-spring-2009/11-337js09.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(503, 'Introduction to Urban Design and Development', 'This course examines both the structure of cities and ways they can be changed.', 'This course examines both the structure of cities and ways they can be changed. Its scope includes historical forces that have produced cities, models of urban analysis, contemporary theories of urban design, and implementation strategies. Core lectures are supplemented by discussion sessions focusing on student work and field trips. Guest speakers present cases involving current projects illustrating the scope and methods of urban design practice.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-301j-introduction-to-urban-design-and-development-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-301j-introduction-to-urban-design-and-development-fall-2006/11-301jf06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(504, 'Urban Design Politics', 'This is a seminar about the ways that urban design contributes to the distribution of political power and resources in cities.', 'This is a seminar about the ways that urban design contributes to the distribution of political power and resources in cities. &quot;Design,&quot; in this view, is not some value-neutral aesthetic applied to efforts at urban development but is, instead, an integral part of the motives driving that development. The class investigates the nature of the relations between built form and political purposes through close examination of a wide variety of situations where public and private sector design commissions and planning processes have been clearly motivated by political pressures, as well as situations where the political assumptions have remained more tacit. We will explore cases from both developed and developing countries.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-302j-urban-design-politics-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-302j-urban-design-politics-spring-2010/11-302js10.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(505, 'Site and Infrastructure Systems Planning', 'This course is a client-based land analysis and site planning project.', 'This course is a client-based land analysis and site planning project. The primary focus of the course changes from year to year. This year the focus is on Japan''s New Towns. Students will review land inventory, analysis, and planning of sites and the infrastructure systems that serve them.&nbsp;They will&nbsp;also examine spatial organization of uses, parcelization, design of roadways, grading, utility systems, stormwater runoff, parking, traffic and off-site impacts, as well as landscaping. Lectures&nbsp;will cover&nbsp;analytical techniques and examples of good site-planning practice. Requirements include a series of assignments and a client-based project.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-304j-site-and-infrastructure-systems-planning-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-304j-site-and-infrastructure-systems-planning-spring-2009/11-304js09.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(506, 'Introduction to Design Inquiry', 'This subject explores the varied nature and practice of computation in design.', 'This subject explores the varied nature and practice of computation in design. We will view computation and design broadly. Computation will include both work done on the computer (digital computing) and by-hand. Design will include both the process of making designs and artifacts, as well as the designs and artifacts themselves. The aim of the course is to develop a view of computation and design beyond the specifics of techniques and tools, and a critical, self-awareness of our own approaches and metaphors for computation and design.', 'http://ocw.mit.edu/courses/architecture/4-273-introduction-to-design-inquiry-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-273-introduction-to-design-inquiry-fall-2004/4-273f04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(507, 'Introduction to Design Inquiry', 'Introduction to Design Inquiry explores the nature and exercise of design intelligence.', 'Introduction to Design Inquiry explores the nature and exercise of design intelligence. It aims to open avenues for further research and, along them, to open vistas on the teaching of design and on more mindful professional design practices. We see design as processes located in individuals and groups, shaped by the formation and experience of each individual and by the characteristics of the groups that play a role in the design process. People construct the worlds they inhabit out of what they know and have experienced. So also does the designer, but the designer&#8217;s worlds must be possible for others to inhabit and, therefore, to construct. Indeed the success of a design depends in large part on the degree to which these constructive processes yield similar results.', 'http://ocw.mit.edu/courses/architecture/4-273-introduction-to-design-inquiry-fall-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-273-introduction-to-design-inquiry-fall-2001/4-273f01.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(508, 'Research Topics in Architecture: Citizen-Centered Design of Open Governance Systems', 'In this seminar, students will design and perfect a digital environment to house the activities of large-scale organizations of people making bottom-up decisions, such as with citizen-government affairs, voting corporate shareholders or voting members of global non-profits and labor unions.', 'In this seminar, students will design and perfect a digital environment to house the activities of large-scale organizations of people making bottom-up decisions, such as with citizen-government affairs, voting corporate shareholders or voting members of global non-profits and labor unions. A working Open Source prototype created last semester will be used as the starting point, featuring collaborative filtering and electronic agent technology pioneered at the Media Lab. This course focuses on development of online spaces as part of an interdependent human environment, including physical architectures, mapped work processes and social/political dimensions. A cross-disciplinary approach will be taken; students with background in architecture, urban planning, law, cognition, business, digital media and computer science are encouraged to participate. No prior technical knowledge is necessary, though a rudimentary understanding of web page creation is helpful.', 'http://ocw.mit.edu/courses/architecture/4-285-research-topics-in-architecture-citizen-centered-design-of-open-governance-systems-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-285-research-topics-in-architecture-citizen-centered-design-of-open-governance-systems-fall-2002/4-285f02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(509, 'Furniture Making', 'Furniture making is in many ways like bridge building, connections holding posts apart with spans to support a deck.', 'Furniture making is in many ways like bridge building, connections holding posts apart with spans to support a deck. Many architects have tried their hand at furniture design, Wright, Mies Van Der Rohe, Aalto, Saarinen, Le Corbusier, and Gerhy. We will review the history of furniture making in America with a visit to the Decorative Arts Collection at the Museum of Fine Arts in Boston and have Cambridge artist/craftsman Mitch Ryerson show us his work and talk about design process. Students will learn traditional woodworking techniques beginning with the use of hand tools, power tools and finally woodworking machines. Students will build a single piece of furniture of an original design that must support someone weighing 185 lbs. sitting on it 12 inches off the ground made primarily of wood. Students should expect to spend approximately 80 hours in the shop outside of class time. Preregistered architecture students will get first priority but first meeting attendance is mandatory. Twelve student maximum, no exceptions.', 'http://ocw.mit.edu/courses/architecture/4-296-furniture-making-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-296-furniture-making-spring-2005/4-296s05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(510, 'Special Problems in Architecture Studies', 'The course investigates e-Learning systems from a business, policy, technical and legal perspective.', 'The course investigates e-Learning systems from a business, policy, technical and legal perspective. The issues presented will be tackled by discussion of the design and structure of the various example systems. The connection between information architectures and the physical workplace of the users will also be examined. The course will be comprised of readings, discussions, guest speakers and group design sessions. Laboratory sessions will be focused on implementation tools and opportunities to create one''s own working prototypes. Students will learn to describe information architectures using the Unified Modeling Language (used to specify, design and structure web applications) and XML (to designate meaningful content).', 'http://ocw.mit.edu/courses/architecture/4-297-special-problems-in-architecture-studies-fall-2000', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-297-special-problems-in-architecture-studies-fall-2000/4-297f00.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(511, 'The Production of Space: Art, Architecture and Urbanism in Dialogue', 'This seminar engages in the notion of space from various points of departure.', 'This seminar engages in the notion of space from various points of departure. The goal is first of all to engage in the term and secondly to examine possibilities of art, architecture within urban settings in order to produce what is your interpretation of space.', 'http://ocw.mit.edu/courses/architecture/4-303-the-production-of-space-art-architecture-and-urbanism-in-dialogue-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-303-the-production-of-space-art-architecture-and-urbanism-in-dialogue-fall-2006/4-303f06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(512, 'Photography and Related Media', 'Subject combines practical instruction, readings, lectures, field trips, visiting artists, group discussions, and individual reviews.', 'Subject combines practical instruction, readings, lectures, field trips, visiting artists, group discussions, and individual reviews. Fosters a critical awareness of how images in our culture are produced and constructed. Student-initiated term project at the core of exploration. Special consideration given to the relationship of space and the photographic image. Practical instruction in basic black and white techniques, digital imaging, fundamentals of camera operation, lighting, film exposure, development, and printing. Open to beginning and advanced students. Lab fee. Enrollment limited with preference given to current Master of Architecture students.', 'http://ocw.mit.edu/courses/architecture/4-343-photography-and-related-media-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-343-photography-and-related-media-fall-2002/4-343f02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(513, 'Studio Seminar in Public Art', 'How do we define Public Art? This course focuses on the production of projects for public places.', 'How do we define Public Art? This course focuses on the production of projects for public places. Public Art is a concept that is in constant discussion and revision, as much as the evolution and transformation of public spaces and cities are. Monuments are repositories of memory and historical presences with the expectation of being permanent. Public interventions are created not to impose and be temporary, but as forms intended to activate discourse and discussion. Considering the concept of a museum as a public device and how they are searching for new ways of avoiding generic identities, we will deal with the concept of the personal imaginary museum. It should be considered as a point of departure to propose a personal individual construction based on the concept of defining a personal imaginary museum - concept, program, collection, events, architecture, public diffusion, etc.', 'http://ocw.mit.edu/courses/architecture/4-367-studio-seminar-in-public-art-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-367-studio-seminar-in-public-art-spring-2006/4-367s06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(514, 'Interrogative Design Workshop', '''Parrhesia'' was an Athenian right to frank and open speaking, the right that, like the First Amendment, demands a ''fearless speaker'' who must challenge political powers with criticism and unsolicited advice.', '''Parrhesia'' was an Athenian right to frank and open speaking, the right that, like the First Amendment, demands a ''fearless speaker'' who must challenge political powers with criticism and unsolicited advice. Can designer and artist respond today to such a democratic call and demand? Is it possible to do so despite the (increasing) restrictions imposed on our liberties today? Can the designer or public artist operate as a proactive ''parrhesiatic'' agent and contribute to the protection, development and dissemination of ''fearless speaking'' in Public Space?', 'http://ocw.mit.edu/courses/architecture/4-370-interrogative-design-workshop-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-370-interrogative-design-workshop-fall-2005/4-370f05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(515, 'Daylighting', 'This course explores natural and electric lighting that integrates occupant comfort, energy efficiency and daylight availability in an architectural context.', 'This course explores natural and electric lighting that integrates occupant comfort, energy efficiency and daylight availability in an architectural context. Students are asked to evaluate daylighting in real space and simulations, and also high dynamic range photography and physical model building.', 'http://ocw.mit.edu/courses/architecture/4-430-daylighting-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-430-daylighting-spring-2012/4-430s12.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(516, 'Building Technologies III: Building Structural Systems II', 'This course addresses advanced topics in structures, exterior envelopes and contemporary production technologies.', 'This course addresses advanced topics in structures, exterior envelopes and contemporary production technologies. It continues the exploration of structural elements and systems; expanding to include more complex determinant, indeterminate, long-span and high-rise systems. Some of the topics covered include reinforced concrete, steel and engineered wood design, and an introduction to tensile systems. The contemporary exterior envelope is discussed with an emphasis on the classification of systems, their performance attributes and advanced manufacturing technologies. This course is the second of two graduate structures courses, the first of which is 4.462. They offer an expanded version of the content presented in the undergraduate course, 4.440.', 'http://ocw.mit.edu/courses/architecture/4-463-building-technologies-iii-building-structural-systems-ii-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-463-building-technologies-iii-building-structural-systems-ii-fall-2002/4-463f02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(517, 'Analysis of Historic Structures', 'An analysis of historical structures is presented themed sections based around construction materials.', 'An analysis of historical structures is presented themed sections based around construction materials. Structures from all periods of history are analyzed. The goal of the class is to provide an understanding of the preservation of historic structures for all students.', 'http://ocw.mit.edu/courses/architecture/4-448-analysis-of-historic-structures-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-448-analysis-of-historic-structures-fall-2004/4-448f04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(518, 'Basic Structural Design', 'This course provides students with a basic knowledge of structural analysis and design for buildings, bridges and other structures.', 'This course provides students with a basic knowledge of structural analysis and design for buildings, bridges and other structures. The course emphasizes the historical development of structural form and the evolution of structural design knowledge, from Gothic cathedrals to long span suspension bridges. Students will investigate the behavior of structural systems and elements through design exercises, case studies, and load testing of models. Students will design structures using timber, masonry, steel, and concrete and will gain an appreciation of the importance of structural design today, with an emphasis on environmental impact of large scale construction.', 'http://ocw.mit.edu/courses/architecture/4-440-basic-structural-design-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-440-basic-structural-design-spring-2009/4-440s09.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(519, 'Building Technology III: Building Structural Systems', 'This course addresses advanced structures, exterior envelopes and contemporary production technologies.', 'This course addresses advanced structures, exterior envelopes and contemporary production technologies. It continues the exploration of structural elements and systems, and expands to include more complex determinate, indeterminate, long-span and high-rise systems. It covers topics such as reinforced concrete, steel and engineered wood design, and provides an introduction to tensile systems. Lectures also address the contemporary exterior envelope with an emphasis on their performance attributes and advanced manufacturing technologies. This course is required of MArch students.', 'http://ocw.mit.edu/courses/architecture/4-463-building-technology-iii-building-structural-systems-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-463-building-technology-iii-building-structural-systems-fall-2004/4-463f04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(520, 'Building Technologies III: Building Structural Systems II', 'This course addresses advanced topics in structures, exterior envelopes and contemporary production technologies.', 'This course addresses advanced topics in structures, exterior envelopes and contemporary production technologies. It continues the exploration of structural elements and systems; expanding to include more complex determinant, indeterminate, long-span and high-rise systems. Some of the topics covered include reinforced concrete, steel and engineered wood design, and an introduction to tensile systems. The contemporary exterior envelope is discussed with an emphasis on the classification of systems, their performance attributes and advanced manufacturing technologies. This course is the second of two graduate structures courses, the first of which is 4.462. They offer an expanded version of the content presented in the undergraduate course, 4.440.', 'http://ocw.mit.edu/courses/architecture/4-463-building-technologies-iii-building-structural-systems-ii-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-463-building-technologies-iii-building-structural-systems-ii-fall-2002/4-463f02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(521, 'Natural Light in Design', 'Today, computer-based simulations are becoming increasingly popular, especially when daylighting and energy conservation are amongst the key goals for a project.', 'Today, computer-based simulations are becoming increasingly popular, especially when daylighting and energy conservation are amongst the key goals for a project. This two-week workshop will expose participants to the current daylighting simulation models and beyond, by introducing realistic and dynamic assessment methods through hands-on exercises and application to a design project. Open to students and practitioners. This course is offered during the Independent Activities Period (IAP), which is a special 4-week term at MIT that runs from the first week of January until the end of the month.', 'http://ocw.mit.edu/courses/architecture/4-493-natural-light-in-design-january-iap-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-493-natural-light-in-design-january-iap-2006/4-493iap06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(522, 'Emergent Materials II', 'This course will focus on providing students with the tools needed to practice responsible architecture in a contemporary context.', 'This course will focus on providing students with the tools needed to practice responsible architecture in a contemporary context. It will familiarize students with the materials currently used in responsible practice, as well as the material properties most relevant to assembly. The course will also introduce students to materials that are untested but hold promise for future usage. Finally, the course will challenge students to refine their understanding of responsible or sustainable design practice by looking at the evolution of those ideas within the field of architecture.', 'http://ocw.mit.edu/courses/architecture/4-493-emergent-materials-ii-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-493-emergent-materials-ii-spring-2005/4-493s05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(523, 'Digital Design Fabrication', 'This course will guide graduate students through the process of using rapid prototyping and CAD/CAM devices in a studio environment.', 'This course will guide graduate students through the process of using rapid prototyping and CAD/CAM devices in a studio environment. The class has a theoretical focus on machine use within the process of design. Each student is expected to have completed one graduate level of design computing with a full understanding of solid modeling in CAD. Students are also expected to have completed at least one graduate design studio.', 'http://ocw.mit.edu/courses/architecture/4-510-digital-design-fabrication-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-510-digital-design-fabrication-fall-2008/4-510f08.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(524, 'Computational Design I: Theory and Applications', 'This class introduces design as a computational enterprise in which rules are developed to compose and describe architectural and other designs.', 'This class introduces design as a computational enterprise in which rules are developed to compose and describe architectural and other designs. The class covers topics such as shapes, shape arithmetic, symmetry, spatial relations, shape computations, and shape grammars. It focuses on the application of shape grammars in creative design, and teaches shape grammar fundamentals through in-class, hands-on exercises with abstract shape grammars. The class discusses issues related to practical applications of shape grammars.', 'http://ocw.mit.edu/courses/architecture/4-520-computational-design-i-theory-and-applications-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-520-computational-design-i-theory-and-applications-fall-2005/4-520f05.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(525, 'Inquiry into Computation and Design', 'This subject explores the varied nature and practice of computation in design.', 'This subject explores the varied nature and practice of computation in design. We will view computation and design broadly. Computation will include both work done on the computer (digital computing) and by-hand. Design will include both the process of making designs and artifacts, as well as the designs and artifacts themselves. The aim of the course is to develop a view of computation and design beyond the specifics of techniques and tools, and a critical, self-awareness of our own approaches and metaphors for computation and design.', 'http://ocw.mit.edu/courses/architecture/4-580-inquiry-into-computation-and-design-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-580-inquiry-into-computation-and-design-fall-2006/4-580f06.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(526, 'Thinking About Architecture: In History and at Present', 'This class will be constructed as a lecture-discussion, the purpose being to engage important theoretical issues while simultaneously studying their continuing historical significance.', 'This class will be constructed as a lecture-discussion, the purpose being to engage important theoretical issues while simultaneously studying their continuing historical significance. To enhance discussion, three debates will be held in class. Each student will be required to participate in one of these debates. Each student will also be required to write three short papers. Class participation is essential and will be factored into the final grade.The course will portray the history of theory neither as the history of architectural theory exclusively, nor as a series of prepackaged static pronouncements, but as part of a broader set of issues with an active history that must be continually probed and queried. The sequence of topics will not be absolutely predetermined, but some of the primary issues that will be addressed are: pedagogy, professionalism, nature, modernity and the Enlightenment. Classroom discussions and debates are intended to demonstrate differences of opinion and enhance awareness of the consequences that these differences had in specific historical contexts.', 'http://ocw.mit.edu/courses/architecture/4-607-thinking-about-architecture-in-history-and-at-present-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-607-thinking-about-architecture-in-history-and-at-present-fall-2009/4-607f09.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(527, 'Selected Topics in Architecture: Architecture from 1750 to the Present', 'This class is a general study of modern architecture as a response to important technological, cultural, environmental, aesthetic, and theoretical challenges after the European Enlightenment.', 'This class is a general study of modern architecture as a response to important technological, cultural, environmental, aesthetic, and theoretical challenges after the European Enlightenment. It focuses on the theoretical, historiographic, and design approaches to architectural problems encountered in the age of industrial and post-industrial expansion across the globe, with specific attention to the dominance of European modernism in setting the agenda for the discourse of a global modernity at large. It explores modern architectural history through thematic exposition rather than as a simple chronological succession of ideas.', 'http://ocw.mit.edu/courses/architecture/4-645-selected-topics-in-architecture-architecture-from-1750-to-the-present-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-645-selected-topics-in-architecture-architecture-from-1750-to-the-present-fall-2004/4-645f04.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(528, 'Theory and Method in the Study of Architecture and Art', 'This seminar is open to graduate students, and is intended to offer a synoptic view of selected methodologies and thinkers in art history (with some implications for architecture).', 'This seminar is open to graduate students, and is intended to offer a synoptic view of selected methodologies and thinkers in art history (with some implications for architecture). It is a writing-intensive class based on the premise that writing and editing are forms of critical thinking. The syllabus outlines the structure of the course and the readings and assignments for each week. The discipline of art history periodically surges into ''crisis.'' The demise of formalism as a guiding tenet, or connoisseurial appreciation as a general guide, plunged the field into confusion during the 1970s when the battle raged over ''social histories of art'' or ''revisionism;'' in the late 1990s the debate was staged between ''visual studies'' versus ''normative art history.'' The course takes this confusion as itself worthy of study, and seeks to make available some of the new methodologies that have emerged over the past two decades. The ultimate goal is to bring students closer to discovering their own individual methods and voices as writers of art historical prose. In broader terms, we will attempt to understand the historiography of visual art and images more broadly. Our efforts will be predicated on the conviction that art history can serve as a generative discipline for all humanistic disciplines, and even those that style themselves as ''Bildwissenschaft'' (or ''image-science'').', 'http://ocw.mit.edu/courses/architecture/4-661-theory-and-method-in-the-study-of-architecture-and-art-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-661-theory-and-method-in-the-study-of-architecture-and-art-fall-2002/4-661f02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(529, 'Contemporary Architecture and Critical Debate', 'This class, required of all Master of Architecture students, presents a critical review of works, theories, and polemics in architecture in the aftermath of World War II.', 'This class, required of all Master of Architecture students, presents a critical review of works, theories, and polemics in architecture in the aftermath of World War II. The aim is to present a historical understanding of the period, and to develop a meaningful framework to assess contemporary issues in architecture. Special attention will be paid to historiographic questions of how architects construe the terms of their ''present.''', 'http://ocw.mit.edu/courses/architecture/4-665-contemporary-architecture-and-critical-debate-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-665-contemporary-architecture-and-critical-debate-spring-2002/4-665s02.jpg', 'Architecture', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(530, 'Fundamentals of Computational Media Design', 'This class covers the history of 20th century art and design from the perspective of the technologist.', 'This class covers the history of 20th century art and design from the perspective of the technologist. Methods for visual analysis, oral critique, and digital expression are introduced. Class projects this term use the OLPC XO (One Laptop Per Child) laptop, Csound and Python software.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-110-fundamentals-of-computational-media-design-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-110-fundamentals-of-computational-media-design-fall-2008/mas-110f08.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(531, 'Introduction to Doing Research in Media Arts and Sciences', 'This course is intended for students pursuing research projects at the Media Laboratory.', 'This course is intended for students pursuing research projects at the Media Laboratory. Topics include Media Lab research areas, documenting research progress, ethical issues in research; patents, copyrights, intellectual property, and giving oral, written, and online presentations of results. A final oral presentation is required. Enrollment limited with preference given to students in the Media Arts and Sciences freshman program.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-111-introduction-to-doing-research-in-media-arts-and-sciences-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-111-introduction-to-doing-research-in-media-arts-and-sciences-spring-2011/mas-111s11.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(532, 'Computational Camera and Photography', 'A computational camera attempts to digitally capture the essence of visual information by exploiting the synergistic combination of task-specific optics, illumination, sensors and processing.', 'A computational camera attempts to digitally capture the essence of visual information by exploiting the synergistic combination of task-specific optics, illumination, sensors and processing. In this course we will study this emerging multi-disciplinary field at the intersection of signal processing, applied optics, computer graphics and vision, electronics, art, and online sharing through social networks. If novel cameras can be designed to sample light in radically new ways, then rich and useful forms of visual information may be recorded &mdash; beyond those present in traditional photographs. Furthermore, if computational process can be made aware of these novel imaging models, them the scene can be analyzed in higher dimensions and novel aesthetic renderings of the visual information can be synthesized.We will discuss and play with thermal cameras, multi-spectral cameras, high-speed, and 3D range-sensing cameras and camera arrays. We will learn about opportunities in scientific and medical imaging, mobile-phone based photography, camera for HCI and sensors mimicking animal eyes. We will learn about the complete camera pipeline. In several hands-on projects we will build physical imaging prototypes and understand how each stage of the imaging process can be manipulated.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-531-computational-camera-and-photography-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-531-computational-camera-and-photography-fall-2009/mas-531f09.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(533, 'Holographic Imaging', 'MAS.', 'MAS.450 is a laboratory course about holography and holographic imaging. This course teaches holography from a scientific and analytical point of view, moving from interference and diffraction to imaging of single points to the display of three-dimensional images. Using a ''hands-on'' approach, students explore the underlying physical phenomena that make holograms work, as well as designing laboratory setups to make their own images. The course also teaches mathematical techniques that allow the behavior of holography to be understood, predicted, and harnessed. Holography today brings together the fields of optics, chemistry, computer science, electrical engineering, visualization, three-dimensional display, and human perception in a unique and comprehensive way. As such, MAS.450 offers interesting and useful exposure to a wide range of principles and ideas. As a course satisfying the Institute Laboratory Requirement, MAS.450 teaches about science, scientific research, and the scientific method through observation and exploration, hinting at the excitement that inventors feel before they put their final equations to paper.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-450-holographic-imaging-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-450-holographic-imaging-spring-2003/mas-450s03.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(534, 'NextLab I: Designing Mobile Technologies for the Next Billion Users', 'Can you make a cellphone change the world? NextLab is a hands-on year-long design course in which students research, develop and deploy mobile technologies for the next billion mobile users in developing countries.', 'Can you make a cellphone change the world? NextLab is a hands-on year-long design course in which students research, develop and deploy mobile technologies for the next billion mobile users in developing countries. Guided by real-world needs as observed by local partners, students work in multidisciplinary teams on term-long projects, closely collaborating with NGOs and communities at the local level, field practitioners, and experts in relevant fields. Students are expected to leverage technical ingenuity in both mobile and internet technologies together with social insight in order to address social challenges in areas such as health, microfinance, entrepreneurship, education, and civic activism. Students with technically and socially viable prototypes may obtain funding for travel to their target communities, in order to obtain the first-hand feedback necessary to prepare their technologies for full fledged deployment into the real world (subject to guidelines and limitations).', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-965-nextlab-i-designing-mobile-technologies-for-the-next-billion-users-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-965-nextlab-i-designing-mobile-technologies-for-the-next-billion-users-fall-2008/mas-965f08.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(535, 'Special Topics in Multimedia Production: Experiences in Interactive Art', 'This class deals with interactive art.', 'This class deals with interactive art. Visiting artists will discuss their work from a theoretical and practical perspective. Discussions of the history of interactive digital art and contemporary issues in the field will take place. Students will develop an interactive art project for a final exhibition or submit a short paper.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-878-special-topics-in-multimedia-production-experiences-in-interactive-art-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-878-special-topics-in-multimedia-production-experiences-in-interactive-art-fall-2003/mas-878f03.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(536, 'Signals, Systems and Information for Media Technology', 'This class teaches the fundamentals of signals and information theory with emphasis on modeling audio/visual messages and physiologically derived signals, and the human source or recipient.', 'This class teaches the fundamentals of signals and information theory with emphasis on modeling audio/visual messages and physiologically derived signals, and the human source or recipient. Topics include linear systems, difference equations, Z-transforms, sampling and sampling rate conversion, convolution, filtering, modulation, Fourier analysis, entropy, noise, and Shannon''s fundamental theorems. Additional topics may include data compression, filter design, and feature detection. The undergraduate subject MAS.160 meets with the two half-semester graduate subjects MAS.510 and MAS.511, but assignments differ.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-160-signals-systems-and-information-for-media-technology-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-160-signals-systems-and-information-for-media-technology-fall-2007/mas-160f07.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(537, 'Computational Camera and Photography', 'A computational camera attempts to digitally capture the essence of visual information by exploiting the synergistic combination of task-specific optics, illumination, sensors and processing.', 'A computational camera attempts to digitally capture the essence of visual information by exploiting the synergistic combination of task-specific optics, illumination, sensors and processing. In this course we will study this emerging multi-disciplinary field at the intersection of signal processing, applied optics, computer graphics and vision, electronics, art, and online sharing through social networks. If novel cameras can be designed to sample light in radically new ways, then rich and useful forms of visual information may be recorded &mdash; beyond those present in traditional photographs. Furthermore, if computational process can be made aware of these novel imaging models, them the scene can be analyzed in higher dimensions and novel aesthetic renderings of the visual information can be synthesized.We will discuss and play with thermal cameras, multi-spectral cameras, high-speed, and 3D range-sensing cameras and camera arrays. We will learn about opportunities in scientific and medical imaging, mobile-phone based photography, camera for HCI and sensors mimicking animal eyes. We will learn about the complete camera pipeline. In several hands-on projects we will build physical imaging prototypes and understand how each stage of the imaging process can be manipulated.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-531-computational-camera-and-photography-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-531-computational-camera-and-photography-fall-2009/mas-531f09.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(538, 'Pattern Recognition and Analysis', 'This class deals with the fundamentals of characterizing and recognizing patterns and features of interest in numerical data.', 'This class deals with the fundamentals of characterizing and recognizing patterns and features of interest in numerical data. We discuss the basic tools and theory for signal understanding problems with applications to user modeling, affect recognition, speech recognition and understanding, computer vision, physiological analysis, and more. We also cover decision theory, statistical classification, maximum likelihood and Bayesian estimation, nonparametric methods, unsupervised learning and clustering. Additional topics on machine and human learning from active research are also talked about in the class.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-622j-pattern-recognition-and-analysis-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-622j-pattern-recognition-and-analysis-fall-2006/mas-622jf06.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(539, 'Conversational Computer Systems', 'This class explores interaction with mobile computing systems and telephones by voice, including speech synthesis, recognition, digital recording, and browsing recorded speech.', 'This class explores interaction with mobile computing systems and telephones by voice, including speech synthesis, recognition, digital recording, and browsing recorded speech. Emphasis on human interface design issues and interaction techniques appropriate for cognitive requirements of speech. Topics include human speech production and perception, speech recognition and text-to-speech algorithms, telephone networks, and spatial and time-compressed listening. Extensive reading from current research literature.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-632-conversational-computer-systems-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-632-conversational-computer-systems-fall-2008/mas-632f08.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(540, 'Developmental Entrepreneurship', 'This class surveys developmental entrepreneurship via case examples of both successful and failed businesses and generally grapples with deploying and diffusing products and services through entrepreneurial action.', 'This class surveys developmental entrepreneurship via case examples of both successful and failed businesses and generally grapples with deploying and diffusing products and services through entrepreneurial action. By drawing on live and historical cases, especially from South Asia, Africa, Latin America as well as Eastern Europe, China, and other developing regions, we seek to cover the broad spectrum of challenges and opportunities facing developmental entrepreneurs. Finally, we explore a range of established and emerging business models as well as new business opportunities enabled by developmental technologies developed in MIT labs and beyond.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-666-developmental-entrepreneurship-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-666-developmental-entrepreneurship-fall-2003/mas-666f03.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(541, 'How to Learn (Almost) Anything', 'As the digital revolution brings with it radical changes in how and what we learn, people must continue to learn all the time.', 'As the digital revolution brings with it radical changes in how and what we learn, people must continue to learn all the time. New technologies make possible new approaches to learning, new contexts for learning, new tools to support learning, and new ideas of what can be learned. This course will explore these new opportunities for learning with a special focus on what can be learned through immersive, hands-on activities. Students will participate in (and reflect on) a variety of learning situations, and will use Media Lab technologies to develop new workshops, iteratively run and refine the workshops, and analyze how and what the workshop participants learn.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-712-how-to-learn-almost-anything-spring-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-712-how-to-learn-almost-anything-spring-2001/mas-712s01.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(542, 'The Society of Mind', 'This course is an introduction to a theory that tries to explain how minds are made from collections of simpler processes.', 'This course is an introduction to a theory that tries to explain how minds are made from collections of simpler processes. The subject treats such aspects of thinking as vision, language, learning, reasoning, memory, consciousness, ideals, emotions, and personality. Ideas incorporate psychology, artificial intelligence, and computer science to resolve theoretical issues such as whole vs. parts, structural vs. functional descriptions, declarative vs. procedural representations, symbolic vs. connectionist models, and logical vs. common-sense theories of learning.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-868j-the-society-of-mind-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-868j-the-society-of-mind-spring-2007/6-868js07.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(543, 'Industrial Design Intelligence: A Cognitive Approach to Engineering', 'This class investigates cognitive science and technology as it is applied to the industrial design process.', 'This class investigates cognitive science and technology as it is applied to the industrial design process. The class introduces prototyping techniques and approaches for objective evaluation as part of the design process. Students practice evaluating products with mechanical and electronic aspects. Evaluation processes are applied to creating functioning smart product prototypes. This is a project oriented subject that draws upon engineering, aesthetic, and creative skills.&#160;It is geared toward students interested in creating physical products which encompass electronics and computers, aimed at including them in smart scenarios. Students in the class will present readings, learn prototyping skills, create a product prototype, and complete a publication style paper.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-742-industrial-design-intelligence-a-cognitive-approach-to-engineering-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-742-industrial-design-intelligence-a-cognitive-approach-to-engineering-fall-2003/mas-742f03.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(544, 'Autism Theory and Technology', 'This course illuminates current theories about autism together with challenges faced by people on the autism spectrum.', 'This course illuminates current theories about autism together with challenges faced by people on the autism spectrum. Theories in communicating, interacting socially, managing cognitive and affective overload, and achieving independent lifestyles are covered. In parallel, the course presents state-of-the-art technologies being developed for helping improve both theoretical understanding and practical outcomes. Participants are expected to meet and interact with people on the autism spectrum. Weekly reading, discussion, and a term project are required.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-771-autism-theory-and-technology-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-771-autism-theory-and-technology-spring-2011/mas-771s11.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(545, 'Special Topics in Cinematic Storytelling', 'This seminar explores approaches to representation for distributed cinematic storytelling.', 'This seminar explores approaches to representation for distributed cinematic storytelling. The relationship between story creation and story appreciation is analyzed. Readings are drawn from literary and cinematic criticism, as well as from descriptions of interactive, distributed works. Students analyze a range of storytelling techniques; they develop a proposal using visualization techniques; and they prototype a working story experience, culminating in a final project displayed at the end of the semester.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-845-special-topics-in-cinematic-storytelling-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-845-special-topics-in-cinematic-storytelling-spring-2004/mas-845s04.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(546, 'Optical Engineering', 'This course concerns the theory and practice of optical methods in engineering and system design, with an emphasis on diffraction, statistical optics, holography, and imaging.', 'This course concerns the theory and practice of optical methods in engineering and system design, with an emphasis on diffraction, statistical optics, holography, and imaging. It provides the engineering methodology skills necessary to incorporate optical components in systems serving diverse areas such as precision engineering and metrology, bio-imaging, and computing (sensors, data storage, communication in multi-processor systems). Experimental demonstrations and a design project are included.', 'http://ocw.mit.edu/courses/mechanical-engineering/2-717j-optical-engineering-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mechanical-engineering/2-717j-optical-engineering-spring-2002/2-717js02.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(547, 'How to Make (Almost) Anything', 'This course provides a hands-on introduction to the resources for designing and fabricating smart systems, including CAD/CAM/CAE; NC machining, 3-D printing, injection molding, laser cutting; PCB layout and fabrication; sensors and actuators; analog instrumentation; embedded digital processing; wired and wireless communications.', 'This course provides a hands-on introduction to the resources for designing and fabricating smart systems, including CAD/CAM/CAE; NC machining, 3-D printing, injection molding, laser cutting; PCB layout and fabrication; sensors and actuators; analog instrumentation; embedded digital processing; wired and wireless communications. This course also puts emphasis on learning how to use the tools as well as understand how they work.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-863-how-to-make-almost-anything-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-863-how-to-make-almost-anything-fall-2002/mas-863f02.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(548, 'Special Topics in Multimedia Production: Experiences in Interactive Art', 'This class deals with interactive art.', 'This class deals with interactive art. Visiting artists will discuss their work from a theoretical and practical perspective. Discussions of the history of interactive digital art and contemporary issues in the field will take place. Students will develop an interactive art project for a final exhibition or submit a short paper.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-878-special-topics-in-multimedia-production-experiences-in-interactive-art-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-878-special-topics-in-multimedia-production-experiences-in-interactive-art-fall-2003/mas-878f03.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(549, 'Networks, Complexity and Its Applications', 'Networks are a ubiquitous way to represent complex systems, including those in the social and economic sciences.', 'Networks are a ubiquitous way to represent complex systems, including those in the social and economic sciences. The goal of the course is to equip students with conceptual tools that can help them understand complex systems that emerge in both nature and social systems. This is a course intended for a general audience and will discuss applications of networks and complexity to diverse systems, including epidemic spreading, social networks and the evolution of economic development.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-961-networks-complexity-and-its-applications-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-961-networks-complexity-and-its-applications-spring-2011/mas-961s11.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(550, 'Special Topics: Designing Sociable Media', 'This project-based course explores new design strategies for social interaction in the computer mediated world.', 'This project-based course explores new design strategies for social interaction in the computer mediated world. Through weekly readings and design assignments we will examine topics such as: Data-based portraiture Depicting growth, change and the passage of time Visualizing conversations, crowds, and networks Interfaces for the connected city Mobile social technologies The course emphasizes developing visual and interactive literacy.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-961-special-topics-designing-sociable-media-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-961-special-topics-designing-sociable-media-spring-2008/mas-961s08.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(551, 'Seminar on Deep Engagement', 'Innovation in expression, as realized in media, tangible objects, performance and more, &nbsp;generates new questions and new potentials for human engagement.', 'Innovation in expression, as realized in media, tangible objects, performance and more, &nbsp;generates new questions and new potentials for human engagement. When and how does expression engage us deeply? Many personal stories confirm the hypothesis that once we experience deep engagement, it is a state we long for, remember, and want to repeat. This class will explore&nbsp;what&nbsp;underlying&nbsp;principles and innovative methods can ensure the development of higher-quality ''deep engagement''&nbsp;products (artifacts, experiences, environments, performances, etc.) that appeal to a broad audience and that have lasting value over the long term.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-961-seminar-on-deep-engagement-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-961-seminar-on-deep-engagement-fall-2004/mas-961f04.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(552, 'Numeric Photography', 'The aim of the students from the Numeric Photography class at the MIT Media Laboratory was to present an exhibition of digital artworks which blend photography and computation, in the context of scene capture, image play, and interaction.', 'The aim of the students from the Numeric Photography class at the MIT Media Laboratory was to present an exhibition of digital artworks which blend photography and computation, in the context of scene capture, image play, and interaction. Equipped with low end digital cameras, students created weekly software projects to explore aesthetic issues in signal processing and interaction design. The results are more than a hundred Java&reg; applets, many of which are interactive, that suggest new avenues for image play on the computer. These weekly exercises led to the final product, an exhibition of the student work.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-961-numeric-photography-fall-1998', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-961-numeric-photography-fall-1998/mas-961f98.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(553, 'Special Topics: New Textiles', 'This project-based course will explore the future of textiles, focusing particularly on blending rich crafting traditions with new technologies.', 'This project-based course will explore the future of textiles, focusing particularly on blending rich crafting traditions with new technologies. Topics will include textile-based electronics, textile fabrication, algorithmic pattern design, and composites. We will experiment with a wide range of fibers, yarns, and fabrics including traditional materials like wool and cotton as well as metal fibers and yarns, fusible plastics, papers, and resins. We will also explore techniques like felting, laser cutting, CNC knitting, digital printing, and CNC embroidery. Students will complete weekly hands-on assignments and a final project.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-962-special-topics-new-textiles-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-962-special-topics-new-textiles-spring-2010/mas-962s10.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(554, 'The Nature of Constructionist Learning', 'This course examines the philosophical and theoretical foundations of constructionism as a paradigm for formulating and evaluating new theories for learning and approaches to education.', 'This course examines the philosophical and theoretical foundations of constructionism as a paradigm for formulating and evaluating new theories for learning and approaches to education. One of the goals of this course is to help new learning researchers situate their work within the constructionist framework through readings and projects that will focus on the rich interplay between the process of knowledge construction and the development and co-evolution of ideas, learners, tools, and contexts.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-962-the-nature-of-constructionist-learning-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-962-the-nature-of-constructionist-learning-spring-2003/mas-962s03.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(555, 'Special Topics in Media Technology: Computational Semantics', 'How do words get their meanings? How can word meanings be represented and used by machines? We will explore three families of approaches to these questions from a computational perspective.', 'How do words get their meanings? How can word meanings be represented and used by machines? We will explore three families of approaches to these questions from a computational perspective. Relational / structural methods such as semantic networks represent the meaning of words in terms of their relations to other words. Knowledge of the world through perception and action leads to the notion of external grounding, a process by which word meanings are ''attached'' to the world. How an agent theorizes about, and conceptualizes its world provides yet another foundation for word meanings. We will examine each of these perspectives, and consider ways to integrate them.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-962-special-topics-in-media-technology-computational-semantics-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-962-special-topics-in-media-technology-computational-semantics-fall-2002/mas-962f02.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(556, 'Digital Typography', 'This class introduces studies in the algorithmic manipulation of type as word, symbol, and form.', 'This class introduces studies in the algorithmic manipulation of type as word, symbol, and form. Problems covered will include semantic filtering, inherently unstable letterforms, and spoken letters.&nbsp;The history and traditions of typography, and their entry into the digital age, will be studied. Weekly assignments using Java&reg;&nbsp;will explore new ways of looking at and manipulating type.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-962-digital-typography-fall-1997', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-962-digital-typography-fall-1997/mas-962f97.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(557, 'Ambient Intelligence', 'This course focuses on Ambient Intelligence, and how it envisions a world where people are surrounded by intelligent and intuitive interfaces embedded in the everyday objects around them.', 'This course focuses on Ambient Intelligence, and how it envisions a world where people are surrounded by intelligent and intuitive interfaces embedded in the everyday objects around them. These interfaces recognize and respond to the presence and behavior of an individual in a personalized and relevant way. Students are required to do extensive literary research on the subject and participate in class discussions.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-963-ambient-intelligence-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-963-ambient-intelligence-spring-2004/mas-963s04.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(558, 'Techno-identity: Who we are and how we perceive ourselves and others', 'The nature of human identity - how we think of ourselves, how we perceive others - is a mutable concept, changing with the rise and fall of religious beliefs, social mores, philosophical theories.', 'The nature of human identity - how we think of ourselves, how we perceive others - is a mutable concept, changing with the rise and fall of religious beliefs, social mores, philosophical theories. Today, we live in a world in which science and technology are among the most powerful forces reshaping our culture - and thus our definitions and perceptions of identity. In this seminar, we will examine the impact of science and technology on identity. The instructor''s course page may be viewed at http://smg.media.mit.edu/classes/IdentitySeminar/', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-963-techno-identity-who-we-are-and-how-we-perceive-ourselves-and-others-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-963-techno-identity-who-we-are-and-how-we-perceive-ourselves-and-others-spring-2002/mas-963s02.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(559, 'Out of Context: A Course on Computer Systems That Adapt To, and Learn From, Context', 'Increasingly, we are realizing that to make computer systems more intelligent and responsive to users, we will have to make them more sensitive to context.', 'Increasingly, we are realizing that to make computer systems more intelligent and responsive to users, we will have to make them more sensitive to context. Traditional hardware and software design overlooks context because it conceptualizes systems as input-output functions. Systems take input explicitly given to them by a human, act upon that input alone and produce explicit output. But this view is too restrictive. Smart computers, intelligent agent software, and digital devices of the future will also have to operate on data that they observe or gather for themselves. They may have to sense their environment, decide which aspects of a situation are really important, and infer the user''s intention from concrete actions. The system''s actions may be dependent on time, place, or the history of interaction, in other words, dependent upon context. But what exactly is context? We''ll look at perspectives from machine learning, sensors and embedded devices, information visualization, philosophy and psychology. We''ll see how each treats the problem of context, and discuss the implications for design of context-sensitive hardware and software. Course requirements will consist of critiques of class readings (about 3 papers/week), and a final project (paper or computer implementation project).', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-963-out-of-context-a-course-on-computer-systems-that-adapt-to-and-learn-from-context-fall-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-963-out-of-context-a-course-on-computer-systems-that-adapt-to-and-learn-from-context-fall-2001/mas-963f01.gif', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(560, 'Common Sense Reasoning for Interactive Applications', 'This course will explore the state of the art in common sense knowledge, and class projects will design and build interfaces that can exploit this knowledge to make more usable and helpful interfaces.', 'This course will explore the state of the art in common sense knowledge, and class projects will design and build interfaces that can exploit this knowledge to make more usable and helpful interfaces. Course requirements will consist of critiques of class readings (about 2 papers/week), and a final project (paper or computer implementation project). Grades will be based primarily on the projects, as well as a small component for class and online participation', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-964-common-sense-reasoning-for-interactive-applications-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-964-common-sense-reasoning-for-interactive-applications-fall-2002/mas-964f02.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(561, 'Relational Machines', 'This course examines the issues, principles, and challenges toward building relational machines through a combination of studio-style design and critique along with lecture, lively discussion of course readings, and assignments.', 'This course examines the issues, principles, and challenges toward building relational machines through a combination of studio-style design and critique along with lecture, lively discussion of course readings, and assignments. Insights from social psychology, human-computer interaction, and design will be examined, as well as how these ideas are manifest in a broad range of applications for software agents and robots.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-965-relational-machines-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-965-relational-machines-spring-2005/mas-965s05.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(562, 'Social Visualization', 'Millions of people are on-line today and the number is rapidly growing - yet this virtual crowd is often invisible.', 'Millions of people are on-line today and the number is rapidly growing - yet this virtual crowd is often invisible. In this course we will examine ways of visualizing people, their activities and their interactions. Students will study the cognitive and cultural basis for social visualization through readings drawn from sociology, psychology and interface design and they will explore new ways of depicting virtual crowds and mapping electronic spaces through a series of design exercises.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-965-social-visualization-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-965-social-visualization-fall-2004/mas-965f04.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(563, 'Digital Anthropology', 'Digital Anthropology is a Spring 2003 applied social science and media arts seminar, surveying the blossoming arena of digital-artifact enabled experimental sociology/anthropology.', 'Digital Anthropology is a Spring 2003 applied social science and media arts seminar, surveying the blossoming arena of digital-artifact enabled experimental sociology/anthropology. We will emphasize on both (a) Technology Testbeds &#8211; systematically deploying research lab prototypes and corporate pre-production products in a sample human organizational population and carefully observing the social consequences, and (b) Sociometrics &#8211; using digital artifacts to better observe and measure the complex social reality of interesting human systems.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-966-digital-anthropology-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-966-digital-anthropology-spring-2003/mas-966s03.jpg', 'Media Arts and Sciences', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(564, 'Introduction to Urban Design and Development', 'This course examines the evolving structure of cities and the way that cities, suburbs, and metropolitan areas can be designed and developed.', 'This course examines the evolving structure of cities and the way that cities, suburbs, and metropolitan areas can be designed and developed. Boston and other American cities are studied to see how physical, social, political and economic forces interact to shape and reshape cities over time.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-001j-introduction-to-urban-design-and-development-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-001j-introduction-to-urban-design-and-development-spring-2006/11-001js06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(565, 'Fundamentals of Public Policy', 'Fundamentals of Public Policy is an introductory course that explores policy-making as both a problem-solving process and a political process.', 'Fundamentals of Public Policy is an introductory course that explores policy-making as both a problem-solving process and a political process. We look at policy-making from the perspective of different focal actors and institutions, including: administrative agencies, legislators, the courts, the mass public, interest groups, and the media. We examine the interplay between policy development and institutions, and review normative and empirical models of policy-making. Exploring these issues will require us to address questions like: How and why does something come to be seen as a &quot;public problem&quot; requiring a governmental response, while others fail to get attention? Why do we need public policies? What determines the content and nature of public policies? Who decides public policy priorities? Does public policy ever accomplish anything worthwhile?', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-002j-fundamentals-of-public-policy-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-002j-fundamentals-of-public-policy-fall-2004/11-002jf04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(566, 'CityScope: New Orleans', 'Do you want to think about ways to help solve New Orleans'' problems? CityScope is a project-based introduction to the contemporary city.', 'Do you want to think about ways to help solve New Orleans'' problems? CityScope is a project-based introduction to the contemporary city. ''Problem solving in complex (urban) environments'' is different than ''solving complex problems.'' As a member of a team, you will learn to assess scenarios for the purpose of formulating social, economic and design strategies to provide humane and sustainable solutions. A visit to New Orleans is planned for spring break 2007.', 'http://ocw.mit.edu/courses/architecture/4-001j-cityscope-new-orleans-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-001j-cityscope-new-orleans-spring-2007/4-001js07.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(567, 'The Art and Science of Negotiation', 'This course provides an introduction to bargaining and negotiation in public, business, and legal settings.', 'This course provides an introduction to bargaining and negotiation in public, business, and legal settings. It combines a ''hands-on'' skill-building orientation with a look at pertinent social theory. Strategy, communications, ethics, and institutional influences are examined as they influence the ability of actors to analyze problems, negotiate agreements, and resolve disputes in social, organizational, and political circumstances characterized by interdependent interests.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-011-the-art-and-science-of-negotiation-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-011-the-art-and-science-of-negotiation-spring-2006/11-011s06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(568, 'The Ancient City', 'This course focuses on the archaeology of the Greek and Roman city.', 'This course focuses on the archaeology of the Greek and Roman city. It investigates the relationship between urban architecture and the political, social, and economic role of cities in the Greek and Roman world, by analyzing a range of archaeological and literary evidence relevant to the use of space in Greek and Roman cities (e.g. Athens, Paestum, Rome, Pompeii) and a range of theoretical frameworks for the study of ancient urbanism.', 'http://ocw.mit.edu/courses/history/21h-405j-the-ancient-city-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-405j-the-ancient-city-spring-2005/21h-405js05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(569, 'American Urban History I', 'This course is a seminar on the history of institutions and institutional change in American cities from roughly 1850 to the present.', 'This course is a seminar on the history of institutions and institutional change in American cities from roughly 1850 to the present. Among the institutions to be looked at are political machines, police departments, courts, schools, prisons, public authorities, and universities. The focus of the course is on readings and discussions.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-013j-american-urban-history-i-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-013j-american-urban-history-i-spring-2010/11-013js10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(570, 'Riots, Strikes, and Conspiracies in American History', 'This course uses readings and discussions to focus on a series of short-term events that shed light on American politics, culture, and social organization.', 'This course uses readings and discussions to focus on a series of short-term events that shed light on American politics, culture, and social organization. It emphasizes finding ways to make sense of these complicated, highly traumatic events, and on using them to understand larger processes of change in American history. The class also gives students experience with primary documentation research through a term paper assignment.', 'http://ocw.mit.edu/courses/history/21h-104j-riots-strikes-and-conspiracies-in-american-history-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-104j-riots-strikes-and-conspiracies-in-american-history-fall-2010/21h-104jf10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(571, 'The Once and Future City', 'What is a city? What shapes it? How does its history influence future development? How do physical form and institutions vary from city to city and how are these differences significant? How are cities changing and what is their future? This course will explore these and other questions, with emphasis upon twentieth-century American cities.', 'What is a city? What shapes it? How does its history influence future development? How do physical form and institutions vary from city to city and how are these differences significant? How are cities changing and what is their future? This course will explore these and other questions, with emphasis upon twentieth-century American cities. A major focus will be on the physical form of cities - from downtown and inner-city to suburb and edge city - and the processes that shape them. The class Web site can be found here: The City.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-016j-the-once-and-future-city-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-016j-the-once-and-future-city-spring-2006/11-016js06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(572, 'Poverty, Public Policy and Controversy', 'This course covers topics and questions such as: What is poverty? How is it defined and measured in the United States and other countries? What are the different program designs that countries use to relieve poverty? To answer these questions, the course examines the main public policy frames that guide theory, research, policy, and practice.', 'This course covers topics and questions such as: What is poverty? How is it defined and measured in the United States and other countries? What are the different program designs that countries use to relieve poverty? To answer these questions, the course examines the main public policy frames that guide theory, research, policy, and practice. How do the definition and policies to deal with poverty change over time? What are the economic, political, and social forces that contribute to the persistence of poverty and its periodic reframing? Can social science to help to resolve the public policy debates that make poverty and its relief so controversial?', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-020-poverty-public-policy-and-controversy-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-020-poverty-public-policy-and-controversy-fall-2003/11-020f03.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(573, 'Downtown', 'This seminar focuses on downtowns in U.', 'This seminar focuses on downtowns in U.S. cities from the late nineteenth century to the late twentieth century. Emphasis will be placed on downtown as an idea, place, and cluster of interests; on the changing character of downtown; and on recent efforts to rebuild it. Subjects to be considered will include subways, skyscrapers, highways, urban renewal, and retail centers. The focus will be on readings, discussions, and individual research projects.', 'http://ocw.mit.edu/courses/history/21h-234j-downtown-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-234j-downtown-spring-2005/21h-234js05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(574, 'City to City: Comparing, Researching and Writing about Cities: New Orleans', 'City to City, as a class, will jump into the complexity of planning in New Orleans, a post-disaster city.', 'City to City, as a class, will jump into the complexity of planning in New Orleans, a post-disaster city. City-to-City will ask how a post-disaster city grapple with its ideas of identity, what it is, who it represents, and how it projects its sense of self to residences, businesses, tourists, and to the outside world. In considering its people, how do city planners think about who lives where and why? At the same time, how can city planners celebrate a city''s history and its culture and how can these elements be woven into reconstruction? Students will travel from Cambridge to New Orleans over Spring Break to meet and consult with their alumni clients, and continue to work on projects.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-027-city-to-city-comparing-researching-and-writing-about-cities-new-orleans-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-027-city-to-city-comparing-researching-and-writing-about-cities-new-orleans-spring-2011/11-027s11.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(575, 'City to City: Comparing, Researching and Writing about Cities', 'This course introduces undergraduate planning students to the role of the planner in researching issues in cities both in the United States and abroad.', 'This course introduces undergraduate planning students to the role of the planner in researching issues in cities both in the United States and abroad. This course is a practical, hands-on workshop that challenges students to research, write and present their ideas on two different cities: A U.S. City (preferably somewhere close) and Copenhagen. Students will be equipped to: select and research a thesis topic, work professionally with faculty and other experts on the topic of their choice, and research, write and present.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-027-city-to-city-comparing-researching-and-writing-about-cities-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-027-city-to-city-comparing-researching-and-writing-about-cities-spring-2006/11-027s06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(576, 'Big Plans', 'This course explores social, technological, political, economic, and cultural implications of ''Big Plans'' in the urban context.', 'This course explores social, technological, political, economic, and cultural implications of ''Big Plans'' in the urban context. Local and international case studies (such as Boston''s Central Artery and Curitiba, Brazil''s bus transit system) are used to understand the process of making major changes to the city fabric. The efficacy of top-down and bottom-up planning and the applicability of planning strategies across cultural boundaries are considered.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-123-big-plans-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-123-big-plans-spring-2003/11-123s03.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(577, 'Introduction to Education: Looking Forward and Looking Back on Education', 'An introductory course on teaching and learning science and mathematics in a variety of K-12 settings.', 'An introductory course on teaching and learning science and mathematics in a variety of K-12 settings. Topics include education and media, education reform, the history of education, simulations, games, and the digital divide.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-124-introduction-to-education-looking-forward-and-looking-back-on-education-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-124-introduction-to-education-looking-forward-and-looking-back-on-education-fall-2011/11-124f11.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(578, 'Introduction to Education: Understanding and Evaluating Education', 'This class uses K-12 classroom experiences, along with student-centered classroom activities and student-led classes, to explore issues in schools and education.', 'This class uses K-12 classroom experiences, along with student-centered classroom activities and student-led classes, to explore issues in schools and education. Students in this course spend time each week observing pre-college math and science classes. Topics of study include design and implementation of curriculum, addressing the needs of a diversity of students, standards in math and science, student misconceptions, methods of instruction, the digital divide, teaching through different media, and student assessment.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-125-introduction-to-education-understanding-and-evaluating-education-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-125-introduction-to-education-understanding-and-evaluating-education-spring-2009/11-125s09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(579, 'Computer Games and Simulations for Investigation and Education', 'In this project-based course, students from all disciplines are encouraged to understand how we learn from interactive computer environments, and delve into the process of designing and understanding simulations and games for learning.', 'In this project-based course, students from all disciplines are encouraged to understand how we learn from interactive computer environments, and delve into the process of designing and understanding simulations and games for learning.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-127j-computer-games-and-simulations-for-investigation-and-education-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-127j-computer-games-and-simulations-for-investigation-and-education-spring-2009/11-127js09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(580, 'Information Technology and the Labor Market', 'This course explores how information technology is reshaping different dimensions of the U.', 'This course explores how information technology is reshaping different dimensions of the U.S. labor market: the way work is organized, the mix of occupations, the skills required to perform in an occupation, economy-wide labor productivity, and the distribution of wages.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-128-information-technology-and-the-labor-market-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-128-information-technology-and-the-labor-market-spring-2005/11-128s05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(581, 'Educational Theory and Practice I', 'This course is designed to prepare you for a successful student teaching experience.', 'This course is designed to prepare you for a successful student teaching experience. Some of the major themes and activities are: analysis of yourself as a teacher and as a learner, subject knowledge, adolescent development, student learning styles, lesson planning, assessment strategies, classroom management techniques and differentiated instruction. The course requires significant personal involvement and time. You will observe high school classes, begin to pursue a more active role in the classroom in the latter part of the semester, do reflective writings on what you see and think (journal), design and teach a mini-lesson, design a major curriculum unit and engage in our classroom discussions and activities.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-129-educational-theory-and-practice-i-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-129-educational-theory-and-practice-i-fall-2011/11-129f11.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(582, 'Energy Decisions, Markets, and Policies', 'This course examines the choices and constraints regarding sources and uses of energy by households, firms, and governments through a number of frameworks to describe and explain behavior at various levels of aggregation.', 'This course examines the choices and constraints regarding sources and uses of energy by households, firms, and governments through a number of frameworks to describe and explain behavior at various levels of aggregation. Examples include a wide range of countries, scope, settings, and analytical approaches. This course is one of many OCW Energy Courses, and it is a core subject in MIT''s undergraduate Energy Studies Minor. This Institute-wide program complements the deep expertise obtained in any major with a broad understanding of the interlinked realms of science, technology, and social sciences as they relate to energy and associated environmental challenges.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-031j-energy-decisions-markets-and-policies-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-031j-energy-decisions-markets-and-policies-spring-2012/15-031js12.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(583, 'Law and Society', 'Law is a common and yet distinct aspect of everyday life in modern societies.', 'Law is a common and yet distinct aspect of everyday life in modern societies.&#160;This course examines the central features of law as a social institution and as a feature of popular culture.&#160;We will explore the nature of law as a set of social systems, central actors in the systems, legal reasoning, and the relationship of the legal form and reasoning to social change. The course emphasizes the relationship between the internal logic of legal devices and economic, political and social processes.&#160;Emphasis is placed upon developing a perspective which views law as a practical resource, a mechanism for handling the widest range of unspecified social issues, problems, and conflicts, and at the same time, as a set of shared representations and aspirations. We will explore the range of experiences of law for its ministers (lawyers, judges, law enforcement agents and administrators) as well as for its supplicants (citizens, plaintiffs, defendants).&#160;We will examine how law is mobilized and deployed by professionals and ordinary citizens.&#160;We cannot cover all aspects of the legal system, nor focus on all the different actors. A set of topics has been selected to develop understanding of the situational and systemic demands within which actors in the legal system operate and perform their roles; at the same time, we will try to discover systematic patterns in the uses and consequences of law.&#160;Throughout the course there is concern for understanding what we mean by legality and the rule of law.', 'http://ocw.mit.edu/courses/anthropology/21a-219-law-and-society-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-219-law-and-society-spring-2003/21a-219s03.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(584, 'Human Rights in Theory and Practice', 'This course provides a rigorous and critical introduction to the foundation, structure and operation of the international human rights movement.', 'This course provides a rigorous and critical introduction to the foundation, structure and operation of the international human rights movement. It includes leading theoretical and institutional issues and the functioning of the international human rights mechanisms including non-governmental and inter-governmental ones. It covers cutting-edge human rights issues including gender and race discrimination, religion and state, national security and terrorism, globalization and human rights, and technology and human rights.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-164-human-rights-in-theory-and-practice-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-164-human-rights-in-theory-and-practice-fall-2010/11-164f10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(585, 'Law, Social Movements, and Public Policy: Comparative and International Experience', 'The course introduces theoretical frameworks from legal and social movement theories as applied to court opinions, legislation, treaties, law-related articles, and policy-oriented materials and focuses on the impact of the relationship between courts and grassroots activism on current issues like trade, environmental regulation, and human rights enforcement.', 'The course introduces theoretical frameworks from legal and social movement theories as applied to court opinions, legislation, treaties, law-related articles, and policy-oriented materials and focuses on the impact of the relationship between courts and grassroots activism on current issues like trade, environmental regulation, and human rights enforcement. Students examine case studies of institutional processes including the World Trade Organization and the World Bank from key countries like the US and India.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-166-law-social-movements-and-public-policy-comparative-and-international-experience-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-166-law-social-movements-and-public-policy-comparative-and-international-experience-fall-2002/11-166f02.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(586, 'Economic Development & Technical Capabilities', 'The economic growth of developing countries requires the acquisition of technological capabilities.', 'The economic growth of developing countries requires the acquisition of technological capabilities. In countries at the world technological frontier, such capabilities refer to cutting edge skills to innovate entirely new products. In developing countries, the requisite technological capabilities are broader, and include production engineering, project execution and incremental innovation to make borrowed technology work. Theories of technology acquisition are examined. The empirical evidence is taken from two sets of developing countries; the most advanced (Taiwan, Korea, India, China and Brazil) and the least advanced (Africa and Middle Eastern countries).', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-167-economic-development-technical-capabilities-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-167-economic-development-technical-capabilities-spring-2004/11-167s04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(587, 'Global Freshwater Crisis', 'For the first time in history, the global demand for freshwater is overtaking its supply in many parts of the world.', 'For the first time in history, the global demand for freshwater is overtaking its supply in many parts of the world. The U.N. predicts that by 2025, more than half of the countries in the world will be experiencing water stress or outright shortages. Lack of water can cause disease, food shortages, starvation, migrations, political conflict, and even lead to war. Models of cooperation, both historic and contemporary, show the way forward. The first half of the course details the multiple facets of the water crisis. Topics include water systems, water transfers, dams, pollution, climate change, scarcity, water conflict/cooperation, food security, and agriculture. The second half of the course describes innovative solutions: Adaptive technologies and adaptation through policy, planning, management, economic tools, and finally, human behaviors required to preserve this precious and imperiled resource. Several field trips to water/wastewater/biosolids reuse and water-energy sites will help us to better comprehend both local and international challenges and solutions.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-s196-global-freshwater-crisis-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-s196-global-freshwater-crisis-spring-2011/11-s196s11.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(588, 'Downtown', 'This seminar focuses on downtowns in U.', 'This seminar focuses on downtowns in U.S. cities from the late nineteenth century to the late twentieth century. Emphasis will be placed on downtown as an idea, place, and cluster of interests; on the changing character of downtown; and on recent efforts to rebuild it. Subjects to be considered will include subways, skyscrapers, highways, urban renewal, and retail centers. The focus will be on readings, discussions, and individual research projects.', 'http://ocw.mit.edu/courses/history/21h-234j-downtown-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-234j-downtown-spring-2005/21h-234js05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(589, 'Infrastructure and Energy Technology Challenges', 'This seminar examines efforts in developing and advanced nations and regions to create, finance, and regulate infrastructure and energy technologies from a variety of methodological and disciplinary perspectives.', 'This seminar examines efforts in developing and advanced nations and regions to create, finance, and regulate infrastructure and energy technologies from a variety of methodological and disciplinary perspectives. It is conducted with intensive in-class discussions and debates.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-165-infrastructure-and-energy-technology-challenges-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-165-infrastructure-and-energy-technology-challenges-fall-2011/11-165f09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(590, 'Global Freshwater Crisis', 'For the first time in history, the global demand for freshwater is overtaking its supply in many parts of the world.', 'For the first time in history, the global demand for freshwater is overtaking its supply in many parts of the world. The U.N. predicts that by 2025, more than half of the countries in the world will be experiencing water stress or outright shortages. Lack of water can cause disease, food shortages, starvation, migrations, political conflict, and even lead to war. Models of cooperation, both historic and contemporary, show the way forward. The first half of the course details the multiple facets of the water crisis. Topics include water systems, water transfers, dams, pollution, climate change, scarcity, water conflict/cooperation, food security, and agriculture. The second half of the course describes innovative solutions: Adaptive technologies and adaptation through policy, planning, management, economic tools, and finally, human behaviors required to preserve this precious and imperiled resource. Several field trips to water/wastewater/biosolids reuse and water-energy sites will help us to better comprehend both local and international challenges and solutions.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-s196-global-freshwater-crisis-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-s196-global-freshwater-crisis-spring-2011/11-s196s11.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(591, 'A Workshop on Geographic Information Systems', 'This class uses lab exercises and a workshop setting to help students develop a solid understanding of the planning and public management uses of geographic information systems (GIS).', 'This class uses lab exercises and a workshop setting to help students develop a solid understanding of the planning and public management uses of geographic information systems (GIS). The goals are to help students: acquire technical skills in the use of GIS software; acquire qualitative methods skills in data and document gathering, analyzing information, and presenting results; and investigate the potential and practicality of GIS technologies in a typical planning setting and evaluate possible applications. The workshop teaches GIS techniques and basic database management at a level that extends somewhat beyond the basic thematic mapping and data manipulation skills included in the MCP core classes (viz. 11.204 and 11.220). Instead of focusing on one thematic map of a single variable, students will concentrate on more open-ended planning questions that invite spatial analysis but will require judgment and exploration to select relevant data and mapping techniques; involve mixing and matching new, local data with extracts from official records (such as census data, parcel data and regional employment and population forecasts); utilize spatial analysis techniques such as buffering, address matching, overlays; use other modeling and visualization techniques beyond thematic mapping; and raise questions about the skills, strategy, and organizational support needed to sustain such analytic capability within a variety of local and regional planning settings. Students seeking graduate credit should enroll in the subject 11.520; undergraduates should enroll in the subject 11.188. The subjects meet together and have nearly identical content. ArcGIS/ArcMap/ArcInfo Graphical User Interface is the intellectual property of ESRI and is used herein with permission. Copyright &copy; ESRI. All rights reserved.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-520-a-workshop-on-geographic-information-systems-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-520-a-workshop-on-geographic-information-systems-fall-2005/11-520f05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(592, 'Gateway to the Profession of Planning', 'The purpose of the course is to cultivate the sensibilities necessary for effective planning practice.', 'The purpose of the course is to cultivate the sensibilities necessary for effective planning practice. This objective rests on one key assumption: that a set of key sensibilities creates the right mindset for practice.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-201-gateway-to-the-profession-of-planning-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-201-gateway-to-the-profession-of-planning-fall-2010/11-201f10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(593, 'Gateway: Planning Action', 'This course introduces incoming students in the Master in City Planning (MCP) program to the theory and history of planning in the public interest.', 'This course introduces incoming students in the Master in City Planning (MCP) program to the theory and history of planning in the public interest. It relies primarily on challenging real-world cases to highlight persistent dilemmas: the power and limits of planning, the multiple roles in which planners find themselves in communities around the globe, and the political, ethical, and practical dilemmas that planners face as they try to be effective. As such, the course provides an introduction to the major ideas and debates that define what the field labels &quot;planning theory,&quot; as well as a (necessarily) condensed global history of modern planning. Courses in planning history, politics, and ethics&mdash;often several of them&mdash;are required in all accredited graduate programs in planning in the U.S. Gateway: Planning Action combines those contents, with a stronger focus on real-world cases than more conventional lecture-based planning theory and history courses at other schools. It also adds several opportunities to strengthen hands-on professional competencies, especially in communication.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-201-gateway-planning-action-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-201-gateway-planning-action-fall-2007/11-201f07.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(594, 'Gateway: Planning Action', 'This class introduces first semester MCP students to the persistent themes and challenges facing planners.', 'This class introduces first semester MCP students to the persistent themes and challenges facing planners. The goals of this class are: to excite students about their chosen profession; to offer a theoretical framework for thinking about the kinds of interventions that planners are expected to take; to introduce students to some of the most interesting and challenging theoretical debates in the planning field; and to press students to think about the best way of using their time to ensure their own personal professional development.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-201-gateway-planning-action-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-201-gateway-planning-action-fall-2002/11-201f02.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(595, 'Planning Economics', 'Planning Economics will apply microeconomic theory to issues that markets don''t always handle well and so are not usually covered in a standard microeconomics course.', 'Planning Economics will apply microeconomic theory to issues that markets don''t always handle well and so are not usually covered in a standard microeconomics course. Issues for this year include global warming, how you value a national park, the economics and politics of New York City development, how cities form and why people are willing to pay more to live in, say, the Boston Metro area, than they would pay to live in rural North Dakota, and how to evaluate costs and benefits that occur at different points in time.&nbsp;', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-202-planning-economics-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-202-planning-economics-fall-2010/11-202f10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(596, 'Microeconomics', 'Microeconomics will ground you in - surprise - basic microeconomics-how markets function, how to think about allocating scarce resources among competing uses, what profit maximizing behavior means in industries with different numbers of competitors, how technology and trade reshapes the opportunities people face, and so on.', 'Microeconomics will ground you in - surprise - basic microeconomics-how markets function, how to think about allocating scarce resources among competing uses, what profit maximizing behavior means in industries with different numbers of competitors, how technology and trade reshapes the opportunities people face, and so on. We will apply economic ideas to understand current economic problems, including the housing bubble, the current unemployment situation (particularly for high school gradutes), how Google makes its money and why healthcare costs are rising so fast.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-203-microeconomics-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-203-microeconomics-fall-2010/11-203f10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(597, 'Introduction to Computers in Public Management II', 'Second of two modules facilitating a basic understanding of computing in planning and public management.', 'Second of two modules facilitating a basic understanding of computing in planning and public management. Students develop problem-solving skills using computer-based tools for ''what-if'' analyses. Emphasis on spatial analysis using geographic information systems and database query tools.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-208-introduction-to-computers-in-public-management-ii-january-iap-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-208-introduction-to-computers-in-public-management-ii-january-iap-2002/11-208iap02.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(598, 'Quantitative Reasoning & Statistical Methods for Planners I', 'This course develops logical, empirically based arguments using statistical techniques and analytic methods.', 'This course develops logical, empirically based arguments using statistical techniques and analytic methods. Elementary statistics, probability, and other types of quantitative reasoning useful for description, estimation, comparison, and explanation are covered. Emphasis is on the use and limitations of analytical techniques in planning practice.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-220-quantitative-reasoning-statistical-methods-for-planners-i-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-220-quantitative-reasoning-statistical-methods-for-planners-i-spring-2009/11-220s09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(599, 'Argumentation and Communication', 'This Communication and Argumentation seminar is an intensive writing workshop that focuses on argumentation and communication.', 'This Communication and Argumentation seminar is an intensive writing workshop that focuses on argumentation and communication. Students learn to write and present their ideas in cogent, persuasive arguments and other analytical frameworks. Reading and writing assignments and other exercises stress the connections between clear thinking, critical reading, and effective writing.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-225-argumentation-and-communication-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-225-argumentation-and-communication-fall-2006/11-225f06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(600, 'Research Design for Policy Analysis and Planning', 'This course develops skills in research design for policy analysis and planning.', 'This course develops skills in research design for policy analysis and planning. The emphasis is on the logic of the research process and its constituent elements. The course relies on a seminar format so students are expected to read all of the assigned materials and come to class prepared to discuss key themes, ideas, and controversies. Since the materials draw broadly on the social sciences, and since students have diverse interests and methodological preferences, ongoing themes in our discussions will be linking concepts to planning scholarship in general and considering how different epistemological orientations and methodological techniques map on to planning specializations.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-233-research-design-for-policy-analysis-and-planning-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-233-research-design-for-policy-analysis-and-planning-fall-2007/11-233f07.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(601, 'Crafting Research Questions and Qualitative Methodology', 'This course covers approaches to research and evaluation in the planning field, for those preparing to write 1st-year doctoral and other research papers.', 'This course covers approaches to research and evaluation in the planning field, for those preparing to write 1st-year doctoral and other research papers. Topics include narrowing down research interests, using quantitative and qualitative techniques complementarily, and interviewing and other fieldwork challenges. The course uses a seminar-type format in which readings, class discussions, and assignments are built around (1) generic themes that run across the research interests and paper topics of students in the class, and (2) lessons about methodology to be learned from the case comparison studies assigned.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-233-crafting-research-questions-and-qualitative-methodology-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-233-crafting-research-questions-and-qualitative-methodology-fall-2005/11-233f05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(602, 'Analyzing Projects and Organizations', 'This course teaches students how to understand the rationality behind how organizations and their programs behave, and to be comfortable and analytical with a live organization.', 'This course teaches students how to understand the rationality behind how organizations and their programs behave, and to be comfortable and analytical with a live organization. It thereby builds analytic skills for evaluating programs and projects, organizations, and environments. It draws on the literature of the sociology of organizations, political science, public administration, and historical experience-and is based on both developing-country and developed-country experience.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-235-analyzing-projects-and-organizations-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-235-analyzing-projects-and-organizations-fall-2009/11-235f09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(603, 'Information Technology and the Labor Market', 'This course explores how information technology is reshaping different dimensions of the U.', 'This course explores how information technology is reshaping different dimensions of the U.S. labor market: the way work is organized, the mix of occupations, the skills required to perform in an occupation, economy-wide labor productivity, and the distribution of wages.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-128-information-technology-and-the-labor-market-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-128-information-technology-and-the-labor-market-spring-2005/11-128s05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(604, 'Economics of Education', 'This class discusses the economic aspects of current issues in education, using both economic theory and econometric and institutional readings.', 'This class discusses the economic aspects of current issues in education, using both economic theory and econometric and institutional readings. Topics include discussion of basic human capital theory, the growing impact of education on earnings and earnings inequality, statistical issues in determining the true rate of return to education, the labor market for teachers, implications of the impact of computers on the demand for worker skills, the effectiveness of mid-career training for adult workers, the roles of school choice, charter schools, state standards and educational technology in improving K-12 education, and the issue of college financial aid.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-126j-economics-of-education-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-126j-economics-of-education-spring-2007/11-126js07.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(605, 'Computer Games and Simulations for Investigation and Education', 'In this project-based course, students from all disciplines are encouraged to understand how we learn from interactive computer environments, and delve into the process of designing and understanding simulations and games for learning.', 'In this project-based course, students from all disciplines are encouraged to understand how we learn from interactive computer environments, and delve into the process of designing and understanding simulations and games for learning.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-127j-computer-games-and-simulations-for-investigation-and-education-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-127j-computer-games-and-simulations-for-investigation-and-education-spring-2009/11-127js09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(606, 'Organizations and Environments', 'The goal of this doctoral course is to familiarize students with major conceptual frameworks, debates, and developments in contemporary organization theory.', 'The goal of this doctoral course is to familiarize students with major conceptual frameworks, debates, and developments in contemporary organization theory. This is an inter-disciplinary domain of inquiry drawing primarily from sociology, and secondarily from economics, psychology, anthropology, and political science. The course focuses on inter-organizational processes, and also addresses the economic, institutional and cultural contexts that organizations must face. This is an introduction to a vast and multifaceted domain of inquiry. Due to time limitations, this course will touch lightly on many important topics, and neglect others entirely; its design resembles more a map than an encyclopedia. Also, given the focus on theoretical matters, methodological issues will move to the background. Empirical material will be used to illustrate how knowledge is produced from a particular standpoint and trying to answer particular questions, leaving the bulk of the discussion on quantitative and qualitative procedures to seminars such as 15.347, 15.348, and the like.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-342j-organizations-and-environments-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-342j-organizations-and-environments-fall-2004/15-342jf04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(607, 'Introduction to Urban Design and Development', 'This course examines both the structure of cities and ways they can be changed.', 'This course examines both the structure of cities and ways they can be changed. Its scope includes historical forces that have produced cities, models of urban analysis, contemporary theories of urban design, and implementation strategies. Core lectures are supplemented by discussion sessions focusing on student work and field trips. Guest speakers present cases involving current projects illustrating the scope and methods of urban design practice.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-301j-introduction-to-urban-design-and-development-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-301j-introduction-to-urban-design-and-development-fall-2006/11-301jf06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(608, 'Urban Design Politics', 'This is a seminar about the ways that urban design contributes to the distribution of political power and resources in cities.', 'This is a seminar about the ways that urban design contributes to the distribution of political power and resources in cities. &quot;Design,&quot; in this view, is not some value-neutral aesthetic applied to efforts at urban development but is, instead, an integral part of the motives driving that development. The class investigates the nature of the relations between built form and political purposes through close examination of a wide variety of situations where public and private sector design commissions and planning processes have been clearly motivated by political pressures, as well as situations where the political assumptions have remained more tacit. We will explore cases from both developed and developing countries.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-302j-urban-design-politics-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-302j-urban-design-politics-spring-2010/11-302js10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(609, 'Beijing Urban Design Studio', 'In 2008, the Beijing Urban Design Studio will focus on the issue of Beijing''s urban transformation under the theme of de-industrialization, by preparing an urban design and development plan for the Shougang (Capital Steel Factory) site.', 'In 2008, the Beijing Urban Design Studio will focus on the issue of Beijing''s urban transformation under the theme of de-industrialization, by preparing an urban design and development plan for the Shougang (Capital Steel Factory) site. This studio will address whether portions of the old massive factory infrastructure can be preserved as a national industrial heritage site embedded into future new development; how to balance the cultural and recreational value of the site with environmental challenges; as well as how to use the site for urban development. A special focus of the studio will be to consider development approaches that minimize energy utilization. To research these questions, students will be asked to interact with clients from the factory, local residents, city officials and experts on transportation, environment, energy and real estate. They will assess strategic options for the steel factory and propose comprehensive plans for the design and development of the brownfield site.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-307-beijing-urban-design-studio-summer-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-307-beijing-urban-design-studio-summer-2008/11-307su08.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(610, 'Beijing Urban Design Studio', 'This is the 20th anniversary of the Beijing Urban Design Studio, which is a joint program between the MIT and Tsinghua University Schools of Architecture and Planning.', 'This is the 20th anniversary of the Beijing Urban Design Studio, which is a joint program between the MIT and Tsinghua University Schools of Architecture and Planning. The goal of the studio is to foster international cooperation through the undertaking of a joint urban design and planning initiative in the city of Beijing involving important, often controversial, sites and projects. Since 1995, almost 250 MIT and Tsinghua University students and faculty have participated in this annual studio, making it one of the most successful and enduring international academic programs between China and the U.S. It has received the Irwin Sizer Award from MIT for outstanding innovation in education. The studio takes place over five weeks in June and July including several weeks in residence at Tsinghua University and two brief study tours to locations and projects that inform the work. It will include 18-20 MIT and 10-15 Tsinghua Architecture and Planning students. The Beijing City Planning Institute, responsible for strategic planning in the city, participates in the studio as the client.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-307-beijing-urban-design-studio-summer-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-307-beijing-urban-design-studio-summer-2006/11-307su06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(611, 'Advanced Seminar: Urban Nature and City Design', 'This course explores the urban environment as a natural phenomenon, human habitat, medium of expression, and forum for action.', 'This course explores the urban environment as a natural phenomenon, human habitat, medium of expression, and forum for action. The course has several major themes: how ideas of nature influence the way cities are perceived, designed, built, and managed; how natural processes and urban form interact and the consequences for human health and welfare; how planners and designers can shape the urban natural environment. Each student researches and presents a case, either historical or an example of contemporary theory and practice.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-308j-advanced-seminar-urban-nature-and-city-design-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-308j-advanced-seminar-urban-nature-and-city-design-fall-2005/11-308jf05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(612, 'Sites in Sight: Photography as Inquiry', 'This course explores photography as a disciplined way of seeing, of investigating landscapes and expressing ideas.', 'This course explores photography as a disciplined way of seeing, of investigating landscapes and expressing ideas. Readings, observations, and photographs form the basis of discussions on landscape, light, significant detail, place, poetics, narrative, and how photography can inform design and planning, among other issues. The class website can be found here: Sites in Sight: Photography as Inquiry.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-309j-sites-in-sight-photography-as-inquiry-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-309j-sites-in-sight-photography-as-inquiry-fall-2003/11-309jf03.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(613, 'Media Technology and City Design and Development', 'This workshop explores the potential of media technology and the Internet to enhance communication and transform city design and community development in inner-city neighborhoods.', 'This workshop explores the potential of media technology and the Internet to enhance communication and transform city design and community development in inner-city neighborhoods. The class introduces a variety of methods for describing or representing a place and its residents, for simulating actions and changes, for presenting visions of the future, and for engaging multiple actors in the process of envisioning change and guiding action. Students will engage one neighborhood, meet real people working on real projects, put theory into practice, and reflect on insights gained in the process. This year the course will examine what it means to be an urban designer/planner and how to create a digital teaching tool (using digital storytelling) that supports others in learning about the relationship between design and planning professionals, on the one hand, and members of the communities they serve, on the other. What is the nature of the knowledge that resides in a community and how can designers and planners learn about, tap, and use that knowledge? What is the relationship between community organizing and urban design and planning? What are the relationships between you as a professional, the place(s) in which you work, and the values and care you bring to that work? We will explore these themes in the context of Camfield Estates in Lower Roxbury, MA and its participation in the US Department of Housing and Urban Development''s (HUD) Demonstration Disposition Project. There have been many stories written about Camfield Estates'' participation in the Demonstration Disposition project, for it has been widely regarded as a model of success. There are two stories that have not yet been told, however: the story of the residents who organized the community and the story of the architects and planners who participated in the project. This course will use digital storytelling to reconstruct and connect these two stories.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-310j-media-technology-and-city-design-and-development-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-310j-media-technology-and-city-design-and-development-fall-2002/11-310jf02.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(614, 'Urban Design Skills: Observing, Interpreting, and Representing the City', 'The course is designed to be an introduction to methods of analyzing, evaluating, and recording the urban environment first hand.', 'The course is designed to be an introduction to methods of analyzing, evaluating, and recording the urban environment first hand. Its aim is to supplement existing courses that cover theory and history of city design and planning and to better prepare students without prior design background for the studio sequence.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-328j-urban-design-skills-observing-interpreting-and-representing-the-city-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-328j-urban-design-skills-observing-interpreting-and-representing-the-city-fall-2004/11-328jf04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(615, 'Theory of City Form', 'Theories about cities and the form that settlements should take will be discussed.', 'Theories about cities and the form that settlements should take will be discussed. Attempts will be made at a distinction between descriptive and normative theory, by examining examples of various theories of city form over time. The class will concentrate on the origins of the modern city and theories about its emerging form, including the transformation of the nineteenth-century city and its organization. It analyzes current issues of city form in relation to city making, social structure, and physical design. Case studies of several cities will be presented as examples of the theories discussed in the class.', 'http://ocw.mit.edu/courses/architecture/4-241j-theory-of-city-form-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-241j-theory-of-city-form-spring-2004/4-241js04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(616, 'Urban Design Studio: Providence', 'This studio discusses in great detail the design of urban environments, specifically in Providence, RI.', 'This studio discusses in great detail the design of urban environments, specifically in Providence, RI. It will propose strategies for change in large areas of cities, to be developed over time, involving different actors. Fitting forms into natural, man-made, historical, and cultural contexts; enabling desirable activity patterns; conceptualizing built form; providing infrastructure and service systems; guiding the sensory character of development: all are topics covered in the studio.&nbsp;The course&nbsp;integrates architecture and planning students in joint work and requires individual designs and planning guidelines as a final product.', 'http://ocw.mit.edu/courses/architecture/4-163j-urban-design-studio-providence-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/architecture/4-163j-urban-design-studio-providence-spring-2005/4-163js05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(617, 'Urban Design', 'For many years, Cambridge, MA, as host to two major research universities, has been the scene of debates as to how best to meet the competing expectations of different stakeholders.', 'For many years, Cambridge, MA, as host to two major research universities, has been the scene of debates as to how best to meet the competing expectations of different stakeholders. Where there has been success, it has frequently been the result, at least in part, of inventive urban design proposals and the design and implementation of new institutional arrangements to accomplish those proposals. Where there has been failure it has often been explained by the inability - or unwillingness - of one stakeholder to accept and accommodate the expectations of another. The two most recent fall Urban Design Studios have examined these issues at a larger scale. In 2001 we looked at the possible patterns for growth and change in Cambridge, UK, as triggered by the plans of Cambridge University. And in 2002 we looked at these same issues along the length of the MIT ''frontier'' in Cambridge, MA as they related to the development of MIT and the biotech research industry. In the fall 2003 Urban Design Studio we propose to focus in on an area adjacent to Cambridgeport and the western end of the MIT campus, roughly centered on Fort Washington. Our goal is to discover the ways in which good urban form, an apt mix of activities, and effective institutional mechanisms might all be brought together in ways that respect shared expectations and reconcile competing expectations - perhaps in unexpected and adroit ways.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-332j-urban-design-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-332j-urban-design-fall-2003/11-332jf03.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(618, 'Urban Design Policy and Action', 'In this course we examine the relationship between public policy and urban design through readings, discussions, presentations, and papers.', 'In this course we examine the relationship between public policy and urban design through readings, discussions, presentations, and papers. We also analyze the ways in which policies shape cities, and investigate how governments implement urban design. Students gain a critical understanding of both the complex system of governance within which urban design occurs and the effective tools available for creative intervention.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-337j-urban-design-policy-and-action-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-337j-urban-design-policy-and-action-spring-2009/11-337js09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(619, 'Urban Design Policy and Action', 'Governments at every level assume a measure of responsibility for seeking good design.', 'Governments at every level assume a measure of responsibility for seeking good design. Some of that responsibility is exercised directly&mdash;through the design and construction of government buildings, for example. But most changes to our environments are neither designed nor built by governments. Rather, they are the result of the actions and investments of private individuals, institutions, corporations, joint ventures, or private/public collaborations. Yet, the actions of all of these actors are affected by the design policies of government and the interventions that are undertaken to implement those policies. In this advanced graduate-level seminar we will explore new ways of thinking about urban design policy in an attempt to better understand just what government does&mdash;and what it can do effectively&mdash;in the realm of design policy.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-337j-urban-design-policy-and-action-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-337j-urban-design-policy-and-action-spring-2007/11-337js07.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(620, 'The Impact of Globalization on the Built Environment', 'The course is designed to provide a better understanding of the built environment, globalization, the current financial crisis and the impact of these factors on the rapidly changing and evolving international architecture, engineering, construction fields.', 'The course is designed to provide a better understanding of the built environment, globalization, the current financial crisis and the impact of these factors on the rapidly changing and evolving international architecture, engineering, construction fields. We will, hopefully, obtain a better understanding of how these forces of globalization and the current financial crisis are having an impact on the built environment and how they will affect firms and your future career opportunities. We will also identify, review and discuss best practices and lessons that can be learned from recent events. We will explore the &quot;international built environment&quot; in detail, examining how it functions and asking what are the managerial, entrepreneurial and professional opportunities, challenges and risks in it, especially growing crossover and multi-disciplinary opportunities; and we will seek to understand what makes this &quot;built environment&quot; so different from other sectors.', 'http://ocw.mit.edu/courses/civil-and-environmental-engineering/1-463j-the-impact-of-globalization-on-the-built-environment-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/civil-and-environmental-engineering/1-463j-the-impact-of-globalization-on-the-built-environment-fall-2009/1-463jf09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(621, 'Community Growth and Land Use Planning', 'This course combines a seminar format with fieldwork to examine strategies of planning and control for growth and land use, chiefly at the municipal level.', 'This course combines a seminar format with fieldwork to examine strategies of planning and control for growth and land use, chiefly at the municipal level. Specific topics include growth and its local consequences; land use planning approaches; and implementation tools including innovative zoning and regulatory techniques, physical design, and natural systems integration. Projects are arranged with small teams serving municipal clients.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-360-community-growth-and-land-use-planning-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-360-community-growth-and-land-use-planning-fall-2006/11-360f06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(622, 'Community Growth and Land Use Planning', 'This course combines a seminar format with fieldwork to examine strategies of planning and control for growth and land use, chiefly at the municipal level.', 'This course combines a seminar format with fieldwork to examine strategies of planning and control for growth and land use, chiefly at the municipal level. Specific topics include growth and its local consequences; land use planning approaches; and implementation tools including innovative zoning and regulatory techniques, physical design, and natural systems integration. Projects are arranged with small teams serving municipal clients.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-360-community-growth-and-land-use-planning-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-360-community-growth-and-land-use-planning-fall-2005/11-360f05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(623, 'Community Growth and Land Use Planning', 'This subject explores the techniques, processes, and personal and professional skills required to effectively manage growth and land use change.', 'This subject explores the techniques, processes, and personal and professional skills required to effectively manage growth and land use change. While primarily focused on the planning practice in the United States, the principles and techniques reviewed and presented may have international application. This course is not for bystanders; it is designed for those who wish to become actively involved or exposed to the planning discipline and profession as it is practiced today, and as it may need to be practiced in the future.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-360-community-growth-and-land-use-planning-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-360-community-growth-and-land-use-planning-fall-2003/11-360f03.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(624, 'Civil Society and the Environment', 'This graduate seminar examines civic engagement in international, national and local environmental governance.', 'This graduate seminar examines civic engagement in international, national and local environmental governance. We will consider theories pertaining to civil society development, social movement mobilization, and the relations that nongovernmental organizations (NGOs) have with governments and corporations. During the course of the semester, particular attention will be given to the legitimacy and accountability of NGOs. Case studies of NGO and community responses to specific environmental issues will be used to illustrate theoretical issues and assess the impacts that these actors have on environmental policy and planning.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-363-civil-society-and-the-environment-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-363-civil-society-and-the-environment-spring-2005/11-363s05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(625, 'International Environmental Negotiation', 'This seminar will explore the difficulties of getting agreement on global definitions of sustainability; in particularly building international support for efforts to combat climate change created by greenhouse gas emissions as well as other international resource management efforts.', 'This seminar will explore the difficulties of getting agreement on global definitions of sustainability; in particularly building international support for efforts to combat climate change created by greenhouse gas emissions as well as other international resource management efforts. We will focus on possible changes in the way global environmental agreements are formulated and implemented, especially on ways of shifting from the current &quot;pollution control&quot; approach to combating climate change to a more comprehensive strategy for taking advantage of sustainable development opportunities.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-364-international-environmental-negotiation-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-364-international-environmental-negotiation-fall-2010/11-364f10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(626, 'Planning for Sustainable Development', 'This course explores policy and planning for sustainable development.', 'This course explores policy and planning for sustainable development. It critically examines concept of sustainability as a process of social, organizational, and political development drawing on cases from the U.S. and Europe. It also explores pathways to sustainability through debates on ecological modernization; sustainable technology development, international and intergenerational fairness, and democratic governance.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-366j-planning-for-sustainable-development-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-366j-planning-for-sustainable-development-spring-2006/11-366js06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(627, 'Brownfields Policy and Practice', 'There are several hundred thousand Brownfield sites across the country.', 'There are several hundred thousand Brownfield sites across the country. The large number of sites, combined with how a majority of these properties are located in urban and historically underserved communities, dictate that redevelopment of these sites stands to be a common theme in urban planning for the foreseeable future. Students form a grounded understanding of the Brownfield lifecycle: how and why they were created, their potential role in community revitalization, and the general processes governing their redevelopment. Using case studies and guest speakers from the public, private and non-profit sectors, students develop and hone skills to effectively address the problems posed by these inactive sites.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-370-brownfields-policy-and-practice-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-370-brownfields-policy-and-practice-fall-2005/11-370f05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(628, 'Introduction to Sustainable Energy', 'This class assesses current and potential future energy systems, covering resources, extraction, conversion, and end-use technologies, with emphasis on meeting regional and global energy needs in the 21st century in a sustainable manner.', 'This class assesses current and potential future energy systems, covering resources, extraction, conversion, and end-use technologies, with emphasis on meeting regional and global energy needs in the 21st century in a sustainable manner. Instructors and guest lecturers will examine various renewable and conventional energy production technologies, energy end-use practices and alternatives, and consumption practices in different countries. Students will learn a quantitative framework to aid in evaluation and analysis of energy technology system proposals in the context of engineering, political, social, economic, and environmental goals. Students taking the graduate version, Sustainable Energy, complete additional assignments.', 'http://ocw.mit.edu/courses/nuclear-engineering/22-081j-introduction-to-sustainable-energy-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/nuclear-engineering/22-081j-introduction-to-sustainable-energy-fall-2010/22-081jf10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(629, 'Science, Politics, and Environmental Policy', 'This class examines the role of science in the US environmental policy-making process.', 'This class examines the role of science in the US environmental policy-making process. It investigates the methods scientists use to learn about the natural world, the way scientific knowledge accumulates, the treatment of science by advocates and the media, and the role of science in legislative, administrative and judicial decision making. It also considers how other political systems use science in an effort to put the US approach in comparative perspective.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-373-science-politics-and-environmental-policy-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-373-science-politics-and-environmental-policy-fall-2004/11-373f04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(630, 'Urban Transportation Planning', 'This class is an introduction to planning transportation in metropolitan areas.', 'This class is an introduction to planning transportation in metropolitan areas. The approach, while rooted on the analytical tools which estimate outcomes and&#160;alternatives,&#160;is holistic. This means starting from a scan of the site, its history and its current trends, in order to frame properly the problem, including the relevant actors, institutions, roles and interests. The design and evaluation of alternatives considers this complexity, in addition to construction, operation and maintenance issues.&#160; The decision-making&#160;and implementation process, including the needed feedback mechanisms, focuses as well on the need to build constituencies and alliances. The course topics include the history of urban transportation, highway finance, environmental and planning regulations, air quality, modal characteristics, land use and transportation interaction and emerging information technologies for transportation planning. Students either with a primary or peripheral interest in transportation are equally welcome. The course uses examples from the Boston metropolitan area extensively, both because of its proximity and the strong influence Boston has had on US transport policy. In parallel, examples from other countries describe the challenges faced elsewhere, as well as lessons learned. There will be walking tours of several transportation sites in Boston.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-380j-urban-transportation-planning-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-380j-urban-transportation-planning-fall-2002/11-380jf02.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(631, 'Introduction to Housing, Community and Economic Development', 'This class explores how public policy and private markets affect housing, economic development, and the local economy.', 'This class explores how public policy and private markets affect housing, economic development, and the local economy. It provides an overview of techniques and specified programs, policies, and strategies that are (and have been) directed at neighborhood development.&nbsp;It gives students an opportunity to reflect on their personal sense of the housing and community development process. And it emphasizes the institutional context within which public and private actions are undertaken.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-401-introduction-to-housing-community-and-economic-development-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-401-introduction-to-housing-community-and-economic-development-fall-2004/11-401f04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(632, 'Introduction to Housing, Community and Economic Development', 'As an introduction to the field of Housing, Community, and Economic Development (HCED), the course is structured to: Advance student''s understanding of how public policy and private markets affect housing, economic development, the local economy, and neighborhood institutions; Provide an overview of techniques for framing public and private interventions to meet housing and community development agendas, broadly defined, of inner city and low income neighborhoods; Review and critique specific programs, policies and strategies that are (and have been) directed at local development and neighborhood regeneration issues; Give students an opportunity to reflect on their personal sense of the &quot;housing, community, and economic development&quot; process and the various roles that planners play in implementing the elements of that agenda.', 'As an introduction to the field of Housing, Community, and Economic Development (HCED), the course is structured to: Advance student''s understanding of how public policy and private markets affect housing, economic development, the local economy, and neighborhood institutions; Provide an overview of techniques for framing public and private interventions to meet housing and community development agendas, broadly defined, of inner city and low income neighborhoods; Review and critique specific programs, policies and strategies that are (and have been) directed at local development and neighborhood regeneration issues; Give students an opportunity to reflect on their personal sense of the &quot;housing, community, and economic development&quot; process and the various roles that planners play in implementing the elements of that agenda.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-401-introduction-to-housing-community-and-economic-development-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-401-introduction-to-housing-community-and-economic-development-fall-2003/11-401f03.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(633, 'Housing and Human Services', 'This class focuses on how the housing and human service systems interact: how networks and social capital can build between elements of the two systems.', 'This class focuses on how the housing and human service systems interact: how networks and social capital can build between elements of the two systems. It explores ways in which the differing world views, professional perspectives, and institutional needs of the two systems play out operationally. Part I establishes the nature of the action frames of these two systems. Part II applies these insights to particular vulnerable groups: ''at risk'' households in transitional housing, the chronically mentally ill, and the frail elderly.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-421-housing-and-human-services-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-421-housing-and-human-services-spring-2005/11-421s05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(634, 'Downtown Management Organizations', 'This course focuses on the origins, functions, and implications of downtown management organizations (DMOs), such as business improvement districts, in a variety of national contexts including the United States, Canada, South Africa, and the United Kingdom.', 'This course focuses on the origins, functions, and implications of downtown management organizations (DMOs), such as business improvement districts, in a variety of national contexts including the United States, Canada, South Africa, and the United Kingdom. It critically examines how a range of urban theories provide a rationale for the establishment and design of DMOs; the evolution and transnational transfer of DMO policy; and the spatial and political externalities associated with the local proliferation of DMOs. Particular emphasis is given to the role of DMOs in securing public space.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-422-downtown-management-organizations-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-422-downtown-management-organizations-fall-2006/11-422f06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(635, 'Information and Communication Technologies in Community Development', 'This practicum subject integrates theory and practice through the design, implementation, and evaluation of a comprehensive community information infrastructure that promotes democratic involvement and informs community development projects.', 'This practicum subject integrates theory and practice through the design, implementation, and evaluation of a comprehensive community information infrastructure that promotes democratic involvement and informs community development projects. Students work with Lawrence Community Works, Inc. to involve constituents and generate solutions to an important planning problem in the City of Lawrence, Massachusetts. Final project presentations take place in a public forum, and serve to inform future development of the information infrastructure. Subject begins with an overview of the digital divide, e-government, public participation GIS, and neighborhood information systems. Subject includes a reflection component and a deliberate investigation of race, class, and gender dynamics.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-423-information-and-communication-technologies-in-community-development-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-423-information-and-communication-technologies-in-community-development-spring-2004/11-423s04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(636, 'Real Estate Capital Markets', 'This half-semester course introduces and surveys the major public capital market real estate vehicles, REITs and MBS (with primary emphasis on CMBS).', 'This half-semester course introduces and surveys the major public capital market real estate vehicles, REITs and MBS (with primary emphasis on CMBS). Some background is also included in basic modern portfolio theory and equilibrium asset pricing. This course is primarily designed to provide MSRED students with a basic introduction to the public capital market sources of financial capital for real estate, and how those markets value such capital investments.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-432j-real-estate-capital-markets-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-432j-real-estate-capital-markets-spring-2007/11-432js07.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(637, 'Real Estate Economics', 'This course, offered by the MIT Center for Real Estate, focuses on developing an understanding of the macroeconomic factors that shape and influence markets for real property.', 'This course, offered by the MIT Center for Real Estate, focuses on developing an understanding of the macroeconomic factors that shape and influence markets for real property. We will develop the theory of land markets and locational choice. The material covered includes studies of changing economic activities, demographic trends, transportation and local government behavior as they affect real estate.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-433j-real-estate-economics-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-433j-real-estate-economics-fall-2008/11-433jf08.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(638, 'Tools for Analysis: Design for Real Estate and Infrastructure Development', '', '', 'http://ocw.mit.edu/courses/engineering-systems-division/esd-712-tools-for-analysis-design-for-real-estate-and-infrastructure-development-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/engineering-systems-division/esd-712-tools-for-analysis-design-for-real-estate-and-infrastructure-development-spring-2010/esd-712s10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(639, 'Financing Economic Development', 'This course focuses on financing tools and program models to support local economic development.', 'This course focuses on financing tools and program models to support local economic development. It includes an overview of private capital markets and financing sources to understand capital market imperfections that constrain economic development; business accounting; financial statement analysis; federal economic development programs; and public finance tools. Program models covered include revolving loan funds, guarantee programs, venture capital funds, bank holding companies, community development loan funds and credit unions, micro enterprise funds, and the use of the Community Reinvestment Act to leverage bank financing.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-437-financing-economic-development-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-437-financing-economic-development-spring-2006/11-437s06.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(640, 'Revitalizing Urban Main Streets: St. Claude Avenue, New Orleans', 'This course focuses on the physical and economic renewal of urban neighborhood Main Streets by combining classroom work with an applied class project.', 'This course focuses on the physical and economic renewal of urban neighborhood Main Streets by combining classroom work with an applied class project. The course content covers four broad areas: An overview of the causes for urban business district decline, the challenges faced in revitalization and the type of revitalization strategies employed; The physical and economic development planning tools used to understand and assess urban Main Streets from physical design and economic development perspectives; The policies, interventions, and investments used to foster urban commercial revitalization; and The formulation of a revitalization plan for an urban commercial district.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-439-revitalizing-urban-main-streets-st-claude-avenue-new-orleans-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-439-revitalizing-urban-main-streets-st-claude-avenue-new-orleans-spring-2009/11-439s09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(641, 'Revitalizing Urban Main Streets: Hyde/Jackson Square & Roslindale Square, Boston', 'This course focuses on the physical and economic renewal of urban neighborhood Main Streets by combining classroom work with an applied class project.', 'This course focuses on the physical and economic renewal of urban neighborhood Main Streets by combining classroom work with an applied class project. The course content covers four broad areas:an overview of the causes for urban business district decline, the challenges faced in revitalization and the type of revitalization strategies employed;the physical and economic development planning tools used to understand and assess urban Main Streets from physical design and economic development perspectives;the policies, interventions, and investments used to foster urban commercial revitalization; andthe formulation of a revitalization plan for an urban commercial district.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-439-revitalizing-urban-main-streets-hyde-jackson-square-roslindale-square-boston-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-439-revitalizing-urban-main-streets-hyde-jackson-square-roslindale-square-boston-spring-2005/chp_gateways.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(642, 'Property Rights in Transition', 'This course examines the theories and policy debates over who can own real property, how to communicate and enforce property rights, and the range of liberties that they confer.', 'This course examines the theories and policy debates over who can own real property, how to communicate and enforce property rights, and the range of liberties that they confer. It explores alternative economic, political, and sociological perspectives of property rights and their policy and planning implications.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-467j-property-rights-in-transition-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-467j-property-rights-in-transition-spring-2005/11-467js05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(643, 'Urban Sociology in Theory and Practice', 'This course is intended to introduce graduate students to a set of core writings in the field of urban sociology.', 'This course is intended to introduce graduate students to a set of core writings in the field of urban sociology. Topics include the changing nature of community, social inequality, political power, socio-spatial change, technological change, and the relationship between the built environment and human behavior. We examine the key theoretical paradigms that have constituted the field since its founding, assess how and why they have changed over time, and discuss the implications of these paradigmatic shifts for urban scholarship, social policy and the planning practice.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-469-urban-sociology-in-theory-and-practice-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-469-urban-sociology-in-theory-and-practice-spring-2009/11-469s09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(644, 'Targeting the Poor: Local Economic Development in Developing Countries', 'This course treats public-sector policies, programs, and projects that attempt to increase employment through development-promoting measures in the economic realm, through support and regulation.', 'This course treats public-sector policies, programs, and projects that attempt to increase employment through development-promoting measures in the economic realm, through support and regulation. It discusses the types of initiatives, tasks, and environments that are most conducive to equitable outcomes, and emphasizes throughout the understandings gained about why certain initiatives work and others don&rsquo;t.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-471-targeting-the-poor-local-economic-development-in-developing-countries-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-471-targeting-the-poor-local-economic-development-in-developing-countries-spring-2010/11-471s10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(645, 'Water and Sanitation Infrastructure Planning in Developing Countries', 'This course examines the policy and planning for the provision of water supply and sanitation services in developing countries.', 'This course examines the policy and planning for the provision of water supply and sanitation services in developing countries. It reviews available technologies, but emphasizes the planning and policy process, including economic, social, environmental, and health issues. The course incorporates considerations of financing, pricing, institutional structure, consumer demand, and community participation in the planning process. And it evaluates policies and projects in case studies from Asia, Africa, Latin America, and Central and Eastern Europe.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-479-water-and-sanitation-infrastructure-planning-in-developing-countries-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-479-water-and-sanitation-infrastructure-planning-in-developing-countries-spring-2005/11-479s05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(646, 'Water and Sanitation Infrastructure in Developing Countries', 'This course deals with the principles of infrastructure planning in developing countries, with a focus on appropriate and sustainable technologies for water and sanitation.', 'This course deals with the principles of infrastructure planning in developing countries, with a focus on appropriate and sustainable technologies for water and sanitation. It also incorporates technical, socio-cultural, public health, and economic factors into the planning and design of water and sanitation systems. Upon completion, students will be able to plan simple, yet reliable, water supply and sanitation systems for developing countries that are compatible with local customs and available human and material resources. Graduate and upper division students from any department who are interested in international development at the grassroots level are encouraged to participate in this interdisciplinary subject. Acknowledgment This course was jointly developed by Earthea Nance and Susan Murcott in Spring 2006.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-479j-water-and-sanitation-infrastructure-in-developing-countries-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-479j-water-and-sanitation-infrastructure-in-developing-countries-spring-2007/11-479js07.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(647, 'Analyzing and Accounting for Regional Economic Growth', 'This course focuses on alternative ways in which the issues of growth, restructuring, innovation, knowledge, learning, and accounting and measurements can be examined, covering both industrialized and emerging countries.', 'This course focuses on alternative ways in which the issues of growth, restructuring, innovation, knowledge, learning, and accounting and measurements can be examined, covering both industrialized and emerging countries. We give special emphasis to recent transformations in regional economies throughout the world and to the implications these changes have for the theories and research methods used in spatial economic analyses. Readings will relate mainly to the United States, but we cover pertinent material on foreign countries in lectures.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-481j-analyzing-and-accounting-for-regional-economic-growth-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-481j-analyzing-and-accounting-for-regional-economic-growth-spring-2009/11-481js09.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(648, 'Regional Socioeconomic Impact Analyses and Modeling', 'The seminar is designed to provide advanced graduate students with a thorough understanding of selected regional economic theories and techniques and with experience in using alternative socioeconomic impact assessment models and related regional techniques on microcomputers.', 'The seminar is designed to provide advanced graduate students with a thorough understanding of selected regional economic theories and techniques and with experience in using alternative socioeconomic impact assessment models and related regional techniques on microcomputers. Discussions will be held on particular theoretical modeling and economic issues; linkages among theories, accounts, and policies; relationships between national and regional economic structures; and methods of adjusting and estimating regional input-output accounts and tables. Examples from the Boston area and other U.S. cities/regions will be used to illustrate points throughout the seminar. We will also examine how such models are used in other countries. New material on analyzing regional development issues will be covered.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-482j-regional-socioeconomic-impact-analyses-and-modeling-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-482j-regional-socioeconomic-impact-analyses-and-modeling-fall-2007/11-482jf07.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(649, 'Housing and Land Use in Rapidly Urbanizing Regions', 'A truly inter-disciplinary course, Housing and Land Use in Rapidly Urbanizing Regions reviews how law, economics, sociology, political science, and planning conceptualize urban land and property rights and uses cases to discuss what these different lenses illuminate and obscure.', 'A truly inter-disciplinary course, Housing and Land Use in Rapidly Urbanizing Regions reviews how law, economics, sociology, political science, and planning conceptualize urban land and property rights and uses cases to discuss what these different lenses illuminate and obscure. It also looks at how the social sciences might be informed by how design, cartography, and visual studies conceptualize space''s physicality. This year''s topics include land trusts for affordable housing, mixed-use in public space, and critical cartography.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-483-housing-and-land-use-in-rapidly-urbanizing-regions-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-483-housing-and-land-use-in-rapidly-urbanizing-regions-fall-2011/11-483f11.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(650, 'Project Appraisal in Developing Countries', 'This course covers techniques of financial analysis of investment expenditures as well as the economic and distributive appraisal of those projects.', 'This course covers techniques of financial analysis of investment expenditures as well as the economic and distributive appraisal of those projects. The course gives special consideration to cases in the developing world. Students will engage in a critical analysis of these tools and their role in the political economy of international development. The course will cover topics such as alternative planning strategies for conditions of uncertainty; organizations and project cycle management; the political environment; and interactions of clients and advisers, engineers, planners, policy analysts, and other professionals. Introductory micro-economics is a pre-requisite for this course.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-484-project-appraisal-in-developing-countries-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-484-project-appraisal-in-developing-countries-spring-2005/11-484s05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(651, 'Urban Public Finance in Developing Countries', 'This readings-based course analyzes the structure and operation of government systems in developing countries, with particular emphasis on regional and local governments.', 'This readings-based course analyzes the structure and operation of government systems in developing countries, with particular emphasis on regional and local governments. Major topics include: the role of decentralization in national economic reform programs, the potential impact of decentralized governments on local economic development, determination of optimal arrangements for sharing fiscal responsibilities among levels of government, evaluation of local revenue and expenditure decisions, and assessment of prospects and options for intergovernmental fiscal reform. Emphasis is on basic economic concerns, with consideration given to political, institutional, and cultural factors.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-487-urban-public-finance-in-developing-countries-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-487-urban-public-finance-in-developing-countries-fall-2004/11-487f04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(652, 'The Growth and Spatial Structure of Cities', 'This course examines the economic, political, social, and spatial dynamics of urban growth and decline in cities and their key component areas (downtown, suburbs, etc.', 'This course examines the economic, political, social, and spatial dynamics of urban growth and decline in cities and their key component areas (downtown, suburbs, etc.). Topics include impacts of industrialization, technology, politics, and social practices on cities. Students will examine the role of public and private sector activities, ranging from zoning and subsidies to infrastructure development and real estate investment, in affecting urban growth and decline. Readings are both theoretical and empirical, with considerable thought paid to comparative and historical differences.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-489-the-growth-and-spatial-structure-of-cities-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-489-the-growth-and-spatial-structure-of-cities-fall-2005/11-489f05.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(653, 'Economic Development, Policy Analysis, and Industrialization', 'This class analyzes the theoretical and historical reasons why governments in latecomer countries have intervened with a wide array of policies to foster industrial development at various turning points: the initiation of industrial activity; the diversification of the industrial base; the restructuring of major industrial institutions; and the entry into high-technology sectors.', 'This class analyzes the theoretical and historical reasons why governments in latecomer countries have intervened with a wide array of policies to foster industrial development at various turning points: the initiation of industrial activity; the diversification of the industrial base; the restructuring of major industrial institutions; and the entry into high-technology sectors.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-491j-economic-development-policy-analysis-and-industrialization-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-491j-economic-development-policy-analysis-and-industrialization-fall-2004/11-491jf04.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(654, 'Law, Social Movements, and Public Policy: Comparative and International Experience', 'The course introduces theoretical frameworks from legal and social movement theories as applied to court opinions, legislation, treaties, law-related articles, and policy-oriented materials and focuses on the impact of the relationship between courts and grassroots activism on current issues like trade, environmental regulation, and human rights enforcement.', 'The course introduces theoretical frameworks from legal and social movement theories as applied to court opinions, legislation, treaties, law-related articles, and policy-oriented materials and focuses on the impact of the relationship between courts and grassroots activism on current issues like trade, environmental regulation, and human rights enforcement. Students examine case studies of institutional processes including the World Trade Organization and the World Bank from key countries like the US and India.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-166-law-social-movements-and-public-policy-comparative-and-international-experience-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-166-law-social-movements-and-public-policy-comparative-and-international-experience-fall-2002/11-166f02.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(655, 'Human Rights in Theory and Practice', 'This course provides a rigorous and critical introduction to the foundation, structure and operation of the international human rights movement.', 'This course provides a rigorous and critical introduction to the foundation, structure and operation of the international human rights movement. It includes leading theoretical and institutional issues and the functioning of the international human rights mechanisms including non-governmental and inter-governmental ones. It covers cutting-edge human rights issues including gender and race discrimination, religion and state, national security and terrorism, globalization and human rights, and technology and human rights.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-164-human-rights-in-theory-and-practice-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-164-human-rights-in-theory-and-practice-fall-2010/11-164f10.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(656, 'Introduction to Technology and Cities', 'This seminar is an introduction to the usage and impacts of information and communication technologies (ICTs) on urban planning, the urban environment and communities.', 'This seminar is an introduction to the usage and impacts of information and communication technologies (ICTs) on urban planning, the urban environment and communities. Students will explore how social relationships, our sense of community, the urban infrastructure, and planning practice have been affected by technological change. Literature reviews, guest speakers, and web surfing will provide examples and issues that are debated in class and homework exercises. We will examine metropolitan information infrastructures, urban modeling and visualization, e-government, collaborative planning, and cyber communities. Students will attend a regular Tuesday seminar and occasional seminars of invited speakers during lunchtime on Fridays or Mondays. During the past two decades, ICTs have become so pervasive and disruptive that their impact on urban planning and social relationships has begun to reach far beyond their immediate use as efficient bookkeeping and automation tools. This seminar will examine ICT impacts on our sense of community, urban planning practice, the meaning of ''place'', and the nature of metropolitan governance. In each of the four areas, we will utilize readings, class discussion, guest lectures, and homework exercises to identify and critique key trends, relevant theories, and promising directions for research and professional practice.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-501-introduction-to-technology-and-cities-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-501-introduction-to-technology-and-cities-fall-2002/11-501f02.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(657, 'Spatial Database Management and Advanced Geographic Information Systems', 'This semester long subject (11.', 'This semester long subject (11.521) is divided into two halves. The first half focuses on learning spatial database management techniques and methods and the second half focuses on using these skills to address a ''real world,'' client-oriented planning problem. The first half of the semester may be taken separately using the class number 11.523 and the second half may be taken separately as 11.524. In order to help shape and utilize the information infrastructure that will support the management and development of our metropolitan areas, planners need a basic understanding of the tools and technology for querying, analyzing, and sharing complex databases and maps. Managing online access to large and constantly-changing spatial datasets can be a powerful aid to planning and can facilitate inter-agency cooperation and collaboration in an increasingly decentralized world. But it requires the use of knowledge representation methods, client-server technologies and access control issues that are quite different from what are needed to model and visualize standalone datasets on a personal computer. Hence, planners should acquire basic skills in database management, digital spatial data analysis, and networking. The 11.523 portion of the semester addresses these issues while retaining a focus on planning (rather than on computer science).&nbsp;This is an intensive, hands-on class that stresses learning by doing. Exercises and examples involving real-world data, maps, and images are used to develop skills with database query languages and the design development and use of structured databases. Class work utilizes web tools, GIS, and database software with lab exercises primarily on the new high-performance PC computing cluster. Specifically, we will access an Oracle 8i database using SQL (structured query language) and use ArcView for GIS. Each week there are two sixty to ninety-minute classes plus another 90+ minute hands-on lab in electronic classrooms. Class lectures will focus on concepts and case discussion, the scheduled lab time focuses on computer mechanics and skill building. Specific topics during 11.523 include: finding, understanding and structuring digital spatial data that are available on the Internet using various browsing, visualization, and data management tools; considerable work with relational database technologies and the Structured Query Language (SQL) to design, construct, query, and update urban planning databases; some experience with so-called ''client/server'' and ''enterprise GIS'' technologies for facilitating distributed access to complex spatial data and urban planning applications; advanced GIS topics such as 3D visualizations and geospatial web services. The 11.524 portion of the semester will treat the classroom like a professional planning office, working as a team to produce a two deliverables for their client, Lawrence Community Works, Inc. (LCW), a community development corporation located in the City of Lawrence, Massachusetts. LCW and DUSP recently agreed to work together for the next five years to design and implement a multi-tier web-based planning system that promotes democratic involvement and informs community development projects. Your involvement this semester is critical, because the implementation plan that you craft this semester will serve as the road map for both organizations for years to come and the simple web-based planning tool that you design will engage stakeholders by giving them a better sense of how technologies can aid decision-making processes. To assist you with the more technical aspects of the project, we hired Robert Cheetham, President of Azavea, Inc. (http://www.azavea.com/&nbsp;), to provide exactly 100 hours of consultancy services. Through their project work, students will enhance important professional skills by: formulating an implementation plan for a real client; designing a simple web-based tool for understanding problems; engaging constituents and stakeholders in a real setting; integrating theory and practice by evaluating the role of technology in community development; learning to communicate effectively within a group and with a professional consultant; working with such tools as the WWW, Access, ArcView, ArcIMS, SDE, etc. &nbsp;', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-521-spatial-database-management-and-advanced-geographic-information-systems-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-521-spatial-database-management-and-advanced-geographic-information-systems-spring-2003/11-521s03.jpg', 'Urban Studies and Planning', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(658, 'Research Seminar on Urban Information Systems', 'Seminar participants and invited guests will lead critical discussions of current literature and ongoing research.', 'Seminar participants and invited guests will lead critical discussions of current literature and ongoing research. Each student will be responsible for identifying, reviewing, and presenting one structured discussion of articles from the current literature that are relevant to their research topic. The remaining time will be spent working on individual projects or thesis proposals. This fall, the seminar will focus on the following core issues that underlie most implementations of urban information systems and decision support tools: the sustainable acquisition and representation of urban knowledge; the emergent technological infrastructure for supporting metropolitan decision-making; and the innovative organizational and institutional arrangements that can take advantage of modern urban information systems.ArcGIS/ArcMap/ArcInfo Graphical User Interface is the intellectual property of ESRI and is used herein with permission. Copyright &copy; ESRI. All rights reserved.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-522-research-seminar-on-urban-information-systems-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-522-research-seminar-on-urban-information-systems-fall-2005/11-522f05.jpg', 'Urban Studies and Planning', 'MIT');
