INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1552, 'Japanese I', 'This course covers Japanese: The Spoken Language Lessons 1 through 6, providing opportunities to acquire basic skills for conversation, reading and writing.', 'This course covers Japanese: The Spoken Language Lessons 1 through 6, providing opportunities to acquire basic skills for conversation, reading and writing. The program emphasizes active command of Japanese, not passive knowledge. The goal is not simply to study the grammar and vocabulary, but to improve the ability to use Japanese accurately and appropriately with fluency. Students will learn 56 Kanji characters in this course, as well as introducing Hiragana and Katakana.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-501-japanese-i-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-501-japanese-i-fall-2009/21f-501f04.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1553, 'Intermediate Japanese I', 'This course covers JSL (Japanese: the Spoken Language, Part 1, by Eleanor H.', 'This course covers JSL (Japanese: the Spoken Language, Part 1, by Eleanor H. Jorden with Mari Noda, Yale University Press, 1987) Lessons 12 through 17, providing opportunities to acquire basic skills for conversation, reading, and writing. The program emphasizes ACTIVE command of Japanese, not passive knowledge. The goal is not simply to study the grammar and vocabulary, but to improve the ability to use Japanese accurately and appropriately with fluency, building on the basic skills gained in Japanese I and II. Students learn approximately 80 Kanji characters in this course.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-503-intermediate-japanese-i-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-503-intermediate-japanese-i-fall-2004/21f-503f04.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1554, 'Japanese IV', 'This course covers Japanese: The Spoken Language lessons 17 through 22.', 'This course covers Japanese: The Spoken Language lessons 17 through 22. It will further develop the four basic skills, speaking, listening, reading and writing, that students have acquired through Japanese I, II and III courses, with emphasis on oral communication skills in various practical situations. Students will learn approximately 100 Kanji characters in this course. Sessions in English cover grammar explanation, socio-cultural information and other important issues for using the language, while Japanese lessons focus on the actual use of the language, integrating students'' prior knowledge with newly learned patterns, and communicating within the frame given in the class.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-504-japanese-iv-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-504-japanese-iv-spring-2009/21f-504s09.gif', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1555, 'Advanced Japanese I', 'This course covers lessons 22 through 27 of Japanese: The Spoken Language by Eleanor H.', 'This course covers lessons 22 through 27 of Japanese: The Spoken Language by Eleanor H. Jordan with Mari Noda. The goal of the course is to continue to build oral proficiency by expanding your knowledge of vocabulary and grammar. Class hours will be devoted to developing speaking skills in a variety of circumstances; making requests, invitations, apologies, suggestions, dealing with problems, expressing your opinions, etc. Grammatical and social appropriateness on your utterances will be stressed. Keep in mind that daily tape-work is essential.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-505-advanced-japanese-i-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-505-advanced-japanese-i-fall-2005/21f-505f05.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1556, 'Intro to Japanese Culture', 'This course examines the major aesthetic, social, and political elements which have shaped modern Japanese culture and society.', 'This course examines the major aesthetic, social, and political elements which have shaped modern Japanese culture and society. There are readings on contemporary Japan and historical evolution of the culture are coordinated with study of literary texts, film, and art, along with an analysis of everyday life and leisure activities.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-064-intro-to-japanese-culture-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-064-intro-to-japanese-culture-spring-2012/21f-064s12.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1557, 'Japanese Literature and Cinema', 'This course includes surveys for both cinematic and literary representations of diverse eras and aspects of Japanese culture such as the classical era, the samurai age, wartime Japan and the atomic bombings, social change in the postwar period, and the appropriation of foreign cultural themes, with an emphasis on the modern period.', 'This course includes surveys for both cinematic and literary representations of diverse eras and aspects of Japanese culture such as the classical era, the samurai age, wartime Japan and the atomic bombings, social change in the postwar period, and the appropriation of foreign cultural themes, with an emphasis on the modern period. The directors include Akira Kurosawa and Hiroshi Teshigahara. The authors include Kobo Abe and Yukio Mishima. The films are shown with subtitles in English. The course is taught in English.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-065-japanese-literature-and-cinema-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-065-japanese-literature-and-cinema-spring-2006/21f-065s06.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1558, 'Spanish I', 'Spanish I is very different from other classes at MIT.', 'Spanish I is very different from other classes at MIT. The central component of the text and workbook is a series of 26 half-hour video episodes. The videos allow students to learn authentic Spanish and experience its cultural diversity while following a good story full of surprises and human emotions. Students also listen to an audio-only program integrated with the text and workbook. In the classroom, students do a variety of activities and exercises, which include talking in Spanish about the video program, practicing pronunciation and grammar, and interacting in Spanish with classmates in pairs and small groups. The class is conducted in Spanish as much as possible, but English is used where necessary for clarity and efficiency. This course deals with all basic language skills: aural comprehension, speaking, reading, and writing. This class assumes no previous knowledge of Spanish.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-701-spanish-i-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-701-spanish-i-fall-2003/21f-701f03.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1559, 'Spanish III', 'This course is the first intermediate-level course in Spanish, with a focus on grammar review, additional vocabulary, writing of essays in Spanish and enhancement of cultural awareness.', 'This course is the first intermediate-level course in Spanish, with a focus on grammar review, additional vocabulary, writing of essays in Spanish and enhancement of cultural awareness. Group activities and projects, and conversation are emphasized. There are detailed simulation activities, readings about literature and art from Latin America and Spain, activities with music videos and interviews, and viewings of recent films such as El espinazo del diablo, Juana la loca, and Mar&iacute;a llena eres de gracia. Students also participate in the MITUPV Exchange project, a multimedia-centered Web site that deals with university life at MIT, the Universidad Polit&eacute;cnica de Valencia in Spain, and other universities.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-703-spanish-iii-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-703-spanish-iii-spring-2006/21f-703s06.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1560, 'Spanish IV', 'Spanish IV aims at developing and improving student''s oral and written communication through the continued study of the language, literature and culture of Spain, Latin America and Hispanic communities in the United States.', 'Spanish IV aims at developing and improving student''s oral and written communication through the continued study of the language, literature and culture of Spain, Latin America and Hispanic communities in the United States. It also seeks to improve students'' ability to read and appreciate literary and non-literary texts in Spanish, deepening this way students'' awareness and understanding of the cultural diversity of the Spanish-speaking world. The course is organized by themes based on contemporary social, political and cultural issues of Spanish-speaking societies such as: cultural identity, the changing roles of women and family, economic development and its effects on cultural heritage and environment, and the individual''s rights in the political system.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-704-spanish-iv-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-704-spanish-iv-spring-2005/21f-704s05.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1561, 'Oral Communication in Spanish', 'A second-year intermediate course that includes vocabulary enhancement and limited review of selected points of grammar.', 'A second-year intermediate course that includes vocabulary enhancement and limited review of selected points of grammar. Focuses on listening comprehension and speaking, with group activities, discussions and individual oral reports based on readings, films, music and art.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-705-oral-communication-in-spanish-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-705-oral-communication-in-spanish-spring-2004/21f-705s04.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1562, 'Spanish Conversation and Composition', 'A third-year intermediate course designed to improve speaking and writing, with opportunities for vocabulary acquisition, listening comprehension and reading practice as well.', 'A third-year intermediate course designed to improve speaking and writing, with opportunities for vocabulary acquisition, listening comprehension and reading practice as well. Uses literary and cultural readings, films, and group activities. Students give oral reports and participate in discussions and group projects.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-712-spanish-conversation-and-composition-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-712-spanish-conversation-and-composition-fall-2003/21f-712f03.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1563, 'Spanish for Bilingual Students', 'Spanish for Bilingual Students is an intermediate course designed principally for heritage learners, but which includes other students interested&nbsp; in specific content areas, such as US Latino immigration, identity, ethnicity, education and representation in the media.', 'Spanish for Bilingual Students is an intermediate course designed principally for heritage learners, but which includes other students interested&nbsp; in specific content areas, such as US Latino immigration, identity, ethnicity, education and representation in the media. Linguistic goals include vocabulary acquisition, improvement in writing, and enhancement of formal communicative skills.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-714-spanish-for-bilingual-students-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-714-spanish-for-bilingual-students-spring-2003/21f-714s03.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1564, 'Introduction to Contemporary Hispanic Literature', 'This course studies representative twentieth and twenty-first-century texts and films from Hispanic America and Spain.', 'This course studies representative twentieth and twenty-first-century texts and films from Hispanic America and Spain. Emphasis is on developing strategies for analyzing the genres of the novel, the short story, the poem, the fictional film, and the theatrical script. The novels read this semester are Magali Garc&iacute;a Ramis''s Felices d&iacute;as, T&iacute;o Sergio (1986, Puerto Rico) and Javier Cercas''s Soldados de Salamina (2001, Spain). We will study Lorca''s play ''La casa de Bernarda Alba'' (1936, Spain), films from Spain, M&eacute;xico, and Cuba, poems by Dar&iacute;o (Nicaragua), Machado (Spain), Lorca (Spain), Hern&aacute;ndez (Spain), Vallejo (Per&uacute;), Cernuda (Spain), and Luis Pal&eacute;s Matos (Puerto Rico), and short stories from M&eacute;xico (by an exiled Spanish writer), Chile, Argentina, and Cuba. Thematic emphasis is on the Spanish Civil War, changing attitudes toward gender, the Spanish-speaking Caribbean, and the history of race in the Americas.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-716-introduction-to-contemporary-hispanic-literature-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-716-introduction-to-contemporary-hispanic-literature-fall-2007/21f-716f07.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1565, 'Introduction to Spanish Culture', 'This course has several purposes.', 'This course has several purposes. The major concern will be the examination of Spanish culture including Spain''s history, architecture, art, literature and film, to determine if there is a uniquely Spanish manner of seeing and understanding the world - one which emerges as clearly distinct from our own and that of other Western European nations.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-717-introduction-to-spanish-culture-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-717-introduction-to-spanish-culture-fall-2004/21f-717f04.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1566, 'Twentieth and Twentyfirst-Century Spanish American Literature', 'Este semestre la materia combina obras ya canonizadas de finales del siglo XIX y del XX con algunas obras de reciente aparici&oacute;n.', 'Este semestre la materia combina obras ya canonizadas de finales del siglo XIX y del XX con algunas obras de reciente aparici&oacute;n. De los g&eacute;neros literarios, vemos poes&iacute;a, el cuento corto, la novela y la autobiograf&iacute;a. Tambi&eacute;n vemos una pel&iacute;cula de tema ficticio y dos documentales. El estudiante que se interese por hacerlo puede usar los materiales en la Reserva y en la red para familiarizarse con la historia literaria y cinematogr&aacute;fica de este per&iacute;odo. Nuestro enfoque principal, sin embargo, ser&aacute; leer bien y de modo anal&iacute;tico, textos literarios de diferentes momentos hist&oacute;ricos y culturas nacionales (e internacionales). Aunque discutiremos anal&iacute;ticamente las tres pel&iacute;culas, su inter&eacute;s principal para nosotros ser&aacute;, en un caso, conocer el argumento ideol&oacute;gico de una novela muy famosa e influyente que, por falta de tiempo, no vamos a poder leer en sus totalidad, y en los dos otros casos, para contextualizar una autobiograf&iacute;a y tener un punto de comparaci&oacute;n para estudiar c&oacute;mo se puede reconstruir la memoria simult&aacute;neamente individual y colectiva. Otros temas, que se estudiar&aacute;n en varias obras, ser&aacute;n el biling&uuml;ismo (espa&ntilde;ol/ingl&eacute;s) en ciertos escritores hispanoamericanos contempor&aacute;neos, y el auge de la novela best seller en la Am&eacute;rica Hispana.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-730-twentieth-and-twentyfirst-century-spanish-american-literature-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-730-twentieth-and-twentyfirst-century-spanish-american-literature-spring-2003/21f-730s03.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1567, 'The New Spain: 1977-Present', 'In this class we will come to understand the vast changes in Spanish life that have taken place since Franco''s death in 1975.', 'In this class we will come to understand the vast changes in Spanish life that have taken place since Franco''s death in 1975. We will focus on the new freedom from censorship, the re-emergence of movements for regional autonomy, the new cinema, reforms in education and changes in daily life: sex roles, work, and family that have occurred in the last decade. In so doing, we will examine myths that are often considered commonplaces when describing Spain and its people.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-740-the-new-spain-1977-present-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-740-the-new-spain-1977-present-spring-2010/21f-740f05.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1568, 'Spanish II', 'Spanish II continues to develop students'' listening, speaking, reading and writing skills using the second part of the&nbsp;video-based program, Destinos, begun in Spanish I.', 'Spanish II continues to develop students'' listening, speaking, reading and writing skills using the second part of the&nbsp;video-based program, Destinos, begun in Spanish I. Destinos is a soap opera that allows students to learn Spanish and experience its cultural diversity while following a good story full of surprises and human emotions. Spanish II also includes additional materials, such as Spanish films and other media, various types of reading selections and online resources.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-702-spanish-ii-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-702-spanish-ii-spring-2004/21f-702s04.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1569, 'Portuguese I', 'This course provides an introduction to the language and culture of the Portuguese-speaking world, with special attention to Brazilian Portuguese.', 'This course provides an introduction to the language and culture of the Portuguese-speaking world, with special attention to Brazilian Portuguese. This course focuses are on basic oral expression, listening comprehension, and elementary reading and writing. Students develop their vocabulary and understanding of grammatical concepts through active communication. This is a coordinated language lab program and is designed for students with no knowledge of Portuguese. Class is conducted entirely in Portuguese.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-801-portuguese-i-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-801-portuguese-i-fall-2011/21f-801f11.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1570, 'Portuguese II', 'This course is a continuation of 21F.', 'This course is a continuation of 21F.801. It focuses on expanding communication skills and further development of linguistic competency using a variety of authentic sources, such as the Internet, audio, video, and printed materials, to help develop cultural awareness and linguistic proficiency. This course is conducted entirely in Portuguese.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-802-portuguese-ii-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-802-portuguese-ii-spring-2012/21f-802s12.JPG', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1571, 'Portuguese II', 'This course is a continuation of 21F.', 'This course is a continuation of 21F.801. It focuses on expanding communication skills and further development of linguistic competency using a variety of authentic sources, such as the Internet, audio, video, and printed materials, to help develop cultural awareness and linguistic proficiency. This course is conducted entirely in Portuguese.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-802-portuguese-ii-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-802-portuguese-ii-spring-2012/21f-802s12.JPG', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1572, 'Smashing the Iron Rice Bowl: Chinese East Asia', 'This subject examines the experiences of ordinary Chinese people as they lived through the tumultuous changes of the nineteenth and twentieth centuries.', 'This subject examines the experiences of ordinary Chinese people as they lived through the tumultuous changes of the nineteenth and twentieth centuries. We look at personal narratives, primary sources, films alongside a textbook to think about how individual and family lives connect with the broader processes of change in modern China. In the readings and discussions, you should focus on how major political events have an impact on the characters'' daily lives, and how the decisions they make cause large-scale social transformation.', 'http://ocw.mit.edu/courses/history/21h-560-smashing-the-iron-rice-bowl-chinese-east-asia-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-560-smashing-the-iron-rice-bowl-chinese-east-asia-fall-2004/21h-560f04.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1573, 'Communicating Across Cultures', 'It has become commonplace knowledge that globalization is one of the major forces shaping our world.', 'It has become commonplace knowledge that globalization is one of the major forces shaping our world. If we look at the spread of information, ideas, capital, media, cultural artifacts - or for that matter, people - we can see the boundaries and borders that have historically separated one country or one group from another are becoming more and more permeable. For proof of this close to home, you need only to look at the composition of the MIT student body:&nbsp;8 percent of the undergraduates and 37 percent of the graduate students are from 109 different countries.&quot;Communicating Across Cultures&quot; is designed to help you meet the challenges of living in a world in which, increasingly, you will be asked to interact with people who may not be like you in fundamental ways. Its primary goals are to help you become more sensitive to intercultural communication differences, and to provide you with the knowledge and skills that will help you interact successfully with people from cultures other than your own. We hope the course will accomplish those goals by exposing you to some of the best writers and scholars on the subject of intercultural communication, and by giving you a variety of opportunities to practice intercultural communication yourself. As you read the syllabus for this course, we hope you get a sense of our commitment to making this course a rewarding experience for you.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-019-communicating-across-cultures-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-019-communicating-across-cultures-spring-2005/21f-019s05.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1574, 'Topics in Culture and Globalization: Reggae as Transnational Culture', 'This course considers reggae, or Jamaican popular music more generally&mdash;in its various forms (ska, rocksteady, roots, dancehall)&mdash;as constituted by international movements and exchanges and as a product that circulates globally in complex ways.', 'This course considers reggae, or Jamaican popular music more generally&mdash;in its various forms (ska, rocksteady, roots, dancehall)&mdash;as constituted by international movements and exchanges and as a product that circulates globally in complex ways. By reading across the reggae literature, as well as considering reggae texts themselves (songs, films, videos, and images), students will scrutinize the different interpretations of reggae''s significance and the implications of different interpretations of the story of Jamaica and its music. Beginning with a consideration of how Jamaica''s popular music industry emerged out of transnational exchanges, the course will proceed to focus on reggae''s circulation outside of Jamaica via diasporic networks and commercial mediascapes. Among other sites, we will consider reggae''s resonance and impact elsewhere in the Anglo Caribbean (e.g., Trinidad, Barbados), the United Kingdom (including British reggae styles but also such progeny as jungle, grime, and dubstep), the United States (both as reggae per se and in hip-hop), Panama and Puerto Rico and other Latin American locales (e.g., Brazil), Japan and Australia, as well as West, South, and East Africa (CÃ´te d''Ivoire, Tanzania, Uganda).', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-035-topics-in-culture-and-globalization-reggae-as-transnational-culture-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-035-topics-in-culture-and-globalization-reggae-as-transnational-culture-fall-2010/21f-035f10.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1575, 'Topics in Culture and Globalization', 'The concept of globalization fosters the understanding of the interconnectedness of cultures and societies geographically wide apart; America, Europe, Asia, and Africa.', 'The concept of globalization fosters the understanding of the interconnectedness of cultures and societies geographically wide apart; America, Europe, Asia, and Africa. Subject scans existing debates over globalization around the world. This course explores how globalization impacts everyday life in the First and Third World; how globalization leads to a common cosmopolitan culture; the emergence of a global youth culture; and religious, social, and political movements that challenge globalization. Materials examined include pop music, advertisements, film posters, and political cartoons.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-035-topics-in-culture-and-globalization-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-035-topics-in-culture-and-globalization-fall-2003/21f-035f03.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1576, 'Chinese I (Regular)', 'This subject is the first semester of two that form an introduction to modern standard Chinese, commonly called Mandarin.', 'This subject is the first semester of two that form an introduction to modern standard Chinese, commonly called Mandarin. Though not everyone taking this course will be an absolute beginner, the course presupposes no prior background in the language. The purpose of this course is to develop: Basic conversational abilities (pronunciation, fundamental grammatical patterns, common vocabulary, and standard usage) Basic reading and writing skills (in both the traditional character set and the simplified) An understanding of the language learning process so that you are able to continue studying effectively on your own. The main text is Wheatley, J. K. Learning Chinese: A Foundation Course in Mandarin. Part I. (unpublished, but available online). (Part II of the book forms the basis of 21F.102 / 152, which is also published on OpenCourseWare.) Chinese Sequence on OCW OpenCourseWare now offers a complete sequence of four Chinese language courses, covering beginning to intermediate levels of instruction at MIT. They can be used not just as the basis for taught courses, but also for self-instruction and elementary-to-intermediate review. The four Chinese subjects provide the following materials: an online textbook in four parts, J. K. Wheatley''s Learning Chinese: A Foundation Course in Mandarin; audio files of the main conversational and narrative material in this book; and syllabi and day-by-day schedules for each term. Course sequnce on OCW. CHINESE&nbsp;COURSES COURSE&nbsp;SITES Chinese I (Spring 2006) 21F.101/151 Chinese II (Spring 2006) 21F.102/152 Chinese III (Fall 2005) 21F.103 Chinese IV (Spring 2006) 21F.104', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-101-chinese-i-regular-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-101-chinese-i-regular-spring-2006/21f-101s06.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1577, 'Chinese I (Streamlined)', 'This course, along with 21F.', 'This course, along with 21F.108/158 offered in the spring, form the elementary level of the streamlined sequence, which is for students who have some basic conversational skills gained, typically, from growing up in a Chinese speaking environment, but lack a corresponding level of literacy. The focus of the course is on learning standard everyday usage, on reading in both traditional and simplified characters, and on writing.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-107-chinese-i-streamlined-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-107-chinese-i-streamlined-fall-2005/21f-107f05.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1578, 'Chinese II (Streamlined)', 'This course, along with 21F.', 'This course, along with 21F.107 / 21F.157 Chinese I (Streamlined) offered in the previous fall, form the elementary level of the streamlined sequence, which is intended for students who, when they began the sequence at beginning level, had basic conversational skills (gained, typically, from growing up in a Chinese speaking environment), but lacked a corresponding level of literacy. The focus of the course is on learning standard usage of expressions for everyday use, on reading in both traditional and simplified characters, and on writing.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-108-chinese-ii-streamlined-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-108-chinese-ii-streamlined-spring-2006/21f-108s06.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1579, 'Listening, Speaking, and Pronunciation', 'This course is designed for high-intermediate ESL students who need to develop better listening comprehension and oral skills, which will primarily be achieved by detailed instructions on pronunciation.', 'This course is designed for high-intermediate ESL students who need to develop better listening comprehension and oral skills, which will primarily be achieved by detailed instructions on pronunciation.&nbsp;Our focus will be on (1) producing accurate and intelligible English, (2) becoming more comfortable listening to rapidly spoken English, and (3) learning common expressions, gambits, and idioms used in both formal and informal contexts.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-223-listening-speaking-and-pronunciation-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-223-listening-speaking-and-pronunciation-fall-2004/21f-223f04.gif', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1580, 'Advanced Speaking and Critical Listening Skills (ELS)', 'This course is for advanced students who wish to build confidence and skills in spoken English.', 'This course is for advanced students who wish to build confidence and skills in spoken English. It focuses on the appropriate oral presentation of material in a variety of professional contexts: group discussions, classroom explanations and interactions, and theses/research proposals. It is valuable for those who intend to teach or lecture in English and includes language laboratory assignments. The goal of the workshop is to develop effective speaking and listening skills for academic and professional contexts.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-232-advanced-speaking-and-critical-listening-skills-els-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-232-advanced-speaking-and-critical-listening-skills-els-spring-2007/21f-232s07.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1581, 'French I', '21F.', '21F.301/351 offers an introduction to the French language and culture with an emphasis on the acquisition of vocabulary and grammatical concepts through active communication. The course is conducted entirely in French, and students interact in French with their classmates from the very beginning. They also receive exposure to the language via a variety of authentic sources such as the Internet, audio, video and printed materials which help them develop cultural awareness as well as linguistic proficiency. There is a coordinated language lab program.This course is taught in rotation by the following instructors: Laura Ceia-Minjares, Cathy Culot, Gilberte Furstenberg, and Johann Sadock.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-301-french-i-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-301-french-i-fall-2004/21f-301f04.gif', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1582, 'French II', '21F.', '21F.302/352 is the second part of an introductory course to the French language and culture with an emphasis on the acquisition of vocabulary and grammatical concepts through active communication. The course is conducted entirely in French and students interact in French with their classmates from the very beginning. They also receive exposure to the language via a variety of authentic sources such as the Internet, audio, video and printed materials which help them develop cultural awareness as well as linguistic proficiency. There is a coordinated language lab program.This course is taught in rotation by the following instructors: Laura Ceia-Minjares, Cathy Culot, Gilberte Furstenberg, and Johann Sadock.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-302-french-ii-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-302-french-ii-fall-2004/21f-302f04.gif', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1583, 'Japanese I', 'This course covers Japanese: The Spoken Language Lessons 1 through 6, providing opportunities to acquire basic skills for conversation, reading and writing.', 'This course covers Japanese: The Spoken Language Lessons 1 through 6, providing opportunities to acquire basic skills for conversation, reading and writing. The program emphasizes active command of Japanese, not passive knowledge. The goal is not simply to study the grammar and vocabulary, but to improve the ability to use Japanese accurately and appropriately with fluency. Students will learn 56 Kanji characters in this course, as well as introducing Hiragana and Katakana.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-501-japanese-i-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-501-japanese-i-fall-2009/21f-501f04.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1584, 'Japanese I', 'This course covers Japanese: The Spoken Language Lessons 1 through 6, providing opportunities to acquire basic skills for conversation, reading and writing.', 'This course covers Japanese: The Spoken Language Lessons 1 through 6, providing opportunities to acquire basic skills for conversation, reading and writing. The program emphasizes active command of Japanese, not passive knowledge. The goal is not simply to study the grammar and vocabulary, but to improve the ability to use Japanese accurately and appropriately with fluency. Students will learn 56 Kanji characters in this course, as well as introducing Hiragana and Katakana.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-501-japanese-i-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-501-japanese-i-fall-2009/21f-501f04.jpg', 'Foreign Languages and Literatures', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1585, 'How to Stage a Revolution', '21H.', '21H.001, a HASS-D, CI course, explores fundamental questions about the causes and nature of revolutions. How do people overthrow their rulers? How do they establish new governments? Do radical upheavals require bloodshed, violence, or even terror? How have revolutionaries attempted to establish their ideals and realize their goals? We will look at a set of major political transformations throughout the world and across centuries to understand the meaning of revolution and evaluate its impact. By the end of the course, students will be able to offer reasons why some revolutions succeed and others fail. Materials for the course include the writings of revolutionaries, declarations and constitutions, music, films, art, memoirs, and newspapers.', 'http://ocw.mit.edu/courses/history/21h-001-how-to-stage-a-revolution-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-001-how-to-stage-a-revolution-fall-2007/21h-001f07.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1586, 'American History to 1865', 'This course provides a basic history of American social, economic, and political development from the colonial period through the Civil War.', 'This course provides a basic history of American social, economic, and political development from the colonial period through the Civil War. It examines the colonial heritages of Spanish and British America; the American Revolution and its impact; the establishment and growth of the new nation; and the Civil War, its background, character, and impact. Readings include writings of the period by J. Winthrop, T. Paine, T. Jefferson, J. Madison, W. H. Garrison, G. Fitzhugh, H. B. Stowe, and A. Lincoln.', 'http://ocw.mit.edu/courses/history/21h-101-american-history-to-1865-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-101-american-history-to-1865-fall-2010/21h-101f10.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1587, 'The Emergence of Modern America 1865-Present', 'This subject studies the changing structure of American politics, economics, and society from the end of the Civil War to the present.', 'This subject studies the changing structure of American politics, economics, and society from the end of the Civil War to the present. We will consider secondary historical accounts and primary documents to examine some of the key issues in the development of modern America: industrialization and urbanization; U.S. emergence as a global power; ideas about rights and equality; and the changing structures of gender, class, and race. This subject also examines the multiple answers that Americans gave to the question of what it means to be an American in the modern age. As a communications intensive subject, students will be expected to engage intensively with the material through frequent oral and written exercises.', 'http://ocw.mit.edu/courses/history/21h-102-the-emergence-of-modern-america-1865-present-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-102-the-emergence-of-modern-america-1865-present-spring-2003/21h-102s03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1588, 'American Classics', 'This subject is devoted to reading and discussing basic American historical texts that are often cited but often remain unread, understanding their meaning, and assessing their continuing significance in American culture.', 'This subject is devoted to reading and discussing basic American historical texts that are often cited but often remain unread, understanding their meaning, and assessing their continuing significance in American culture. Since it is a ''Communications Intensive'' subject, 21H.105 is also dedicated to improving students'' capacities to write and speak well. It requires a substantial amount of writing, participation in discussions, and individual presentations to the class.', 'http://ocw.mit.edu/courses/history/21h-105-american-classics-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-105-american-classics-spring-2006/21h-105s06.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1589, 'American Classics', '''What then is the American, this new man?'' asked J.', '''What then is the American, this new man?'' asked J. Hector St-John de Cr&egrave;vecoeur in his Letters from an American Farmer in 1782. This subject takes Cr&egrave;vecoeur''s question as the starting point for an examination of the changing meanings of national identity in the American past. We will consider a diverse collection of classic texts in American history to see how Americans have defined themselves and their nation in politics, literature, art, and popular culture. As a communications-intensive subject, students will be expected to engage intensively with the material through frequent oral and written exercises.', 'http://ocw.mit.edu/courses/history/21h-105-american-classics-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-105-american-classics-fall-2002/21h-105f02.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1590, 'Black Matters: Introduction to Black Studies', 'This class is an interdisciplinary survey that explores the experiences of people of African descent through the overlapping approaches of history, literature, anthropology, legal studies, media studies, performance, linguistics, and creative writing.', 'This class is an interdisciplinary survey that explores the experiences of people of African descent through the overlapping approaches of history, literature, anthropology, legal studies, media studies, performance, linguistics, and creative writing. It connects the experiences of African Americans and of other American minorities, focusing on social, political, and cultural histories, and on linguistic patterns. Activities include lectures, discussions, workshops, and required field trips that involve minimal cost to students.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-630j-black-matters-introduction-to-black-studies-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-630j-black-matters-introduction-to-black-studies-fall-2009/21m-630jf09.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1591, 'The Civil War and Reconstruction', 'Although attention will be devoted to the causes and long-term consequences of the Civil War, this class will focus primarily on the war years (1861-1865) with special emphasis on the military and technological aspects of the conflict.', 'Although attention will be devoted to the causes and long-term consequences of the Civil War, this class will focus primarily on the war years (1861-1865) with special emphasis on the military and technological aspects of the conflict. Four questions, long debated by historians, will receive close scrutiny: What caused the war? Why did the North win the war? Could the South have won? To what extent is the Civil War America''s &quot;defining moment&quot;?', 'http://ocw.mit.edu/courses/history/21h-116j-the-civil-war-and-reconstruction-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-116j-the-civil-war-and-reconstruction-fall-2005/21h-116jf05.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1592, 'America in the Nuclear Age', 'This course examines the&nbsp;American experience at home and abroad from Pearl Harbor to the end of the Cold War.', 'This course examines the&nbsp;American experience at home and abroad from Pearl Harbor to the end of the Cold War. Topics include: America''s role as global superpower, foreign and domestic anticommunism, social movements of left and right, suburbanization, and popular culture.', 'http://ocw.mit.edu/courses/history/21h-131-america-in-the-nuclear-age-fall-2000', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-131-america-in-the-nuclear-age-fall-2000/21h-131f00.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1593, 'Medieval Economic History in Comparative Perspective', 'This course will survey the conditions of material life and changing social and economic conditions in medieval Europe with reference to the comparative context of contemporary Islamic, Chinese, and central Asian experiences.', 'This course will survey the conditions of material life and changing social and economic conditions in medieval Europe with reference to the comparative context of contemporary Islamic, Chinese, and central Asian experiences. Subject covers the emergence and decline of feudal institutions, the transformation of peasant agriculture, living standards and the course of epidemic disease, and the ebb and flow of long-distance trade across the Eurasian system. Particular emphasis will be placed on the study of those factors, both institutional and technological, which have contributed to the emergence of capitalist organization and economic growth in Western Europe in contrast to the trajectories followed by the other major medieval economies.', 'http://ocw.mit.edu/courses/history/21h-134j-medieval-economic-history-in-comparative-perspective-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-134j-medieval-economic-history-in-comparative-perspective-spring-2012/21h-134js12.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1594, 'Race and Gender in Asian America', 'In this seminar we will examine various issues related to the intersection of race and gender in Asian America, starting with the nineteenth century, but focusing on contemporary issues.', 'In this seminar we will examine various issues related to the intersection of race and gender in Asian America, starting with the nineteenth century, but focusing on contemporary issues. Topics to be covered may include racial and gender discourse, the stereotyping of Asian American women and men in the media, Asian American masculinity, Asian American feminisms and their relation to mainstream American feminism, the debate between feminism and ethnic nationalism, gay and lesbian identity, class and labor issues, domestic violence, interracial dating and marriage, and multiracial identity.', 'http://ocw.mit.edu/courses/history/21h-153j-race-and-gender-in-asian-america-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-153j-race-and-gender-in-asian-america-spring-2006/21h-153js06.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1595, 'American Consumer Culture', 'This class examines how and why twentieth-century Americans came to define the ''good life'' through consumption, leisure, and material abundance.', 'This class examines how and why twentieth-century Americans came to define the ''good life'' through consumption, leisure, and material abundance. We will explore how such things as department stores, nationally advertised brand-name goods, mass-produced cars, and suburbs transformed the American economy, society, and politics. The course is organized both thematically and chronologically. Each period deals with a new development in the history of consumer culture. Throughout we explore both celebrations and critiques of mass consumption and abundance.', 'http://ocw.mit.edu/courses/history/21h-206-american-consumer-culture-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-206-american-consumer-culture-fall-2007/21h-206f07.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1596, 'The Energy Crisis: Past and Present', 'This course will explore how Americans have confronted energy challenges since the end of World War II.', 'This course will explore how Americans have confronted energy challenges since the end of World War II. Beginning in the 1970s, Americans worried about the supply of energy. As American production of oil declined, would the US be able to secure enough fuel to sustain their high consumption lifestyles? At the same time, Americans also began to fear the environmental side affects of energy use. Even if the US had enough fossil fuel, would its consumption be detrimental to health and safety? This class examines how Americans thought about these questions in the last half-century. We will consider the political, diplomatic, economic, cultural, and technological aspects of the energy crisis. Topics include nuclear power, suburbanization and the new car culture, the environmental movement and the challenges of clean energy, the Middle East and supply of oil, the energy crisis of the 1970s, and global warming.', 'http://ocw.mit.edu/courses/history/21h-207-the-energy-crisis-past-and-present-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-207-the-energy-crisis-past-and-present-fall-2010/21h-207f10.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1597, 'The Places of Migration in United States History', 'This course examines the history of the United States as a ''nation of immigrants'' within a broader global context.', 'This course examines the history of the United States as a ''nation of immigrants'' within a broader global context. It considers migration from the mid-19th century to the present through case studies of such places as New York''s Lower East Side, South Texas, Florida, and San Francisco''s Chinatown. It also examines the role of memory, media, and popular culture in shaping ideas about migration. The course includes optional field trip to New York City.', 'http://ocw.mit.edu/courses/history/21h-221-the-places-of-migration-in-united-states-history-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-221-the-places-of-migration-in-united-states-history-fall-2006/21h-221f06.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1598, 'War & American Society', 'Writing in the wake of the Civil War, poet Walt Whitman insisted that ''the real war will never get in the books.', 'Writing in the wake of the Civil War, poet Walt Whitman insisted that ''the real war will never get in the books.'' Throughout American history, the experience of war has fundamentally shaped the ways that Americans think about themselves, their fellow Americans, and the meanings of national citizenship. War has also posed challenges of representation, both for those who fought as well as those who did not. This subject examines how Americans have told the stories of modern war in history, literature, and popular culture, and interprets them in terms of changing ideas about American national identity.', 'http://ocw.mit.edu/courses/history/21h-223-war-american-society-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-223-war-american-society-fall-2002/21h-223f02.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1599, 'Law and Society in US History', 'As events of the last few years have shown, the Supreme Court has played a crucial role in American political life.', 'As events of the last few years have shown, the Supreme Court has played a crucial role in American political life. There is practically no issue of social significance in the American past that did not at some point end up in the nation''s courtrooms, yet much of the workings of the constitution remain obscure. This subject is designed to introduce students to the main themes and events of American constitutional law since 1787. It introduces terms and concepts of law and legal history, focusing on three recurring themes in American public life: liberty, equality, and property. Readings consist mostly of original court cases, especially from the U.S. Supreme Court, but the focus of the class is on the historical connections between those cases and broader social, political, and cultural trends.', 'http://ocw.mit.edu/courses/history/21h-224-law-and-society-in-us-history-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-224-law-and-society-in-us-history-spring-2003/21h-224s03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1600, 'American Urban History I', 'This course is a seminar on the history of institutions and institutional change in American cities from roughly 1850 to the present.', 'This course is a seminar on the history of institutions and institutional change in American cities from roughly 1850 to the present. Among the institutions to be looked at are political machines, police departments, courts, schools, prisons, public authorities, and universities. The focus of the course is on readings and discussions.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-013j-american-urban-history-i-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-013j-american-urban-history-i-spring-2010/11-013js10.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1601, 'American Urban History II', 'This is a seminar course that explores the history of selected features of the physical environment of urban America.', 'This is a seminar course that explores the history of selected features of the physical environment of urban America. Among the features considered are parks, cemeteries, tenements, suburbs, zoos, skyscrapers, department stores, supermarkets, and amusement parks. The course gives students experience in working with primary documentation sources through its selection of readings and class discussions. Students then have the opportunity to apply this experience by researching their own historical questions and writing a term paper.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-014j-american-urban-history-ii-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-014j-american-urban-history-ii-fall-2011/11-014jf11.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1602, 'Downtown', 'This seminar focuses on downtowns in U.', 'This seminar focuses on downtowns in U.S. cities from the late nineteenth century to the late twentieth century. Emphasis will be placed on downtown as an idea, place, and cluster of interests; on the changing character of downtown; and on recent efforts to rebuild it. Subjects to be considered will include subways, skyscrapers, highways, urban renewal, and retail centers. The focus will be on readings, discussions, and individual research projects.', 'http://ocw.mit.edu/courses/history/21h-234j-downtown-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-234j-downtown-spring-2005/21h-234js05.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1603, 'The Ancient World: Greece', 'This course elaborates the history of Ancient Greece from the Bronze Age to the death of Alexander.', 'This course elaborates the history of Ancient Greece from the Bronze Age to the death of Alexander. It covers major social, economic, political, and religious trends. It also includes discussions on Homer, heroism, and the Greek identity; the hoplite revolution and the rise of the city-state; Herodotus, Persia, and the (re)birth of history; Empire, Thucydidean rationalism, and the Peloponnesian War; Platonic constructs; Aristotle, Macedonia, and Hellenism. Emphasis is on use of primary sources in translation.', 'http://ocw.mit.edu/courses/history/21h-301-the-ancient-world-greece-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-301-the-ancient-world-greece-fall-2004/21h-301f04.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1604, 'The Ancient World: Rome', 'This course elaborates the history of Rome from its humble beginnings to the fifth century A.', 'This course elaborates the history of Rome from its humble beginnings to the fifth century A.D. The first half of the course covers Kingship to Republican form; the conquest of Italy; Roman expansion: Pyrrhus, Punic Wars and provinces; classes, courts, and the Roman revolution; Augustus and the formation of empire. The second half of the course covers Virgil to the Vandals; major social, economic, political and religious trends at Rome and in the provinces. Emphasis is placed on the use of primary sources in translation.', 'http://ocw.mit.edu/courses/history/21h-302-the-ancient-world-rome-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-302-the-ancient-world-rome-spring-2005/21h-302s05.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1605, 'The Emergence of Europe: 500-1300', 'This course surveys the social, cultural, and political development of western Europe between 500 and 1350.', 'This course surveys the social, cultural, and political development of western Europe between 500 and 1350. A number of topics are incorporated into the broad chronological sweep of the course, including: the Germanic conquest of the ancient Mediterranean world; the rise of a distinct northern culture and the Carolingian Renaissance; the emergence of feudalism and the breakdown of political order;&#160;contact with the Byzantine and Islamic East and the Crusading movement; the quality of religious life; the vitality of the high medieval economy and culture; and the catastrophes of the fourteenth century.', 'http://ocw.mit.edu/courses/history/21h-306-the-emergence-of-europe-500-1300-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-306-the-emergence-of-europe-500-1300-fall-2003/21h-306f03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1606, 'The Making of Russia in the Worlds of Byzantium, Mongolia, and Europe', 'Medieval and early modern Russia stood at the crossroads of Europe and Asia.', 'Medieval and early modern Russia stood at the crossroads of Europe and Asia. In this course we will examine some of the native developments and foreign influences which most affected the course of Russian history. Particular topics include the rise of the Kievan State, the Mongol Yoke, the rise of Muscovy, Ivan the Terrible, Peter the Great, relations with Western Europe. How did foreigners perceive Russia? How did those living in the Russian lands perceive foreigners? What social relations were developing between nobility and peasantry, town and country, women and men? What were the relations of each of these groups to the state? How did state formation come about in Kievan and Muscovite Russia? What were the political, religious, economic, and social factors affecting relations between state and society? In examining these questions we will consider a variety of sources including contemporary accounts (both domestic and foreign), legal and political documents, historical monographs and interpretive essays.', 'http://ocw.mit.edu/courses/history/21h-326-the-making-of-russia-in-the-worlds-of-byzantium-mongolia-and-europe-spring-1998', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-326-the-making-of-russia-in-the-worlds-of-byzantium-mongolia-and-europe-spring-1998/21h-326s98.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1607, 'The Royal Family', 'This course is an an exploration of British culture and politics, focusing on the changing role of the monarchy from the accession of the House of Hanover (later Windsor) in 1714 to the present.', 'This course is an an exploration of British culture and politics, focusing on the changing role of the monarchy from the accession of the House of Hanover (later Windsor) in 1714 to the present. The dynasty has encountered a series of crises, in which the personal and the political have been inextricably combined: for example, George III''s mental illness; the scandalous behavior of his son, George IV; Victoria''s withdrawal from public life after the death of Prince Albert; the abdication of Edward VIII; and the public antagonism sparked by sympathy for Diana, Princess of Wales.', 'http://ocw.mit.edu/courses/history/21h-342-the-royal-family-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-342-the-royal-family-fall-2003/21h-342f03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1608, 'France, 1660-1815: Enlightenment, Revolution, Napoleon', 'This course covers French politics, culture, and society from Louis XIV to Napoleon Bonaparte.', 'This course covers French politics, culture, and society from Louis XIV to Napoleon Bonaparte. Attention is given to the growth of the central state, the beginnings of a modern consumer society, the Enlightenment, the French Revolution, including its origins, and the rise and fall of Napoleon.', 'http://ocw.mit.edu/courses/history/21h-346-france-1660-1815-enlightenment-revolution-napoleon-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-346-france-1660-1815-enlightenment-revolution-napoleon-spring-2011/21h-346s11.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1609, 'The Ancient City', 'This course focuses on the archaeology of the Greek and Roman city.', 'This course focuses on the archaeology of the Greek and Roman city. It investigates the relationship between urban architecture and the political, social, and economic role of cities in the Greek and Roman world, by analyzing a range of archaeological and literary evidence relevant to the use of space in Greek and Roman cities (e.g. Athens, Paestum, Rome, Pompeii) and a range of theoretical frameworks for the study of ancient urbanism.', 'http://ocw.mit.edu/courses/history/21h-405j-the-ancient-city-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-405j-the-ancient-city-spring-2005/21h-405js05.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1610, 'History of Western Thought, 500-1300', 'This course examines the development of the western intellectual tradition from the fall of the Roman Empire through the High Middle Ages.', 'This course examines the development of the western intellectual tradition from the fall of the Roman Empire through the High Middle Ages. Our basic premise will be that the triumph of Christianity in the west was not the inevitable outcome it might appear from hindsight. Our attention will therefore be focused not only on the development of Christian thought and practice, but on its challengers as well. The core themes of the course include the emergence of a uniform Christian orthodoxy in late antiquity; the development of monastic practice and its attendant intellectual traditions; and the geographical spread of Christian beliefs. Working in opposition to those trends were other forces, which we will also address in our readings. In particular, we will consider the persistence of northern paganism; the rise of Islam; the solidification of a separate Byzantine orthodoxy; indigenous heretical movements; and the ambiguous position of Jews in all of European society.', 'http://ocw.mit.edu/courses/history/21h-411-history-of-western-thought-500-1300-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-411-history-of-western-thought-500-1300-fall-2004/21h-411f04.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1611, 'From Print to Digital: Technologies of the Word, 1450-Present', 'There has been much discussion in recent years, on this campus and elsewhere, about the death of the book.', 'There has been much discussion in recent years, on this campus and elsewhere, about the death of the book. Digitization and various forms of electronic media, some critics say, are rendering the printed text as obsolete as the writing quill. In this subject, we will examine the claims for and against the demise of the book, but we will also supplement these arguments with an historical perspective they lack: we will examine texts, printing technologies, and reading communities from roughly 1450 to the present. We will begin with the theoretical and historical overviews of Walter Ong and Elizabeth Eisenstein, after which we will study specific cases such as English chapbooks, Inkan knotted and dyed strings, late nineteenth-century recording devices, and newspapers online today. We will also visit a rare book library and make a poster on a hand-set printing press.', 'http://ocw.mit.edu/courses/history/21h-418-from-print-to-digital-technologies-of-the-word-1450-present-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-418-from-print-to-digital-technologies-of-the-word-1450-present-fall-2005/21h-418f05.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1612, 'The Age of Reason: Europe from the 17th to the Early 19th Centuries', 'This course asks students to consider the ways in which social theorists, institutional reformers, and political revolutionaries in the 17th through 19th centuries seized upon insights developed in the natural sciences and mathematics to change themselves and the society in which they lived.', 'This course asks students to consider the ways in which social theorists, institutional reformers, and political revolutionaries in the 17th through 19th centuries seized upon insights developed in the natural sciences and mathematics to change themselves and the society in which they lived. Students study trials, art, literature and music to understand developments in Europe and its colonies in these two centuries. Covers works by Newton, Locke, Voltaire, Rousseau, Marx, and Darwin.', 'http://ocw.mit.edu/courses/history/21h-433-the-age-of-reason-europe-from-the-17th-to-the-early-19th-centuries-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-433-the-age-of-reason-europe-from-the-17th-to-the-early-19th-centuries-spring-2011/21h-433s11.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1613, 'European Imperialism in the 19th and 20th Centuries', 'From pineapples grown in Hawaii to English-speaking call centers outsourced to India, the legacy of the ''Age of Imperialism'' appears everywhere in our modern world.', 'From pineapples grown in Hawaii to English-speaking call centers outsourced to India, the legacy of the ''Age of Imperialism'' appears everywhere in our modern world. This class explores the history of European imperialism in its political, economic, and cultural dimensions from the 1840s through the 1960s.', 'http://ocw.mit.edu/courses/history/21h-443-european-imperialism-in-the-19th-and-20th-centuries-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-443-european-imperialism-in-the-19th-and-20th-centuries-spring-2006/21h-443s06.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1614, 'Nazi Germany and the Holocaust', 'The rise and fall of National Socialism is one of the most intensively-studied topics in European history.', 'The rise and fall of National Socialism is one of the most intensively-studied topics in European history. Nevertheless, after more than half a century, popular views of Nazism in the media and among the public remain simplistic-essentialized by equal parts fascination and horror. Adolf Hitler, for instance, is often portrayed as an evil genius of supernatural ability; while the Nazi state is similarly imagined to have held absolute power over every aspect of its subjects'' lives. Such characterizations allow ordinary Germans to be portrayed as helpless victims of Nazism, ensnared or coerced into submission by forces beyond their control. Another popular characterization is that German culture itself is fundamentally flawed - that all Germans were basically Nazis at heart. This schema conveniently erases the manifestations of fascism in other Western nations, and allows Americans and other Westerners to reassure themselves that the horrors of Nazism could never emerge in their own enlightened national cultures.', 'http://ocw.mit.edu/courses/history/21h-447-nazi-germany-and-the-holocaust-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-447-nazi-germany-and-the-holocaust-fall-2004/21h-447f04.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1615, 'Soviet Politics and Society, 1917-1991', 'At its greatest extent the former Soviet Union encompassed a geographical area that covered one-sixth of the Earth''s landmass.', 'At its greatest extent the former Soviet Union encompassed a geographical area that covered one-sixth of the Earth''s landmass. It spanned 11 time zones and contained over 100 distinct nationalities, 22 of which numbered over one million in population. In the 74 years from the October Revolution in 1917 to the fall of Communism in 1991, the Union of Soviet Socialist Republics, its leaders and its people, had to face a number of difficult challenges: the overthrow of the Tsarist autocracy, the establishment of a new state, four years of civil war, a famine, transition to a mixed economy, political strife after Lenin''s death, industrialization, collectivization, a second famine, political Show Trials, World War II, post-war reconstruction and repression, the ''Thaw'' after Stalin''s death, Khrushchev''s experimentation, and Brezhnev''s decline. Each of these challenges engendered new solutions and modifications in what can be loosely called the evolving ''Soviet system.''', 'http://ocw.mit.edu/courses/political-science/17-57j-soviet-politics-and-society-1917-1991-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-57j-soviet-politics-and-society-1917-1991-spring-2003/17-57js03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1616, 'East Asia in the World', 'This subject examines the interactions of East Asia with the rest of the world and the relationships of each of the East Asian countries with each other, from ca.', 'This subject examines the interactions of East Asia with the rest of the world and the relationships of each of the East Asian countries with each other, from ca. 1500 to 2000 A.D. Primary focus on China and Japan, with some reference to Korea, Vietnam, and Central Asia. Asks how international diplomatic, commercial, military, religious, and cultural relationships joined with internal processes to direct the development of East Asian societies. Subject addresses perceptions and misperceptions among East Asians and foreigners.', 'http://ocw.mit.edu/courses/history/21h-504-east-asia-in-the-world-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-504-east-asia-in-the-world-spring-2003/21h-504s03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1617, 'Japan in the Age of the Samurai: History and Film', 'This course covers medieval Japanese society and culture from the twelfth to the nineteenth centuries, when political power rested largely in the hands of feudal warriors.', 'This course covers medieval Japanese society and culture from the twelfth to the nineteenth centuries, when political power rested largely in the hands of feudal warriors. Topics include religion (especially Zen Buddhism); changing concepts of ''the way of the warrior;'' women under feudalism; popular culture; and protest and rebellion. Presentations include weekly feature films. Assigned readings include many literary writings in translation.', 'http://ocw.mit.edu/courses/history/21h-522-japan-in-the-age-of-the-samurai-history-and-film-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-522-japan-in-the-age-of-the-samurai-history-and-film-fall-2006/21h-522f06.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1618, 'The Making of Modern South Asia', 'Survey of Indian civilization from 2500 BC to present-day.', 'Survey of Indian civilization from 2500 BC to present-day. Traces major political events as well as economic, social, ecological, and cultural developments. Primary and secondary readings enhance understanding of this unique civilization, and shape and improve understanding in analyzing and interpreting historical data. Examines major thematic debates in Indian history through class discussion.', 'http://ocw.mit.edu/courses/history/21h-571-the-making-of-modern-south-asia-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-571-the-making-of-modern-south-asia-fall-2006/21h-571f06.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1619, 'Women in South Asia from 1800 to Present', 'This course is designed to introduce and help students understand the changes and continuities in the lives of women in South Asia from a historical perspective.', 'This course is designed to introduce and help students understand the changes and continuities in the lives of women in South Asia from a historical perspective. Using gender as a lens of examining the past, we will examine how politics of race, class, caste and religion affected and continue to impact women in South Asian countries, primarily in India, Pakistan, Bangladesh and Sri Lanka. We will reflect upon current debates within South Asian women''s history in order to examine some of the issues and problems that arise in re-writing the past from a gendered perspective and these are found in primary documents, secondary readings, films, newspaper articles, and the Internet.', 'http://ocw.mit.edu/courses/history/21h-575j-women-in-south-asia-from-1800-to-present-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-575j-women-in-south-asia-from-1800-to-present-fall-2006/21h-575jf06.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1620, 'From the Silk Road to the Great Game: China, Russia, and Central Eurasia', 'This subject examines interactions across the Eurasian continent between Russians, Chinese, Mongolian nomads, and Turkic oasis dwellers during the last millennium and a half.', 'This subject examines interactions across the Eurasian continent between Russians, Chinese, Mongolian nomads, and Turkic oasis dwellers during the last millennium and a half. As empires rose and fell, religions, trade, and war flowed back and forth continuously across this vast space. Today, the fall of the Soviet Union and China''s reforms have opened up new opportunities for cultural interaction.', 'http://ocw.mit.edu/courses/history/21h-580-from-the-silk-road-to-the-great-game-china-russia-and-central-eurasia-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-580-from-the-silk-road-to-the-great-game-china-russia-and-central-eurasia-fall-2003/21h-580f03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1621, 'The Middle East in 20th Century', 'This course explores the 20th-century history of the Middle East, concentrating on the Fertile Crescent, Egypt, Turkey, the Arabian peninsula, and Iran.', 'This course explores the 20th-century history of the Middle East, concentrating on the Fertile Crescent, Egypt, Turkey, the Arabian peninsula, and Iran. We will begin by examining the late Ottoman Empire and close with the events of 9/11 and their aftermath. Readings will include historical surveys, novels, and primary source documents.', 'http://ocw.mit.edu/courses/history/21h-615-the-middle-east-in-20th-century-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-615-the-middle-east-in-20th-century-spring-2003/21h-615s03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1622, 'Modern Latin America, 1808-Present: Revolution, Dictatorship, Democracy', 'This class is a selective survey of Latin American history from the beginning of the twentieth century to the present.', 'This class is a selective survey of Latin American history from the beginning of the twentieth century to the present.&nbsp;Issues studied include Latin America in the global economy, relations between Latin America and the U.S., dictatorships and democracies in the twentieth century, African and Indigenous cultures, feminism and gender, cultural politics, revolution in Mexico, Cuba, and Central America, and Latin American identity.', 'http://ocw.mit.edu/courses/history/21h-802-modern-latin-america-1808-present-revolution-dictatorship-democracy-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-802-modern-latin-america-1808-present-revolution-dictatorship-democracy-spring-2005/21h-802s05.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1623, 'Trials in History', 'This seminar examines a number of famous trials in European and American history.', 'This seminar examines a number of famous trials in European and American history. It considers the salient issues (political, social, cultural) of several trials, the ways in which each trial was constructed and covered in public discussions at the time, the ways in which legal reasoning and storytelling interacted in each trial and in the later retellings of the trial, and the ways in which trials serve as both spectacle and a forum for moral and political reasoning. Students have an opportunity to study one trial in depth and present their findings to the class.', 'http://ocw.mit.edu/courses/history/21h-907-trials-in-history-fall-2000', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-907-trials-in-history-fall-2000/21h-907f00.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1624, 'The World Since 1492', 'This class offers a look into the last five hundred years of world history.', 'This class offers a look into the last five hundred years of world history. Rather than attempt an exhaustive chronology of everything that has occurred on the globe since 1492 - an impossible task for a lifetime, let alone a single semester - we will be focusing on certain geographic areas at specific times, in order to highlight a particular historical problem or to examine the roots of processes that have had an enormous impact on the contemporary world.', 'http://ocw.mit.edu/courses/history/21h-912-the-world-since-1492-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-912-the-world-since-1492-fall-2004/21h-912f04.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1625, 'The World Since 1492', 'This course explores the last 500 years of world history.', 'This course explores the last 500 years of world history. Rather than trying to cover all regions for all periods of time, we will focus on four related themes: the struggles between Europeans and colonized peoples; the global formation of capitalist economies and industrialization; the emergence of modern states; and the development of the tastes and disciplines of bourgeois society.&#160; Note: This course is based on a model developed by Professor Daniel Segal of Pitzer College.', 'http://ocw.mit.edu/courses/history/21h-912-the-world-since-1492-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-912-the-world-since-1492-spring-2003/21h-912s03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1626, 'Making the Modern World: The Industrial Revolution in Global Perspective', 'This class is a global survey of the great transformation in history known as the &quot;Industrial Revolution.', 'This class is a global survey of the great transformation in history known as the &quot;Industrial Revolution.&quot; Topics include origins of mechanized production, the factory system, steam propulsion, electrification, mass communications, mass production and automation. Emphasis on the transfer of technology and its many adaptations around the world. Countries treated include Great Britain, France, Germany, the US, Sweden, Russia, Japan, China, and India. Includes brief reflection papers and a final paper.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-025j-making-the-modern-world-the-industrial-revolution-in-global-perspective-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-025j-making-the-modern-world-the-industrial-revolution-in-global-perspective-fall-2009/sts-025jf09.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1627, 'Visualizing Cultures', 'In this new course, students will study how images have been used to shape the identity of peoples and cultures.', 'In this new course, students will study how images have been used to shape the identity of peoples and cultures. A prototype digital project looking at American and Japanese graphics depicting the opening of Japan to the outside world in the 1850s will be used as a case study to introduce the conceptual and practical issues involved in &quot;visualizing cultures&quot;. The major course requirement will be creation and presentation of a project involving visualized cultures.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-027j-visualizing-cultures-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-027j-visualizing-cultures-spring-2008/21f-027js08.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1628, 'The Economic History of Work and Family', 'This course will explore the relation of women and men in both pre-industrial and modern societies to the changing map of public and private (household) work spaces, examining how that map affected their opportunities for both productive activity and the consumption of goods and leisure.', 'This course will explore the relation of women and men in both pre-industrial and modern societies to the changing map of public and private (household) work spaces, examining how that map affected their opportunities for both productive activity and the consumption of goods and leisure. The reproductive strategies of women, either in conjunction with or in opposition to their families, will be the third major theme of the course. We will consider how a place and an ideal of the &quot;domestic&quot; arose in the early modern west, to what extent it was effective in limiting the economic position of women, and how it has been challenged, and with what success, in the post-industrial period. Finally, we will consider some of the policy implications for contemporary societies as they respond to changes in the composition of the paid work force, as well as to radical changes in their national demographic profiles. Although most of the material for the course will focus on western Europe since the Middle Ages and on the United States, we will also consider how these issues have played themselves out in non-western cultures.', 'http://ocw.mit.edu/courses/history/21h-927j-the-economic-history-of-work-and-family-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-927j-the-economic-history-of-work-and-family-spring-2005/21h-927js05.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1629, 'Seminar in Historical Methods', 'This course is designed to acquaint students with a variety of approaches to the past used by historians writing in the twentieth century.', 'This course is designed to acquaint students with a variety of approaches to the past used by historians writing in the twentieth century. The books we read have all made significant contributions to their respective sub-fields and have been selected to give as wide a coverage in both field and methodology as possible in one semester''s worth of reading. We examine how historians conceive of their object of study, how they use primary sources as a basis for their accounts, how they structure the narrative and analytic discussion of their topic, and what are the advantages and drawbacks of their various approaches.', 'http://ocw.mit.edu/courses/history/21h-931-seminar-in-historical-methods-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-931-seminar-in-historical-methods-spring-2004/21h-931s04.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1630, 'Seminar in Historical Methods', 'This course is designed to introduce students to fundamental issues and debates in the writing of history.', 'This course is designed to introduce students to fundamental issues and debates in the writing of history. It will feature innovative historical accounts written in recent years. The class will consider such questions as the words historians use, their language, sources, methods, organization, framing, and style. How does the choice of each of these affect the historian''s work? How does the author choose, analyze, and present evidence? How effective are different methodologies?', 'http://ocw.mit.edu/courses/history/21h-931-seminar-in-historical-methods-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-931-seminar-in-historical-methods-spring-2002/21h-931s02.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1631, 'Readings in American History Since 1877', 'This seminar aims to develop a teaching knowledge of the field through extensive reading and discussion of major works.', 'This seminar aims to develop a teaching knowledge of the field through extensive reading and discussion of major works. The reading covers a broad range of topics - political, economic, social, and cultural - and represents a variety of historical methods.', 'http://ocw.mit.edu/courses/history/21h-952j-readings-in-american-history-since-1877-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-952j-readings-in-american-history-since-1877-fall-2003/21h-952jf03.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1632, 'Nature, Environment, and Empire', 'This class examines the relationship between the study of natural history, both domestic and exotic, by Europeans and Americans, and exploration and exploitation of the natural world, focusing on the eighteenth and nineteenth centuries.', 'This class examines the relationship between the study of natural history, both domestic and exotic, by Europeans and Americans, and exploration and exploitation of the natural world, focusing on the eighteenth and nineteenth centuries.', 'http://ocw.mit.edu/courses/history/21h-968j-nature-environment-and-empire-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-968j-nature-environment-and-empire-spring-2010/21h-968js10.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1633, 'Theories and Methods in the Study of History', 'We will doggedly ask two questions in this class: &quot;What is history?&quot; and &quot;How do you do it in 2010?&quot; In pursuit of the answers, we will survey a variety of approaches to the past used by historians writing in the last several decades.', 'We will doggedly ask two questions in this class: &quot;What is history?&quot; and &quot;How do you do it in 2010?&quot; In pursuit of the answers, we will survey a variety of approaches to the past used by historians writing in the last several decades. We will examine how these historians conceive of their object of study, how they use primary sources as a basis for their accounts, how they structure the narrative and analytical discussion of their topic, and the advantages and limitations of their approaches.', 'http://ocw.mit.edu/courses/history/21h-991-theories-and-methods-in-the-study-of-history-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-991-theories-and-methods-in-the-study-of-history-fall-2010/21h-991f10.jpg', 'History', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1634, 'Problems in Philosophy', 'The course has two goals.', 'The course has two goals. First, to give you a sense of what philosophers think about and why. Here we look at a number of perennial philosophical problems, including some or all of: how knowledge differs from &quot;mere opinion,&quot; the objectivity (or not) of moral judgment, logical paradoxes, mind/body relations, the nature and possibility of free will, and how a person remains the same over time, as their bodily and psychological traits change. The second goal is to get you thinking philosophically yourself. This will help you develop your critical and argumentative skills more generally. Readings will be from late, great classical authors and influential contemporary figures.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-00-problems-in-philosophy-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-00-problems-in-philosophy-fall-2010/24-00f10.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1635, 'Classics in Western Philosophy', 'This course will introduce you to the Western philosophical tradition, through the study of major figures such as Plato, Aristotle, Descartes, Hume, and Kant.', 'This course will introduce you to the Western philosophical tradition, through the study of major figures such as Plato, Aristotle, Descartes, Hume, and Kant. You will get to grips with questions that have been significant to philosophy from its beginnings: questions about the nature of the mind or soul, the existence of God, the foundations of knowledge, ethics and the good life. In the process of evaluating the arguments of these philosophers, you will develop your own philosophical and analytical skills. You will also observe changes of intellectual outlook over time, and the effect of scientific, religious and political concerns on the development of philosophical ideas. Lecture handouts will be supplied for Lec #1-8, and #16-25. For the section on Descartes'' Meditations, Lec #9-15, my separate Study Guide to Descartes'' Meditations is available in the study materials section.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-01-classics-in-western-philosophy-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-01-classics-in-western-philosophy-spring-2006/24-01s06.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1636, 'Moral Problems and the Good Life', 'This course will focus on issues that arise in contemporary public debate concerning matters of social justice.', 'This course will focus on issues that arise in contemporary public debate concerning matters of social justice. Topics will likely include: euthanasia, gay marriage, racism and racial profiling, free speech, hunger and global inequality. Students will be exposed to multiple points of view on the topics and will be given guidance in analyzing the moral frameworks informing opposing positions. The goal will be to provide the basis for respectful and informed discussion of matters of common moral concern.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-02-moral-problems-and-the-good-life-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-02-moral-problems-and-the-good-life-fall-2008/24-02f08.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1637, 'Justice', 'This course explores the ideal of social justice.', 'This course explores the ideal of social justice. What makes a society just? We will approach this question by studying three opposing theories of justice&mdash;utilitarianism, libertarianism, and egalitarian liberalism&mdash;each foundational to contemporary political thought and discourse.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-04j-justice-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-04j-justice-spring-2012/24-04js12.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1638, 'Bioethics', 'This course does not seek to provide answers to ethical questions.', 'This course does not seek to provide answers to ethical questions. Instead, the course hopes to teach students two things. First, how do you recognize ethical or moral problems in science and medicine? When something does not feel right (whether cloning, or failing to clone) &mdash; what exactly is the nature of the discomfort? What kind of tensions and conflicts exist within biomedicine? Second, how can you think productively about ethical and moral problems? What processes create them? Why do people disagree about them? How can an understanding of philosophy or history help resolve them? By the end of the course students will hopefully have sophisticated and nuanced ideas about problems in bioethics, even if they do not have comfortable answers.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-06j-bioethics-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-06j-bioethics-spring-2009/24-06js09.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1639, 'Philosophical Issues in Brain Science', 'This course provides an introduction to important philosophical questions about the mind, specifically those that are intimately connected with contemporary psychology and neuroscience.', 'This course provides an introduction to important philosophical questions about the mind, specifically those that are intimately connected with contemporary psychology and neuroscience. Are our concepts innate or are they acquired by experience? And what does it even mean to call a concept ''innate''? Are ''mental images'' pictures in the head? Is color in the mind or in the world? Is the mind nothing more than the brain? Can there be a science of consciousness? The course includes guest lectures by philosophers and cognitive scientists.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-08j-philosophical-issues-in-brain-science-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-08j-philosophical-issues-in-brain-science-spring-2009/24-08js09.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1640, 'Philosophy of Quantum Mechanics', 'Quantum mechanics--even in the ordinary, non-relativistic, ''particle'' formulation that will be the primary focus of this course--has been a staggeringly successful physical theory, surely one of the crowning achievements of 20th century science.', 'Quantum mechanics--even in the ordinary, non-relativistic, ''particle'' formulation that will be the primary focus of this course--has been a staggeringly successful physical theory, surely one of the crowning achievements of 20th century science. It''s also rather bizarre--bizarre enough to lead very intelligent and otherwise sensible people to make such claims as that the universe is perpetually splitting into many copies of itself, that conscious minds have the power to make physical systems ''jump'' in unpredictable ways, that classical logic stands in need of fundamental revision, and much, much more. In this course, we intelligent and sensible people will attempt to take a sober look at these and other alleged implications of quantum mechanics, as well as certain stubborn problems that continue to trouble its foundations. Along the way, we will take plenty of time out to discuss philosophical questions about science that quantum mechanics raises in new and interesting ways: e.g., what it means to attribute probabilities to physical events, what the aims of scientific inquiry are (does it aim at something true, or merely at something useful?), what the role of observation is in constructing a scientific theory, what it means to say that there is an ''objective'' physical world, whether something as basic as logic can be viewed as an empirical discipline, whether there can be meaningful scientific questions whose answers cannot possibly be settled by experiment, and more.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-111-philosophy-of-quantum-mechanics-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-111-philosophy-of-quantum-mechanics-spring-2005/24-111s05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1641, 'Paradox and Infinity', 'In this class we will study a cluster of puzzles, paradoxes and intellectual wonders - from Zeno''s Paradox to Godel''s Theorem - and discuss their philosophical implications.', 'In this class we will study a cluster of puzzles, paradoxes and intellectual wonders - from Zeno''s Paradox to Godel''s Theorem - and discuss their philosophical implications.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-118-paradox-and-infinity-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-118-paradox-and-infinity-fall-2006/24-118f06.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1642, 'Moral Psychology', 'This course is an examination of philosophical theories of action and motivation in the light of empirical findings from social psychology, sociology, and neuroscience.', 'This course is an examination of philosophical theories of action and motivation in the light of empirical findings from social psychology, sociology, and neuroscience. Topics include belief, desire, and moral motivation; sympathy and empathy; intentions and other committing states; strength of will and weakness of will; free will; addiction and compulsion; guilt, shame and regret; evil; self-knowledge and self-deception; and, virtues and character traits. This course is a CI-M course.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-120-moral-psychology-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-120-moral-psychology-spring-2009/24-120s09.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1643, 'Topics in the History of Philosophy: Kant', 'In this course we shall study the Critique of Pure Reason with special focus on questions about idealism, about our ignorance of things in themselves, and about what, if anything, idealism has to do with this kind of ignorance.', 'In this course we shall study the Critique of Pure Reason with special focus on questions about idealism, about our ignorance of things in themselves, and about what, if anything, idealism has to do with this kind of ignorance. Along the way we shall consider Kant''s distinctive account of space, matter, and force, all of which had a significant role to play in his own philosophy, and in the historical evolution of field theory. In the last part of the course we shall look at an alternative, and unorthodox, interpretation of Kant''s distinction between phenomena and things in themselves (as argued for in my own book, Kantian Humility).', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-201-topics-in-the-history-of-philosophy-kant-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-201-topics-in-the-history-of-philosophy-kant-fall-2005/24-201f05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1644, 'Philosophy In Film and Other Media', 'This course examines works of film in relation to thematic issues of philosophical importance that also occur in other arts, particularly literature and opera.', 'This course examines works of film in relation to thematic issues of philosophical importance that also occur in other arts, particularly literature and opera. Emphasis is put on film''s ability to represent and express feeling as well as cognition. Both written and cinematic works by Sturges, Shaw, Cocteau, Hitchcock, Joyce, and Bergman, among others, are considered. There are no tests or quizzes, however students write two major papers on media/philosophical research topics of their choosing.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-209-philosophy-in-film-and-other-media-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-209-philosophy-in-film-and-other-media-spring-2004/24-209s04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1645, 'Theory of Knowledge', 'This course focuses on the study of problems concerning our concept of knowledge, our knowledge of the past, our knowledge of the thoughts and feelings of ourselves and others, and our knowledge of the existence and properties of physical objects in our immediate environment.', 'This course focuses on the study of problems concerning our concept of knowledge, our knowledge of the past, our knowledge of the thoughts and feelings of ourselves and others, and our knowledge of the existence and properties of physical objects in our immediate environment.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-211-theory-of-knowledge-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-211-theory-of-knowledge-fall-2003/24-211f03.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1646, 'Metaphysics', 'This course focuses on the study of basic metaphysical issues concerning existence, the mind-body problem, personal identity, and causation plus its implications for freedom.', 'This course focuses on the study of basic metaphysical issues concerning existence, the mind-body problem, personal identity, and causation plus its implications for freedom. The course explores classical as well as contemporary readings.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-221-metaphysics-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-221-metaphysics-fall-2005/24-221f05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1647, 'Metaphysics: Free Will', 'This course is a study of free will.', 'This course is a study of free will. It explores the main topic through the lenses of the consequence argument, unavoidability, law breaking, libertarianism, the concept of the person, moral responsibility, action, intention, choice, social psychology, and addiction.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-221-metaphysics-free-will-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-221-metaphysics-free-will-fall-2004/24-221f04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1648, 'Decisions, Games, and Rational Choice', 'Foundations and philosophical applications of Bayesian decision theory, game theory and theory of collective choice.', 'Foundations and philosophical applications of Bayesian decision theory, game theory and theory of collective choice. Why should degrees of belief be probabilities? Is it always rational to maximize expected utility? If so, why and what is its utility? What is a solution to a game? What does a game-theoretic solution concept such as Nash equilibrium say about how rational players will, or should, act in a game? How are the values and the actions of groups, institutions and societies related to the values and actions of the individuals that constitute them?', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-222-decisions-games-and-rational-choice-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-222-decisions-games-and-rational-choice-spring-2008/24-222s08.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1649, 'Philosophy of Law', '', '', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-235j-philosophy-of-law-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-235j-philosophy-of-law-spring-2012/24-235js12.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1650, 'Feminist Political Thought', 'In this course we will examine the development of feminist theory over time.', 'In this course we will examine the development of feminist theory over time. Some subjects we will examine in detail include suffrage and equality; radical feminism; psychoanalysis and feminism; theories of power; sexuality and gender; embodied knowledge; pornography; identities and global feminism; militarism; and the welfare state. Throughout the course we will analyze different ways of looking at power and political culture in modern societies, issues of race and class, poverty and welfare, sexuality and morality.', 'http://ocw.mit.edu/courses/special-programs/sp-601j-feminist-political-thought-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-601j-feminist-political-thought-spring-2010/sp-601js10.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1651, 'Logic I', 'In this course we will cover central aspects of modern formal logic, beginning with an explanation of what constitutes good reasoning.', 'In this course we will cover central aspects of modern formal logic, beginning with an explanation of what constitutes good reasoning. Topics will include validity and soundness of arguments, formal derivations, truth-functions, translations to and from a formal language, and truth-tables. We will thoroughly cover sentential calculus and predicate logic, including soundness and completeness results.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-241-logic-i-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-241-logic-i-fall-2009/24-241f09.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1652, 'Logic II', 'This course begins with an introduction to the theory of computability, then proceeds to a detailed study of its most illustrious result: Kurt G&ouml;del''s theorem that, for any system of true arithmetical statements we might propose as an axiomatic basis for proving truths of arithmetic, there will be some arithmetical statements that we can recognize as true even though they don''t follow from the system of axioms.', 'This course begins with an introduction to the theory of computability, then proceeds to a detailed study of its most illustrious result: Kurt G&ouml;del''s theorem that, for any system of true arithmetical statements we might propose as an axiomatic basis for proving truths of arithmetic, there will be some arithmetical statements that we can recognize as true even though they don''t follow from the system of axioms. In my opinion, which is widely shared, this is the most important single result in the entire history of logic, important not only on its own right but for the many applications of the technique by which it''s proved. We''ll discuss some of these applications, among them: Church''s theorem that there is no algorithm for deciding when a formula is valid in the predicate calculus; Tarski''s theorem that the set of true sentence of a language isn''t definable within that language; and G&ouml;del''s second incompleteness theorem, which says that no consistent system of axioms can prove its own consistency.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-242-logic-ii-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-242-logic-ii-spring-2004/24-242s04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1653, 'Modal Logic', 'This course covers sentential and quantified modal logic, with emphasis on the model theory (&quot;possible worlds semantics&quot;).', 'This course covers sentential and quantified modal logic, with emphasis on the model theory (&quot;possible worlds semantics&quot;). Topics include soundness, completeness, characterization results for alternative systems, sense and dynamic logics, epistemic logics, as well as logics of necessity and possibility. Course material applies to philosophy, theoretical computer science, and linguistics.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-244-modal-logic-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-244-modal-logic-fall-2009/24-244f09.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1654, 'Introduction to Philosophy of Language', 'This course explores the nature of meaning and truth, and their bearing on the use of language in communication.', 'This course explores the nature of meaning and truth, and their bearing on the use of language in communication. No knowledge of logic or linguistics is presupposed.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-251-introduction-to-philosophy-of-language-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-251-introduction-to-philosophy-of-language-fall-2011/24-251f11.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1655, 'Introduction to Philosophy of Language', 'In this introductory course on the philosophy of language, we examine views on the nature of meaning, reference, truth, and their relationships.', 'In this introductory course on the philosophy of language, we examine views on the nature of meaning, reference, truth, and their relationships. Other topics may include relationships between language and logic, language and knowledge, language and reality, language and acts performed through its use. No knowledge of logic or linguistics presupposed.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-251-introduction-to-philosophy-of-language-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-251-introduction-to-philosophy-of-language-spring-2005/24-251s05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1656, 'Topics in Philosophy: David Lewis', 'The class will be devoted to the work of David Lewis, one of the most exciting and influential philosophers of the late twentieth century.', 'The class will be devoted to the work of David Lewis, one of the most exciting and influential philosophers of the late twentieth century. We will have seminar-style discussions about his work on counterfactuals, time, causation, probability, and decision-theory.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-260-topics-in-philosophy-david-lewis-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-260-topics-in-philosophy-david-lewis-spring-2008/24-260s08.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1657, 'Philosophy of Love in the Western World', 'This course is a seminar on the nature of love and sex, approached as topics both in philosophy and in literature.', 'This course is a seminar on the nature of love and sex, approached as topics both in philosophy and in literature. Readings from recent philosophy as well as classic myths of love that occur in works of literature and lend themselves to philosophical analysis.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-261-philosophy-of-love-in-the-western-world-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-261-philosophy-of-love-in-the-western-world-fall-2004/24-261f04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1658, 'The Nature of Creativity', 'This course is an introduction to problems about creativity as it pervades human experience and behavior.', 'This course is an introduction to problems about creativity as it pervades human experience and behavior. Questions about imagination and innovation are studied in relation to the history of philosophy as well as more recent work in philosophy, affective psychology, cognitive studies, and art theory. Readings and guidance are aligned with the student''s focus of interest.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-263-the-nature-of-creativity-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-263-the-nature-of-creativity-fall-2005/24-263f05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1659, 'Film as Visual and Literary Mythmaking', 'This course examines problems in the philosophy of film as well as literature studied in relation to their making of myths.', 'This course examines problems in the philosophy of film as well as literature studied in relation to their making of myths. The readings and films that are discussed in this course draw upon classic myths of the western world. Emphasis is placed on meaning and technique as the basis of creative value in both media.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-264-film-as-visual-and-literary-mythmaking-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-264-film-as-visual-and-literary-mythmaking-fall-2005/24-264f05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1660, 'Other Minds', 'This is a seminar on issues connected with the traditional ''problem of other minds''.', 'This is a seminar on issues connected with the traditional ''problem of other minds''. In addition to reading some of the classic papers on other minds, we will look at recent work on related topics. There will be no lectures. Each week I will spend half an hour or so introducing the assigned reading, and the rest of the time will be devoted to discussion.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-500-other-minds-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-500-other-minds-spring-2003/24-500s03.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1661, 'Introduction to Linguistics', 'This class will provide some answers to basic questions about the nature of human language.', 'This class will provide some answers to basic questions about the nature of human language. Throughout the course, we will be learning (in many different ways) that human language is a surprisingly intricate -- yet law-governed and fascinating mental system. In the first 2/3 of the class, we will study some core aspects of this system in detail. In the last part of the class, we will use what we have learned to address a variety of questions, including how children acquire language, ways in which languages are affected by contact with other languages, and the representation of linguistic phenomena in the brain, among others.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-900-introduction-to-linguistics-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-900-introduction-to-linguistics-spring-2004/24-900s04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1662, 'Language and Its Structure I: Phonology', '24.', '24.901 is designed to give you a preliminary understanding of how the sound systems of different languages are structured, how and why they may differ from each other. The course also aims to provide you with analytical tools in phonology, enough to allow you to sketch the analysis of an entire phonological system by the end of the term. On a non-linguistic level, the course aims to teach you by example the virtues of formulating precise and explicit descriptive statements; and to develop your skills in making and evaluating arguments.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-901-language-and-its-structure-i-phonology-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-901-language-and-its-structure-i-phonology-fall-2010/24-901f10.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1663, 'Language and its Structure II: Syntax', 'This course will acquaint you with some of the important results and ideas of the last half - century of research in syntax.', 'This course will acquaint you with some of the important results and ideas of the last half - century of research in syntax. We will explore a large number of issues and a large amount of data so that you can learn something of what this field is all about. From time to time, we will discuss related work in language acquisition and processing. The class will emphasize ideas and arguments for these ideas in addition to the the details of particular analyses. At the same time, you will learn the mechanics of one particular approach (sometimes called Principles and Parameters syntax). Most of all, the course tries to show why the study of syntax is exciting, and why its results are important to researchers in other language sciences. The class assumes some familiarity with basic concepts of theoretical linguistics, of the sort you could acquire in&nbsp;24.900.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-902-language-and-its-structure-ii-syntax-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-902-language-and-its-structure-ii-syntax-fall-2003/24-902f03.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1664, 'Language Acquisition', 'Covers the major results in the study of first language acquisition concentrating on the development of linguistic structure, including sentence structure and morphology.', 'Covers the major results in the study of first language acquisition concentrating on the development of linguistic structure, including sentence structure and morphology. Universal aspects of development are discussed, as well as a variety of cross-linguistic phenomena. Theories of language learning are considered, including parameter-setting and maturation.', 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-57j-language-acquisition-fall-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-57j-language-acquisition-fall-2001/9-57f01.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1665, 'Psycholinguistics', 'This course covers central topics in language processing, including: the structure of language; sentence, discourse, and morphological processing; storage and access of words in the mental dictionary; speech processing; the relationship between the computational resources available in working memory and the language processing mechanism; and ambiguity resolution.', 'This course covers central topics in language processing, including: the structure of language; sentence, discourse, and morphological processing; storage and access of words in the mental dictionary; speech processing; the relationship between the computational resources available in working memory and the language processing mechanism; and ambiguity resolution. The course also considers computational modeling, including connectionist models; the relationship between language and thought; and issues in language acquisition including critical period phenomena, the acquisition of speech, and the acquisition of words. Experimental methodologies such as self-paced reading, eye-tracking, cross-modal priming, and neural imaging methods are also examined.', 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-59j-psycholinguistics-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-59j-psycholinguistics-spring-2005/9-59js05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1666, 'The Linguistic Study of Bilingualism', 'This course describes development of bilingualism in human history (from Lucy to present day).', 'This course describes development of bilingualism in human history (from Lucy to present day). It focuses on linguistic aspects of bilingualism; models of bilingualism and language acquisition; competence versus performance; effects of bilingualism on other domains of human cognition; brain imaging studies; early versus late bilingualism; opportunities to observe and conduct original research; and implications for educational policies among others. The course is taught in English.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-906j-the-linguistic-study-of-bilingualism-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-906j-the-linguistic-study-of-bilingualism-fall-2006/24-906jf06.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1667, 'Topics in Linguistic Theory: Propositional Attitudes', 'This course explores topics related to the representation and expression of propositional attitudes (e.', 'This course explores topics related to the representation and expression of propositional attitudes (e.g. belief, knowledge, and desires) and speech acts (e.g. saying and asking) in natural language. The main focus will be on semantics of predicates such as believe, know, want, say, ask, etc. Other topics will include the syntax of main and embedded clauses and formal representation of the pragmatics of conversation. The course provides practice in written and oral communication.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-910-topics-in-linguistic-theory-propositional-attitudes-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-910-topics-in-linguistic-theory-propositional-attitudes-spring-2009/24-910s09.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1668, 'Topics in Linguistic Theory: Laboratory Phonology', 'The goal of this course is to prepare you to engage in experimental investigations of questions related to linguistic theory, focusing on phonetics and phonology.', 'The goal of this course is to prepare you to engage in experimental investigations of questions related to linguistic theory, focusing on phonetics and phonology.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-910-topics-in-linguistic-theory-laboratory-phonology-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-910-topics-in-linguistic-theory-laboratory-phonology-spring-2007/24-910s07.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1669, 'Topics in Linguistics Theory', 'I realize that ''Modes of Assertion'' is a rather cryptic title for the course.', 'I realize that ''Modes of Assertion'' is a rather cryptic title for the course. What we will explore are ways of modulating the force of an assertion. This will engage us in formal semantics and pragmatics, the theory of speech acts and performative utterances, and quite a bit of empirical work on a not-too-well understood complex of data. ''It is obvious that he made a big mistake.'' If you''re like me you didn''t feel much of a difference. But now see what happens when you embed the two sentences: ''We have to fire him, because he obviously made a big mistake.'' ''We have to fire him, because it is obvious that he made a big mistake.'' One of the two examples is unremarkable, the other suggests that the reason he needs to be fired is not that he made a big mistake but the fact that it is obvious that he did. We will try to understand what is going on here and look at related constructions not just in English but also German (with its famous discourse particles like ja) and Quechua and Tibetan (with their systems of evidentiality-marking, as recently studied in dissertations from Stanford and UCLA).', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-910-topics-in-linguistics-theory-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-910-topics-in-linguistics-theory-spring-2003/24-910s03.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1670, 'Topics in Linguistics: Creole Languages and Caribbean Identities', 'The Creole languages spoken in the Caribbean are linguistic by-products of the historical events triggered by colonization and the slave trade in Africa and the ''New World''.', 'The Creole languages spoken in the Caribbean are linguistic by-products of the historical events triggered by colonization and the slave trade in Africa and the ''New World''. In a nutshell, these languages are the results of language acquisition in the specific social settings defined by the history of contact between African and European peoples in 17th-/18th-century Caribbean colonies. One of the best known Creole languages, and the one with the largest community of speakers, is Haitian Creole. Its lexicon and various aspects of its grammar are primarily derived from varieties of French as spoken in 17th-/18th-century colonial Haiti. Other aspects of its grammar seem to have emerged under the influence of African languages, mostly from West and Central Africa. And yet other properties seem to have no analogues in any of the source languages. Through a sample of linguistic case studies focusing on Haitian Creole morphosyntax, we will explore creolization from a cognitive, historical and comparative perspective. Using Haitian Creole and some of its Caribbean congeners as test cases, we will evaluate various hypotheses about the development of Creole languages and about the role of first- and second-language acquisition in such development. We will also explore the concept of Creolization in its non-linguistic senses. Then we will address questions of ''Caribbean identities'' by examining a sample of Creole speakers'' attitudes toward the Creole language and the corresponding European language and toward the African and European components of their ethnic make-up.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-919-topics-in-linguistics-creole-languages-and-caribbean-identities-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-919-topics-in-linguistics-creole-languages-and-caribbean-identities-spring-2004/24-919s04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1671, 'Linguistic Theory and the Japanese Language', 'This course is a detailed examination of the grammar of Japanese and its structure which is significantly different from English, with special emphasis on problems of interest in the study of linguistic universals.', 'This course is a detailed examination of the grammar of Japanese and its structure which is significantly different from English, with special emphasis on problems of interest in the study of linguistic universals. Data from a broad group of languages is studied for comparison with Japanese. This course assumes familiarity with linguistic theory.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-946-linguistic-theory-and-the-japanese-language-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-946-linguistic-theory-and-the-japanese-language-fall-2004/24-946f04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1672, 'Proseminar in Philosophy I', 'An intensive seminar on the foundations of analytic philosophy for first-year graduate students.', 'An intensive seminar on the foundations of analytic philosophy for first-year graduate students.&nbsp;A large selection of classic texts, from Frege''s Foundations of Arithmetic to Wittgenstein''s Philosophical Investigations, is covered in this course.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-400-proseminar-in-philosophy-i-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-400-proseminar-in-philosophy-i-fall-2003/24-400f03.JPG', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1673, 'Topics in Philosophy of Mind: Self-Knowledge', 'This is a seminar on ''self-knowledge'' -- knowledge of one''s own mental states.', 'This is a seminar on ''self-knowledge'' -- knowledge of one''s own mental states. In addition to reading some of the classic papers on self-knowledge, we will look at some very recent work on the topic. There will be no lectures. Each week I will spend half an hour or so introducing the assigned reading, and the rest of the time will be devoted to discussion.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-500-topics-in-philosophy-of-mind-self-knowledge-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-500-topics-in-philosophy-of-mind-self-knowledge-spring-2005/24-500s05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1674, 'Political Philosophy: Global Justice', 'This course explores the foundations and content of norms of justice that apply beyond the borders of a single state.', 'This course explores the foundations and content of norms of justice that apply beyond the borders of a single state. We examine issues of political justice, economic justice, and human rights. Topics include the case for skepticism about global justice; the idea of global democracy; intellectual property rights; the nature of distributive justice at the global level; pluralism and human rights; and rights to control borders. It meets jointly with Harvard''s Philosophy 271, and is taught by Professors Joshua Cohen, Thomas Scanlon, and Amartya Sen. Readings are from Kant, Habermas, Rawls, Sen, Beitz, Nussbaum, Stiglitz, Ignatieff, Walzer, among others.', 'http://ocw.mit.edu/courses/political-science/17-000j-political-philosophy-global-justice-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-000j-political-philosophy-global-justice-spring-2003/17-000js03.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1675, 'Topics in Philosophy of Language: Modeling Representation', 'The seminar will be devoted to understanding what we''re up to when we ascribe contents to a person''s assertions and mental attitudes.', 'The seminar will be devoted to understanding what we''re up to when we ascribe contents to a person''s assertions and mental attitudes. We seek to make clear the rules of the game for the philosophy of language. We''ll survey classic discussions of the issue by Field, Lewis and Stalnaker. But much of the emphasis of the class will be on getting clear about the limitations of our theoretical tools. I''d like to focus on places where our theorizing runs into trouble, or breaks down altogether.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-729-topics-in-philosophy-of-language-modeling-representation-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-729-topics-in-philosophy-of-language-modeling-representation-spring-2009/24-729s09.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1676, 'Topics in Theory of Knowledge: A Priori Knowledge', 'The seminar will explore the phenomenon of a priori knowledge.', 'The seminar will explore the phenomenon of a priori knowledge. We''ll consider some notable attempts to account for a priori knowledge in the history of philosophy (e.g., by Plato, Descartes, Hume, and Kant), some influential critiques of the notion; we will end by considering some contemporary approaches to the a priori.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-805-topics-in-theory-of-knowledge-a-priori-knowledge-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-805-topics-in-theory-of-knowledge-a-priori-knowledge-spring-2005/24-805s05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1677, 'Topics in Philosophy of Science: Social Science', 'This course offers an advanced survey of current debates about the ontology, methodology, and aims of the social sciences.', 'This course offers an advanced survey of current debates about the ontology, methodology, and aims of the social sciences.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-810-topics-in-philosophy-of-science-social-science-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-810-topics-in-philosophy-of-science-social-science-fall-2006/24-810f06.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1678, 'Classification, Natural Kinds, and Conceptual Change: Race as a Case Study', 'T', 'This course will consider the claim that there is no such thing as race, with a particular emphasis on the question whether races should be thought of as natural kinds: is our concept of race a natural kind concept? Is the term ''race'' a natural kind term? If so, is Appiah right to conclude that there are no races? How should one go about ''analyzing'' the concept of race?', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-892-classification-natural-kinds-and-conceptual-change-race-as-a-case-study-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-892-classification-natural-kinds-and-conceptual-change-race-as-a-case-study-spring-2004/24-892s04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1679, 'Language and Its Structure I: Phonology', '24.', '24.901 is designed to give you a preliminary understanding of how the sound systems of different languages are structured, how and why they may differ from each other. The course also aims to provide you with analytical tools in phonology, enough to allow you to sketch the analysis of an entire phonological system by the end of the term. On a non-linguistic level, the course aims to teach you by example the virtues of formulating precise and explicit descriptive statements; and to develop your skills in making and evaluating arguments.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-901-language-and-its-structure-i-phonology-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-901-language-and-its-structure-i-phonology-fall-2010/24-901f10.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1680, 'Language and its Structure III: Semantics and Pragmatics', 'This course gives an introduction to the science of linguistic meaning.', 'This course gives an introduction to the science of linguistic meaning. There are two branches to this discipline: semantics, the study of conventional, ''compositional meaning'', and pragmatics, the study of interactional meaning. There are other contributaries: philosophy, logic, syntax, and psychology. We will try to give you an understanding of the concepts of semantics and pragmatics and of some of the technical tools that we use.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-903-language-and-its-structure-iii-semantics-and-pragmatics-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-903-language-and-its-structure-iii-semantics-and-pragmatics-spring-2005/24-903s05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1681, 'The Lexicon and Its Features', 'This course provides an overview of the distinctive features which distinguish sound categories of languages of the world.', 'This course provides an overview of the distinctive features which distinguish sound categories of languages of the world. Theories which relate these categories to their acoustic and articulatory correlates, both universally and in particular languages, are covered. Models of word recognition by listeners, features, and phonological structure are also discussed. In addition, the course offers a variety of perspectives on these issues, drawn from Electrical Engineering, Linguistics and Cognitive Science.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-941j-the-lexicon-and-its-features-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-941j-the-lexicon-and-its-features-spring-2007/24-941js07.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1682, 'Language Processing', 'This course is a seminar in real-time language comprehension.', 'This course is a seminar in real-time language comprehension. It considers models of sentence and discourse comprehension from the linguistic, psychology, and artificial intelligence literature, including symbolic and connectionist models. Topics include ambiguity resolution and linguistic complexity; the use of lexical, syntactic, semantic, pragmatic, contextual and prosodic information in language comprehension; the relationship between the computational resources available in working memory and the language processing mechanism; and the psychological reality of linguistic representations.', 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-591j-language-processing-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-591j-language-processing-fall-2004/9-591jf04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1683, 'Language Acquisition I', 'Lectures, reading, and discussion of current theory and data concerning the psychology and biology of language acquisition.', 'Lectures, reading, and discussion of current theory and data concerning the psychology and biology of language acquisition. Emphasizes learning of syntax and morphology, together with some discussion of phonology, and especially research relating grammatical theory and learnability theory to empirical studies of children.', 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-601j-language-acquisition-i-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-601j-language-acquisition-i-spring-2002/9-601s02.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1684, 'Introduction to Syntax', 'This course is concerned with the concepts and principles which have been of central significance in the recent development of syntactic theory, with special focus on the ''Government and Binding'' (GB) / ''Principles and Parameters'' (P&amp;P) / ''Minimalist Program'' (MP) approach.', 'This course is concerned with the concepts and principles which have been of central significance in the recent development of syntactic theory, with special focus on the ''Government and Binding'' (GB) / ''Principles and Parameters'' (P&amp;P) / ''Minimalist Program'' (MP) approach. It is the first of a series of two courses (24.951 is taught during the Fall and 24.952 is taught in the Spring). This course deals mostly with phrase structure, argument structure and its syntactic expression, including ''A-movement''. Though other issues (e.g. wh-movement, antecedent-contained deletion, extraposition) may be mentioned during the semester, the course will not systematically investigate these topics in class until 24.952. The goal of the course is to understand why certain problems have been treated in certain ways. Thus, on many occasions a variety of approaches will be discussed, and the (recent) historical development of these approaches are emphasized.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-951-introduction-to-syntax-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-951-introduction-to-syntax-fall-2003/24-951f03.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1685, 'Argument Structure and Syntax', 'This course is a detailed investigation of the major issues and problems in the study of lexical argument structure and how it determines syntactic structure.', 'This course is a detailed investigation of the major issues and problems in the study of lexical argument structure and how it determines syntactic structure. Its empirical scope&#160; is along three dimensions: typology, lexical class, and theoretical framework. The range of linguistic types include English, Japanese, Navajo, and Warlpiri. Lexical classes include those of Levin''s English Verb Classes and others producing emerging work on diverse languages. The theoretical emphasis of this course is on structural relations among elements of argument structure.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-953-argument-structure-and-syntax-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-953-argument-structure-and-syntax-spring-2003/24-953s03.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1686, 'Pragmatics in Linguistic Theory', 'This course is the third and final part of our graduate introduction to semantics.', 'This course is the third and final part of our graduate introduction to semantics. The other two classes are 24.970 Introduction to Semantics and 24.973 Advanced Semantics. The semester will be divided into somewhat independent units. One unit will be devoted to conversational implicatures (mainly scalar implicatures) and another to presupposition. In each unit, we will discuss basic concepts and technical tools and then devote some time to recent work which illustrates their application.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-954-pragmatics-in-linguistic-theory-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-954-pragmatics-in-linguistic-theory-spring-2010/24-954s10.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1687, 'Pragmatics in Linguistic Theory', 'The course introduces formal theories of context-dependency, presupposition, implicature, context-change, focus and topic.', 'The course introduces formal theories of context-dependency, presupposition, implicature, context-change, focus and topic. Special emphasis is on the division of labor between semantics and pragmatics. It also covers applications to the analysis of quantification, definiteness, presupposition projection, conditionals and modality, anaphora, questions and answers.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-954-pragmatics-in-linguistic-theory-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-954-pragmatics-in-linguistic-theory-fall-2006/24-954f06.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1688, 'Introduction to Phonology', 'The year-long Introduction to Phonology reviews at the graduate level fundamental notions of phonological analysis and introduces students to current debates, research and analytical techniques.', 'The year-long Introduction to Phonology reviews at the graduate level fundamental notions of phonological analysis and introduces students to current debates, research and analytical techniques. The Fall term reviews issues pertaining to the nature of markedness and phonological representations - features, prosodies, syllables and stress - while the second term deals with the relation between the phonological component and the lexicon, morphology and syntax. The second term course will also treat in more detail certain phonological phenomena.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-961-introduction-to-phonology-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-961-introduction-to-phonology-fall-2002/24-961f02.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1689, 'Advanced Phonology', 'This course focuses on phonological phenomena that are sensitive to morphological structure, including base-reduplicant identity, cyclicity, level ordering, derived environment effects, opaque rule interactions, and morpheme structure constraints.', 'This course focuses on phonological phenomena that are sensitive to morphological structure, including base-reduplicant identity, cyclicity, level ordering, derived environment effects, opaque rule interactions, and morpheme structure constraints. In the recent OT literature, it has been claimed that all of these phenomena can be analyzed with a single theoretical device: correspondence constraints, which regulate the similarity of lexically related forms (such as input and output, base and derivative, base and reduplicant).', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-962-advanced-phonology-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-962-advanced-phonology-spring-2005/24-962s05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1690, 'Linguistic Phonetics', 'This course is a study of speech sounds: how we produce and perceive them and their acoustic properties.', 'This course is a study of speech sounds: how we produce and perceive them and their acoustic properties. It explores the influence of the production and perception systems on phonological patterns and sound change. Acoustic analysis and experimental techniques are also discussed.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-963-linguistic-phonetics-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-963-linguistic-phonetics-fall-2005/24-963f05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1691, 'Topics in Phonology', 'This course introduces students to the theory and practice of modeling phonology, with an empirical focus on modeling the discovery of static phonotactics, the discovery of alternations, learning in the midst of variation and exceptions, and the discovery of gradient patterns.', 'This course introduces students to the theory and practice of modeling phonology, with an empirical focus on modeling the discovery of static phonotactics, the discovery of alternations, learning in the midst of variation and exceptions, and the discovery of gradient patterns. This course is also intended to provide hands-on experience with various aspects of using and developing models, including preparing training data, running simulations, and interpreting their results.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-964-topics-in-phonology-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-964-topics-in-phonology-fall-2004/24-964f04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1692, 'Laboratory on the Physiology, Acoustics, and Perception of Speech', 'The course focuses on experimental investigations of speech processes.', 'The course focuses on experimental investigations of speech processes. Topics include: measurement of articulatory movements, measurements of pressures and airflows in speech production, computer-aided waveform analysis and spectral analysis of speech, synthesis of speech, perception and discrimination of speechlike sounds, speech prosody, models for speech recognition, speech disorders, and other topics. Two 1-hour lectures per week Two labs per week Brief lab reports Term project, with short term paper No exams', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-542j-laboratory-on-the-physiology-acoustics-and-perception-of-speech-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-542j-laboratory-on-the-physiology-acoustics-and-perception-of-speech-fall-2005/6-542jf05.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1693, 'Speech Communication', '6.', '6.541J surveys the structural properties of natural languages, with special emphasis on the sound pattern. Topics covered include: representation of the lexicon; physiology of speech production; articulatory phonetics; acoustical theory of speech production; acoustical and articulatory descriptions of phonetic features and of prosodic aspects of speech; perception of speech; models of lexical access and of speech production and planning; and applications to recognition and generation of speech by machine, and to the study of speech disorders.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-541j-speech-communication-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-541j-speech-communication-spring-2004/6-541js04.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1694, 'Topics in Semantics', 'This seminar will investigate the expression of gradability, comparison and degree in natural language.', 'This seminar will investigate the expression of gradability, comparison and degree in natural language. The course will address the following major issues, though we may also include other topics according to the interests and direction of the class: The underlying semantic representation of expressions of degree and the linguistic significance of scalar representations The logical form of comparative constructions, and the implications of comparatives for other aspects of the grammar The semantics of degree and the role of scalar representations in grammatical categories other than gradable adjectives', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-979-topics-in-semantics-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-979-topics-in-semantics-fall-2002/24-979f02.jpg', 'Linguistics and Philosophy', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1695, 'Writing About Literature', 'Students, scholars, bloggers, reviewers, fans, and book-group members write about literature, but so do authors themselves.', 'Students, scholars, bloggers, reviewers, fans, and book-group members write about literature, but so do authors themselves. Through the ways they engage with their own texts and those of other artists, sampling, remixing, and rethinking texts and genres, writers reflect on and inspire questions about the creative process. We will examine Mary Shelley''s reshaping of Milton''s Paradise Lost, German fairy tales, tales of scientific discovery, and her husband''s poems to make Frankenstein (1818, 1831); Melville''s redesign of a travel narrative into a Gothic novella in Benito Cereno (1856); and Alison Bechdel''s rewriting of Oscar Wilde''s The Importance of Being Earnest (1895) in her graphic novel Fun Home (2006). Showings of film versions of some of these works will allow us to project forward in the remixing process as well.', 'http://ocw.mit.edu/courses/literature/21l-000j-writing-about-literature-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-000j-writing-about-literature-fall-2010/21l-000jf10.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1696, 'Foundations of Western Culture: Homer to Dante', 'As we read broadly from throughout the vast chronological period that is ''Homer to Dante,'' we will pepper our readings of individual ancient and medieval texts with broader questions like: what images, themes, and philosophical questions recur through the period; are there distinctly ''classical'' or ''medieval'' ways of depicting or addressing them; and what do terms like ''Antiquity'' or ''the Middle Ages'' even mean? (What are the Middle Ages in the ''middle'' of, for example?) Our texts will include adventure tales of travel and self-discovery (Homer''s Odyssey and Dante''s Inferno); courtroom dramas of vengeance and reconciliation (Aeschylus''s Oresteia and the Icelandic Nj&aacute;ls saga); short poems of love and transformation (Ovid''s Metamorphoses and the Lais of Marie de France); and epics of war, nation-construction, and empire (Homer''s Iliad, Virgil''s Aeneid, and the Anglo-Saxon Beowulf).', 'As we read broadly from throughout the vast chronological period that is ''Homer to Dante,'' we will pepper our readings of individual ancient and medieval texts with broader questions like: what images, themes, and philosophical questions recur through the period; are there distinctly ''classical'' or ''medieval'' ways of depicting or addressing them; and what do terms like ''Antiquity'' or ''the Middle Ages'' even mean? (What are the Middle Ages in the ''middle'' of, for example?) Our texts will include adventure tales of travel and self-discovery (Homer''s Odyssey and Dante''s Inferno); courtroom dramas of vengeance and reconciliation (Aeschylus''s Oresteia and the Icelandic Nj&aacute;ls saga); short poems of love and transformation (Ovid''s Metamorphoses and the Lais of Marie de France); and epics of war, nation-construction, and empire (Homer''s Iliad, Virgil''s Aeneid, and the Anglo-Saxon Beowulf).', 'http://ocw.mit.edu/courses/literature/21l-001-foundations-of-western-culture-homer-to-dante-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-001-foundations-of-western-culture-homer-to-dante-fall-2008/21l-001f08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1697, 'Foundations of World Culture I: World Civilizations and Texts', 'This course aims to introduce students to the rich diversity of human culture from antiquity to the early 17th century.', 'This course aims to introduce students to the rich diversity of human culture from antiquity to the early 17th century. In this course, we will explore human culture in its myriad expressions, focusing on the study of literary, religious and philosophical texts as ways of narrating, symbolizing, and commenting on all aspects of human social and material life. We will work comparatively, reading texts from various cultures: Mesopotamian, Greek, Judeo-Christian, Chinese, Indian, and Muslim. Throughout the semester, we will be asking questions like: How have different cultures imagined themselves? What are the rules that they draw up for human behavior? How do they represent the role of the individual in society? How do they imagine ''universal'' concepts like love, family, duty? How have their writers and artists dealt with encounters with other cultures and other civilizations?', 'http://ocw.mit.edu/courses/literature/21l-001x-foundations-of-world-culture-i-world-civilizations-and-texts-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-001x-foundations-of-world-culture-i-world-civilizations-and-texts-fall-2011/21L-001xf11.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1698, 'Foundations of Western Culture II', 'Complementary to 21L.', 'Complementary to 21L.001. A broad survey of texts - literary, philosophical, and sociological - studied to trace the growth of secular humanism, the loss of a supernatural perspective upon human events, and changing conceptions of individual, social, and communal purpose. Stresses appreciation and analysis of texts that came to represent the common cultural possession of our time. Enrollment limited. HASS-D, CI. Readings this semester ranging from political theory and oratory to autobiography, poetry, and science fiction reflect on war, motives for war, reconciliation and memory. The readings are largely organized around three historical moments: the Renaissance and first contacts between Europe and America (Machiavelli, Cort&eacute;s, Sahag&uacute;n); the European age of revolutions (Voltaire, Blake, Williams); the American Civil War and the abolition of slavery (Stowe, Whitman, Lincoln). Readings from the twentieth-century include poetry by Lowell and Walcott and fiction by Ondaatje and O.S. Card.', 'http://ocw.mit.edu/courses/literature/21l-002-foundations-of-western-culture-ii-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-002-foundations-of-western-culture-ii-fall-2002/21l-002f02.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1699, 'Foundations of Western Culture II: Renaissance to Modernity', 'This subject offers a broad survey of texts (both literary and philosophical) drawn from the Western tradition and selected to trace the growth of ideas about the nature of mankind''s ethical and political life in the West since the renaissance.', 'This subject offers a broad survey of texts (both literary and philosophical) drawn from the Western tradition and selected to trace the growth of ideas about the nature of mankind''s ethical and political life in the West since the renaissance. It will deal with the change in perspective imposed by scientific ideas, the general loss of a supernatural or religious perspective upon human events, and the effects for good or ill of the increasing authority of&#160;an intelligence uninformed by religion as a guide to life.&#160;The readings are roughly complementary to the readings in 21L001, and classroom discussion will stress appreciation and analysis of texts that came to represent the cultural heritage of the modern world.', 'http://ocw.mit.edu/courses/literature/21l-002-2-foundations-of-western-culture-ii-renaissance-to-modernity-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-002-2-foundations-of-western-culture-ii-renaissance-to-modernity-spring-2003/21l-002-2s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1700, 'Foundations of World Culture II: World Literatures and Texts', 'This class continues our study of the foundational texts of human culture, focusing on early modernity until the recent past.', 'This class continues our study of the foundational texts of human culture, focusing on early modernity until the recent past. In many ways, this includes several questions such as: Why did these works achieve the fame and influence they achieved? How do they present what it means to be a human being? How do they describe the role of a member of a family, community, tradition, social class, gender? How do they distinguish between proper and improper behavior? How do they characterize the members of other groups? However, in several ways, these texts are also iconoclastic, breaking with centuries of established tradition to shed light on previously unexplored subjects, such as the status of women in society or the legacy of the colonial expansion of European countries. They also question well-established social beliefs like religion, monarchical rule and human nature in general.', 'http://ocw.mit.edu/courses/literature/21l-002x-foundations-of-world-culture-ii-world-literatures-and-texts-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-002x-foundations-of-world-culture-ii-world-literatures-and-texts-spring-2012/21l-002xs12.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1701, 'Introduction to Fiction', 'This course investigates the uses and boundaries of fiction in a range of novels and narrative styles--traditional and innovative, western and nonwestern--and raises questions about the pleasures and meanings of verbal texts in different cultures, times, and forms.', 'This course investigates the uses and boundaries of fiction in a range of novels and narrative styles--traditional and innovative, western and nonwestern--and raises questions about the pleasures and meanings of verbal texts in different cultures, times, and forms. Toward the end of the term, we will be particularly concerned with the relationship between art and war in a diverse selection of works.', 'http://ocw.mit.edu/courses/literature/21l-003-introduction-to-fiction-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-003-introduction-to-fiction-fall-2003/21l-003f03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1702, 'Introduction to Fiction', 'This course investigates the uses and boundaries of fiction in a range of novels and narrative styles, traditional and innovative, western and non-western, and raises questions about the pleasures and meanings of verbal texts in different cultures, times, and forms.', 'This course investigates the uses and boundaries of fiction in a range of novels and narrative styles, traditional and innovative, western and non-western, and raises questions about the pleasures and meanings of verbal texts in different cultures, times, and forms.', 'http://ocw.mit.edu/courses/literature/21l-003-introduction-to-fiction-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-003-introduction-to-fiction-spring-2002/21l-003s02.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1703, 'Reading Fiction: Dysfunctional Families', 'This course explores the form, content, and historical context of various works of fiction specifically through the thematic lens of ''dysfunctional families.', 'This course explores the form, content, and historical context of various works of fiction specifically through the thematic lens of ''dysfunctional families.'' We will focus primarily on questions pertaining to the structure, language, story, and characters of these fictional works.', 'http://ocw.mit.edu/courses/literature/21l-003-1-reading-fiction-dysfunctional-families-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-003-1-reading-fiction-dysfunctional-families-spring-2007/21l-003-1s07.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1704, 'Reading Fiction', 'This course introduces prose narrative, both short stories and the novel.', 'This course introduces prose narrative, both short stories and the novel. It examines the construction of narrative and the analysis of literary response.', 'http://ocw.mit.edu/courses/literature/21l-003-2-reading-fiction-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-003-2-reading-fiction-fall-2006/21l-003-2f06.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1705, 'Reading Poetry', '&quot;Reading Poetry&quot; has several aims: primarily, to increase the ways you can become more engaged and curious readers of poetry; to increase your confidence as writers thinking about literary texts; and to provide you with the language for literary description.', '&quot;Reading Poetry&quot; has several aims: primarily, to increase the ways you can become more engaged and curious readers of poetry; to increase your confidence as writers thinking about literary texts; and to provide you with the language for literary description. The course is not designed as a historical survey course but rather as an introductory approach to poetry from various directions &ndash; as public or private utterances; as arranged imaginative shapes; and as psychological worlds, for example. One perspective offered is that poetry offers intellectual, moral and linguistic pleasures as well as difficulties to our private lives as readers and to our public lives as writers. Expect to hear and read poems aloud and to memorize lines; the class format will be group discussion, occasional lecture.', 'http://ocw.mit.edu/courses/literature/21l-004-reading-poetry-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-004-reading-poetry-spring-2009/21l-004s09.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1706, 'Major Poets', 'This subject is an introduction to poetry as a genre; most of our texts are originally written in English.', 'This subject is an introduction to poetry as a genre; most of our texts are originally written in English. We read poems from the Renaissance through the 17th and 18th centuries, Romanticism, and Modernism. Focus will be on analytic reading, on literary history, and on the development of the genre and its forms; in writing we attend to techniques of persuasion and of honest evidenced sequential argumentation. Poets to be read will include William Shakespeare, Queen Elizabeth, William Wordsworth, John Keats, T.S. Eliot, Langston Hughes, Sylvia Plath, Elizabeth Bishop, and some contemporary writers.', 'http://ocw.mit.edu/courses/literature/21l-004-major-poets-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-004-major-poets-fall-2005/21l-004f05.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1707, 'Introduction to Drama', 'Drama might be described as a game played with something sacred.', 'Drama might be described as a game played with something sacred. It tells stories that go right to the heart of what people believe about themselves. And it is enacted in the moment, which means it has an added layer of interpretive mystery and playfulness, or ''theatricality.'' This course will explore theater and theatricality across periods and cultures, through intensive engagement with texts and with our own readings.', 'http://ocw.mit.edu/courses/literature/21l-005-introduction-to-drama-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-005-introduction-to-drama-fall-2008/21l-005f08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1708, 'Introduction to Drama', 'Drama combines the literary arts of storytelling and poetry with the world of live performance.', 'Drama combines the literary arts of storytelling and poetry with the world of live performance.&nbsp;As a form of ritual as well as entertainment, drama has served to unite communities and challenge social norms, to vitalize and disturb its audiences.&nbsp;In order to understand this rich art form more fully, we will study and discuss a sampling of plays that exemplify different kinds of dramatic structure; class members will also participate in, attend, and review dramatic performances.', 'http://ocw.mit.edu/courses/literature/21l-005-introduction-to-drama-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-005-introduction-to-drama-fall-2004/21l-005f04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1709, 'American Literature', 'This is a HASS-D CI course.', 'This is a HASS-D CI course. Like other communications-intensive courses in the humanities, arts, and social sciences, it allows students to produce 20 pages of polished writing with careful attention to revision. It also offers substantial opportunities for oral expression, through presentations of written work, student-led discussion, and class participation.&nbsp;The class has a low enrollment that ensures maximum attention to student writing and opportunity for oral expression, and a writing fellow/tutor is available for consultation on drafts and revisions.', 'http://ocw.mit.edu/courses/literature/21l-006-american-literature-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-006-american-literature-fall-2002/21l-006f02.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1710, 'After Columbus', 'Sometime after 1492, the concept of the New World or America came into being, and this concept appeared differently - as an experience or an idea - for different people and in different places.', 'Sometime after 1492, the concept of the New World or America came into being, and this concept appeared differently - as an experience or an idea - for different people and in different places. This semester, we will read three groups of texts: first, participant accounts of contact between native Americans and French or English speaking Europeans, both in North America and in the Caribbean and Brazil; second, transformations of these documents into literary works by contemporaries; third, modern texts which take these earlier materials as a point of departure for rethinking the experience and aftermath of contact. The reading will allow us to compare perspectives across time and space, across the cultural geographies of religion, nation and ethnicity, and finally across a range of genres - reports, captivity narratives, essays, novels, poetry, drama, and film. Some of the earlier authors we will read are Michel Montaigne, William Shakespeare, Jean de L&#233;ry, Daniel Defoe and Mary Rowlandson; more recent authors include Derek Walcott, and J. M. Coetzee.', 'http://ocw.mit.edu/courses/literature/21l-007j-after-columbus-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-007j-after-columbus-fall-2003/21l-007jf03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1711, 'Black Matters: Introduction to Black Studies', 'This class is an interdisciplinary survey that explores the experiences of people of African descent through the overlapping approaches of history, literature, anthropology, legal studies, media studies, performance, linguistics, and creative writing.', 'This class is an interdisciplinary survey that explores the experiences of people of African descent through the overlapping approaches of history, literature, anthropology, legal studies, media studies, performance, linguistics, and creative writing. It connects the experiences of African Americans and of other American minorities, focusing on social, political, and cultural histories, and on linguistic patterns. Activities include lectures, discussions, workshops, and required field trips that involve minimal cost to students.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-630j-black-matters-introduction-to-black-studies-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-630j-black-matters-introduction-to-black-studies-fall-2009/21m-630jf09.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1712, 'Shakespeare', 'Three hundred and eighty years after his death, William Shakespeare remains the central author of the English-speaking world; he is the most quoted poet and the most regularly produced playwright &#8212; and now among the most popular screenwriters as well.', 'Three hundred and eighty years after his death, William Shakespeare remains the central author of the English-speaking world; he is the most quoted poet and the most regularly produced playwright &#8212; and now among the most popular screenwriters as well. Why is that, and who ''is'' he?&#160;Why do so many people think his writing is so great? What meanings did his plays have in his own time, and how do we read, speak, or listen to his words now? What should we watch for when viewing his plays in performance?&#160;Whose plays are we watching, anyway? We''ll consider these questions as we carefully examine a sampling of Shakespeare''s plays from a variety of critical perspectives.', 'http://ocw.mit.edu/courses/literature/21l-009-shakespeare-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-009-shakespeare-spring-2004/21l-009s04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1713, 'The Film Experience', 'This course concentrates on close analysis and criticism of a wide range of films, including works from the early silent period, documentary and avant-garde films, European art cinema, and contemporary Hollywood fare.', 'This course concentrates on close analysis and criticism of a wide range of films, including works from the early silent period, documentary and avant-garde films, European art cinema, and contemporary Hollywood fare. Through comparative reading of films from different eras and countries, students develop the skills to turn their in-depth analyses into interpretations and explore theoretical issues related to spectatorship. Syllabus varies from term to term, but usually includes such directors as Coppola, Eisentein, Fellini, Godard, Griffith, Hawks, Hitchcock, Kubrick, Kurosawa, Tarantino, Welles, Wiseman, and Zhang.', 'http://ocw.mit.edu/courses/literature/21l-011-the-film-experience-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-011-the-film-experience-fall-2012/21l-011f12.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1714, 'Forms of Western Narrative', 'This course examines some leading examples of major genres of storytelling in the Western tradition, among them epic (Homer''s Odyssey), romance (from the Arthurian tradition), and novel (Cervantes''s Don Quixote).', 'This course examines some leading examples of major genres of storytelling in the Western tradition, among them epic (Homer''s Odyssey), romance (from the Arthurian tradition), and novel (Cervantes''s Don Quixote). We will be asking why people tell (and have always told) stories, how they tell them, why they might tell them the way they do, and what difference it makes how they tell them. We''ll combine an investigation of the changing formal properties of narratives with consideration of the historical, cultural, and technological factors that have influenced how tales got told. In keeping with its CI-H and HASS-D label, this course will involve substantial attention to students'' writing and speaking abilities.', 'http://ocw.mit.edu/courses/literature/21l-012-forms-of-western-narrative-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-012-forms-of-western-narrative-fall-2007/21l-012f07.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1715, 'Forms of Western Narrative', 'This class will investigate the ways in which the formal aspects of Western storytelling in various media have shaped both fantasies and perceptions, making certain understandings of experience possible through the selection, arrangement, and processing of narrative material.', 'This class will investigate the ways in which the formal aspects of Western storytelling in various media have shaped both fantasies and perceptions, making certain understandings of experience possible through the selection, arrangement, and processing of narrative material. Surveying the field chronologically across the major narrative genres and sub-genres from Homeric epic through the novel and across media to include live performance, film, and video games, we will be examining the ways in which new ideologies and psychological insights become available through the development of various narrative techniques and new technologies. Emphasis will be placed on the generic conventions of story-telling as well as on literary and cultural issues, the role of media and modes of transmission, the artistic significance of the chosen texts and their identity as anthropological artifacts whose conventions and assumptions are rooted in particular times, places, and technologies. Authors will include: Homer, Sophocles, Herodotus, Christian evangelists, Marie de France, Cervantes, La Clos, Poe, Lang, Cocteau, Disney-Pixar, and Maxis-Electronic Arts, with theoretical readings in Propp, Bakhtin, Girard, Freud, and Marx.', 'http://ocw.mit.edu/courses/literature/21l-012-forms-of-western-narrative-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-012-forms-of-western-narrative-spring-2004/21l-012s04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1716, 'Introduction to Media Studies', 'Introduction to Media Studies is designed for students who have grown up in a rapidly changing global multimedia environment and want to become more literate and critical consumers and producers of culture.', 'Introduction to Media Studies is designed for students who have grown up in a rapidly changing global multimedia environment and want to become more literate and critical consumers and producers of culture. Through an interdisciplinary comparative and historical lens, the course defines ''media'' broadly as including oral, print, theatrical, photographic, broadcast, cinematic, and digital cultural forms and practices. The course looks at the nature of mediated communication, the functions of media, the history of transformations in media and the institutions that help define media''s place in society. Over the course of the semester we explore different theoretical perspectives on the role and power of media in society in influencing our social values, political beliefs, identities and behaviors. Students also have the opportunity to analyze specific media texts (such as films and television shows) and explore the meaning of the changes that occur when a particular narrative is adapted into different media forms. We look at the ways in which the politics of class, gender and race influence both the production and reception of media. To represent different perspectives on media, several guest speakers also present lectures. Through the readings, lectures, and discussions as well as their own writing and oral presentations, students have multiple opportunities to engage with critical debates in the field as well as explore the role of media in their own lives.', 'http://ocw.mit.edu/courses/literature/21l-015-introduction-to-media-studies-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-015-introduction-to-media-studies-fall-2003/21l-015f03.gif', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1717, 'Learning from the Past: Drama, Science, Performance', 'This class explores the creation (and creativity) of the modern scientific and cultural world through study of western Europe in the 17th century, the age of Descartes and Newton, Shakespeare, Milton and Ford.', 'This class explores the creation (and creativity) of the modern scientific and cultural world through study of western Europe in the 17th century, the age of Descartes and Newton, Shakespeare, Milton and Ford. It compares period thinking to present-day debates about the scientific method, art, religion, and society. This team-taught, interdisciplinary subject draws on a wide range of literary, dramatic, historical, and scientific texts and images, and involves theatrical experimentation as well as reading, writing, researching and conversing. The primary theme of the class is to explore how England in the mid-seventeenth century became ''a world turned upside down'' by the new ideas and upheavals in religion, politics, and philosophy, ideas that would shape our modern world. Paying special attention to the ''theatricality'' of the new models and perspectives afforded by scientific experimentation, the class will read plays by Shakespeare, Tate, Brecht, Ford, Churchill, and Kushner, as well as primary and secondary texts from a wide range of disciplines. Students will also compose and perform in scenes based on that material.', 'http://ocw.mit.edu/courses/literature/21l-016-learning-from-the-past-drama-science-performance-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-016-learning-from-the-past-drama-science-performance-spring-2009/21l-016s09.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1718, 'The Art of the Probable: Literature and Probability', '''The Art of the Probable'' addresses the history of scientific ideas, in particular the emergence and development of mathematical probability.', '''The Art of the Probable'' addresses the history of scientific ideas, in particular the emergence and development of mathematical probability. But it is neither meant to be a history of the exact sciences per se nor an annex to, say, the Course 6 curriculum in probability and statistics. Rather, our objective is to focus on the formal, thematic, and rhetorical features that imaginative literature shares with texts in the history of probability. These shared issues include (but are not limited to): the attempt to quantify or otherwise explain the presence of chance, risk, and contingency in everyday life; the deduction of causes for phenomena that are knowable only in their effects; and, above all, the question of what it means to think and act rationally in an uncertain world. Our course therefore aims to broaden students'' appreciation for and understanding of how literature interacts with &ndash; both reflecting upon and contributing to &ndash; the scientific understanding of the world. We are just as centrally committed to encouraging students to regard imaginative literature as a unique contribution to knowledge in its own right, and to see literary works of art as objects that demand and richly repay close critical analysis. It is our hope that the course will serve students well if they elect to pursue further work in Literature or other discipline in SHASS, and also enrich or complement their understanding of probability and statistics in other scientific and engineering subjects they elect to take.', 'http://ocw.mit.edu/courses/literature/21l-017-the-art-of-the-probable-literature-and-probability-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-017-the-art-of-the-probable-literature-and-probability-spring-2008/21l-017s08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1719, 'Bestsellers: Detective Fiction', 'This course focuses on works that caught the popular imagination in the past or present.', 'This course focuses on works that caught the popular imagination in the past or present. It emphasizes texts that are related by genre, theme or style. The books studied in this course vary from semester to semester, and the topic for Fall 2006 is Detective Fictions.', 'http://ocw.mit.edu/courses/literature/21l-310-bestsellers-detective-fiction-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-310-bestsellers-detective-fiction-fall-2006/21l-310f06.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1720, 'Prizewinners', 'This 6-unit subject gives students the opportunity to immerse themselves in the poetry of two living Nobel Laureates: the Caribbean poet, Derek Walcott, and the Northern-Irish poet, Seamus Heaney.', 'This 6-unit subject gives students the opportunity to immerse themselves in the poetry of two living Nobel Laureates: the Caribbean poet, Derek Walcott, and the Northern-Irish poet, Seamus Heaney. We will begin and end the semester with their magnificent epic works: Heaney''s translation of the Anglo-Saxon epic Beowulf, and Walcott''s Omeros (a modern epic set in the West Indies). Between these major narrative poems, we will read a rich selection of their shorter poems, as well as some of their reflections in prose on what poetry does, on what other poets do, and what it means to write in English from the historical and political situation of Northern Ireland (for Heaney) or the Caribbean (for Walcott).', 'http://ocw.mit.edu/courses/literature/21l-315-prizewinners-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-315-prizewinners-spring-2007/21l-315s07.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1721, 'Small Wonders: Media, Modernity, and the Moment: Experiments in Time', 'The &quot;small wonders&quot; to which our course will attend are moments of present time, depicted in the verbal and visual media of the modern age: newspapers, novels and stories, poems, photographs, films, etc.', 'The &quot;small wonders&quot; to which our course will attend are moments of present time, depicted in the verbal and visual media of the modern age: newspapers, novels and stories, poems, photographs, films, etc. We will move between visual and verbal media across a considerable span of time, from eighteenth-century poetry and prose fiction to twenty-first century social networking and microblogging sites, and from sculpture to photography, film, and digital visual media. With help from philosophers, contemporary cultural historians, and others, we will begin to think about a media practice largely taken for granted in our own moment.', 'http://ocw.mit.edu/courses/literature/21l-325-small-wonders-media-modernity-and-the-moment-experiments-in-time-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-325-small-wonders-media-modernity-and-the-moment-experiments-in-time-fall-2010/21l-325f10.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1722, 'Literary Studies: The Legacy of England', 'Topic: The English sense of humor.', 'Topic: The English sense of humor. This course examines English literature across genre and historical periods. It is designed for students who want to study English literature or writing in some depth, or to know more about English literary culture and history. Students will also learn about the relationships between literary themes, forms, and conventions and the times in which they were produced. Materials include: Medieval tales, riddles, and character sketches; Renaissance lyrics and a play, 18th-century satires in words and images, 19th century irony, modern stories and film.', 'http://ocw.mit.edu/courses/literature/21l-420-literary-studies-the-legacy-of-england-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-420-literary-studies-the-legacy-of-england-spring-2006/21l-420s06.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1723, 'Comedy', 'This course looks at comedy in drama, novels, and films from Classical Greece to the twentieth century.', 'This course looks at comedy in drama, novels, and films from Classical Greece to the twentieth century. Focusing on examples from Aristophanes, Shakespeare, Cervantes, Moli&egrave;re, Wilde, Chaplin, and Billy Wilder, along with theoretical contexts, the class examines comedy as a transgressive mode with revolutionary social and political implications. This is a Communications Intensive (CI) class with emphasis on discussion, and frequent, short essays.', 'http://ocw.mit.edu/courses/literature/21l-421-comedy-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-421-comedy-spring-2008/21l-421s08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1724, 'Tragedy', '&quot;Tragedy&quot; is a name originally applied to a particular kind of dramatic art and subsequently to other literary forms; it has also been applied to particular events, often implying thereby a particular view of life.', '&quot;Tragedy&quot; is a name originally applied to a particular kind of dramatic art and subsequently to other literary forms; it has also been applied to particular events, often implying thereby a particular view of life. Throughout the history of Western literature it has sustained this double reference. Uniquely and insistently, the realm of the tragic encompasses both literature and life.Through careful, critical reading of literary texts, this subject will examine three aspects of the tragic experience:&nbsp;&nbsp;&nbsp;&nbsp;the scapegoatthe tragic herothe ethical crisisThese aspects of the tragic will be pursued in readings that range in the reference of their materials from the warfare of the ancient world to the experience of the modern extermination camps.', 'http://ocw.mit.edu/courses/literature/21l-422-tragedy-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-422-tragedy-fall-2002/21l-422f02.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1725, 'Popular Culture and Narrative: Literature, Comics, and Culture', 'In this course, we will investigate popular culture and narrative by focusing on the relationship between literary texts and comics.', 'In this course, we will investigate popular culture and narrative by focusing on the relationship between literary texts and comics. Several questions shape the syllabus and provide a framework for approaching the course materials: How do familiar aspects of comics trace their origins to literary texts and broader cultural concerns? How have classic comics gone on to influence literary fiction? In what ways do contemporary graphic narratives bring a new kind of seriousness of purpose to comics, blurring what''s left of the boundaries between the highbrow and the lowbrow? Readings and materials for the course range from the nineteenth century to the present, and include novels, short stories, essays, older and newer comics, and some older and newer films. Expectations include diligent reading, active participation, occasional discussion leading, and two papers.', 'http://ocw.mit.edu/courses/literature/21l-430-popular-culture-and-narrative-literature-comics-and-culture-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-430-popular-culture-and-narrative-literature-comics-and-culture-fall-2010/21l-430f10.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1726, 'Popular Narrative: Masterminds', 'Our purpose is to consider some of the most elaborate and thoughtful efforts to define and delineate ''all-mastering,'' and to consider some of the delineations of ''all-mastering the intellect'' in various guises - from magicians to master spies to detectives to scientists (mad and otherwise).', 'Our purpose is to consider some of the most elaborate and thoughtful efforts to define and delineate ''all-mastering,'' and to consider some of the delineations of ''all-mastering the intellect'' in various guises - from magicians to master spies to detectives to scientists (mad and otherwise). The major written work of the term will be an ongoing reading journal, which you will circulate to your classmates using an e-mail mailing list. The use of that list is fundamental - it is my intention to generate a sort of ongoing cyberconversation.', 'http://ocw.mit.edu/courses/literature/21l-430-popular-narrative-masterminds-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-430-popular-narrative-masterminds-fall-2004/21l-430f04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1727, 'Understanding Television', 'The subtitle of this course for the spring 2003 term is ''American Television: A Cultural History.', 'The subtitle of this course for the spring 2003 term is ''American Television: A Cultural History.'' The class takes a cultural approach to television''s evolution as a technology and system of representation, considering television as a system of storytelling and myth-making, and as a cultural practice, studied from anthropological, literary, and cinematic perspectives. The course focuses on prime-time commercial broadcasting, the medium''s technological and economic history, and theoretical perspectives. There is much required viewing as well as readings in media theory and cultural interpretation.', 'http://ocw.mit.edu/courses/literature/21l-432-understanding-television-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-432-understanding-television-spring-2003/21l-432s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1728, 'Darwin and Design', 'Humans are social animals; social demands, both cooperative and competitive, structure our development, our brain and our mind.', 'Humans are social animals; social demands, both cooperative and competitive, structure our development, our brain and our mind. This course covers social development, social behaviour, social cognition and social neuroscience, in both human and non-human social animals. Topics include altruism, empathy, communication, theory of mind, aggression, power, groups, mating, and morality. Methods include evolutionary biology, neuroscience, cognitive science, social psychology and anthropology.', 'http://ocw.mit.edu/courses/literature/21l-448j-darwin-and-design-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-448j-darwin-and-design-fall-2010/21l-448jf10.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1729, 'Darwin and Design', 'This subject offers a broad survey of texts (both literary and philosophical) drawn from the Western tradition and selected to trace the immediate intellectual antecedents and some of the implications of the ideas animating Darwin''s revolutionary On the Origin of Species.', 'This subject offers a broad survey of texts (both literary and philosophical) drawn from the Western tradition and selected to trace the immediate intellectual antecedents and some of the implications of the ideas animating Darwin''s revolutionary On the Origin of Species. Darwin''s text, of course, is about the mechanism that drives the evolution of life on this planet, but the fundamental ideas of the text have implications that range well beyond the scope of natural history, and the assumptions behind Darwin''s arguments challenge ideas that go much further back than the set of ideas that Darwin set himself explicitly to question - ideas of decisive importance when we think about ourselves, the nature of the material universe, the planet that we live upon, and our place in its scheme of life. In establishing his theory of natural selection, Darwin set himself, rather self-consciously, to challenge a whole way of thinking about these things. The main focus of attention will be Darwin''s contribution to the so-called ''argument from design'' - the notion that innumerable aspects of the world (and most particularly the organisms within it) display features directly analogous to objects of human design and, since design implies a designer, that an intelligent, conscious agency must have been responsible for their organization and creation. Previously, it had been argued that such features must have only one of two ultimate sources - chance or conscious agency. Darwin proposed and elaborated a third source, which he called Natural Selection, an unconscious agency capable of outdoing the most complex feats of human intelligence. The course of study will not only examine the immediate inspiration for this idea in the work of Adam Smith and Thomas Malthus and place Darwin''s Origin and the theory of Natural Selection in the history of ensuing debate, but it will also touch upon related issues.', 'http://ocw.mit.edu/courses/literature/21l-448j-darwin-and-design-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-448j-darwin-and-design-fall-2003/21l-448jf03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1730, 'End of Nature', 'This subject offers a broad survey of texts (both literary and philosophical) drawn from the Western tradition and selected to trace the growth of ideas about nature and the natural environment of mankind.', 'This subject offers a broad survey of texts (both literary and philosophical) drawn from the Western tradition and selected to trace the growth of ideas about nature and the natural environment of mankind.&#160;The term nature in this context has to do with the varying ways in which the physical world has been conceived as the habitation of mankind, a source of imperatives for the collective organization and conduct of human life. In this sense, nature is less the object of complex scientific investigation than the object of individual experience and direct observation. Using the term ''nature'' in this sense, we can say that modern reference to ''the environment'' owes much to three ideas about the relation of mankind to nature. In the first of these, which harks back to ancient medical theories and notions about weather, geographical nature was seen as a neutral agency affecting or transforming agent of mankind''s character and institutions. In the second, which derives from religious and classical sources in the Western tradition, the earth was designed as a fit environment for mankind or, at the least, as adequately suited for its abode, and civic or political life was taken to be consonant with the natural world. In the third, which also makes its appearance in the ancient world but becomes important only much later, nature and mankind are regarded as antagonists, and one must conquer the other or be subjugated by it.', 'http://ocw.mit.edu/courses/literature/21l-449-end-of-nature-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-449-end-of-nature-spring-2002/21l-449s02.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1731, 'Introduction to Literary Theory', 'This subject examines the ways in which we read.', 'This subject examines the ways in which we read. It introduces some of the different strategies of reading, comprehending and engaging with literary texts developed in the twentieth century, paying special attention to post-structuralist theories and their legacy. (What poststructuralism means will be discussed often in this course, so don''t worry if you don''t know what it means right now!) The course is organized around specific theoretical paradigms. In general, we will: (1) work through selected readings in order to see how they determine or define the task of literary interpretation; (2) locate the limits of each particular approach; and (3) trace the emergence of subsequent theoretical paradigms as responses to the achievements and limitations of what came before. The literary texts and films accompanying the theoretical material will serve as concrete cases that allow us to see theory in action. For the most part, each week will pair a text or film with a particular interpretative approach, using the former to explore the latter. Rather than attempting a definitive or full analysis of the literary or film work, we will exploit it (unashamedly &mdash; and indeed sometimes reductively) to understand better the theoretical reading it accompanies.', 'http://ocw.mit.edu/courses/literature/21l-451-introduction-to-literary-theory-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-451-introduction-to-literary-theory-spring-2010/21l-451s10.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1732, 'Classical Literature: The Golden Age of Augustan Rome', 'Roman Literature of the Golden Age of Augustus Caesar, produced during the transition from Republican to Imperial forms of government, was to have a profound and defining influence on Western European and American societies.', 'Roman Literature of the Golden Age of Augustus Caesar, produced during the transition from Republican to Imperial forms of government, was to have a profound and defining influence on Western European and American societies.&#160;These writings ultimately established lasting models of aesthetic refinement, philosophical aspiration, and political ambition that continue to shape modern cultures.&#160;This class will be exploring the Golden Age of Latin Literature from an historical perspective in order to provide an intensive examination of the cultural contexts in which these monumental works of classical art were first produced.&#160;Readings will emphasize the transition from a Republican form of government to an Empire under the rule of Augustus Caesar and the diversity of responses among individual authors to the profound structural changes that Roman society was undergoing at this time. Particular attention will be devoted to the reorganization of society and the self through textuality, the changing dimensions of the public and the private, the roles of class and gender, and the relationship between art and pleasure.&#160;Writings covering a wide variety of literary genres will include the works of Caesar, Cicero, Catullus, Livy, Virgil, Horace, and Ovid, with additional readings from Cassius Dio for background.', 'http://ocw.mit.edu/courses/literature/21l-455-classical-literature-the-golden-age-of-augustan-rome-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-455-classical-literature-the-golden-age-of-augustan-rome-fall-2004/21l-455f04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1733, 'The Bible', 'This course is an introduction to major books from both the Hebrew Bible and the New Testament.', 'This course is an introduction to major books from both the Hebrew Bible and the New Testament. Particular attention has been given to literary techniques, issues resulting from translation from the original Hebrew and Greek, and the different historical periods that produced and are reflected in the Bible. Investigation of the Bible as influence in later narrative, philosophic, and artistic traditions.', 'http://ocw.mit.edu/courses/literature/21l-458-the-bible-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-458-the-bible-spring-2007/21l-458s07.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1734, 'Medieval Literature: Medieval Women Writers', 'This survey provides a general introduction to medieval European literature (from Late Antiquity to the Fifteenth Century) from the perspective of women writers from a variety of cultures, social backgrounds, and historical timeperiods.', 'This survey provides a general introduction to medieval European literature (from Late Antiquity to the Fifteenth Century) from the perspective of women writers from a variety of cultures, social backgrounds, and historical timeperiods.&#160;Though much of the class will be devoted to exploring the evolution of a new literary tradition by and for women from its earliest emergence in the West, wider historical and cultural movements will also be addressed: the Fall of the Roman Empire, the growth of religious communities, the shift from orality to literacy, the culture of chivalry and courtly love, the emergence of scholasticism and universities, changes in devotional practices, the persecution of heretics, the rise of nationalism and class consciousness.&#160;Authors will include some of the most famous women of the period: Hildegard of Bingen, Heloise of Paris, Marie de France, Christine de Pizan, Joan of Arc, Margery Kempe, along with many interesting and intriguing though lesser known figures.', 'http://ocw.mit.edu/courses/literature/21l-460-medieval-literature-medieval-women-writers-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-460-medieval-literature-medieval-women-writers-spring-2004/21l-460s04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1735, 'Renaissance Literature', 'The Renaissance has justly become both famous and notorious as an age of discovery, and its voyages took place in many realms.', 'The Renaissance has justly become both famous and notorious as an age of discovery, and its voyages took place in many realms. This semester, we will read several history making narratives of early modern travel: first-hand accounts of discovery, captivity, conquest, or cultural encounter. As Europeans came to acquire experience of unfamiliar places, literary texts of the period began to assimilate this experience by describing imagined voyages across real or fantastic landscapes. Finally, voyages of exploration served Renaissance writers as a metaphor: for intellectual inquiry, for spiritual development, or for the pursuit of love. Among the literary genres sampled this semester will be sonnets, plays, prose narratives, utopias, and chivalric romance. Authors and travellers will include Francis Petrarch, Amerigo Vespucci, Thomas More, Christopher Marlowe, Edmund Spenser, Hern&aacute;n Cort&eacute;s, John Donne, Francis Drake, Mary Rowlandson, Francis Bacon.', 'http://ocw.mit.edu/courses/literature/21l-463-renaissance-literature-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-463-renaissance-literature-fall-2008/21l-463f08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1736, 'Eighteenth-Century Literature: Versions of the Self in 18th-C Britain', 'When John Locke declared (in the 1690 Essay Concerning Human Understanding) that knowledge was derived solely from experience, he raised the possibility that human understanding and identity were not the products of God''s will or of immutable laws of nature so much as of one''s personal history and background.', 'When John Locke declared (in the 1690 Essay Concerning Human Understanding) that knowledge was derived solely from experience, he raised the possibility that human understanding and identity were not the products of God''s will or of immutable laws of nature so much as of one''s personal history and background. If on the one hand Locke''s theory led some to pronounce that individuals could determine the course of their own lives, however, the idea that we are the products of our experience just as readily supported the conviction that we are nothing more than machines acting out lives whose destinies we do not control. This course will track the formulation of that problem, and a variety of responses to it, in the literature of the ''long eighteenth century.'' Readings will range widely across genre, from lyric poetry and the novel to diary entries, philosophical prose, and political essays, including texts by Alexander Pope, Jonathan Swift, Mary Astell, David Hume, Laurence Sterne, Olaudah Equiano, Mary Hays, and Mary Shelley. Topics to be discussed include the construction of gender identities; the individual in society; imagination and the poet''s work. There will be two essays, one 5-6 pages and one 8-10 pages in length, and required presentations.', 'http://ocw.mit.edu/courses/literature/21l-470-eighteenth-century-literature-versions-of-the-self-in-18th-c-britain-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-470-eighteenth-century-literature-versions-of-the-self-in-18th-c-britain-spring-2003/21l-470s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1737, 'Major English Novels', 'This course studies several important examples of the genre that between the early 18th century and the end of the 20th has come to seem the definitive literary form for representing and coming to terms with modernity.', 'This course studies several important examples of the genre that between the early 18th century and the end of the 20th has come to seem the definitive literary form for representing and coming to terms with modernity. Syllabi vary, but the class usually attempts to convey a sense of the form''s development over the past few centuries. Among topics likely to be considered are: developments in narrative technique, the novel''s relation to history, national versus linguistic definitions of an ''English'' novel, social criticism in the novel, realism versus ''romance,'' the novel''s construction of subjectivities. Writers studied have included Daniel Defoe, Henry Fielding, Samuel Richardson, Lawrence Sterne, Mary Shelley, Jane Austen, Walter Scott, Emily and Charlotte Bront&#235;, Charles Dickens, George Eliot, Joseph Conrad, H. G. Wells, James Joyce, Virginia Woolf, Salman Rushdie.', 'http://ocw.mit.edu/courses/literature/21l-471-major-english-novels-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-471-major-english-novels-spring-2004/21l-471s04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1738, 'Major English Novels: Reading Romantic Fiction', 'Though the era of British Romanticism (ca.', 'Though the era of British Romanticism (ca. 1790-1830) is sometimes exclusively associated with the poetry of these years, this period was just as importantly a time of great innovation in British prose fiction.&#160;Romantic novelists pioneered or revolutionized several genres, including social/philosophical problem novels, tales of sentiment and sensibility, and the historical novel.&#160;Writing in the years of the French Revolution, the Napoleonic wars, and the early industrial revolution, these writers conveyed a spirit of chaos and upheaval even in stories whose settings are seemingly farthest removed from those cataclysmic historical events.&#160;In this year''s offering of ''Major English Novels,'' we will read of plagues, wars, hysterics, monsters and more in novels by authors including William Godwin, Maria Edgeworth, Jane Austen, Mary Shelley, and Walter Scott.&#160;In the final weeks of the semester, we will read one major novel of the Victorian era, Charles Dickens''s Great Expectations, in light of these earlier texts.&#160;There will be two essay assignments, one 5-7 pages and one 8-10 pages in length, and required presentations.', 'http://ocw.mit.edu/courses/literature/21l-471-major-english-novels-reading-romantic-fiction-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-471-major-english-novels-reading-romantic-fiction-spring-2002/21l-471s02.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1739, 'Major European Novels', 'This subject traces the history of the European novel by studying texts that have been influential in connection with two interrelated ideas.', 'This subject traces the history of the European novel by studying texts that have been influential in connection with two interrelated ideas. (1) When serious fiction deals with matters of great consequence, it should not deal with the actions of persons of consequence&mdash;kings, princes, high elected officials and the like&mdash;but rather with the lives of apparently ordinary people and the everyday details of their social ambitions and desires. To use a phrase of Balzac''s, serious fiction deals with ''what happens everywhere''. (2) This idea sometimes goes with another: that the most significant representations of the human condition are those dealing with persons who try to compel society to accept them as its destined agent, despite their absence of high birth or inheritance.', 'http://ocw.mit.edu/courses/literature/21l-472-major-european-novels-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-472-major-european-novels-fall-2008/21l-472f08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1740, 'Victorian Literature and Culture', 'The course covers British literature and culture during Queen Victoria''s long reign, 1837-1901.', 'The course covers British literature and culture during Queen Victoria''s long reign, 1837-1901. This was the brilliant age of Charles Dickens, the Bront&#235;s, Lewis Carroll, George Eliot, Robert Browning, Oscar Wilde, Arthur Conan Doyle, Rudyard Kipling, Alfred, Lord Tennyson &#8211; and many others. It was also the age of urbanization, steam power, class conflict, Darwin, religious crisis, imperial expansion, information explosion, bureaucratization &#8211; and much more.', 'http://ocw.mit.edu/courses/literature/21l-481-victorian-literature-and-culture-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-481-victorian-literature-and-culture-spring-2003/21l-481s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1741, '20th-Century Fiction', 'Tradition and innovation in representative fiction of the early modern period.', 'Tradition and innovation in representative fiction of the early modern period. Recurring themes: the role of the artist in the modern period, the representation of psychological and sexual experience, the virtues (and defects) of the aggressively experimental character of so many modern books. Works by such writers as Conrad, Kipling, Isaac Babel, Kafka, James, Lawrence, Mann, Ford Madox Ford, Joyce, Woolf, Faulkner, and Nabokov.', 'http://ocw.mit.edu/courses/literature/21l-485-20th-century-fiction-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-485-20th-century-fiction-fall-2002/21l-485f02.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1742, 'Modern Drama', 'This course analyzes major modern plays featuring works by Shaw, Pirandello, Beckett, Brecht, Williams, Soyinka, Hwang, Churchill, Wilson, Frayn, Stoppard, Deveare Smith, and Kushner.', 'This course analyzes major modern plays featuring works by Shaw, Pirandello, Beckett, Brecht, Williams, Soyinka, Hwang, Churchill, Wilson, Frayn, Stoppard, Deveare Smith, and Kushner. The class particularly considers performance, sociopolitical and aesthetic contexts, and the role of theater in the world of modern multimedia.', 'http://ocw.mit.edu/courses/literature/21l-486-modern-drama-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-486-modern-drama-spring-2006/21l-486s06.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1743, 'Contemporary Literature: Literature, Development, and Human Rights', 'Central to our era is the gradual movement of all the world''s regions toward a uniform standard of economic and political development.', 'Central to our era is the gradual movement of all the world''s regions toward a uniform standard of economic and political development. In this class we will read a variety of recent narratives that partake of, dissent from, or contribute to this story, ranging from novels and poems to World Bank and IMF statements and National Geographic reports. We will seek to understand the many motives and voices &ndash; sometimes congruent, sometimes clashing &ndash; that are currently engaged in producing accounts of people in the developing world: their hardships, laughter, and courage, and how they help themselves and are helped by outsiders who may or may not have philanthropic motives. Readings will include literature by J. G. Ballard, Jamaica Kincaid, Rohinton Mistry, and John le Carr&eacute;, as well as policy documents, newspaper and magazine articles, and the Web sites of a variety of trade and development commissions and organizations.', 'http://ocw.mit.edu/courses/literature/21l-488-contemporary-literature-literature-development-and-human-rights-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-488-contemporary-literature-literature-development-and-human-rights-spring-2008/21l-488s08.JPG', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1744, 'Contemporary Literature: British Novels Now', 'What is Britain now? Its metropolises are increasingly multicultural.', 'What is Britain now? Its metropolises are increasingly multicultural. Its hold over its distant colonies is a thing of the past. Its sway within the global political arena is weak. Its command over Northern Ireland, Wales, and Scotland is broken or threatened. What have novelists made of all this? What are they writing as the old empire fades away and as new social and political formations emerge? These are the questions that will concern us in this course.', 'http://ocw.mit.edu/courses/literature/21l-488-contemporary-literature-british-novels-now-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-488-contemporary-literature-british-novels-now-spring-2007/21l-488s07.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1745, 'Contemporary Literature', 'This semester, Contemporary Literature (21L.', 'This semester, Contemporary Literature (21L.488) deals with Irish literature, a subject broad and deep. To achieve a manageable volume of study, the course focuses primarily on poetry and prose, at drama''s expense, and on living writers, at the expense of their predecessors. Each class session follows a discussion format, often with students assigned to lead-off or summarize the day''s topic.', 'http://ocw.mit.edu/courses/literature/21l-488-contemporary-literature-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-488-contemporary-literature-spring-2003/21l-488s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1746, 'Interactive and Non-Linear Narrative: Theory and Practice', 'This course explores the properties of non-linear, multi-linear, and interactive forms of narratives as they have evolved from print to digital media.', 'This course explores the properties of non-linear, multi-linear, and interactive forms of narratives as they have evolved from print to digital media. Works covered in this course range from the Talmud, classics of non-linear novels, experimental literature, early sound and film experiments to recent multi-linear and interactive films and games. The study of the structural properties of narratives that experiment with digression, multiple points of view, disruptions of time, space, and of storyline is complemented by theoretical texts about authorship/readership, plot/story, properties of digital media and hypertext. Questions that will be addressed in this course include: How can we define ''non-linearity/multi-linearity'', ''interactivity'', ''narrative''. To what extend are these aspects determined by the text, the reader, the digital format? What kinds of narratives are especially suited for a nonlinear/ interactive format? Are there stories that can only be told in a digital format? What can we learn from early non-digital examples of non-linear and interactive story telling?', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-765j-interactive-and-non-linear-narrative-theory-and-practice-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-765j-interactive-and-non-linear-narrative-theory-and-practice-spring-2004/21w-765js04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1747, 'Theory and Practice of Non-linear and Interactive Narrative', 'This class covers a range of topics including hypertext, interactive cinema, games, installation art, and soundscapes.', 'This class covers a range of topics including hypertext, interactive cinema, games, installation art, and soundscapes. It examines the potential for dynamic narrative in traditional media like novels and films and as well as in computer-based stories and games. The course focuses on the creation of electronic stories and games using simple authoring systems and multimedia software tools. Students present and constructively critique one another''s work in progress in a workshop setting aimed at expanding the representational powers of a new creative medium.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-765j-theory-and-practice-of-non-linear-and-interactive-narrative-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-765j-theory-and-practice-of-non-linear-and-interactive-narrative-spring-2003/21w-765js03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1748, 'The American Novel', 'This course explores the metaphorical, historical, social, and psychological value of ghosts in the American novel.', 'This course explores the metaphorical, historical, social, and psychological value of ghosts in the American novel. Using the theme of ''haunting'' as a flashpoint for class discussion and a thematic center for our readerly attention, this course examines the American novel in the context of the various histories which might be said to haunt fictional characters in the American novel, to haunt the American novel itself, and ultimately to haunt us: America''s colonial past, its slave past, and other memorable and painful chapters in its past.', 'http://ocw.mit.edu/courses/literature/21l-501-the-american-novel-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-501-the-american-novel-fall-2006/21l-501f06.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1749, 'Race and Identity in American Literature: Keepin'' it Real Fake', 'This course explores the ways in which various American artists view race and class as performed or performable identities.', 'This course explores the ways in which various American artists view race and class as performed or performable identities. Discussions will focus on some of the following questions: What does it mean to act black, white, privileged, or underprivileged? What do these artists suggest are the implications of performing (indeed playing at or with) racial identity, ethnicity, gender, and class status? How and why are race and class status often conflated in these performances?', 'http://ocw.mit.edu/courses/literature/21l-504j-race-and-identity-in-american-literature-keepin-it-real-fake-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-504j-race-and-identity-in-american-literature-keepin-it-real-fake-spring-2007/21l-504js07.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1750, 'American Authors: American Women Authors', 'This subject, cross-listed in Literature and Women''s Studies, examines a range of American women authors from the seventeenth century to the present.', 'This subject, cross-listed in Literature and Women''s Studies, examines a range of American women authors from the seventeenth century to the present.&nbsp;It aims to introduce a number of literary genres and styles- the captivity narrative, slave novel, sensational, sentimental, realistic, and postmodern fiction- and also to address significant historical events in American women''s history: Puritanism, the American Revolution, industrialization and urbanization in the nineteenth century, the Harlem Renaissance, World War II, the 60s civil rights movements.&nbsp;A primary focus will be themes studied and understood through the lens of gender: war, violence, and sexual exploitation (Keller, Rowlandson, Rowson); the relationship between women and religion (Rowlandson, Rowson, Stowe); labor, poverty, and working conditions for women (Fern, Davis, Wharton); captivity and slavery (Rowlandson, Jacobs); class struggle (Fern, Davis, Wharton, Larsen); race and identity (Keller, Jacobs, Larsen, Morrison); feminist revisions of history (Stowe, Morrison, Keller); and the myth of the fallen woman (take your pick).&nbsp;Essays and in-class reports will focus more particularly on specific writers and themes and will stress the skills of close reading, annotation, research, and uses of multimedia where appropriate.', 'http://ocw.mit.edu/courses/literature/21l-512-american-authors-american-women-authors-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-512-american-authors-american-women-authors-spring-2003/21l-512s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1751, 'Literary Interpretation: Literature and Urban Experience', 'Alienation, overcrowding, sensory overload, homelessness, criminality, violence, loneliness, sprawl, blight.', 'Alienation, overcrowding, sensory overload, homelessness, criminality, violence, loneliness, sprawl, blight. How have the realities of city living influenced literature''s formal and thematic techniques? How useful is it to think of literature as its own kind of ''map'' of urban space? Are cities too grand, heterogeneous, and shifting to be captured by writers? In this seminar we will seek answers to these questions in key city literature, and in theoretical works that endeavor to understand the culture of cities.', 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-literature-and-urban-experience-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-literature-and-urban-experience-spring-2009/21l-701s09.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1752, 'Literary Interpretation: Literature and Photography: The Image', 'This course introduces the practice and theory of literary criticism.', 'This course introduces the practice and theory of literary criticism. The seminar focuses on topics such as the history of critical methods and techniques, and the continuity of certain subjects in literary history. Instruction and practice in oral and written communication is a major component of the course. Other components include theory and use of figurative language and reading poetry.', 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-literature-and-photography-the-image-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-literature-and-photography-the-image-fall-2005/21l-701f05.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1753, 'Literary Interpretation: Interpreting Poetry', 'This seminar offers a course of readings in lyric poetry.', 'This seminar offers a course of readings in lyric poetry.&#160;It aims to enhance the student''s capacity to understand the nature of poetic language and the enjoyment of poetic texts by treating poems as messages to be deciphered. The seminar will briefly touch upon the history of theories of figurative language since Aristotle and it will attend to the development of those theories during the last thirty years, noting the manner in which they tended to consider figures of speech distinct from normative or literal expression, and it will devote particular attention to the rise of theories that quarrel with this distinction. The seminar also aims to communicate a rough sense of the history of English-speaking poetry since the early modern period. Some attention will be paid as well to the use of metaphor in science.', 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-interpreting-poetry-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-interpreting-poetry-fall-2003/21l-701f03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1754, 'Literary Interpretation: Virginia Woolf''s Shakespeare', 'How does one writer use another writer''s work?&nbsp;Does it matter if one author has been dead 300 years? What difference does it make if she''s a groundbreaking twentieth-century feminist and the writer she values has come to epitomize the English literary tradition? How can a novelist borrow from plays and poems? By reading Virginia Woolf''s major novels and essays in juxtaposition with some of the Shakespeare plays that (depending on one''s interpretation) haunt, enrich, and/or shape her writing, we will try to answer these questions and raise others.', 'How does one writer use another writer''s work?&nbsp;Does it matter if one author has been dead 300 years? What difference does it make if she''s a groundbreaking twentieth-century feminist and the writer she values has come to epitomize the English literary tradition? How can a novelist borrow from plays and poems? By reading Virginia Woolf''s major novels and essays in juxtaposition with some of the Shakespeare plays that (depending on one''s interpretation) haunt, enrich, and/or shape her writing, we will try to answer these questions and raise others. Readings in literary criticism, women''s studies, and other literary texts will complement our focus on the relationship--across time, media, and gender--between Shakespeare and Woolf.&nbsp;As a seminar, we will work to become more astute readers of literature within its historical, artistic, and political contexts, and consider how literature both reflects and contributes to these societal frameworks.&nbsp;Central texts will include Shakespeare''s Othello, Antony and Cleopatra, Cymbeline, and The Winter''s Tale, and Woolf''s A Room of One''s Own, Mrs. Dalloway, Orlando, To the Lighthouse, The Waves, and Between the Acts. This subject is an advanced seminar in both the Literature and the Women''s Studies Program.', 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-virginia-woolfs-shakespeare-spring-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-virginia-woolfs-shakespeare-spring-2001/21l-701s01.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1755, 'Studies in Fiction: Stowe, Twain, and the Transformation of 19th-Century America', 'This seminar looks at two bestselling nineteenth-century American authors whose works made the subject of slavery popular among mainstream readers.', 'This seminar looks at two bestselling nineteenth-century American authors whose works made the subject of slavery popular among mainstream readers. Uncle Tom''s Cabin by Harriet Beecher Stowe and The Adventures of Huckleberry Finn by Mark Twain have subsequently become canonized and reviled, embraced and banned by individuals and groups at both ends of the political and cultural spectrum and everywhere in between.', 'http://ocw.mit.edu/courses/literature/21l-702-studies-in-fiction-stowe-twain-and-the-transformation-of-19th-century-america-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-702-studies-in-fiction-stowe-twain-and-the-transformation-of-19th-century-america-fall-2004/21l-702f04.gif', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1756, 'Studies in Drama: Too Hot to Handle: Forbidden Plays in Modern America', 'Unlike film, theater in America does not have a ratings board that censors content.', 'Unlike film, theater in America does not have a ratings board that censors content. So plays have had more freedom to explore and to transgress normative culture. Yet censorship of the theater has been part of American culture from the beginning, and continues today. How and why does this happen, and who decides whether a play is too dangerous to see or to teach? Are plays dangerous? Sinful? Even demonic? In our seminar, we will study plays that have been censored, either legally or extra-legally (i.e. refused production, closed down during production, denied funding, or taken off school reading lists). We''ll look at laws, both national and local, relating to the ''obscene'', as well as unofficial practices, and think about the way censorship operates in American life now. And of course we will study the offending texts, themselves, to find what is really dangerous about them, for ourselves.', 'http://ocw.mit.edu/courses/literature/21l-703-studies-in-drama-too-hot-to-handle-forbidden-plays-in-modern-america-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-703-studies-in-drama-too-hot-to-handle-forbidden-plays-in-modern-america-fall-2008/21l-703f08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1757, 'Studies in Drama: Theater and Science in a Time of War', 'This course explores the creation (and creativity) of the modern scientific and cultural world through study of western Europe in the 17th century, the age of Descartes and Newton, Shakespeare, Rembrandt and Moli&egrave;re.', 'This course explores the creation (and creativity) of the modern scientific and cultural world through study of western Europe in the 17th century, the age of Descartes and Newton, Shakespeare, Rembrandt and Moli&egrave;re. The class compares period thinking to present-day debates about the scientific method, art, religion, and society. This team-taught, interdisciplinary subject draws on a wide range of literary, dramatic, historical, and scientific texts and images, and involves theatrical experimentation as well as reading, writing, researching and conversing.', 'http://ocw.mit.edu/courses/literature/21l-703-studies-in-drama-theater-and-science-in-a-time-of-war-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-703-studies-in-drama-theater-and-science-in-a-time-of-war-spring-2005/21l-703s05.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1758, 'English Renaissance Drama: Theatre and Society in the Age of Shakespeare', 'Shakespeare ''doth bestride the narrow world'' of the English Renaissance ''like a colossus,'' leaving his contemporaries ''walk under his large legs and peep about'' to find themselves in ''dishonourable graves.', 'Shakespeare ''doth bestride the narrow world'' of the English Renaissance ''like a colossus,'' leaving his contemporaries ''walk under his large legs and peep about'' to find themselves in ''dishonourable graves.'' This course aims in part to correct this grave injustice by surveying the extraordinary output of playwrights whose names have largely been eclipsed by their more luminous compatriot: Marlowe, Jonson, Webster, and Ford, among others. Reading Shakespeare as just one of a group of practitioners -- many of whom were more popular than him during and even after his remarkable career -- will restore, I hope, a sense not just of the richness of English Renaissance drama, but also that of the historical and cultural moment of the English Renaissance itself. This course will examine the relationship between theatre and society through the lens of the drama produced in response to these changes. However, we will not try to map the progress of drama directly onto the social world, as if the former can simply read off the latter. Rather, focusing on discrete issues and problems, we will try to understand the ways in which a particular text not only reflects but responds to and shapes aspects of the culture from which it derives, developing an aesthetic that actively engages its world. The topics addressed over the course of the semester will be wide-ranging but will include: gender and class dynamics in Renaissance society; money, trade, and colonialism; the body as metaphor and theatrical ''object''; allegory and aesthetic form; theatricality and meta-theatricality; the private and the public.', 'http://ocw.mit.edu/courses/literature/21l-703-english-renaissance-drama-theatre-and-society-in-the-age-of-shakespeare-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-703-english-renaissance-drama-theatre-and-society-in-the-age-of-shakespeare-fall-2003/21l-703f03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1759, 'Studies in Poetry: 20th Century Irish Poetry: The Shadow of W. B. Yeats', 'William Butler Yeats occupies a dominant position in the lives and work of the Irish poets who followed him.', 'William Butler Yeats occupies a dominant position in the lives and work of the Irish poets who followed him. We will explore some of that poetry, and consider how later poets, especially female poets, tried to come to grips with, or escape from, that dominance. As a seminar, the subject will place special emphasis on student involvement and control. I will ask you to submit one ten-twelve page essay, two shorter (five page) essays, and to accept the role of ''leadoff person,'' perhaps more than once, That role will demand that you choose from among the assigned readings for that session the poem we should focus upon, and to offer either a provocative articulation of what the poem is about, or a provocative question which the poem confronts, and which we should grapple with, as well.', 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-20th-century-irish-poetry-the-shadow-of-w-b-yeats-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-20th-century-irish-poetry-the-shadow-of-w-b-yeats-spring-2008/21l-704s08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1760, 'Studies in Poetry: From the Sonneteers to the Metaphysicals', 'This course introduces students to some of the most important practitioners of poetry in sixteenth- and seventeenth-century England, locating them in their historical and social contexts.', 'This course introduces students to some of the most important practitioners of poetry in sixteenth- and seventeenth-century England, locating them in their historical and social contexts. We will be emphasizing love poetry or amatory verse, by combining close reading of selected poems with an investigation of the contexts of English verse.', 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-from-the-sonneteers-to-the-metaphysicals-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-from-the-sonneteers-to-the-metaphysicals-spring-2006/21l-704s06.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1761, 'Studies in Poetry - British Poetry and the Sciences of the Mind', 'Do poems think? Recurrent images of the poet as an inspired lunatic, and of poetry as a fundamentally irrational art, have often fostered an understanding of poets and their work as generally extraneous to the work of the sciences.', 'Do poems think? Recurrent images of the poet as an inspired lunatic, and of poetry as a fundamentally irrational art, have often fostered an understanding of poets and their work as generally extraneous to the work of the sciences. Yet poets have long reflected upon and have sought to embody in their work the most elementary processes of mind, and have frequently drawn for these representations on the very sciences to which they are thought to stand - and sometimes do genuinely stand - in opposition. Far from representing a mere departure from reason, then, the poem offers an image of the mind at work, an account of how minds work, a tool for eliciting thought in the reader or auditor. Bringing together readings in British poetry of the eighteenth and nineteenth centuries with writings from the emergent sciences of psychology and the physiology of the brain, this interdisciplinary course will explore the ways in which British poets, in years that witnessed the crucial development of these sciences, sought to capture an image of the mind at work. The primary aim of the course is to examine how several prominent genres of British poetry - the lyric, for instance, and the didactic poem - draw from and engage in this period with accounts of cognition within the sciences of psychology, physiology, and medicine. More broadly, the course aims to give undergraduates with some prior experience in the methods and topics of literary study an introduction to interdisciplinary humanistic research.', 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-british-poetry-and-the-sciences-of-the-mind-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-british-poetry-and-the-sciences-of-the-mind-fall-2004/21l-704f04.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1762, 'Studies in Poetry: Gender and Lyric -- Renaissance Men and Women Writing about Love', 'The core of this seminar will be the great sequences of English love sonnets written by William Shakespeare, Philip Sidney, Edmund Spenser, and Mary Wroth.', 'The core of this seminar will be the great sequences of English love sonnets written by William Shakespeare, Philip Sidney, Edmund Spenser, and Mary Wroth. These poems cover an enormous amount of aesthetic and psychological ground: ranging from the utterly subjective to the entirely public or conventional, from licit to forbidden desires, they might also serve as a manual of experimentation with the resources of sound, rhythm, and figuration in poetry. Around these sequences, we will develop several other contexts, using both Renaissance texts and modern accounts: the Petrarchan literary tradition (poems by Francis Petrarch and Sir Thomas Wyatt); the social, political, and ethical uses of love poetry (seduction, getting famous, influencing policy, elevating morals, compensating for failure); other accounts of ideal masculinity and femininity (conduct manuals, theories of gender and anatomy); and the other limits of the late sixteenth century vogue for love poetry: narrative poems, pornographic poems, poems that don''t work.', 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-gender-and-lyric-renaissance-men-and-women-writing-about-love-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-gender-and-lyric-renaissance-men-and-women-writing-about-love-spring-2003/21l-704s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1763, 'Studies in Poetry: "Does Poetry Matter"', 'The landscape we will explore is the troublesome one of the relevance, impact, and importance of poetry in a troubled modern world.', 'The landscape we will explore is the troublesome one of the relevance, impact, and importance of poetry in a troubled modern world. We will read both poetry and prose by several substantial modern writers, each of whom confronted the question that is the subject''s title.', 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-does-poetry-matter-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-704-studies-in-poetry-does-poetry-matter-fall-2002/21l-704f02.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1764, 'Major Authors: Rewriting Genesis: "Paradise Lost" and Twentieth-Century Fantasy', 'What does the Genesis story of creation and temptation tell us about gender, about heterosexuality, and about the origins of evil? What is the nature of God, and how can we account for that nature in a cosmos where evil exists? When is rebellion justified, and when is authority legitimate? These are some of the key questions that engaged the poet John Milton, and that continue to engage readers of his work.', 'What does the Genesis story of creation and temptation tell us about gender, about heterosexuality, and about the origins of evil? What is the nature of God, and how can we account for that nature in a cosmos where evil exists? When is rebellion justified, and when is authority legitimate? These are some of the key questions that engaged the poet John Milton, and that continue to engage readers of his work.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-rewriting-genesis-paradise-lost-and-twentieth-century-fantasy-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-rewriting-genesis-paradise-lost-and-twentieth-century-fantasy-spring-2009/21l-705s09.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1765, 'Major Authors: John Milton', 'In 1667, John Milton published what he intended both as the crowning achievement of a poetic career and a justification of God''s ways to man: an epic poem which retold and reimagined the Biblical story of creation, temptation, and original sin.', 'In 1667, John Milton published what he intended both as the crowning achievement of a poetic career and a justification of God''s ways to man: an epic poem which retold and reimagined the Biblical story of creation, temptation, and original sin. Even in a hostile political climate, Paradise Lost was almost immediately recognized as a classic, and one fate of a classic is to be rewritten, both by admirers and by antagonists. In this seminar, we will read Paradise Lost alongside works of 20th century fantasy and science fiction which rethink both Milton''s text and its source. Students should come to the seminar having read Paradise Lost straight through at least once; this can be accomplished by taking the IAP subject, Reading Paradise Lost (21L.995), or independently. Twentieth century authors will include C. S. Lewis (Perelandra, The Lion, the Witch and the Wardrobe) and Philip Pullman (His Dark Materials), as well as assorted criticism. Each week, one class meeting will focus on Milton, and the other on one of the modern novels.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-john-milton-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-john-milton-spring-2008/21l-705s08.JPG', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1766, 'Major Authors: After the Masterpiece: Novels by Melville, Twain, Faulkner, and Morrison', 'This seminar provides intensive study of exciting texts by four influential American authors.', 'This seminar provides intensive study of exciting texts by four influential American authors. In studying paired works, we can enrich our sense of each author''s distinctive methods, get a deeper sense of the development of their careers, and shake up our preconceptions about what makes an author or a work ''great.'' Students will get an opportunity to research an author in depth, as well as making broader comparisons across the syllabus.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-after-the-masterpiece-novels-by-melville-twain-faulkner-and-morrison-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-after-the-masterpiece-novels-by-melville-twain-faulkner-and-morrison-fall-2006/21l-705f06.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1767, 'Major Authors: Oscar Wilde and the ''90''s', 'At this distance Oscar Wilde seems not only to be on the threshold between centuries and between cultural-systems: in many ways he seems to be the threshold.', 'At this distance Oscar Wilde seems not only to be on the threshold between centuries and between cultural-systems: in many ways he seems to be the threshold. His aesthetics look backwards to the aestheticism of Pater and the moral sensibility of Ruskin, and they look forward to Modernism. His antecedents are 18th century playwrights, and he opened a path of irony and structural self-reflexivity that leads to Beckett and Tom Stoppard. He was Irish but achieved his great successes in England. Arguably, his greatest success was his greatest public failure: in his scandalous trials he shaped 20th century attitudes toward homosexuality and toward theatricality and toward performativity. His greatest performance was the role of &quot;Oscar Wilde&quot;: in that sense he taught the 20th century how to be itself.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-oscar-wilde-and-the-90s-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-oscar-wilde-and-the-90s-spring-2003/21l-705s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1768, 'Major Authors: Melville and Morrison', 'This seminar provides intensive study of texts by two American authors (Herman Melville, 1819-1891, and Toni Morrison, 1931-) who, using lyrical, radically innovative prose, explore in different ways epic notions of American identity.', 'This seminar provides intensive study of texts by two American authors (Herman Melville, 1819-1891, and Toni Morrison, 1931-) who, using lyrical, radically innovative prose, explore in different ways epic notions of American identity. Focusing on Melville''s Typee (1846), Moby-Dick (1851), and The Confidence-Man (1857) and Morrison''s Sula (1973), Beloved (1987), Jazz (1992), and Paradise (1998), the class will address their common concerns with issues of gender, race, language, and nationhood. Be prepared to read deeply (i.e. a small number of texts with considerable care), to draw on a variety of sources in different media, and to employ them in creative research, writing, and multimedia projects.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-melville-and-morrison-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-melville-and-morrison-fall-2003/21l-705f03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1769, 'Studies in Film', 'This course investigates relationships between two media, film and literature, studying works linked across the two media by genre, topic, and style.', 'This course investigates relationships between two media, film and literature, studying works linked across the two media by genre, topic, and style. It aims to sharpen appreciation of major works of cinema and of literary narrative. The course explores how artworks challenge and cross cultural, political and aesthetic boundaries. It includes some attention to theory of narrative. Films to be studied include works by Akira Kurosawa, John Ford, Francis Ford Coppolla, Clint Eastwood, Orson Welles, Billy Wilder, and Federico Fellini, among others. Literary works include texts by Aeschylus, Sophocles, Shakespeare, Cervantes, Honor&eacute; de Balzac, Henry James and F. Scott Fitzgerald.', 'http://ocw.mit.edu/courses/literature/21l-706-studies-in-film-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-706-studies-in-film-fall-2005/21l-706f05.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1770, 'Writing Early American Lives: Gender, Race, Nation, Faith', 'This course focuses on the period between roughly 1550-1850.', 'This course focuses on the period between roughly 1550-1850. American ideas of race had taken on a certain shape by the middle of the nineteenth century, consolidated by legislation, economics, and the institution of chattel slavery. But both race and identity meant very different things three hundred years earlier, both in their dictionary definitions and in their social consequences. How did people constitute their identities in early America, and how did they speak about these identities? Texts will include travel writing, captivity narratives, orations, letters, and poems, by Native American, English, Anglo-American, African, and Afro-American writers.', 'http://ocw.mit.edu/courses/literature/21l-707-writing-early-american-lives-gender-race-nation-faith-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-707-writing-early-american-lives-gender-race-nation-faith-fall-2005/21l-707f05.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1771, 'Technologies of Humanism', 'This course explores the properties of non-sequential, multi-linear, and interactive forms of narratives as they have evolved from print to digital media.', 'This course explores the properties of non-sequential, multi-linear, and interactive forms of narratives as they have evolved from print to digital media. Works covered in this course range from the Talmud, classics of non-linear novels, experimental literature, early sound and film experiments to recent multi-linear and interactive films and games. The study of the structural properties of narratives that experiment with digression, multiple points of view, disruptions of time, space, and of storyline is complemented by theoretical texts about authorship/readership, plot/story, properties of digital media and hypertext. Questions that will be addressed in this course include: How can we define &#8216;non-sequentiality/multi-linearity&#8217;, &#8216;interactivity&#8217;, &#8216;narrative&#8217;. To what extend are these aspects determined by the text, the reader, the digital format? What are the roles of the reader and the author? What kinds of narratives are especially suited for a non-linear/interactive format? Are there stories that can only be told in a digital format? What can we learn from early non-digital examples of non-linear and interactive story telling?', 'http://ocw.mit.edu/courses/literature/21l-708-technologies-of-humanism-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-708-technologies-of-humanism-spring-2003/21l-708s03.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1772, 'Studies in Literary History: Modernism: From Nietzsche to Fellini', 'How do literature, philosophy, film and other arts respond to the profound changes in world view and lifestyle that mark the twentieth century? This course considers a broad range of works from different countries, different media, and different genres, in exploring the transition to a decentered &quot;Einsteinian&quot; universe.', 'How do literature, philosophy, film and other arts respond to the profound changes in world view and lifestyle that mark the twentieth century? This course considers a broad range of works from different countries, different media, and different genres, in exploring the transition to a decentered &quot;Einsteinian&quot; universe.', 'http://ocw.mit.edu/courses/literature/21l-709-studies-in-literary-history-modernism-from-nietzsche-to-fellini-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-709-studies-in-literary-history-modernism-from-nietzsche-to-fellini-fall-2010/21l-709f10.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1773, 'Media in Cultural Context: Popular Readerships', 'What is the history of popular reading in the Western world? How does widespread access to print relate to distinctions between highbrow and lowbrow culture, between good taste and bad judgment, and between men and women readers? This course will introduce students to the broad history of popular reading and to controversies about taste and gender that have characterized its development.', 'What is the history of popular reading in the Western world? How does widespread access to print relate to distinctions between highbrow and lowbrow culture, between good taste and bad judgment, and between men and women readers? This course will introduce students to the broad history of popular reading and to controversies about taste and gender that have characterized its development. Our grounding in historical material will help make sense of our main focus: recent developments in the theory and practice of reading, including fan-fiction, Oprah''s book club, comics, hypertext, mass-market romance fiction, mega-chain bookstores, and reader response theory.', 'http://ocw.mit.edu/courses/literature/21l-715-media-in-cultural-context-popular-readerships-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-715-media-in-cultural-context-popular-readerships-fall-2007/21l-715f07.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1774, 'Special Topics in Literature: Milton''s "Paradise Lost"', 'In this 3-unit class, we will read Milton''s epic poem Paradise Lost.', 'In this 3-unit class, we will read Milton''s epic poem Paradise Lost. The goal of the class is for students to come away feeling comfortable with its language and argument; meeting in a small group will also allow us to talk about the key questions and issues raised by the poem. This course is offered during the Independent Activities Period (IAP), which is a special 4-week term at MIT that runs from the first week of January until the end of the month.', 'http://ocw.mit.edu/courses/literature/21l-995-special-topics-in-literature-miltons-paradise-lost-january-iap-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-995-special-topics-in-literature-miltons-paradise-lost-january-iap-2008/21l-995iap08.jpg', 'Literature', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1775, 'Introduction to Western Music', 'This course gives a broad overview of Western music from the Middle Ages to the 20th century, with emphasis on late baroque, classical, romantic, and modernist styles (1700-1910).', 'This course gives a broad overview of Western music from the Middle Ages to the 20th century, with emphasis on late baroque, classical, romantic, and modernist styles (1700-1910). It is also meant to enhance students'' musical experience by developing listening skills and an understanding of diverse forms and genres. Major composers and their works will be placed in social and cultural contexts. Weekly lectures feature demonstrations by professional performers, and introduce topics to be discussed in sections. The focus of the course is on the weekly listening and reading assignments.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-011-introduction-to-western-music-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-011-introduction-to-western-music-spring-2006/21m-011s06.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1776, 'The Supernatural in Music, Literature and Culture', 'This class explores the relationship between music and the supernatural, focusing on the social history and context of supernatural beliefs as reflected in key literary and musical works from 1600 to the present.', 'This class explores the relationship between music and the supernatural, focusing on the social history and context of supernatural beliefs as reflected in key literary and musical works from 1600 to the present. Provides a better understanding of the place of ambiguity and the role of interpretation in culture, science and art. Explores great works of art by Shakespeare, Verdi, Goethe (in translation), Gounod, Henry James and Benjamin Britten. Readings will also include selections from the most recent scholarship on magic and the supernatural. Writing assignments will range from web-based projects to analytic essays. No previous experience in music is necessary. Projected guest lectures, musical performances, field trips.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-013j-the-supernatural-in-music-literature-and-culture-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-013j-the-supernatural-in-music-literature-and-culture-spring-2009/21m-013js09.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1777, 'Introduction to World Music', 'This course explores the ways that music is both shaped by and gives shape to the cultural settings in which it is performed, through studying selected musical traditions from around the world.', 'This course explores the ways that music is both shaped by and gives shape to the cultural settings in which it is performed, through studying selected musical traditions from around the world. Specific case studies will be examined closely through listening, analysis, and hands-on instruction. The syllabus centers around weekly listening assignments and readings from a textbook with CDs, supplemented by hands-on workshops, lecture/demonstrations and concerts by master musicians from around the world.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-030-introduction-to-world-music-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-030-introduction-to-world-music-fall-2006/21m-030f06.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1778, 'Introduction to Music Composition', 'Through a progressive series of composition projects, students investigate the sonic organization of musical works and performances, focusing on fundamental questions of unity and variety.', 'Through a progressive series of composition projects, students investigate the sonic organization of musical works and performances, focusing on fundamental questions of unity and variety. Aesthetic issues are considered in the pragmatic context of the instructions that composers provide to achieve a desired musical result, whether these instructions are notated in prose, as graphic images, or in symbolic notation. No formal training is required; this version of the class is a general elective suitable for a relatively large-enrollment class. Weekly listening, reading, and composition assignments draw on a broad range of musical styles and intellectual traditions, from various cultures and historical periods.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-065-introduction-to-music-composition-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-065-introduction-to-music-composition-fall-2009/21m-065f09.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1779, 'Introduction to Musical Composition', 'Through a progressive series of composition projects, this course investigates the sonic organization of musical works and performances, focusing on fundamental questions of unity and variety.', 'Through a progressive series of composition projects, this course investigates the sonic organization of musical works and performances, focusing on fundamental questions of unity and variety. Aesthetic issues are considered in the pragmatic context of the instructions that composers provide to achieve a desired musical result, whether these instructions are notated in prose, as graphic images, or in symbolic notation. No formal training is required. Weekly listening, reading, and composition assignments draw on a broad range of musical styles and intellectual traditions, from various cultures and historical periods.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-065-introduction-to-musical-composition-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-065-introduction-to-musical-composition-fall-2005/21m-065f05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1780, 'Developing Musical Structures', 'The goal of this class is practical:&#160;to interrogate, make explicit, and thus&#160;to develop the powerful musical intuitions that are at work as you make sense of the music all around you.', 'The goal of this class is practical:&#160;to interrogate, make explicit, and thus&#160;to develop the powerful musical intuitions that are at work as you make sense of the music all around you.&#160;Reflecting, we will ask how this knowledge develops in ordinary and extraordinary ways.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-113-developing-musical-structures-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-113-developing-musical-structures-fall-2002/21m-113f02.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1781, 'Introduction to Anglo-American Folk Music', 'This course examines the production, transmission, preservation and qualities of folk music in the British Isles and North America from the 18th century to the folk revival of the 1960s and the present.', 'This course examines the production, transmission, preservation and qualities of folk music in the British Isles and North America from the 18th century to the folk revival of the 1960s and the present. There is a special emphasis on balladry, fiddle styles, and African-American influences. The class sings ballads and folk songs from the Child and Lomax collections as well as other sources as we examine them from literary, historical, and musical points of view. Readings supply critical and background materials from a number of sources. Visitors and films bring additional perspectives.', 'http://ocw.mit.edu/courses/literature/21l-423j-introduction-to-anglo-american-folk-music-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-423j-introduction-to-anglo-american-folk-music-fall-2005/21l-423jf05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1782, 'Schubert to Debussy', 'This course is a survey of developments in Western musical style, 1815-1915.', 'This course is a survey of developments in Western musical style, 1815-1915. Students will study works by 35 composers, including the romantics: Schubert, Chopin, and Schumann; the post-romantics: Wagner, Verdi, and Brahms; the turn-of-the-centurians: Mahler, Debussy, and Ravel; and the Americans: Gottschalk, Beach, and Joplin. Score-reading ability is beneficial.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-250-schubert-to-debussy-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-250-schubert-to-debussy-fall-2006/21m-250f06.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1783, 'Modern Music: 1900-1960', 'This subject covers a specific branch of music history: Western concert music of first sixty years of the twentieth century.', 'This subject covers a specific branch of music history: Western concert music of first sixty years of the twentieth century. Although we will be listening to and studying many pieces (most of the highest caliber) the goal of the course is not solely to build up a repertory of works in our memory (though that is indeed a goal). We will be most concerned with larger questions of continuity and change in music. We will also consider questions of reception, or historiography - that is, the creation of history and our perception of it. Why do we perceive much of this music, so much closer in time to us than Mozart or Beethoven, to be so foreign? Is this music aloof and separate from popular music of the twentieth century or is there a real connection (perhaps hidden)? The subject will continue to follow some topics of central interest to music before 1960, such as serialism and aleatory, beyond the 1960 cutoff. Conversely a few topics which get their start just before 1960 but which flourish later (minimalism, computer music) will be covered only in 21M.263.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-262-modern-music-1900-1960-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-262-modern-music-1900-1960-fall-2006/21m-262f06.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1784, 'Studies in Western Music History: Quantitative and Computational Approaches to Music History', 'The disciplines of music history and music theory have been slow to embrace the digital revolutions that have transformed other fields'' text-based scholarship (history and literature in particular).', 'The disciplines of music history and music theory have been slow to embrace the digital revolutions that have transformed other fields'' text-based scholarship (history and literature in particular). Computational musicology opens the door to the possibility of understanding&mdash;even if at a broad level&mdash;trends and norms of behavior of large repertories of music. This class presents the major approaches, results, and challenges of computational musicology through readings in the field, gaining familiarity with datasets, and hands on workshops and assignments on data analysis and &quot;corpus&quot; (i.e., repertory) studies. Class sessions alternate between discussion/lecture and labs on digital tools for studying music. A background in music theory and/or history is required, and experience in computer programming will be extremely helpful. Coursework culminates in an independent research project in quantitative or computational musicology that will be presented to the class as a whole.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-269-studies-in-western-music-history-quantitative-and-computational-approaches-to-music-history-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-269-studies-in-western-music-history-quantitative-and-computational-approaches-to-music-history-spring-2012/21m-269s12.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1785, 'Symphony and Concerto', 'This course is a survey of significant orchestral masterworks composed during three centuries.', 'This course is a survey of significant orchestral masterworks composed during three centuries. Listening assignments include 34 symphonies and 24 concertos, composed from the 1720s to the 1990s. Class discussion and oral presentations focus on the works in 18 miniature scores; prior score-reading experience is helpful. Each of the three written papers reviews a concert attended during the term. Since this is a participatory subject, each student will give oral presentations concerning composers and their symphonies and/or concertos.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-271-symphony-and-concerto-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-271-symphony-and-concerto-spring-2007/21m-271s07.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1786, 'Music of India', 'This course focuses on Hindustani classical music of North India, and also involves learning about the ancient foundations of the rich classical traditions of music and dance of all Indian art and culture.', 'This course focuses on Hindustani classical music of North India, and also involves learning about the ancient foundations of the rich classical traditions of music and dance of all Indian art and culture. Students explore the practice the ragas and talas through learning songs, dance, and drumming compositions, and develop insights through listening, readings, and concert attendance.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-291-music-of-india-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-291-music-of-india-spring-2007/21m-291s07.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1787, 'Popular Musics of the World', 'This course focuses on popular music, i.', 'This course focuses on popular music, i.e. music created for and transmitted by mass media. Various popular music genres from around the world will be studied through listening, reading and written assignments, with an emphasis on class discussion. In particular, we will consider issues of musical change, syncretism, Westernization, globalization, the impact of recording industries, and the post-colonial era. Case studies will include Afro-pop, reggae, bhangra, rave, and global hip-hop.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-294-popular-musics-of-the-world-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-294-popular-musics-of-the-world-spring-2005/21m-294s05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1788, 'Harmony and Counterpoint I', 'In this subject we will study the basic harmonic, melodic, and formal practices of western music, principally the classical music of central Europe during the eighteenth century.', 'In this subject we will study the basic harmonic, melodic, and formal practices of western music, principally the classical music of central Europe during the eighteenth century. Topics will include diatonic harmony, simple counterpoint in two parts, and tones of figuration. The coursework will combine composition, listening, analysis, and work in sight-singing and keyboard musicianship.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-301-harmony-and-counterpoint-i-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-301-harmony-and-counterpoint-i-spring-2005/21m-301s05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1789, 'Harmony and Counterpoint II', 'In this subject, we explore the harmonic, melodic, and formal practices of western music, principally the so-called ''Classical'' idiom of central Europe, ca.', 'In this subject, we explore the harmonic, melodic, and formal practices of western music, principally the so-called ''Classical'' idiom of central Europe, ca. 1750-1825. Topics include a quick review of material covered in 21M.301, chromatic harmony (viio7, bII6, and chords of the augmented sixth), and chromatic modulation; lecture study and discussion are complemented by work in the keyboard laboratory and sight-singing laboratory. All areas of study will be integrated in a semester-long project of composing a theme and two variations in Classical style.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-302-harmony-and-counterpoint-ii-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-302-harmony-and-counterpoint-ii-spring-2005/21m-302s05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1790, 'Writing in Tonal Forms II', 'This course builds on the composition techniques practiced in 21M.', 'This course builds on the composition techniques practiced in 21M.303 Writing in Tonal Forms I. Students undertake further written and analytic exercises in tonal music, including a sonata-form movement for string quartet. Students will also have the opportunity to write short works that experiment with the expanded tonal techniques of the late 19th and early 20th centuries. Musicianship laboratory is required.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-304-writing-in-tonal-forms-ii-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-304-writing-in-tonal-forms-ii-spring-2009/21m-304s09.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1791, 'Composing for Jazz Orchestra', 'This class explores composition and arrangement for the large jazz ensemble from 1920s foundations to current postmodern practice.', 'This class explores composition and arrangement for the large jazz ensemble from 1920s foundations to current postmodern practice. Consideration given to a variety of styles and to the interaction of improvisation and composition. Study of works by Basie, Ellington, Evans, Gillespie, Golson, Mingus, Morris, Nelson, Williams, and others. Open rehearsals, workshops, and performances of student compositions by the MIT Festival Jazz Ensemble and the Aardvark Jazz Orchestra. &nbsp;', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-342-composing-for-jazz-orchestra-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-342-composing-for-jazz-orchestra-fall-2008/21m-342f08.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1792, 'Musical Analysis', 'This class is an introduction to the analysis of tonal music.', 'This class is an introduction to the analysis of tonal music. Students develop analytical techniques based upon concepts learned in 21M.301-21M.302. Students study rhythm and form, harmony, line and motivic relationships at local and large scale levels of musical structure. Three papers (totaling 20 pages, one to be revised) and one oral presentation are required.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-350-musical-analysis-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-350-musical-analysis-spring-2008/21m-350s08.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1793, 'Composing with Computers I (Electronic Music Composition)', 'This class explores sound and what can be done with it.', 'This class explores sound and what can be done with it. Sources are recorded from students'' surroundings - sampled and electronically generated (both analog and digital). Assignments include composing with the sampled sounds, feedback, and noise, using digital signal processing (DSP), convolution, algorithms, and simple mixing. The class focuses on sonic and compositional aspects rather than technology, math, or acoustics, though these are examined in varying detail. Students complete weekly composition and listening assignments; material for the latter is drawn from sound art, experimental electronica, conventional and non-conventional classical electronic works, popular music, and previous students'' compositions.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-361-composing-with-computers-i-electronic-music-composition-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-361-composing-with-computers-i-electronic-music-composition-spring-2008/21m-361s08.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1794, 'Music and Technology: Recording Techniques and Audio Production', 'This course covers foundations, practices, and creative techniques in audio recording and music production, including microphone selection and placement, mixing, mastering, signal processing, automation, and digital audio workstations.', 'This course covers foundations, practices, and creative techniques in audio recording and music production, including microphone selection and placement, mixing, mastering, signal processing, automation, and digital audio workstations.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-380-music-and-technology-recording-techniques-and-audio-production-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-380-music-and-technology-recording-techniques-and-audio-production-spring-2012/21m-380s12.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1795, 'Music and Technology: Live Electronics Performance Practices', 'This course is a creative, hands-on exploration of contemporary and historical approaches to live electronics performance and improvisation, including basic analog instrument design, computer synthesis programming, and hardware and software interface design.', 'This course is a creative, hands-on exploration of contemporary and historical approaches to live electronics performance and improvisation, including basic analog instrument design, computer synthesis programming, and hardware and software interface design.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-380-music-and-technology-live-electronics-performance-practices-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-380-music-and-technology-live-electronics-performance-practices-spring-2011/21m-380s11.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1796, 'Music and Technology (Contemporary History and Aesthetics)', 'This course is an investigation into the history and aesthetics of music and technology as deployed in experimental and popular musics from the 19th century to the present.', 'This course is an investigation into the history and aesthetics of music and technology as deployed in experimental and popular musics from the 19th century to the present. Through original research, creative hands-on projects, readings, and lectures, the following topics will be explored. The history of radio, audio recording, and the recording studio, as well as the development of musique concrÃ¨te and early electronic instruments. The creation and extension of musical interfaces by composers such as Harry Partch, John Cage, Conlon Nancarrow, and others. The exploration of electromagnetic technologies in pickups, and the development of dub, hip-hop, and turntablism. The history and application of the analog synthesizer, from the Moog modular to the Roland TR-808. The history of computer music, including music synthesis and representation languages. Contemporary practices in circuit bending, live electronics, and electro-acoustic music, as well as issues in copyright and intellectual property, will also be examined. No prerequisites.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-380-music-and-technology-contemporary-history-and-aesthetics-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-380-music-and-technology-contemporary-history-and-aesthetics-fall-2009/21m-380f09.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1797, 'Vocal Repertoire and Performance: Women Composers', 'This course is for the singer and/or pianist interested in collaborative study of solo vocal performance.', 'This course is for the singer and/or pianist interested in collaborative study of solo vocal performance. This term we will focus upon the works of Women Composers. Students will gather biographical data and explore art songs, operatic arias, choral masterpieces, and arrangements employing sacred and secular texts. Additionally, students will conduct inquiry into works indicative of their own heritage.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-410-vocal-repertoire-and-performance-women-composers-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-410-vocal-repertoire-and-performance-women-composers-spring-2007/21m-410s07.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1798, 'Vocal Repertoire and Performance: African American Composers', 'The primary focus of this Vocal Repertoire and Performance course is placed upon the works of African American composers and concert artists.', 'The primary focus of this Vocal Repertoire and Performance course is placed upon the works of African American composers and concert artists. Students gather biographical data and explore art songs, operatic arias, ensembles, choral masterpieces, and arrangements employing sacred and secular texts. Additionally, students conduct inquiry into works representative of their own heritage. This course is required for vocalists in the MIT Emerson Music Performance program; others may be admitted by audition.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-410-vocal-repertoire-and-performance-african-american-composers-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-410-vocal-repertoire-and-performance-african-american-composers-spring-2005/21m-410s05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1799, 'Interdisciplinary Approaches to Musical Time', 'This course is an interdisciplinary exploration of three broad topics concerning music in relation to time.', 'This course is an interdisciplinary exploration of three broad topics concerning music in relation to time.Music as Architecture: the creation of musical shapes in time;Music as Memory: how musical understanding depends upon memory and reminiscence, with attention to analysis of musical structures; andTime as the Substance of Music: how different disciplines such as philosophy and neuroscience view the temporal dimension of musical processes and/or performances.Classroom discussion of these topics is complemented by three weekend concerts with pre-concert forums, jointly presented by the Boston Chamber Music Society (BCMS) and MIT Music &amp; Theater Arts.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-542-interdisciplinary-approaches-to-musical-time-january-iap-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-542-interdisciplinary-approaches-to-musical-time-january-iap-2010/21m-542iap10.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1800, 'Principles of Design', 'This course deals with advanced design theories and textual analysis.', 'This course deals with advanced design theories and textual analysis. Emphasis is placed on script analysis in general, as well as the investigation of design principles from a designer''s perspective. Students also refine technical skills in rendering and presentation, historical research, and analysis. Class sessions include interaction with student/faculty directors and other staff designers. The goal of this course is for students to approach text with a fresh vision and translate that vision into design for performance.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-603-principles-of-design-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-603-principles-of-design-fall-2005/21m-603f05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1801, 'Playwriting I', 'This class introduces the craft of writing for the theater.', 'This class introduces the craft of writing for the theater. Through weekly assignments, in class writing exercises, and work on a sustained piece, students explore scene structure, action, events, voice, and dialogue. We examine produced playscripts and discuss student work. This class''s emphasis is on process, risk-taking, and finding one''s own voice and vision.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-604-playwriting-i-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-604-playwriting-i-spring-2005/21m-604s05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1802, 'Foundations of Theater Practice', 'The goals of this class are two-fold: the first is to experience the creative processes and storytelling behind several of theater''s arts and to acquire the analytical skills necessary in assessing the meaning they transmit when they come together in production.', 'The goals of this class are two-fold: the first is to experience the creative processes and storytelling behind several of theater''s arts and to acquire the analytical skills necessary in assessing the meaning they transmit when they come together in production. Secondly, we will introduce you to these languages in a creative way by giving you hands-on experience in each. To that end, several Visiting Artists and MIT faculty in Theater Arts will guest lecture, lead workshops, and give you practical instruction in their individual art forms.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-611-foundations-of-theater-practice-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-611-foundations-of-theater-practice-fall-2009/21m-611f09.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1803, 'Learning from the Past: Drama, Science, Performance', 'This class explores the creation (and creativity) of the modern scientific and cultural world through study of western Europe in the 17th century, the age of Descartes and Newton, Shakespeare, Milton and Ford.', 'This class explores the creation (and creativity) of the modern scientific and cultural world through study of western Europe in the 17th century, the age of Descartes and Newton, Shakespeare, Milton and Ford. It compares period thinking to present-day debates about the scientific method, art, religion, and society. This team-taught, interdisciplinary subject draws on a wide range of literary, dramatic, historical, and scientific texts and images, and involves theatrical experimentation as well as reading, writing, researching and conversing. The primary theme of the class is to explore how England in the mid-seventeenth century became ''a world turned upside down'' by the new ideas and upheavals in religion, politics, and philosophy, ideas that would shape our modern world. Paying special attention to the ''theatricality'' of the new models and perspectives afforded by scientific experimentation, the class will read plays by Shakespeare, Tate, Brecht, Ford, Churchill, and Kushner, as well as primary and secondary texts from a wide range of disciplines. Students will also compose and perform in scenes based on that material.', 'http://ocw.mit.edu/courses/literature/21l-016-learning-from-the-past-drama-science-performance-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-016-learning-from-the-past-drama-science-performance-spring-2009/21l-016s09.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1804, 'Theater and Cultural Diversity in the U.S.', 'This course explores contemporary American theatrical expression as it may be organized around issues of gender and cultural identity.', 'This course explores contemporary American theatrical expression as it may be organized around issues of gender and cultural identity. This exploration will include the analysis of performances, scripts, and video documentation, as well as the invention of original documents of theatrical expression. Class lectures and discussions will analyze samples of Native American, Chicano, African American, and Asian American theater, taking into consideration the historical and political context for the creation of these works. Performance exercises will help students identify theatrical forms and techniques used by these theaters, and how these techniques contribute to the overall goals of specific theatrical expressions.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-621-theater-and-cultural-diversity-in-the-u-s-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-621-theater-and-cultural-diversity-in-the-u-s-spring-2008/21m-621s08.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1805, 'Traditions in American Concert Dance: Gender and Autobiography', 'This course explores the forms, contents, and context of world traditions in dance that played a crucial role in shaping American concert dance.', 'This course explores the forms, contents, and context of world traditions in dance that played a crucial role in shaping American concert dance. For example, we will identify dances from an African American vernacular tradition that were transferred from the social space to the concert stage. We will explore the artistic lives of such American dance artists as Katherine Dunham, Pearl Primus, and Alvin Ailey along with Isadora Duncan, Martha Graham, George Balanchine, and Merce Cunningham as American dance innovators. Of particular importance to our investigation will be the construction of gender and autobiography that lie at the heart of concert dance practice, and the ways in which these qualities have been choreographed by American artists.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-670-traditions-in-american-concert-dance-gender-and-autobiography-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-670-traditions-in-american-concert-dance-gender-and-autobiography-spring-2008/21m-670s08.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1806, 'Dance Theory and Composition', 'This course introduces students to the art and formal ideologies of contemporary dance.', 'This course introduces students to the art and formal ideologies of contemporary dance. We explore the aesthetic and technical underpinnings of contemporary dance composition. Basic compositional techniques are discussed and practiced, with an emphasis on principles such as weight, space, time, effort, and shape. Principles of musicality are considered and developed by each student. Working with each other as the raw material of the dance, students develop short compositions that reveal their understanding of basic techniques. Hopefully, students come to understand a range of compositional possibilities available to artists who work with the medium of the human body.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-675-dance-theory-and-composition-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-675-dance-theory-and-composition-fall-2003/21m-675f03.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1807, 'Script Analysis', 'This course focuses on reading a script theatrically with a view to mounting a coherent production.', 'This course focuses on reading a script theatrically with a view to mounting a coherent production. Through careful, intensive reading of a variety of plays from different periods and different aesthetics, a pattern emerges for discerning what options exist for interpretating a script. The Fall 2005 version of this course contains alternate readings and assignments sections.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-710-script-analysis-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-710-script-analysis-fall-2011/21m-710f11.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1808, 'Studies in Drama: Theater and Science in a Time of War', 'This course explores the creation (and creativity) of the modern scientific and cultural world through study of western Europe in the 17th century, the age of Descartes and Newton, Shakespeare, Rembrandt and Moli&egrave;re.', 'This course explores the creation (and creativity) of the modern scientific and cultural world through study of western Europe in the 17th century, the age of Descartes and Newton, Shakespeare, Rembrandt and Moli&egrave;re. The class compares period thinking to present-day debates about the scientific method, art, religion, and society. This team-taught, interdisciplinary subject draws on a wide range of literary, dramatic, historical, and scientific texts and images, and involves theatrical experimentation as well as reading, writing, researching and conversing.', 'http://ocw.mit.edu/courses/literature/21l-703-studies-in-drama-theater-and-science-in-a-time-of-war-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-703-studies-in-drama-theater-and-science-in-a-time-of-war-spring-2005/21l-703s05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1809, 'The Craft of Costume Design', 'This class provides an overview of some of the techniques used in creating costume pieces that are crafted rather than sewn.', 'This class provides an overview of some of the techniques used in creating costume pieces that are crafted rather than sewn. We will use a variety of materials and techniques to create specific costume pieces while at the same time exploring alternative applications possible for each material/technique.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-715-the-craft-of-costume-design-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-715-the-craft-of-costume-design-fall-2009/21m-715f09.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1810, 'Beginning Costume Design and Construction', 'This is an intermediate workshop designed for students who have a basic understanding of the principles of theatrical design and who want a more intensive study of costume design and the psychology of clothing.', 'This is an intermediate workshop designed for students who have a basic understanding of the principles of theatrical design and who want a more intensive study of costume design and the psychology of clothing. Students develop designs that emerge through a process of character analysis, based on the script and directorial concept. Period research, design, and rendering skills are fostered through practical exercises. Instruction in basic costume construction, including drafting and draping, provide tools for students to produce final projects.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-732-beginning-costume-design-and-construction-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-732-beginning-costume-design-and-construction-fall-2008/21m-732f08.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1811, 'Design for the Theater: Scenery', 'This course will examine theory of scenic design as currently practiced, as well as historical traditions for use of performance space and audience/performer engagement.', 'This course will examine theory of scenic design as currently practiced, as well as historical traditions for use of performance space and audience/performer engagement. Four play scripts and one opera or dance theater piece will be designed after in-depth analysis; emphasis will be on the social, political and cultural milieu at the time of their creation, and now.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-734-design-for-the-theater-scenery-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-734-design-for-the-theater-scenery-spring-2005/21m-734s05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1812, 'Lighting Design for the Theatre', 'This class explores the artistry of Lighting Design.', 'This class explores the artistry of Lighting Design. Students gain an overall technical working knowledge of the tools of the trade, and learn how, and where to apply them to a final design. However essential technical expertise is, the class stresses the artistic, conceptual, collaborative side of the craft. The class format is a ''hands on'' approach, with a good portion of class time spent in a theatre.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-734-lighting-design-for-the-theatre-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-734-lighting-design-for-the-theatre-fall-2003/21m-734f03.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1813, 'Technical Design: Scenery, Mechanisms, and Special Effects', 'This class looks at the special structural and practical needs of theatrical scenery and effects and how they can be constructed.', 'This class looks at the special structural and practical needs of theatrical scenery and effects and how they can be constructed. We map the technical design process from initial meetings to realization on stage. The class emphasizes safety, budgeting, and problem solving. Ten 1-3 page Tech notes are required as well as a final project. Work includes actual production assignments as well as paper design projects.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-735-technical-design-scenery-mechanisms-and-special-effects-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-735-technical-design-scenery-mechanisms-and-special-effects-spring-2004/21m-735s04.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1814, 'Playwrights'' Workshop', 'This course provides continued work in the development of play scripts for the theater.', 'This course provides continued work in the development of play scripts for the theater. Writers work on sustained pieces in weekly workshop meetings, individual consultation with the instructor, and in collaboration with student actors, directors, and designers. Fully developed scripts are eligible for inclusion in the Playwrights'' Workshop Production.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-785-playwrights-workshop-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-785-playwrights-workshop-spring-2012/21m-785s12.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1815, 'Theater Arts Topics - Suburbia', 'Directed practice in acting, production, or design on a sustained theater piece, either one-act or full length, from pre-rehearsal preparation to workshop production.', 'Directed practice in acting, production, or design on a sustained theater piece, either one-act or full length, from pre-rehearsal preparation to workshop production. Consult Theater Arts Office. Includes directed practice in stagecraft. Dramashop rehearses a production of Eric Bogosian''s play ''subUrbia'' for presentation the first two weekends in February. Visiting artist, David R. Gammons, directs. Approximately 10 roles filled by auditions. Students can receive up to six credits for acting or technical positions. Schedule of rehearsals to be arranged, but actors should be available during the afternoon. Students must be available for performances in early February. This course is offered during the Independent Activities Period (IAP), which is a special 4-week term at MIT that runs from the first week of January until the end of the month. Additional Faculty Michael Katz (Technical Director) Yuri Podpaly (Producer) Leslie Cocuzzo Held (Costume Design) Eric Levenson (Scenic Design) Karen Perlow (Lighting Design) Peter Whincop (Sound Design)', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-873-theater-arts-topics-suburbia-january-iap-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-873-theater-arts-topics-suburbia-january-iap-2008/21m-873iap08.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1816, 'Theater Arts Topics', 'Directed practice in acting, directing, or design on a sustained theater piece, either one-act or full length, from pre-rehearsal preparation to workshop production.', 'Directed practice in acting, directing, or design on a sustained theater piece, either one-act or full length, from pre-rehearsal preparation to workshop production.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-873-theater-arts-topics-fall-2004-january-iap-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-873-theater-arts-topics-fall-2004-january-iap-2005/21m-873iap05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1817, 'Vocal Repertoire and Performance: Women Composers', 'This course is for the singer and/or pianist interested in collaborative study of solo vocal performance.', 'This course is for the singer and/or pianist interested in collaborative study of solo vocal performance. This term we will focus upon the works of Women Composers. Students will gather biographical data and explore art songs, operatic arias, choral masterpieces, and arrangements employing sacred and secular texts. Additionally, students will conduct inquiry into works indicative of their own heritage.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-410-vocal-repertoire-and-performance-women-composers-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-410-vocal-repertoire-and-performance-women-composers-spring-2007/21m-410s07.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1818, 'Vocal Repertoire and Performance: African American Composers', 'The primary focus of this Vocal Repertoire and Performance course is placed upon the works of African American composers and concert artists.', 'The primary focus of this Vocal Repertoire and Performance course is placed upon the works of African American composers and concert artists. Students gather biographical data and explore art songs, operatic arias, ensembles, choral masterpieces, and arrangements employing sacred and secular texts. Additionally, students conduct inquiry into works representative of their own heritage. This course is required for vocalists in the MIT Emerson Music Performance program; others may be admitted by audition.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-410-vocal-repertoire-and-performance-african-american-composers-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-410-vocal-repertoire-and-performance-african-american-composers-spring-2005/21m-410s05.jpg', 'Music and Theater Arts', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1819, 'Justice', 'This course explores the ideal of social justice.', 'This course explores the ideal of social justice. What makes a society just? We will approach this question by studying three opposing theories of justice&mdash;utilitarianism, libertarianism, and egalitarian liberalism&mdash;each foundational to contemporary political thought and discourse.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-04j-justice-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-04j-justice-spring-2012/24-04js12.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1820, 'Philosophy of Law', '', '', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-235j-philosophy-of-law-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-235j-philosophy-of-law-spring-2012/24-235js12.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1821, 'Introduction to Political Thought', 'This course examines major texts in the history of political thought and the questions they raise about the design of the political and social order.', 'This course examines major texts in the history of political thought and the questions they raise about the design of the political and social order. It considers the ways in which thinkers have responded to the particular political problems of their day, and the ways in which they contribute to a broader conversation about human goods and needs, justice, democracy, and the proper relationship of the individual to the state. One aim will be to understand the strengths and weaknesses of various regimes and philosophical approaches in order to gain a critical perspective on our own. Thinkers include Plato, Aristotle, Machiavelli, Hobbes, Locke, Rousseau, Marx, and Tocqueville.', 'http://ocw.mit.edu/courses/political-science/17-03-introduction-to-political-thought-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-03-introduction-to-political-thought-spring-2004/17-03s04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1822, 'American Political Thought', 'This course surveys American political thought from the colonial era to the present.', 'This course surveys American political thought from the colonial era to the present. Required readings are drawn mainly from primary sources, including writings of politicians, activists, and theorists. Topics include the relationship between religion and politics, rights, federalism, national identity, republicanism versus liberalism, the relationship of subordinated groups to mainstream political discourse, and the role of ideas in politics. We will analyze the simultaneous radicalism and weakness of American liberalism, how the revolutionary ideas of freedom and equality run up against persistent patterns of inequality. Graduate students are expected to pursue the subject in greater depth through suggested reading and individual research.', 'http://ocw.mit.edu/courses/political-science/17-037-american-political-thought-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-037-american-political-thought-spring-2004/17-037s04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1823, 'Power: Interpersonal, Organizational and Global Dimensions', 'Using examples from anthropology and sociology alongside classical and contemporary social theory, this course explores the nature of dominant and subordinate relationships, types of legitimate authority, and practices of resistance.', 'Using examples from anthropology and sociology alongside classical and contemporary social theory, this course explores the nature of dominant and subordinate relationships, types of legitimate authority, and practices of resistance. The course also examines how we are influenced in subtle ways by the people around us, who makes controlling decisions in the family, how people get ahead at work, and whether democracies, in fact, reflect the ''will of the people.''', 'http://ocw.mit.edu/courses/anthropology/21a-245j-power-interpersonal-organizational-and-global-dimensions-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-245j-power-interpersonal-organizational-and-global-dimensions-fall-2005/21a-245jf05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1824, 'The Politics of Global Financial Relations', 'This course explores effects of globalization of finance on international relations and domestic politics.', 'This course explores effects of globalization of finance on international relations and domestic politics. Topics include international institutions and global governance; the multi-nationalization of production; effects of international capital markets on domestic politics; global finance and the developing world; and financial crises. Discussion of the interplay between politics and economics and the future of the nation-state.', 'http://ocw.mit.edu/courses/political-science/17-125-the-politics-of-global-financial-relations-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-125-the-politics-of-global-financial-relations-fall-2007/17-125f07.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1825, 'Sustainable Development: Theory and Policy', 'This course examines alternative conceptions and theoretical underpinnings of the notion of ''sustainable development.', 'This course examines alternative conceptions and theoretical underpinnings of the notion of ''sustainable development.'' It focuses on the sustainability problems of industrial countries (i.e., aging of populations, sustainable consumption, institutional adjustments, etc.); and of developing states and economies in transition (i.e., managing growth, sustainability of production patterns, pressures of population change, etc.). It also explores the sociology of knowledge around sustainability, the economic and technological dimensions and institutional imperatives along with implications for political constitution of economic performance.', 'http://ocw.mit.edu/courses/political-science/17-181-sustainable-development-theory-and-policy-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-181-sustainable-development-theory-and-policy-spring-2009/17-181s09.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1826, 'Globalization', 'This seminar explores changes in the international economy and their effects on domestic politics, economy, and society.', 'This seminar explores changes in the international economy and their effects on domestic politics, economy, and society. Is globalization really a new phenomenon? Is it irreversible? What are effects on wages and inequality, on social safety nets, on production, and innovation? How does it affect relations between developed countries and developing countries? How globalization affects democracy? These are some of the key issues that will be examined.', 'http://ocw.mit.edu/courses/political-science/17-196-globalization-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-196-globalization-fall-2005/17-196f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1827, 'Working in a Global Economy', 'The course introduces the main debates about the ''new'' global economy and their implications for practice and policy.', 'The course introduces the main debates about the ''new'' global economy and their implications for practice and policy. Experts from academia and business will share their findings about, and direct experiences with, different aspects of globalization.', 'http://ocw.mit.edu/courses/political-science/17-199j-working-in-a-global-economy-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-199j-working-in-a-global-economy-fall-2005/17-199jf05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1828, 'Introduction to the American Political Process', 'This class introduces students to innovative as well as classic approaches to studying U.', 'This class introduces students to innovative as well as classic approaches to studying U.S. government. The writing assignments will help you explore, through a variety of lenses, statis and change in the American political system over the last three decades. In the end each student will have a solid grounding in our national political institutions and processes, sharper reading and writing skills, and insight into approaching politics critically and analytically.', 'http://ocw.mit.edu/courses/political-science/17-20-introduction-to-the-american-political-process-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-20-introduction-to-the-american-political-process-spring-2004/17-20s04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1829, 'The Supreme Court, Civil Liberties, and Civil Rights', 'This course introduces students to the work of the Supreme Court and to the main outlines of American constitutional law, with an emphasis on the development of American ideas about civil rights.', 'This course introduces students to the work of the Supreme Court and to the main outlines of American constitutional law, with an emphasis on the development of American ideas about civil rights. The goal of the course is to provide students with a framework for understanding the major constitutional controversies of the present day through a reading of landmark Supreme Court cases and the public debates they have generated. The principal topics are civil liberties in wartime, race relations, privacy rights, and the law of criminal procedure.', 'http://ocw.mit.edu/courses/political-science/17-245-the-supreme-court-civil-liberties-and-civil-rights-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-245-the-supreme-court-civil-liberties-and-civil-rights-fall-2006/17-245f06.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1830, 'Law and Society', 'Law is a common and yet distinct aspect of everyday life in modern societies.', 'Law is a common and yet distinct aspect of everyday life in modern societies.&#160;This course examines the central features of law as a social institution and as a feature of popular culture.&#160;We will explore the nature of law as a set of social systems, central actors in the systems, legal reasoning, and the relationship of the legal form and reasoning to social change. The course emphasizes the relationship between the internal logic of legal devices and economic, political and social processes.&#160;Emphasis is placed upon developing a perspective which views law as a practical resource, a mechanism for handling the widest range of unspecified social issues, problems, and conflicts, and at the same time, as a set of shared representations and aspirations. We will explore the range of experiences of law for its ministers (lawyers, judges, law enforcement agents and administrators) as well as for its supplicants (citizens, plaintiffs, defendants).&#160;We will examine how law is mobilized and deployed by professionals and ordinary citizens.&#160;We cannot cover all aspects of the legal system, nor focus on all the different actors. A set of topics has been selected to develop understanding of the situational and systemic demands within which actors in the legal system operate and perform their roles; at the same time, we will try to discover systematic patterns in the uses and consequences of law.&#160;Throughout the course there is concern for understanding what we mean by legality and the rule of law.', 'http://ocw.mit.edu/courses/anthropology/21a-219-law-and-society-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-219-law-and-society-spring-2003/21a-219s03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1831, 'Congress and the American Political System II', 'This course analyzes the development of the United States Congress by focusing on the competing theoretical lenses through which legislatures have been studied.', 'This course analyzes the development of the United States Congress by focusing on the competing theoretical lenses through which legislatures have been studied. In particular, it compares sociological and economic models of legislative behavior, applying those models to floor decision-making, committee behavior, political parties, relations with other branches of the Federal government, and elections. Graduate students are expected to pursue the subject in greater depth through reading and individual research.', 'http://ocw.mit.edu/courses/political-science/17-261-congress-and-the-american-political-system-ii-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-261-congress-and-the-american-political-system-ii-fall-2005/17-261f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1832, 'Public Opinion and American Democracy', 'This course will examine public opinion and assess its place in the American political system.', 'This course will examine public opinion and assess its place in the American political system. The course will emphasize both how citizens'' thinking about politics is shaped and the role of public opinion in political campaigns, elections, and government.', 'http://ocw.mit.edu/courses/political-science/17-265-public-opinion-and-american-democracy-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-265-public-opinion-and-american-democracy-spring-2007/17-265s07.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1833, 'Fundamentals of Public Policy', 'Fundamentals of Public Policy is an introductory course that explores policy-making as both a problem-solving process and a political process.', 'Fundamentals of Public Policy is an introductory course that explores policy-making as both a problem-solving process and a political process. We look at policy-making from the perspective of different focal actors and institutions, including: administrative agencies, legislators, the courts, the mass public, interest groups, and the media. We examine the interplay between policy development and institutions, and review normative and empirical models of policy-making. Exploring these issues will require us to address questions like: How and why does something come to be seen as a &quot;public problem&quot; requiring a governmental response, while others fail to get attention? Why do we need public policies? What determines the content and nature of public policies? Who decides public policy priorities? Does public policy ever accomplish anything worthwhile?', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-002j-fundamentals-of-public-policy-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-002j-fundamentals-of-public-policy-fall-2004/11-002jf04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1834, 'U.S. Social Policy', 'This subject examines the historical development and contemporary politics of social policy in the United States.', 'This subject examines the historical development and contemporary politics of social policy in the United States. We will discuss the kinds of risks individuals face over a lifetime and why some are ameliorated by social policy while others are not (and how the U.S. is similar or different from other countries in this regard). We will examine the policymaking process in the U.S., why some alternatives are implemented and others abandoned, why some interests are privileged over others, and how the designs of policies can feed back and shape politics in a given policy area. Along the way we will examine interactions among political institutions, policy elites, the media, and the mass public.', 'http://ocw.mit.edu/courses/political-science/17-317-u-s-social-policy-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-317-u-s-social-policy-spring-2006/17-317s06.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1835, 'Environmental Politics and Policy', '''Environmental Politics &amp; Policy'' explores the workings of environmental policymaking in the United States.', '''Environmental Politics &amp; Policy'' explores the workings of environmental policymaking in the United States. What are the big issues facing environmental policy? How did we end up with the policies we have today? Why does it take a crisis to move environmental policy forward? Why do political factors - economic interests, social and political values, bureaucratic styles, ideologies, elections, etc. - always seem to overwhelm sound scientific and engineering judgment in determining policy outcomes? Case studies ranging from cleaning up toxic waste pollution to endangered species protection probe the clashes between science and politics at local, state, and federal levels.', 'http://ocw.mit.edu/courses/political-science/17-32-environmental-politics-and-policy-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-32-environmental-politics-and-policy-spring-2003/17-32f03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1836, 'Human Rights in Theory and Practice', 'This course provides a rigorous and critical introduction to the foundation, structure and operation of the international human rights movement.', 'This course provides a rigorous and critical introduction to the foundation, structure and operation of the international human rights movement. It includes leading theoretical and institutional issues and the functioning of the international human rights mechanisms including non-governmental and inter-governmental ones. It covers cutting-edge human rights issues including gender and race discrimination, religion and state, national security and terrorism, globalization and human rights, and technology and human rights.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-164-human-rights-in-theory-and-practice-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-164-human-rights-in-theory-and-practice-fall-2010/11-164f10.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1837, 'Seminar on Politics and Conflict in the Middle East', 'This course focuses on evolution of contemporary politics and economics.', 'This course focuses on evolution of contemporary politics and economics. The subject is divided into four parts: Context: historical and strategic perspectives, theoretical issues, and sources and forms of conflict; Continuity: detailed analysis conflicts systems and their persistence, as well as regional competition and recent wars &#8211; focusing on specific countries and cases; Complexity: highlighting situation specific strategic gains and losses; and Convergence: focusing future configurations of conflict and cooperation. Throughout the course, special attention is given to sources and transformations of power, population dynamics and migration, resources and energy, as well as implications of technological change.', 'http://ocw.mit.edu/courses/political-science/17-405-seminar-on-politics-and-conflict-in-the-middle-east-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-405-seminar-on-politics-and-conflict-in-the-middle-east-fall-2003/17-405f03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1838, 'Chinese Foreign Policy: International Relations and Strategy', 'China''s rise as a great power raises important questions about how that power might be used in its relations with other states.', 'China''s rise as a great power raises important questions about how that power might be used in its relations with other states. Nowhere are such questions more salient than in the future trajectory of China''s conflict behavior, including its approach to deterrence, crisis management and the use of force. To explore these important questions in China''s international relations, this seminar examines the evolution of Chinese strategic thought, in primary sources as well as its reflection in the interactions among Chinese states and between China and other states.', 'http://ocw.mit.edu/courses/political-science/17-407-chinese-foreign-policy-international-relations-and-strategy-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-407-chinese-foreign-policy-international-relations-and-strategy-spring-2009/17-407s09.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1839, 'Chinese Foreign Policy', 'This lecture course provides students with a comprehensive introduction to the international relations of the People&rsquo;s Republic of China.', 'This lecture course provides students with a comprehensive introduction to the international relations of the People&rsquo;s Republic of China. China&rsquo;s foreign relations during the Cold War as well as contemporary diplomatic, security and economic issues will be examined to identify and explain China&rsquo;s foreign policy goals and their implementation since 1949. Throughout, this course will investigate the sources of conflict and cooperation in China&rsquo;s behavior, assessing competing explanations for key events and policies. Readings will be drawn from political science, history, and international relations theory.', 'http://ocw.mit.edu/courses/political-science/17-407-chinese-foreign-policy-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-407-chinese-foreign-policy-fall-2005/17-407f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1840, 'Field Seminar: International Relations Theory', 'This seminar provides an overview of the field of international relations.', 'This seminar provides an overview of the field of international relations. Each week, a different approach to explaining international relations will be examined. By surveying major concepts and theories in the field, the seminar will also assist graduate students in preparing for the comprehensive examination and further study in the department''s more specialized offerings in international relations.', 'http://ocw.mit.edu/courses/political-science/17-418-field-seminar-international-relations-theory-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-418-field-seminar-international-relations-theory-fall-2011/17-418f11.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1841, 'Causes and Prevention of War', 'The causes and prevention of interstate war are the central topics of this course.', 'The causes and prevention of interstate war are the central topics of this course. The course goal is to discover and assess the means to prevent or control war. Hence we focus on manipulable or controllable war-causes. The topics covered include the dilemmas, misperceptions, crimes and blunders that caused wars of the past; the origins of these and other war-causes; the possible causes of wars of the future; and possible means to prevent such wars, including short-term policy steps and more utopian schemes. The historical cases covered include the Peloponnesian and Seven Years wars, World War I, World War II, Korea, the Arab-Israel conflict, and the U.S.-Iraq and U.S. al-Queda wars. This is an undergraduate course, but it is open to graduate students.', 'http://ocw.mit.edu/courses/political-science/17-42-causes-and-prevention-of-war-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-42-causes-and-prevention-of-war-spring-2009/17-42s09.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1842, 'International Relations of East Asia', 'The aim of this course is to introduce and analyze the international relations of East Asia.', 'The aim of this course is to introduce and analyze the international relations of East Asia. With four great powers, three nuclear weapons states, and two of the world''s largest economies, East Asia is one of the most dynamic and consequential regions in world politics. This course will examine the sources of conflict and cooperation in both periods, assessing competing explanations for key events in East Asia''s international relations. Readings will be drawn from international relations theory, political science and history.', 'http://ocw.mit.edu/courses/political-science/17-433-international-relations-of-east-asia-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-433-international-relations-of-east-asia-spring-2011/17-433s11.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1843, 'International Politics and Climate Change', 'This course examines the interconnections of international politics and climate change.', 'This course examines the interconnections of international politics and climate change. Beginning with an analysis of the strategic and environmental legacies of the 20th Century, it explores the politicization of the natural environment, the role of science in this process, and the gradual shifts in political concerns to incorporate ''nature''. Two general thrusts of climate-politics connections are pursued, namely those related to (a) conflict &ndash; focusing on threats to security due to environmental dislocations and (b) cooperation &ndash; focusing on the politics of international treaties that have contributed to emergent processes for global accord in response to evidence of climate change. The course concludes by addressing the question of: ''What Next?''', 'http://ocw.mit.edu/courses/political-science/17-441-international-politics-and-climate-change-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-441-international-politics-and-climate-change-fall-2007/17-441f07.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1844, 'Cyberpolitics in International Relations: Theory, Methods, Policy', 'This course focuses on cyberspace and its implications for private and public, sub-national, national, and international actors and entities.', 'This course focuses on cyberspace and its implications for private and public, sub-national, national, and international actors and entities.', 'http://ocw.mit.edu/courses/political-science/17-447-cyberpolitics-in-international-relations-theory-methods-policy-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-447-cyberpolitics-in-international-relations-theory-methods-policy-fall-2011/17-447f11.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1845, 'Cyberpolitics in International Relations: Theory, Methods, Policy', 'This course focuses on cyberspace and its implications for private and public, sub-national, national, and international actors and entities.', 'This course focuses on cyberspace and its implications for private and public, sub-national, national, and international actors and entities.', 'http://ocw.mit.edu/courses/political-science/17-447-cyberpolitics-in-international-relations-theory-methods-policy-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-447-cyberpolitics-in-international-relations-theory-methods-policy-fall-2011/17-447f11.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1846, 'American National Security Policy', 'This course examines the problems and issues confronting American national security policymakers and the many factors that influence the policies that emerge.', 'This course examines the problems and issues confronting American national security policymakers and the many factors that influence the policies that emerge. But this is not a course about ''threats,'' military strategies, or the exercise of military power. What threatens those interests? How should the U.S. defend those interests? What kind of military should we build? Should the U.S. enter into alliances with other countries? Do we need a larger Navy? How much should we spend on weapons procurement? The course has four broad goals: to demonstrate that definitions of national security and the specification of vital interests are subjective and fluid and that they are as much functions of domestic politics as they are responses to international politics and ''objective threats''; to demonstrate that policy decisions involve complex tradeoffs among political, social, economic, military, legal, and ethical goals and values; to explore how the many organizations, institutions, and individuals that participate in American national security policymaking affect policy formulation, implementation, and outcomes; and to better understand the historical context, evolution, and linkages of national security problems and solutions. The course is organized along an historical time line. Beginning with the final days of World War II we follow American national security policy from the first stirrings of confrontation with the Soviet Union and China, into two hot wars in Asia that cost over 100,000 American lives and spawned social upheavals, through a close encounter with nuclear war, stumbling into the era of arms control, and conclude with the collapse of the communism. Selective case studies, memoirs, and original documents act as windows into each period. What were US national security decision makers thinking? What were they worried about? How did they see their options?', 'http://ocw.mit.edu/courses/political-science/17-471-american-national-security-policy-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-471-american-national-security-policy-fall-2002/17-471f02.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1847, 'Introduction to Comparative Politics', 'This class first offers some basic analytical frameworks - culture, social structure, and institutions - that you can use to examine a wide range of political outcomes.', 'This class first offers some basic analytical frameworks - culture, social structure, and institutions - that you can use to examine a wide range of political outcomes. We then use these frameworks to understand (1) the relationship between democracy and economic development and (2) the relative centralization of political authority across countries. We will use theoretical arguments and a wide range of case studies to address several questions: Why are some countries democratic and others not? How does democracy affect economic development and political conflict? Why do some countries centralize power while others threaten to fall apart through secession and civil war? We will use examples from a wide range of countries including Argentina, Brazil, Germany, Iraq, Italy, Mexico, and the United States. The lessons drawn from these countries will prepare you to analyze other countries of your own choosing in the paper assignments. At the end of the course, you should be able to analyze political events around the world, drawing on the theoretical explanations provided in the class.', 'http://ocw.mit.edu/courses/political-science/17-50-introduction-to-comparative-politics-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-50-introduction-to-comparative-politics-fall-2006/17-50f06.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1848, 'The Rise and Fall of Democracy/ Regime Change', 'Coups, civil wars, revolutions, and peaceful transitions are the ''real stuff'' of political science.', 'Coups, civil wars, revolutions, and peaceful transitions are the ''real stuff'' of political science. They show us why politics matters, and they highlight the consequences of political choices in times of institutional crisis. This course will help you understand why democracies emerge and why they die, from ancient times to the recent wave of democratization in Southern Europe, Eastern Europe, and the developing world. Few things are more dramatic than the collapse of a political system, whether through violent conflict or the peaceful negotiation of new political institutions. Explaining why regimes break down, why new ones emerge, and how these new regimes are consolidated are among the most important questions in political science. Not surprisingly, regime change has obsessed scholars for centuries, from Aristotle to Machiavelli to Marx to current theorists of democratization. You will review several broad explanations for regime change before turning to more detailed examination of some of history''s most famous and theoretically interesting political transitions: the collapse of the Weimar Republic in Germany; democratic breakdown, the consolidation of military dictatorship, and re-democratization in Chile; the breakdown of British colonial rule in the Massachussets Bay Colony; and protracted political transition in Mexico. There will be shorter discussions of democratization in Spain, South Africa, and South Korea; as well as democratic collapse in Brazil, Austria, and Italy.', 'http://ocw.mit.edu/courses/political-science/17-508-the-rise-and-fall-of-democracy-regime-change-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-508-the-rise-and-fall-of-democracy-regime-change-spring-2002/17-508s02.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1849, 'Ethnicity and Race in World Politics', 'Discerning the ethnic and racial dimensions of politics is considered by some indispensable to understanding contemporary world politics.', 'Discerning the ethnic and racial dimensions of politics is considered by some indispensable to understanding contemporary world politics. This course seeks to answer fundamental questions about racial and ethnic politics. To begin, what are the bases of ethnic and racial identities? What accounts for political mobilization based upon such identities? What are the political claims and goals of such mobilization and is conflict between groups and/or with government forces inevitable? How do ethnic and racial identities intersect with other identities, such as gender and class, which are themselves the sources of social, political, and economic cleavages? Finally, how are domestic ethnic/racial politics connected to international human rights? To answer these questions, the course begins with an introduction to dominant theoretical approaches to racial and ethnic identity. The course then considers these approaches in light of current events in Africa, Asia, Latin America, Europe, and the United States.', 'http://ocw.mit.edu/courses/political-science/17-523-ethnicity-and-race-in-world-politics-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-523-ethnicity-and-race-in-world-politics-fall-2005/17-523f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1850, 'Democratization in Asia, Africa, and Latin America', 'Recent years have seen an astonishing spread of democracy to many African, Asian, and Latin American countries.', 'Recent years have seen an astonishing spread of democracy to many African, Asian, and Latin American countries. What caused these dramatic political transitions? What challenges do democratizing countries in the Third World face? Will these new democracies endure? We will take up these questions using film, fiction, and popular journalism, as well as scholarly research. We will also focus on a small number of countries (Brazil, India, Mexico, Nigeria, Senegal, Singapore, and Sri Lanka) in order to explore in greater depth some of the most important political challenges faced by developing countries. Although the class focuses on the developing world, many of the lessons should ''travel'' to democratizing countries in other regions.', 'http://ocw.mit.edu/courses/political-science/17-53-democratization-in-asia-africa-and-latin-america-fall-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-53-democratization-in-asia-africa-and-latin-america-fall-2001/17-53f01.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1851, 'Politics and Policy in Contemporary Japan', 'This subject is designed for upper level undergraduates and graduate students as an introduction to politics and the policy process in modern Japan.', 'This subject is designed for upper level undergraduates and graduate students as an introduction to politics and the policy process in modern Japan. The semester is divided into two parts. After a two-week general introduction to Japan and to the dominant approaches to the study of Japanese history, politics and society, we will begin exploring five aspects of Japanese politics: party politics, electoral politics, interest group politics, bureaucratic politics, and policy, which will be broken up into seven additional sections. We will try to understand the ways in which the actors and institutions identified in the first part of the semester affect the policy process across a variety of issues areas.', 'http://ocw.mit.edu/courses/political-science/17-537-politics-and-policy-in-contemporary-japan-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-537-politics-and-policy-in-contemporary-japan-spring-2009/17-537s09.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1852, 'Japanese Politics and Society', 'This course is designed for students seeking a fundamental understanding of Japanese history, politics, culture, and the economy.', 'This course is designed for students seeking a fundamental understanding of Japanese history, politics, culture, and the economy. ''Raw Fish 101'' (as it is often labeled) combines lectures, seminar discussion, small-team case studies, and Web page construction exercises, all designed to shed light on contemporary Japan.', 'http://ocw.mit.edu/courses/political-science/17-541-japanese-politics-and-society-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-541-japanese-politics-and-society-fall-2008/17-541f08.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1853, 'Government and Politics of China', 'This course analyzes contemporary Chinese politics, both pre-Communist and Communist.', 'This course analyzes contemporary Chinese politics, both pre-Communist and Communist. It focuses on the process of modernization and political development of Chinese civilization. Graduate students are expected to pursue the subject at greater depth through reading and individual research.', 'http://ocw.mit.edu/courses/political-science/17-547-government-and-politics-of-china-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-547-government-and-politics-of-china-fall-2002/17-547f02.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1854, 'Introduction to Latin American Studies', 'This course is designed as an introduction to Latin American politics and society for undergraduates at MIT.', 'This course is designed as an introduction to Latin American politics and society for undergraduates at MIT. No background on the region is required. Overall workload (reading, writing, class participation, and examinations) is similar to that of other HASS-D courses. Many of the themes raised here are covered in greater detail in other courses: 21F.020J (New World Literature), 21F.716 (Introduction to Contemporary Hispanic Literature), 21F.730 (Twentieth and Twentyfirst-Century Spanish American Literaturere), 21F.735 (Advanced Topics in Hispanic Literature and Film), 21A.220 (The Conquest of America), 21H.802 (Modern Latin America), 3.982 (The Ancient Andean World), 3.983 (Ancient Mesoamerican Civilization), 17.507 (Democratization and Democratic Collapse), and 17.554 (Political Economy of Latin America).', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-084j-introduction-to-latin-american-studies-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-084j-introduction-to-latin-american-studies-fall-2005/21f-084jf05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1855, 'Comparative Security and Sustainability', 'This course focuses on the complexities associated with security and sustainability of states in international relations.', 'This course focuses on the complexities associated with security and sustainability of states in international relations. Covering aspects of theory, methods and empirical analysis, the course is in three parts, and each consists of seminar sessions focusing on specific topics.', 'http://ocw.mit.edu/courses/political-science/17-559-comparative-security-and-sustainability-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-559-comparative-security-and-sustainability-fall-2004/17-559f04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1856, 'Introduction to Latin American Studies', 'Interdisciplinary introduction to contemporary Latin America, drawing on films, literature, popular press accounts, and scholarly research.', 'Interdisciplinary introduction to contemporary Latin America, drawing on films, literature, popular press accounts, and scholarly research. Topics include economic development, ethnic and racial identity, religion, revolution, democracy, transitional justice, and the rule of law. Examples draw on a range of countries in the region, especially Mexico, Chile, and Brazil. Includes a heavy oral participation component, with regular breakout groups, formal class presentations on pressing social issues (such as criminal justice and land tenure), and a structured class debate.', 'http://ocw.mit.edu/courses/political-science/17-55j-introduction-to-latin-american-studies-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-55j-introduction-to-latin-american-studies-fall-2006/17-55jf06.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1857, 'European Politics', 'This course examines the organization of political power and the dynamics of political change in Britain, France, Germany, and Italy.', 'This course examines the organization of political power and the dynamics of political change in Britain, France, Germany, and Italy. In particular, it focuses on the structure of political power within the state, and on important institutions that form the link between state and society, especially political parties and interest organizations.', 'http://ocw.mit.edu/courses/political-science/17-561-european-politics-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-561-european-politics-fall-2011/17-561f11.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1858, 'Soviet Politics and Society, 1917-1991', 'At its greatest extent the former Soviet Union encompassed a geographical area that covered one-sixth of the Earth''s landmass.', 'At its greatest extent the former Soviet Union encompassed a geographical area that covered one-sixth of the Earth''s landmass. It spanned 11 time zones and contained over 100 distinct nationalities, 22 of which numbered over one million in population. In the 74 years from the October Revolution in 1917 to the fall of Communism in 1991, the Union of Soviet Socialist Republics, its leaders and its people, had to face a number of difficult challenges: the overthrow of the Tsarist autocracy, the establishment of a new state, four years of civil war, a famine, transition to a mixed economy, political strife after Lenin''s death, industrialization, collectivization, a second famine, political Show Trials, World War II, post-war reconstruction and repression, the ''Thaw'' after Stalin''s death, Khrushchev''s experimentation, and Brezhnev''s decline. Each of these challenges engendered new solutions and modifications in what can be loosely called the evolving ''Soviet system.''', 'http://ocw.mit.edu/courses/political-science/17-57j-soviet-politics-and-society-1917-1991-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-57j-soviet-politics-and-society-1917-1991-spring-2003/17-57js03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1859, 'Political Science Scope and Methods', 'This course is designed to provide an introduction to a variety of empirical research methods used by political scientists.', 'This course is designed to provide an introduction to a variety of empirical research methods used by political scientists. The primary aims of the course are to make you a more sophisticated consumer of diverse empirical research and to allow you to conduct advanced independent work in your junior and senior years. This is not a course in data analysis. Rather, it is a course on how to approach political science research.', 'http://ocw.mit.edu/courses/political-science/17-869-political-science-scope-and-methods-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-869-political-science-scope-and-methods-fall-2010/17-869f10.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1860, 'Political Science Laboratory', 'This course introduces students to the conduct of political research using quantitative methodologies.', 'This course introduces students to the conduct of political research using quantitative methodologies. The methods are examined in the context of specific political research activities like public opinion surveys, voting behavior, Congressional behavior, comparisons of political processes in different countries, and the evaluation of public policies. Students participate in joint class projects and conduct individual projects.', 'http://ocw.mit.edu/courses/political-science/17-871-political-science-laboratory-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-871-political-science-laboratory-spring-2004/17-871s04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1861, 'Game Theory and Political Theory', 'Increasingly, political scientists are using game theory to analyze strategic interactions across many different settings.', 'Increasingly, political scientists are using game theory to analyze strategic interactions across many different settings. Each of the sub-fields, to differing degrees, has seen game theoretic concepts enter its vocabulary, and students entering the profession will need to understand the potential and limits of game theory. This course aims to give students an entry-level understanding of the basic concepts of game theory, and how these concepts have been applied to the study of political phenomena. Because an important component of game theory in political science and political economy is the analysis of substantive political phenomena, we will cover illustrative examples each week in combination with methodological developments. The political and economic phenomena that we will examine include legislative rules, nuclear deterrence, electoral competition, and imperfect markets.', 'http://ocw.mit.edu/courses/political-science/17-881-game-theory-and-political-theory-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-881-game-theory-and-political-theory-fall-2004/17-881f04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1862, 'Forms of Political Participation: Old and New', 'How and why do we participate in public life? How do we get drawn into community and political affairs? In this course we examine the associations and networks that connect us to one another and structure our social and political interactions.', 'How and why do we participate in public life? How do we get drawn into community and political affairs? In this course we examine the associations and networks that connect us to one another and structure our social and political interactions. Readings are drawn from a growing body of research suggesting that the social networks, community norms, and associational activities represented by the concepts of civil society and social capital can have important effects on the functioning of democracy, stability and change in political regimes, the capacity of states to carry out their objectives, and international politics.', 'http://ocw.mit.edu/courses/political-science/17-905-forms-of-political-participation-old-and-new-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-905-forms-of-political-participation-old-and-new-spring-2005/17-905s05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1863, 'Reading Seminar in Social Science: The Geopolitics and Geoeconomics of Global Energy', 'This course focuses on strategic and political implications of ongoing trends in global energy markets, particularly markets for crude oil and natural gas.', 'This course focuses on strategic and political implications of ongoing trends in global energy markets, particularly markets for crude oil and natural gas. The course examines the world''s major oil and natural gas producing regions: the Middle East, the Caspian Region, Russia, Venezuela, and the North Sea. Producer-consumer relationships are considered for China, India, Japan, and the United States. United States foreign policy implications, especially with respect to China, are discussed.', 'http://ocw.mit.edu/courses/political-science/17-906-reading-seminar-in-social-science-the-geopolitics-and-geoeconomics-of-global-energy-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-906-reading-seminar-in-social-science-the-geopolitics-and-geoeconomics-of-global-energy-spring-2007/17-906s07.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1864, 'Reading Seminar in Social Science: Intelligence and National Security', 'This course will examine the origins, structure and functions of the U.', 'This course will examine the origins, structure and functions of the U.S. Intelligence Community and its relationship to national security policy. It will look in some detail at the key intelligence agencies and the functions they perform, including collection, analysis, counterintelligence and covert action. It will also look at some of the key intelligence missions, such as strategic warning, counterterrorism, counterproliferation, and counterinsurgency. Finally, it will examine some of the major controversies concerning intelligence, including its successes and failures, relationship to policymakers, congressional oversight, and the need for reform.', 'http://ocw.mit.edu/courses/political-science/17-908-reading-seminar-in-social-science-intelligence-and-national-security-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-908-reading-seminar-in-social-science-intelligence-and-national-security-fall-2005/17-908f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1865, 'Reading Seminar in Social Science: International Political Economy', 'This course examines the politics of international economic relations.', 'This course examines the politics of international economic relations. We begin with a discussion of the analytical ''lenses'' through which we can view the global economy. We then examine the politics of trade policy, multinational corporations, and international monetary and financial relations. We will also examine third-world development, communist transition, and the debate over ''globalization.'' Finally we will explore the fight against terrorist financing and money laundering, the proper role of international financial institutions (including the IMF), and the impact of the global economy on the ability of governments to make policy within their own borders.', 'http://ocw.mit.edu/courses/political-science/17-910-reading-seminar-in-social-science-international-political-economy-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-910-reading-seminar-in-social-science-international-political-economy-fall-2006/17-910f06.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1866, 'International Politics in the New Century - via Simulation, Interactive Gaming, and ''Edutainment''', 'This workshop is designed to introduce students to different perspectives on politics and the state of the world through new visualization techniques and approaches to interactive political gaming (and selective ''edutainment'').', 'This workshop is designed to introduce students to different perspectives on politics and the state of the world through new visualization techniques and approaches to interactive political gaming (and selective ''edutainment''). Specifically, we shall explore applications of&nbsp;interactive tools (such as video&nbsp;and web-based games, blogs or simulations) to examine critical challenges in international politics of the 21C century focusing specifically on general insights and specific understandings generated by operational uses of&nbsp;core concepts in political science.', 'http://ocw.mit.edu/courses/political-science/17-914-international-politics-in-the-new-century-via-simulation-interactive-gaming-and-edutainment-january-iap-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-914-international-politics-in-the-new-century-via-simulation-interactive-gaming-and-edutainment-january-iap-2005/17-914iap05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1867, 'Reading Seminar in Social Science: The Geopolitics and Geoeconomics of Global Energy', 'This course focuses on strategic and political implications of ongoing trends in global energy markets, particularly markets for crude oil and natural gas.', 'This course focuses on strategic and political implications of ongoing trends in global energy markets, particularly markets for crude oil and natural gas. The course examines the world''s major oil and natural gas producing regions: the Middle East, the Caspian Region, Russia, Venezuela, and the North Sea. Producer-consumer relationships are considered for China, India, Japan, and the United States. United States foreign policy implications, especially with respect to China, are discussed.', 'http://ocw.mit.edu/courses/political-science/17-906-reading-seminar-in-social-science-the-geopolitics-and-geoeconomics-of-global-energy-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-906-reading-seminar-in-social-science-the-geopolitics-and-geoeconomics-of-global-energy-spring-2007/17-906s07.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1868, 'Conversations You Can''t Have on Campus: Race, Ethnicity, Gender and Identity', 'What is race? What is ethnicity? How can communication and relationships between men and women be improved? What causes segregation in our society? How do stereotypes develop and why do they persist? How do an individual''s racial, ethnic, and sexual identities form and develop? This course explores these topics and more.', 'What is race? What is ethnicity? How can communication and relationships between men and women be improved? What causes segregation in our society? How do stereotypes develop and why do they persist? How do an individual''s racial, ethnic, and sexual identities form and develop? This course explores these topics and more.', 'http://ocw.mit.edu/courses/political-science/17-s914-conversations-you-cant-have-on-campus-race-ethnicity-gender-and-identity-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-s914-conversations-you-cant-have-on-campus-race-ethnicity-gender-and-identity-spring-2012/17-s914s12.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1869, 'Thesis Research Design Seminar', 'This seminar is for students who plan to write a senior thesis in Political Science, and is required of all MIT Political Science majors.', 'This seminar is for students who plan to write a senior thesis in Political Science, and is required of all MIT Political Science majors. Seminar participants will develop their research topics, review relevant research and scholarship, frame their research questions and arguments, choose an appropriate methodology for analysis, draft the introductory and methodology sections of their theses, and write a complete prospectus of the project.', 'http://ocw.mit.edu/courses/political-science/17-tht-thesis-research-design-seminar-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-tht-thesis-research-design-seminar-fall-2004/17-thtf04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1870, 'Feminist Political Thought', 'In this course we will examine the development of feminist theory over time.', 'In this course we will examine the development of feminist theory over time. Some subjects we will examine in detail include suffrage and equality; radical feminism; psychoanalysis and feminism; theories of power; sexuality and gender; embodied knowledge; pornography; identities and global feminism; militarism; and the welfare state. Throughout the course we will analyze different ways of looking at power and political culture in modern societies, issues of race and class, poverty and welfare, sexuality and morality.', 'http://ocw.mit.edu/courses/special-programs/sp-601j-feminist-political-thought-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-601j-feminist-political-thought-spring-2010/sp-601js10.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1871, 'Citizenship and Pluralism', 'This course will serve as both an introduction to contemporary political philosophy and a way to explore issues of pluralism and multiculturalism.', 'This course will serve as both an introduction to contemporary political philosophy and a way to explore issues of pluralism and multiculturalism. Racial and ethnic groups, national minorities, aboriginals, women, sexual minorities, and other groups have organized to highlight injustice and demand recognition and accommodation on the basis of their differences. In practice, democratic states have granted a variety of group-differentiated rights, such as exemptions from generally applicable laws, special representation rights, language rights, or limited self-government rights, to different types of groups. This course will examine how different theories of citizenship address the challenges raised by different forms of pluralism. We will focus in particular on the following questions: Does justice require granting group-differentiated rights? Do group-differentiated rights conflict with liberal and democratic commitments to equality and justice for all citizens? What, if anything, can hold a multi-religious, multicultural society together? Why should the citizens of such a society want to hold together?', 'http://ocw.mit.edu/courses/political-science/17-042-citizenship-and-pluralism-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-042-citizenship-and-pluralism-fall-2003/17-042f03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1872, 'Political Economy I', 'Political Economy I explores the major social science paradigms for analyzing relations among state, economy, and society.', 'Political Economy I explores the major social science paradigms for analyzing relations among state, economy, and society. Through readings, lectures and discussion of original texts in political liberalism and individualism, neo-classical economics, Marxism, sociological and cultural theories, and neo-institutionalism, the seminar examines the fundamental assumptions on which our understanding of the social world and our research are based.', 'http://ocw.mit.edu/courses/political-science/17-100j-political-economy-i-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-100j-political-economy-i-fall-2010/17-100jf10.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1873, 'Economic Development, Policy Analysis, and Industrialization', 'This class analyzes the theoretical and historical reasons why governments in latecomer countries have intervened with a wide array of policies to foster industrial development at various turning points: the initiation of industrial activity; the diversification of the industrial base; the restructuring of major industrial institutions; and the entry into high-technology sectors.', 'This class analyzes the theoretical and historical reasons why governments in latecomer countries have intervened with a wide array of policies to foster industrial development at various turning points: the initiation of industrial activity; the diversification of the industrial base; the restructuring of major industrial institutions; and the entry into high-technology sectors.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-491j-economic-development-policy-analysis-and-industrialization-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-491j-economic-development-policy-analysis-and-industrialization-fall-2004/11-491jf04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1874, 'Sustainable Development: Theory and Policy', 'This course examines alternative conceptions and theoretical underpinnings of the notion of ''sustainable development.', 'This course examines alternative conceptions and theoretical underpinnings of the notion of ''sustainable development.'' It focuses on the sustainability problems of industrial countries (i.e., aging of populations, sustainable consumption, institutional adjustments, etc.); and of developing states and economies in transition (i.e., managing growth, sustainability of production patterns, pressures of population change, etc.). It also explores the sociology of knowledge around sustainability, the economic and technological dimensions and institutional imperatives along with implications for political constitution of economic performance.', 'http://ocw.mit.edu/courses/political-science/17-181-sustainable-development-theory-and-policy-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-181-sustainable-development-theory-and-policy-spring-2009/17-181s09.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1875, 'Economic Institutions and Growth Policy Analysis', 'This course is designed for students particularly concerned with the practical problems of operating in large formal organizations, either from an operational or a research perspective.', 'This course is designed for students particularly concerned with the practical problems of operating in large formal organizations, either from an operational or a research perspective. It will focus, as the title suggests, upon different forms of economic organizations and institutions in advanced and developing industrial societies and the theories (and theoretical perspectives) which might help us to understand them.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-486j-economic-institutions-and-growth-policy-analysis-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-486j-economic-institutions-and-growth-policy-analysis-fall-2005/11-486jf05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1876, 'Globalization', 'This seminar explores changes in the international economy and their effects on domestic politics, economy, and society.', 'This seminar explores changes in the international economy and their effects on domestic politics, economy, and society. Is globalization really a new phenomenon? Is it irreversible? What are effects on wages and inequality, on social safety nets, on production, and innovation? How does it affect relations between developed countries and developing countries? How globalization affects democracy? These are some of the key issues that will be examined.', 'http://ocw.mit.edu/courses/political-science/17-196-globalization-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-196-globalization-fall-2005/17-196f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1877, 'Graduate Seminar in American Politics II', 'This is the second in a sequence of two field seminars in American politics intended for graduate students in political science, in preparation for taking the general examination in American politics.', 'This is the second in a sequence of two field seminars in American politics intended for graduate students in political science, in preparation for taking the general examination in American politics. The material covered in this semester focuses on American political institutions. The readings covered here are not comprehensive, but it is sufficiently broad to give students an introduction to major empirical questions and theoretical approaches that guide the study of American political institutions these days.', 'http://ocw.mit.edu/courses/political-science/17-202-graduate-seminar-in-american-politics-ii-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-202-graduate-seminar-in-american-politics-ii-spring-2010/17-202s10.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1878, 'Congress and the American Political System II', 'This course analyzes the development of the United States Congress by focusing on the competing theoretical lenses through which legislatures have been studied.', 'This course analyzes the development of the United States Congress by focusing on the competing theoretical lenses through which legislatures have been studied. In particular, it compares sociological and economic models of legislative behavior, applying those models to floor decision-making, committee behavior, political parties, relations with other branches of the Federal government, and elections. Graduate students are expected to pursue the subject in greater depth through reading and individual research.', 'http://ocw.mit.edu/courses/political-science/17-261-congress-and-the-american-political-system-ii-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-261-congress-and-the-american-political-system-ii-fall-2005/17-261f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1879, 'Seminar on Politics and Conflict in the Middle East', 'This course focuses on evolution of contemporary politics and economics.', 'This course focuses on evolution of contemporary politics and economics. The subject is divided into four parts: Context: historical and strategic perspectives, theoretical issues, and sources and forms of conflict; Continuity: detailed analysis conflicts systems and their persistence, as well as regional competition and recent wars &#8211; focusing on specific countries and cases; Complexity: highlighting situation specific strategic gains and losses; and Convergence: focusing future configurations of conflict and cooperation. Throughout the course, special attention is given to sources and transformations of power, population dynamics and migration, resources and energy, as well as implications of technological change.', 'http://ocw.mit.edu/courses/political-science/17-405-seminar-on-politics-and-conflict-in-the-middle-east-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-405-seminar-on-politics-and-conflict-in-the-middle-east-fall-2003/17-405f03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1880, 'Chinese Foreign Policy: International Relations and Strategy', 'China''s rise as a great power raises important questions about how that power might be used in its relations with other states.', 'China''s rise as a great power raises important questions about how that power might be used in its relations with other states. Nowhere are such questions more salient than in the future trajectory of China''s conflict behavior, including its approach to deterrence, crisis management and the use of force. To explore these important questions in China''s international relations, this seminar examines the evolution of Chinese strategic thought, in primary sources as well as its reflection in the interactions among Chinese states and between China and other states.', 'http://ocw.mit.edu/courses/political-science/17-407-chinese-foreign-policy-international-relations-and-strategy-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-407-chinese-foreign-policy-international-relations-and-strategy-spring-2009/17-407s09.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1881, 'Chinese Foreign Policy', 'This lecture course provides students with a comprehensive introduction to the international relations of the People&rsquo;s Republic of China.', 'This lecture course provides students with a comprehensive introduction to the international relations of the People&rsquo;s Republic of China. China&rsquo;s foreign relations during the Cold War as well as contemporary diplomatic, security and economic issues will be examined to identify and explain China&rsquo;s foreign policy goals and their implementation since 1949. Throughout, this course will investigate the sources of conflict and cooperation in China&rsquo;s behavior, assessing competing explanations for key events and policies. Readings will be drawn from political science, history, and international relations theory.', 'http://ocw.mit.edu/courses/political-science/17-407-chinese-foreign-policy-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-407-chinese-foreign-policy-fall-2005/17-407f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1882, 'Advances in International Relations Theory', 'This course offers a critical analysis of contending theories of international relations.', 'This course offers a critical analysis of contending theories of international relations. Focus is on alternative theoretical assumptions, different analytical structures, and a common core of concepts and content. It also focuses on a comparative analysis of realism(s), liberalism(s), institutionalism(s), and new emergent theories. It also presents a discussion of connections between theories of international relations and major changes in international relations.', 'http://ocw.mit.edu/courses/political-science/17-420-advances-in-international-relations-theory-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-420-advances-in-international-relations-theory-spring-2003/17-420s03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1883, 'Field Seminar in International Political Economy', 'This field seminar in international political economy covers major theoretical, empirical, and policy perspectives.', 'This field seminar in international political economy covers major theoretical, empirical, and policy perspectives. The basic orientation is disciplinary and comparative (over time and across countries, regions, firms), spanning issues relevant to both industrial and developing states. Special attention is given to challenges and dilemmas shaped by the macro-level consequences of micro-level behavior, and by micro-level adjustments to macro-level influences.', 'http://ocw.mit.edu/courses/political-science/17-422-field-seminar-in-international-political-economy-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-422-field-seminar-in-international-political-economy-fall-2003/17-422f03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1884, 'International Political Economy of Advanced Industrial Societies', 'This graduate class is designed as a Ph.', 'This graduate class is designed as a Ph.D.-level overview of international political economy (IPE), with an emphasis on the advanced industrial countries. It also serves as preparation for the IPE portion of the International Relations general exam. An important goal of the course is to use economic theories to identify the welfare effects, distributional consequences, and security implication of foreign economic policy decisions, and to use the tools of political science to analyze how interest groups, voters, political parties, electoral institutions, ideas, and power politics interact to share policy outcomes.', 'http://ocw.mit.edu/courses/political-science/17-424-international-political-economy-of-advanced-industrial-societies-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-424-international-political-economy-of-advanced-industrial-societies-fall-2011/17-424f11.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1885, 'Causes of War: Theory and Method', 'This course explores the causes of modern war with a focus on preventable causes.', 'This course explores the causes of modern war with a focus on preventable causes. Course readings cover theoretical, historical, and methodological topics. Major theories of war are explored and assessed in the first few weeks of the class, asking at each stage ''are these good theories?'' and ''how could they be tested?'' Basic social scientific inference -- what are theories? What are good theories? How should theories be framed and tested? -- and case study methodology are also discussed. The second half of the course explores the history of the outbreak of some major wars. We use these cases as raw material for case studies, asking ''if these episodes were the subject of case studies, how should those studies be performed, and what could be learned from them?''', 'http://ocw.mit.edu/courses/political-science/17-432-causes-of-war-theory-and-method-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-432-causes-of-war-theory-and-method-fall-2003/17-432f03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1886, 'Territorial Conflict', 'This graduate seminar introduces an emerging research program within International Relations on territorial conflict.', 'This graduate seminar introduces an emerging research program within International Relations on territorial conflict. While scholars have recognized that territory has been one of the most frequent issues over which states go to war, territorial conflicts have only recently become the subject of systematic study. This course will examine why territorial conflicts arise in the first place, why some of these conflicts escalate to high levels of violence and why other territorial disputes reach settlement, thereby reducing the likelihood of war. Readings in the course draw upon political geography and history as well as qualitative and quantitative approaches to political science.', 'http://ocw.mit.edu/courses/political-science/17-436-territorial-conflict-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-436-territorial-conflict-fall-2004/17-436f04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1887, 'International Politics and Climate Change', 'This course examines the interconnections of international politics and climate change.', 'This course examines the interconnections of international politics and climate change. Beginning with an analysis of the strategic and environmental legacies of the 20th Century, it explores the politicization of the natural environment, the role of science in this process, and the gradual shifts in political concerns to incorporate ''nature''. Two general thrusts of climate-politics connections are pursued, namely those related to (a) conflict &ndash; focusing on threats to security due to environmental dislocations and (b) cooperation &ndash; focusing on the politics of international treaties that have contributed to emergent processes for global accord in response to evidence of climate change. The course concludes by addressing the question of: ''What Next?''', 'http://ocw.mit.edu/courses/political-science/17-441-international-politics-and-climate-change-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-441-international-politics-and-climate-change-fall-2007/17-441f07.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1888, 'Organization Theory and the Military', 'This course explores organizational concepts and research methods that explain the performance and development of military organizations in peace and war.', 'This course explores organizational concepts and research methods that explain the performance and development of military organizations in peace and war. Classic studies are reviewed. Approaches to current policy problems based on theoretical insights into military organizations and practices are also considered. The class stresses development of new theory.', 'http://ocw.mit.edu/courses/political-science/17-466-organization-theory-and-the-military-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-466-organization-theory-and-the-military-spring-2003/17-466s03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1889, 'Comparative Grand Strategy and Military Doctrine', 'This course will conduct a comparative study of the grand strategies of the great powers (Britain, France, Germany and Russia) competing for mastery of Europe from the late nineteenth to the mid-twentieth century.', 'This course will conduct a comparative study of the grand strategies of the great powers (Britain, France, Germany and Russia) competing for mastery of Europe from the late nineteenth to the mid-twentieth century. Grand strategy is the collection of political and military means and ends with which a state attempts to achieve security. We will examine strategic developments in the years preceding World Wars I and II, and how those developments played themselves out in these wars. The following questions will guide the inquiry: What is grand strategy and what are its critical aspects? What recurring factors have exerted the greatest influence on the strategies of the states selected for study? How may the quality of a grand strategy be judged? What consequences seem to follow from grand strategies of different types? A second theme of the course is methodological. We will pay close attention to how comparative historical case studies are conducted.', 'http://ocw.mit.edu/courses/political-science/17-484-comparative-grand-strategy-and-military-doctrine-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-484-comparative-grand-strategy-and-military-doctrine-fall-2004/17-484f04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1890, 'Japan and East Asian Security', 'This subject is designed for graduate students interested in international politics, national security and comparative political economy in East Asia.', 'This subject is designed for graduate students interested in international politics, national security and comparative political economy in East Asia. It examines the political, military, and economic challenges facing Japan, its neighbors, and the international system under conditions of great uncertainty. Topics range from the history of once ''new'' world orders to theories that inform our understanding of international affairs and foreign policy decision-making, as each is related to Japan. We focus on Japanese bilateral, regional, and global security policies from a range of theoretical perspectives. The semester will culminate in a weekend-long Asia-Pacific Crisis Simulation game in which invited U.S. and foreign experts will participate with the graduate students.', 'http://ocw.mit.edu/courses/political-science/17-486-japan-and-east-asian-security-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-486-japan-and-east-asian-security-spring-2008/17-486s08.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1891, 'Ethnic Politics II', 'This course is designed mainly for political science graduate students conducting or considering conducting research on identity politics.', 'This course is designed mainly for political science graduate students conducting or considering conducting research on identity politics. While 17.504 Ethnic Politics I is designed as a primarily theoretical course, Ethnic Politics II switches the focus to methods. It aims to familiarize the student with the current conventional approaches as well as major challenges to them. The course discusses definition and measurement issues as well as briefly addressing survey techniques and modeling.', 'http://ocw.mit.edu/courses/political-science/17-506-ethnic-politics-ii-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-506-ethnic-politics-ii-spring-2007/17-506s07.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1892, 'Politics and Religion', 'This graduate reading seminar explores the role of religious groups, institutions, and ideas in politics using social science theories.', 'This graduate reading seminar explores the role of religious groups, institutions, and ideas in politics using social science theories. It is open to advanced undergraduate students with permission of the instructor.', 'http://ocw.mit.edu/courses/political-science/17-522-politics-and-religion-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-522-politics-and-religion-fall-2006/17-522f06.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1893, 'Nationalism', 'This course provides a broad overview of the theories of and approaches to the study of nationalist thought and practice.', 'This course provides a broad overview of the theories of and approaches to the study of nationalist thought and practice. It also explores the related phenomena termed nationalism: national consciousness and identity, nations, nation-states, and nationalist ideologies and nationalist movements. The course analyzes nationalism''s emergence and endurance as a factor in modern politics and society. Topics include: nationalism and state-building, nationalism and economic modernization, nationalism and democratization, and nationalism and religious conflict.', 'http://ocw.mit.edu/courses/political-science/17-524-nationalism-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-524-nationalism-fall-2004/17-524f04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1894, 'Comparative Politics and China', 'This graduate seminar has two main goals: to explore the main theoretical and methodological approaches to the study of contemporary Chinese politics; and to relate those approches to broader trends in the field of comparative politics.', 'This graduate seminar has two main goals: to explore the main theoretical and methodological approaches to the study of contemporary Chinese politics; and to relate those approches to broader trends in the field of comparative politics. What has the study of China contributed to the field of comparative politics, and vice versa? What are the most effective ways to integrate area studies, broader comparative approaches, and theory? Seminar presumes a basic understanding of the history and politics of contemporary China.', 'http://ocw.mit.edu/courses/political-science/17-544-comparative-politics-and-china-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-544-comparative-politics-and-china-fall-2002/17-544f02.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1895, 'Property Rights in Transition', 'This course examines the theories and policy debates over who can own real property, how to communicate and enforce property rights, and the range of liberties that they confer.', 'This course examines the theories and policy debates over who can own real property, how to communicate and enforce property rights, and the range of liberties that they confer. It explores alternative economic, political, and sociological perspectives of property rights and their policy and planning implications.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-467j-property-rights-in-transition-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-467j-property-rights-in-transition-spring-2005/11-467js05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1896, 'Political Economy of Chinese Reform', 'This course focuses on China''s transition from plan to market.', 'This course focuses on China''s transition from plan to market. What has the trajectory of institutional change in China been, and how has growth been achieved? Is that growth sustainable? Subject examines specific aspects of reform (enterprise, fiscal, financial, social welfare), and the systemic consequences of interaction between various reform measures. Additional topics include the interaction between political and economic change, the transformation of state-society relations, and the generalizability of China''s reform experience. Graduate students are expected to explore the subject in greater depth.', 'http://ocw.mit.edu/courses/political-science/17-552-political-economy-of-chinese-reform-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-552-political-economy-of-chinese-reform-spring-2003/17-552s03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1897, 'Political Economy of Development', 'This course examines theoretical and empirical approaches to understanding the process of late development.', 'This course examines theoretical and empirical approaches to understanding the process of late development. Topics include the role of the state in alleviating or exacerbating poverty, the politics of industrial policy and planning and the relationship between institutional change and growth. How over the past century have some of the world''s poorest nations achieved wealth? How have others remained mired in poverty? What are the social consequences for alternative strategies of development?', 'http://ocw.mit.edu/courses/political-science/17-556-political-economy-of-development-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-556-political-economy-of-development-spring-2003/17-556s03.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1898, 'Comparative Security and Sustainability', 'This course focuses on the complexities associated with security and sustainability of states in international relations.', 'This course focuses on the complexities associated with security and sustainability of states in international relations. Covering aspects of theory, methods and empirical analysis, the course is in three parts, and each consists of seminar sessions focusing on specific topics.', 'http://ocw.mit.edu/courses/political-science/17-559-comparative-security-and-sustainability-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-559-comparative-security-and-sustainability-fall-2004/17-559f04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1899, 'Civil War', 'This course surveys the social science literature on civil war.', 'This course surveys the social science literature on civil war. Students will study the origins of civil war, discuss variables that affect the duration of civil war, and examine the termination of conflict. This course is highly interdisciplinary and covers a wide variety of cases.', 'http://ocw.mit.edu/courses/political-science/17-582-civil-war-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-582-civil-war-spring-2010/17-582s10.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1900, 'Warlords, Terrorists, and Militias: Theorizing on Violent Non-State Actors', 'This course is a general overview of the recent political science literature on violent non-state actors.', 'This course is a general overview of the recent political science literature on violent non-state actors. Its aim is to examine why non-state actors (such as warlords, terrorists, militias, etc.) resort to violence, what means and tactics they use, and what can be done to counter that violence. In that regard, the class will cover works pertaining to the production side of non-state violence (i.e. the objectives and organization of insurgents/terrorists/militias/warlords, their mobilization strategies and support base, how they coerce opponents, etc.); as well as the response that violence elicits from governments or other actors (i.e. counter-insurgency or counter-terrorism strategies, among others). Apart from introducing the basic variables and theoretical and empirical findings in the literature, this course will also grapple with questions of definition, operationalization of variables, and general methodology relevant to conducting research in this area of violent conflict. Though thematically-driven, this course will also reference cases from the contemporary battlefields of insurgency and terrorism (be it Afghanistan, Iraq, Pakistan, the West Bank and Gaza, Colombia, etc.) as they relate to the pertinent themes.', 'http://ocw.mit.edu/courses/political-science/17-586-warlords-terrorists-and-militias-theorizing-on-violent-non-state-actors-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-586-warlords-terrorists-and-militias-theorizing-on-violent-non-state-actors-spring-2009/17-586s09.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1901, 'Collective Choice I', 'This is an applied theory course covering topics in the political economy of democratic countries.', 'This is an applied theory course covering topics in the political economy of democratic countries. This course examines political institutions from a rational choice perspective. The now burgeoning rational choice literature on legislatures, bureaucracies, courts, and elections constitutes the chief focus. Some focus will be placed on institutions from a comparative and/or international perspective.', 'http://ocw.mit.edu/courses/political-science/17-812j-collective-choice-i-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-812j-collective-choice-i-fall-2008/17-812jf08.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1902, 'Quantitative Research in Political Science and Public Policy', 'This course provides students with a rigorous introduction to Statistics for Political Science.', 'This course provides students with a rigorous introduction to Statistics for Political Science. Topics include basic mathematical tools used in social science modeling and statistics, probability theory, theory of estimation and inference, and statistical methods, especially differences of means and regression. The course is often taken by students outside of political science, especially those in business, urban studies, and various fields of public policy, such as public health. Examples draw heavily from political science, but some problems come from other areas, such as labor economics.', 'http://ocw.mit.edu/courses/political-science/17-872-quantitative-research-in-political-science-and-public-policy-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-872-quantitative-research-in-political-science-and-public-policy-spring-2004/17-872s04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1903, 'Qualitative Research: Design and Methods', 'This course is intended for graduate students planning to conduct qualitative research in a variety of different settings.', 'This course is intended for graduate students planning to conduct qualitative research in a variety of different settings. Its topics include: Case studies, interviews, documentary evidence, participant observation, and survey research. The primary goal of this course is to assist students in preparing their (Masters and PhD) dissertation proposals.', 'http://ocw.mit.edu/courses/political-science/17-878-qualitative-research-design-and-methods-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-878-qualitative-research-design-and-methods-fall-2007/17-878f07.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1904, 'Qualitative Research: Design and Methods', 'This seminar explores the development and application of qualitative research designs and methods in political analysis.', 'This seminar explores the development and application of qualitative research designs and methods in political analysis. It considers a broad array of approaches, from exploratory narratives to focused-comparison case studies, for investigating plausible alternative hypotheses. The focus is on analysis, not data collection.', 'http://ocw.mit.edu/courses/political-science/17-878-qualitative-research-design-and-methods-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-878-qualitative-research-design-and-methods-spring-2005/17-878s05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1905, 'Understanding Military Operations', 'A proper understanding of modern military operations requires a prior understanding of both the material side of war, and the human or organizational side of war.', 'A proper understanding of modern military operations requires a prior understanding of both the material side of war, and the human or organizational side of war. This seminar will break apart selected past, current, and future sea, air, space, and land battlefields into their constituent parts and look at the interaction in each of those warfare areas between existing military doctrine and weapons, sensors, communications, and information processing technologies. It will specifically seek to explore how technological development, whether innovative or stagnant, is influenced in each warfare area by military doctrine.', 'http://ocw.mit.edu/courses/political-science/17-950-understanding-military-operations-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-950-understanding-military-operations-spring-2010/17-950s10.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1906, 'Intelligence: Practice, Problems and Prospects', 'This course will explore the organization and functions of the U.', 'This course will explore the organization and functions of the U.S. Intelligence Community, its interaction with national security policymakers, key issues about its workings, and the challenges it faces in defining its future role. The events of 9/11 and the invasion of Iraq have focused new attention on national intelligence, including the most significant reorganization of the community since the National Security Act of 1947. The course will highlight some of the major debates about the role, practices, and problems of national intelligence.', 'http://ocw.mit.edu/courses/political-science/17-951-intelligence-practice-problems-and-prospects-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-951-intelligence-practice-problems-and-prospects-spring-2005/17-951s05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1907, 'Special Graduate Topic in Political Science: Political Behavior', 'This graduate seminar provides an examination of mass and elite political behavior in the United States, with an emphasis on political participation, political inequality, elections, voting behavior, and political organizations.', 'This graduate seminar provides an examination of mass and elite political behavior in the United States, with an emphasis on political participation, political inequality, elections, voting behavior, and political organizations.', 'http://ocw.mit.edu/courses/political-science/17-951-special-graduate-topic-in-political-science-political-behavior-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-951-special-graduate-topic-in-political-science-political-behavior-fall-2005/17-951f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1908, 'Special Graduate Topic in Political Science: Public Opinion', 'This course provides an introduction to the vast literature devoted to public opinion.', 'This course provides an introduction to the vast literature devoted to public opinion. In the next 12 weeks, we will survey the major theoretical approaches and empirical research&nbsp;in the field of political behavior (though we will only tangentially discuss political&nbsp; participation and voting). For the most part we will focus on American public opinion, though some of the work we will read is comparative in nature.', 'http://ocw.mit.edu/courses/political-science/17-951-special-graduate-topic-in-political-science-public-opinion-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-951-special-graduate-topic-in-political-science-public-opinion-spring-2004/17-951s04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1909, 'U.S. Budgets for National Security', 'This course is for students who want to know how the dollars we spend on national security relate to military forces, systems, and policy choices, and who wish to develop a personal tool kit for framing and assessing defense policy alternatives.', 'This course is for students who want to know how the dollars we spend on national security relate to military forces, systems, and policy choices, and who wish to develop a personal tool kit for framing and assessing defense policy alternatives.', 'http://ocw.mit.edu/courses/political-science/17-953-u-s-budgets-for-national-security-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-953-u-s-budgets-for-national-security-fall-2010/17-953f10.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1910, 'Civil Society, Social Capital, and the State in Comparative Perspective', 'In recent years both scholars and policymakers have expressed a remarkable amount of interest in the concepts of social capital and civil society.', 'In recent years both scholars and policymakers have expressed a remarkable amount of interest in the concepts of social capital and civil society. A growing body of research suggests that the social networks, community norms, and associational activities signified by these concepts can have important effects on social welfare, political stability, economic development, and governmental performance. This discussion based course examines the roles played by these networks, norms, and organizations in outcomes ranging from local public goods provision and the performance of democracies to ethnic conflict and funding for terrorism.', 'http://ocw.mit.edu/courses/political-science/17-955-civil-society-social-capital-and-the-state-in-comparative-perspective-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-955-civil-society-social-capital-and-the-state-in-comparative-perspective-fall-2004/17-955f04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1911, 'Organizational Analysis', 'This reading course seeks to provide students with frameworks for understanding organizational behavior and research tools for studying them.', 'This reading course seeks to provide students with frameworks for understanding organizational behavior and research tools for studying them. It offers an overview of major theories and approaches, and an opportunity to discuss major and classic works on military and non-military organizations. For advanced graduate students, preferably those selecting a dissertation topic.', 'http://ocw.mit.edu/courses/political-science/17-959-organizational-analysis-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-959-organizational-analysis-fall-2005/17-959f05.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1912, 'Foundations of Political Science', 'This subject, required of all first-year PhD students in political science, introduces fundamental ideas, theories, and methods in contemporary political science through the study of a small number of major books and articles that are intrinsically good and have been influential in the field.', 'This subject, required of all first-year PhD students in political science, introduces fundamental ideas, theories, and methods in contemporary political science through the study of a small number of major books and articles that are intrinsically good and have been influential in the field. The first semester focuses principally on issues of political theory and international relations, while the second&nbsp;focuses principally on American and comparative politics. Readings in the fall semester from Rawls, A Theory of Justice; Hayek, The Constitution of Liberty; Arrow Social Choice and Individual Values; Olson, The Logic of Collective Action; Waltz, Theory of International Relations; Bull, The Anarchical Society; Foucault, Discipline and Punish; Elster, Cement of Society; Keohane, After Hegemony, Allison and Zelikow, The Essence of Decision, and Doyle, ''Kant, Liberal Legacies, and Foreign Affairs.''', 'http://ocw.mit.edu/courses/political-science/17-960-foundations-of-political-science-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-960-foundations-of-political-science-fall-2004/17-960f04.jpg', 'Political Science', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1913, 'Technology in American History', 'This course will consider the ways in which technology, broadly defined, has contributed to the building of American society from colonial times to the present.', 'This course will consider the ways in which technology, broadly defined, has contributed to the building of American society from colonial times to the present. This course has three primary goals: to train students to ask critical questions of both technology and the broader American culture of which it is a part; to provide an historical perspective with which to frame and address such questions; and to encourage students to be neither blind critics of new technologies, nor blind advocates for technologies in general, but thoughtful and educated participants in the democratic process.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-001-technology-in-american-history-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-001-technology-in-american-history-spring-2006/sts-001s06.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1914, 'Toward the Scientific Revolution', 'This subject traces the evolution of ideas about nature, and how best to study and explain natural phenomena, beginning in ancient times and continuing through the Middle Ages and the Renaissance.', 'This subject traces the evolution of ideas about nature, and how best to study and explain natural phenomena, beginning in ancient times and continuing through the Middle Ages and the Renaissance. A central theme of the subject is the intertwining of conceptual and institutional relations within diverse areas of inquiry: cosmology, natural history, physics, mathematics, and medicine.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-002-toward-the-scientific-revolution-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-002-toward-the-scientific-revolution-fall-2003/sts-002f03.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1915, 'The Rise of Modern Science', 'This subject introduces the history of science from antiquity to the present.', 'This subject introduces the history of science from antiquity to the present. Students consider the impact of philosophy, art, magic, social structure, and folk knowledge on the development of what has come to be called &quot;science&quot; in the Western tradition, including those fields today designated as physics, biology, chemistry, medicine, astronomy and the mind sciences. Topics include concepts of matter, nature, motion, body, heavens, and mind as these have been shaped over the course of history. Students read original works by Aristotle, Vesalius, Newton, Lavoisier, Darwin, Freud, and Einstein, among others.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-003-the-rise-of-modern-science-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-003-the-rise-of-modern-science-fall-2010/sts-003f10.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1916, 'Bioethics', 'This course does not seek to provide answers to ethical questions.', 'This course does not seek to provide answers to ethical questions. Instead, the course hopes to teach students two things. First, how do you recognize ethical or moral problems in science and medicine? When something does not feel right (whether cloning, or failing to clone) &mdash; what exactly is the nature of the discomfort? What kind of tensions and conflicts exist within biomedicine? Second, how can you think productively about ethical and moral problems? What processes create them? Why do people disagree about them? How can an understanding of philosophy or history help resolve them? By the end of the course students will hopefully have sophisticated and nuanced ideas about problems in bioethics, even if they do not have comfortable answers.', 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-06j-bioethics-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/linguistics-and-philosophy/24-06j-bioethics-spring-2009/24-06js09.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1917, 'Technology in History', 'Today many people assume that technological change is the major factor in historical change and that it tends to lead to historical progress.', 'Today many people assume that technological change is the major factor in historical change and that it tends to lead to historical progress. This class turns these assumptions into a questionâ€”what is the role of technology in history?â€”by focusing on four key historical transitions: the human revolution (the emergence of humans as a history-making species), the Neolithic Revolution (the emergence of agriculture-based civilizations); the great leap in productivity (also known as the industrial revolution), and the great acceleration that has come with the rise of human empire on the planet. These topics are studied through a mix of textbook reading (David Christian''s ''Maps of Time''), supplementary readings (ranging from Auel, ''The Clan of the Cave Bear'' to Hersey, ''Hiroshima''), illustrated lectures, class discussions, guest lectures/discussions, short ''problem paper'' assignments, and a final project defined by the student. Because MIT is celebrating its 150th anniversary in 2011, this version of the class will also focus on connections between MIT as an institution and technology in the history of the last 150 years.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-007-technology-in-history-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-007-technology-in-history-fall-2010/sts-007f10.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1918, 'Neuroscience and Society', 'This course explores the social relevance of neuroscience, considering how emerging areas of brain research at once reflect and reshape social attitudes and agendas.', 'This course explores the social relevance of neuroscience, considering how emerging areas of brain research at once reflect and reshape social attitudes and agendas. Topics include brain imaging and popular media; neuroscience of empathy, trust, and moral reasoning; new fields of neuroeconomics and neuromarketing; ethical implications of neurotechnologies such as cognitive enhancement pharmaceuticals; neuroscience in the courtroom; and neuroscientific recasting of social problems such as addiction and violence. Guest lectures by neuroscientists, class discussion, and weekly readings in neuroscience, popular media, and science studies.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-010-neuroscience-and-society-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-010-neuroscience-and-society-spring-2010/sts-010s10.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1919, 'Principles and Practice of Science Communication', 'This course helps in developing skills as science communicators through projects and analysis of theoretical principles.', 'This course helps in developing skills as science communicators through projects and analysis of theoretical principles. Case studies explore the emergence of popular science communication over the past two centuries and consider the relationships among authors, audiences and media. Project topics are identified early in the term and students work with MIT Museum staff. Projects may include physical exhibits, practical demonstrations, or scripts for public programs.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-014-principles-and-practice-of-science-communication-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-014-principles-and-practice-of-science-communication-spring-2006/sts-014s06.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1920, 'Making the Modern World: The Industrial Revolution in Global Perspective', 'This class is a global survey of the great transformation in history known as the &quot;Industrial Revolution.', 'This class is a global survey of the great transformation in history known as the &quot;Industrial Revolution.&quot; Topics include origins of mechanized production, the factory system, steam propulsion, electrification, mass communications, mass production and automation. Emphasis on the transfer of technology and its many adaptations around the world. Countries treated include Great Britain, France, Germany, the US, Sweden, Russia, Japan, China, and India. Includes brief reflection papers and a final paper.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-025j-making-the-modern-world-the-industrial-revolution-in-global-perspective-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-025j-making-the-modern-world-the-industrial-revolution-in-global-perspective-fall-2009/sts-025jf09.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1921, 'The Civil War and Reconstruction', 'Although attention will be devoted to the causes and long-term consequences of the Civil War, this class will focus primarily on the war years (1861-1865) with special emphasis on the military and technological aspects of the conflict.', 'Although attention will be devoted to the causes and long-term consequences of the Civil War, this class will focus primarily on the war years (1861-1865) with special emphasis on the military and technological aspects of the conflict. Four questions, long debated by historians, will receive close scrutiny: What caused the war? Why did the North win the war? Could the South have won? To what extent is the Civil War America''s &quot;defining moment&quot;?', 'http://ocw.mit.edu/courses/history/21h-116j-the-civil-war-and-reconstruction-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-116j-the-civil-war-and-reconstruction-fall-2005/21h-116jf05.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1922, 'The History of Computing', 'This course focuses on one particular aspect of the history of computing: the use of the computer as a scientific instrument.', 'This course focuses on one particular aspect of the history of computing: the use of the computer as a scientific instrument. The electronic digital computer was invented to do science, and its applications range from physics to mathematics to biology to the humanities. What has been the impact of computing on the practice of science? Is the computer different from other scientific instruments? Is computer simulation a valid form of scientific experiment? Can computer models be viewed as surrogate theories? How does the computer change the way scientists approach the notions of proof, expertise, and discovery? No comprehensive history of scientific computing has yet been written. This seminar examines scientific articles, participants&#8217; memoirs, and works by historians, sociologists, and anthropologists of science to provide multiple perspectives on the use of computers in diverse fields of physical, biological, and social sciences and the humanities. We explore how the computer transformed scientific practice, and how the culture of computing was influenced, in turn, by scientific applications.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-035-the-history-of-computing-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-035-the-history-of-computing-spring-2004/sts-035s04.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1923, 'Technology and Nature in American History', 'This course considers how the visual and material world of ''nature'' has been reshaped by industrial practices, ideologies, and institutions, particularly in nineteenth- and twentieth-century America.', 'This course considers how the visual and material world of ''nature'' has been reshaped by industrial practices, ideologies, and institutions, particularly in nineteenth- and twentieth-century America. Topics include land-use patterns; the changing shape of cities and farms; the redesign of water systems; the construction of roads, dams, bridges, irrigation systems; the creation of national parks; ideas about wilderness; and the role of nature in an industrial world. From small farms to suburbia, Walden Pond to Yosemite, we will ask how technological and natural forces have interacted, and whether there is a place for nature in a technological world. Acknowledgement This class is based on one originally designed and taught by Prof. Deborah Fitzgerald. Her Fall 2004 version can be viewed by following the link under Archived Courses on the right side of this page.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-036-technology-and-nature-in-american-history-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-036-technology-and-nature-in-american-history-spring-2008/sts-036s08.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1924, 'Energy and Environment in American History: 1705-2005', 'A survey of how America has become the world''s largest consumer of energy.', 'A survey of how America has become the world''s largest consumer of energy. Explores American history from the perspective of energy and its relationship to politics, diplomacy, the economy, science and technology, labor, culture, and the environment. Topics include muscle and water power in early America, coal and the Industrial Revolution, electrification, energy consumption in the home, oil and U.S. foreign policy, automobiles and suburbanization, nuclear power, OPEC and the 70''s energy crisis, global warming, and possible paths for the future.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-038-energy-and-environment-in-american-history-1705-2005-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-038-energy-and-environment-in-american-history-1705-2005-fall-2006/sts-038f06.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1925, 'Technology and Gender in American History', 'This course centers on the changing relationships between men, women, and technology in American history.', 'This course centers on the changing relationships between men, women, and technology in American history. Topics include theories of gender, technologies of production and consumption, the gendering of public and private space, men''s and women''s roles in science and technology, the effects of industrialization on sexual divisions of labor, gender and identity at home and at work.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-049j-technology-and-gender-in-american-history-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-049j-technology-and-gender-in-american-history-spring-2004/sts-049js04.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1926, 'The History of MIT', 'This course examines the history of MIT through the lens of the broader history of science and technology, and vice versa.', 'This course examines the history of MIT through the lens of the broader history of science and technology, and vice versa. The course covers the founding of MIT in 1861 and goes through the present, including such topics as William Barton Rogers, educational philosophy, biographies of MIT students and professors, intellectual and organizational development, the role of science, changing laboratories and practices, and MIT''s relationship with Boston, the federal government, and industry. Assignments include short papers, presentations, and final paper. A number of classes are concurrent with the MIT150 Symposia.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-050-the-history-of-mit-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-050-the-history-of-mit-spring-2011/sts-050s11.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1927, 'The Anthropology of Biology', 'If the twentieth century was the century of physics, the twenty-first promises to be the century of biology.', 'If the twentieth century was the century of physics, the twenty-first promises to be the century of biology. This subject examines the cultural, political, and economic dimensions of biology in the age of genomics, biotechnological enterprise, biodiversity conservation, pharmaceutical bioprospecting, and synthetic biology. Although we examine such social concerns as bioterrorism, genetic modification, and cloning, this is not a class in bioethics, but rather an anthropological inquiry into how the substances and explanations of biology &mdash; increasingly cellular, molecular, genetic, and informatic &mdash; are changing, and with them broader ideas about the relationship between ''nature'' and ''culture.'' Looking at such cultural artifacts as cell lines, biodiversity databases, and artificial life models, and using primary sources in biology, social studies of the life sciences, and literary and cinematic materials, we rephrase Erwin Schr&ouml;dinger''s famous 1944 question, ''What Is Life?'' to ask, in the early 2000s, ''What Is Life Becoming?''', 'http://ocw.mit.edu/courses/anthropology/21a-355j-the-anthropology-of-biology-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-355j-the-anthropology-of-biology-spring-2009/21a-335js09.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1928, 'The Anthropology of Sound', 'This class examines the ways humans experience the realm of sound and how perceptions and technologies of sound emerge from cultural, economic, and historical worlds.', 'This class examines the ways humans experience the realm of sound and how perceptions and technologies of sound emerge from cultural, economic, and historical worlds. In addition to learning about how environmental, linguistic, and musical sounds are construed cross-culturally, students learn about the rise of telephony, architectural acoustics, and sound recording, as well as about the globalized travel of these technologies. Questions of ownership, property, authorship, and copyright in the age of digital file sharing are also addressed. A major concern will be with how the sound/noise boundary has been imagined, created, and modeled across diverse sociocultural and scientific contexts. Auditory examples &mdash; sound art, environmental recordings, music &mdash; will be provided and invited throughout the term.', 'http://ocw.mit.edu/courses/anthropology/21a-360j-the-anthropology-of-sound-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-360j-the-anthropology-of-sound-spring-2008/21a-360js08.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1929, 'Scientific Visualization across Disciplines: A Critical Introduction', 'This subject exposes students to a variety of visualization techniques so that they learn to understand the work involved in producing them and to critically assess the power and limits of each.', 'This subject exposes students to a variety of visualization techniques so that they learn to understand the work involved in producing them and to critically assess the power and limits of each. Students concentrate on areas where visualizations are crucial for meaning making and data production. Drawing on scholarship in science and technology studies on visualization, critical art theory, and core discussions in science and engineering, students work through a series of case studies in order to become better readers and producers of visualizations.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-067-scientific-visualization-across-disciplines-a-critical-introduction-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-067-scientific-visualization-across-disciplines-a-critical-introduction-spring-2005/sts-067s05.gif', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1930, 'Technology in a Dangerous World', 'Aim is to analyze important current events for what they reveal about the nature and working of our technological world.', 'Aim is to analyze important current events for what they reveal about the nature and working of our technological world. Starting point is connection between technology and terrorism. Subject also explores how a human-built world can foster insecurity and danger, and how human beings respond. Many invited guests help develop a strong interdisciplinary approach (science, engineering, social science, humanities). Topics include technological risk and remediation, sociotechnical systems, imagination of disaster, technology and identity, technology and religion, technology and education, and technology and trust. Written and oral assignments and a final project required. Service-learning proposals and web-based presentations, in addition to written work, may be considered for the final project by the instructor.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-069-technology-in-a-dangerous-world-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-069-technology-in-a-dangerous-world-fall-2002/sts-069f02.gif', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1931, 'Technology and Culture', 'This course examines relationships among technology, culture, and politics in a variety of social and historical settings ranging from 19th century factories to 21st century techno dance floors, from colonial Melanesia to capitalist Massachusetts.', 'This course examines relationships among technology, culture, and politics in a variety of social and historical settings ranging from 19th century factories to 21st century techno dance floors, from colonial Melanesia to capitalist Massachusetts. We will be interested in whether technology has produced a better world, and for whom.', 'http://ocw.mit.edu/courses/anthropology/21a-340j-technology-and-culture-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-340j-technology-and-culture-fall-2006/21a-340jf06.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1932, 'Ethics and the Law on the Electronic Frontier', 'This course considers the interaction between law, policy, and technology as they relate to the evolving controversies over control of the Internet.', 'This course considers the interaction between law, policy, and technology as they relate to the evolving controversies over control of the Internet. In addition, there will be an in-depth treatment of privacy and the notion of ''transparency'' -- regulations and technologies that govern the use of information, as well as access to information. Topics explored will include: Legal Background for Regulation of the Internet Fourth Amendment Law and Electronic Surveillance Profiling, Data Mining, and the U.S. PATRIOT Act Technologies for Anonymity and Transparency The Policy-Aware Web', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-805-ethics-and-the-law-on-the-electronic-frontier-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-805-ethics-and-the-law-on-the-electronic-frontier-fall-2005/6-805f05.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1933, 'Cultures of Computing', 'This course examines computers anthropologically, as artifacts revealing the social orders and cultural practices that create them.', 'This course examines computers anthropologically, as artifacts revealing the social orders and cultural practices that create them. Students read classic texts in computer science along with cultural analyses of computing history and contemporary configurations. It explores the history of automata, automation and capitalist manufacturing; cybernetics and WWII operations research; artificial intelligence and gendered subjectivity; robots, cyborgs, and artificial life; creation and commoditization of the personal computer; the growth of the Internet as a military, academic, and commercial project; hackers and gamers; technobodies and virtual sociality. Emphasis is placed on how ideas about gender and other social differences shape labor practices, models of cognition, hacking culture, and social media.', 'http://ocw.mit.edu/courses/anthropology/21a-350j-cultures-of-computing-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-350j-cultures-of-computing-fall-2011/21a-350jf11.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1934, 'Godzilla and the Bullet Train: Technology and Culture in Modern Japan', 'This course explores how and why Japan, a late-comer to modernization, emerged as an industrial power and the world''s second-richest nation, notwithstanding its recent difficulties.', 'This course explores how and why Japan, a late-comer to modernization, emerged as an industrial power and the world''s second-richest nation, notwithstanding its recent difficulties. We are particularly concerned with the historical development of technology in Japan especially after 1945, giving particular attention to the interplays between business, ideology, technology, and culture. We will discuss key historical phenomena that symbolize modern Japan as a technological power in the world; specific examples to be discussed in class include kamikaze aircraft, the Shinkansen high-speed bullet train, Godzilla, and anime.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-s28-godzilla-and-the-bullet-train-technology-and-culture-in-modern-japan-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-s28-godzilla-and-the-bullet-train-technology-and-culture-in-modern-japan-fall-2005/sts-s28f05.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1935, 'Social Theory and Analysis', 'This course covers major theorists and theoretical schools since the late 19th century.', 'This course covers major theorists and theoretical schools since the late 19th century. Marx, Weber, Durkheim, Bourdieu, Levi-Strauss, Geertz, Foucault, Gramsci, and others.', 'http://ocw.mit.edu/courses/anthropology/21a-750j-social-theory-and-analysis-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-750j-social-theory-and-analysis-fall-2011/21a-750f11.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1936, 'History of Science', 'This seminar explores recent historiographical approaches within the history of science.', 'This seminar explores recent historiographical approaches within the history of science. Students will read a wide variety of studies covering topics from the seventeenth through the twentieth centuries, from the physical sciences to natural history and medicine. Emphasis will be placed on: deciphering different theoretical approaches; the pros and cons of different research questions, subjects, and sources of evidence; and what makes for good and interesting history of science.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-310-history-of-science-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-310-history-of-science-fall-2005/sts-310f05.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1937, 'History and Anthropology of Medicine and Biology', 'This course explores recent historical and anthropological approaches to the study of life, in both medicine and biology.', 'This course explores recent historical and anthropological approaches to the study of life, in both medicine and biology. After grounding our conversation in accounts of natural history and medicine that predate the rise of biology as a discipline, we explore modes of theorizing historical and contemporary bioscience. Drawing on the work of historian William Coleman, we examine the forms, functions, and transformations of biological and medical objects of study. Along the way we treat the history of heredity, molecular biology, race, medicine in the colonies and the metropole, and bioeconomic exchange. We read anthropological literature on old and new forms of biopower, at scales from the molecular to the organismic to the global. The course includes readings from the HASTS Common Exam List. The aim of this seminar is to train students to be participants in scholarly debates in the humanities, social sciences, and natural sciences about the nature of life, the body, and biomedicine.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-330j-history-and-anthropology-of-medicine-and-biology-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-330j-history-and-anthropology-of-medicine-and-biology-spring-2009/sts-330js09.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1938, 'Introduction to the History of Technology', 'This course is an introduction to the consideration of technology as the outcome of particular technical, historical, cultural, and political efforts, especially in the United States during the 19th and 20th centuries.', 'This course is an introduction to the consideration of technology as the outcome of particular technical, historical, cultural, and political efforts, especially in the United States during the 19th and 20th centuries. Topics include industrialization of production and consumption, development of engineering professions, the emergence of management and its role in shaping technological forms, the technological construction of gender roles, and the relationship between humans and machines.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-340j-introduction-to-the-history-of-technology-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-340j-introduction-to-the-history-of-technology-fall-2006/sts-340jf06.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1939, 'Social Study of Science and Technology', 'This course surveys canonical and recent theories and methods in science studies.', 'This course surveys canonical and recent theories and methods in science studies. We will organize our discussions around the concept of &quot;reproduction,&quot; referring variously to: Scientific reproduction (how results are replicated in lab, field, disciplinary contexts) Social reproduction (how social knowledge and relations are regenerated over time) Biological reproduction (how organic substance is managed in the genetic age) Electronic reproduction (how information is reassembled in techniques of transcription, simulation, computation). Examining intersections and disruptions of these genres of reproduction, we seek to map relations among our social, biological, and electronic lives.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-350-social-study-of-science-and-technology-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-350-social-study-of-science-and-technology-spring-2004/sts-350s04.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1940, 'Readings in American History Since 1877', 'This seminar aims to develop a teaching knowledge of the field through extensive reading and discussion of major works.', 'This seminar aims to develop a teaching knowledge of the field through extensive reading and discussion of major works. The reading covers a broad range of topics - political, economic, social, and cultural - and represents a variety of historical methods.', 'http://ocw.mit.edu/courses/history/21h-952j-readings-in-american-history-since-1877-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-952j-readings-in-american-history-since-1877-fall-2003/21h-952jf03.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1941, 'Nature, Environment, and Empire', 'This class examines the relationship between the study of natural history, both domestic and exotic, by Europeans and Americans, and exploration and exploitation of the natural world, focusing on the eighteenth and nineteenth centuries.', 'This class examines the relationship between the study of natural history, both domestic and exotic, by Europeans and Americans, and exploration and exploitation of the natural world, focusing on the eighteenth and nineteenth centuries.', 'http://ocw.mit.edu/courses/history/21h-968j-nature-environment-and-empire-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-968j-nature-environment-and-empire-spring-2010/21h-968js10.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1942, 'The Structure of Engineering Revolutions', '6.', '6.933J / STS.420J&#160;provides an integrated approach to engineering practice in the real world. Students of 6.933J / STS.420J&#160;research the life cycle of a major engineering project, new technology, or startup company from multiple perspectives: technical, economic, political, and cultural.&#160;Research involves interviewing inventors, reading laboratory notebooks, evaluating patents, and looking over the shoulders of engineers as they developed today''s technologies. This subject is for students who recognize that technical proficiency alone is only part of the formula for success in technology.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-933j-the-structure-of-engineering-revolutions-fall-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-933j-the-structure-of-engineering-revolutions-fall-2001/6-933jf01.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1943, 'Food and Power in the Twentieth Century', 'In this class, food serves as both the subject and the object of historical analysis.', 'In this class, food serves as both the subject and the object of historical analysis. As a subject, food has been transformed over the last 100 years, largely as a result of ever more elaborate scientific and technological innovations. From a need to preserve surplus foods for leaner times grew an elaborate array of techniques &ndash; drying, freezing, canning, salting, etc &ndash; that changed not only what people ate, but how far they could/had to travel, the space in which they lived, their relations with neighbors and relatives, and most of all, their place in the economic order of things. The role of capitalism in supporting and extending food preservation and development was fundamental. As an object, food offers us a way into cultural, political, economic, and techno-scientific history. Long ignored by historians of science and technology, food offers a rich source for exploring, e.g., the creation and maintenance of mass-production techniques, industrial farming initiatives, the politics of consumption, vertical integration of business firms, globalization, changing race and gender identities, labor movements, and so forth. How is food different in these contexts, from other sorts of industrial goods? What does the trip from farm to table tell us about American culture and history?', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-429-food-and-power-in-the-twentieth-century-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-429-food-and-power-in-the-twentieth-century-spring-2005/sts-429s05.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1944, 'Cold War Science', 'This seminar examines the history and legacy of the Cold War on American science.', 'This seminar examines the history and legacy of the Cold War on American science. It explores scientist''s new political roles after World War II, ranging from elite policy makers in the nuclear age to victims of domestic anti Communism. It also examines the changing institutions in which the physical sciences and social sciences were conducted during the postwar decades, investigating possible epistemic effects on forms of knowledge. The subject closes by considering the place of science in the post-Cold War era.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-436-cold-war-science-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-436-cold-war-science-fall-2008/sts-436f08.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1945, 'Technologies for Creative Learning', 'This course explores the design of innovative educational technologies and creative learning environments, drawing on specific case studies such as the LEGO&reg; Programmable Brick, Scratch software and Computer Clubhouse after-school learning centers.', 'This course explores the design of innovative educational technologies and creative learning environments, drawing on specific case studies such as the LEGO&reg; Programmable Brick, Scratch software and Computer Clubhouse after-school learning centers. Includes activities with new educational technologies, reflections on learning experiences, and discussion of strategies and principles underlying the design of new tools and activities.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-714j-technologies-for-creative-learning-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-714j-technologies-for-creative-learning-fall-2009/mas-714jf09.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1946, 'Social Studies of Bioscience and Biotech', 'In this course, social, ethical and clinical issues associated with the development of new biotechnologies and their integration into clinical practice is discussed.', 'In this course, social, ethical and clinical issues associated with the development of new biotechnologies and their integration into clinical practice is discussed. Basic scientists, clinicians, bioethicists, and social scientists present on the following four general topics: changing political economy of biotech research; problems associated with the adaption of new biotechnologies and findings from molecular biology for clinical settings; the ethical issues that emerge from clinical research and clinical use of new technologies; and the broader social ethics of access and inequality.', 'http://ocw.mit.edu/courses/health-sciences-and-technology/hst-930j-social-studies-of-bioscience-and-biotech-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/health-sciences-and-technology/hst-930j-social-studies-of-bioscience-and-biotech-fall-2005/hst-930jf05.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1947, 'Integrating Doctoral Seminar on Emerging Technologies', 'This team-taught subject is for doctoral students working on emerging technologies at the interface of technology, policy and societal issues.', 'This team-taught subject is for doctoral students working on emerging technologies at the interface of technology, policy and societal issues. It integrates concepts of research strategy and design from a variety of disciplines. The class addresses problem identification and formulation of research topics, the role of qualitative and quantitative research methods, and the use of various data collection techniques. Coursework focuses on students'' thesis proposals, faculty-student study panels, critical evaluation of research design, and ethical issues in conducting research and gathering data.', 'http://ocw.mit.edu/courses/engineering-systems-division/esd-85j-integrating-doctoral-seminar-on-emerging-technologies-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/engineering-systems-division/esd-85j-integrating-doctoral-seminar-on-emerging-technologies-fall-2005/esd-85jf05.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1948, 'Social and Political Implications of Technology', 'This course is a graduate reading seminar, in which historical and contemporary studies are used to explore the interaction of technology with social and political values.', 'This course is a graduate reading seminar, in which historical and contemporary studies are used to explore the interaction of technology with social and political values. Emphasis is on how technological devices, structures, and systems influence the organization of society and the behavior of its members. Examples are drawn from the technologies of war, transportation, communication, production, and reproduction.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-462-social-and-political-implications-of-technology-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-462-social-and-political-implications-of-technology-spring-2006/sts-462s06.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1949, 'Cultural History of Technology', 'The subject of this course is the historical process by which the meaning of ''technology'' has been constructed.', 'The subject of this course is the historical process by which the meaning of ''technology'' has been constructed. Although the word itself is traceable to the ancient Greek root teckhne (meaning art), it did not enter the English language until the 17th century, and did not acquire its current meaning until after World War I. The aim of the course, then, is to explore various sectors of industrializing 19th and 20th century Western society and culture with a view to explaining and assessing the emergence of technology as a pivotal word (and concept) in contemporary (especially Anglo-American) thought and expression. Note: In the interests of freshness and topicality we regard the STS.464 syllabus as sufficiently flexible to permit some &mdash; mostly minor &mdash; variations from year to year. One example of a different STS.464 syllabus can be found in STS.464 Technology and the Literary Imagination, Spring 2008.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-464-cultural-history-of-technology-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-464-cultural-history-of-technology-spring-2005/sts-464s05.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1950, 'Research Seminar in Deep Sea Archaeology', 'STS.', 'STS.467 examines the intellectual foundations of archaeology in the deep sea. The course explores the current convergence of oceanography, archaeology, and engineering which allows scientists to discover, survey, and excavate shipwrecks in deep water with robots and submarines. The course seeks to address the following questions: How are new devices best employed for archaeological work? How do new capabilities (e.g. higher frequencies, higher resolution, all digital data output) change operations plans and research designs? What new technologies will be required? Area studies focus on the Aegean in Minoan times and western Sicily during Phoenician, Greek, and Roman hegemony.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-467-research-seminar-in-deep-sea-archaeology-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-467-research-seminar-in-deep-sea-archaeology-spring-2002/sts-467s02.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1951, 'Engineering Apollo: The Moon Project as a Complex System', 'This course is a detailed technical and historical exploration of the Apollo project to ''fly humans to the moon and return them safely to earth'' as an example of a complex engineering system.', 'This course is a detailed technical and historical exploration of the Apollo project to ''fly humans to the moon and return them safely to earth'' as an example of a complex engineering system. Emphasis is on how the systems worked, the technical and social processes that produced them, mission operations, and historical significance. Guest lectures are featured by MIT-affiliated engineers who contributed to and participated in the Apollo missions. Students work in teams on a final project analyzing an aspect of the historical project to articulate and synthesize ideas in engineering systems.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-471j-engineering-apollo-the-moon-project-as-a-complex-system-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-471j-engineering-apollo-the-moon-project-as-a-complex-system-spring-2007/sts-471js07.jpg', 'Science, Technology, and Society', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1952, 'Writing on Contemporary Issues: Social and Ethical Issues', 'This course provides the opportunity for students-as readers, viewers, writers and speakers-to engage with social and ethical issues they care deeply about.', 'This course provides the opportunity for students-as readers, viewers, writers and speakers-to engage with social and ethical issues they care deeply about. Over the course of the semester, through discussing the writing of classic and contemporary authors, we will explore different perspectives on a range of social issues such as free speech, poverty and homelessness, mental illness, capital punishment and racial and gender inequality. In addition, we will analyze selected documentary and feature films and photographs that represent or dramatize social problems or issues. In assigned essays, students will have the opportunity to write about social and ethical issues of their own choice. This course aims to help students to grow significantly in their ability to understand and grapple with arguments, to integrate secondary print and visual sources and to craft well-reasoned and elegant essays. Students will also keep a reading journal and give oral presentations. In class we will discuss assigned texts, explore strategies for successful academic writing, freewrite and respond to one another''s essays.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-writing-on-contemporary-issues-social-and-ethical-issues-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-writing-on-contemporary-issues-social-and-ethical-issues-spring-2010/21w-730s10.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1953, 'Expository Writing: Social and Ethical Issues in Print, Photography and Film', 'This section of Expository Writing provides the opportunity for students- as readers, viewers, writers and speakers - to engage with social and ethical issues that they care deeply about.', 'This section of Expository Writing provides the opportunity for students- as readers, viewers, writers and speakers - to engage with social and ethical issues that they care deeply about. Through discussing selected documentary and feature films and the writings of such authors as Maya Angelou, Robert Coles, Charles Dickens, Barbara Ehrenreich, Martin Luther King, Jr., Jonathan Kozol, and Alice Walker, we will explore different perspectives on a range of social problems such as poverty, homelessness, and racial and gender inequality. In assigned essays, students will have the opportunity to write about social and ethical issues of their own choice. This course aims to help students to grow significantly in their ability to understand and grapple with arguments, to integrate secondary print and visual sources and to craft well-reasoned and elegant essays. Students will also keep a reader-writer notebook and give at least one oral presentation. In class we will discuss assigned films and readings, explore strategies for successful academic writing, freewrite and critique one another''s essays. Satisfies Phase I and CI Writing Requirements.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-1-expository-writing-social-and-ethical-issues-in-print-photography-and-film-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-1-expository-writing-social-and-ethical-issues-in-print-photography-and-film-fall-2005/21w-730-1f05.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1954, 'Expository Writing: Exploring Social and Ethical Issues through Film and Print', 'This section of Expository Writing provides the opportunity for students- as readers, viewers, writers and speakers - to engage with social and ethical issues that they care deeply about.', 'This section of Expository Writing provides the opportunity for students- as readers, viewers, writers and speakers - to engage with social and ethical issues that they care deeply about. Through discussing selected documentary and feature films and the writings of such authors as Maya Angelou, Robert Coles, Charles Dickens, Barbara Ehrenreich, Martin Luther King, Jr., Jonathan Kozol, and Alice Walker, we will explore different perspectives on a range of social problems such as poverty, homelessness, and racial and gender inequality. In assigned essays, students will have the opportunity to write about social and ethical issues of their own choice. This course aims to help students to grow significantly in their ability to understand and grapple with arguments, to integrate secondary print and visual sources and to craft well-reasoned and elegant essays. Students will also keep a reader-writer notebook and give at least one oral presentation. In class we will discuss assigned films and readings, explore strategies for successful academic writing, freewrite and critique one another''s essays. Satisfies Phase I and CI Writing Requirements.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-1-expository-writing-exploring-social-and-ethical-issues-through-film-and-print-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-1-expository-writing-exploring-social-and-ethical-issues-through-film-and-print-fall-2002/21w-730-1f02.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1955, 'Writing and the Environment', 'Environmentalists have traditionally relied upon the power of their prose to transform the thoughts and behavior of their contemporaries.', 'Environmentalists have traditionally relied upon the power of their prose to transform the thoughts and behavior of their contemporaries. In this class, we will do our best to follow in their footsteps. We will consider the strategies of popular science writers, lesser-known geologists, biologists, and hydrologists, and famous environmentalists. Students will have a chance to try out several ways of characterizing and explaining natural environments. Weekly writing exercises will help students develop and explore material for the longer papers.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-3-writing-and-the-environment-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-3-writing-and-the-environment-spring-2005/21w-730-3s05.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1956, 'Consumer Culture', 'What is the good life, and can you shop there? Would you want that life if you couldn''t? Has shopping replaced working as the activity that gives the most meaning to our lives? The theme for this Expository Writing class is Consumer Culture.', 'What is the good life, and can you shop there? Would you want that life if you couldn''t? Has shopping replaced working as the activity that gives the most meaning to our lives? The theme for this Expository Writing class is Consumer Culture. The class will explore what it means to belong to a consumer society&mdash;to think of ourselves, as Douglas Rushkoff puts it, less as citizens than as consumers. Readings will serve both as examples of effective writing techniques and as springboards for discussion. We&rsquo;ll read essays that explore a variety of cultural meanings of shopping and that analyze the way advertising works. We will also read essays that critique consumer culture from several perspectives, including those of psychology, gender, art, environmentalism and ethics. Readings and essay assignments will invite you to reflect on personal, familial and cultural meanings of shopping; to analyze advertisements; and to join in conversation with critics of consumer culture and offer your own critiques.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-3-consumer-culture-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-3-consumer-culture-fall-2002/21w-730-3f02.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1957, 'Expository Writing: Autobiography - Theory and Practice', 'Focus: What can we believe when we read an autobiography? How do writers recall, select, shape, and present their lives to construct life stories?&#160; Readings that ground these questions include selections from Incidents in the Life of a Slave Girl by Linda Brent (pseudonym for Harriet Jacobs), ''A Sketch of the Past'' by Virginia Woolf, Notes of A Native Son by James Baldwin, ''The Achievement of Desire'' by Richard Rodriguez, The Woman Warrior by Maxine Hong Kingston, and ''Our Secret'' by Susan Griffin.', 'Focus: What can we believe when we read an autobiography? How do writers recall, select, shape, and present their lives to construct life stories?&#160; Readings that ground these questions include selections from Incidents in the Life of a Slave Girl by Linda Brent (pseudonym for Harriet Jacobs), ''A Sketch of the Past'' by Virginia Woolf, Notes of A Native Son by James Baldwin, ''The Achievement of Desire'' by Richard Rodriguez, The Woman Warrior by Maxine Hong Kingston, and ''Our Secret'' by Susan Griffin. Discussion, papers, and brief oral presentations will focus on the content of the life stories as well as the forms and techniques authors use to shape autobiography. We will identify masks and stances used to achieve various goals, sources and interrelationships of technical and thematic concerns, and ''fictions'' of autobiographical writing. Assignments will allow students to consider texts in terms of their implicit theories of autobiography, of theories we read, and of students'' experiences; assignments also allow some autobiographical writing.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-3-expository-writing-autobiography-theory-and-practice-spring-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-3-expository-writing-autobiography-theory-and-practice-spring-2001/21w-730-3s01.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1958, 'Expository Writing: Analyzing Mass Media', 'This course focuses on developing and refining the skills that will you need to express your voice more effectively as an academic writer.', 'This course focuses on developing and refining the skills that will you need to express your voice more effectively as an academic writer. As a focus for our writing this semester, this course explores what it means to live in the age of mass media. We will debate the power of popular American media in shaping our ideas of self, family and community and in defining social issues. Throughout the semester, students will focus on writing as a process of drafting and revising to create essays that are lively, clear, engaging and meaningful to a wider audience.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-4-expository-writing-analyzing-mass-media-spring-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-4-expository-writing-analyzing-mass-media-spring-2001/21w-730-4s01.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1959, 'Writing on Contemporary Issues: Culture Shock! Writing, Editing, and Publishing in Cyberspace', 'This course is an introduction to writing prose for a public audience&mdash;specifically, prose that is both critical and personal, that features your ideas, your perspective, and your voice to engage readers.', 'This course is an introduction to writing prose for a public audience&mdash;specifically, prose that is both critical and personal, that features your ideas, your perspective, and your voice to engage readers. The focus of our reading and your writing will be American popular culture, broadly defined. That is, you will write essays that critically engage elements and aspects of contemporary American popular culture and that do so via a vivid personal voice and presence. In the coming weeks we will read a number of pieces that address current issues in popular culture. These readings will address a great many subjects from the contemporary world to launch and elaborate an argument or position or refined observation. And you yourselves will write a great deal, attending always to the ways your purpose in writing and your intended audience shape what and how you write. The end result of our collaborative work will be a new edition, the seventh, of Culture Shock!, an online magazine of writings on American popular culture, which we will post on the Web for the worldwide reading public to enjoy.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-5-writing-on-contemporary-issues-culture-shock-writing-editing-and-publishing-in-cyberspace-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-5-writing-on-contemporary-issues-culture-shock-writing-editing-and-publishing-in-cyberspace-fall-2008/21w-730-5f08.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1960, 'Writing on Contemporary Issues: Imagining the Future', 'Turn-of-the-century eras have historically been times when people are more than usually inclined to scrutinize the present and speculate about the future.', 'Turn-of-the-century eras have historically been times when people are more than usually inclined to scrutinize the present and speculate about the future. Now, the turn not just of a century but of a millennium having recently passed, such scrutiny and speculations inevitably intensify. What will the future that awaits us in this twenty-first century and beyond be like? And how do visions of that future reflect and respond to the world we live in now? In this course we will read and write about how some writers and filmmakers have responded to the present as a way of imagining&mdash;and warning about&mdash;possible worlds to come. Guided by our reading and discussion, we will scrutinize our own present and construct our own visions of the future through close readings of the texts as well as of some aspects of contemporary culture&mdash;urban and environmental crises, economic imperialism, sexual and reproductive politics, the ethics of biotechnologies, issues of race and gender, the romance of technology, robotics and cyborg cultures, media saturation, language and representation&mdash;and the persistent questions they pose about what it means to be human at this start of a new millennium.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-5-writing-on-contemporary-issues-imagining-the-future-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-730-5-writing-on-contemporary-issues-imagining-the-future-fall-2007/21w-730-5f07.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1961, 'Writing and Experience: Crossing Borders', 'In this era of globalization, many of us have multi- or bi-cultural, multilingual or bilingual backgrounds, and even if we don''t have such a background, we need urgently to understand the experiences of people who do.', 'In this era of globalization, many of us have multi- or bi-cultural, multilingual or bilingual backgrounds, and even if we don''t have such a background, we need urgently to understand the experiences of people who do. You will very likely work outside the United States at some point in your future; you will almost certainly work with people who speak more than one language, whose ancestry or origins are in a country other than the U.S., who have crossed borders of nation, language, culture, class to amalgamate into the large and diverse culture that is America. In this class we will read the personal narratives of bilingual and bicultural writers, some of whom have struggled to assimilate, others of whom have celebrated their own contributions to a culture of diversity. You will write four personal essays of your own for the class, each of which will receive workshop discussion in class and response from me; you will then revise the essays to polish them for possible publication. One of your essays will be an investigative one, where you will focus on a subject of your choice, investigate it thoroughly, and then write with authority about it. The process of the class will encourage you to both improve your writing significantly and gain a greater understanding of experiences of people who are in some way like you as well as those who are in some way different.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-731-2-writing-and-experience-crossing-borders-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-731-2-writing-and-experience-crossing-borders-fall-2010/21w-731-2f10.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1962, 'Writing and Experience', 'MIT students bring rich cultural backgrounds to their college experience.', 'MIT students bring rich cultural backgrounds to their college experience. This course explores the splits, costs, confusions, insights, and opportunities of living in two traditions, perhaps without feeling completely at home in either. Course readings include accounts of growing up Asian-American, Hispanic, Native American, and South-East Asian-American, and of mixed race. The texts include selections from Maxine Hong Kingston''s The Woman Warrior, Kesaya E. Noda''s ''Growing Up Asian in America,'' Sandra Cisneros''s Woman Hollering Creek, Gary Soto''s ''Like Mexicans,'' Sherman Alexie''s The Toughest Indian in the World, Jhumpa Lahiri''s Interpreter of Maladies, the movies Smoke Signals and Mississippi Masala, Danzy Senna''s Caucasia, and others. We will also use students'' writings as ways to investigate our multiple identities, exploring the constraints and contributions of cultural and ethnic traditions. Students need not carry two passports in order to enroll; an interest in reading and writing about being shaped by multiple influences suffices.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-731-4-writing-and-experience-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-731-4-writing-and-experience-spring-2002/21w-731-4s02.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1963, 'Science Writing and New Media', 'This course introduces writing, graphics, meetings, oral presentation, collaboration, and design as tools for product development.', 'This course introduces writing, graphics, meetings, oral presentation, collaboration, and design as tools for product development. The communication instruction is embedded in design projects that require students to work in teams to conceive, design, prototype and evaluate energy related products. The communication instruction focuses on the communication tasks that are integral to this design process, ranging, across design notebooks, email communications, informal oral presentations, meeting etiquette, literature searches, white papers reports, and formal presentations. In addition to the assignments specific to product development, a few assignments, especially reading and reflection, will address the cultural situation of engineers and engineering in the world at large.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-science-writing-and-new-media-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-science-writing-and-new-media-fall-2010/21w-732f10.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1964, 'Introduction to Technical Communication: Ethics in Science and Technology', 'This section of Introduction to Technical Communication deals with ethical issues associated with the design, use, and propagation of technology.', 'This section of Introduction to Technical Communication deals with ethical issues associated with the design, use, and propagation of technology. At virtually all stages of development and use, any technology can carry with it ethical dilemmas for both creators and users. Of particular interest is how such dilemmas are resolved (or complicated) according to how effectively they are communicated to stakeholders.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-2-introduction-to-technical-communication-ethics-in-science-and-technology-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-2-introduction-to-technical-communication-ethics-in-science-and-technology-fall-2006/21w-732-2f06.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1965, 'Intro to Tech Communication', 'An information-based society necessitates good writing in all careers.', 'An information-based society necessitates good writing in all careers. Many scientists and technical professionals must write progress reports, analyses, literature reviews, or other documents to communicate within their workplaces, and many must also address more general audiences in grant proposals, conference papers, articles, and so on. This course is designed to serve as a basic introduction to the practice of technical writing for those who work as scientists and technical researchers. Because scientific and technical fields are becoming more interdisciplinary and more globally connected everyday, we will also consider intercultural communication issues at some length.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-2-intro-to-tech-communication-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-2-intro-to-tech-communication-fall-2002/21w-732-2f02.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1966, 'Introduction to Technical Communication: Explorations in Scientific and Technical Writing', 'This course is designed to help you develop skills that will enable you to produce clear and effective scientific and technical documents.', 'This course is designed to help you develop skills that will enable you to produce clear and effective scientific and technical documents. We will focus on basic principles of good writing-which scientific and technical writing shares with other forms of writing-and on types of documents common in scientific and technical fields and organizations. While the emphasis will be on writing, oral communication of scientific and technical information will form an important component of the course, as well.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-5-introduction-to-technical-communication-explorations-in-scientific-and-technical-writing-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-5-introduction-to-technical-communication-explorations-in-scientific-and-technical-writing-fall-2006/21w-732-5f06.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1967, 'Writing About Literature', 'Students, scholars, bloggers, reviewers, fans, and book-group members write about literature, but so do authors themselves.', 'Students, scholars, bloggers, reviewers, fans, and book-group members write about literature, but so do authors themselves. Through the ways they engage with their own texts and those of other artists, sampling, remixing, and rethinking texts and genres, writers reflect on and inspire questions about the creative process. We will examine Mary Shelley''s reshaping of Milton''s Paradise Lost, German fairy tales, tales of scientific discovery, and her husband''s poems to make Frankenstein (1818, 1831); Melville''s redesign of a travel narrative into a Gothic novella in Benito Cereno (1856); and Alison Bechdel''s rewriting of Oscar Wilde''s The Importance of Being Earnest (1895) in her graphic novel Fun Home (2006). Showings of film versions of some of these works will allow us to project forward in the remixing process as well.', 'http://ocw.mit.edu/courses/literature/21l-000j-writing-about-literature-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-000j-writing-about-literature-fall-2010/21l-000jf10.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1968, 'Writing and Reading the Essay', 'This is a course focused on the literary genre of the essay, that wide-ranging, elastic, and currently very popular form that attracts not only nonfiction writers but also fiction writers, poets, scientists, physicians, and others to write in the form, and readers of every stripe to read it.', 'This is a course focused on the literary genre of the essay, that wide-ranging, elastic, and currently very popular form that attracts not only nonfiction writers but also fiction writers, poets, scientists, physicians, and others to write in the form, and readers of every stripe to read it. Some say we are living in era in which the essay is enjoying a renaissance; certainly essays, both short and long, are at present easier to get published than are short stories or novels, and essays are featured regularly and prominently in the mainstream press (both magazines and newspapers) and on the New York Times bestseller books list. But the essay has a history, too, a long one, which goes back at least to the sixteenth-century French writer Montaigne, generally considered the progenitor of the form. It will be our task, and I hope our pleasure, to investigate the possibilities of the essay together this semester, both by reading and by writing.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-735-writing-and-reading-the-essay-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-735-writing-and-reading-the-essay-fall-2005/21w-735f05.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1969, 'Writing and Reading the Essay', 'As the course title suggests, this class is meant to acquaint you with the literary and rhetorical tradition of the essay, a genre which has been described by one scholar as ''the meeting ground between art and philosophy,'' and by another as ''the place where the self finds a pattern in the world, and the world finds a pattern in the self''.', 'As the course title suggests, this class is meant to acquaint you with the literary and rhetorical tradition of the essay, a genre which has been described by one scholar as ''the meeting ground between art and philosophy,'' and by another as ''the place where the self finds a pattern in the world, and the world finds a pattern in the self''. Though the essay is part of a tradition of prose which stretches back to antiquity, it is also a thoroughly modern and popular form of writing, found in print media and on the web.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-735-writing-and-reading-the-essay-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-735-writing-and-reading-the-essay-fall-2004/21w-735f04.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1970, 'Darwin and Design', 'This subject offers a broad survey of texts (both literary and philosophical) drawn from the Western tradition and selected to trace the immediate intellectual antecedents and some of the implications of the ideas animating Darwin''s revolutionary On the Origin of Species.', 'This subject offers a broad survey of texts (both literary and philosophical) drawn from the Western tradition and selected to trace the immediate intellectual antecedents and some of the implications of the ideas animating Darwin''s revolutionary On the Origin of Species. Darwin''s text, of course, is about the mechanism that drives the evolution of life on this planet, but the fundamental ideas of the text have implications that range well beyond the scope of natural history, and the assumptions behind Darwin''s arguments challenge ideas that go much further back than the set of ideas that Darwin set himself explicitly to question - ideas of decisive importance when we think about ourselves, the nature of the material universe, the planet that we live upon, and our place in its scheme of life. In establishing his theory of natural selection, Darwin set himself, rather self-consciously, to challenge a whole way of thinking about these things. The main focus of attention will be Darwin''s contribution to the so-called ''argument from design'' - the notion that innumerable aspects of the world (and most particularly the organisms within it) display features directly analogous to objects of human design and, since design implies a designer, that an intelligent, conscious agency must have been responsible for their organization and creation. Previously, it had been argued that such features must have only one of two ultimate sources - chance or conscious agency. Darwin proposed and elaborated a third source, which he called Natural Selection, an unconscious agency capable of outdoing the most complex feats of human intelligence. The course of study will not only examine the immediate inspiration for this idea in the work of Adam Smith and Thomas Malthus and place Darwin''s Origin and the theory of Natural Selection in the history of ensuing debate, but it will also touch upon related issues.', 'http://ocw.mit.edu/courses/literature/21l-448j-darwin-and-design-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-448j-darwin-and-design-fall-2003/21l-448jf03.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1971, 'Black Matters: Introduction to Black Studies', 'This class is an interdisciplinary survey that explores the experiences of people of African descent through the overlapping approaches of history, literature, anthropology, legal studies, media studies, performance, linguistics, and creative writing.', 'This class is an interdisciplinary survey that explores the experiences of people of African descent through the overlapping approaches of history, literature, anthropology, legal studies, media studies, performance, linguistics, and creative writing. It connects the experiences of African Americans and of other American minorities, focusing on social, political, and cultural histories, and on linguistic patterns. Activities include lectures, discussions, workshops, and required field trips that involve minimal cost to students.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-630j-black-matters-introduction-to-black-studies-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-630j-black-matters-introduction-to-black-studies-fall-2009/21m-630jf09.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1972, 'Writing About Race: Narratives of Multiraciality', 'In this course we will read essays, novels, memoirs, and graphic texts, and view documentary and experimental films and videos which explore race from the standpoint of the multiracial.', 'In this course we will read essays, novels, memoirs, and graphic texts, and view documentary and experimental films and videos which explore race from the standpoint of the multiracial. Examining the varied work of multiracial authors and filmmakers such as Danzy Senna, Ruth Ozeki, Kip Fulbeck, James McBride and others, we will focus not on how multiracial people are seen or imagined by the dominant culture, but instead on how they represent themselves. How do these authors approach issues of family, community, nation, language and history? What can their work tell us about the complex interconnections between race, gender, class, sexuality, and citizenship? Is there a relationship between their experiences of multiraciality and a willingness to experiment with form and genre? In addressing these and other questions, we will endeavor to think and write more critically and creatively about race as a social category and a lived experience.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-narratives-of-multiraciality-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-narratives-of-multiraciality-fall-2008/21w-742jf08.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1973, 'Advanced Essay Workshop', 'This course is a workshop for students with some experience in writing essays, nonfiction prose.', 'This course is a workshop for students with some experience in writing essays, nonfiction prose. Our focus will be negotiating and representing identities grounded in gender, race, class, nationality, sexuality, and other categories of identity, either our own or others'', in prose that is expository, exploratory, investigative, persuasive, lyrical, or incantatory. We will read nonfiction prose works by a wide array of writers who have used language to negotiate and represent aspects of identity and the ways the different determinants of identity intersect, compete, and cooperate.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-745-advanced-essay-workshop-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-745-advanced-essay-workshop-spring-2008/21w-745s08.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1974, 'Humanistic Perspectives on Medicine: From Ancient Greece to Modern America', 'This course is designed to explore the human side of medicine: the nature of the physician''s identity and obligations; the history and philosophy of the Western medical tradition; the experience of being ill and being a patient; and the challenges of medical ethics.', 'This course is designed to explore the human side of medicine: the nature of the physician''s identity and obligations; the history and philosophy of the Western medical tradition; the experience of being ill and being a patient; and the challenges of medical ethics. The writing in this class is therefore conceived as an instrument of exploration, and is an integral part of the class''s activities.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-746-humanistic-perspectives-on-medicine-from-ancient-greece-to-modern-america-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-746-humanistic-perspectives-on-medicine-from-ancient-greece-to-modern-america-spring-2005/21w-746s05.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1975, 'Classical Rhetoric and Modern Political Discourse', 'This course is an introduction to the history, theory, practice, and implications of rhetoric, the art and craft of persuasion throughAnalyzing persuasive texts and speechesCreating persuasive texts and speechesThrough class discussions, presentations, and written assignments, you will get to practice your own rhetorical prowess.', 'This course is an introduction to the history, theory, practice, and implications of rhetoric, the art and craft of persuasion throughAnalyzing persuasive texts and speechesCreating persuasive texts and speechesThrough class discussions, presentations, and written assignments, you will get to practice your own rhetorical prowess. Through the readings, you''ll also learn some ways to make yourself a more efficient reader, as you turn your analytical skills on the texts themselves. This combination of reading, speaking, and writing will help you succeed in:learningto read and think criticallytechniques of rhetorical analysistechniques of argumentto enhance your written and oral discourse with appropriate figures of speechsome techniques of oral presentation and the use of visual aids and visual rhetoric.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-747-classical-rhetoric-and-modern-political-discourse-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-747-classical-rhetoric-and-modern-political-discourse-fall-2009/21w-747f09.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1976, 'Rhetoric', 'This course is an introduction to the theory, the practice, and the implications (both social and ethical) of rhetoric, the art and craft of persuasion.', 'This course is an introduction to the theory, the practice, and the implications (both social and ethical) of rhetoric, the art and craft of persuasion. This semester, many of your skills will have the opportunity to be deepened by practice, including your analytical and critical thinking skills, your persuasive writing skills, and your oral presentation skills. In this course you will act as both a rhetor (a person who uses rhetoric) and as a rhetorical critic (one who studies the art of rhetoric). Both write to persuade; both ask and answer important questions. Always one of their goals is to create new knowledge for all of us, so no endeavor in this class is a &quot;mere exercise.&quot;', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-747-1-rhetoric-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-747-1-rhetoric-spring-2010/21w-747-1s10.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1977, 'Rhetoric', 'This course is an introduction to the history, the theory, the practice, and the implications (both social and ethical) of rhetoric, the art and craft of persuasion.', 'This course is an introduction to the history, the theory, the practice, and the implications (both social and ethical) of rhetoric, the art and craft of persuasion. By the end of the semester, you will have been exposed to several of the key concepts of rhetoric (e.g., ethos, pathos, logos, invention, style, arrangement, kairos, stasis, commonplaces) and to the over-riding importance of writing to your audience. You will have gotten a taste of rhetorical history and theory. You will explore and analyze and respond to some key texts by significant writers. You will have had a chance to practice speaking and debating before the class. You will have written and revised several texts. You will have examined some of your core beliefs and assumptions. In this course you will act as both a rhetor (a person who uses rhetoric) and a rhetorician (one who studies the art of rhetoric). Because the study of rhetoric has always had as one of its goals the creation of active and informed citizens and because rhetors write to influence the real world and thus to become agents of positive change, the topics you choose and the essays you write will have the important purpose of persuading your readers (the class and me).', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-747-1-rhetoric-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-747-1-rhetoric-spring-2006/21w-747-1s06.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1978, 'Rhetoric', 'This course is an introduction to the history, the theory, the practice, and the implications (both social and ethical) of rhetoric, the art and craft of persuasion.', 'This course is an introduction to the history, the theory, the practice, and the implications (both social and ethical) of rhetoric, the art and craft of persuasion. This semester, many of your skills will be deepened by practice, including your analytical skills, your critical thinking skills, your persuasive writing skills, and your oral presentation skills. In this course you will act as both a rhetor (a person who uses rhetoric) and a rhetorician (one who studies the art of rhetoric).', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-747-1-rhetoric-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-747-1-rhetoric-fall-2006/21w-747-1f06.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1979, 'Documentary Photography and Photojournalism: Still Images of a World in Motion', 'This course is an introduction to the great tradition of documentary photography.', 'This course is an introduction to the great tradition of documentary photography. Students learn to see the world around them in a new way and produce a documentary project. The course requires reading and writing about photography, as well as doing it on a regular basis. The class emphasis is on thinking about why people photograph, what photographs do and do not mean to us, and on doing documentary work, on telling stories with photographs. This is not a technical class, and it should not be considered an &quot;introduction to photography.&quot; I work on the assumption that any student signing up for the class has at least a minimal sense of the difference between f stops and T stops, and can find his or her way around a camera. While there will be some technical discussion in class, it will be limited.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-749-documentary-photography-and-photojournalism-still-images-of-a-world-in-motion-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-749-documentary-photography-and-photojournalism-still-images-of-a-world-in-motion-spring-2009/21w-749s09.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1980, 'Documentary Photography and Photo Journalism: Still Images of A World In Motion', 'Documentary Photography and Photojournalism: Still Images of A World In Motion exposes students to the work of a number of great documentary photographers and photojournalists, as well as to writing about the documentary tradition.', 'Documentary Photography and Photojournalism: Still Images of A World In Motion exposes students to the work of a number of great documentary photographers and photojournalists, as well as to writing about the documentary tradition. Students work throughout the term on a photo documentary project of their own, attempting to reduce a tiny area of the moving world to a set of still images that convey what the viewer needs to know about what they saw - without hearing the sounds, smelling the odors, experiencing what was happening outside the viewfinder, and without seeing the motion. Students also write papers about the subjects of their photo documentaries.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-749-documentary-photography-and-photo-journalism-still-images-of-a-world-in-motion-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-749-documentary-photography-and-photo-journalism-still-images-of-a-world-in-motion-spring-2002/21w-749s02.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1981, 'Writing and Reading Short Stories', 'This course is an introduction to the short story.', 'This course is an introduction to the short story. Students will write stories and short descriptive sketches. Students will read great short stories and participate in class discussions of students'' writing and the assigned stories in their historical and social contexts.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-755-writing-and-reading-short-stories-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-755-writing-and-reading-short-stories-spring-2012/21w-755s12.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1982, 'Writing and Reading Short Stories', 'This course is an introduction to the short story.', 'This course is an introduction to the short story. Students will write stories and short descriptive sketches. Students will read great short stories and participate in class discussions of students'' writing and the assigned stories in their historical and social contexts.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-755-writing-and-reading-short-stories-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-755-writing-and-reading-short-stories-spring-2012/21w-755s12.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1983, 'Interactive and Non-Linear Narrative: Theory and Practice', 'This course covers techniques of creating narratives that take advantage of the flexibility of form offered by the computer.', 'This course covers techniques of creating narratives that take advantage of the flexibility of form offered by the computer. The course studies the structural properties of book-based narratives that experiment with digression, multiple points of view, disruptions of time and of storyline. The class analyzes the structure and evaluates the literary qualities of computer-based narratives including hypertexts, adventure games, and classic artificial intelligence programs like Eliza. With this base, students use authoring systems to model a variety of narrative techniques and to create their own fictions. Knowledge of programming is helpful but not necessary.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-765j-interactive-and-non-linear-narrative-theory-and-practice-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-765j-interactive-and-non-linear-narrative-theory-and-practice-spring-2006/21w-765js06.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1984, 'Interactive and Non-Linear Narrative: Theory and Practice', 'This course explores the properties of non-linear, multi-linear, and interactive forms of narratives as they have evolved from print to digital media.', 'This course explores the properties of non-linear, multi-linear, and interactive forms of narratives as they have evolved from print to digital media. Works covered in this course range from the Talmud, classics of non-linear novels, experimental literature, early sound and film experiments to recent multi-linear and interactive films and games. The study of the structural properties of narratives that experiment with digression, multiple points of view, disruptions of time, space, and of storyline is complemented by theoretical texts about authorship/readership, plot/story, properties of digital media and hypertext. Questions that will be addressed in this course include: How can we define ''non-linearity/multi-linearity'', ''interactivity'', ''narrative''. To what extend are these aspects determined by the text, the reader, the digital format? What kinds of narratives are especially suited for a nonlinear/ interactive format? Are there stories that can only be told in a digital format? What can we learn from early non-digital examples of non-linear and interactive story telling?', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-765j-interactive-and-non-linear-narrative-theory-and-practice-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-765j-interactive-and-non-linear-narrative-theory-and-practice-spring-2004/21w-765js04.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1985, 'Digital Poetry', 'This class investigates theory and practice of digital or new media poetry with emphasis on workshop review of digital poetry created by students.', 'This class investigates theory and practice of digital or new media poetry with emphasis on workshop review of digital poetry created by students.&nbsp;Each week students examine published examples of digital poetry in a variety of forms including but not limited to soundscapes, hypertext poetry, animation, code poems, interactive games, location-based poems using handheld devices, digital video and wikis.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-772-digital-poetry-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-772-digital-poetry-fall-2005/21w-772f05.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1986, 'Writing about Nature and Environmental Issues', 'This course focuses on traditional nature writing and the environmentalist essay.', 'This course focuses on traditional nature writing and the environmentalist essay. Students will keep a Web log as a journal. Writings are drawn from the tradition of nature writing and from contemporary forms of the environmentalist essay.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-775-writing-about-nature-and-environmental-issues-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-775-writing-about-nature-and-environmental-issues-fall-2006/21w-775f06.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1987, 'The Science Essay', 'The science essay uses science to think about the human condition; it uses humanistic thinking to reflect on the possibilities and limits of science and technology.', 'The science essay uses science to think about the human condition; it uses humanistic thinking to reflect on the possibilities and limits of science and technology. In this class we read and practice writing science essays of varied lengths and purposes. We will read a wide variety of science essays, ranging across disciplines, both to learn more about this genre and to inspire your own writing. This semester''s reading centers on ''The Dark Side,'' with essays ranging from Alan Lightman''s ''Prisoner of the Wired World'' through Robin Marantz Henig''s cautionary account of nano-technology (''Our Silver-Coated Future'') to David Quammen''s investigation of diseases that jump from animals to humans (''Deadly Contact'').', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-777-the-science-essay-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-777-the-science-essay-spring-2009/21w-777s09.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1988, 'Communicating in Technical Organizations', 'This course has two parallel aims: To improve student writing about technical subject matters, including forms of writing commonly employed in technical organizations, and Critically to examine the nature of technologically-assisted communication, focusing somewhat on professional communication among scientists and engineers.', 'This course has two parallel aims: To improve student writing about technical subject matters, including forms of writing commonly employed in technical organizations, and Critically to examine the nature of technologically-assisted communication, focusing somewhat on professional communication among scientists and engineers. We will often combine these two goals, by practicing critical investigation of communications technologies in written formats (and other media) that employ communications technologies.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-780-communicating-in-technical-organizations-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-780-communicating-in-technical-organizations-spring-2005/21w-780s05.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1989, 'Communicating in Technical Organizations', 'This course focuses on an exploration of the role that communication plays in the work of the contemporary engineering and science professional.', 'This course focuses on an exploration of the role that communication plays in the work of the contemporary engineering and science professional. Emphasis is placed on analyzing how composition and publication contribute to work management and knowledge production, as well as the ''how-to'' aspects of writing specific kinds of documents in a clear style. Topics include: communication as organizational process, electronic modes such as e-mail and the Internet, the informational and social roles of specific document forms, writing as collaboration, the writing process, the elements of style, methods of oral presentation, and communication ethics. Case studies used as the basis for class discussion and some writing assignments. Several short documents, a longer report or article, and a short oral presentation are required.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-780-communicating-in-technical-organizations-fall-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-780-communicating-in-technical-organizations-fall-2001/21w-780f01.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1990, 'Science and Engineering Writing for Phase II', '21W.', '21W.783 is a series of seminars focusing on common writing problems faced by professional engineers and scientists. Participants will tune up their writing skills and prepare a pair of technical documents under the guidance of the instructor. The writing assignments focus on a single topic of the student''s choosing, preferably one for which the necessary research has been done, or is in the process of being done. In addition to the writing component, students will deliver an oral presentation based on the written work.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-783-science-and-engineering-writing-for-phase-ii-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-783-science-and-engineering-writing-for-phase-ii-fall-2002/21w-783f02.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1991, 'Communicating in Cyberspace', 'This class covers the analysis, design, implementation and testing of various forms of digital communication based on group collaboration.', 'This class covers the analysis, design, implementation and testing of various forms of digital communication based on group collaboration. Students are encouraged to think about the Web and other new digital interactive media not just in terms of technology but also broader issues such as language (verbal and visual), design, information architecture, communication and community. Students work in small groups on a semester-long project of their choice.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-785-communicating-in-cyberspace-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-785-communicating-in-cyberspace-fall-2003/21w-784f09.jpg', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1992, 'Communicating with Mobile Technology', 'Students work in small collaborative design teams to propose, build, and document a semester-long project focused on mobile applications for cell phones.', 'Students work in small collaborative design teams to propose, build, and document a semester-long project focused on mobile applications for cell phones. Additional assignments include creating several small mobile applications such as context-aware mobile media capture and games. Students document their work through a series of written and oral proposals, progress reports, and final reports. This course covers the basics of J2ME and explores mobile imaging and media creation, GPS location, user-centered design, usability testing, and prototyping. Java experience is recommended.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-789-communicating-with-mobile-technology-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-789-communicating-with-mobile-technology-spring-2011/21w-789s11.png', 'Writing and Humanistic Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1993, 'Rethinking the Family, Sex, and Gender', 'Through investigating cross-cultural case studies, this course introduces students to the anthropological study of the social institutions and symbolic meanings of family, household, gender, and sexuality.', 'Through investigating cross-cultural case studies, this course introduces students to the anthropological study of the social institutions and symbolic meanings of family, household, gender, and sexuality. We will explore the myriad forms that families and households take and evaluate their social, emotional, and economic dynamics.', 'http://ocw.mit.edu/courses/anthropology/21a-232j-rethinking-the-family-sex-and-gender-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-232j-rethinking-the-family-sex-and-gender-fall-2010/21a-232jf10.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1994, 'New Culture of Gender: Queer France', 'This course addresses the place of contemporary queer identities in French discourse and discusses the new generation of queer authors and their principal concerns.', 'This course addresses the place of contemporary queer identities in French discourse and discusses the new generation of queer authors and their principal concerns. Class discussions and readings will introduce students to the main classical references of queer subcultures, from Proust and Vivien to Hocquenghem and Wittig. Throughout the course, students will examines current debates on post-colonial and globalized queer identities through essays, songs, movies, and novels. Authors covered include Didier Eribon, Anne GarrÃ©ta, Abdellah TaÃ¯a, Anne Scott, and Nina Bouraoui. This class is taught in French.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-325j-new-culture-of-gender-queer-france-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-325j-new-culture-of-gender-queer-france-fall-2011/21f-325jf11.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1995, 'The Invention of French Theory: A History of Transatlantic Intellectual Life since 1945', 'In the decades following the Second World War, a cluster of extraordinary French thinkers were widely translated and read in American universities.', 'In the decades following the Second World War, a cluster of extraordinary French thinkers were widely translated and read in American universities. Their works were soon labeled as &quot;French Theory.&quot; Why would sharing the same nationality make authors such as Lacan, Cixous, Derrida, Foucault or Debord, ambassadors of a specifically &quot;French&quot; theory? The course will explore the maze of transatlantic intellectual debates since 1945 and the heyday of French existentialism. We will study the debates on communism, decolonization, neoâ€liberalism, gender, youth culture and mass media. This course is taught in English.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-068j-the-invention-of-french-theory-a-history-of-transatlantic-intellectual-life-since-1945-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-068j-the-invention-of-french-theory-a-history-of-transatlantic-intellectual-life-since-1945-spring-2012/21f-068js12.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1996, 'Introduction to Women''s and Gender Studies', 'This course offers an introduction to Women''s and Gender Studies, an interdisciplinary academic field that asks critical questions about the meaning of gender in society.', 'This course offers an introduction to Women''s and Gender Studies, an interdisciplinary academic field that asks critical questions about the meaning of gender in society. The primary goal of this course is to familiarize students with key issues, questions and debates in Women''s and Gender Studies scholarship, both historical and contemporary. Gender scholarship critically analyzes themes of gendered performance and power in a range of social spheres, such as law, culture, work, medicine and the family.', 'http://ocw.mit.edu/courses/special-programs/sp-401-introduction-to-womens-and-gender-studies-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-401-introduction-to-womens-and-gender-studies-fall-2010/sp-401f10.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1997, 'Introduction to Women''s and Gender Studies', 'This course is designed as an introduction to the interdisciplinary field of Women''s and Gender Studies, an academic area of study focused on the ways that sex and gender manifest themselves in social, cultural, and political contexts.', 'This course is designed as an introduction to the interdisciplinary field of Women''s and Gender Studies, an academic area of study focused on the ways that sex and gender manifest themselves in social, cultural, and political contexts. The primary goal of this course is to familiarize students with key issues, questions and debates in Women''s Studies scholarship, both historical and contemporary. This semester you will become acquainted with many of the critical questions and concepts feminist scholars have developed as tools for thinking about gendered experience. In addition, we will study the interconnections among systems of oppression (such as sexism, racism, classism, ethnocentrism, homophobia/heterosexism, transphobia, ableism and others.) In this course you will learn to ''read'' and analyze gender, exploring how it impacts our understanding of the world.', 'http://ocw.mit.edu/courses/special-programs/sp-401-introduction-to-womens-and-gender-studies-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-401-introduction-to-womens-and-gender-studies-spring-2009/sp-401s09.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1998, 'Sexual and Gender Identities', 'This course introduces scholarly debates about sexual identities, gender identities and expressions, and sexual orientation and its representation in film and literature.', 'This course introduces scholarly debates about sexual identities, gender identities and expressions, and sexual orientation and its representation in film and literature. We begin with a contemporary debate about biology and gender identity, considering its relationship to the historical understanding of sex, gender, and sexual identity. Our investigation continues with the theoretical underpinnings of the emerging field of queer studies, from the nineteenth century to the present day, and considers how subsequent work in transgender studies continues to challenge traditional understandings of sex, gender, and sexuality.', 'http://ocw.mit.edu/courses/special-programs/sp-406-sexual-and-gender-identities-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-406-sexual-and-gender-identities-fall-2010/sp-406f06.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(1999, 'Literary Interpretation: Virginia Woolf''s Shakespeare', 'How does one writer use another writer''s work?&nbsp;Does it matter if one author has been dead 300 years? What difference does it make if she''s a groundbreaking twentieth-century feminist and the writer she values has come to epitomize the English literary tradition? How can a novelist borrow from plays and poems? By reading Virginia Woolf''s major novels and essays in juxtaposition with some of the Shakespeare plays that (depending on one''s interpretation) haunt, enrich, and/or shape her writing, we will try to answer these questions and raise others.', 'How does one writer use another writer''s work?&nbsp;Does it matter if one author has been dead 300 years? What difference does it make if she''s a groundbreaking twentieth-century feminist and the writer she values has come to epitomize the English literary tradition? How can a novelist borrow from plays and poems? By reading Virginia Woolf''s major novels and essays in juxtaposition with some of the Shakespeare plays that (depending on one''s interpretation) haunt, enrich, and/or shape her writing, we will try to answer these questions and raise others. Readings in literary criticism, women''s studies, and other literary texts will complement our focus on the relationship--across time, media, and gender--between Shakespeare and Woolf.&nbsp;As a seminar, we will work to become more astute readers of literature within its historical, artistic, and political contexts, and consider how literature both reflects and contributes to these societal frameworks.&nbsp;Central texts will include Shakespeare''s Othello, Antony and Cleopatra, Cymbeline, and The Winter''s Tale, and Woolf''s A Room of One''s Own, Mrs. Dalloway, Orlando, To the Lighthouse, The Waves, and Between the Acts. This subject is an advanced seminar in both the Literature and the Women''s Studies Program.', 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-virginia-woolfs-shakespeare-spring-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-virginia-woolfs-shakespeare-spring-2001/21l-701s01.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2000, 'Gender and Representation of Asian Women', 'This course explores stereotypes associated with Asian women in colonial, nationalist, state-authoritarian, and global/diasporic narratives about gender and power.', 'This course explores stereotypes associated with Asian women in colonial, nationalist, state-authoritarian, and global/diasporic narratives about gender and power. Students will read ethnography, cultural studies, and history, and view films to examine the politics and circumstances that create and perpetuate the representation of Asian women as dragon ladies, lotus blossoms, despotic tyrants, desexualized servants, and docile subordinates. Students are introduced to the debates about Orientalism, gender, and power.', 'http://ocw.mit.edu/courses/anthropology/21a-470j-gender-and-representation-of-asian-women-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-470j-gender-and-representation-of-asian-women-spring-2010/21a-470js10.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2001, 'Identity and Difference', 'This course explores how identities, whether of individuals or groups, are produced, maintained, and transformed.', 'This course explores how identities, whether of individuals or groups, are produced, maintained, and transformed. Students will be introduced to various theoretical perspectives that deal with identity formation, including constructions of &quot;the normal.&quot; We will explore the utility of these perspectives for understanding identity components such as gender, sexual orientation, race, ethnicity, religion, language, social class, and bodily difference. By semester''s end students will understand better how an individual can be at once cause and consequence of society, a unique agent of social action as well as a social product.', 'http://ocw.mit.edu/courses/anthropology/21a-218j-identity-and-difference-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-218j-identity-and-difference-spring-2010/21a-218js10.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2002, 'The Contemporary American Family', 'We begin by considering briefly the evolution of the family, its cross-cultural variability, and its history in the West.', 'We begin by considering briefly the evolution of the family, its cross-cultural variability, and its history in the West. We next examine how the family is currently defined in the U.S., discussing different views about what families should look like.&#160;Class and ethnic variability and the effects of changing gender roles are discussed in this section. We next look at sexuality, traditional and non-traditional marriage, parenting, divorce, family violence, family economics, poverty, and family policy.&#160;Controversial issues dealt with include day care, welfare policy, and the ''Family Values'' debate.', 'http://ocw.mit.edu/courses/anthropology/21a-230j-the-contemporary-american-family-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-230j-the-contemporary-american-family-spring-2004/21a-230js04.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2003, 'Gender, Power, and International Development', 'After decades of efforts to promote development, why is there so much poverty in the world? What are some of the root causes of inequality world-wide and why do poverty, economic transformations and development policies often have different consequences for women and men? This course explores these issues while also examining the history of development itself, its underlying assumptions, and its range of supporters and critics.', 'After decades of efforts to promote development, why is there so much poverty in the world? What are some of the root causes of inequality world-wide and why do poverty, economic transformations and development policies often have different consequences for women and men? This course explores these issues while also examining the history of development itself, its underlying assumptions, and its range of supporters and critics. It considers the various meanings given to development by women and men, primarily as residents of particular regions, but also as aid workers, policy makers and government officials. In considering how development projects and policies are experienced in daily life in urban and rural areas in Africa, Latin America, Asia and Melanesia, this course asks what are the underlying political, economic, social, and gender dynamics that make ''development'' an ongoing problem world-wide.', 'http://ocw.mit.edu/courses/anthropology/21a-338j-gender-power-and-international-development-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-338j-gender-power-and-international-development-fall-2003/21a-338jf03.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2004, 'Women in South Asia from 1800 to Present', 'This course is designed to introduce and help students understand the changes and continuities in the lives of women in South Asia from a historical perspective.', 'This course is designed to introduce and help students understand the changes and continuities in the lives of women in South Asia from a historical perspective. Using gender as a lens of examining the past, we will examine how politics of race, class, caste and religion affected and continue to impact women in South Asian countries, primarily in India, Pakistan, Bangladesh and Sri Lanka. We will reflect upon current debates within South Asian women''s history in order to examine some of the issues and problems that arise in re-writing the past from a gendered perspective and these are found in primary documents, secondary readings, films, newspaper articles, and the Internet.', 'http://ocw.mit.edu/courses/history/21h-575j-women-in-south-asia-from-1800-to-present-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-575j-women-in-south-asia-from-1800-to-present-fall-2006/21h-575jf06.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2005, 'Cultures of Computing', 'This course examines computers anthropologically, as artifacts revealing the social orders and cultural practices that create them.', 'This course examines computers anthropologically, as artifacts revealing the social orders and cultural practices that create them. Students read classic texts in computer science along with cultural analyses of computing history and contemporary configurations. It explores the history of automata, automation and capitalist manufacturing; cybernetics and WWII operations research; artificial intelligence and gendered subjectivity; robots, cyborgs, and artificial life; creation and commoditization of the personal computer; the growth of the Internet as a military, academic, and commercial project; hackers and gamers; technobodies and virtual sociality. Emphasis is placed on how ideas about gender and other social differences shape labor practices, models of cognition, hacking culture, and social media.', 'http://ocw.mit.edu/courses/anthropology/21a-350j-cultures-of-computing-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-350j-cultures-of-computing-fall-2011/21a-350jf11.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2006, 'Popular Narrative: Masterminds', 'Our purpose is to consider some of the most elaborate and thoughtful efforts to define and delineate ''all-mastering,'' and to consider some of the delineations of ''all-mastering the intellect'' in various guises - from magicians to master spies to detectives to scientists (mad and otherwise).', 'Our purpose is to consider some of the most elaborate and thoughtful efforts to define and delineate ''all-mastering,'' and to consider some of the delineations of ''all-mastering the intellect'' in various guises - from magicians to master spies to detectives to scientists (mad and otherwise). The major written work of the term will be an ongoing reading journal, which you will circulate to your classmates using an e-mail mailing list. The use of that list is fundamental - it is my intention to generate a sort of ongoing cyberconversation.', 'http://ocw.mit.edu/courses/literature/21l-430-popular-narrative-masterminds-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-430-popular-narrative-masterminds-fall-2004/21l-430f04.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2007, 'Literary Interpretation: Literature and Urban Experience', 'Alienation, overcrowding, sensory overload, homelessness, criminality, violence, loneliness, sprawl, blight.', 'Alienation, overcrowding, sensory overload, homelessness, criminality, violence, loneliness, sprawl, blight. How have the realities of city living influenced literature''s formal and thematic techniques? How useful is it to think of literature as its own kind of ''map'' of urban space? Are cities too grand, heterogeneous, and shifting to be captured by writers? In this seminar we will seek answers to these questions in key city literature, and in theoretical works that endeavor to understand the culture of cities.', 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-literature-and-urban-experience-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-literature-and-urban-experience-spring-2009/21l-701s09.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2008, 'Major Authors: Melville and Morrison', 'This seminar provides intensive study of texts by two American authors (Herman Melville, 1819-1891, and Toni Morrison, 1931-) who, using lyrical, radically innovative prose, explore in different ways epic notions of American identity.', 'This seminar provides intensive study of texts by two American authors (Herman Melville, 1819-1891, and Toni Morrison, 1931-) who, using lyrical, radically innovative prose, explore in different ways epic notions of American identity. Focusing on Melville''s Typee (1846), Moby-Dick (1851), and The Confidence-Man (1857) and Morrison''s Sula (1973), Beloved (1987), Jazz (1992), and Paradise (1998), the class will address their common concerns with issues of gender, race, language, and nationhood. Be prepared to read deeply (i.e. a small number of texts with considerable care), to draw on a variety of sources in different media, and to employ them in creative research, writing, and multimedia projects.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-melville-and-morrison-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-melville-and-morrison-fall-2003/21l-705f03.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2009, 'Medieval Literature: Medieval Women Writers', 'This survey provides a general introduction to medieval European literature (from Late Antiquity to the Fifteenth Century) from the perspective of women writers from a variety of cultures, social backgrounds, and historical timeperiods.', 'This survey provides a general introduction to medieval European literature (from Late Antiquity to the Fifteenth Century) from the perspective of women writers from a variety of cultures, social backgrounds, and historical timeperiods.&#160;Though much of the class will be devoted to exploring the evolution of a new literary tradition by and for women from its earliest emergence in the West, wider historical and cultural movements will also be addressed: the Fall of the Roman Empire, the growth of religious communities, the shift from orality to literacy, the culture of chivalry and courtly love, the emergence of scholasticism and universities, changes in devotional practices, the persecution of heretics, the rise of nationalism and class consciousness.&#160;Authors will include some of the most famous women of the period: Hildegard of Bingen, Heloise of Paris, Marie de France, Christine de Pizan, Joan of Arc, Margery Kempe, along with many interesting and intriguing though lesser known figures.', 'http://ocw.mit.edu/courses/literature/21l-460-medieval-literature-medieval-women-writers-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-460-medieval-literature-medieval-women-writers-spring-2004/21l-460s04.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2010, 'American Authors: American Women Authors', 'This subject, cross-listed in Literature and Women''s Studies, examines a range of American women authors from the seventeenth century to the present.', 'This subject, cross-listed in Literature and Women''s Studies, examines a range of American women authors from the seventeenth century to the present.&nbsp;It aims to introduce a number of literary genres and styles- the captivity narrative, slave novel, sensational, sentimental, realistic, and postmodern fiction- and also to address significant historical events in American women''s history: Puritanism, the American Revolution, industrialization and urbanization in the nineteenth century, the Harlem Renaissance, World War II, the 60s civil rights movements.&nbsp;A primary focus will be themes studied and understood through the lens of gender: war, violence, and sexual exploitation (Keller, Rowlandson, Rowson); the relationship between women and religion (Rowlandson, Rowson, Stowe); labor, poverty, and working conditions for women (Fern, Davis, Wharton); captivity and slavery (Rowlandson, Jacobs); class struggle (Fern, Davis, Wharton, Larsen); race and identity (Keller, Jacobs, Larsen, Morrison); feminist revisions of history (Stowe, Morrison, Keller); and the myth of the fallen woman (take your pick).&nbsp;Essays and in-class reports will focus more particularly on specific writers and themes and will stress the skills of close reading, annotation, research, and uses of multimedia where appropriate.', 'http://ocw.mit.edu/courses/literature/21l-512-american-authors-american-women-authors-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-512-american-authors-american-women-authors-spring-2003/21l-512s03.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2011, 'Writing About Race: Narratives of Multiraciality', 'In this course we will read essays, novels, memoirs, and graphic texts, and view documentary and experimental films and videos which explore race from the standpoint of the multiracial.', 'In this course we will read essays, novels, memoirs, and graphic texts, and view documentary and experimental films and videos which explore race from the standpoint of the multiracial. Examining the varied work of multiracial authors and filmmakers such as Danzy Senna, Ruth Ozeki, Kip Fulbeck, James McBride and others, we will focus not on how multiracial people are seen or imagined by the dominant culture, but instead on how they represent themselves. How do these authors approach issues of family, community, nation, language and history? What can their work tell us about the complex interconnections between race, gender, class, sexuality, and citizenship? Is there a relationship between their experiences of multiraciality and a willingness to experiment with form and genre? In addressing these and other questions, we will endeavor to think and write more critically and creatively about race as a social category and a lived experience.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-narratives-of-multiraciality-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-narratives-of-multiraciality-fall-2008/21w-742jf08.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2012, 'Writing About Race', 'In The Souls of Black Folk (1903), the great cultural critic W.', 'In The Souls of Black Folk (1903), the great cultural critic W. E. B. Du Bois wrote that ''...the problem of the Twentieth Century is the problem of the color line.'' A century after Du Bois penned those words, most Americans would agree that at the beginning of the twenty-first century, the color line remains one of our most pressing social issues. In this course, we will explore the terrain of race in America by reading the works of writers of color and others concerned with the issue of race, by viewing films that address racial issues, and by writing to explore how the fictions and facts of race condition all our lives, social and civic, private and public. We will consider the complex question of racial identity, test the givens of history by uncovering histories that have been more elusive or more thoroughly suppressed, and explore how writing and reading can both reflect and challenge racial categories, hierarchies, and perceptions. We will read the work of such writers as Suzan-Lori Parks, Toni Morrison, Sandra Cisneros, James Baldwin, Louise Erdrich, Amy Tan, Chang-Rae Lee, Jhumpa Lahiri, and William Faulkner, among others, as we consider the story of race in its peculiarly American dimensions. We will also view films such as Skins, The Long Walk Home, and Crash. Both the reading and the writing of members of the class will be the focus of class discussion and workshops. Students explore race and ethnicity in personal essays, pieces of cultural criticism or analysis, or (with permission of instructor) fiction.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-spring-2007/21w-742js07.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2013, 'Advanced Essay Workshop', 'This course is a workshop for students with some experience in writing essays, nonfiction prose.', 'This course is a workshop for students with some experience in writing essays, nonfiction prose. Our focus will be negotiating and representing identities grounded in gender, race, class, nationality, sexuality, and other categories of identity, either our own or others'', in prose that is expository, exploratory, investigative, persuasive, lyrical, or incantatory. We will read nonfiction prose works by a wide array of writers who have used language to negotiate and represent aspects of identity and the ways the different determinants of identity intersect, compete, and cooperate.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-745-advanced-essay-workshop-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-745-advanced-essay-workshop-spring-2008/21w-745s08.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2014, 'Gender and the Law in U.S. History', 'This subject explores the legal history of the United States as a gendered system.', 'This subject explores the legal history of the United States as a gendered system. It examines how women have shaped the meanings of American citizenship through pursuit of political rights such as suffrage, jury duty, and military service, how those political struggles have varied for across race, religion, and class, as well as how the legal system has shaped gender relations for both women and men through regulation of such issues as marriage, divorce, work, reproduction, and the family. The course readings will draw from primary and secondary materials in American history, as well as some court cases. However, the focus of the class is on the broader relationship between law and society, and no technical legal knowledge is required or assumed.', 'http://ocw.mit.edu/courses/history/21h-225j-gender-and-the-law-in-u-s-history-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-225j-gender-and-the-law-in-u-s-history-spring-2004/21h-225js04.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2015, 'Cultural Performances of Asia', 'This course examines cultural performances of Asia, including both traditional and contemporary forms, in a variety of genres.', 'This course examines cultural performances of Asia, including both traditional and contemporary forms, in a variety of genres. Students will explore the communicative power of performances with attention to the ways performers, media, cultural settings, and audiences interact. The representation of cultural difference is considered and how it is altered through processes of globalization. Performances are viewed live when possible, but the course also relies on video, audio, and online materials as necessary. There are no prerequisites for this course and it is taught in English.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-067j-cultural-performances-of-asia-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-067j-cultural-performances-of-asia-fall-2005/21f-067jf05.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2016, 'The Economic History of Work and Family', 'This course will explore the relation of women and men in both pre-industrial and modern societies to the changing map of public and private (household) work spaces, examining how that map affected their opportunities for both productive activity and the consumption of goods and leisure.', 'This course will explore the relation of women and men in both pre-industrial and modern societies to the changing map of public and private (household) work spaces, examining how that map affected their opportunities for both productive activity and the consumption of goods and leisure. The reproductive strategies of women, either in conjunction with or in opposition to their families, will be the third major theme of the course. We will consider how a place and an ideal of the &quot;domestic&quot; arose in the early modern west, to what extent it was effective in limiting the economic position of women, and how it has been challenged, and with what success, in the post-industrial period. Finally, we will consider some of the policy implications for contemporary societies as they respond to changes in the composition of the paid work force, as well as to radical changes in their national demographic profiles. Although most of the material for the course will focus on western Europe since the Middle Ages and on the United States, we will also consider how these issues have played themselves out in non-western cultures.', 'http://ocw.mit.edu/courses/history/21h-927j-the-economic-history-of-work-and-family-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-927j-the-economic-history-of-work-and-family-spring-2005/21h-927js05.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2017, 'Violence, Human Rights, and Justice', 'This course examines the contemporary problem of political violence and the way that human rights have been conceived as a means to protect and promote freedom, peace and justice for citizens against the abuses of the state.', 'This course examines the contemporary problem of political violence and the way that human rights have been conceived as a means to protect and promote freedom, peace and justice for citizens against the abuses of the state.', 'http://ocw.mit.edu/courses/anthropology/21a-225j-violence-human-rights-and-justice-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-225j-violence-human-rights-and-justice-fall-2004/21a-225jf04.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2018, 'Dilemmas in Bio-Medical Ethics: Playing God or Doing Good?', 'This course is an introduction to the cross-cultural study of bio-medical ethics.', 'This course is an introduction to the cross-cultural study of bio-medical ethics. It examines moral foundations of the science and practice of western bio-medicine through case studies of abortion, contraception, cloning, organ transplantation, and other issues. It also evaluates challenges that new medical technologies pose to the practice and availability of medical services around the globe, and to cross-cultural ideas of kinship and personhood. It discusses critiques of the bio-medical tradition from anthropological, feminist, legal, religious, and cross-cultural theorists.', 'http://ocw.mit.edu/courses/anthropology/21a-216j-dilemmas-in-bio-medical-ethics-playing-god-or-doing-good-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-216j-dilemmas-in-bio-medical-ethics-playing-god-or-doing-good-spring-2005/21a-216js05.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2019, 'Studies in Women''s Life Narratives: Feminist Inquiry', 'Feminist Inquiry starts with questions: What is feminism? What is feminist scholarship? Is feminist scholarship inherently interdisciplinary? Must feminist work interrogate disciplinarity? Must feminists collaborate? Our aim is to promote the development of feminist theory and methods by providing a forum for sharing, assessing, discussing and debating strategies used by feminist scholars to study topics such as gender and the body; sexualities; color and whiteness; migration, colonialism, and indigeneity.', 'Feminist Inquiry starts with questions: What is feminism? What is feminist scholarship? Is feminist scholarship inherently interdisciplinary? Must feminist work interrogate disciplinarity? Must feminists collaborate? Our aim is to promote the development of feminist theory and methods by providing a forum for sharing, assessing, discussing and debating strategies used by feminist scholars to study topics such as gender and the body; sexualities; color and whiteness; migration, colonialism, and indigeneity.', 'http://ocw.mit.edu/courses/special-programs/sp-691-studies-in-womens-life-narratives-feminist-inquiry-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-691-studies-in-womens-life-narratives-feminist-inquiry-spring-2009/sp-691s09.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2020, 'Issues of Representation: Women, Representation, and Music in Selected Folk Traditions of the British Isles and North America', 'This subject investigates the special relation of women to several musical folk traditions in the British Isles and North America.', 'This subject investigates the special relation of women to several musical folk traditions in the British Isles and North America. Throughout, we will be examining the implications of gender in the creation, transmission, and performance of music. Because virtually all societies operate to some extent on a gendered division of labor (and of expressive roles) the music of these societies is marked by the gendering of musical repertoires, traditions of instrumentation, performance settings, and styles. This seminar will examine the gendered dimensions of the music - the song texts, the performance styles, processes of dissemination (collection, literary representation) and issues of historiography - with respect to selected traditions within the folk musics of North America and the British Isles, with the aim of analyzing the special contributions of women to these traditions. In addition to telling stories about women''s musical lives, and studying elements of female identity and subjectivity in song texts and music, we will investigate the ways in which women''s work and women''s cultural roles have affected the folk traditions of these several countries.', 'http://ocw.mit.edu/courses/special-programs/sp-694-issues-of-representation-women-representation-and-music-in-selected-folk-traditions-of-the-british-isles-and-north-america-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-694-issues-of-representation-women-representation-and-music-in-selected-folk-traditions-of-the-british-isles-and-north-america-fall-2005/sp-694f05.jpg', 'Women''s and Gender Studies', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2021, 'Fundamentals of Biology', 'Fundamentals of Biology focuses on the basic principles of biochemistry, molecular biology, genetics, and recombinant DNA.', 'Fundamentals of Biology focuses on the basic principles of biochemistry, molecular biology, genetics, and recombinant DNA. These principles are necessary to understanding the basic mechanisms of life and anchor the biological knowledge that is required to understand many of the challenges in everyday life, from human health and disease to loss of biodiversity and environmental quality.', 'http://ocw.mit.edu/courses/biology/7-01sc-fundamentals-of-biology-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-01sc-fundamentals-of-biology-fall-2011/7-01scf11.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2022, 'Introduction to Biology', 'The MIT Biology Department core courses, 7.', 'The MIT Biology Department core courses, 7.012, 7.013, and 7.014, all cover the same core material, which includes the fundamental principles of biochemistry, genetics, molecular biology, and cell biology. Biological function at the molecular level is particularly emphasized and covers the structure and regulation of genes, as well as, the structure and synthesis of proteins, how these molecules are integrated into cells, and how these cells are integrated into multicellular systems and organisms. In addition, each version of the subject has its own distinctive material.7.012 focuses on the exploration of current research in cell biology, immunology, neurobiology, genomics, and molecular medicine.AcknowledgmentsThe study materials, problem sets, and quiz materials used during Fall 2004 for 7.012 include contributions from past instructors, teaching assistants, and other members of the MIT Biology Department affiliated with course #7.012. Since the following works have evolved over a period of many years, no single source can be attributed.', 'http://ocw.mit.edu/courses/biology/7-012-introduction-to-biology-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-012-introduction-to-biology-fall-2004/7-012f04.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2023, 'Introductory Biology', 'The MIT Biology Department core courses, 7.', 'The MIT Biology Department core courses, 7.012, 7.013, and 7.014, all cover the same core material, which includes the fundamental principles of biochemistry, genetics, molecular biology, and cell biology. Biological function at the molecular level is particularly emphasized and covers the structure and regulation of genes, as well as, the structure and synthesis of proteins, how these molecules are integrated into cells, and how these cells are integrated into multicellular systems and organisms. In addition, each version of the subject has its own distinctive material. 7.013 focuses on the application of the fundamental principles toward an understanding of human biology. Topics include genetics, cell biology, molecular biology, disease (infectious agents, inherited diseases and cancer), developmental biology, neurobiology and evolution. Acknowledgments The study materials, problem sets, and quiz materials used during Spring 2005 for 7.013 include contributions from past instructors, teaching assistants, and other members of the MIT Biology Department affiliated with course 7.013. Since the following works have evolved over a period of many years, no single source can be attributed.', 'http://ocw.mit.edu/courses/biology/7-013-introductory-biology-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-013-introductory-biology-spring-2006/7-013s06.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2024, 'Experimental Biology & Communication', 'This introductory biology laboratory course covers the application of experimental techniques in microbiology, biochemistry, cell and developmental biology.', 'This introductory biology laboratory course covers the application of experimental techniques in microbiology, biochemistry, cell and developmental biology. Emphasis is placed on the integration of factual knowledge with understanding of the design of the experiments and data analysis in order to prepare the students for future research projects. Development of skills critical for writing about scientific findings in modern biology is also covered in the Scientific Communications portion of the curriculum, 7.02CI. Additional Faculty Dr. Katherine Bacon Schneider Dr. Jean-Francois Hamel Ms. Deborah Kruzel Dr. Megan Rokop', 'http://ocw.mit.edu/courses/biology/7-02-experimental-biology-communication-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-02-experimental-biology-communication-spring-2005/7-02s05.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2025, 'Experimental Biology - Communications Intensive', 'This course is the scientific communications portion of course 7.', 'This course is the scientific communications portion of course 7.02, Experimental Biology and Communication. Students develop their skills as writers of scientific research, skills that also contribute to the learning of the 7.02 course materials. Through in class and out of class writing exercises, students explore the genre of the research article and its components while developing an understanding of the materials covered in the 7.02 laboratory.', 'http://ocw.mit.edu/courses/biology/7-02ci-experimental-biology-communications-intensive-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-02ci-experimental-biology-communications-intensive-spring-2005/7-02cis05.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2026, 'Genetics', 'This course discusses the principles of genetics with application to the study of biological function at the level of molecules, cells, and multicellular organisms, including humans.', 'This course discusses the principles of genetics with application to the study of biological function at the level of molecules, cells, and multicellular organisms, including humans. The topics include: structure and function of genes, chromosomes and genomes, biological variation resulting from recombination, mutation, and selection, population genetics, use of genetic methods to analyze protein function, gene regulation and inherited disease.', 'http://ocw.mit.edu/courses/biology/7-03-genetics-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-03-genetics-fall-2004/7-03f04.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2027, 'Biological Chemistry II', 'This course deals with a more advanced treatment of the biochemical mechanisms that underlie biological processes.', 'This course deals with a more advanced treatment of the biochemical mechanisms that underlie biological processes. Emphasis will be given&nbsp;to the experimental methods used to unravel how these processes fit into the cellular context as well as the coordinated regulation of these processes. Topics include macromolecular machines for energy and force transduction, regulation of biosynthetic and degradative pathways, and the structure and function of nucleic acids.', 'http://ocw.mit.edu/courses/chemistry/5-08j-biological-chemistry-ii-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/chemistry/5-08j-biological-chemistry-ii-spring-2004/5-08js04.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2028, 'Experimental Microbial Genetics', 'In this class, students engage in independent research projects to probe various aspects of the physiology of the bacterium&nbsp;Pseudomonas aeruginosa PA14, an opportunistic pathogen isolated from the lungs of cystic fibrosis patients.', 'In this class, students engage in independent research projects to probe various aspects of the physiology of the bacterium&nbsp;Pseudomonas aeruginosa PA14, an opportunistic pathogen isolated from the lungs of cystic fibrosis patients. Students use molecular genetics to examine survival in stationary phase, antibiotic resistance, phase variation, toxin production, and secondary metabolite production. Projects aim to discover the molecular basis for these processes using both classical and cutting-edge techniques. These include plasmid manipulation, genetic complementation, mutagenesis, PCR, DNA sequencing, enzyme assays, and gene expression studies. Instruction and practice in written and oral communication are also emphasized. WARNING NOTICE The experiments described in these materials are potentially hazardous and require a high level of safety training, special facilities and equipment, and supervision by appropriate individuals. You bear the sole responsibility, liability, and risk for the implementation of such safety procedures and measures. MIT shall have no responsibility, liability, or risk for the content or implementation of any of the material presented. Legal Notice', 'http://ocw.mit.edu/courses/biology/7-13-experimental-microbial-genetics-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-13-experimental-microbial-genetics-fall-2008/7-13f08.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2029, 'Experimental Microbial Genetics', 'Also referred to as the Microbial Genetics Project Lab, this is a hands-on research course designed to introduce the student to the strategies and challenges associated with microbiology research.', 'Also referred to as the Microbial Genetics Project Lab, this is a hands-on research course designed to introduce the student to the strategies and challenges associated with microbiology research. Students take on independent and original research projects that are designed to be instructive with the goal of advancing a specific field of research in microbiology.', 'http://ocw.mit.edu/courses/biology/7-13-experimental-microbial-genetics-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-13-experimental-microbial-genetics-fall-2003/7-13f03.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2030, 'Topics in Experimental Biology', 'This independent experimental study course is designed to allow students with a strong interest in independent research to fulfill the project laboratory requirement for the Biology Department Program in the context of a research laboratory at MIT.', 'This independent experimental study course is designed to allow students with a strong interest in independent research to fulfill the project laboratory requirement for the Biology Department Program in the context of a research laboratory at MIT. The research should be a continuation of a previous project under the direction of a member of the Biology Department faculty. This course provides instruction and practice in written and oral communication. Journal club discussions are used to help students evaluate and write scientific papers.', 'http://ocw.mit.edu/courses/biology/7-18-topics-in-experimental-biology-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-18-topics-in-experimental-biology-fall-2005/7-18f05.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2031, 'Developmental Biology', 'This graduate and advanced undergraduate level lecture and literature discussion course covers the current understanding of the molecular mechanisms that regulate animal development.', 'This graduate and advanced undergraduate level lecture and literature discussion course covers the current understanding of the molecular mechanisms that regulate animal development. Evolutionary mechanisms are emphasized as well as the discussion of relevant diseases. Vertebrate (mouse, chick, frog, fish) and invertebrate (fly, worm) models are covered. Specific topics include formation of early body plan, cell type determination, organogenesis, morphogenesis, stem cells, cloning, and issues in human development.', 'http://ocw.mit.edu/courses/biology/7-22-developmental-biology-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-22-developmental-biology-fall-2005/7-22f05.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2032, 'Principles of Human Disease', 'This course covers current understanding of, and modern approaches to human disease, emphasizing the molecular and cellular basis of both genetic disease and cancer.', 'This course covers current understanding of, and modern approaches to human disease, emphasizing the molecular and cellular basis of both genetic disease and cancer. Topics include: The Genetics of Simple and Complex Traits; Karyotypic Analysis and Positional Cloning; Genetic Diagnosis; The Roles of Oncogenes and Tumor Suppressors in Tumor Initiation, Progression, and Treatment; The Interaction between Genetics and Environment; Animal Models of Human Disease; Cancer; and Conventional and Gene Therapy Treatment Strategies.', 'http://ocw.mit.edu/courses/biology/7-27-principles-of-human-disease-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-27-principles-of-human-disease-spring-2006/7-27s06.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2033, 'Cellular Neurobiology', 'This course serves as an introduction to the structure and function of the nervous system.', 'This course serves as an introduction to the structure and function of the nervous system. Emphasis is placed on the cellular properties of neurons and other excitable cells. Topics covered include the structure and biophysical properties of excitable cells, synaptic transmission, neurochemistry, neurodevelopment, and the integration of information in simple systems and the visual system.', 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-09j-cellular-neurobiology-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-09j-cellular-neurobiology-spring-2005/9-09js05.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2034, 'Ecology I: The Earth System', 'We will cover fundamentals of ecology, considering Earth as an integrated dynamic system.', 'We will cover fundamentals of ecology, considering Earth as an integrated dynamic system. Topics include coevolution of the biosphere, geosphere, atmosphere and oceans; photosynthesis and respiration; the hydrologic, carbon and nitrogen cycles. We will examine the flow of energy and materials through ecosystems; regulation of the distribution and abundance of organisms; structure and function of ecosystems, including evolution and natural selection; metabolic diversity; productivity; trophic dynamics; models of population growth, competition, mutualism and predation. This course is designated as Communication-Intensive; instruction and practice in oral and written communication provided. Biology is a recommended prerequisite.', 'http://ocw.mit.edu/courses/civil-and-environmental-engineering/1-018j-ecology-i-the-earth-system-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/civil-and-environmental-engineering/1-018j-ecology-i-the-earth-system-fall-2009/1-018jf09.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2035, 'Regenerative Medicine: from Bench to Bedside', 'Regenerative medicine involves the repair and regeneration of tissues for therapeutic purposes, such as replacing bone marrow in leukemia, cartilage in osteoarthritis or cells of the heart after a heart attack.', 'Regenerative medicine involves the repair and regeneration of tissues for therapeutic purposes, such as replacing bone marrow in leukemia, cartilage in osteoarthritis or cells of the heart after a heart attack. In this course, we will explore basic mechanisms of how cells differentiate into specific tissues in response to a variety of biologic signaling molecules. We will discuss the use of such factors for in vitro tissue production. We will also study the cellular mechanisms involved in the cloning of animals and how Scottish researchers produced the sheep Dolly using the nucleus of a mammary gland cell from an adult sheep. We will read papers describing organ production, such as the in vitro formation of beating heart cells. We will also consider the molecular bases of cellular tissue remodeling to correct these changes. We will discuss how studies of the developmental, cellular and molecular biology of regeneration have led to the discovery of new drugs. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-340-regenerative-medicine-from-bench-to-bedside-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-340-regenerative-medicine-from-bench-to-bedside-spring-2010/7-340s10.png', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2036, 'Under the Radar Screen: How Bugs Trick Our Immune Defenses', 'In this course, we will explore the specific ways by which microbes defeat our immune system and the molecular mechanisms that are under attack (phagocytosis, the ubiquitin/proteasome pathway, MHC I/II antigen presentation).', 'In this course, we will explore the specific ways by which microbes defeat our immune system and the molecular mechanisms that are under attack (phagocytosis, the ubiquitin/proteasome pathway, MHC I/II antigen presentation). Through our discussion and dissection of the primary research literature, we will explore aspects of host-pathogen interactions. We will particularly emphasize the experimental techniques used in the field and how to read and understand research data. Technological advances in the fight against microbes will also be discussed, with specific examples. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-340-under-the-radar-screen-how-bugs-trick-our-immune-defenses-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-340-under-the-radar-screen-how-bugs-trick-our-immune-defenses-spring-2007/7-340s07.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2037, 'Avoiding Genomic Instability: DNA Replication, the Cell Cycle, and Cancer', 'In this class we will learn about how the process of DNA replication is regulated throughout the cell cycle and what happens when DNA replication goes awry.', 'In this class we will learn about how the process of DNA replication is regulated throughout the cell cycle and what happens when DNA replication goes awry. How does the cell know when and where to begin replicating its DNA? How does a cell prevent its DNA from being replicated more than once? How does damaged DNA cause the cell to arrest DNA replication until that damage has been repaired? And how is the duplication of the genome coordinated with other essential processes? We will examine both classical and current papers from the scientific literature to provide answers to these questions and to gain insights into how biologists have approached such problems. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-340-avoiding-genomic-instability-dna-replication-the-cell-cycle-and-cancer-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-340-avoiding-genomic-instability-dna-replication-the-cell-cycle-and-cancer-fall-2006/7-340f06.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2038, 'Nano-life: An Introduction to Virus Structure and Assembly', 'Watson and Crick noted that the size of a viral genome was insufficient to encode a protein large enough to encapsidate it and reasoned, therefore that a virus shell must be composed of multiple, but identical subunits.', 'Watson and Crick noted that the size of a viral genome was insufficient to encode a protein large enough to encapsidate it and reasoned, therefore that a virus shell must be composed of multiple, but identical subunits. Today, high resolution structures of virus capsids reveal the basis of this genetic economy as a highly symmetrical structure, much like a geodesic dome composed of protein subunits. Crystallographic structures and cryo-electron microscopy reconstructions combined with molecular data are beginning to reveal how these nano-structures are built. Topics covered in the course will include basic principles of virus structure and symmetry, capsid assembly, strategies for enclosing nucleic acid, proteins involved in entry and exit, and the life cycles of well understood pathogens such as HIV, influenza, polio, and Herpes. A review of cutting edge structural methods is also covered. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting.', 'http://ocw.mit.edu/courses/biology/7-340-nano-life-an-introduction-to-virus-structure-and-assembly-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-340-nano-life-an-introduction-to-virus-structure-and-assembly-fall-2005/7-340f05.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2039, 'Ubiquitination: The Proteasome and Human Disease', 'This course is one of many&nbsp;Advanced Undergraduate Seminars offered by the Biology Department at MIT.', 'This course is one of many&nbsp;Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. This seminar provides a deeper understanding of the post-translational mechanisms evolved by eukaryotic cells to target proteins for degradation. Students learn how proteins are recognized and degraded by specific machinery (the proteasome) through their previous tagging with another small protein, ubiquitin. Additional topics include principles of ubiquitin-proteasome function, its control of the most important cellular pathways, and the implication of this system in different human diseases. Finally, speculation on the novel techniques that arose from an increased knowledge of the ubiquitin-proteosome system and current applications in the design of new pharmacological agents to battle disease is also covered.', 'http://ocw.mit.edu/courses/biology/7-340-ubiquitination-the-proteasome-and-human-disease-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-340-ubiquitination-the-proteasome-and-human-disease-fall-2004/7-340f04.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2040, 'Harnessing the Biosphere: Natural Products and Biotechnology', 'What do the organisms of the biosphere, specifically microorganisms, have to offer to biotechnological endeavors? In this course we will focus on the production of biomolecules using microbial systems.', 'What do the organisms of the biosphere, specifically microorganisms, have to offer to biotechnological endeavors? In this course we will focus on the production of biomolecules using microbial systems. We will discuss potential growth substrates (such as agricultural waste and carbon dioxide) that can be used and learn about both established and cutting-edge manipulation techniques in the field of synthetic biology. We will also cover the production of biofuels, bioplastics, amino acids (e.g. lysine), food additives (e.g. monosodium glutamate, MSG), specialty chemicals (e.g. succinate), and biopharmaceuticals (e.g. plasmids for gene therapy). This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-341-harnessing-the-biosphere-natural-products-and-biotechnology-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-341-harnessing-the-biosphere-natural-products-and-biotechnology-fall-2012/7-341f12.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2041, 'Bench to Bedside: Molecularly Targeted Therapies in Blood Disorders and Malignancy', 'Where do new drugs and treatments come from? This class will take you from the test tubes and mice of the laboratory to the treatment of patients with deadly blood disorders.', 'Where do new drugs and treatments come from? This class will take you from the test tubes and mice of the laboratory to the treatment of patients with deadly blood disorders. Students will learn how to think as a scientist through discussion of primary research papers describing the discoveries of several novel treatments. Topics such as gene therapy, the potential of drugs based on RNA interference and the reprogramming of somatic cells into stem cells for regenerative medicine will be discussed. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-341-bench-to-bedside-molecularly-targeted-therapies-in-blood-disorders-and-malignancy-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-341-bench-to-bedside-molecularly-targeted-therapies-in-blood-disorders-and-malignancy-fall-2009/7-341f09.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2042, 'Brightening up Life: Harnessing the Power of Fluorescence Imaging to Observe Biology in Action', 'One summer in the 1960s a young Japanese researcher, with the help of a few high school students, chopped up ten thousand jellyfish.', 'One summer in the 1960s a young Japanese researcher, with the help of a few high school students, chopped up ten thousand jellyfish. As a by-product of this harvest, they isolated a green fluorescent protein (GFP). Since then, GFP has triggered a revolution in our understanding of gene expression and signaling in live cells. In this seminar, we will examine how this small protein generates fluorescence, i.e. absorbs light of one wavelength and emits light of a longer wavelength. We will discuss how the color palette has been extended from green to blue, red and many other colors, based on protein engineering of GFP and the study of vividly colorful coral reefs. We will then investigate how these fluorescent proteins can be used to track the motion of DNA, RNA and protein in living cells, as well as to see waves of signaling molecules propagate across a cell. GFP is also a powerful tool for fluorescent imaging of whole organisms, from worms to mice, and we will see how it has been used in tracking the spread of cancer cells, controlling malaria and in understanding how neuronal connections form. In this seminar, we will explore this wonderful protein as well as other important methods and reagents for fluorescent imaging. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-341-brightening-up-life-harnessing-the-power-of-fluorescence-imaging-to-observe-biology-in-action-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-341-brightening-up-life-harnessing-the-power-of-fluorescence-imaging-to-observe-biology-in-action-fall-2006/7-341f06.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2043, 'To Divide or Not To Divide: Control of Cell Cycle and Growth by Extracellular Cues', 'Cells, regardless of whether they are in an organ in the human body or a component of a bacterial colony, can sense the chemical composition of the environment, the presence of neighboring cells, and even the types of their neighboring cells.', 'Cells, regardless of whether they are in an organ in the human body or a component of a bacterial colony, can sense the chemical composition of the environment, the presence of neighboring cells, and even the types of their neighboring cells. Depending on the identity of a cell and the information it receives from its environment, it can grow (increase in size), proliferate (make more cells), become quiescent (stop growing and dividing), differentiate (make different types of cells), or die. How cells achieve the astonishing feat of appropriately sensing and responding to their environment has been a major question in biology. In this course we will read and critically discuss the primary scientific literature with the goal of highlighting the basic principles of cell growth, adaptation, and differentiation. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-342-to-divide-or-not-to-divide-control-of-cell-cycle-and-growth-by-extracellular-cues-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-342-to-divide-or-not-to-divide-control-of-cell-cycle-and-growth-by-extracellular-cues-fall-2012/7-342f12.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2044, 'Powerhouse Rules: The Role of Mitochondria in Human Diseases', 'The primary role of mitochondria is to produce 90% of a cell''s energy in the form of ATP through a process called oxidative phosphorylation.', 'The primary role of mitochondria is to produce 90% of a cell''s energy in the form of ATP through a process called oxidative phosphorylation. A variety of clinical disorders have been shown to include &quot;mitochondrial dysfunction,&quot; which loosely refers to defective oxidative phosphorylation and usually coincides with the occurrence of excess Reactive Oxygen Species (ROS) production, placing cells under oxidative stress. A known cause and effect of oxidative stress is damage to and mutation of mitochondrial DNA. We will use this class to explore issues relating to mitochondrial DNA integrity and how it can be damaged, repaired, mutated, and compromised in human diseases. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-342-powerhouse-rules-the-role-of-mitochondria-in-human-diseases-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-342-powerhouse-rules-the-role-of-mitochondria-in-human-diseases-spring-2011/7-342s11.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2045, 'Systems and Synthetic Biology: How the Cell Solves Problems', 'A millennial challenge in biology is to decipher how vast arrays of molecular interactions inside the cell work in concert to produce a cellular function.', 'A millennial challenge in biology is to decipher how vast arrays of molecular interactions inside the cell work in concert to produce a cellular function. Systems biology, a new interdisciplinary field of science, brings together biologists and physicists to tackle this grand challenge through quantitative experiments and models. In this course, we will discuss the unifying principles that all organisms use to perform cellular functions. We will also discuss key challenges faced by a cell in both single and multi-cellular organisms. Finally, we will discuss how researchers in the field of synthetic biology are using the new knowledge gained from studying naturally-occurring biological systems to create artificial gene networks capable of performing new functions. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-342-systems-and-synthetic-biology-how-the-cell-solves-problems-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-342-systems-and-synthetic-biology-how-the-cell-solves-problems-fall-2010/7-342f10.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2046, 'The X in Sex: A Genetic, Medical, and Evolutionary View of the X Chromosome', 'This course will explore a diverse collection of striking biological phenomena associated with the X chromosome.', 'This course will explore a diverse collection of striking biological phenomena associated with the X chromosome. We will examine the genetic basis and significance of several X-linked mutations. We will also discuss why men are more likely than women to display X-linked traits. We will look at the different mechanisms by which X chromosome gene expression is equalized in mammals, flies, and worms and how these mechanisms can yield unusual phenotypes. Throughout our discussions of the X chromosome we will use both recent and classic primary research papers to learn about this chromosome''s fascinating biology. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-342-the-x-in-sex-a-genetic-medical-and-evolutionary-view-of-the-x-chromosome-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-342-the-x-in-sex-a-genetic-medical-and-evolutionary-view-of-the-x-chromosome-fall-2009/7-342f09.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2047, 'Developmental and Molecular Biology of Regeneration', 'How does a regenerating animal ''know'' what''s missing? How are stem cells or differentiated cells used to create new tissues during regeneration? In this class we will take a comparative approach to explore this fascinating problem by critically examining classic and modern scientific literature about the developmental and molecular biology of regeneration.', 'How does a regenerating animal ''know'' what''s missing? How are stem cells or differentiated cells used to create new tissues during regeneration? In this class we will take a comparative approach to explore this fascinating problem by critically examining classic and modern scientific literature about the developmental and molecular biology of regeneration. We will learn about conserved developmental pathways that are necessary for regeneration, and we will discuss the relevance of these findings for regenerative medicine. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-342-developmental-and-molecular-biology-of-regeneration-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-342-developmental-and-molecular-biology-of-regeneration-spring-2008/7-342s08.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2048, 'G-Protein Coupled Receptors: Vision and Disease', 'How do we communicate with the outside world? How are our senses of vision, smell, taste and pain controlled at the cellular and molecular levels? What causes medical conditions like allergies, hypertension, depression, obesity and various central nervous system disorders? G-protein coupled receptors (GPCRs) provide a major part of the answer to all of these questions.', 'How do we communicate with the outside world? How are our senses of vision, smell, taste and pain controlled at the cellular and molecular levels? What causes medical conditions like allergies, hypertension, depression, obesity and various central nervous system disorders? G-protein coupled receptors (GPCRs) provide a major part of the answer to all of these questions. GPCRs constitute the largest family of cell-surface receptors and in humans are encoded by more than 1,000 genes. GPCRs convert extracellular messages into intracellular responses and are involved in essentially all physiological processes. GPCR dysfunction results in numerous human disorders, and over 50% of all prescription drugs on the market today directly or indirectly target GPCRs. In this course, we will discuss GPCR signal transduction pathways, GPCR oligomerization and the diseases caused by GPCR dysfunction. We will study the structure and function of rhodopsin, a dim-light photoreceptor and a well-studied GPCR that converts light into electric impulses sent to the brain and leads to vision. We will also discuss how mutations in rhodopsin cause retinal degeneration and congenital night blindness. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-342-g-protein-coupled-receptors-vision-and-disease-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-342-g-protein-coupled-receptors-vision-and-disease-spring-2007/7-342s07.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2049, 'Chronic Infection and Inflammation: What are the Consequences on Your Health?', 'In this course we will explore the new emerging field of pathogen-induced chronic diseases.', 'In this course we will explore the new emerging field of pathogen-induced chronic diseases. Work in this field has redefined the causes of some major disorders, such as ulcers. By reading the primary research literature we will learn about the molecular mechanisms through which pathogens cause disease. The diseases that we cover will be introduced with a short patient case study. We will discuss the bacterium Helicobacter pylori and gastric disease, HPV and cervical cancer, hepatitis C virus and liver disease, Epstein-Barr virus and lymphoma, Cytomegalovirus and atherosclerosis, as well as diabetes and multiple sclerosis. We will study technical advances in the fight against microbes and explore future directions for new treatment strategies of chronic infections and inflammation. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-342-chronic-infection-and-inflammation-what-are-the-consequences-on-your-health-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-342-chronic-infection-and-inflammation-what-are-the-consequences-on-your-health-fall-2007/7-342f07.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2050, 'Reading the Blueprint of Life: Transcription, Stem Cells and Differentiation', 'In this course, we will address how transcriptional regulators both prohibit and drive differentiation during the course of development.', 'In this course, we will address how transcriptional regulators both prohibit and drive differentiation during the course of development. How does a stem cell know when to remain a stem cell and when to become a specific cell type? Are there global differences in the way the genome is read in multipotent and terminally differentiated cells? We will explore how stem cell pluripotency is preserved, how master regulators of cell-fate decisions execute developmental programs, and how chromatin regulators control undifferentiated versus differentiated states. Additionally, we will discuss how aberrant regulation of transcriptional regulators produces disorders such as developmental defects and cancer.This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-342-reading-the-blueprint-of-life-transcription-stem-cells-and-differentiation-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-342-reading-the-blueprint-of-life-transcription-stem-cells-and-differentiation-fall-2006/7-342f06.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2051, 'Network Medicine: Using Systems Biology and Signaling Networks to Create Novel Cancer Therapeutics', 'In this course, we will survey the primary systems biology literature, particularly as it pertains to understanding and treating various forms of cancer.', 'In this course, we will survey the primary systems biology literature, particularly as it pertains to understanding and treating various forms of cancer. We will consider various computational and experimental techniques being used in the field of systems biology, focusing on how systems principles have helped advance biological understanding. We will also discuss the application of the principles of systems biology and network biology to drug development, an emerging discipline called &quot;network medicine.&quot; This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-343-network-medicine-using-systems-biology-and-signaling-networks-to-create-novel-cancer-therapeutics-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-343-network-medicine-using-systems-biology-and-signaling-networks-to-create-novel-cancer-therapeutics-fall-2012/7-343f12.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2052, 'When Development Goes Awry: How Cancer Co-opts Mechanisms of Embryogensis', 'During this course, we will study the similarities between cancer and normal development to understand how tumors co-opt normal developmental processes to facilitate cancer initiation, maintenance and progression.', 'During this course, we will study the similarities between cancer and normal development to understand how tumors co-opt normal developmental processes to facilitate cancer initiation, maintenance and progression. We will examine critical signaling pathways that govern these processes and, importantly, how some of these pathways hold promise as therapeutic targets for cancer treatment. We will discuss how future treatments might be personalized to target cancer cells in specific patients. We will also consider examples of newly-approved drugs that have dramatically helped patients combat this devastating disease. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-343-when-development-goes-awry-how-cancer-co-opts-mechanisms-of-embryogensis-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-343-when-development-goes-awry-how-cancer-co-opts-mechanisms-of-embryogensis-fall-2009/7-343f09.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2053, 'Sophisticated Survival Skills of Simple Microorganisms', 'In this course, we will discuss the microbial physiology and genetics of stress responses in aquatic ecosystems, astrobiology, bacterial pathogenesis and other environments.', 'In this course, we will discuss the microbial physiology and genetics of stress responses in aquatic ecosystems, astrobiology, bacterial pathogenesis and other environments. We will learn about classical and novel methods utilized by researchers to uncover bacterial mechanisms induced under both general and environment-specific stresses. Finally, we will compare and contrast models for bacterial stress responses to gain an understanding of distinct mechanisms of survival and of why there are differences among bacterial genera. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting.', 'http://ocw.mit.edu/courses/biology/7-343-sophisticated-survival-skills-of-simple-microorganisms-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-343-sophisticated-survival-skills-of-simple-microorganisms-spring-2008/7-343s08.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2054, 'The Radical Consequences of Respiration: Reactive Oxygen Species in Aging and Disease', 'This course&nbsp;will start with a survey of basic oxygen radical biochemistry followed by a discussion of the mechanisms of action of cellular as well as dietary antioxidants.', 'This course&nbsp;will start with a survey of basic oxygen radical biochemistry followed by a discussion of the mechanisms of action of cellular as well as dietary antioxidants. After considering the normal physiological roles of oxidants, we will examine the effects of elevated ROS and a failure of cellular redox capacity on the rate of organismal and cellular aging as well as on the onset and progression of several major diseases that are often age-related. Topics will include ROS-induced effects on stem cell regeneration, insulin resistance, heart disease, neurodegenerative disorders, and cancer. The role of antioxidants in potential therapeutic strategies for modulating ROS levels will also be discussed. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-343-the-radical-consequences-of-respiration-reactive-oxygen-species-in-aging-and-disease-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-343-the-radical-consequences-of-respiration-reactive-oxygen-species-in-aging-and-disease-fall-2007/7-343f07.JPG', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2055, 'Photosynthesis: Life from Light', 'In this course, you will journey through the web of physical, chemical, and biological reactions that collectively constitute photosynthesis.', 'In this course, you will journey through the web of physical, chemical, and biological reactions that collectively constitute photosynthesis. We will begin with light harvesting and follow photons to the sites of primary photochemistry: the photoreaction centers. A molecular-scale view will show in atomic detail how these protein complexes capture and energize electrons. Then we will follow the multiple pathways electrons take as they carry out their work. Consequent reactions, such as the synthesis of ATP and the reduction of CO2 during the synthesis of carbohydrates, will also be discussed in structural detail. Lastly, we will delve into the evolution of these systems and also discuss other photosynthetic strategies, such as light-driven proton pumps and anoxygenic photosynthesis. The course will include a visit to an electron microscope to allow students to directly observe proteins involved in photosynthesis. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-343-photosynthesis-life-from-light-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-343-photosynthesis-life-from-light-fall-2006/7-343f06.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2056, 'A Love-Hate Relationship: Cholesterol in Health and Disease', 'In this class, we will examine cholesterol''s role in the cell and in the body as a whole, from its function as a structural component of the membrane to its function in signaling.', 'In this class, we will examine cholesterol''s role in the cell and in the body as a whole, from its function as a structural component of the membrane to its function in signaling. We will discuss mechanisms of cholesterol sensing, mechanisms of feedback regulation in cells, cholesterol in the brain, cholesterol in the circulation, ''good cholesterol'' and ''bad cholesterol,'' cholesterol-related human disorders, and the drugs that deal with some of these disorders. This course is one of many&nbsp;Advanced Undergraduate Seminars&nbsp;offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the&nbsp;Advanced Undergraduate Seminars&nbsp;are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-343-a-love-hate-relationship-cholesterol-in-health-and-disease-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-343-a-love-hate-relationship-cholesterol-in-health-and-disease-fall-2005/7-343f05.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2057, 'Tumor Suppressor Gene p53: How the Guardian of our Genome Prevents Cancer', 'Cancer is a leading cause of death worldwide.', 'Cancer is a leading cause of death worldwide. Cancer involves uncontrolled cell growth, resistance to cell death, failure to differentiate into a particular cell type, and increased cellular motility. A family of gate-keeper genes, known as tumor suppressor genes, plays important roles in preventing the initiation and progression of cancer. Among these, p53 is the most famous. Because of its essential role in maintaining genomic integrity, p53 is often called the guardian of the genome. During this course, we will study how p53 serves as a pivotal tumor suppressor gene in preventing cancer.This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-344-tumor-suppressor-gene-p53-how-the-guardian-of-our-genome-prevents-cancer-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-344-tumor-suppressor-gene-p53-how-the-guardian-of-our-genome-prevents-cancer-fall-2010/7-344f10.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2058, 'Directed Evolution: Engineering Biocatalysts', 'Directed evolution has been used to produce enzymes with many unique properties.', 'Directed evolution has been used to produce enzymes with many unique properties. The technique of directed evolution comprises two essential steps: mutagenesis of the gene encoding the enzyme to produce a library of variants, and selection of a particular variant based on its desirable catalytic properties. In this course we will examine what kinds of enzymes are worth evolving and the strategies used for library generation and enzyme selection. We will focus on those enzymes that are used in the synthesis of drugs and in biotechnological applications. This course is one of many&nbsp;Advanced Undergraduate Seminars&nbsp;offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-344-directed-evolution-engineering-biocatalysts-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-344-directed-evolution-engineering-biocatalysts-spring-2008/7-344s08.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2059, 'Antibiotics, Toxins, and Protein Engineering', 'The lethal poison Ricin (best known as a weapon of bioterrorism), Diphtheria toxin (the causative agent of a highly contagious bacterial disease), and the widely used antibiotic tetracycline have one thing in common: They specifically target the cell''s translational apparatus and disrupt protein synthesis.', 'The lethal poison Ricin (best known as a weapon of bioterrorism), Diphtheria toxin (the causative agent of a highly contagious bacterial disease), and the widely used antibiotic tetracycline have one thing in common: They specifically target the cell''s translational apparatus and disrupt protein synthesis. In this course, we will explore the mechanisms of action of toxins and antibiotics, their roles in everyday medicine, and the emergence and spread of drug resistance. We will also discuss the identification of new drug targets and how we can manipulate the protein synthesis machinery to provide powerful tools for protein engineering and potential new treatments for patients with devastating diseases, such as cystic fibrosis and muscular dystrophy. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-344-antibiotics-toxins-and-protein-engineering-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-344-antibiotics-toxins-and-protein-engineering-spring-2007/7-344s07.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2060, 'RNA Interference: A New Tool for Genetic Analysis and Therapeutics', 'This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT.', 'This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. To understand and treat any disease with a genetic basis or predisposition, scientists and clinicians need effective ways of manipulating the levels of genes and gene products. Conventional methods for the genetic modification of many experimental organisms are technically demanding and time consuming. Just over 5 years ago, a new mechanism of gene-silencing, termed RNA interference (RNAi), was discovered. In addition to being a fascinating biological process, RNAi provides a revolutionary technology that has already changed the way biomedical research is done and that may even prove useful for genetic interventions in a clinical context. In this course, students learn how RNAi was discovered, how it works, and what its physiological relevance might be. How RNAi can be harnessed to modulate gene expression and perform genetic screens, both in cells and in various organisms is also covered. Finally, this course examines the first attempts to use RNAi for the treatment of models of human diseases in experimental animals.', 'http://ocw.mit.edu/courses/biology/7-344-rna-interference-a-new-tool-for-genetic-analysis-and-therapeutics-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-344-rna-interference-a-new-tool-for-genetic-analysis-and-therapeutics-fall-2004/7-344f04.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2061, 'Non-coding RNAs: Junk or Critical Regulators in Health and Disease?', 'Every time we scientists think that we have dissected the precise biological nature of a process, an incidental finding, a brilliantly designed experiment, or an unexpected result can turn our world upside down.', 'Every time we scientists think that we have dissected the precise biological nature of a process, an incidental finding, a brilliantly designed experiment, or an unexpected result can turn our world upside down. Until recently thought by many to be cellular &quot;junk&quot; because they do not encode proteins, non-coding RNAs are gaining a growing recognition for their roles in the regulation of a wide scope of processes, ranging from embryogenesis and development to cancer and degenerative disorders. The aim of this class is to introduce the diversity of the RNA world, inhabited by microRNAs, lincRNAs, piRNAs, and many others. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-345-non-coding-rnas-junk-or-critical-regulators-in-health-and-disease-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-345-non-coding-rnas-junk-or-critical-regulators-in-health-and-disease-spring-2012/7-345s12.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2062, 'Survival in Extreme Conditions: The Bacterial Stress Response', 'Bacteria survive in almost all environments on Earth, including some considered extremely harsh.', 'Bacteria survive in almost all environments on Earth, including some considered extremely harsh. From the steaming hot springs of Yellowstone to the frozen tundra of the arctic to the barren deserts of Chile, microbes have been found thriving. Their tenacity to survive in such extreme and varied conditions allows them to play fundamental roles in global nutrient cycling. Microbes also cause a wide range of human diseases and can survive inhospitable conditions found in the human body. In this course, we will examine the molecular systems that bacteria use to adapt to changes in their environment. We will consider stresses commonly encountered, such as starvation, oxidative stress and heat shock, and also discuss how the adaptive responses affect the evolution of the bacteria. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-345-survival-in-extreme-conditions-the-bacterial-stress-response-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-345-survival-in-extreme-conditions-the-bacterial-stress-response-fall-2010/7-345f10.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2063, 'Evolution of the Immune System', 'In this course, evolutionary pathways that have led to the development of innate and adaptive immunity are analyzed, the conserved and unique features of the immune response from bacteria to higher vertebrates is traced, and factors, such as adaptive changes in pathogens that have shaped the evolution of immune system are identified.', 'In this course, evolutionary pathways that have led to the development of innate and adaptive immunity are analyzed, the conserved and unique features of the immune response from bacteria to higher vertebrates is traced, and factors, such as adaptive changes in pathogens that have shaped the evolution of immune system are identified.This course is one of many&nbsp;Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting.', 'http://ocw.mit.edu/courses/biology/7-345-evolution-of-the-immune-system-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-345-evolution-of-the-immune-system-spring-2005/7-345-s05.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2064, 'Cellular Garbage Disposal: Misfolded Proteins in Normal Biology and Human Disease', 'The endoplasmic reticulum (ER) orchestrates different cellular processes by which proteins are synthesized, correctly folded, modified and ultimately transported to their final destinations.', 'The endoplasmic reticulum (ER) orchestrates different cellular processes by which proteins are synthesized, correctly folded, modified and ultimately transported to their final destinations. As part of this crucial biosynthetic process, proteins that are not properly folded and consequently detrimental to normal cellular function are constantly generated. A common signature of many neurodegenerative diseases, including Alzheimer''s and Parkinson''s, is accumulation and deposition of misfolded proteins that arise when the ability of cells to deal with the burden of misfolded proteins is compromised. In this course, we will explore how the ER quality control machinery ensures that only properly assembled proteins exit the ER while distinguishing between nascent proteins en route to their biologically active folded state from those that are terminally misfolded.', 'http://ocw.mit.edu/courses/biology/7-346-cellular-garbage-disposal-misfolded-proteins-in-normal-biology-and-human-disease-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-346-cellular-garbage-disposal-misfolded-proteins-in-normal-biology-and-human-disease-fall-2011/7-346f11.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2065, 'RNAi: A Revolution in Biology and Therapeutics', 'Despite centuries of effort, modern medicine still struggles to find the source of disease and to provide specific treatment without side effects.', 'Despite centuries of effort, modern medicine still struggles to find the source of disease and to provide specific treatment without side effects. Both traditional small molecules and protein-based therapeutics have achieved only limited success. What is the next therapeutic frontier? The answer may be RNA interference. In this course, we will focus on the therapeutic potential of RNAi. We will discuss its discovery functions in normal biological processes, utility as an experimental tool, potential for therapeutic use, and pursuit by the biotechnology industry. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-346-rnai-a-revolution-in-biology-and-therapeutics-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-346-rnai-a-revolution-in-biology-and-therapeutics-spring-2010/7-346s10.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2066, 'Fueling Sustainability: Engineering Microbial Systems for Biofuel Production', 'The need to identify sustainable forms of energy as an alternative to our dependence on depleting worldwide oil reserves is one of the grand challenges of our time.', 'The need to identify sustainable forms of energy as an alternative to our dependence on depleting worldwide oil reserves is one of the grand challenges of our time. The energy from the sun converted into plant biomass is the most promising renewable resource available to humanity. This seminar will examine each of the critical steps along the pathway towards the conversion of plant biomass into ethanol. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-347-fueling-sustainability-engineering-microbial-systems-for-biofuel-production-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-347-fueling-sustainability-engineering-microbial-systems-for-biofuel-production-spring-2011/7-347s11.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2067, 'Stem Cells: A Cure or Disease?', 'Have you ever considered going to a pharmacy to order some new cardiomyocytes (heart muscle cells) for your ailing heart? It might sound crazy, but recent developments in stem cell science have made this concept not so futuristic.', 'Have you ever considered going to a pharmacy to order some new cardiomyocytes (heart muscle cells) for your ailing heart? It might sound crazy, but recent developments in stem cell science have made this concept not so futuristic. In this course, we will explore the underlying biology behind the idea of using stem cells to treat disease, specifically analyzing the mechanisms that enable a single genome to encode multiple cell states ranging from neurons to fibroblasts to T cells. Overall, we hope to provide a comprehensive overview of this exciting new field of research and its clinical relevance. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-349-stem-cells-a-cure-or-disease-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-349-stem-cells-a-cure-or-disease-spring-2011/7-349s11.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2068, 'From Molecules to Behavior: Synaptic Neurophysiology', 'Like transistors in a computer, synapses perform complex computations and connect the brain''s non-linear processing elements (neurons) into a functional circuit.', 'Like transistors in a computer, synapses perform complex computations and connect the brain''s non-linear processing elements (neurons) into a functional circuit. Understanding the role of synapses in neuronal computation is essential to understanding how the brain works. In this course students will be introduced to cutting-edge research in the field of synaptic neurophysiology. The course will cover such topics as synapse formation, synaptic function, synaptic plasticity, the roles of synapses in higher cognitive processes and how synaptic dysfunction can lead to disease. This course is one of many Advanced Undergraduate Seminars offered by the Biology Department at MIT. These seminars are tailored for students with an interest in using primary research literature to discuss and learn about current biological research in a highly interactive setting. Many instructors of the Advanced Undergraduate Seminars are postdoctoral scientists with a strong interest in teaching.', 'http://ocw.mit.edu/courses/biology/7-349-from-molecules-to-behavior-synaptic-neurophysiology-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-349-from-molecules-to-behavior-synaptic-neurophysiology-spring-2010/7-349s10.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2069, 'Introduction to Bioengineering (BE.010J)', 'Bioengineering at MIT is represented by the diverse curricula offered by most Departments in the School of Engineering.', 'Bioengineering at MIT is represented by the diverse curricula offered by most Departments in the School of Engineering. This course samples the wide variety of bioengineering options for students who plan to major in one of the undergraduate Engineering degree programs. The beginning lectures describe the science basis for bioengineering with particular emphasis on molecular cell biology and systems biology. Bioengineering faculty will then describe the bioengineering options in a particular engineering course as well as the type of research conducted by faculty in the department.', 'http://ocw.mit.edu/courses/biological-engineering/20-010j-introduction-to-bioengineering-be-010j-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biological-engineering/20-010j-introduction-to-bioengineering-be-010j-spring-2006/20-010js06.jpg', 'Biology', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2070, 'Concept-Centered Teaching', 'Do you like teaching, but find yourself frustrated by how little students seem to learn? Would you like to try teaching, but are nervous about whether you will be any good at it? Are you interested in new research on science education? Research in science education shows that the greatest obstacle to student learning is the failure to identify and confront the misconceptions with which the students enter the class or those that they acquire during their studies.', 'Do you like teaching, but find yourself frustrated by how little students seem to learn? Would you like to try teaching, but are nervous about whether you will be any good at it? Are you interested in new research on science education? Research in science education shows that the greatest obstacle to student learning is the failure to identify and confront the misconceptions with which the students enter the class or those that they acquire during their studies. This weekly seminar course focuses on developing the participants'' ability to uncover and confront student misconceptions and to foster student understanding and retention of key concepts. Participants read primary literature on science education, uncover basic concepts often overlooked when teaching biology, and lead a small weekly discussion session for students currently enrolled in introductory biology classes. The instructor for this course, Dr. Kosinski-Collins, is a member of the HHMI Education Group.', 'http://ocw.mit.edu/courses/biology/7-391-concept-centered-teaching-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/biology/7-391-concept-centered-teaching-spring-2006/7-391s06.jpg', 'Biology', 'MIT');
