INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2268, 'Calculus with Theory', '18.', '18.014, Calculus with Theory, covers the same material as 18.01 (Single Variable Calculus), but at a deeper and more rigorous level. It emphasizes careful reasoning and understanding of proofs. The course assumes knowledge of elementary calculus.', 'http://ocw.mit.edu/courses/mathematics/18-014-calculus-with-theory-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-014-calculus-with-theory-fall-2010/18-014f10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2269, 'Multivariable Calculus', 'This course covers differential, integral and vector calculus for functions of more than one variable.', 'This course covers differential, integral and vector calculus for functions of more than one variable. These mathematical tools and methods are used extensively in the physical sciences, engineering, economics and computer graphics.', 'http://ocw.mit.edu/courses/mathematics/18-02sc-multivariable-calculus-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-02sc-multivariable-calculus-fall-2010/18-02scf10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2270, 'Multivariable Calculus', 'This course covers vector and multi-variable calculus.', 'This course covers vector and multi-variable calculus. It is the second semester in the freshman calculus sequence. Topics include vectors and matrices, partial derivatives, double and triple integrals, and vector calculus in 2 and 3-space. MIT OpenCourseWare offers another version of 18.02, from the Spring 2006 term. Both versions cover the same material, although they are taught by different faculty and rely on different textbooks. Multivariable Calculus (18.02) is taught during the Fall and Spring terms at MIT, and is a required subject for all MIT undergraduates.', 'http://ocw.mit.edu/courses/mathematics/18-02-multivariable-calculus-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-02-multivariable-calculus-fall-2007/18-02f07.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2271, 'Calculus of Several Variables', 'This is a variation on 18.', 'This is a variation on 18.02 Multivariable Calculus. It covers the same topics as in 18.02, but with more focus on mathematical concepts.', 'http://ocw.mit.edu/courses/mathematics/18-022-calculus-of-several-variables-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-022-calculus-of-several-variables-fall-2010/18-022f10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2272, 'Multivariable Calculus with Theory', 'This course is a continuation of 18.', 'This course is a continuation of 18.014. It covers the same material as 18.02 (Multivariable Calculus), but at a deeper level, emphasizing careful reasoning and understanding of proofs. There is considerable emphasis on linear algebra and vector integral calculus.', 'http://ocw.mit.edu/courses/mathematics/18-024-multivariable-calculus-with-theory-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-024-multivariable-calculus-with-theory-spring-2011/18-024s11.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2273, 'Differential Equations', 'The laws of nature are expressed as differential equations.', 'The laws of nature are expressed as differential equations. Scientists and engineers must know how to model the world in terms of differential equations, and how to solve those equations and interpret the solutions. This course focuses on the equations and techniques most useful in science and engineering.', 'http://ocw.mit.edu/courses/mathematics/18-03sc-differential-equations-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-03sc-differential-equations-fall-2011/18-03scf11.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2274, 'Honors Differential Equations', 'This course covers the same material as Differential Equations (18.', 'This course covers the same material as Differential Equations (18.03) with more emphasis on theory. In addition, it treats mathematical aspects of ordinary differential equations such as existence theorems.', 'http://ocw.mit.edu/courses/mathematics/18-034-honors-differential-equations-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-034-honors-differential-equations-spring-2009/18-034s09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2275, 'Honors Differential Equations', 'This course covers the same material as 18.', 'This course covers the same material as 18.03 with more emphasis on theory. Topics include first order equations, separation, initial value problems, systems, linear equations, independence of solutions, undetermined coefficients, and singular points and periodic orbits for planar systems.', 'http://ocw.mit.edu/courses/mathematics/18-034-honors-differential-equations-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-034-honors-differential-equations-spring-2004/18-034s04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2276, 'Complex Variables with Applications', 'This course explored topics such as complex algebra and functions, analyticity, contour integration, Cauchy''s theorem, singularities, Taylor and Laurent series, residues, evaluation of integrals, multivalued functions, potential theory in two dimensions, Fourier analysis and Laplace transforms.', 'This course explored topics such as complex algebra and functions, analyticity, contour integration, Cauchy''s theorem, singularities, Taylor and Laurent series, residues, evaluation of integrals, multivalued functions, potential theory in two dimensions, Fourier analysis and Laplace transforms.', 'http://ocw.mit.edu/courses/mathematics/18-04-complex-variables-with-applications-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-04-complex-variables-with-applications-fall-2003/18-04f03.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2277, 'Introduction to Probability and Statistics', 'This course provides an elementary introduction to probability and statistics with applications.', 'This course provides an elementary introduction to probability and statistics with applications. Topics include: basic probability models; combinatorics; random variables; discrete and continuous probability distributions; statistical estimation and testing; confidence intervals; and an introduction to linear regression.', 'http://ocw.mit.edu/courses/mathematics/18-05-introduction-to-probability-and-statistics-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-05-introduction-to-probability-and-statistics-spring-2005/18-05s05.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2278, 'Linear Algebra', 'This course covers matrix theory and linear algebra, emphasizing topics useful in other disciplines such as physics, economics and social sciences, natural sciences, and engineering.', 'This course covers matrix theory and linear algebra, emphasizing topics useful in other disciplines such as physics, economics and social sciences, natural sciences, and engineering.', 'http://ocw.mit.edu/courses/mathematics/18-06sc-linear-algebra-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-06sc-linear-algebra-fall-2011/18-06scf11.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2279, 'Linear Algebra', 'This is a basic subject on matrix theory and linear algebra.', 'This is a basic subject on matrix theory and linear algebra. Emphasis is given to topics that will be useful in other disciplines, including systems of equations, vector spaces, determinants, eigenvalues, similarity, and positive definite matrices.', 'http://ocw.mit.edu/courses/mathematics/18-06-linear-algebra-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-06-linear-algebra-spring-2010/18-06s10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2280, 'Mathematics for Computer Science', 'This course covers elementary discrete mathematics for computer science and engineering.', 'This course covers elementary discrete mathematics for computer science and engineering. It emphasizes mathematical definitions and proofs as well as applicable methods. Topics include formal logic notation, proof methods; induction, well-ordering; sets, relations; elementary graph theory; integer congruences; asymptotic notation and growth of functions; permutations and combinations, counting principles; discrete probability. Further selected topics may also be covered, such as recursive definition and structural induction; state machines and invariants; recurrences; generating functions.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-042j-mathematics-for-computer-science-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-042j-mathematics-for-computer-science-fall-2010/6-042jf10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2281, 'Mathematics for Computer Science', 'This course is offered to undergraduates and is an elementary discrete mathematics course oriented towards applications in computer science and engineering.', 'This course is offered to undergraduates and is an elementary discrete mathematics course oriented towards applications in computer science and engineering. Topics covered include: formal logic notation, induction, sets and relations, permutations and combinations, counting principles, and discrete probability.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-042j-mathematics-for-computer-science-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-042j-mathematics-for-computer-science-spring-2005/6-042js05.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2282, 'Mathematics for Computer Science', 'This is an introductory course in Discrete Mathematics oriented toward Computer Science and Engineering.', 'This is an introductory course in Discrete Mathematics oriented toward Computer Science and Engineering. The course divides roughly into thirds: Fundamental Concepts of Mathematics: Definitions, Proofs, Sets, Functions, Relations Discrete Structures: Modular Arithmetic, Graphs, State Machines, Counting Discrete Probability Theory A version of this course from a&nbsp;previous term&nbsp;was also taught as part of the Singapore-MIT Alliance (SMA) programme as course number SMA 5512 (Mathematics for Computer Science).', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-042j-mathematics-for-computer-science-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-042j-mathematics-for-computer-science-fall-2005/6-042jf05.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2283, 'Mathematical Exposition', 'This course provides techniques of effective presentation of mathematical material.', 'This course provides techniques of effective presentation of mathematical material. Each section of this course is associated with a regular mathematics subject, and uses the material of that subject as a basis for written and oral presentations. The section presented here is on chaotic dynamical systems.', 'http://ocw.mit.edu/courses/mathematics/18-091-mathematical-exposition-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-091-mathematical-exposition-spring-2005/18-091s05.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2284, 'Street-Fighting Mathematics', 'This course teaches the art of guessing results and solving problems without doing a proof or an exact calculation.', 'This course teaches the art of guessing results and solving problems without doing a proof or an exact calculation. Techniques include extreme-cases reasoning, dimensional analysis, successive approximation, discretization, generalization, and pictorial analysis. Applications include mental calculation, solid geometry, musical intervals, logarithms, integration, infinite series, solitaire, and differential equations. (No epsilons or deltas are harmed by taking this course.) This course is offered during the Independent Activities Period (IAP), which is a special 4-week term at MIT that runs from the first week of January until the end of the month.', 'http://ocw.mit.edu/courses/mathematics/18-098-street-fighting-mathematics-january-iap-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-098-street-fighting-mathematics-january-iap-2008/18-098iap08.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2285, 'Introduction to Analysis', 'Analysis I (18.', 'Analysis I (18.100) in its various versions covers fundamentals of mathematical analysis: continuity, differentiability, some form of the Riemann integral, sequences and series of numbers and functions, uniform convergence with applications to interchange of limit operations, some point-set topology, including some work in Euclidean n-space. MIT students may choose to take one of three versions of 18.100: Option A (18.100A) chooses less abstract definitions and proofs, and gives applications where possible. Option B (18.100B) is more demanding and for students with more mathematical maturity; it places more emphasis from the beginning on point-set topology and n-space, whereas Option A is concerned primarily with analysis on the real line, saving for the last weeks work in 2-space (the plane) and its point-set topology. Option C (18.100C) is a 15-unit variant of Option B, with further instruction and practice in written and oral communication.', 'http://ocw.mit.edu/courses/mathematics/18-100a-introduction-to-analysis-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-100a-introduction-to-analysis-fall-2012/18-100af12.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2286, 'Real Analysis', '', '', 'http://ocw.mit.edu/courses/mathematics/18-100c-real-analysis-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-100c-real-analysis-fall-2012/18-100cf12.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2287, 'Analysis II', 'This course continues from Analysis I (18.', 'This course continues from Analysis I (18.100B), in the direction of manifolds and global analysis. The first half of the course covers multivariable calculus. The rest of the course covers the theory of differential forms in n-dimensional vector spaces and manifolds.', 'http://ocw.mit.edu/courses/mathematics/18-101-analysis-ii-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-101-analysis-ii-fall-2005/18-101f05.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2288, 'Introduction to Functional Analysis', 'This is a undergraduate course.', 'This is a undergraduate course. It will cover normed spaces, completeness, functionals, Hahn-Banach theorem, duality, operators; Lebesgue measure, measurable functions, integrability, completeness of L-p spaces; Hilbert space; compact, Hilbert-Schmidt and trace class operators; as well as spectral theorem.', 'http://ocw.mit.edu/courses/mathematics/18-102-introduction-to-functional-analysis-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-102-introduction-to-functional-analysis-spring-2009/18-102s09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2289, 'Seminar in Analysis: Applications to Number Theory', '18.', '18.104 is an undergraduate level seminar for mathematics majors. Students present and discuss subject matter taken from current journals or books. Instruction and practice in written and oral communication is provided. The topics vary from year to year. The topic for this term is Applications to Number Theory.', 'http://ocw.mit.edu/courses/mathematics/18-104-seminar-in-analysis-applications-to-number-theory-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-104-seminar-in-analysis-applications-to-number-theory-fall-2006/18-104f06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2290, 'Functions of a Complex Variable', 'This is an advanced undergraduate course dealing with calculus in one complex variable with geometric emphasis.', 'This is an advanced undergraduate course dealing with calculus in one complex variable with geometric emphasis. Since the course Analysis I (18.100B) is a prerequisite, topological notions like compactness, connectedness, and related properties of continuous functions are taken for granted. This course offers biweekly problem sets with solutions, two term tests and a final exam, all with solutions.', 'http://ocw.mit.edu/courses/mathematics/18-112-functions-of-a-complex-variable-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-112-functions-of-a-complex-variable-fall-2008/18-112f08.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2291, 'Introduction to Partial Differential Equations', 'This course introduces three main types of partial differential equations: diffusion, elliptic, and hyperbolic.', 'This course introduces three main types of partial differential equations: diffusion, elliptic, and hyperbolic. It includes mathematical tools, real-world examples and applications.', 'http://ocw.mit.edu/courses/mathematics/18-152-introduction-to-partial-differential-equations-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-152-introduction-to-partial-differential-equations-fall-2011/18-152f11.gif', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2292, 'Geometry and Quantum Field Theory', 'Geometry and Quantum Field Theory, designed for mathematicians, is a rigorous introduction to perturbative quantum field theory, using the language of functional integrals.', 'Geometry and Quantum Field Theory, designed for mathematicians, is a rigorous introduction to perturbative quantum field theory, using the language of functional integrals. It covers the basics of classical field theory, free quantum theories and Feynman diagrams. The goal is to discuss, using mathematical language, a number of basic notions and results of QFT that are necessary to understand talks and papers in QFT and String Theory.', 'http://ocw.mit.edu/courses/mathematics/18-238-geometry-and-quantum-field-theory-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-238-geometry-and-quantum-field-theory-fall-2002/18-238f02.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2293, 'Linear Partial Differential Equations: Analysis and Numerics', 'This course provides students with the basic analytical and computational tools of linear partial differential equations (PDEs) for practical applications in science engineering, including heat/diffusion, wave, and Poisson equations.', 'This course provides students with the basic analytical and computational tools of linear partial differential equations (PDEs) for practical applications in science engineering, including heat/diffusion, wave, and Poisson equations. Analytics emphasize the viewpoint of linear algebra and the analogy with finite matrix problems. Numerics focus on finite-difference and finite-element techniques to reduce PDEs to matrix problems.', 'http://ocw.mit.edu/courses/mathematics/18-303-linear-partial-differential-equations-analysis-and-numerics-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-303-linear-partial-differential-equations-analysis-and-numerics-fall-2010/18-303f10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2294, 'Linear Partial Differential Equations', 'This course covers the classical partial differential equations of applied mathematics: diffusion, Laplace/Poisson, and wave equations.', 'This course covers the classical partial differential equations of applied mathematics: diffusion, Laplace/Poisson, and wave equations. It also includes methods and tools for solving these PDEs, such as separation of variables, Fourier series and transforms, eigenvalue problems, and Green''s functions.', 'http://ocw.mit.edu/courses/mathematics/18-303-linear-partial-differential-equations-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-303-linear-partial-differential-equations-fall-2006/18-303f06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2295, 'Principles of Applied Mathematics', 'Principles of Applied Mathematics is a study of illustrative topics in discrete applied mathematics including sorting algorithms, information theory, coding theory, secret codes, generating functions, linear programming, game theory.', 'Principles of Applied Mathematics is a study of illustrative topics in discrete applied mathematics including sorting algorithms, information theory, coding theory, secret codes, generating functions, linear programming, game theory. There is an emphasis on topics that have direct application in the real world. This course was recently revised to meet the MIT Undergraduate Communication Requirement (CR). It covers the same content as 18.310, but assignments are structured with an additional focus on writing.', 'http://ocw.mit.edu/courses/mathematics/18-310c-principles-of-applied-mathematics-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-310c-principles-of-applied-mathematics-fall-2007/18-310cf07.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2296, 'Principles of Applied Mathematics', 'This course is about mathematical analysis of continuum models of various natural phenomena.', 'This course is about mathematical analysis of continuum models of various natural phenomena. Such models are generally described by partial differential equations (PDE) and for this reason much of the course is devoted to the analysis of PDE. Examples of applications come from physics, chemistry, biology, complex systems: traffic flows, shock waves, hydraulic jumps, bio-fluid flows, chemical reactions, diffusion, heat transfer, population dynamics, and pattern formation.', 'http://ocw.mit.edu/courses/mathematics/18-311-principles-of-applied-mathematics-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-311-principles-of-applied-mathematics-spring-2009/18-311s09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2297, 'Algebraic Combinatorics', 'This is an introductory course in algebraic combinatorics.', 'This is an introductory course in algebraic combinatorics. No prior knowledge of combinatorics is expected, but assumes a familiarity with linear algebra and finite groups. Topics were chosen to show the beauty and power of techniques in algebraic combinatorics. Rigorous mathematical proofs are expected.', 'http://ocw.mit.edu/courses/mathematics/18-312-algebraic-combinatorics-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-312-algebraic-combinatorics-spring-2009/18-312s09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2298, 'Introduction to Numerical Analysis', 'This course analyzed the basic techniques for the efficient numerical solution of problems in science and engineering.', 'This course analyzed the basic techniques for the efficient numerical solution of problems in science and engineering. Topics spanned root finding, interpolation, approximation of functions, integration, differential equations, direct and iterative methods in linear algebra.', 'http://ocw.mit.edu/courses/mathematics/18-330-introduction-to-numerical-analysis-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-330-introduction-to-numerical-analysis-spring-2012/18-330s12.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2299, 'Introduction to Numerical Analysis', 'This course analyzed the basic techniques for the efficient numerical solution of problems in science and engineering.', 'This course analyzed the basic techniques for the efficient numerical solution of problems in science and engineering. Topics spanned root finding, interpolation, approximation of functions, integration, differential equations and direct and iterative methods in linear algebra.', 'http://ocw.mit.edu/courses/mathematics/18-330-introduction-to-numerical-analysis-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-330-introduction-to-numerical-analysis-spring-2004/18-330s04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2300, 'Nonlinear Dynamics I: Chaos', 'This course provides an introduction to the theory and phenomenology of nonlinear dynamics and chaos in dissipative systems.', 'This course provides an introduction to the theory and phenomenology of nonlinear dynamics and chaos in dissipative systems. The content is structured to be of general interest to undergraduates in science and engineering.', 'http://ocw.mit.edu/courses/earth-atmospheric-and-planetary-sciences/12-006j-nonlinear-dynamics-i-chaos-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/earth-atmospheric-and-planetary-sciences/12-006j-nonlinear-dynamics-i-chaos-fall-2006/12-006jf06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2301, 'Automata, Computability, and Complexity', 'This course provides a challenging introduction to some of the central ideas of theoretical computer science.', 'This course provides a challenging introduction to some of the central ideas of theoretical computer science. Beginning in antiquity, the course will progress through finite automata, circuits and decision trees, Turing machines and computability, efficient algorithms and reducibility, the P versus NP problem, NP-completeness, the power of randomness, cryptography and one-way functions, computational learning theory, and quantum computing. It examines the classes of problems that can and cannot be solved by various kinds of machines. It tries to explain the key differences between computational models that affect their power.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-045j-automata-computability-and-complexity-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-045j-automata-computability-and-complexity-spring-2011/6-045js11.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2302, 'Introduction to Algorithms (SMA 5503)', 'This course teaches techniques for the design and analysis of efficient algorithms, emphasizing methods useful in practice.', 'This course teaches techniques for the design and analysis of efficient algorithms, emphasizing methods useful in practice. Topics covered include: sorting; search trees, heaps, and hashing; divide-and-conquer; dynamic programming; amortized analysis; graph algorithms; shortest paths; network flow; computational geometry; number-theoretic algorithms; polynomial and matrix calculations; caching; and parallel computing.This course was also taught as part of the Singapore-MIT Alliance (SMA) programme as course number SMA 5503 (Analysis and Design of Algorithms).', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-046j-introduction-to-algorithms-sma-5503-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-046j-introduction-to-algorithms-sma-5503-fall-2005/6-046jf05.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2303, 'Error-Correcting Codes Laboratory', 'This course introduces students to iterative decoding algorithms and the codes to which they are applied, including Turbo Codes, Low-Density Parity-Check Codes, and Serially-Concatenated Codes.', 'This course introduces students to iterative decoding algorithms and the codes to which they are applied, including Turbo Codes, Low-Density Parity-Check Codes, and Serially-Concatenated Codes. The course will begin with an introduction to the fundamental problems of Coding Theory and their mathematical formulations. This will be followed by a study of Belief Propagation--the probabilistic heuristic which underlies iterative decoding algorithms. Belief Propagation will then be applied to the decoding of Turbo, LDPC, and Serially-Concatenated codes. The technical portion of the course will conclude with a study of tools for explaining and predicting the behavior of iterative decoding algorithms, including EXIT charts and Density Evolution.', 'http://ocw.mit.edu/courses/mathematics/18-413-error-correcting-codes-laboratory-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-413-error-correcting-codes-laboratory-spring-2004/18-413s04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2304, 'Probability and Random Variables', 'This course introduces students to probability and random variables.', 'This course introduces students to probability and random variables. Topics include distribution functions, binomial, geometric, hypergeometric, and Poisson distributions. The other topics covered are uniform, exponential, normal, gamma and beta distributions; conditional probability; Bayes theorem; joint distributions; Chebyshev inequality; law of large numbers; and central limit theorem.', 'http://ocw.mit.edu/courses/mathematics/18-440-probability-and-random-variables-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-440-probability-and-random-variables-spring-2011/18-440s11.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2305, 'Statistics for Applications', 'This course is a broad treatment of statistics, concentrating on specific statistical techniques used in science and industry.', 'This course is a broad treatment of statistics, concentrating on specific statistical techniques used in science and industry. Topics include: hypothesis testing and estimation, confidence intervals, chi-square tests, nonparametric statistics, analysis of variance, regression, correlation, decision theory, and Bayesian statistics. Note: Please see the syllabus for a description of the different versions of 18.443 taught at MIT.', 'http://ocw.mit.edu/courses/mathematics/18-443-statistics-for-applications-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-443-statistics-for-applications-spring-2009/18-443s09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2306, 'Statistics for Applications', 'This course offers a broad treatment of statistics, concentrating on specific statistical techniques used in science and industry.', 'This course offers a broad treatment of statistics, concentrating on specific statistical techniques used in science and industry. Topics include: hypothesis testing and estimation, confidence intervals, chi-square tests, nonparametric statistics, analysis of variance, regression, and correlation. OCW offers an earlier version of this course, from Fall 2003. This newer version focuses less on estimation theory and more on multiple linear regression models. In addition, a number of Matlab examples are included here.', 'http://ocw.mit.edu/courses/mathematics/18-443-statistics-for-applications-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-443-statistics-for-applications-fall-2006/18-443f06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2307, 'Linear Algebra', 'This course offers a rigorous treatment of linear algebra, including vector spaces, systems of linear equations, bases, linear independence, matrices, determinants, eigenvalues, inner products, quadratic forms, and canonical forms of matrices.', 'This course offers a rigorous treatment of linear algebra, including vector spaces, systems of linear equations, bases, linear independence, matrices, determinants, eigenvalues, inner products, quadratic forms, and canonical forms of matrices. Compared with Linear Algebra (18.06), more emphasis is placed on theory and proofs.', 'http://ocw.mit.edu/courses/mathematics/18-700-linear-algebra-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-700-linear-algebra-fall-2005/18-700f05.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2308, 'Algebra I', 'This undergraduate level Algebra I course covers groups, vector spaces, linear transformations, symmetry groups, bilinear forms, and linear groups.', 'This undergraduate level Algebra I course covers groups, vector spaces, linear transformations, symmetry groups, bilinear forms, and linear groups.', 'http://ocw.mit.edu/courses/mathematics/18-701-algebra-i-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-701-algebra-i-fall-2010/18-701f10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2309, 'Algebra II', 'This undergraduate level course follows Algebra I.', 'This undergraduate level course follows Algebra I. Topics include group representations, rings, ideals, fields, polynomial rings, modules, factorization, integers in quadratic number fields, field extensions, and Galois theory.', 'http://ocw.mit.edu/courses/mathematics/18-702-algebra-ii-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-702-algebra-ii-spring-2011/18-702s11.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2310, 'Seminar in Algebra and Number Theory: Rational Points on Elliptic Curves', 'This is a seminar for mathematics majors, where the students present the lectures.', 'This is a seminar for mathematics majors, where the students present the lectures. No prior experience giving lectures is necessary.', 'http://ocw.mit.edu/courses/mathematics/18-704-seminar-in-algebra-and-number-theory-rational-points-on-elliptic-curves-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-704-seminar-in-algebra-and-number-theory-rational-points-on-elliptic-curves-fall-2004/18-704f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2311, 'Introduction to Representation Theory', 'The goal of this course is to give an undergraduate-level introduction to representation theory (of groups, Lie algebras, and associative algebras).', 'The goal of this course is to give an undergraduate-level introduction to representation theory (of groups, Lie algebras, and associative algebras). Representation theory is an area of mathematics which, roughly speaking, studies symmetry in linear spaces.', 'http://ocw.mit.edu/courses/mathematics/18-712-introduction-to-representation-theory-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-712-introduction-to-representation-theory-fall-2010/18-712f08.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2312, 'Theory of Numbers', 'This course is an elementary introduction to number theory with no algebraic prerequisites.', 'This course is an elementary introduction to number theory with no algebraic prerequisites. Topics covered include primes, congruences, quadratic reciprocity, diophantine equations, irrational numbers, continued fractions, and partitions.&nbsp; &nbsp;', 'http://ocw.mit.edu/courses/mathematics/18-781-theory-of-numbers-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-781-theory-of-numbers-spring-2012/18-781s12.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2313, 'Seminar in Topology', 'This course is a seminar in topology.', 'This course is a seminar in topology. The main mathematical goal is to learn about the fundamental group, homology and cohomology. The main non-mathematical goal is to obtain experience giving math talks.', 'http://ocw.mit.edu/courses/mathematics/18-904-seminar-in-topology-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-904-seminar-in-topology-spring-2011/18-904s11.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2314, 'Differential Geometry', 'This course is an introduction to differential geometry.', 'This course is an introduction to differential geometry. The course itself is mathematically rigorous, but still emphasizes concrete aspects of geometry, centered on the notion of curvature.', 'http://ocw.mit.edu/courses/mathematics/18-950-differential-geometry-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-950-differential-geometry-fall-2008/18-950f08.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2315, 'Seminar in Geometry', 'In this course, students take turns in giving lectures.', 'In this course, students take turns in giving lectures. For the most part, the lectures are based on Robert Osserman''s classic book A Survey of Minimal Surfaces, Dover Phoenix Editions. New York: Dover Publications, May 1, 2002. ISBN: 0486495140.', 'http://ocw.mit.edu/courses/mathematics/18-994-seminar-in-geometry-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-994-seminar-in-geometry-fall-2004/18-994f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2316, 'The Art of Counting', 'The subject of enumerative combinatorics deals with counting the number of elements of a finite set.', 'The subject of enumerative combinatorics deals with counting the number of elements of a finite set. For instance, the number of ways to write a positive integer n as a sum of positive integers, taking order into account, is 2n-1. We will be concerned primarily with bijective proofs, i.e., showing that two sets have the same number of elements by exhibiting a bijection (one-to-one correspondence) between them. This is a subject which requires little mathematical background to reach the frontiers of current research. Students will therefore have the opportunity to do original research. It might be necessary to limit enrollment.', 'http://ocw.mit.edu/courses/mathematics/18-s66-the-art-of-counting-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-s66-the-art-of-counting-spring-2003/18-s66s03.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2317, 'Introduction To MATLAB Programming', 'This course is intended to assist undergraduates with learning the basics of programming in general and programming MATLAB&reg; in particular.', 'This course is intended to assist undergraduates with learning the basics of programming in general and programming MATLAB&reg; in particular.', 'http://ocw.mit.edu/courses/mathematics/18-s997-introduction-to-matlab-programming-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-s997-introduction-to-matlab-programming-fall-2011/18-s997f11.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2318, 'Advanced Calculus for Engineers', 'This course analyzes the functions of a complex variable and the calculus of residues.', 'This course analyzes the functions of a complex variable and the calculus of residues. It also covers subjects such as ordinary differential equations, partial differential equations, Bessel and Legendre functions, and the Sturm-Liouville theory.', 'http://ocw.mit.edu/courses/mathematics/18-075-advanced-calculus-for-engineers-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-075-advanced-calculus-for-engineers-fall-2004/18-075f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2319, 'Mathematical Methods for Engineers II', 'This graduate-level course is a continuation of Mathematical Methods for Engineers I (18.', 'This graduate-level course is a continuation of Mathematical Methods for Engineers I (18.085). Topics include numerical methods; initial-value problems; network flows; and optimization.', 'http://ocw.mit.edu/courses/mathematics/18-086-mathematical-methods-for-engineers-ii-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-086-mathematical-methods-for-engineers-ii-spring-2006/18-086s06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2320, 'Teaching College-Level Science and Engineering', 'This participatory seminar focuses on the knowledge and skills necessary for teaching science and engineering in higher education.', 'This participatory seminar focuses on the knowledge and skills necessary for teaching science and engineering in higher education. This course is designed for graduate students interested in an academic career, and anyone else interested in teaching. Topics include theories of adult learning; course development; promoting active learning, problem-solving, and critical thinking in students; communicating with a diverse student body; using educational technology to further learning; lecturing; creating effective tests and assignments; and assessment and evaluation. Students research and present a relevant topic of particular interest. The subject is appropriate for both novices and those with teaching experience.', 'http://ocw.mit.edu/courses/chemistry/5-95j-teaching-college-level-science-and-engineering-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/chemistry/5-95j-teaching-college-level-science-and-engineering-fall-2012/5-95jf12.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2321, 'Teaching College-Level Science and Engineering', 'This participatory seminar focuses on the knowledge and skills necessary for teaching science and engineering in higher education.', 'This participatory seminar focuses on the knowledge and skills necessary for teaching science and engineering in higher education. This course is designed for graduate students interested in an academic career, and anyone else interested in teaching. Readings and discussions include: teaching equations for understanding, designing exam and homework questions, incorporating histories of science, creating absorbing lectures, teaching for transfer, the evils of PowerPoint, and planning a course. The subject is appropriate for both novices and those with teaching experience.', 'http://ocw.mit.edu/courses/chemistry/5-95j-teaching-college-level-science-and-engineering-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/chemistry/5-95j-teaching-college-level-science-and-engineering-spring-2009/5-95js09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2322, 'Measure and Integration', 'This graduate-level course covers Lebesgue''s integration theory with applications to analysis, including an introduction to convolution and the Fourier transform.', 'This graduate-level course covers Lebesgue''s integration theory with applications to analysis, including an introduction to convolution and the Fourier transform.', 'http://ocw.mit.edu/courses/mathematics/18-125-measure-and-integration-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-125-measure-and-integration-fall-2003/18-125f03.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2323, 'Differential Analysis', 'This is the first semester of a two-semester sequence on Differential Analysis.', 'This is the first semester of a two-semester sequence on Differential Analysis. Topics include fundamental solutions for elliptic; hyperbolic and parabolic differential operators; method of characteristics; review of Lebesgue integration; distributions; fourier transform; homogeneous distributions; asymptotic methods.', 'http://ocw.mit.edu/courses/mathematics/18-155-differential-analysis-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-155-differential-analysis-fall-2004/18-155f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2324, 'Differential Analysis', 'The main goal of this course is to give the students a solid foundation in the theory of elliptic and parabolic linear partial differential equations.', 'The main goal of this course is to give the students a solid foundation in the theory of elliptic and parabolic linear partial differential equations.&#160;It is the second semester of a two-semester, graduate-level sequence on Differential Analysis.', 'http://ocw.mit.edu/courses/mathematics/18-156-differential-analysis-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-156-differential-analysis-spring-2004/18-156s04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2325, 'Advanced Analytic Methods in Science and Engineering', 'Advanced Analytic Methods in Science and Engineering is a comprehensive treatment of the advanced methods of applied mathematics.', 'Advanced Analytic Methods in Science and Engineering is a comprehensive treatment of the advanced methods of applied mathematics. It was designed to strengthen the mathematical abilities of graduate students and train them to think on their own.', 'http://ocw.mit.edu/courses/mathematics/18-305-advanced-analytic-methods-in-science-and-engineering-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-305-advanced-analytic-methods-in-science-and-engineering-fall-2004/18-305f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2326, 'Advanced Partial Differential Equations with Applications', 'The focus of the course is the concepts and techniques for solving the partial differential equations (PDE) that permeate various scientific disciplines.', 'The focus of the course is the concepts and techniques for solving the partial differential equations (PDE) that permeate various scientific disciplines. The emphasis is on nonlinear PDE. Applications include problems from fluid dynamics, electrical and mechanical engineering, materials science, quantum mechanics, etc.', 'http://ocw.mit.edu/courses/mathematics/18-306-advanced-partial-differential-equations-with-applications-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-306-advanced-partial-differential-equations-with-applications-fall-2009/18-306f09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2327, 'Integral Equations', 'This course emphasizes concepts and techniques for solving integral equations from an applied mathematics perspective.', 'This course emphasizes concepts and techniques for solving integral equations from an applied mathematics perspective. Material is selected from the following topics: Volterra and Fredholm equations, Fredholm theory, the Hilbert-Schmidt theorem; Wiener-Hopf Method; Wiener-Hopf Method and partial differential equations; the Hilbert Problem and singular integral equations of Cauchy type; inverse scattering transform; and group theory. Examples are taken from fluid and solid mechanics, acoustics, quantum mechanics, and other applications.', 'http://ocw.mit.edu/courses/mathematics/18-307-integral-equations-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-307-integral-equations-spring-2006/18-307s06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2328, 'Combinatorial Theory: Hyperplane Arrangements', 'This is a graduate-level course in combinatorial theory.', 'This is a graduate-level course in combinatorial theory.&nbsp;The content varies year to year, according to the interests of the instructor and the students.&nbsp;The topic of this course is hyperplane arrangements, including background material from the theory of posets and matroids.', 'http://ocw.mit.edu/courses/mathematics/18-315-combinatorial-theory-hyperplane-arrangements-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-315-combinatorial-theory-hyperplane-arrangements-fall-2004/18-315f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2329, 'Topics in Algebraic Combinatorics', 'The course consists of a sampling of topics from algebraic combinatorics.', 'The course consists of a sampling of topics from algebraic combinatorics. The topics include the matrix-tree theorem and other applications of linear algebra, applications of commutative and exterior algebra to counting faces of simplicial complexes, and applications of algebra to tilings.', 'http://ocw.mit.edu/courses/mathematics/18-318-topics-in-algebraic-combinatorics-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-318-topics-in-algebraic-combinatorics-spring-2006/18-318s06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2330, 'Geometric Combinatorics', 'This course offers an introduction to discrete and computational geometry.', 'This course offers an introduction to discrete and computational geometry. Emphasis is placed on teaching methods in combinatorial geometry. Many results presented are recent, and include open (as yet unsolved) problems.', 'http://ocw.mit.edu/courses/mathematics/18-319-geometric-combinatorics-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-319-geometric-combinatorics-fall-2005/18-319f05.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2331, 'Introduction to Numerical Methods', 'This course offers an advanced introduction to numerical linear algebra.', 'This course offers an advanced introduction to numerical linear algebra. Topics include direct and iterative methods for linear systems, eigenvalue decompositions and QR/SVD factorizations, stability and accuracy of numerical algorithms, the IEEE floating point standard, sparse and structured matrices, preconditioning, linear algebra software. Problem sets require some knowledge of MATLAB&reg;.', 'http://ocw.mit.edu/courses/mathematics/18-335j-introduction-to-numerical-methods-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-335j-introduction-to-numerical-methods-fall-2010/18-335jf10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2332, 'Introduction to Numerical Methods', 'The focus of this course is on numerical linear algebra and numerical methods for solving ordinary differential equations.', 'The focus of this course is on numerical linear algebra and numerical methods for solving ordinary differential equations. Topics include linear systems of equations, least square problems, eigenvalue problems, and singular value problems.', 'http://ocw.mit.edu/courses/mathematics/18-335j-introduction-to-numerical-methods-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-335j-introduction-to-numerical-methods-fall-2004/18-335jf04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2333, 'Numerical Methods for Partial Differential Equations', 'This graduate-level course is an advanced introduction to applications and theory of numerical methods for solution of differential equations.', 'This graduate-level course is an advanced introduction to applications and theory of numerical methods for solution of differential equations. In particular, the course focuses on physically-arising partial differential equations, with emphasis on the fundamental ideas underlying various methods.', 'http://ocw.mit.edu/courses/mathematics/18-336-numerical-methods-for-partial-differential-equations-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-336-numerical-methods-for-partial-differential-equations-spring-2009/18-336s09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2334, 'Infinite Random Matrix Theory', 'In this course on the mathematics of infinite random matrices, students will learn about the tools such as the Stieltjes transform and Free Probability used to characterize infinite random matrices.', 'In this course on the mathematics of infinite random matrices, students will learn about the tools such as the Stieltjes transform and Free Probability used to characterize infinite random matrices.', 'http://ocw.mit.edu/courses/mathematics/18-338j-infinite-random-matrix-theory-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-338j-infinite-random-matrix-theory-fall-2004/18-338jf04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2335, 'Random Walks and Diffusion', 'This graduate-level subject explores various mathematical aspects of (discrete) random walks and (continuum) diffusion.', 'This graduate-level subject explores various mathematical aspects of (discrete) random walks and (continuum) diffusion. Applications include polymers, disordered media, turbulence, diffusion-limited aggregation, granular flow, and derivative securities.', 'http://ocw.mit.edu/courses/mathematics/18-366-random-walks-and-diffusion-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-366-random-walks-and-diffusion-fall-2006/18-366f06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2336, 'Mathematical Methods in Nanophotonics', 'Find out what solid-state physics has brought to Electromagnetism in the last 20 years.', 'Find out what solid-state physics has brought to Electromagnetism in the last 20 years. This course surveys the physics and mathematics of nanophotonics&mdash;electromagnetic waves in media structured on the scale of the wavelength. Topics include computational methods combined with high-level algebraic techniques borrowed from solid-state quantum mechanics: linear algebra and eigensystems, group theory, Bloch''s theorem and conservation laws, perturbation methods, and coupled-mode theories, to understand surprising optical phenomena from band gaps to slow light to nonlinear filters. Note: An earlier version of this course was published on OCW as 18.325 Topics in Applied Mathematics: Mathematical Methods in Nanophotonics, Fall 2005.', 'http://ocw.mit.edu/courses/mathematics/18-369-mathematical-methods-in-nanophotonics-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-369-mathematical-methods-in-nanophotonics-spring-2008/18-369s08.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2337, 'Nonlinear Dynamics and Waves', 'This graduate-level course provides a unified treatment of nonlinear oscillations and wave phenomena with applications to mechanical, optical, geophysical, fluid, electrical and flow-structure interaction problems.', 'This graduate-level course provides a unified treatment of nonlinear oscillations and wave phenomena with applications to mechanical, optical, geophysical, fluid, electrical and flow-structure interaction problems.', 'http://ocw.mit.edu/courses/mechanical-engineering/2-034j-nonlinear-dynamics-and-waves-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mechanical-engineering/2-034j-nonlinear-dynamics-and-waves-spring-2007/2-034js07.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2338, 'Nonlinear Dynamics and Chaos', 'This graduate level course focuses on nonlinear dynamics with applications.', 'This graduate level course focuses on nonlinear dynamics with applications. It takes an intuitive approach with emphasis on geometric thinking, computational and analytical methods and makes extensive use of demonstration software.', 'http://ocw.mit.edu/courses/mathematics/18-385j-nonlinear-dynamics-and-chaos-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-385j-nonlinear-dynamics-and-chaos-fall-2004/18-385jf04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2339, 'Theory of Computation', 'This graduate level course is more extensive and theoretical treatment of the material in Computability, and Complexity (6.', 'This graduate level course is more extensive and theoretical treatment of the material in Computability, and Complexity (6.045J / 18.400J). Topics include Automata and Language Theory, Computability Theory, and Complexity Theory.', 'http://ocw.mit.edu/courses/mathematics/18-404j-theory-of-computation-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-404j-theory-of-computation-fall-2006/18-404jf06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2340, 'Topics in Theoretical Computer Science: An Algorithmist''s Toolkit', 'This course covers a collection of geometric techniques that apply broadly in modern algorithm design.', 'This course covers a collection of geometric techniques that apply broadly in modern algorithm design.', 'http://ocw.mit.edu/courses/mathematics/18-409-topics-in-theoretical-computer-science-an-algorithmists-toolkit-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-409-topics-in-theoretical-computer-science-an-algorithmists-toolkit-fall-2009/18-409f09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2341, 'Behavior of Algorithms', 'This course is a study of Behavior of Algorithms and covers an area of current interest in theoretical computer science.', 'This course is a study of Behavior of Algorithms and covers an area of current interest in theoretical computer science. The topics vary from term to term. During this term, we discuss rigorous approaches to explaining the typical performance of algorithms with a focus on the following approaches:&nbsp;smoothed analysis, condition numbers/parametric analysis, and subclassing inputs.', 'http://ocw.mit.edu/courses/mathematics/18-409-behavior-of-algorithms-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-409-behavior-of-algorithms-spring-2002/18-409s02.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2342, 'Advanced Algorithms', 'This is a graduate course on the design and analysis of algorithms, covering several advanced topics not studied in typical introductory courses on algorithms.', 'This is a graduate course on the design and analysis of algorithms, covering several advanced topics not studied in typical introductory courses on algorithms. It is especially designed for doctoral students interested in theoretical computer science.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-854j-advanced-algorithms-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-854j-advanced-algorithms-fall-2008/6-854jf08.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2343, 'Randomized Algorithms', 'This course examines how randomization can be used to make algorithms simpler and more efficient via random sampling, random selection of witnesses, symmetry breaking, and Markov chains.', 'This course examines how randomization can be used to make algorithms simpler and more efficient via random sampling, random selection of witnesses, symmetry breaking, and Markov chains. Topics covered include: randomized computation; data structures (hash tables, skip lists); graph algorithms (minimum spanning trees, shortest paths, minimum cuts); geometric algorithms (convex hulls, linear programming in fixed or arbitrary dimension); approximate counting; parallel algorithms; online algorithms; derandomization techniques; and tools for probabilistic analysis of algorithms.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-856j-randomized-algorithms-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-856j-randomized-algorithms-fall-2002/6-856jf02.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2344, 'Introduction to Computational Molecular Biology', 'This course introduces the basic computational methods used to understand the cell on a molecular level.', 'This course introduces the basic computational methods used to understand the cell on a molecular level. It covers subjects such as the sequence alignment algorithms: dynamic programming, hashing, suffix trees, and Gibbs sampling. Furthermore, it focuses on computational approaches to: genetic and physical mapping; genome sequencing, assembly, and annotation; RNA expression and secondary structure; protein structure and folding; and molecular interactions and dynamics.', 'http://ocw.mit.edu/courses/mathematics/18-417-introduction-to-computational-molecular-biology-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-417-introduction-to-computational-molecular-biology-fall-2004/18-417f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2345, 'Advanced Topics in Cryptography', 'The topics covered in this course include interactive proofs, zero-knowledge proofs, zero-knowledge proofs of knowledge, non-interactive zero-knowledge proofs, secure protocols, two-party secure computation, multiparty secure computation, and chosen-ciphertext security.', 'The topics covered in this course include interactive proofs, zero-knowledge proofs, zero-knowledge proofs of knowledge, non-interactive zero-knowledge proofs, secure protocols, two-party secure computation, multiparty secure computation, and chosen-ciphertext security.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-876j-advanced-topics-in-cryptography-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-876j-advanced-topics-in-cryptography-spring-2003/6-876js03.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2346, 'Distributed Algorithms', 'Distributed algorithms are algorithms designed to run on multiple processors, without tight centralized control.', 'Distributed algorithms are algorithms designed to run on multiple processors, without tight centralized control. In general, they are harder to design and harder to understand than single-processor sequential algorithms. Distributed algorithms are used in many practical systems, ranging from large computer networks to multiprocessor shared-memory systems. They also have a rich theory, which forms the subject matter for this course. The core of the material will consist of basic distributed algorithms and impossibility results, as covered in Prof. Lynch''s book Distributed Algorithms. This will be supplemented by some updated material on topics such as self-stabilization, wait-free computability, and failure detectors, and some new material on scalable shared-memory concurrent programming.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-852j-distributed-algorithms-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-852j-distributed-algorithms-fall-2009/6-852jf09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2347, 'Topics in Statistics: Statistical Learning Theory', 'The main goal of this course is to study the generalization ability of a number of popular machine learning algorithms such as boosting, support vector machines and neural networks.', 'The main goal of this course is to study the generalization ability of a number of popular machine learning algorithms such as boosting, support vector machines and neural networks. Topics include Vapnik-Chervonenkis theory, concentration inequalities in product spaces, and other elements of empirical process theory.', 'http://ocw.mit.edu/courses/mathematics/18-465-topics-in-statistics-statistical-learning-theory-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-465-topics-in-statistics-statistical-learning-theory-spring-2007/18-465s07.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2348, 'Topics in Statistics: Nonparametrics and Robustness', 'This graduate-level course focuses on one-dimensional nonparametric statistics developed mainly from around 1945 and deals with order statistics and ranks, allowing very general distributions.', 'This graduate-level course focuses on one-dimensional nonparametric statistics developed mainly from around 1945 and deals with order statistics and ranks, allowing very general distributions. For multidimensional nonparametric statistics, an early approach was to choose a fixed coordinate system and work with order statistics and ranks in each coordinate. A more modern method, to be followed in this course, is to look for rotationally or affine invariant procedures. These can be based on empirical processes as in computer learning theory. Robustness, which developed mainly from around 1964, provides methods that are resistant to errors or outliers in the data, which can be arbitrarily large. Nonparametric methods tend to be robust.', 'http://ocw.mit.edu/courses/mathematics/18-465-topics-in-statistics-nonparametrics-and-robustness-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-465-topics-in-statistics-nonparametrics-and-robustness-spring-2005/18-465s05.JPG', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2349, 'Commutative Algebra', 'In this course students will learn about Noetherian rings and modules, Hilbert basis theorem, Cayley-Hamilton theorem, integral dependence, Noether normalization, the Nullstellensatz, localization, primary decomposition, DVRs, filtrations, length, Artin rings, Hilbert polynomials, tensor products, and dimension theory.', 'In this course students will learn about Noetherian rings and modules, Hilbert basis theorem, Cayley-Hamilton theorem, integral dependence, Noether normalization, the Nullstellensatz, localization, primary decomposition, DVRs, filtrations, length, Artin rings, Hilbert polynomials, tensor products, and dimension theory.', 'http://ocw.mit.edu/courses/mathematics/18-705-commutative-algebra-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-705-commutative-algebra-fall-2008/18-705f08.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2350, 'Algebraic Geometry', 'This course covers the fundamental notions and results about algebraic varieties over an algebraically closed field.', 'This course covers the fundamental notions and results about algebraic varieties over an algebraically closed field. It also analyzes the relations between complex algebraic varieties and complex analytic varieties.', 'http://ocw.mit.edu/courses/mathematics/18-725-algebraic-geometry-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-725-algebraic-geometry-fall-2003/18-725f03.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2351, 'Algebraic Geometry', 'This course provides an introduction to the language of schemes, properties of morphisms, and sheaf cohomology.', 'This course provides an introduction to the language of schemes, properties of morphisms, and sheaf cohomology. Together with 18.725 Algebraic Geometry, students gain an understanding of the basic notions and techniques of modern algebraic geometry.', 'http://ocw.mit.edu/courses/mathematics/18-726-algebraic-geometry-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-726-algebraic-geometry-spring-2009/18-726s09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2352, 'Topics in Algebraic Geometry: Intersection Theory on Moduli Spaces', 'The topics for this course vary each semester.', 'The topics for this course vary each semester. This semester, the course aims to introduce techniques for studying intersection theory on moduli spaces. In particular, it covers the geometry of homogeneous varieties, the Deligne-Mumford moduli spaces of stable curves and the Kontsevich moduli spaces of stable maps using intersection theory.', 'http://ocw.mit.edu/courses/mathematics/18-727-topics-in-algebraic-geometry-intersection-theory-on-moduli-spaces-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-727-topics-in-algebraic-geometry-intersection-theory-on-moduli-spaces-spring-2006/18-727s06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2353, 'Double Affine Hecke Algebras in Representation Theory, Combinatorics, Geometry, and Mathematical Physics', 'Double affine Hecke algebras (DAHA), also called Cherednik algebras, and their representations appear in many contexts: integrable systems (Calogero-Moser and Ruijsenaars models), algebraic geometry (Hilbert schemes), orthogonal polynomials, Lie theory, quantum groups, etc.', 'Double affine Hecke algebras (DAHA), also called Cherednik algebras, and their representations appear in many contexts: integrable systems (Calogero-Moser and Ruijsenaars models), algebraic geometry (Hilbert schemes), orthogonal polynomials, Lie theory, quantum groups, etc. In this course we will review the basic theory of DAHA and their representations, emphasizing their connections with other subjects and open problems.', 'http://ocw.mit.edu/courses/mathematics/18-735-double-affine-hecke-algebras-in-representation-theory-combinatorics-geometry-and-mathematical-physics-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-735-double-affine-hecke-algebras-in-representation-theory-combinatorics-geometry-and-mathematical-physics-fall-2009/18-735f09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2354, 'Introduction to Lie Groups', 'This course is devoted to the theory of Lie Groups with emphasis on its connections with Differential Geometry.', 'This course is devoted to the theory of Lie Groups with emphasis on its connections with Differential Geometry. The text for this class is&nbsp;Differential Geometry, Lie Groups and Symmetric Spaces by Sigurdur Helgason (American Mathematical Society, 2001). Much of the course material is based on Chapter&nbsp;I (first half) and Chapter&nbsp;II of the text. The text however develops basic Riemannian Geometry, Complex Manifolds, as well as a detailed theory of Semisimple Lie Groups and Symmetric Spaces.', 'http://ocw.mit.edu/courses/mathematics/18-755-introduction-to-lie-groups-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-755-introduction-to-lie-groups-fall-2004/18-755f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2355, 'Analytic Number Theory', 'This course is an introduction to analytic number theory, including the use of zeta functions and L-functions to prove distribution results concerning prime numbers (e.', 'This course is an introduction to analytic number theory, including the use of zeta functions and L-functions to prove distribution results concerning prime numbers (e.g., the prime number theorem in arithmetic progressions).', 'http://ocw.mit.edu/courses/mathematics/18-785-analytic-number-theory-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-785-analytic-number-theory-spring-2007/18-785s07.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2356, 'Topics in Algebraic Number Theory', 'This course provides an introduction to algebraic number theory.', 'This course provides an introduction to algebraic number theory. Topics covered include dedekind domains, unique factorization of prime ideals, number fields, splitting of primes, class group, lattice methods, finiteness of the class number, Dirichlet''s units theorem, local fields, ramification, discriminants.', 'http://ocw.mit.edu/courses/mathematics/18-786-topics-in-algebraic-number-theory-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-786-topics-in-algebraic-number-theory-spring-2010/18-786s10.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2357, 'Topics in Algebraic Number Theory', 'This course is a first course in algebraic number theory.', 'This course is a first course in algebraic number theory. Topics to be covered include number fields, class numbers, Dirichlet''s units theorem, cyclotomic fields, local fields, valuations, decomposition and inertia groups, ramification, basic analytic methods, and basic class field theory. An additional theme running throughout the course will be the use of computer algebra to investigate number-theoretic questions; this theme will appear primarily in the problem sets.', 'http://ocw.mit.edu/courses/mathematics/18-786-topics-in-algebraic-number-theory-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-786-topics-in-algebraic-number-theory-spring-2006/18-786s06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2358, 'Algebraic Topology II', 'In this second term of Algebraic Topology, the topics covered include fibrations, homotopy groups, the Hurewicz theorem, vector bundles, characteristic classes, cobordism, and possible further topics at the discretion of the instructor.', 'In this second term of Algebraic Topology, the topics covered include fibrations, homotopy groups, the Hurewicz theorem, vector bundles, characteristic classes, cobordism, and possible further topics at the discretion of the instructor.', 'http://ocw.mit.edu/courses/mathematics/18-906-algebraic-topology-ii-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-906-algebraic-topology-ii-spring-2006/18-906s06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2359, 'Topics in Algebraic Topology: The Sullivan Conjecture', 'The goal of this course is to describe some of the tools which enter into the proof of Sullivan''s conjecture.', 'The goal of this course is to describe some of the tools which enter into the proof of Sullivan''s conjecture.', 'http://ocw.mit.edu/courses/mathematics/18-917-topics-in-algebraic-topology-the-sullivan-conjecture-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-917-topics-in-algebraic-topology-the-sullivan-conjecture-fall-2007/18-917f07.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2360, 'Geometry of Manifolds', 'Geometry of Manifolds analyzes topics such as the differentiable manifolds and vector fields and forms.', 'Geometry of Manifolds analyzes topics such as the differentiable manifolds and vector fields and forms. It also makes an introduction to Lie groups, the de Rham theorem, and Riemannian manifolds.', 'http://ocw.mit.edu/courses/mathematics/18-965-geometry-of-manifolds-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-965-geometry-of-manifolds-fall-2004/18-965f04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2361, 'Topics in Geometry: Mirror Symmetry', 'This course will focus on various aspects of mirror symmetry.', 'This course will focus on various aspects of mirror symmetry. It is aimed at students who already have some basic knowledge in symplectic and complex geometry (18.966, or equivalent). The geometric concepts needed to formulate various mathematical versions of mirror symmetry will be introduced along the way, in variable levels of detail and rigor.', 'http://ocw.mit.edu/courses/mathematics/18-969-topics-in-geometry-mirror-symmetry-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-969-topics-in-geometry-mirror-symmetry-spring-2009/18-969s09.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2362, 'Topics in Geometry: Dirac Geometry', 'This is an introductory (i.', 'This is an introductory (i.e. first year graduate students are welcome and expected) course in generalized geometry, with a special emphasis on Dirac geometry, as developed by Courant, Weinstein, and Severa, as well as generalized complex geometry, as introduced by Hitchin. Dirac geometry is based on the idea of unifying the geometry of a Poisson structure with that of a closed 2-form, whereas generalized complex geometry unifies complex and symplectic geometry. For this reason, the latter is intimately related to the ideas of mirror symmetry.', 'http://ocw.mit.edu/courses/mathematics/18-969-topics-in-geometry-dirac-geometry-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-969-topics-in-geometry-dirac-geometry-fall-2006/18-969f06.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2363, 'Random Matrix Theory and Its Applications', 'This course is an introduction to the basics of random matrix theory, motivated by engineering and scientific applications.', 'This course is an introduction to the basics of random matrix theory, motivated by engineering and scientific applications.', 'http://ocw.mit.edu/courses/mathematics/18-996-random-matrix-theory-and-its-applications-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-996-random-matrix-theory-and-its-applications-spring-2004/18-996s04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2364, 'Simplicity Theory', 'This is an advanced topics course in model theory whose main theme is simple theories.', 'This is an advanced topics course in model theory whose main theme is simple theories. We treat simple theories in the framework of compact abstract theories, which is more general than that of first order theories. We cover the basic properties of independence (i.e., non-dividing) in simple theories, the characterization of simple theories by the existence of a notion of independence, and hyperimaginary canonical bases.', 'http://ocw.mit.edu/courses/mathematics/18-996a-simplicity-theory-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-996a-simplicity-theory-spring-2004/18-996as04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2365, 'Topics in Combinatorial Optimization', 'In this graduate-level course, we will be covering advanced topics in combinatorial optimization.', 'In this graduate-level course, we will be covering advanced topics in combinatorial optimization. We will start with non-bipartite matchings and cover many results extending the fundamental results of matchings, flows and matroids. The emphasis is on the derivation of purely combinatorial results, including min-max relations, and not so much on the corresponding algorithmic questions of how to find such objects. The intended audience consists of Ph.D. students interested in optimization, combinatorics, or combinatorial algorithms.', 'http://ocw.mit.edu/courses/mathematics/18-997-topics-in-combinatorial-optimization-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/mathematics/18-997-topics-in-combinatorial-optimization-spring-2004/18-997s04.jpg', 'Mathematics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2366, 'Physics I: Classical Mechanics', 'Physics I is a first-year, first-semester course that provides an introduction to Classical Mechanics.', 'Physics I is a first-year, first-semester course that provides an introduction to Classical Mechanics. It covers the basic concepts of Newtonian mechanics, fluid mechanics, and kinetic gas theory.', 'http://ocw.mit.edu/courses/physics/8-01sc-physics-i-classical-mechanics-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-01sc-physics-i-classical-mechanics-fall-2010/8-01scf10.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2367, 'Physics I', 'Physics I is a first-year physics course which introduces students to classical mechanics.', 'Physics I is a first-year physics course which introduces students to classical mechanics. Topics include: space and time; straight-line kinematics; motion in a plane; forces and equilibrium; experimental basis of Newton''s laws; particle dynamics; universal gravitation; collisions and conservation laws; work and potential energy; vibrational motion; conservative forces; inertial forces and non-inertial frames; central force motions; rigid bodies and rotational dynamics.', 'http://ocw.mit.edu/courses/physics/8-01-physics-i-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-01-physics-i-fall-2003/8-01f03.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2368, 'Physics I: Classical Mechanics', '8.', '8.01 is a first-semester freshman physics class in Newtonian Mechanics, Fluid Mechanics, and Kinetic Gas Theory. In addition to the basic concepts of Newtonian Mechanics, Fluid Mechanics, and Kinetic Gas Theory, a variety of interesting topics are covered in this course: Binary Stars, Neutron Stars, Black Holes, Resonance Phenomena, Musical Instruments, Stellar Collapse, Supernovae, Astronomical observations from very high flying balloons (lecture 35), and you will be allowed a peek into the intriguing Quantum World. Also by Walter Lewin Courses: Electricity and Magnetism (8.02) - with a complete set of 36 video lectures from the Spring of 2002 Vibrations and Waves (8.03) - with a complete set of 23 video lectures from the Fall of 2004 Talks: For The Love Of Physics - Professor of Physics Emeritus Walter Lewin''s last MIT lecture, complete with some of his most famous physics demonstrations to celebrate the publication of his new book. Videos featuring Walter Lewin on MIT TechTV', 'http://ocw.mit.edu/courses/physics/8-01-physics-i-classical-mechanics-fall-1999', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-01-physics-i-classical-mechanics-fall-1999/8-01f99.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2369, 'Physics I: Classical Mechanics', '8.', '8.01L is an introductory mechanics course, which covers all the topics covered in 8.01T. The class meets throughout the fall, and continues throughout the Independent Activities Period (IAP).', 'http://ocw.mit.edu/courses/physics/8-01l-physics-i-classical-mechanics-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-01l-physics-i-classical-mechanics-fall-2005/8-01lf05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2370, 'Physics I', 'This freshman-level course is an introduction to classical mechanics.', 'This freshman-level course is an introduction to classical mechanics. The subject is taught using the TEAL (Technology Enabled Active Learning) format which features small group interaction via table-top experiments utilizing laptops for data acquisition and problem solving workshops. Acknowledgements The TEAL project is supported by The Alex and Brit d''Arbeloff Fund for Excellence in MIT Education, MIT iCampus, the Davis Educational Foundation, the National Science Foundation, the Class of 1960 Endowment for Innovation in Education, the Class of 1951 Fund for Excellence in Education, the Class of 1955 Fund for Excellence in Teaching, and the Helena Foundation.', 'http://ocw.mit.edu/courses/physics/8-01t-physics-i-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-01t-physics-i-fall-2004/8-01tf04.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2371, 'Physics I: Classical Mechanics with an Experimental Focus', 'Physics I is a first-year physics course which introduces students to classical mechanics.', 'Physics I is a first-year physics course which introduces students to classical mechanics. This course has a hands-on focus, and approaches mechanics through take-home experiments. Topics include: kinematics, Newton''s laws of motion, universal gravitation, statics, conservation laws, energy, work, momentum, and special relativity.', 'http://ocw.mit.edu/courses/physics/8-01x-physics-i-classical-mechanics-with-an-experimental-focus-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-01x-physics-i-classical-mechanics-with-an-experimental-focus-fall-2002/8-01xf02.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2372, 'Physics II: Electricity and Magnetism', 'This freshman-level course is the second semester of introductory physics.', 'This freshman-level course is the second semester of introductory physics. The focus is on electricity and magnetism. The subject is taught using the TEAL (Technology Enabled Active Learning) format which utilizes small group interaction and current technology. The TEAL/Studio Project at MIT is a new approach to physics education designed to help students develop much better intuition about, and conceptual models of, physical phenomena. OpenCourseWare presents another version of 8.02: Electricity and Magnetism (Spring 2002) with Professor Walter Lewin, which includes 36 videotaped lectures. &nbsp; Staff Visualizations: Prof. John Belcher Instructors: Dr. Peter Dourmashkin Prof. Bruce Knuteson Prof. Gunther Roland Prof. Bolek Wyslouch Dr. Brian Wecht Prof. Eric Katsavounidis Prof. Robert Simcoe Prof. Joseph Formaggio Course Co-Administrators: Dr. Peter Dourmashkin Prof. Robert Redwine Technical Instructors: Andy Neely Matthew Strafuss Course Material: Dr. Peter Dourmashkin Prof. Eric Hudson Dr. Sen-Ben Liao Acknowledgements The TEAL project is supported by The Alex and Brit d''Arbeloff Fund for Excellence in MIT Education, MIT iCampus, the Davis Educational Foundation, the National Science Foundation, the Class of 1960 Endowment for Innovation in Education, the Class of 1951 Fund for Excellence in Education, the Class of 1955 Fund for Excellence in Teaching, and the Helena Foundation. Many people have contributed to the development of the course materials. (PDF)', 'http://ocw.mit.edu/courses/physics/8-02-physics-ii-electricity-and-magnetism-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-02-physics-ii-electricity-and-magnetism-spring-2007/8-02s07.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2373, 'Electricity and Magnetism', 'In addition to the basic concepts of Electromagnetism, a vast variety of interesting topics are covered in this course: Lightning, Pacemakers, Electric Shock Treatment, Electrocardiograms, Metal Detectors, Musical Instruments, Magnetic Levitation, Bullet Trains, Electric Motors, Radios, TV, Car Coils, Superconductivity, Aurora Borealis, Rainbows, Radio Telescopes, Interferometers, Particle Accelerators (a.', 'In addition to the basic concepts of Electromagnetism, a vast variety of interesting topics are covered in this course: Lightning, Pacemakers, Electric Shock Treatment, Electrocardiograms, Metal Detectors, Musical Instruments, Magnetic Levitation, Bullet Trains, Electric Motors, Radios, TV, Car Coils, Superconductivity, Aurora Borealis, Rainbows, Radio Telescopes, Interferometers, Particle Accelerators (a.k.a. Atom Smashers or Colliders), Mass Spectrometers, Red Sunsets, Blue Skies, Haloes around Sun and Moon, Color Perception, Doppler Effect, Big-Bang Cosmology. OpenCourseWare presents another version of 8.02T: Electricity and Magnetism. Also by Walter Lewin Courses: Classical Mechanics (8.01)- with a complete set of 35 video lectures from the Fall of 1999 Vibrations and Waves (8.03) - with a complete set of 23 video lectures from the Fall of 2004 Talks: For The Love Of Physics - Professor of Physics Emeritus Walter Lewin''s last MIT lecture, complete with some of his most famous physics demonstrations to celebrate the publication of his new book. Videos featuring Walter Lewin on MIT TechTV &nbsp;', 'http://ocw.mit.edu/courses/physics/8-02-electricity-and-magnetism-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-02-electricity-and-magnetism-spring-2002/8-02s02.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2374, 'Physics II: Electricity and Magnetism', 'This course runs parallel to 8.', 'This course runs parallel to 8.02, but assumes that students have some knowledge of vector calculus. The class introduces Maxwell''s equations, in both differential and integral form, along with electrostatic and magnetic vector potential, and the properties of dielectrics and magnetic materials. This class was taught by an undergraduate in the Experimental Study Group (ESG). Student instructors are paired with ESG faculty members, who advise and oversee the students'' teaching efforts.', 'http://ocw.mit.edu/courses/physics/8-022-physics-ii-electricity-and-magnetism-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-022-physics-ii-electricity-and-magnetism-fall-2006/8-022f06.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2375, 'Physics II: Electricity and Magnetism', 'Parallel to 8.', 'Parallel to 8.02: Physics II, but more advanced mathematically. Some knowledge of vector calculus assumed. Maxwell''s equations, in both differential and integral form. Electrostatic and magnetic vector potential. Properties of dielectrics and magnetic materials. In addition to the theoretical subject matter, several experiments in electricity and magnetism are performed by the students in the laboratory.', 'http://ocw.mit.edu/courses/physics/8-022-physics-ii-electricity-and-magnetism-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-022-physics-ii-electricity-and-magnetism-fall-2002/8-022f02.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2376, 'Electricity and Magnetism', 'This freshman-level course is the second semester of introductory physics.', 'This freshman-level course is the second semester of introductory physics. The focus is on electricity and magnetism. The subject is taught using the TEAL (Technology Enabled Active Learning) format which utilizes small group interaction and current technology. The TEAL/Studio Project at MIT is a new approach to physics education designed to help students develop much better intuition about, and conceptual models of, physical phenomena.&nbsp;OpenCourseWare presents another version of 8.02: Electricity and Magnetism (Spring 2002) with Professor Walter Lewin, which includes 36 videotaped lectures. Acknowledgements The TEAL project is supported by The Alex and Brit d''Arbeloff Fund for Excellence in MIT Education, MIT iCampus, the Davis Educational Foundation, the National Science Foundation, the Class of 1960 Endowment for Innovation in Education, the Class of 1951 Fund for Excellence in Education, the Class of 1955 Fund for Excellence in Teaching, and the Helena Foundation. Many people have contributed to the development of the course materials. (PDF)', 'http://ocw.mit.edu/courses/physics/8-02t-electricity-and-magnetism-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-02t-electricity-and-magnetism-spring-2005/8-02ts05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2377, 'Physics II: Electricity & Magnetism with an Experimental Focus', 'This course is an introduction to electromagnetism and electrostatics.', 'This course is an introduction to electromagnetism and electrostatics. Topics include: electric charge, Coulomb''s law, electric structure of matter, conductors and dielectrics, concepts of electrostatic field and potential, electrostatic energy, electric currents, magnetic fields, Ampere''s law, magnetic materials, time-varying fields, Faraday''s law of induction, basic electric circuits, electromagnetic waves, and Maxwell''s equations. The course has an experimental focus, and includes several experiments that are intended to illustrate the concepts being studied. Acknowledgements Prof. Roland wishes to acknowledge that the structure and content of this course owe much to the contributions of Prof. Ambrogio Fasoli.', 'http://ocw.mit.edu/courses/physics/8-02x-physics-ii-electricity-magnetism-with-an-experimental-focus-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-02x-physics-ii-electricity-magnetism-with-an-experimental-focus-spring-2005/8-02xs05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2378, 'Physics III', 'Mechanical vibrations and waves, simple harmonic motion, superposition, forced vibrations and resonance, coupled oscillations and normal modes, vibrations of continuous systems, reflection and refraction, phase and group velocity.', 'Mechanical vibrations and waves, simple harmonic motion, superposition, forced vibrations and resonance, coupled oscillations and normal modes, vibrations of continuous systems, reflection and refraction, phase and group velocity. Optics, wave solutions to Maxwell''s equations, polarization, Snell''s law, interference, Huygens''s principle, Fraunhofer diffraction, and gratings.', 'http://ocw.mit.edu/courses/physics/8-03-physics-iii-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-03-physics-iii-spring-2003/8-03s03.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2379, 'Relativity', 'This course, which concentrates on special relativity, is normally taken by physics majors in their sophomore year.', 'This course, which concentrates on special relativity, is normally taken by physics majors in their sophomore year. Topics include Einstein''s postulates, the Lorentz transformation, relativistic effects and paradoxes, and applications involving electromagnetism and particle physics. This course also provides a brief introduction to some concepts of general relativity, including the principle of equivalence, the Schwartzschild metric and black holes, and the FRW metric and cosmology.', 'http://ocw.mit.edu/courses/physics/8-033-relativity-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-033-relativity-fall-2006/8-033f06.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2380, 'Quantum Physics I', 'This course covers the experimental basis of quantum physics, introduces wave mechanics, SchrÃ¶dinger''s equation in a single dimension, and SchrÃ¶dinger''s equation in three dimensions.', 'This course covers the experimental basis of quantum physics, introduces wave mechanics, SchrÃ¶dinger''s equation in a single dimension, and SchrÃ¶dinger''s equation in three dimensions.', 'http://ocw.mit.edu/courses/physics/8-04-quantum-physics-i-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-04-quantum-physics-i-spring-2006/8-04s06.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2381, 'Quantum Physics III', 'Together, this course and its predecessor, 8.', 'Together, this course and its predecessor, 8.05: Quantum Physics II, cover quantum physics with applications drawn from modern physics. Topics in this course include units, time-independent approximation methods, the structure of one- and two-electron atoms, charged particles in a magnetic field, scattering, and time-dependent perturbation theory. In this second term, students are required to research and write a paper on a topic related to the content of 8.05 and 8.06.', 'http://ocw.mit.edu/courses/physics/8-06-quantum-physics-iii-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-06-quantum-physics-iii-spring-2005/8-06s05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2382, 'Electromagnetism II', 'This course is the second in a series on Electromagnetism beginning with Electromagnetism I (8.', 'This course is the second in a series on Electromagnetism beginning with Electromagnetism I (8.02 or 8.022). It is a survey of basic electromagnetic phenomena: electrostatics; magnetostatics; electromagnetic properties of matter; time-dependent electromagnetic fields; Maxwell''s equations; electromagnetic waves; emission, absorption, and scattering of radiation; and relativistic electrodynamics and mechanics.', 'http://ocw.mit.edu/courses/physics/8-07-electromagnetism-ii-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-07-electromagnetism-ii-fall-2005/8-07f05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2383, 'Statistical Physics II', 'This course covers probability distributions for classical and quantum systems.', 'This course covers probability distributions for classical and quantum systems. Topics include: Microcanonical, canonical, and grand canonical partition-functions and associated thermodynamic potentials. Also discussed are conditions of thermodynamic equilibrium for homogenous and heterogenous systems. The course follows 8.044, Statistical Physics I, and is second in this series of undergraduate Statistical Physics courses.', 'http://ocw.mit.edu/courses/physics/8-08-statistical-physics-ii-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-08-statistical-physics-ii-spring-2005/8-08s05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2384, 'Experimental Physics I & II "Junior Lab"', 'Junior Lab consists of two undergraduate courses in experimental physics.', 'Junior Lab consists of two undergraduate courses in experimental physics. The courses are offered by the MIT Physics Department, and are usually taken by Juniors (hence the name). Officially, the courses are called Experimental Physics I and II and are numbered 8.13 for the first half, given in the fall semester, and 8.14 for the second half, given in the spring. The purposes of Junior Lab are to give students hands-on experience with some of the experimental basis of modern physics and, in the process, to deepen their understanding of the relations between experiment and theory, mostly in atomic and nuclear physics. Each term, students choose 5 different experiments from a list of 21 total labs.', 'http://ocw.mit.edu/courses/physics/8-13-14-experimental-physics-i-ii-junior-lab-fall-2007-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-13-14-experimental-physics-i-ii-junior-lab-fall-2007-spring-2008/8-13-14s08.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2385, 'Introduction to Special Relativity', 'This course introduces the basic ideas and equations of Einstein''s Special Theory of Relativity.', 'This course introduces the basic ideas and equations of Einstein''s Special Theory of Relativity. If you have hoped to understand the physics of Lorentz contraction, time dilation, the &quot;twin paradox&quot;, and E=mc2, you''re in the right place.AcknowledgementsProf. Knuteson wishes to acknowledge that this course was originally designed and taught by Prof. Robert Jaffe.', 'http://ocw.mit.edu/courses/physics/8-20-introduction-to-special-relativity-january-iap-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-20-introduction-to-special-relativity-january-iap-2005/8-20iap05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2386, 'The Physics of Energy', 'This course is designed to give you the scientific understanding you need to answer questions like: How much energy can we really get from wind? How does a solar photovoltaic work? What is an OTEC (Ocean Thermal Energy Converter) and how does it work? What is the physics behind global warming? What makes engines efficient? How does a nuclear reactor work, and what are the realistic hazards? The course is designed for MIT sophomores, juniors, and seniors who want to understand the fundamental laws and physical processes that govern the sources, extraction, transmission, storage, degradation, and end uses of energy.', 'This course is designed to give you the scientific understanding you need to answer questions like: How much energy can we really get from wind? How does a solar photovoltaic work? What is an OTEC (Ocean Thermal Energy Converter) and how does it work? What is the physics behind global warming? What makes engines efficient? How does a nuclear reactor work, and what are the realistic hazards? The course is designed for MIT sophomores, juniors, and seniors who want to understand the fundamental laws and physical processes that govern the sources, extraction, transmission, storage, degradation, and end uses of energy.', 'http://ocw.mit.edu/courses/physics/8-21-the-physics-of-energy-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-21-the-physics-of-energy-fall-2009/8-21f09.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2387, 'Einstein, Oppenheimer, Feynman: Physics in the 20th Century', 'This course covers the role of physics and physicists during the 20th century, focusing on Einstein, Oppenheimer, and Feynman.', 'This course covers the role of physics and physicists during the 20th century, focusing on Einstein, Oppenheimer, and Feynman. Beyond just covering the scientific developments, institutional, cultural, and political contexts will also be examined.', 'http://ocw.mit.edu/courses/science-technology-and-society/sts-042j-einstein-oppenheimer-feynman-physics-in-the-20th-century-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/science-technology-and-society/sts-042j-einstein-oppenheimer-feynman-physics-in-the-20th-century-spring-2011/sts-042js11.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2388, 'Physics of Solids I', 'This course offers an introduction to the basic concepts of the quantum theory of solids.', 'This course offers an introduction to the basic concepts of the quantum theory of solids.', 'http://ocw.mit.edu/courses/physics/8-231-physics-of-solids-i-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-231-physics-of-solids-i-fall-2006/8-231f06.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2389, 'String Theory for Undergraduates', 'This course introduces string theory to undergraduate and is based upon Prof.', 'This course introduces string theory to undergraduate and is based upon Prof. Zwiebach''s textbook entitled A First Course in String Theory. Since string theory is quantum mechanics of a relativistic string, the foundations of the subject can be explained to students exposed to both special relativity and basic quantum mechanics. This course develops the aspects of string theory and makes it accessible to students familiar with basic electromagnetism and statistical mechanics.', 'http://ocw.mit.edu/courses/physics/8-251-string-theory-for-undergraduates-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-251-string-theory-for-undergraduates-spring-2007/8-251s07.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2390, 'Introduction to Astronomy', 'Introduction to Astronomy provides a quantitative introduction to the physics of the solar system, stars, the interstellar medium, the galaxy, and the&nbsp;universe, as determined from a variety of astronomical observations and models.', 'Introduction to Astronomy provides a quantitative introduction to the physics of the solar system, stars, the interstellar medium, the galaxy, and the&nbsp;universe, as determined from a variety of astronomical observations and models.', 'http://ocw.mit.edu/courses/physics/8-282j-introduction-to-astronomy-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-282j-introduction-to-astronomy-spring-2006/8-282js06.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2391, 'Modern Astrophysics', 'This course explores the applications of physics (Newtonian, statistical, and quantum mechanics) to fundamental processes that occur in celestial objects.', 'This course explores the applications of physics (Newtonian, statistical, and quantum mechanics) to fundamental processes that occur in celestial objects. The list of topics includes Main-sequence Stars, Collapsed Stars (White Dwarfs, Neutron Stars, and Black Holes), Pulsars, Supernovae, the Interstellar Medium, Galaxies, and as time permits, Active Galaxies, Quasars, and Cosmology. Observational data is also discussed.', 'http://ocw.mit.edu/courses/physics/8-284-modern-astrophysics-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-284-modern-astrophysics-spring-2006/8-284s06.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2392, 'The Early Universe', 'The Early Universe provides an introduction to modern cosmology.', 'The Early Universe provides an introduction to modern cosmology. The first half deals with the development of the big-bang theory from 1915 to 1980, and latter half with recent impact of particle theory.', 'http://ocw.mit.edu/courses/physics/8-286-the-early-universe-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-286-the-early-universe-spring-2004/8-286s04.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2393, 'Astrophysics I', 'This course provides a graduate-level introduction to stellar astrophysics.', 'This course provides a graduate-level introduction to stellar astrophysics. It covers a variety of topics, ranging from stellar structure and evolution to galactic dynamics and dark matter.', 'http://ocw.mit.edu/courses/physics/8-901-astrophysics-i-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-901-astrophysics-i-spring-2006/8-901s06.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2394, 'Electromagnetic Theory', 'Electromagnetic Theory covers the basic principles of electromagnetism: experimental basis, electrostatics, magnetic fields of steady currents, motional e.', 'Electromagnetic Theory covers the basic principles of electromagnetism: experimental basis, electrostatics, magnetic fields of steady currents, motional e.m.f. and electromagnetic induction, Maxwell''s equations, propagation and radiation of electromagnetic waves, electric and magnetic properties of matter, and conservation laws. This is a graduate level subject which uses appropriate mathematics but whose emphasis is on physical phenomena and principles.', 'http://ocw.mit.edu/courses/physics/8-311-electromagnetic-theory-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-311-electromagnetic-theory-spring-2004/8-311s04.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2395, 'Quantum Theory I', '8.', '8.321 is the first semester of a two-semester subject on quantum theory, stressing principles. Topics covered include: Hilbert spaces, observables, uncertainty relations, eigenvalue problems and methods for solution thereof, time-evolution in the Schrodinger, Heisenberg, and interaction pictures, connections between classical and quantum mechanics, path integrals, quantum mechanics in EM fields, angular momentum, time-independent perturbation theory, density operators, and quantum measurement.', 'http://ocw.mit.edu/courses/physics/8-321-quantum-theory-i-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-321-quantum-theory-i-fall-2002/8-321f02.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2396, 'Relativistic Quantum Field Theory I', '8.', '8.323, Relativistic Quantum Field Theory I, is a one-term self-contained subject in quantum field theory. Concepts and basic techniques are developed through applications in elementary particle physics, and condensed matter physics.', 'http://ocw.mit.edu/courses/physics/8-323-relativistic-quantum-field-theory-i-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-323-relativistic-quantum-field-theory-i-spring-2008/8-323s08.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2397, 'Relativistic Quantum Field Theory II', 'This course is the second course of the quantum field theory trimester sequence beginning with Relativistic Quantum Field Theory I (8.', 'This course is the second course of the quantum field theory trimester sequence beginning with Relativistic Quantum Field Theory I (8.323) and ending with Relativistic Quantum Field Theory III (8.325). It develops in depth some of the topics discussed in 8.323 and introduces some advanced material.', 'http://ocw.mit.edu/courses/physics/8-324-relativistic-quantum-field-theory-ii-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-324-relativistic-quantum-field-theory-ii-fall-2010/8-324f10.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2398, 'Relativistic Quantum Field Theory III', 'This course is the third and last term of the quantum field theory sequence.', 'This course is the third and last term of the quantum field theory sequence. Its aim is the proper theoretical discussion of the physics of the standard model. Topics include: quantum chromodynamics; the Higgs phenomenon and a description of the standard model; deep-inelastic scattering and structure functions; basics of lattice gauge theory; operator products and effective theories; detailed structure of the standard model; spontaneously broken gauge theory and its quantization; instantons and theta-vacua; topological defects; introduction to supersymmetry.', 'http://ocw.mit.edu/courses/physics/8-325-relativistic-quantum-field-theory-iii-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-325-relativistic-quantum-field-theory-iii-spring-2007/8-325s03.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2399, 'Statistical Mechanics I: Statistical Mechanics of Particles', 'Statistical Mechanics is a probabilistic approach to equilibrium properties of large numbers of degrees of freedom.', 'Statistical Mechanics is a probabilistic approach to equilibrium properties of large numbers of degrees of freedom. In this two-semester course, basic principles are examined. Topics include: thermodynamics, probability theory, kinetic theory, classical statistical mechanics, interacting systems, quantum statistical mechanics, and identical particles.', 'http://ocw.mit.edu/courses/physics/8-333-statistical-mechanics-i-statistical-mechanics-of-particles-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-333-statistical-mechanics-i-statistical-mechanics-of-particles-fall-2007/8-333f07.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2400, 'Statistical Mechanics II: Statistical Physics of Fields', 'This is the second term in a two-semester course on statistical mechanics.', 'This is the second term in a two-semester course on statistical mechanics. Basic principles are examined in 8.334, such as the laws of thermodynamics and the concepts of temperature, work, heat, and entropy. Topics from modern statistical mechanics are also explored including the hydrodynamic limit and classical field theories.', 'http://ocw.mit.edu/courses/physics/8-334-statistical-mechanics-ii-statistical-physics-of-fields-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-334-statistical-mechanics-ii-statistical-physics-of-fields-spring-2008/8-334s08.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2401, 'Classical Mechanics: A Computational Approach', 'We will study the fundamental principles of classical mechanics, with a modern emphasis on the qualitative structure of phase space.', 'We will study the fundamental principles of classical mechanics, with a modern emphasis on the qualitative structure of phase space. We will use computational ideas to formulate the principles of mechanics precisely. Expression in a computational framework encourages clear thinking and active exploration. We will consider the following topics: the Lagrangian formulation; action, variational principles, and equations of motion; Hamilton''s principle; conserved quantities; rigid bodies and tops; Hamiltonian formulation and canonical equations; surfaces of section; chaos; canonical transformations and generating functions; Liouville''s theorem and Poincar&eacute; integral invariants; Poincar&eacute;-Birkhoff and KAM theorems; invariant curves and cantori; nonlinear resonances; resonance overlap and transition to chaos; properties of chaotic motion. Ideas will be illustrated and supported with physical examples. We will make extensive use of computing to capture methods, for simulation, and for symbolic analysis.', 'http://ocw.mit.edu/courses/earth-atmospheric-and-planetary-sciences/12-620j-classical-mechanics-a-computational-approach-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/earth-atmospheric-and-planetary-sciences/12-620j-classical-mechanics-a-computational-approach-fall-2008/12-620jf08.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2402, 'Teaching College-Level Science and Engineering', 'This participatory seminar focuses on the knowledge and skills necessary for teaching science and engineering in higher education.', 'This participatory seminar focuses on the knowledge and skills necessary for teaching science and engineering in higher education. This course is designed for graduate students interested in an academic career, and anyone else interested in teaching. Topics include theories of adult learning; course development; promoting active learning, problem-solving, and critical thinking in students; communicating with a diverse student body; using educational technology to further learning; lecturing; creating effective tests and assignments; and assessment and evaluation. Students research and present a relevant topic of particular interest. The subject is appropriate for both novices and those with teaching experience.', 'http://ocw.mit.edu/courses/chemistry/5-95j-teaching-college-level-science-and-engineering-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/chemistry/5-95j-teaching-college-level-science-and-engineering-fall-2012/5-95jf12.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2403, 'Teaching College-Level Science and Engineering', 'This participatory seminar focuses on the knowledge and skills necessary for teaching science and engineering in higher education.', 'This participatory seminar focuses on the knowledge and skills necessary for teaching science and engineering in higher education. This course is designed for graduate students interested in an academic career, and anyone else interested in teaching. Readings and discussions include: teaching equations for understanding, designing exam and homework questions, incorporating histories of science, creating absorbing lectures, teaching for transfer, the evils of PowerPoint, and planning a course. The subject is appropriate for both novices and those with teaching experience.', 'http://ocw.mit.edu/courses/chemistry/5-95j-teaching-college-level-science-and-engineering-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/chemistry/5-95j-teaching-college-level-science-and-engineering-spring-2009/5-95js09.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2404, 'Atomic and Optical Physics II', 'This is the second of a two-semester subject sequence beginning with Atomic and Optical Physics I (8.', 'This is the second of a two-semester subject sequence beginning with Atomic and Optical Physics I (8.421) that provides the foundations for contemporary research in selected areas of atomic and optical physics. Topics covered include non-classical states of light, multi-photon processes, coherence, trapping and cooling, atomic interactions, and experimental methods.', 'http://ocw.mit.edu/courses/physics/8-422-atomic-and-optical-physics-ii-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-422-atomic-and-optical-physics-ii-spring-2005/8-422s05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2405, 'Theory of Solids II', 'This is the second term of a theoretical treatment of the physics of solids.', 'This is the second term of a theoretical treatment of the physics of solids. Topics covered include linear response theory; the physics of disorder; superconductivity; the local moment and itinerant magnetism; the Kondo problem and Fermi liquid theory.', 'http://ocw.mit.edu/courses/physics/8-512-theory-of-solids-ii-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-512-theory-of-solids-ii-spring-2009/8-512s09.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2406, 'Many-Body Theory for Condensed Matter Systems', 'This course covers the concepts and physical pictures behind various phenomena that appear in interacting many-body systems.', 'This course covers the concepts and physical pictures behind various phenomena that appear in interacting many-body systems. Visualization occurs through concentration on path integral, mean-field theories and semi-classical picture of fluctuations around mean-field state.', 'http://ocw.mit.edu/courses/physics/8-513-many-body-theory-for-condensed-matter-systems-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-513-many-body-theory-for-condensed-matter-systems-fall-2004/8-513f04.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2407, 'Strongly Correlated Systems in Condensed Matter Physics', 'In this course we shall develop theoretical methods suitable for the description of the many-body phenomena, such as Hamiltonian second-quantized operator formalism, Greens functions, path integral, functional integral, and the quantum kinetic equation.', 'In this course we shall develop theoretical methods suitable for the description of the many-body phenomena, such as Hamiltonian second-quantized operator formalism, Greens functions, path integral, functional integral, and the quantum kinetic equation. The concepts to be introduced include, but are not limited to, the random phase approximation, the mean field theory (aka saddle-point, or semiclassical approximation), the tunneling dynamics in imaginary time, instantons, Berry phase, coherent state path integral, renormalization group.', 'http://ocw.mit.edu/courses/physics/8-514-strongly-correlated-systems-in-condensed-matter-physics-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-514-strongly-correlated-systems-in-condensed-matter-physics-fall-2003/8-514f03.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2408, 'Systems Biology', 'This course introduces the mathematical modeling techniques needed to address key questions in modern biology.', 'This course introduces the mathematical modeling techniques needed to address key questions in modern biology. An overview of modeling techniques in molecular biology and genetics, cell biology and developmental biology is covered. Key experiments that validate mathematical models are also discussed, as well as molecular, cellular, and developmental systems biology, bacterial chemotaxis, genetic oscillators, control theory and genetic networks, and gradient sensing systems. Additional specific topics include: constructing and modeling of genetic networks, lambda phage as a genetic switch, synthetic genetic switches, circadian rhythms, reaction diffusion equations, local activation and global inhibition models, center finding networks, general pattern formation models, modeling cell-cell communication, quorum sensing, and finally, models for Drosophila development.', 'http://ocw.mit.edu/courses/physics/8-591j-systems-biology-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-591j-systems-biology-fall-2004/8-591jf04.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2409, 'Statistical Physics in Biology', 'Statistical Physics in Biology is a survey of problems at the interface of statistical physics and modern biology.', 'Statistical Physics in Biology is a survey of problems at the interface of statistical physics and modern biology. Topics include: bioinformatic methods for extracting information content of DNA; gene finding, sequence comparison, and phylogenetic trees; physical interactions responsible for structure of biopolymers; DNA double helix, secondary structure of RNA, and elements of protein folding; considerations of force, motion, and packaging; protein motors, membranes. We also look at collective behavior of biological elements, cellular networks, neural networks, and evolution.', 'http://ocw.mit.edu/courses/physics/8-592j-statistical-physics-in-biology-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-592j-statistical-physics-in-biology-spring-2005/8-592js05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2410, 'Introduction to Neural Networks', 'This course explores the organization of synaptic connectivity as the basis of neural computation and learning.', 'This course explores the organization of synaptic connectivity as the basis of neural computation and learning. Perceptrons and dynamical theories of recurrent networks including amplifiers, attractors, and hybrid computation are covered. Additional topics include backpropagation and Hebbian learning, as well as models of perception, motor control, memory, and neural development.', 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-641j-introduction-to-neural-networks-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/brain-and-cognitive-sciences/9-641j-introduction-to-neural-networks-spring-2005/9-641js05.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2411, 'Introduction to Plasma Physics I', 'In this course, students will learn about plasmas, the fourth state of matter.', 'In this course, students will learn about plasmas, the fourth state of matter. The plasma state dominates the visible universe, and is of increasing economic importance. Plasmas behave in lots of interesting and sometimes unexpected ways. The course is intended only as a first plasma physics course, but includes&nbsp;critical concepts needed for a foundation for further study. A solid undergraduate background in classical physics, electromagnetic theory including Maxwell''s equations, and mathematical familiarity with partial differential equations and complex analysis are prerequisites. The course introduces plasma phenomena relevant to energy generation by controlled thermonuclear fusion and to astrophysics, coulomb collisions and transport processes, motion of charged particles in magnetic fields, plasma confinement schemes, MHD models, simple equilibrium and stability analysis. It also covers two-fluid hydrodynamic plasma models, wave propagation in a magnetic field, kinetic theory, Vlasov plasma model, electron plasma waves and Landau damping, ion-acoustic waves, and&nbsp;streaming instabilities. A subject description tailored to fit the background and interests of the attending students is distributed shortly before and at the beginning of the subject.', 'http://ocw.mit.edu/courses/nuclear-engineering/22-611j-introduction-to-plasma-physics-i-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/nuclear-engineering/22-611j-introduction-to-plasma-physics-i-fall-2003/22-611jf03.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2412, 'Introduction to Nuclear and Particle Physics', 'The phenomenology and experimental foundations of particle and nuclear physics are explored in this course.', 'The phenomenology and experimental foundations of particle and nuclear physics are explored in this course. Emphasis is on the fundamental forces and particles, as well as composites.', 'http://ocw.mit.edu/courses/physics/8-701-introduction-to-nuclear-and-particle-physics-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-701-introduction-to-nuclear-and-particle-physics-spring-2004/8-701s04.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2413, 'String Theory', 'This is a one-semester class about gauge/gravity duality (often called AdS/CFT) and its applications.', 'This is a one-semester class about gauge/gravity duality (often called AdS/CFT) and its applications.', 'http://ocw.mit.edu/courses/physics/8-821-string-theory-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-821-string-theory-fall-2008/8-821f08.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2414, 'Selected Topics in Theoretical Particle Physics: Branes and Gauge Theory Dynamics', 'This course is an introduction to branes in string theory and their world volume dynamics.', 'This course is an introduction to branes in string theory and their world volume dynamics. Instead of looking at the theory from the point of view of the world-sheet observer, we will approach the problem from the point of view of an observer which lives on a brane. Instead of writing down conformal field theory on the world-sheet and studying the properties of these theories, we will look at various branes in string theory and ask how the physics on their world-volume looks like.', 'http://ocw.mit.edu/courses/physics/8-871-selected-topics-in-theoretical-particle-physics-branes-and-gauge-theory-dynamics-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-871-selected-topics-in-theoretical-particle-physics-branes-and-gauge-theory-dynamics-fall-2004/8-871f04.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2415, 'Astrophysics II', 'This is the second course in a two-semester sequence on astrophysics.', 'This is the second course in a two-semester sequence on astrophysics. Topics include galactic dynamics, groups and clusters on galaxies, phenomenological cosmology, Newtonian cosmology, Roberston-Walker models, and galaxy formation.', 'http://ocw.mit.edu/courses/physics/8-902-astrophysics-ii-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-902-astrophysics-ii-fall-2004/8-902f04.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2416, 'Cosmology', 'This course provides an overview of astrophysical cosmology with emphasis on the Cosmic Microwave Background (CMB) radiation, galaxies and related phenomena at high redshift, and cosmic structure formation.', 'This course provides an overview of astrophysical cosmology with emphasis on the Cosmic Microwave Background (CMB) radiation, galaxies and related phenomena at high redshift, and cosmic structure formation. Additional topics include cosmic inflation, nucleosynthesis and baryosynthesis, quasar (QSO) absorption lines, and gamma-ray bursts. Some background in general relativity is assumed.', 'http://ocw.mit.edu/courses/physics/8-942-cosmology-fall-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-942-cosmology-fall-2001/8-942f01.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2417, 'General Relativity', '8.', '8.962 is MIT''s graduate course in general relativity, which covers the basic principles of Einstein''s general theory of relativity, differential geometry, experimental tests of general relativity, black holes, and cosmology.', 'http://ocw.mit.edu/courses/physics/8-962-general-relativity-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-962-general-relativity-spring-2006/8-962s06.jpg', 'Physics', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2418, 'Competition in Telecommunications', 'Competition in Telecommunications provides an introduction to the economics, business strategies, and technology of telecommunications markets.', 'Competition in Telecommunications provides an introduction to the economics, business strategies, and technology of telecommunications markets.&nbsp;This includes markets for wireless communications, local and long-distance services, and customer equipment. The convergence of computers, cable TV and telecommunications and the competitive emergence of the Internet are covered in depth. A number of speakers from leading companies in the industry will give course lectures.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-020-competition-in-telecommunications-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-020-competition-in-telecommunications-fall-2003/15-020f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2419, 'Energy Decisions, Markets, and Policies', 'This course examines the choices and constraints regarding sources and uses of energy by households, firms, and governments through a number of frameworks to describe and explain behavior at various levels of aggregation.', 'This course examines the choices and constraints regarding sources and uses of energy by households, firms, and governments through a number of frameworks to describe and explain behavior at various levels of aggregation. Examples include a wide range of countries, scope, settings, and analytical approaches. This course is one of many OCW Energy Courses, and it is a core subject in MIT''s undergraduate Energy Studies Minor. This Institute-wide program complements the deep expertise obtained in any major with a broad understanding of the interlinked realms of science, technology, and social sciences as they relate to energy and associated environmental challenges.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-031j-energy-decisions-markets-and-policies-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-031j-energy-decisions-markets-and-policies-spring-2012/15-031js12.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2420, 'Optimization Methods in Management Science', '15.', '15.053 introduces students to the theory, algorithms, and applications of optimization. Optimization methodologies include linear programming, network optimization, integer programming, decision trees, and dynamic programming. The methods have applications to logistics, manufacturing, transportation, marketing, project management, and finance.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-053-optimization-methods-in-management-science-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-053-optimization-methods-in-management-science-spring-2007/15-053s07.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2421, 'Management Communication for Undergraduates', 'The goal of this course is to help students learn to communicate strategically within a professional setting.', 'The goal of this course is to help students learn to communicate strategically within a professional setting. Students are asked to analyze their intended audience, the purpose of their communication, and the context in which they are operating before developing the message. The course focuses specifically on improving students&rsquo; ability to write, speak, work in a team, and communicate across cultures in their roles as future managers.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-279-management-communication-for-undergraduates-fall-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-279-management-communication-for-undergraduates-fall-2012/15-279f12.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2422, 'Managerial Psychology', 'This course introduces you to behavioral science theories, methods, and tools and provides opportunities to use and apply them to problems you will encounter in your work and career.', 'This course introduces you to behavioral science theories, methods, and tools and provides opportunities to use and apply them to problems you will encounter in your work and career. The course material will begin with an overview of work and organizations in modern industrial society, and then examine individual behavior, move to behavior in groups or teams, and finally discuss organizations as a whole. It is expected that at the end of the course you will: (a) know something about managerial psychology, (b) know how to learn more, (c) understand the behavioral research process, and (d) develop skills in presenting your ideas in oral and written reports.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-301-managerial-psychology-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-301-managerial-psychology-fall-2006/15-301f06.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2423, 'Managerial Psychology Laboratory', 'We function in our personal and professional lives based on knowledge and intuitions.', 'We function in our personal and professional lives based on knowledge and intuitions. Our intuition that we know a lot is very powerful. But sometimes intuitions are accurate and sometimes they are not; without research, it is hard to tell. This course combines a few different goals: develop a critical eye for making inferences from data; be able to carry out simple data analysis; learn about managerial psychology; develop interesting new questions about managerial psychology and test these questions.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-301-managerial-psychology-laboratory-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-301-managerial-psychology-laboratory-fall-2004/15-301f04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2424, 'Managerial Psychology', 'This course introduces you to behavioral science theories, methods, and tools and provides opportunities to use and apply them to problems you will encounter in your work and career.', 'This course introduces you to behavioral science theories, methods, and tools and provides opportunities to use and apply them to problems you will encounter in your work and career. The course material will begin with an overview of work and organizations in modern industrial society, and then examine individual behavior, move to behavior in groups or teams, and finally discuss organizations as a whole. It is expected that at the end of the course you will: (a) know something about managerial psychology, (b) know how to learn more, (c) understand the behavioral research process, and (d) develop skills in presenting your ideas in oral and written reports.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-301-managerial-psychology-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-301-managerial-psychology-fall-2006/15-301f06.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2425, 'Managerial Psychology Laboratory', 'We function in our personal and professional lives based on knowledge and intuitions.', 'We function in our personal and professional lives based on knowledge and intuitions. Our intuition that we know a lot is very powerful. But sometimes intuitions are accurate and sometimes they are not; without research, it is hard to tell. This course combines a few different goals: develop a critical eye for making inferences from data; be able to carry out simple data analysis; learn about managerial psychology; develop interesting new questions about managerial psychology and test these questions.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-301-managerial-psychology-laboratory-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-301-managerial-psychology-laboratory-fall-2004/15-301f04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2426, 'Managerial Psychology Laboratory', 'Surveys social psychology and organization theory interpreted in the context of the managerial environment.', 'Surveys social psychology and organization theory interpreted in the context of the managerial environment. Shares lectures with 15.301, with a separate recitation required. 15.301 is intended primarily for non-Sloan students, both graduate and undergraduate. Deals with a number of diverse subjects, including motivation and reward systems for engineers and scientists in industry; the aging of technical groups; the management of R&amp;D matrix organizations; and the architecture of R&amp;D laboratories and its effect on communication patterns in the organization. 15.301 is a core subject for students majoring in management science. A laboratory is a required element of the course for these students. It involves projects of an applied nature in behavioral science. Emphasizes use of behavioral science research methods to test hypotheses concerning organizational behavior. Instruction and practice in communication include report writing, team decision-making, and oral and visual presentation.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-310-managerial-psychology-laboratory-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-310-managerial-psychology-laboratory-spring-2003/15-310s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2427, 'Integrating eSystems & Global Information Systems', 'The strategic importance of information technology is now widely accepted.', 'The strategic importance of information technology is now widely accepted. It has also become increasingly clear that the identification of strategic applications alone does not result in success for an organization. A careful coordination of strategic applications, information technologies, and organizational structures must be made to attain success. This course addresses strategic, technological, and organizational connectivity issues to support effective and meaningful integration of information and systems. This course is especially relevant to those who wish to effectively exploit information technology and create new business processes and opportunities.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-565j-integrating-esystems-global-information-systems-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-565j-integrating-esystems-global-information-systems-spring-2002/15-565js02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2428, 'Practical Information Technology Management', 'The course purpose is to provide the substance and skill necessary to make sound business decisions relating to information systems and to work with senior line managers in the resolution of issues and problems in this area.', 'The course purpose is to provide the substance and skill necessary to make sound business decisions relating to information systems and to work with senior line managers in the resolution of issues and problems in this area. Categories of issues which will be addressed in the course include: How do IT and its various manifestations in business, such as the Internet, affect current&nbsp;and future COMPETITIVENESS? How do we align business strategy and plans with IT strategy and IT plans? How can we&nbsp;ENGAGE executives in learning and leading IT-related change? How do we&nbsp;IMPLEMENT new systems,&nbsp;CHANGE work behavior,&nbsp;MANAGE projects? How should we&nbsp;ORGANIZE and&nbsp;GOVERN IT in an organization?', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-568a-practical-information-technology-management-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-568a-practical-information-technology-management-spring-2005/15-568as05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2429, 'Integrating eSystems & Global Information Systems', 'The strategic importance of information technology is now widely accepted.', 'The strategic importance of information technology is now widely accepted. It has also become increasingly clear that the identification of strategic applications alone does not result in success for an organization. A careful coordination of strategic applications, information technologies, and organizational structures must be made to attain success. This course addresses strategic, technological, and organizational connectivity issues to support effective and meaningful integration of information and systems. This course is especially relevant to those who wish to effectively exploit information technology and create new business processes and opportunities.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-565j-integrating-esystems-global-information-systems-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-565j-integrating-esystems-global-information-systems-spring-2002/15-565js02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2430, 'Patents, Copyrights, and the Law of Intellectual Property', 'This weekly seminar examines key concepts of U.', 'This weekly seminar examines key concepts of U.S. intellectual property law, with emphasis on patents and copyrights and a briefer look at trade secrets and trademarks. Current issues relating to information technologies and business methods will be highlighted. The seminar has no prerequisites, and is designed for both graduate students and undergraduates. Half of the seats in the seminar are reserved for students from MIT departments other than Sloan.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-628-patents-copyrights-and-the-law-of-intellectual-property-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-628-patents-copyrights-and-the-law-of-intellectual-property-spring-2003/15-628s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2431, 'Law for the Entrepreneur and Manager', 'This course provides a basic understanding of legal issues that corporations face during their existence.', 'This course provides a basic understanding of legal issues that corporations face during their existence. The course starts by providing the basic building blocks of business law. We then follow a firm through its life cycle from its &quot;breakaway&quot; from an established firm through it going public. The materials covered during 15.647 (the first half of the semester) emphasize the organization and financing of the company. In the second half of the course we examine a broad array of law-sensitive issues relating to intellectual property, product development, M&amp;A transactions, international trade, the duties of directors and officers, business disputes, and bankruptcy and reorganization. The goal of the course is not to impart technical legal skills, but to enhance the judgment which students will bring to their responsibilities as entrepreneurs, managers in established companies, or consultants and advisors. There are two take-home exercises, and no exams. Students wishing to take only the first half of the Manager''s Legal Function should register for 15.647, which meets only during the H1 period.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-615-law-for-the-entrepreneur-and-manager-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-615-law-for-the-entrepreneur-and-manager-spring-2003/15-615s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2432, 'People and Organizations', '15.', '15.668 People and Organizations examines the historical evolution and current human and organizational contexts in which scientists, engineers and other professionals work. It outlines today''s major challenges facing the management profession. The course uses interactive exercises, simulations and problems to develop critical skills in negotiations, teamwork and leadership. Students will be introduced to concepts and tools to analyze work and leadership experiences in optional undergraduate fieldwork projects.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-668-people-and-organizations-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-668-people-and-organizations-fall-2010/15-668f10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2433, 'System Dynamics Self Study', 'Many books and thousands of papers cover the field of system dynamics.', 'Many books and thousands of papers cover the field of system dynamics. With all of these resources available, it can be difficult to know where to begin. The System Dynamics in Education Project at MIT put together these resources to help people sort through the vast library of books and papers on system dynamics. This course site includes a collection of papers and computer exercises entitled &ldquo;Road Maps,&rdquo; as well as a collection of assignments and solutions that were initially part of a guided study to system dynamics.&nbsp; Note that while the level of the course indicated in the upper right corner of the screen is ''Undergraduate / Graduate,'' the material is suitable for people ranging from K-12 students to chief executives of corporations.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-988-system-dynamics-self-study-fall-1998-spring-1999', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-988-system-dynamics-self-study-fall-1998-spring-1999/15-988f98.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2434, 'Economic Analysis for Business Decisions', '15.', '15.010 is the Sloan School''s core subject in microeconomics, with sections for non-Sloan students labeled 15.011. Our objective is to give you a working knowledge of the analytical tools that bear most directly on the economic decisions firms must regularly make. We will emphasize market structure and industrial performance, including the strategic interaction of firms. We will examine the behavior of individual markets -- and the producers and consumers that sell and buy in those markets -- in some detail, focusing on cost analysis, the determinants of market demand, pricing strategy, market power, and the implications of government regulatory policies. We will also examine the implications of economics on other business practices, such as incentive plans, auctions, and transfer pricing.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-010-economic-analysis-for-business-decisions-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-010-economic-analysis-for-business-decisions-fall-2004/15-010f04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2435, 'Economic Analysis for Business Decisions', '15.', '15.010 is the Sloan School''s core subject in microeconomics, with sections for non-Sloan students labeled 15.011. Our objective is to give you a working knowledge of the analytical tools that bear most directly on the economic decisions firms must regularly make. We will emphasize market structure and industrial performance, including the strategic interaction of firms. We will examine the behavior of individual markets -- and the producers and consumers that sell and buy in those markets -- in some detail, focusing on cost analysis, the determinants of market demand, pricing strategy, market power, and the implications of government regulatory policies. We will also examine the implications of economics on other business practices, such as incentive plans, auctions, and transfer pricing.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-010-economic-analysis-for-business-decisions-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-010-economic-analysis-for-business-decisions-fall-2004/15-010f04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2436, 'Applied Macro- and International Economics', '15.', '15.014 focuses on using case studies to investigate the macroenvironment in which firms operate.&nbsp;The course is divided in five parts: Basic tools of macroeconomic management Evaluation of different economic development strategies Crises in emerging markets: causes, solutions, and prevention Problems faced by transition economies Challenges of developed countries &nbsp; This course is a full-term version of Applied Macro- and International Economics (15.012), with additional topics.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-014-applied-macro-and-international-economics-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-014-applied-macro-and-international-economics-spring-2004/15-014s04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2437, 'Macro and International Economics', '15.', '15.015 Macro and International Economics focuses on the policy and economic environment of firms. This subject divided in three parts. The first part of the course is a study of the closed economy and how monetary and fiscal policy interacts with employment, GNP, inflation, and interest rates. Next, the course provides an examination of national economic strategies for development and growth and recent financial and currency crises in emerging markets. Finally, the course addresses the problems faced by transition economies and the role of institutions both as the engine of growth, and as the constraints for policy.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-015-macro-and-international-economics-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-015-macro-and-international-economics-fall-2011/15-015f11.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2438, 'Real Estate Economics', 'This course, offered by the MIT Center for Real Estate, focuses on developing an understanding of the macroeconomic factors that shape and influence markets for real property.', 'This course, offered by the MIT Center for Real Estate, focuses on developing an understanding of the macroeconomic factors that shape and influence markets for real property. We will develop the theory of land markets and locational choice. The material covered includes studies of changing economic activities, demographic trends, transportation and local government behavior as they affect real estate.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-433j-real-estate-economics-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-433j-real-estate-economics-fall-2008/11-433jf08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2439, 'Applied Economics for Managers', 'The fact of scarcity forces individuals, firms, and societies to choose among alternative uses &#8211; or allocations &#8211; of its limited resources.', 'The fact of scarcity forces individuals, firms, and societies to choose among alternative uses &#8211; or allocations &#8211; of its limited resources. Accordingly, the first part of this summer course seeks to understand how economists model the choice process of individual consumers and firms, and how markets work to coordinate these choices. It also examines how well markets perform this function using the economist''s criterion of market efficiency. Overall, this course focuses on microeconomics, with some topics from macroeconomics and international trade. It emphasizes the integration of theory, data, and judgment in the analysis of corporate decisions and public policy, and in the assessment of changing U.S. and international business environments.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-024-applied-economics-for-managers-summer-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-024-applied-economics-for-managers-summer-2004/15-024su04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2440, 'Engineering, Economics and Regulation of the Electric Power Sector', 'The course presents an in-depth interdisciplinary perspective of electric power systems, with regulation providing the link among the engineering, economic, legal and environmental viewpoints.', 'The course presents an in-depth interdisciplinary perspective of electric power systems, with regulation providing the link among the engineering, economic, legal and environmental viewpoints. Generation dispatch, demand response, optimal network flows, risk allocation, reliability of service, renewable energy sources, ancillary services, tariff design, distributed generation, rural electrification, environmental impacts and strategic sustainability issues will be among the topics addressed under both traditional and competitive regulatory frameworks.', 'http://ocw.mit.edu/courses/engineering-systems-division/esd-934-engineering-economics-and-regulation-of-the-electric-power-sector-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/engineering-systems-division/esd-934-engineering-economics-and-regulation-of-the-electric-power-sector-spring-2010/esd-934s10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2441, 'Game Theory for Managers', 'This half-term course examines the choices that we make which affect others and the choices others make that affect us.', 'This half-term course examines the choices that we make which affect others and the choices others make that affect us. Such situations are known as ''games'' and game-playing, while sounding whimsical, is serious business. Managers frequently play ''games'' both within the firm and outside it&nbsp;&ndash; with competitors, customers, regulators, and even capital markets! The goal of this course is to enhance&nbsp;a student''s ability to think strategically in complex, interactive environments. Knowledge of game theory will give students an advantage in such strategic settings. The course is structured around three ''themes for acquiring advantage in games'': commitment / strategic moves, exploiting hidden information, and limited rationality.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-040-game-theory-for-managers-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-040-game-theory-for-managers-spring-2004/15-040s04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2442, 'Data, Models, and Decisions', 'This course is designed to introduce first-year MBA students to the fundamental quantitative techniques of using data to make informed management decisions.', 'This course is designed to introduce first-year MBA students to the fundamental quantitative techniques of using data to make informed management decisions. In particular, the course focuses on various ways of modeling, or thinking structurally about, decision problems in order to enhance decision-making skills. Topics include decision analysis, probability, random variables, statistical estimation, regression, simulation, linear optimization, as well as nonlinear and discrete optimization. Management cases are used extensively to illustrate the practical use of modeling tools to improve the management practice.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-060-data-models-and-decisions-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-060-data-models-and-decisions-fall-2007/15-060f07.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2443, 'Data Mining', 'Data that has relevance for managerial decisions is accumulating at an incredible rate due to a host of technological advances.', 'Data that has relevance for managerial decisions is accumulating at an incredible rate due to a host of technological advances. Electronic data capture has become inexpensive and ubiquitous as a by-product of innovations such as the internet, e-commerce, electronic banking, point-of-sale devices, bar-code readers, and intelligent machines. Such data is often stored in data warehouses and data marts specifically intended for management decision support. Data mining is a rapidly growing field that is concerned with developing techniques to assist managers to make intelligent use of these repositories. A number of successful applications have been reported in areas such as credit rating, fraud detection, database marketing, customer relationship management, and stock market investments. The field of data mining has evolved from the disciplines of statistics and artificial intelligence. This course will examine methods that have emerged from both fields and proven to be of value in recognizing patterns and making predictions from an applications perspective. We will survey applications and provide an opportunity for hands-on experimentation with algorithms for data mining using easy-to- use software and cases.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-062-data-mining-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-062-data-mining-spring-2003/15-062s03.JPG', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2444, 'Communicating With Data', 'Communicating With Data has a distinctive structure and content, combining fundamental quantitative techniques of using data to make informed management decisions with illustrations of how real decision makers, even highly trained professionals, fall prey to errors and biases in their understanding.', 'Communicating With Data has a distinctive structure and content, combining fundamental quantitative techniques of using data to make informed management decisions with illustrations of how real decision makers, even highly trained professionals, fall prey to errors and biases in their understanding. We present the fundamental concepts underlying the quantitative techniques as a way of thinking, not just a way of calculating, in order to enhance decision-making skills.&nbsp;Rather than survey all of the techniques of management science, we stress those fundamental concepts and tools that we believe are most important for the practical analysis of management decisions, presenting the material as much as possible in the context of realistic business situations from a variety of settings. Exercises and examples drawn from marketing, finance, operations management, strategy, and other management functions.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-063-communicating-with-data-summer-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-063-communicating-with-data-summer-2003/15-063su03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2445, 'Competitive Decision-Making and Negotiation', 'This course is centered on twelve negotiation exercises that simulate competitive business situations.', 'This course is centered on twelve negotiation exercises that simulate competitive business situations. Specific topics covered include distributive bargaining (split the pie!), mixed motive bargaining (several issues at stake) with two and with more than two parties, auctions and fair division. Ethical dilemmas in negotiation are discussed at various times throughout the course. There are two principal objectives for this course. The first is to provide you with negotiation tools that enable you to achieve your negotiation objectives in a fair and responsible fashion. The second is to ''learn by doing.'' That is, we provide a forum in which you actively apply these tools to a wide variety of business oriented negotiation settings.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-067-competitive-decision-making-and-negotiation-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-067-competitive-decision-making-and-negotiation-spring-2003/15-067s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2446, 'Advanced Stochastic Processes', 'The class covers the analysis and modeling of stochastic processes.', 'The class covers the analysis and modeling of stochastic processes. Topics include measure theoretic probability, martingales, filtration, and stopping theorems, elements of large deviations theory, Brownian motion and reflected Brownian motion, stochastic integration and Ito calculus and functional limit theorems. In addition, the class will go over some applications to finance theory, insurance, queueing and inventory models.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-070-advanced-stochastic-processes-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-070-advanced-stochastic-processes-fall-2005/15-070f05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2447, 'Queues: Theory and Applications', 'This class deals with the modeling and analysis of queueing systems, with applications in communications, manufacturing, computers, call centers, service industries and transportation.', 'This class deals with the modeling and analysis of queueing systems, with applications in communications, manufacturing, computers, call centers, service industries and transportation. Topics include birth-death processes and simple Markovian queues, networks of queues and product form networks, single and multi-server queues, multi-class queueing networks, fluid models, adversarial queueing networks, heavy-traffic theory and diffusion approximations. The course will cover state of the art results which lead to research opportunities.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-072j-queues-theory-and-applications-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-072j-queues-theory-and-applications-spring-2006/15-072js06-.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2448, 'Logistical and Transportation Planning Methods', 'The class will cover quantitative techniques of Operations Research with emphasis on applications in transportation systems analysis (urban, air, ocean, highway, pick-up and delivery systems) and in the planning and design of logistically oriented urban service systems (e.', 'The class will cover quantitative techniques of Operations Research with emphasis on applications in transportation systems analysis (urban, air, ocean, highway, pick-up and delivery systems) and in the planning and design of logistically oriented urban service systems (e.g., fire and police departments, emergency medical services, emergency repair services). It presents a unified study of functions of random variables, geometrical probability, multi-server queueing theory, spatial location theory, network analysis and graph theory, and relevant methods of simulation. There will be discussion focused on the difficulty of implementation, among other topics.', 'http://ocw.mit.edu/courses/civil-and-environmental-engineering/1-203j-logistical-and-transportation-planning-methods-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/civil-and-environmental-engineering/1-203j-logistical-and-transportation-planning-methods-fall-2004/1-203jf04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2449, 'Introduction to Mathematical Programming', 'This course is an introduction to linear optimization and its extensions emphasizing the underlying mathematical structures, geometrical ideas, algorithms and solutions of practical problems.', 'This course is an introduction to linear optimization and its extensions emphasizing the underlying mathematical structures, geometrical ideas, algorithms and solutions of practical problems. The topics covered include: formulations, the geometry of linear optimization, duality theory, the simplex method, sensitivity analysis, robust optimization, large scale optimization network flows, solving problems with an exponential number of constraints and the ellipsoid method, interior point methods, semidefinite optimization, solving real world problems problems with computer software, discrete optimization formulations and algorithms.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-251j-introduction-to-mathematical-programming-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-251j-introduction-to-mathematical-programming-fall-2009/6-251jf09.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2450, 'Network Optimization', '15.', '15.082J/6.855J/ESD.78J is a graduate subject in the theory and practice of network flows and its extensions. Network flow problems form a subclass of linear programming problems with applications to transportation, logistics, manufacturing, computer science, project management, and finance, as well as a number of other domains. This subject will survey some of the applications of network flows and focus on key special cases of network flow problems including the following: the shortest path problem, the maximum flow problem, the minimum cost flow problem, and the multi-commodity flow problem. We will also consider other extensions of network flow problems.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-082j-network-optimization-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-082j-network-optimization-fall-2010/15-082jf10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2451, 'Nonlinear Programming', 'This course introduces students to the fundamentals of nonlinear optimization theory and methods.', 'This course introduces students to the fundamentals of nonlinear optimization theory and methods. Topics include unconstrained and constrained optimization, linear and quadratic programming, Lagrange and conic duality theory, interior-point algorithms and theory, Lagrangian relaxation, generalized programming, and semi-definite programming. Algorithmic methods used in the class include steepest descent, Newton''s method, conditional gradient and subgradient optimization, interior-point methods and penalty and barrier methods.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-084j-nonlinear-programming-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-084j-nonlinear-programming-spring-2004/15-084jf04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2452, 'Nonlinear Programming', '6.', '6.252J is a course in the department''s ''Communication, Control, and Signal Processing'' concentration. This course provides a unified analytical and computational approach to nonlinear optimization problems. The topics covered in this course include: unconstrained optimization methods, constrained optimization methods, convex analysis, Lagrangian relaxation, nondifferentiable optimization, and applications in integer programming. There is also a comprehensive treatment of optimality conditions, Lagrange multiplier theory, and duality theory. Throughout the course, applications are drawn from control, communications, power systems, and resource allocation problems.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-252j-nonlinear-programming-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-252j-nonlinear-programming-spring-2003/6-252js03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2453, 'Fundamentals of Probability', 'This is a course on the fundamentals of probability geared towards first- or second-year graduate students who are interested in a rigorous development of the subject.', 'This is a course on the fundamentals of probability geared towards first- or second-year graduate students who are interested in a rigorous development of the subject. The course covers most of the topics in 6.431 (sample space, random variables, expectations, transforms, Bernoulli and Poisson processes, finite Markov chains, limit theorems) but at a faster pace and in more depth. There are also a number of additional topics, such as language, terminology, and key results from measure theory; interchange of limits and expectations; multivariate Gaussian distributions; deeper understanding of conditional distributions and expectations.', 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-436j-fundamentals-of-probability-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/electrical-engineering-and-computer-science/6-436j-fundamentals-of-probability-fall-2008/6-436jf08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2454, 'Systems Optimization: Models and Computation (SMA 5223)', 'This class is an applications-oriented course covering the modeling of large-scale systems in decision-making domains and the optimization of such systems using state-of-the-art optimization tools.', 'This class is an applications-oriented course covering the modeling of large-scale systems in decision-making domains and the optimization of such systems using state-of-the-art optimization tools. Application domains include: transportation and logistics planning, pattern classification and image processing, data mining, design of structures, scheduling in large systems, supply-chain management, financial engineering, and telecommunications systems planning. Modeling tools and techniques include linear, network, discrete and nonlinear optimization, heuristic methods, sensitivity and post-optimality analysis, decomposition methods for large-scale systems, and stochastic optimization. This course was also taught as part of the Singapore-MIT Alliance (SMA) programme as course number SMA 5223 (System Optimisation: Models and Computation).', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-094j-systems-optimization-models-and-computation-sma-5223-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-094j-systems-optimization-models-and-computation-sma-5223-spring-2004/15-094js04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2455, 'Prediction: Machine Learning and Statistics', 'Prediction is at the heart of almost every scientific discipline, and the study of generalization (that is, prediction) from data is the central topic of machine learning and statistics, and more generally, data mining.', 'Prediction is at the heart of almost every scientific discipline, and the study of generalization (that is, prediction) from data is the central topic of machine learning and statistics, and more generally, data mining. Machine learning and statistical methods are used throughout the scientific world for their use in handling the &quot;information overload&quot; that characterizes our current digital age. Machine learning developed from the artificial intelligence community, mainly within the last 30 years, at the same time that statistics has made major advances due to the availability of modern computing. However, parts of these two fields aim at the same goal, that is, of prediction from data. This course provides a selection of the most important topics from both of these subjects.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-097-prediction-machine-learning-and-statistics-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-097-prediction-machine-learning-and-statistics-spring-2012/15-097s12.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2456, 'Special Seminar in Applied Probability and Stochastic Processes', 'This seminar is intended for doctoral students and discusses topics in applied probability.', 'This seminar is intended for doctoral students and discusses topics in applied probability. This semester includes a variety of fields, namely statistical physics (local weak convergence and correlation decay), artificial intelligence (belief propagation algorithms), computer science (random K-SAT problem, coloring, average case complexity) and electrical engineering (low density parity check (LDPC) codes).', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-098-special-seminar-in-applied-probability-and-stochastic-processes-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-098-special-seminar-in-applied-probability-and-stochastic-processes-spring-2006/15-098s06.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2457, 'Principles and Practice of Drug Development', 'This course serves as a description and critical assessment of the major issues and stages of developing a pharmaceutical or biopharmaceutical.', 'This course serves as a description and critical assessment of the major issues and stages of developing a pharmaceutical or biopharmaceutical. Topics covered include drug discovery, preclinical development, clinical investigation, manufacturing and regulatory issues considered for small and large molecules, and economic and financial considerations of the drug development process. A multidisciplinary perspective is provided by the faculty, who represent clinical, life, and management sciences. Various industry guests also participate.', 'http://ocw.mit.edu/courses/chemical-engineering/10-547j-principles-and-practice-of-drug-development-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/chemical-engineering/10-547j-principles-and-practice-of-drug-development-fall-2005/10-547jf05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2458, 'Global Strategy and Organization', 'This subject focuses on the specifics of strategy and organization of the multinational company, and provides a framework for formulating successful and adaptive strategies in an increasingly complex world economy.', 'This subject focuses on the specifics of strategy and organization of the multinational company, and provides a framework for formulating successful and adaptive strategies in an increasingly complex world economy. Topics include the globalization of industries, the continuing role of country factors in competition, organization of multinational enterprises, and building global networks. This particular version of the subject is taught and tailored specifically to those enrolled in the MIT Sloan Fellows Program.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-220-global-strategy-and-organization-spring-2012', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-220-global-strategy-and-organization-spring-2012/15-220s12.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2459, 'Global Strategy and Organization', 'Companies today confront an increasing array of choices regarding markets, locations for key activities, outsourcing and ownership modes, and organization and processes for managing across borders.', 'Companies today confront an increasing array of choices regarding markets, locations for key activities, outsourcing and ownership modes, and organization and processes for managing across borders. This course provides students with the conceptual tools necessary to understand and work effectively in today''s interconnected world by developing strategic perspectives that link this changing environment, the state of the global industry, and the capabilities and position of the firm. The goal of this subject is to provide the foundations for taking effective action in the multi-layered world of international business. The first section of the course provides frameworks for identifying and taking advantage of the opportunities presented in a dynamic global environment at the level of the country and industry. The second section of the course focuses on firm-level strategic choices regarding where to engage in which activities. The third section focuses on the challenges of integrating the multiple perspectives, functions, and interests that constitute the multinational firm.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-220-global-strategy-and-organization-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-220-global-strategy-and-organization-spring-2008/15-220s08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2460, 'Global Markets, National Politics and the Competitive Advantage of Firms', 'This course examines the opportunities and risks firms face in today''s global world.', 'This course examines the opportunities and risks firms face in today''s global world. The course provides conceptual tools for analyzing how governments and a variety of social and economic institutions influence competition among firms embedded in different national settings. Public policies and institutions that shape competitive outcomes are examined through cases and analytic readings on different companies and industries operating in both developed and emerging markets.&nbsp; In addition to traditional case/class discussions, this course will include some presentations by various guest speakers. The hope is that greater exposure to/interaction with these real-world practitioners will ''bring to life'' some of the issues discussed in the readings/cases. Whenever possible, informal dinners and/or coffees will be organized for small groups of students interested in meeting with our guest speakers.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-224-global-markets-national-politics-and-the-competitive-advantage-of-firms-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-224-global-markets-national-politics-and-the-competitive-advantage-of-firms-spring-2003/15-224s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2461, 'Economy and Business in Modern China and India', 'As markets or production bases, China and India are becoming important and integral players in the global economy.', 'As markets or production bases, China and India are becoming important and integral players in the global economy. Foreign direct investment (FDI), portfolio investments and outsourcing businesses have increased dramatically in these two economies. Despite the rising importance of these two economies on the world stage, our knowledge and analysis of these two countries in an integrated manner has remained poor. The two are often lumped together by business analysts as ''emerging markets,'' despite the substantial differences in their political systems, reform policies and business organizations. Academics, in contrast, have tended to treat the two countries separately, preferring to specialize in issues and questions specific to one or the other country. The purpose of this course is to analyze these two countries within a coherent analytical framework. Our learning model is inductive, and heavily based on class discussions and participation. The group projects should aim at integrating analysis, knowledge and understanding of these two countries. We will also experiment with other forms of group projects, such as creating and working on business plans and those projects that integrate research from field trips with more traditional research, such as library research. There is no prerequisite but 15.012 (Applied Macro- and International Economics) and 15.223 (Global Markets, National Policies and the Competitive Advantage of Firms) are highly recommended.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-225-economy-and-business-in-modern-china-and-india-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-225-economy-and-business-in-modern-china-and-india-spring-2008/15-225s08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2462, 'Literature, Ethics, Authority', 'Literature, Ethics, and Authority uses story in the form of readings and movies to address the relationship between ethics and leadership.', 'Literature, Ethics, and Authority uses story in the form of readings and movies to address the relationship between ethics and leadership. The course covers a range of topics, from issues of diversity and gender in the workplace to coping with the human realities of war and death. The course syllabus includes short stories, novels, plays, works of non-fiction, and films, and is representative of many different cultures and nationalities. This class is taught as a seminar to encourage discussion of these issues.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-269-literature-ethics-authority-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-269-literature-ethics-authority-fall-2009/15-269f09.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2463, 'Ethical Practice: Professionalism, Social Responsibility, and the Purpose of the Corporation', 'This course is an introduction to ethics in business, with a focus on business management.', 'This course is an introduction to ethics in business, with a focus on business management. Over the course of thirteen sessions, students explore theoretical concepts in business ethics, and cases representing the challenges they will likely face as managers. Individual sessions take the form of moderated discussion, with occasional short lectures from instructor.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-270-ethical-practice-professionalism-social-responsibility-and-the-purpose-of-the-corporation-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-270-ethical-practice-professionalism-social-responsibility-and-the-purpose-of-the-corporation-spring-2010/15-270s10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2464, 'Special Seminar in Communications: Leadership and Personal Effectiveness Coaching', 'This course builds on the work done concurrently in 15.', 'This course builds on the work done concurrently in 15.280 Communication for Managers and 15.311 Organizational Processes in the first semester of the MBA program. 15.280 is offered for 6 units and 15.277 provides an additional 3 units for a total of 9 units in Managerial Communication. 15.277 acts as a lab component to 15.280 and provides students additional opportunities to hone their communication skills through a variety of in-class exercises. Emphasis is on both individual and team communication.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-277-special-seminar-in-communications-leadership-and-personal-effectiveness-coaching-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-277-special-seminar-in-communications-leadership-and-personal-effectiveness-coaching-fall-2008/15-277f08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2465, 'Communication for Managers', 'Writing and speaking skills necessary for a career in management.', 'Writing and speaking skills necessary for a career in management. Students polish communication strategies and methods through discussion, examples, and practice. Several written and oral assignments, most based on material from other subjects and from career development activities. Schedule and curriculum coordinated with 15.311 Organizational Processes class. Restricted to first-year Sloan School of Management graduate students. Students may also enroll in 15.277 Special Seminar in Communication: Leadership and Personal Effectiveness Coaching. 15.280 is offered for 6 units and 15.277 provides an additional 3 units for a total of 9 units in Managerial Communication. 15.277 acts as a lab component to 15.280 and provides students additional opportunities to hone their communication skills through a variety of in-class exercises.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-280-communication-for-managers-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-280-communication-for-managers-fall-2008/15-280f08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2466, 'Communication Skills for Academics', 'Your success as an academic will depend heavily on your ability to communicate to fellow researchers in your discipline, to colleagues in your department and university, to undergraduate and graduate students, and perhaps even to the public at large.', 'Your success as an academic will depend heavily on your ability to communicate to fellow researchers in your discipline, to colleagues in your department and university, to undergraduate and graduate students, and perhaps even to the public at large. Communicating well in an academic setting depends not only on following the basic rules that govern all good communication (for example, tailoring the message to meet the needs of a specific audience), but also on adhering to the particular norms of academic genres. The purpose of this course, then, is threefold. First, the course will acquaint you with guidelines that will help you create well-crafted academic communication. Second, it will give you the opportunity to practice your communication skills and to receive extensive feedback from your colleagues and from me.&nbsp;You will write and/or revise an article manuscript or conference paper, present a conference paper or job talk, write a manuscript peer review, and engage in various other communication exercises. The article and talk, which are the major assignments of the course, will be based on material from your own doctoral studies. Third, the course will provide an opportunity for you to learn about professional norms for a range of activities that surround the academic enterprise, including, for example, the scholarly publication process and the job search process.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-289-communication-skills-for-academics-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-289-communication-skills-for-academics-spring-2002/15-289s02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2467, 'Organizational Processes', 'Organizational Processes enhances students'' ability to take effective action in complex organizational settings by providing the analytic tools needed to analyze, manage, and lead the organizations of the future.', 'Organizational Processes enhances students'' ability to take effective action in complex organizational settings by providing the analytic tools needed to analyze, manage, and lead the organizations of the future. Emphasis is placed on the importance of the organizational context in influencing which individual styles and skills are effective.&nbsp;The subject centers on three complementary perspectives, or ''lenses'', on an organization: political, cultural, and strategic design. Students enrolled in this class are also jointly enrolled in 15.328, Team Project, in order to complete a field study of an&nbsp;organizational change initiative. Organizational Processes also operates in conjunction with 15.280, Communication for Managers, by sharing certain assignments and holding some joint classes.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-311-organizational-processes-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-311-organizational-processes-fall-2003/15-311f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2468, 'Building and Leading Effective Teams', 'This course is an intensive one-week introduction to leadership, teams, and learning communities.', 'This course is an intensive one-week introduction to leadership, teams, and learning communities. The class meets daily for&nbsp;five days. The class serves as an introduction of concepts and uses a variety of experiential exercises to develop individual and team skills, as well as supportive relationships within the Leaders for Manufacturing class.&nbsp;As part of the focus on leadership, it discusses the idea of the ''Universe Within'', the images, thoughts, and experiences that are internal to all leaders.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-316-building-and-leading-effective-teams-summer-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-316-building-and-leading-effective-teams-summer-2005/15-316su05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2469, 'Strategic Organizational Design', '15.', '15.320 Strategic Organizational Design focuses on effective organizational design in both traditional and innovative organizations, with special emphasis on innovative organizational forms that can provide strategic advantage. Topics include when to use functional, divisional, or matrix organizations, how IT creates new organizational possibilities, and examples of innovative organizational possibilities, such as democratic decision-making, crowd-based organizations, internal resource markets, and other forms of collective intelligence. Team projects include inventing new possibilities for real organizations.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-320-strategic-organizational-design-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-320-strategic-organizational-design-spring-2011/15-320s11.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2470, 'Leading Organizations II', 'Through lectures, discussions, and class exercises, 15.', 'Through lectures, discussions, and class exercises, 15.322 analyzes the human processes underlying organizational behavior and change. The class makes students aware of the challenge of organizational change and equips them to better handle it. There are many psychological and sociological phenomena that regularly occur in organizations, though many of these forces are difficult to see. The aim is to increase the students'' understanding of these forces &#8211; in themselves and in others&#160;&#8211; so they become more visible and manageable. The prerequisite for this course is 15.321, Leading Organizations I.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-322-leading-organizations-ii-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-322-leading-organizations-ii-fall-2003/15-322f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2471, 'Team Project', 'The Team Project has the goals of (1) developing teamwork and leadership skills and (2) learning from the analysis of a change initiative in a real-world company using concepts from other core courses.', 'The Team Project has the goals of (1) developing teamwork and leadership skills and (2) learning from the analysis of a change initiative in a real-world company using concepts from other core courses. This class has no regular class schedule or weekly readings. Almost everything is oriented around your team and your project, with only a few deadlines.&nbsp;Each team is responsible for analyzing a recent, ongoing, or anticipated initiative at a real company. Examples might be a strategic reorientation, organizational restructuring, introduction of a new technology, or worker participation program. This course is closely integrated with other MBA core classes: readings are assigned through Organizational Processes (15.311) and oral presentations are given in Communication for Managers (15.280).', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-328-team-project-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-328-team-project-fall-2003/15-328f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2472, 'Organizations and Environments', 'The goal of this doctoral course is to familiarize students with major conceptual frameworks, debates, and developments in contemporary organization theory.', 'The goal of this doctoral course is to familiarize students with major conceptual frameworks, debates, and developments in contemporary organization theory. This is an inter-disciplinary domain of inquiry drawing primarily from sociology, and secondarily from economics, psychology, anthropology, and political science. The course focuses on inter-organizational processes, and also addresses the economic, institutional and cultural contexts that organizations must face. This is an introduction to a vast and multifaceted domain of inquiry. Due to time limitations, this course will touch lightly on many important topics, and neglect others entirely; its design resembles more a map than an encyclopedia. Also, given the focus on theoretical matters, methodological issues will move to the background. Empirical material will be used to illustrate how knowledge is produced from a particular standpoint and trying to answer particular questions, leaving the bulk of the discussion on quantitative and qualitative procedures to seminars such as 15.347, 15.348, and the like.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-342j-organizations-and-environments-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-342j-organizations-and-environments-fall-2004/15-342jf04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2473, 'Managing Transformations in Work, Organizations, and Society', 'The course focuses on skills managers need to adapt to current sweeping changes in the nature of work and the workforce, in business organizations and their roles in society, and in the institutions that interact with work, particularly the labor market, community and family-centered groups.', 'The course focuses on skills managers need to adapt to current sweeping changes in the nature of work and the workforce, in business organizations and their roles in society, and in the institutions that interact with work, particularly the labor market, community and family-centered groups. This year''s teaching will be the basis for a workshop session at the Sloan School''s 50th Anniversary Convocation. The course will involve a mix of on-campus and off-campus students taking the course via distance learning, and professionals from a variety of organizations who will participate in specific modules of interest to them. One session will be linked to colleagues at Cambridge University in England where a parallel course is being offered. Managerial issues addressed are associated with managing changes and innovations occurring in the nature of work and organizations and the role of the corporation in society. Topics covered include the changing social contract at work, integrating work and family, managing diversity, managing strategic labor-management partnerships, and managing relations between the firm and its multiple stakeholders. Subject is open to distance learning as well as on-campus students and to industry participants.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-343-managing-transformations-in-work-organizations-and-society-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-343-managing-transformations-in-work-organizations-and-society-spring-2002/15-343s02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2474, 'Doctoral Seminar in Research Methods I', 'This course is designed to lay the foundations of good empirical research in the social sciences.', 'This course is designed to lay the foundations of good empirical research in the social sciences.&nbsp;It does not deal with specific techniques per se, but rather with the assumptions and the logic underlying social research. Students become acquainted with a variety of approaches to research design, and are helped to develop their own research projects and to evaluate the products of empirical research.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-347-doctoral-seminar-in-research-methods-i-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-347-doctoral-seminar-in-research-methods-i-fall-2004/15-347f04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2475, 'Managing Innovation and Entrepreneurship', 'This course discusses the basics every manager needs to organize successful technology-driven innovation in both entrepreneurial and established firms.', 'This course discusses the basics every manager needs to organize successful technology-driven innovation in both entrepreneurial and established firms. We start by examining innovation-based strategies as a source of competitive advantage and then examine how to build organizations that excel at identifying, building and commercializing technological innovations. Major topics include how the innovation process works; creating an organizational environment that rewards innovation and entrepreneurship; designing appropriate innovation processes (e.g. stage-gate, portfolio management); organizing to take advantage of internal and external sources of innovation; and structuring entrepreneurial and established organizations for effective innovation. The course examines how entrepreneurs can shape their firms so that they continuously build and commercialize valuable innovations. Many of the examples also focus on how established firms can become more entrepreneurial in their approach to innovation.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-351-managing-innovation-and-entrepreneurship-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-351-managing-innovation-and-entrepreneurship-spring-2008/15-351s08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2476, 'Managing the Innovation Process', 'This course approaches ''managing the innovation process'' through five levels of analysis: individual, team, network, organizational, and industrial.', 'This course approaches ''managing the innovation process'' through five levels of analysis: individual, team, network, organizational, and industrial. At each level of analysis, particular attention is given to the conditions under which innovation processes succeed and fail. The weekly readings consist of a mixture of book chapters, journal articles, and cases, and an online forum will be used for further discussion of the required readings outside of class. Tuesday classes will begin with a reflection exercise that entails critical thinking about the topic for the week, followed by an activity and lecture introducing material found both within and outside of the readings. Thursday classes will begin with a case analysis completed in small groups, followed by a discussion based on the issues raised in the case and online forum. The primary goal of the course is to expose students to a variety of perspectives on innovation, while building on past work experiences and preparing for work experiences in the future.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-351-managing-the-innovation-process-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-351-managing-the-innovation-process-fall-2002/15-351f02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2477, 'Managing Innovation: Emerging Trends', 'Important emerging trends in innovation are identified, and their implications for innovation management are explored.', 'Important emerging trends in innovation are identified, and their implications for innovation management are explored. Major topics to be discussed include the trend to open information (''open source'') rather than protected intellectual property; the distribution of innovation over many independent but collaborating actors; and toolkits that empower users to innovate for themselves.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-352-managing-innovation-emerging-trends-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-352-managing-innovation-emerging-trends-spring-2005/15-352s05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2478, 'The Software Business', 'This subject is a seminar-style course aimed at anyone who is interested in founding a software company or working for a software company or company that uses software technology extensively as a senior manager, developer, or product/program manager.', 'This subject is a seminar-style course aimed at anyone who is interested in founding a software company or working for a software company or company that uses software technology extensively as a senior manager, developer, or product/program manager. It is also appropriate for people interested in the industry or in working as an industry analyst. Many of the issues we discuss are highly relevant for companies whose businesses are heavily dependent on software, such as in e-business or financial services, or embedded software for industrial applications.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-358-the-software-business-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-358-the-software-business-fall-2005/15-358f05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2479, 'Corporate Entrepreneurship: Strategies for Technology-Based New Business Development', 'This course is about ''corporate entrepreneurship'', my label for the alternative approaches that existing firms use to generate new technology-based products and businesses.', 'This course is about ''corporate entrepreneurship'', my label for the alternative approaches that existing firms use to generate new technology-based products and businesses. It emphasizes various kinds of internal ventures and multiple ''external'' collaborative approaches that include corporate venture capital investments, licensing and different types of alliances and formal joint ventures. Basis for the new knowledge presented in this course is a combination of academic research and my personal experience supplemented by that of the several guest lecturers.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-369-corporate-entrepreneurship-strategies-for-technology-based-new-business-development-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-369-corporate-entrepreneurship-strategies-for-technology-based-new-business-development-fall-2007/15-369f07.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2480, 'Global Entrepreneurship Lab: Asia-Pacific', '15.', '15.389A Global Entrepreneurship Lab: Asia-Pacific enables teams of students to work with the top management of global start-ups and gain experience in running, and consulting to, a new enterprise outside the United States. The focus is on start-ups operating in emerging markets throughout the world, with a special focus on the Asia-Pacific region. The course combines an internship in a growing firm with in-class discussions of the issues and policies that affect the climate for innovation and start-up success around the world.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-389a-global-entrepreneurship-lab-asia-pacific-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-389a-global-entrepreneurship-lab-asia-pacific-fall-2010/15-389af10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2481, 'Early Stage Capital', 'If you are an entrepreneur, one of your priorities, in addition to building your company, is ensuring you have enough money at the right times.', 'If you are an entrepreneur, one of your priorities, in addition to building your company, is ensuring you have enough money at the right times. Early Stage Capital will consider a broad range of questions that entrepreneurs deal with on this front, including the following: What should your strategy and your priorities be in raising early stage capital? What are the market norms and standards in structuring VC deals? What are the critical negotiating strategies and tactics? How will your company be valued? How can you obtain the optimal valuation for your new venture? What are the critical elements in the relationship between venture capitalists and entrepreneurs? How is the &quot;venture model&quot; evolving? Is it broken? What is the impact of Super Angels and micro VCs? These are key questions that face all entrepreneurs in 2010, particularly first-time entrepreneurs. This course aims to prepare you for these decisions, as either a potential entrepreneur or venture capitalist. Using live interactions with leading figures in the venture finance community, most of the class sessions will analyze fundamental strategies of the venture-capital investment process and the critical importance of the relationship between entrepreneur and investor. As well, we will have a tactical focus on demystifying the legalities and jargon of the term sheet and the &quot;A round&quot; financing process. Significantly for 2010, we will also frequently consider the rapid and arguably fundamental change in VC today as the &quot;lean startup&quot; model threatens much of the traditional role and value of the venture investor. Disclaimer: The websites for this course and the materials they offer are provided for educational use only. They are not a substitute for the advice of an attorney and no attorney-client relationship is created by using them. All materials are provided &quot;as-is&quot;, without any express or implied warranties.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-391-early-stage-capital-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-391-early-stage-capital-fall-2010/15-391f10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2482, 'Designing and Leading the Entrepreneurial Organization', 'This subject is about building, running, and growing an organization.', 'This subject is about building, running, and growing an organization. Subject has four central themes: How to think analytically about designing organizational systems How leaders, especially founders, play a critical role in shaping an organization''s culture What really needs to be done to build a successful organization for the long-term and What one can do to improve the likelihood of personal success. Not a survey of entrepreneurship or leadership; subject addresses the principles of organizational architecture, group behavior and performance, interpersonal influence, leadership and motivation in entrepreneurial settings. Through a series of cases, lectures, readings and exercises students develop competencies in organizational design, human resources management, leadership and organizational behavior in the context of a new, small firm.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-394-designing-and-leading-the-entrepreneurial-organization-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-394-designing-and-leading-the-entrepreneurial-organization-spring-2003/15-394s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2483, 'Finance Theory II', 'The objective of this course is to learn the financial tools needed to make good business decisions.', 'The objective of this course is to learn the financial tools needed to make good business decisions. The course presents the basic insights of corporate finance theory, but emphasizes the application of theory to real business decisions. Each session involves class discussion, some centered on lectures and others around business cases.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-402-finance-theory-ii-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-402-finance-theory-ii-spring-2003/15-402s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2484, 'Real Estate Finance and Investment', 'This course is an introduction to the most fundamental concepts, principles, analytical methods and tools useful for making investment and finance decisions regarding commercial real estate assets.', 'This course is an introduction to the most fundamental concepts, principles, analytical methods and tools useful for making investment and finance decisions regarding commercial real estate assets. As the first of a two-course sequence, this course will focus on the basic building blocks and the ''micro'' level, which pertains to individual properties and deals.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-431j-real-estate-finance-and-investment-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-431j-real-estate-finance-and-investment-fall-2006/11-431jf06.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2485, 'Real Estate Capital Markets', 'This half-semester course introduces and surveys the major public capital market real estate vehicles, REITs and MBS (with primary emphasis on CMBS).', 'This half-semester course introduces and surveys the major public capital market real estate vehicles, REITs and MBS (with primary emphasis on CMBS). Some background is also included in basic modern portfolio theory and equilibrium asset pricing. This course is primarily designed to provide MSRED students with a basic introduction to the public capital market sources of financial capital for real estate, and how those markets value such capital investments.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-432j-real-estate-capital-markets-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-432j-real-estate-capital-markets-spring-2007/11-432js07.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2486, 'Tools for Analysis: Design for Real Estate and Infrastructure Development', '', '', 'http://ocw.mit.edu/courses/engineering-systems-division/esd-712-tools-for-analysis-design-for-real-estate-and-infrastructure-development-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/engineering-systems-division/esd-712-tools-for-analysis-design-for-real-estate-and-infrastructure-development-spring-2010/esd-712s10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2487, 'Entrepreneurial Finance', '15.', '15.431 Entrepreneurial Finance examines the elements of entrepreneurial finance, focusing on technology-based start-up ventures and the early stages of company development. The course addresses key questions which challenge all entrepreneurs: how much money can and should be raised; when should it be raised and from whom; what is a reasonable valuation of the company; and how should funding, employment contracts and exit decisions be structured. It aims to prepare students for these decisions, both as entrepreneurs and venture capitalists. In addition, the course includes an in-depth analysis of the structure of the private equity industry.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-431-entrepreneurial-finance-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-431-entrepreneurial-finance-spring-2011/15-431s11.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2488, 'Investments', 'The focus of this course is on financial theory and empirical evidence for making investment decisions.', 'The focus of this course is on financial theory and empirical evidence for making investment decisions. Topics include: portfolio theory; equilibrium models of security prices (including the capital asset pricing model and the arbitrage pricing theory); the empirical behavior of security prices; market efficiency; performance evaluation; and behavioral finance.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-433-investments-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-433-investments-spring-2003/15-433s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2489, 'Analytics of Finance', 'This course covers the key quantitative methods of finance: financial econometrics and statistical inference for financial applications; dynamic optimization; Monte Carlo simulation; stochastic (ItÃ´) calculus.', 'This course covers the key quantitative methods of finance: financial econometrics and statistical inference for financial applications; dynamic optimization; Monte Carlo simulation; stochastic (ItÃ´) calculus. These techniques, along with their computer implementation, are covered in depth. Application areas include portfolio management, risk management, derivatives, and proprietary trading.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-450-analytics-of-finance-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-450-analytics-of-finance-fall-2010/15-450f10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2490, 'Financial and Managerial Accounting', '15.', '15.514 is an&nbsp;intensive introduction to the preparation and interpretation of financial information for investors (external users) and managers (internal users) and to the use of financial instruments to support system and project creation. The course adopts a decision-maker perspective on accounting and finance with the goal of helping students&nbsp;develop a framework for understanding financial, managerial, and tax reports. 15.514 is&nbsp;restricted to System Design and Management students. Acknowledgements Acknowledgement is hereby given to Professor G. Peter Wilson for his authorship of the following content in this course: The Five Challenges (see Syllabus and Lecture 1) ''What Do Intel and Accountants Have in Common?'' (see Lecture 1) A Conceptual Framework for Financial Accounting (see Lecture 1)', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-514-financial-and-managerial-accounting-summer-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-514-financial-and-managerial-accounting-summer-2003/15-514su03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2491, 'Financial Accounting', 'Our goal is to help you develop a framework for understanding financial, managerial, and tax reports.', 'Our goal is to help you develop a framework for understanding financial, managerial, and tax reports. The course goal is divided into five subordinate challenges that can help you organize the way you learn accounting: The record keeping and reporting challenge The computation challenge The judgment challenge The usage challenge The search challenge The course adopts a decision-maker perspective of accounting by emphasizing the relation between accounting data and the underlying economic events generating them. Restricted to first-year Sloan MBA students. Acknowledgements Acknowledgment is hereby given to Professor G. Peter Wilson for his authorship of the following content in this course: The Five Challenges (see Syllabus and Lecture 1) ''What Do Intel and Accountants Have in Common?'' (see Lecture 1) A Conceptual Framework for Financial Accounting (see Lecture 1)', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-515-financial-accounting-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-515-financial-accounting-fall-2003/15-515f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2492, 'Introduction to Financial and Managerial Accounting', 'This course studies basic concepts of financial and managerial reporting.', 'This course studies basic concepts of financial and managerial reporting. The viewpoint is that of readers of financial and managerial reports rather than the accountants who prepare them.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-501-introduction-to-financial-and-managerial-accounting-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-501-introduction-to-financial-and-managerial-accounting-spring-2004/15-501s04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2493, 'Management Accounting and Control', 'This course examines management accounting and related analytical methodologies for decision making and control in profit-directed organizations.', 'This course examines management accounting and related analytical methodologies for decision making and control in profit-directed organizations. It also defines product costing, budgetary control systems, and performance evaluation systems for planning, coordinating, and monitoring the performance of a business. This course defines principles of measurement and develops framework for assessing behavioral dimensions of control systems; impact of different managerial styles on motivation and performance in an organization.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-521-management-accounting-and-control-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-521-management-accounting-and-control-spring-2003/15-521s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2494, 'Business Analysis Using Financial Statements', 'The purpose of this class is to advance your understanding of how to use financial information to value and analyze firms.', 'The purpose of this class is to advance your understanding of how to use financial information to value and analyze firms. We will apply your economics/accounting/finance skills to problems from today''s business news to help us understand what is contained in financial reports, why firms report certain information, and how to be a sophisticated user of this information.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-535-business-analysis-using-financial-statements-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-535-business-analysis-using-financial-statements-spring-2003/15-535s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2495, 'Information Technology Essentials', 'This class offers a broad coverage of technology concepts and trends underlying current and future developments in information technology, and fundamental principles for the effective use of computer-based information systems.', 'This class offers a broad coverage of technology concepts and trends underlying current and future developments in information technology, and fundamental principles for the effective use of computer-based information systems. There will be a special emphasis on networks and distributed computing, including the World Wide Web. Other topics include: hardware and operating systems, software development tools and processes, relational databases, security and cryptography, enterprise applications, and electronic commerce. Hands-on exposure to Web, database, and graphical user interface (GUI) tools. This course is intended for students with little or no background in computer technology. Students with extensive education or work experience in computer technology should consider taking a more advanced course.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-561-information-technology-essentials-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-561-information-technology-essentials-spring-2005/15-561s05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2496, 'Information Technology as an Integrating Force in Manufacturing', 'In virtually every industry and every firm, information technology is driving change, creating opportunities and challenges.', 'In virtually every industry and every firm, information technology is driving change, creating opportunities and challenges. Leaders who don''t understand at least the fundamentals of information systems will be at a strategic disadvantage. This course provides broad coverage of technology concepts and trends underlying current and future developments in information technology, and fundamental principles for the effective use of computer-based information systems. There will be a special emphasis on manufacturing. Information Systems topics that will be covered include networks and distributed computing, including the World Wide Web, hardware and operating systems, software development tools and processes, relational databases, security and cryptography, enterprise applications, B2B, the semantic web and electronic commerce. Sloan LFM students with an interest in Information Systems are encouraged to register for this course.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-566-information-technology-as-an-integrating-force-in-manufacturing-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-566-information-technology-as-an-integrating-force-in-manufacturing-spring-2003/15-566s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2497, 'The Economics of Information: Strategy, Structure and Pricing', '15.', '15.567 The Economics of Information provides an analysis of the underlying economics of information with management implications. It studies the effects of digitization and technology on industry, organizational structure, and business strategy, and examines pricing, bundling, and versioning of digital goods, including music, video, software, and communication services. In addition, the course considers the managerial implications of social networks, search, targeted advertising, personalization, privacy, network externalities, open source, and alliances.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-567-the-economics-of-information-strategy-structure-and-pricing-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-567-the-economics-of-information-strategy-structure-and-pricing-fall-2010/15-567f10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2498, 'Generating Business Value from Information Technology', 'This course provides concepts and frameworks for understanding the potential impact of information technology (IT) on business strategy and performance.', 'This course provides concepts and frameworks for understanding the potential impact of information technology (IT) on business strategy and performance. We will examine how some firms make IT a strategic asset while other firms struggle to realize value from IT investments. The course focuses on the implications of increased digitization for defining business strategies and operating models, and explores the roles of both general managers and IT executives in using IT to achieve operational excellence and business agility. Topics include business operating models, IT investment and prioritization, business strategy and IT alignment, the design and governance of digitized processes, and the role of the IT unit. Draws heavily on research and case studies from MIT Sloan Center for Information Systems Research. Restricted to graduate students.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-571-generating-business-value-from-information-technology-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-571-generating-business-value-from-information-technology-spring-2009/15-571s09.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2499, 'IT and Business Transformation', 'The purpose of this Proseminar in Information Technology and Business Transformation (ITBT) is to provide students with a view of IT-enabled transformation and the strategic issues in the management of IT.', 'The purpose of this Proseminar in Information Technology and Business Transformation (ITBT) is to provide students with a view of IT-enabled transformation and the strategic issues in the management of IT. The seminar will bring in CIOs, CEOs, and experienced consultants and industry observers to provide their perspectives and tell their stories about the use and management of IT today. Their talks will deal with the new technology, the new applications, the issues of implementation, the changes in industries and companies, and the strategic management of IT. In addition, there will be several case discussions of issues to be decided by senior management, with students taking on the position of executives and consultants. There will also be frameworks presented and used to position all material and speakers. Finally, one session will consist of ITBT alumni discussing career opportunities and issues for students, particularly from MIT, with these interests. Students will gain a perspective of the strategic role of and issues in managing IT as manifested in e-business applications, as a driver and enabler of business transformation, and as an underlying infrastructure resource for all businesses.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-598-it-and-business-transformation-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-598-it-and-business-transformation-spring-2003/15-598s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2500, 'Workshop in IT: Collaborative Innovation Networks', 'Diversity begets creativity&mdash;in this seminar we tap the amazing power of swarm creativity on the Web by studying and working together as Collaborative Innovation Networks (COINs).', 'Diversity begets creativity&mdash;in this seminar we tap the amazing power of swarm creativity on the Web by studying and working together as Collaborative Innovation Networks (COINs). As interdisciplinary teams of MIT management, SCAD design, University of Cologne informatics, and Aalto University software engineering students we will explore how to discover latest trends on the Web, and how to make them succeed in online social networks. We study a wide range of methods for predictive analytics (coolhunting) and online social marketing (coolfarming), mostly based on social network analysis and the emerging science of collaboration. Students will also learn to use our own unique MIT-developed Condor tool for Web mining, social network analysis, and trend prediction.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-599-workshop-in-it-collaborative-innovation-networks-fall-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-599-workshop-in-it-collaborative-innovation-networks-fall-2011/15-599f11.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2501, 'Innovative Businesses and Breakthrough Technologies - The Legal Issues', '15.', '15.616 is an introduction to business law which covers the fundamentals, including contracts, liability, regulation, employment, and corporations, with an in-depth treatment of the legal issues relating to breakthrough technologies, including the legal framework of R&amp;D, the commercialization of new high-technology products in start-ups and mature companies, and the liability and regulatory implications of new products and innovative business models. There is extensive attention to national and international intellectual property protection and strategies. Examples are drawn from many industries, including information technology, communications, and life sciences. Note: This course used to be numbered 15.648.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-616-innovative-businesses-and-breakthrough-technologies-the-legal-issues-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-616-innovative-businesses-and-breakthrough-technologies-the-legal-issues-fall-2004/15-616f04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2502, 'The Law of Mergers and Acquisitions', 'This course is designed to give students an introduction to the law-sensitive aspects of Mergers &amp; Acquisitions (M&amp;A).', 'This course is designed to give students an introduction to the law-sensitive aspects of Mergers &amp; Acquisitions (M&amp;A). In Module I, we examine the legal implications of key roles and deal structures, and walk through some of the issues that would typically arise in a simple and friendly transaction. We also give a class to the legal issues arising in LBOs and the legal concerns of financial sponsors more generally, and another class to employment-related issues, including those relating to managers facing unsettled circumstances. In Module II, we look at a variety of complications, including those that arise in the friendly or unfriendly purchase of a publicly-held company; deals involving distressed and hi-tech companies; antitrust concerns; allegations of misconduct by management or board members; and deals involving non-U.S. companies.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-649-the-law-of-mergers-and-acquisitions-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-649-the-law-of-mergers-and-acquisitions-spring-2003/15-649s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2503, 'Strategic HR Management', 'This course is about both the design and execution of human resource management strategies.', 'This course is about both the design and execution of human resource management strategies. This course has two central themes: (1) How to think systematically and strategically about aspects of managing the organization''s human assets, and (2) What really needs to be done to implement these policies and to achieve competitive advantage. It adopts the perspective of a general manager and addresses human resource topics (including reward systems, performance management, high-performance human resource systems, training and development, recruitment, retention, equal employment opportunity laws, work-force diversity, and union-management relationships) from a strategic perspective.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-660-strategic-hr-management-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-660-strategic-hr-management-spring-2003/15-660s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2504, 'Power and Negotiation', 'This course is designed to provide you with a competitive advantage in negotiation.', 'This course is designed to provide you with a competitive advantage in negotiation. You will learn and practice the technical skills and analytic frameworks that are necessary to negotiate successfully with peers from other top business schools, and you will learn methods for developing the powerful social capital you will need to rise in the executive ranks of any organization. In this course, you will learn to successfully face the challenge of negotiating materially rewarding deals while also building your social capital. You will work with training materials on leadership and relationship building that have been used with over 200 principals and partners in international professional service firms (40% were non-US nationals), and a social capital assessment tool used by these executives to receive feedback from senior partners and over 2000 clients. In addition, you will have the opportunity to participate in a lunchtime workshop on ''Leadership and Emotional Intelligence'' led by an executive coach, Charles Wolfe of Charles J Wolfe Associates. Overall, this course is designed to enhance your ability to negotiate within the context of an ongoing relationship. As a manager, consultant, or professional service provider you will negotiate with your counterparts, team members, clients, and subordinates on an ongoing basis. Further, in today''s less hierarchical organizations, you will be forced to negotiate with others to get your work done. Every time a project falls behind, critical new information is uncovered, or the competitive landscape of your industry changes, you will need to renegotiate tasks, plans, goals, or fees with your key stakeholders. In sum, we will focus both on the analytic tools necessary to become a highly successful negotiator and on the relationship building skills necessary to negotiate deals that will enhance your social capital, your ability to lead others, and your book of loyal clients.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-665b-power-and-negotiation-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-665b-power-and-negotiation-fall-2002/15-665bf02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2505, 'Work, Employment, and Industrial Relations Theory', 'This seminar will cover the multi-disciplinary theoretical and empirical foundations of research on work, employment, labor markets, and industrial relations.', 'This seminar will cover the multi-disciplinary theoretical and empirical foundations of research on work, employment, labor markets, and industrial relations. We begin by tracing the historical development of theory and research in the field, paying special attention to how the normative premises, concepts, and methodological traditions of industrial relations compare to those of other disciplines that contribute to the study of work and employment relations. Then we will review a number of current theoretical and policy debates shaping the field. This will be followed by a series of modules introducing different disciplinary perspectives used to study work and employment issues today. Emphasis will be given to recent research from different industries that illustrate the mix of methods&mdash;field work, qualitative and quantitative data collection and analysis, etc.&mdash;we encourage in this field of study.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-676-work-employment-and-industrial-relations-theory-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-676-work-employment-and-industrial-relations-theory-spring-2008/15-676s08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2506, 'Urban Labor Markets and Employment Policy', 'This subject discusses the broader trends in the labor market, how urban labor markets function, public and private training policy, other labor market programs, the link between labor market policy and economic development, and the organization of work within firms.', 'This subject discusses the broader trends in the labor market, how urban labor markets function, public and private training policy, other labor market programs, the link between labor market policy and economic development, and the organization of work within firms.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-677j-urban-labor-markets-and-employment-policy-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-677j-urban-labor-markets-and-employment-policy-spring-2005/15-677js05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2507, 'Political Economy I', 'Political Economy I explores the major social science paradigms for analyzing relations among state, economy, and society.', 'Political Economy I explores the major social science paradigms for analyzing relations among state, economy, and society. Through readings, lectures and discussion of original texts in political liberalism and individualism, neo-classical economics, Marxism, sociological and cultural theories, and neo-institutionalism, the seminar examines the fundamental assumptions on which our understanding of the social world and our research are based.', 'http://ocw.mit.edu/courses/political-science/17-100j-political-economy-i-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/political-science/17-100j-political-economy-i-fall-2010/17-100jf10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2508, 'Introduction to Operations Management', 'This half-term course introduces students to problems and analysis related to the design, planning, control, and improvement of manufacturing and service operations.', 'This half-term course introduces students to problems and analysis related to the design, planning, control, and improvement of manufacturing and service operations. Class sessions involve explaining concepts, working examples, and discussing cases. A wide range of topics are covered, including: process analysis, quality management, supply chain design, procurement, and product development. Toward the end of the course, students work in teams to manage a virtual factory in a web-based simulation exercise.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-760b-introduction-to-operations-management-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-760b-introduction-to-operations-management-spring-2004/15-760bs04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2509, 'Operations Management', 'This course will introduce concepts and techniques for design, planning and control of manufacturing and service operations.', 'This course will introduce concepts and techniques for design, planning and control of manufacturing and service operations.&nbsp;The course provides basic definitions of operations management terms, tools and techniques for analyzing operations, and strategic context for making operational decisions.&nbsp;We present the material in five modules: Operations Analysis Coordination and Planning Quality Management Project Management Logistics and Supply Chain Management', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-761-operations-management-summer-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-761-operations-management-summer-2002/15-761su05.gif', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2510, 'Supply Chain Planning', '15.', '15.762J Supply Chain Planning focuses on effective supply chain strategies for companies that operate globally, with an emphasis on how to plan and integrate supply chain components into a coordinated system. Students are exposed to concepts and models important in supply chain planning with emphasis on key tradeoffs and phenomena. The course introduces and utilizes key tactics such as risk pooling and inventory placement, integrated planning and collaboration, and information sharing. Lectures, computer exercises, and case discussions introduce various models and methods for supply chain analysis and optimization.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-762j-supply-chain-planning-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-762j-supply-chain-planning-spring-2011/15-762js11.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2511, 'The Theory of Operations Management', 'The doctoral seminar 15.', 'The doctoral seminar 15.764 focuses on theoretical work for studying operations planning and control problems. This term''s special topic, ''Customer-Driven Operations,'' considers how a number of companies have succeeded in focusing their operation systems on the customer. The class reviews the quantitative models and theoretical tools underlying some of the customer-driven operational practices of these cutting-edge companies. Students will read and present research papers on topics such as distribution systems, short life-cycle product management, and forecast evolution models. This MIT OpenCourseWare site is dedicated to the memory of Bhuwan Singh, a member of the class.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-764-the-theory-of-operations-management-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-764-the-theory-of-operations-management-spring-2004/15-764s04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2512, 'Management of Services: Concepts, Design, and Delivery', '15.', '15.768 Management of Services: Concepts, Design, and Delivery explores the use of operations tools and perspectives in the service sector, including both for-profit and not-for-profit organizations. The course builds on conceptual frameworks and cases from a wide range of service operations, selected from health care, hospitality, internet services, supply chain, transportation, retailing, food service, entertainment, financial services, humanitarian services, government services, and others.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-768-management-of-services-concepts-design-and-delivery-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-768-management-of-services-concepts-design-and-delivery-fall-2010/15-768f10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2513, 'Operations Strategy', '15.', '15.769 Operations Strategy provides a unifying framework for analyzing strategic issues in manufacturing and service operations. Students analyze the relationships between manufacturing and service companies and their suppliers, customers, and competitors. The course covers strategic decisions in technology, facilities, vertical integration, human resources, and other areas, and also explores means of competition such as cost, quality, and innovativeness.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-769-operations-strategy-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-769-operations-strategy-fall-2010/15-769f10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2514, 'Logistics Systems', 'This subject is a survey of the fundamental analytic tools, approaches, and techniques which are useful in the design and operation of logistics systems and integrated supply chains.', 'This subject is a survey of the fundamental analytic tools, approaches, and techniques which are useful in the design and operation of logistics systems and integrated supply chains. The material is taught from a managerial perspective, with an emphasis on where and how specific tools can be used to improve the overall performance and reduce the total cost of a supply chain. We place a strong emphasis on the development and use of fundamental models to illustrate the underlying concepts involved in both intra and inter-company logistics operations. While our main objective is to develop and use models to help us analyze these situations, we will make heavy use of examples from industry to provide illustrations of the concepts in practice. This is neither a purely theoretical nor a case study course, but rather an analytical course that addresses real problems found in practice.', 'http://ocw.mit.edu/courses/engineering-systems-division/esd-260j-logistics-systems-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/engineering-systems-division/esd-260j-logistics-systems-fall-2006/esd-260jf06.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2515, 'Management of Supply Networks for Products and Services', 'This course covers organizational, strategic and operational aspects of managing Supply Networks (SNs) from domestic and international perspectives.', 'This course covers organizational, strategic and operational aspects of managing Supply Networks (SNs) from domestic and international perspectives. Topics include alternative SN structures, strategic alliances, design of delivery systems and the role of third party logistics providers. Many of the activities exchanged among enterprises in a SN are of a service nature, and the final output is often a combination of tangible products and services which the end-customer purchases. A series of concepts, frameworks and analytic tools are provided to better understand the management of service operations. Guest speakers share their experiences in managing SNs and services. Restricted to MIT Sloan Fellows in Innovation and Global Leadership.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-778-management-of-supply-networks-for-products-and-services-summer-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-778-management-of-supply-networks-for-products-and-services-summer-2004/15-778su07.JPG', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2516, 'Product Design and Development', 'Product Design and Development is a project-based course that covers modern tools and methods for product design and development.', 'Product Design and Development is a project-based course that covers modern tools and methods for product design and development. The cornerstone is a project in which teams of management, engineering, and industrial design students conceive, design and prototype a physical product. Class sessions are conducted in workshop mode and employ cases and hands-on exercises to reinforce the key ideas. Topics include identifying customer needs, concept generation, product architecture, industrial design, and design-for-manufacturing.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-783j-product-design-and-development-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-783j-product-design-and-development-spring-2006/15-783js06.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2517, 'Seminar in Operations Management', 'This seminar will explore the purposes and development of Technology Roadmaps for systematically mapping out possible development paths for various technological domains and the industries that build on them.', 'This seminar will explore the purposes and development of Technology Roadmaps for systematically mapping out possible development paths for various technological domains and the industries that build on them. Data of importance for such roadmaps include rates of innovation, key bottlenecks, physical limitations, improvement trendlines, corporate intent, and value chain and industry evolutionary paths. The course will build on ongoing work on the MIT Communications Technology Roadmap project, but will explore other domains selected from Nanotechnology, Bio-informatics, Geno/Proteino/Celleomics, Neurotechnology, Imaging &amp; Diagnostics, etc. Thesis and Special Project opportunities will be offered.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-795-seminar-in-operations-management-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-795-seminar-in-operations-management-fall-2002/15-795f02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2518, 'Marketing Management', '15.', '15.810 Marketing Management is designed to serve as an introduction to the theory and practice of marketing. Students will improve their ability to develop effective marketing strategies and assess market opportunities, as well as design strategy implementation programs. In addition, students will have the opportunity to communicate and defend their recommendations and build upon the recommendations of their peers. We will explore the theory and applications of marketing concepts through a mix of cases, discussions, lectures, guest speakers, individual assignments, and group projects. We will draw materials from a variety of sources and settings including services, consumer and business-to-business products.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-810-marketing-management-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-810-marketing-management-fall-2010/15-810f10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2519, 'Marketing Management', 'This course provides an introduction to the fundamental concepts of marketing, including a customer orientation, matched with attention to competition and core strengths.', 'This course provides an introduction to the fundamental concepts of marketing, including a customer orientation, matched with attention to competition and core strengths. It is organized so that each class is either a lecture or a case discussion. This course is a half semester MBA course taught to students in their first semester at Sloan. Together with their other core courses, students have the option of taking this course or an introductory finance course. This course is a prerequisite for all of the advanced marketing courses.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-810-marketing-management-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-810-marketing-management-fall-2004/15-810f04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2520, 'Pricing', 'This course is designed to teach students how to price goods and services by providing a framework for understanding pricing strategies and tactics.', 'This course is designed to teach students how to price goods and services by providing a framework for understanding pricing strategies and tactics. Topics covered include economic value analysis, price elasticities, price customization, pricing complementary products, pricing in platform markets and anticipating competitive price responses.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-818-pricing-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-818-pricing-spring-2010/15-818s10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2521, 'Listening to the Customer', 'The 15.', 'The 15.821 and 15.822 Sequence Marketing research may be divided into methods that emphasize understanding ''the customer'' and methods that emphasize understanding ''the market.'' This course (15.821) deals with the customer and emphasizes qualitative methods (interviews, focus groups, Voice of the Customer, composing questions for a survey). The companion course (15.822) deals with the market and emphasizes quantitative methods (sampling, survey execution, quantitative data interpretation, conjoint analysis, factor analysis). The methods covered in 15.821 are often used in the ''front-end'' of market research project, whose second-stage is a quantitative survey. The quality of information gathered in the second-stage is greatly enhanced in this way. 15.821 is designed for the nonspecialist, e.g., someone planning a career in general management, product or project management, R&amp;D, advertising, or entrepreneurship. 15.822 teaches analytical techniques that are standard in consulting or marketing research, and is ideally suited for students planning careers in those fields.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-821-listening-to-the-customer-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-821-listening-to-the-customer-fall-2002/15-821f02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2522, 'Strategic Marketing Measurement', 'Marketing research may be divided into methods that emphasize understanding ''the customer'' and methods that emphasize understanding ''the market.', 'Marketing research may be divided into methods that emphasize understanding ''the customer'' and methods that emphasize understanding ''the market.'' This course (15.822) deals with the market. The companion course (15.821) deals with the customer. The course will teach you how to write, conduct and analyze a marketing research survey. The emphasis will be on discovering market structure and segmentation, but you can pursue other project applications. A major objective of the course is to give you some ''hands-on'' exposure to analysis techniques that are widely used in consulting and marketing research factor analysis, perceptual mapping, conjoint, and cluster analysis). These techniques used to be considered advanced but now involve just a few keystrokes on most stat software packages. The course assumes familiarity with basic probability, statistics, and multiple linear regression.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-822-strategic-marketing-measurement-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-822-strategic-marketing-measurement-fall-2002/15-822f02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2523, 'Entrepreneurial Marketing', 'This course clarifies key marketing concepts, methods, and strategic issues relevant for start-up and early-stage entrepreneurs.', 'This course clarifies key marketing concepts, methods, and strategic issues relevant for start-up and early-stage entrepreneurs. At this course, there are two major questions: Marketing Question: What and how am I selling to whom? New Venture Question: How do I best leverage my limited marketing recourses? Specifically, this course is designed to give students a broad and deep understanding of such topics as: What are major strategic constraints and issues confronted by entrepreneurs today? How can one identify and evaluate marketing opportunities? How do entrepreneurs achieve competitive advantages given limited marketing resources? What major marketing/sales tools are most useful in an entrepreneurial setting? Because there is no universal marketing solution applicable to all entrepreneurial ventures, this course is designed to help students develop a flexible way of thinking about marketing problems in general.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-835-entrepreneurial-marketing-spring-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-835-entrepreneurial-marketing-spring-2002/15-835s02.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2524, 'Special Seminar in Marketing: Marketing Management', 'The purpose of 15.', 'The purpose of 15.840 is to: Introduce key marketing ideas and phenomena. Develop students'' skills in marketing analysis and planning. Provide a forum (both written and oral) for presenting and defending recommendations and critically examining and discussing those of others.&#160;An emphasis is placed&#160;on theory and practice that draws on market research, competitive analysis, and marketing science.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-840-special-seminar-in-marketing-marketing-management-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-840-special-seminar-in-marketing-marketing-management-spring-2004/15-840s04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2525, 'System Dynamics for Business Policy', '15.', '15.874 and 15.871 provide an introduction to system dynamics modeling for the analysis of business policy and strategy. Students learn to visualize a business organization in terms of the structures and policies that create dynamics and regulate performance. The course uses role playing games, simulation models, and management flight simulators to develop principles for the successful management of complex strategies. Special emphasis will be placed on case studies of successful strategies using system dynamics. 15.874 is a full semester course and 15.871 is a half semester course. The two classes meet together and cover the same material for the first half of the term. In the second half of the semester, only 15.874 continues.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-874-system-dynamics-for-business-policy-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-874-system-dynamics-for-business-policy-fall-2003/15-874f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2526, 'System Dynamics for Business Policy', '15.', '15.874 and 15.871 provide an introduction to system dynamics modeling for the analysis of business policy and strategy. Students learn to visualize a business organization in terms of the structures and policies that create dynamics and regulate performance. The course uses role playing games, simulation models, and management flight simulators to develop principles for the successful management of complex strategies. Special emphasis will be placed on case studies of successful strategies using system dynamics. 15.874 is a full semester course and 15.871 is a half semester course. The two classes meet together and cover the same material for the first half of the term. In the second half of the semester, only 15.874 continues.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-874-system-dynamics-for-business-policy-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-874-system-dynamics-for-business-policy-fall-2003/15-874f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2527, 'Applications of System Dynamics', '15.', '15.875 is a project-based course that explores how organizations can use system dynamics to achieve important goals. In small groups, students learn modeling and consulting skills by working on a term-long project with real-life managers. A diverse set of businesses and organizations sponsor class projects, from start-ups to the Fortune 500. The course focuses on gaining practical insight from the system dynamics process, and appeals to people interested in system dynamics, consulting, or managerial policy-making.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-875-applications-of-system-dynamics-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-875-applications-of-system-dynamics-spring-2004/15-875s04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2528, 'Strategic Management I', 'This course focuses on some of the important current issues in strategic management.', 'This course focuses on some of the important current issues in strategic management. It will concentrate on modern analytical approaches and on enduring successful strategic practices. It is consciously designed with a technological and global outlook since this orientation in many ways highlights the significant emerging trends in strategic management. The course is intended to provide the students with a pragmatic approach that will guide the formulation and implementation of corporate, business, and functional strategies.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-902-strategic-management-i-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-902-strategic-management-i-fall-2006/15-902f06.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2529, 'Technology Strategy', 'This course provides a series of strategic frameworks for managing high-technology businesses.', 'This course provides a series of strategic frameworks for managing high-technology businesses. The emphasis throughout the course is on the development and application of conceptual models which clarify the interactions between competition, patterns of technological and market change, and the structure and development of organizational capabilities. This is not a course in how to manage product or process development. The main focus is on the acquisition of a set of powerful analytical tools which are critical for the development of a technology strategy as an integral part of business strategy. These tools can provide the framework for deciding which technologies to invest in, how to structure those investments and how to anticipate and respond to the behavior of competitors, suppliers, and customers. The course should be of particular interest to those interested in managing a business for which technology is likely to play a major role, and to those interested in consulting or venture capital.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-912-technology-strategy-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-912-technology-strategy-fall-2008/15-912f08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2530, 'Advanced Strategy', 'This course draws on a wide range of perspectives to explore the roots of long term competitive advantage in unusually successful firms.', 'This course draws on a wide range of perspectives to explore the roots of long term competitive advantage in unusually successful firms. Using a combination of cases, simulations, readings and, most importantly, lively discussion, the course will explore the ways in which long term advantage is built from first mover advantage, increasing returns, and unique organizational competencies. We will focus particularly on the ways in which the actions of senior management build competitive advantage over time, and on the strategic implications of understanding the roots of a firm''s success.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-963-advanced-strategy-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-963-advanced-strategy-spring-2008/15-963s08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2531, 'Management Accounting and Control', 'This course is an introduction to the use of accounting information by managers for decision making, performance evaluation and control.', 'This course is an introduction to the use of accounting information by managers for decision making, performance evaluation and control. The course should be useful for those who intend to work as management consultants, for LFM (Leaders for Manufacturing) students, and in general, for those who will become senior managers.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-963-management-accounting-and-control-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-963-management-accounting-and-control-spring-2007/15-963s07.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2532, 'Technology Strategy for System Design and Management', 'This course provides you with a framework to understand the structure and dynamics of high-tech businesses, together with an approach for their effective strategic management.', 'This course provides you with a framework to understand the structure and dynamics of high-tech businesses, together with an approach for their effective strategic management. It is focused on domains in which systems are important, because either or both products are parts of larger and more complex systems, or they are comprised of systems. The domains covered include computing, communications (in particular the mobile and IP domains), consumer electronics, industrial networking, automotive, aerospace and medical devices. The course will be of particular interest to those interested in managing a business in which technology will likely play a major role, and also to those interested in investing in or providing counsel to these businesses. The emphasis throughout is on the development and application of ways of thinking or mental models that bring clarity to the complex co-evolution of technological innovation, the demand opportunity, systems architecture, business ecosystems, and decision-making and execution within the business.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-965-technology-strategy-for-system-design-and-management-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-965-technology-strategy-for-system-design-and-management-spring-2009/15-965s09.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2533, 'Managing and Volunteering In the Non-Profit Sector', 'This is a course intended to give students a broad overview of the management challenges of the non-profit sector.', 'This is a course intended to give students a broad overview of the management challenges of the non-profit sector. It is not a detailed management course but rather is aimed at students who will likely relate to non-profits in a variety of ways (on the boards, as volunteers, as fund-raisers, and occasionally as staff).', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-967-managing-and-volunteering-in-the-non-profit-sector-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-967-managing-and-volunteering-in-the-non-profit-sector-spring-2005/15-967s05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2534, 'The Sociology of Strategy', 'This seminar provides an introduction to scholarship in a growing research community: the sociologists and sociologically-inclined organization theorists who study issues that relate, at least in a broad sense, to the interdisciplinary field of inquiry that is known as ''strategy'' or ''strategic management'' research.', 'This seminar provides an introduction to scholarship in a growing research community: the sociologists and sociologically-inclined organization theorists who study issues that relate, at least in a broad sense, to the interdisciplinary field of inquiry that is known as ''strategy'' or ''strategic management'' research. The course is not designed to survey the field of strategy. Rather, the focus is on getting a closer understanding of the recent work by sociologists and sociologically-oriented organization theorists that investigates central questions in strategic management. In particular, we will be concerned with identifying and assessing sociological work that aims to shed light on: (a) relative firm performance; (b) the nature of competition and market interaction; (c) organizational capabilities; (d) the beginnings of industries and firms; (e) the diffusion or transfer of ideas and practices across firms; and (f) strategic change.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-968-the-sociology-of-strategy-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-968-the-sociology-of-strategy-spring-2005/15-968s05.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2535, 'Digital Anthropology', 'Digital Anthropology is a Spring 2003 applied social science and media arts seminar, surveying the blossoming arena of digital-artifact enabled experimental sociology/anthropology.', 'Digital Anthropology is a Spring 2003 applied social science and media arts seminar, surveying the blossoming arena of digital-artifact enabled experimental sociology/anthropology. We will emphasize on both (a) Technology Testbeds &#8211; systematically deploying research lab prototypes and corporate pre-production products in a sample human organizational population and carefully observing the social consequences, and (b) Sociometrics &#8211; using digital artifacts to better observe and measure the complex social reality of interesting human systems.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-966-digital-anthropology-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-966-digital-anthropology-spring-2003/mas-966s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2536, 'Developmental Entrepreneurship', 'This class surveys developmental entrepreneurship via case examples of both successful and failed businesses and generally grapples with deploying and diffusing products and services through entrepreneurial action.', 'This class surveys developmental entrepreneurship via case examples of both successful and failed businesses and generally grapples with deploying and diffusing products and services through entrepreneurial action. By drawing on live and historical cases, especially from South Asia, Africa, Latin America as well as Eastern Europe, China, and other developing regions, we seek to cover the broad spectrum of challenges and opportunities facing developmental entrepreneurs. Finally, we explore a range of established and emerging business models as well as new business opportunities enabled by developmental technologies developed in MIT labs and beyond.', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-666-developmental-entrepreneurship-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-666-developmental-entrepreneurship-fall-2003/mas-666f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2537, 'Professional Seminar in Sustainability', 'Sustainability challenges organizations to address the implications â€“ and responses â€“ in their own operations and supply chain, products/services/markets, and community responsibilities.', 'Sustainability challenges organizations to address the implications â€“ and responses â€“ in their own operations and supply chain, products/services/markets, and community responsibilities. This course exposes students to professionals and organizations who are actively working toward making their organizations and industries sustainable.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-972-professional-seminar-in-sustainability-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-972-professional-seminar-in-sustainability-spring-2010/15-972s10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2538, 'Leadership Lab', 'This five-day interactive and experiential workshop focuses on how leaders lead innovations that both promote social responsibility and produce business success.', 'This five-day interactive and experiential workshop focuses on how leaders lead innovations that both promote social responsibility and produce business success. The workshop is organized around three main parts: observation, sense-making, and creating. During the observation phase, students spend a full day inside the Boston office of the design company IDEO and visit some of the most interesting proven innovators in corporate social responsibility such as Ben &amp; Jerry&rsquo;s, KLD, MBDC, Plug Power (fuel cell technology), PwC, Schlumberger, or core team members of the UN Global Compact. After returning from their company visits, students describe to one another what they saw and learned.&nbsp;In the final part of the Lab, students conceive and implement innovation projects that serve the needs of a local community. Each team presents its practical accomplishments on the final day of the Lab. Starting in 2004 this course will be renumbered as 15.975.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-974-leadership-lab-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-974-leadership-lab-spring-2003/15-974s03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2539, 'U-Lab: Leading Profound Innovation for a More Sustainable World', '15.', '15.975 U-Lab: Leading Profound Innovation for a More Sustainable World is an interactive and experiential class about leading profound innovation for pioneering a more sustainable economy and society. The class is organized around personal reflection practices, relational practices, and societal practices. It focuses on the intertwined relationship between the evolution of capitalism, multi-stakeholder innovation, and presencing.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-975-u-lab-leading-profound-innovation-for-a-more-sustainable-world-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-975-u-lab-leading-profound-innovation-for-a-more-sustainable-world-fall-2010/15-975f10.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2540, 'Leadership Tools and Teams: A Product Development Lab', 'In this class you will be creating a leadership development tool for students like yourselves in the leadership program at Sloan.', 'In this class you will be creating a leadership development tool for students like yourselves in the leadership program at Sloan. This tool might be a coaching guide for second-year pilots, a leadership workbook for MBA students to use during their summer employment, a leadership assessment for club presidents or a workshop on networking. You will be free to choose the tool that you want to develop, but by the end of the class there must be a product that can be used at Sloan. In addition, the tools must link in some way to the leadership model used at Sloan.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-978-leadership-tools-and-teams-a-product-development-lab-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-978-leadership-tools-and-teams-a-product-development-lab-spring-2007/15-978s07.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2541, 'Architecture and Communication in Organizations', 'While no businesses succeed based on their architecture or space design, many fail as a result of inattention to the power of spatial relationships.', 'While no businesses succeed based on their architecture or space design, many fail as a result of inattention to the power of spatial relationships. This course demonstrates through live case studies with managers and architects the value of strategic space planning and decision making in relation to business needs. The course presents conceptual frameworks for thinking about architecture, communication and organizations. This course is offered during the Sloan Innovation Period (SIP), which is a one-week period at the MIT Sloan School of Management that occurs midway through each semester.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-990-architecture-and-communication-in-organizations-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-990-architecture-and-communication-in-organizations-fall-2003/15-990f03.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2542, 'S-Lab: Laboratory for Sustainable Business', 'How can we translate real-world challenges into future business opportunities? How can individuals, organizations, and society learn and undergo change at the pace needed to stave off worsening problems? Today, organizations of all kinds&mdash;traditional manufacturing firms, those that extract resources, a huge variety of new start-ups, services, non-profits, and governmental organizations of all types, among many others&mdash;are tackling these very questions.', 'How can we translate real-world challenges into future business opportunities? How can individuals, organizations, and society learn and undergo change at the pace needed to stave off worsening problems? Today, organizations of all kinds&mdash;traditional manufacturing firms, those that extract resources, a huge variety of new start-ups, services, non-profits, and governmental organizations of all types, among many others&mdash;are tackling these very questions. For some, the massive challenges of moving towards sustainability offer real opportunities for new products and services, for reinventing old ones, or for solving problems in new ways. The course aims to provide participants with access and in-depth exposure to firms that are actively grappling with the sustainability-related issues through cases, readings and guest speakers.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-992-s-lab-laboratory-for-sustainable-business-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-992-s-lab-laboratory-for-sustainable-business-spring-2008/15-992s08.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2543, 'Cross-Cultural Leadership', 'Cross Cultural Leadership is a collaborative research seminar that examines what constitutes ''effective'' leadership across cultures.', 'Cross Cultural Leadership is a collaborative research seminar that examines what constitutes ''effective'' leadership across cultures. It is collaborative because the students are expected to provide some of the content. The weekly readings target particular aspects of cultural differentiation. Working within those topics, students are asked to describe aspects of leadership in particular cultures based on their research and/or personal experiences. The goal of the course is to help prepare students for business assignments outside of their native countries. Course deliverables include: active participation in the class, contribution of class content on a weekly basis and an end of course paper that explores some aspect of leadership across cultures.', 'http://ocw.mit.edu/courses/sloan-school-of-management/15-996-cross-cultural-leadership-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/sloan-school-of-management/15-996-cross-cultural-leadership-fall-2004/15-996f04.jpg', 'Management', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2544, 'SCUBA', 'This course will thoroughly educate the successful student with the knowledge and skills necessary to be a certified beginning SCUBA diver.', 'This course will thoroughly educate the successful student with the knowledge and skills necessary to be a certified beginning SCUBA diver. The prerequisite for the course is passing the MIT SCUBA swim test and demonstrating a &quot;comfort level&quot; in the water. At the end of the class, students will attempt to pass the certification exam to become certified divers. The class is taught in two parts each week: a classroom session and a pool session. The classroom sessions along with the reading material will provide the student with the knowledge necessary to pass the written exam. At the pool, the water skills are taught in progressions that build on the previous skills, making the difficult skills seem easy.', 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-210-scuba-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-210-scuba-spring-2007/pe-210s07.jpg', 'Athletics, Physical Education, and Recreation', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2545, 'Designing Your Life', 'This course provides an exciting, eye-opening, and thoroughly useful inquiry into what it takes to live an extraordinary life, on your own terms.', 'This course provides an exciting, eye-opening, and thoroughly useful inquiry into what it takes to live an extraordinary life, on your own terms. The instructors address what it takes to succeed, to be proud of your life, and to be happy in it. Participants tackle career satisfaction, money, body, vices, and relationship to themselves. They learn how to confront issues in their lives, how to live life, and how to learn from it. A short version of this course meets during the Independent Activities Period (IAP), which is a special 4-week term at MIT that runs from the first week of January until the end of the month. Then this semester-long extension of the IAP course is taught to interested members of the MIT community. This not-for-credit course is sponsored by the Department of Science, Technology, and Society. A similar, semester-long version of this course is taught in the Sloan Fellows Program. Acknowledgment The instructors would like to thank Prof. David Mindell for his sponsorship of this course, his hopes for its continued expansion, and his commitment to the well-being of MIT students.', 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-550-designing-your-life-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-550-designing-your-life-spring-2009/pe-550s09.jpg', 'Athletics, Physical Education, and Recreation', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2546, 'Designing Your Life', 'This course provides an exciting, eye-opening, and thoroughly useful inquiry into what it takes to live an extraordinary life, on your own terms.', 'This course provides an exciting, eye-opening, and thoroughly useful inquiry into what it takes to live an extraordinary life, on your own terms. The instructors address what it takes to succeed, to be proud of your life, and to be happy in it. Participants tackle career satisfaction, money, body, vices, and relationship to themselves and others. They learn how to address issues in their lives, how to live life, and how to learn from it. This course is offered during the Independent Activities Period (IAP), which is a special 4-week term at MIT that runs from the first week of January until the end of the month. This not-for-credit course is sponsored by the Department of Science, Technology, and Society. A similar, semester-long version of this course is taught in the Sloan Fellows Program. A semester-long extension of the IAP course is also taught to the population at large of MIT (please see PE.550, Spring). Acknowledgment The instructors would like to thank Prof. David Mindell for his sponsorship of this course, his intention for its continued expansion, and his commitment to the well-being of MIT students.', 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-550-designing-your-life-january-iap-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-550-designing-your-life-january-iap-2007/pe-550iap07.jpg', 'Athletics, Physical Education, and Recreation', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2547, 'Weight Training', 'This 12 session course is designed for the beginning or novice weight lifter, or for those who have experience lifting but lack proper instruction.', 'This 12 session course is designed for the beginning or novice weight lifter, or for those who have experience lifting but lack proper instruction. We will provide an understanding of the biomechanics involved, muscles used for a given exercise, and program development.', 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-720-weight-training-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-720-weight-training-spring-2006/pe-720s06.jpg', 'Athletics, Physical Education, and Recreation', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2548, 'Archery', 'This 12 session course is designed for the beginning or novice archer and uses recurve indoor target bows and equipment.', 'This 12 session course is designed for the beginning or novice archer and uses recurve indoor target bows and equipment. The purpose of the course is to introduce students to the basic techniques of indoor target archery emphasizing the care and use of equipment, range safety, stance and shooting techniques, scoring and competition.', 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-730-archery-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-730-archery-spring-2006/pe-730s06.jpg', 'Athletics, Physical Education, and Recreation', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2549, 'Fencing', 'This course is intended to provide students with the fundamentals of fencing, including footwork, bladework, bouting and refereeing.', 'This course is intended to provide students with the fundamentals of fencing, including footwork, bladework, bouting and refereeing. It will allow students to develop the ability to analyze a fencing bout, and promotes creativity in applying acquired skills in a fencing bout.', 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-740-fencing-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-740-fencing-spring-2007/pe-740s07.jpg', 'Athletics, Physical Education, and Recreation', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2550, 'Physical Intelligence', 'For all of the bodies attached to the many great minds that walk the Institute''s halls, in the work that goes on at MIT the body is present as an object of study, but is all but unrecognized as an important dimension of our intelligence and experience.', 'For all of the bodies attached to the many great minds that walk the Institute''s halls, in the work that goes on at MIT the body is present as an object of study, but is all but unrecognized as an important dimension of our intelligence and experience. Yet the body is the basis of our experience in the world; it is the very foundation on which cognitive intelligence is built. Using the MIT gymnastics gym as our laboratory, the Physical Intelligence activity will take an innovative, hands-on approach to explore the kinesthetic intelligence of the body as applicable to a wide range of disciplines. Via exercises, activities, readings and discussions designed to excavate our physical experience, we will not only develop balance, agility, flexibility and strength, but a deep appreciation for the inherent unity of mind and body that suggests physical intelligence as a powerful complement to cognitive intelligence.', 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-910-physical-intelligence-january-iap-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/athletics-physical-education-and-recreation/pe-910-physical-intelligence-january-iap-2002/pe-910iap02.jpg', 'Athletics, Physical Education, and Recreation', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2551, 'PE for ME', 'The sensing, thinking, moving body is the basis of our experience in the world; it is the very foundation on which cognitive intelligence is built.', 'The sensing, thinking, moving body is the basis of our experience in the world; it is the very foundation on which cognitive intelligence is built. Physical Intelligence, then, is the inherent ability of the human organism to function in extraordinary accord with its physical environment. This class--a joint DAPER/ME offering for both PE and academic credit--uses the MIT gymnastics gym as a laboratory to explore Physical Intelligence as applied to ME and design. Readings, discussions and experiential learning introduce various dimensions of Physical Intelligence which students then apply to the design of innovative exercise equipment.', 'http://ocw.mit.edu/courses/special-programs/sp-251-pe-for-me-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-251-pe-for-me-spring-2005/sp-251s05.jpg', 'Athletics, Physical Education, and Recreation', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2552, 'Science Writing and New Media', 'This course introduces writing, graphics, meetings, oral presentation, collaboration, and design as tools for product development.', 'This course introduces writing, graphics, meetings, oral presentation, collaboration, and design as tools for product development. The communication instruction is embedded in design projects that require students to work in teams to conceive, design, prototype and evaluate energy related products. The communication instruction focuses on the communication tasks that are integral to this design process, ranging, across design notebooks, email communications, informal oral presentations, meeting etiquette, literature searches, white papers reports, and formal presentations. In addition to the assignments specific to product development, a few assignments, especially reading and reflection, will address the cultural situation of engineers and engineering in the world at large.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-science-writing-and-new-media-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-732-science-writing-and-new-media-fall-2010/21w-732f10.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2553, 'Physics I: Classical Mechanics with an Experimental Focus', 'Physics I is a first-year physics course which introduces students to classical mechanics.', 'Physics I is a first-year physics course which introduces students to classical mechanics. This course has a hands-on focus, and approaches mechanics through take-home experiments. Topics include: kinematics, Newton''s laws of motion, universal gravitation, statics, conservation laws, energy, work, momentum, and special relativity.', 'http://ocw.mit.edu/courses/physics/8-01x-physics-i-classical-mechanics-with-an-experimental-focus-fall-2002', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-01x-physics-i-classical-mechanics-with-an-experimental-focus-fall-2002/8-01xf02.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2554, 'Physics II: Electricity and Magnetism', 'This course runs parallel to 8.', 'This course runs parallel to 8.02, but assumes that students have some knowledge of vector calculus. The class introduces Maxwell''s equations, in both differential and integral form, along with electrostatic and magnetic vector potential, and the properties of dielectrics and magnetic materials. This class was taught by an undergraduate in the Experimental Study Group (ESG). Student instructors are paired with ESG faculty members, who advise and oversee the students'' teaching efforts.', 'http://ocw.mit.edu/courses/physics/8-022-physics-ii-electricity-and-magnetism-fall-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/physics/8-022-physics-ii-electricity-and-magnetism-fall-2006/8-022f06.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2555, 'Chemistry of Sports', 'This seminar will focus on three sports: swimming, cycling and running.', 'This seminar will focus on three sports: swimming, cycling and running. There will be two components to the seminar: classroom sessions and a ''laboratory'' in the form of a structured training program. The classroom component will introduce the students to the chemistry of their own biological system. With swimming, running and cycling as sample sports, students are encouraged to apply their knowledge to complete a triathlon shortly after the term.', 'http://ocw.mit.edu/courses/special-programs/sp-235-chemistry-of-sports-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-235-chemistry-of-sports-spring-2009/sp-235s09.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2556, 'Exploring Pharmacology', 'From Abilify to Zyrtec, the world is full of interesting drugs.', 'From Abilify to Zyrtec, the world is full of interesting drugs. Such substances have cured diseases, started wars, and ended careers. This seminar will explain how drugs can elicit a range of medicinal and recreational effects. Planned topics include over-the-counter drugs and ''dietary supplements,'' drugs of abuse, treatments for neurological disorders, psychiatric medications, and many more. Prior experience is neither expected nor required, but student participation is essential.', 'http://ocw.mit.edu/courses/special-programs/sp-236-exploring-pharmacology-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-236-exploring-pharmacology-spring-2009/sp-236s09.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2557, 'Composing Your Life: Exploration of Self through Visual Arts and Writing', 'In this interdisciplinary seminar, we explore a variety of visual and written tools for self exploration and self expression.', 'In this interdisciplinary seminar, we explore a variety of visual and written tools for self exploration and self expression. Through discussion, written assignments, and directed exercises, students practice utilizing a variety of media to explore and express who they are.', 'http://ocw.mit.edu/courses/special-programs/sp-240-composing-your-life-exploration-of-self-through-visual-arts-and-writing-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-240-composing-your-life-exploration-of-self-through-visual-arts-and-writing-spring-2006/sp-240s06.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2558, 'Current Events and Social Issues', 'The goal of this seminar is to have open discussions of controversial political and social issues and raise awareness of current world events in an informal setting.', 'The goal of this seminar is to have open discussions of controversial political and social issues and raise awareness of current world events in an informal setting. Discussions for the first part of each class will focus on current events from that week, while in the second part of class students will discuss a scheduled issue in greater detail. Scheduled issues include the wars in Iraq and Afghanistan, the regulation of marijuana, how our society should punish criminals, genocide in Rwanda and Sudan, discrimination in our society today, the future of social security, whether pornography is sexist, and where we can go from here in the Arab/Israeli Conflict. Discussions will be supplemented by readings, films, and public speakers. Students will also be encouraged to read news media from around the world.', 'http://ocw.mit.edu/courses/special-programs/sp-246-current-events-and-social-issues-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-246-current-events-and-social-issues-fall-2004/sp-246f04.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2559, 'AIDS and Poverty in Africa', 'This is a discussion-based interactive seminar on the two major issues that affect Sub-Saharan Africa: HIV/AIDS and Poverty.', 'This is a discussion-based interactive seminar on the two major issues that affect Sub-Saharan Africa: HIV/AIDS and Poverty. AIDS and Poverty, seemingly different concepts, are more inter-related to each other in Africa than in any other continent. As MIT students, we feel it is important to engage ourselves in a dynamic discussion on the relation between the two - how to fight one and how to solve the other.', 'http://ocw.mit.edu/courses/special-programs/sp-253-aids-and-poverty-in-africa-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-253-aids-and-poverty-in-africa-spring-2005/sp-253s05.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2560, 'Physics of Rock Climbing', 'SP.', 'SP.255 is a lecture, discussion, and project based seminar about the physics of rock climbing. Participants are first exposed to the unsolved problems in the climbing community that could be answered by research and then asked to solve a small part of one of these problems. The seminar provides an introduction to engineering problems, an opportunity to practice communication skills, and a brief stab at doing some research. This seminar explicitly does not include climbing instruction nor is climbing/mountaineering experience a prerequisite.', 'http://ocw.mit.edu/courses/special-programs/sp-255-physics-of-rock-climbing-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-255-physics-of-rock-climbing-spring-2006/sp-255s06.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2561, 'GÃ¶del, Escher, Bach', 'How are math, art, music, and language intertwined? How does intelligent behavior arise from its component parts? Can computers think? Can brains compute? Douglas Hofstadter probes very cleverly at these questions and more in his Pulitzer Prize winning book, ''G&ouml;del, Escher, Bach''.', 'How are math, art, music, and language intertwined? How does intelligent behavior arise from its component parts? Can computers think? Can brains compute? Douglas Hofstadter probes very cleverly at these questions and more in his Pulitzer Prize winning book, ''G&ouml;del, Escher, Bach''. In this seminar, we will read and discuss the book in depth, taking the time to solve its puzzles, appreciate the Bach pieces that inspired its dialogues, and discover its hidden tricks along the way.', 'http://ocw.mit.edu/courses/special-programs/sp-258-goedel-escher-bach-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-258-goedel-escher-bach-spring-2007/sp-258s07.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2562, 'Information and Communication Technology in Africa', 'This is a discussion-based, interactive seminar on the development of information and communication technology in Sub-Saharan Africa.', 'This is a discussion-based, interactive seminar on the development of information and communication technology in Sub-Saharan Africa. The students will seek to understand the issues surrounding designing and instituting policy, and explore the possible ways in which they can make an impact on information and communication technology in Africa.', 'http://ocw.mit.edu/courses/special-programs/sp-259-information-and-communication-technology-in-africa-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-259-information-and-communication-technology-in-africa-spring-2006/sp-259s06.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2563, 'Women''s Novels: A Weekly Book Club', 'This pass/fail seminar should be a fun setting where we can all enjoy a love of good books together.', 'This pass/fail seminar should be a fun setting where we can all enjoy a love of good books together. Students will read approximately one novel every two weeks, and the class will discuss each novel in a relaxed and interactive setting, with attention to whatever themes and issues interest them most about each book. We will read a wide mixture of classic and contemporary novels written by women, including: Edith Wharton, The House of Mirth; Toni Morrison, Jazz; Virginia Woolf, Mrs. Dalloway; Alice Walker, The Color Purple; Charlotte Bront&euml;, Jane Eyre; Sheri Reynolds, The Rapture of Canaan; Jane Austen, Pride and Prejudice; and Sylvia Plath, The Bell Jar. Recurrent issues likely to be discussed include: gender, race, and class; romance, love, and marriage; depression and suicide; and conception, childbirth, and parenthood.', 'http://ocw.mit.edu/courses/special-programs/sp-260-womens-novels-a-weekly-book-club-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-260-womens-novels-a-weekly-book-club-spring-2006/sp-260s06.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2564, 'The Mathematics in Toys and Games', 'We will explore the mathematical strategies behind popular games, toys, and puzzles.', 'We will explore the mathematical strategies behind popular games, toys, and puzzles. Topics covered will combine basic fundamentals of game theory, probability, group theory, and elementary programming concepts. Each week will consist of a lecture and discussion followed by game play to implement the concepts learned in class.', 'http://ocw.mit.edu/courses/special-programs/sp-268-the-mathematics-in-toys-and-games-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-268-the-mathematics-in-toys-and-games-spring-2010/sp-268s10.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2565, 'Passing: Flexibility in Race and Gender', 'This course is primarily a literature seminar.', 'This course is primarily a literature seminar. We will use American literature as a lens through which to examine different passing tropes. It will provide an introduction to queer, gender, and critical race theories for science and math majors. We will read such works as Running A Thousand Miles for Freedom, Incognegro, and Focault''s A History of Sexuality, to name just a few.', 'http://ocw.mit.edu/courses/special-programs/sp-269-passing-flexibility-in-race-and-gender-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-269-passing-flexibility-in-race-and-gender-spring-2009/sp-269s09.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2566, 'Culture Tech', 'This class is divided into a series of sections or &quot;modules&quot;, each of which concentrates on a particular large technology-related topic in a cultural context.', 'This class is divided into a series of sections or &quot;modules&quot;, each of which concentrates on a particular large technology-related topic in a cultural context. The class will start with a four-week module on Samurai Swords and Blacksmithing, followed by smaller units on Chinese Cooking, the Invention of Clocks, and Andean Weaving, and end with a four-week module on Automobiles and Engines. In addition, there will be a series of hands-on projects that tie theory and practice together. The class discussions range across anthropology, history, and individual development, emphasizing recurring themes, such as the interaction between technology and culture and the relation between &quot;skill&quot; knowledge and &quot;craft&quot; knowledge.Culture Tech evolved from a more extensive, two-semester course which formed the centerpiece of the Integrated Studies Program at MIT.&nbsp; For 13 years, ISP was an alternative first-year program combining humanities, physics, learning-by-doing, and weekly luncheons.&nbsp; Culture Tech represents the core principles of ISP distilled into a 6-unit seminar. Although many collections of topics have been used over the years, the modules presented here are a representative sequence.&nbsp;', 'http://ocw.mit.edu/courses/special-programs/sp-272-culture-tech-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-272-culture-tech-spring-2003/sp-272s03.png', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2567, 'Learning Seminar: Experiments in Education', 'This seminar explores experiments in education and discusses how education and learning might be done, through reading and discussion.', 'This seminar explores experiments in education and discusses how education and learning might be done, through reading and discussion. This seminar is not&nbsp;a survey of experiments in education, but rather, its goal is to determine how learning should happen and what kinds of contexts allow it to happen.', 'http://ocw.mit.edu/courses/special-programs/sp-291-learning-seminar-experiments-in-education-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-291-learning-seminar-experiments-in-education-spring-2003/sp-291s03.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2568, 'Writing Workshop', 'MIT students are challenged daily to solve for x, to complete four problem sets, two papers, and prepare for an exam worth 30% of their grade.', 'MIT students are challenged daily to solve for x, to complete four problem sets, two papers, and prepare for an exam worth 30% of their grade... all in one night. When they do stop to breathe, it''s for a shower or a meal. What does this have to do with creative writing? Everything. Creative writing and MIT go together better than you might imagine.', 'http://ocw.mit.edu/courses/special-programs/sp-292-writing-workshop-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-292-writing-workshop-spring-2008/sp-292s08.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2569, 'Lego Robotics', 'LEGO&reg; robotics uses LEGO&reg;s as a fun tool to explore robotics, mechanical systems, electronics, and programming.', 'LEGO&reg; robotics uses LEGO&reg;s as a fun tool to explore robotics, mechanical systems, electronics, and programming. This seminar is primarily a lab experience which provides students with resources to design, build, and program functional robots constructed from LEGO&reg;s and a few other parts such as motors and sensors.', 'http://ocw.mit.edu/courses/special-programs/sp-293-lego-robotics-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-293-lego-robotics-spring-2007/sp-293s07.jpg', 'Experimental Study Group', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2570, 'Ancient Philosophy and Mathematics', 'Western philosophy and theoretical mathematics were born together, and the cross-fertilization of ideas in the two disciplines was continuously acknowledged throughout antiquity.', 'Western philosophy and theoretical mathematics were born together, and the cross-fertilization of ideas in the two disciplines was continuously acknowledged throughout antiquity. In this course, we read works of ancient Greek philosophy and mathematics, and investigate the way in which ideas of definition, reason, argument and proof, rationality and irrationality, number, quality and quantity, truth, and even the idea of an idea were shaped by the interplay of philosophic and mathematical inquiry.', 'http://ocw.mit.edu/courses/special-programs/sp-2h3-ancient-philosophy-and-mathematics-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-2h3-ancient-philosophy-and-mathematics-fall-2009/sp-2h3f09.jpg', 'Experimental Study Group', 'MIT');
