INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2575, 'PE for ME', 'The sensing, thinking, moving body is the basis of our experience in the world; it is the very foundation on which cognitive intelligence is built.', 'The sensing, thinking, moving body is the basis of our experience in the world; it is the very foundation on which cognitive intelligence is built. Physical Intelligence, then, is the inherent ability of the human organism to function in extraordinary accord with its physical environment. This class--a joint DAPER/ME offering for both PE and academic credit--uses the MIT gymnastics gym as a laboratory to explore Physical Intelligence as applied to ME and design. Readings, discussions and experiential learning introduce various dimensions of Physical Intelligence which students then apply to the design of innovative exercise equipment.', 'http://ocw.mit.edu/courses/special-programs/sp-251-pe-for-me-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-251-pe-for-me-spring-2005/sp-251s05.jpg', 'Special Programs', 'MIT');
