INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2577, 'The Coming Years', 'Explore the future through modeling, reading, and discussion in an open-ended seminar! Our fields of interest will include changes in science and technology, culture and lifestyles, and dominant paradigms and societies.', 'Explore the future through modeling, reading, and discussion in an open-ended seminar! Our fields of interest will include changes in science and technology, culture and lifestyles, and dominant paradigms and societies.', 'http://ocw.mit.edu/courses/special-programs/sp-256-the-coming-years-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-256-the-coming-years-spring-2008/sp-256s08.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2578, 'GÃ¶del, Escher, Bach', 'How are math, art, music, and language intertwined? How does intelligent behavior arise from its component parts? Can computers think? Can brains compute? Douglas Hofstadter probes very cleverly at these questions and more in his Pulitzer Prize winning book, ''G&ouml;del, Escher, Bach''.', 'How are math, art, music, and language intertwined? How does intelligent behavior arise from its component parts? Can computers think? Can brains compute? Douglas Hofstadter probes very cleverly at these questions and more in his Pulitzer Prize winning book, ''G&ouml;del, Escher, Bach''. In this seminar, we will read and discuss the book in depth, taking the time to solve its puzzles, appreciate the Bach pieces that inspired its dialogues, and discover its hidden tricks along the way.', 'http://ocw.mit.edu/courses/special-programs/sp-258-goedel-escher-bach-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-258-goedel-escher-bach-spring-2007/sp-258s07.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2579, 'Information and Communication Technology in Africa', 'This is a discussion-based, interactive seminar on the development of information and communication technology in Sub-Saharan Africa.', 'This is a discussion-based, interactive seminar on the development of information and communication technology in Sub-Saharan Africa. The students will seek to understand the issues surrounding designing and instituting policy, and explore the possible ways in which they can make an impact on information and communication technology in Africa.', 'http://ocw.mit.edu/courses/special-programs/sp-259-information-and-communication-technology-in-africa-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-259-information-and-communication-technology-in-africa-spring-2006/sp-259s06.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2580, 'Poetry in Translation', 'This seminar addresses the inherent challenges of translating poetry from different languages, cultures, and eras.', 'This seminar addresses the inherent challenges of translating poetry from different languages, cultures, and eras. Students do some translation of their own, though accommodations are made if a student lacks even a basic knowledge of any foreign language.', 'http://ocw.mit.edu/courses/special-programs/sp-261-poetry-in-translation-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-261-poetry-in-translation-spring-2006/sp-261s06.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2581, 'The Mathematics in Toys and Games', 'We will explore the mathematical strategies behind popular games, toys, and puzzles.', 'We will explore the mathematical strategies behind popular games, toys, and puzzles. Topics covered will combine basic fundamentals of game theory, probability, group theory, and elementary programming concepts. Each week will consist of a lecture and discussion followed by game play to implement the concepts learned in class.', 'http://ocw.mit.edu/courses/special-programs/sp-268-the-mathematics-in-toys-and-games-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-268-the-mathematics-in-toys-and-games-spring-2010/sp-268s10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2582, 'Passing: Flexibility in Race and Gender', 'This course is primarily a literature seminar.', 'This course is primarily a literature seminar. We will use American literature as a lens through which to examine different passing tropes. It will provide an introduction to queer, gender, and critical race theories for science and math majors. We will read such works as Running A Thousand Miles for Freedom, Incognegro, and Focault''s A History of Sexuality, to name just a few.', 'http://ocw.mit.edu/courses/special-programs/sp-269-passing-flexibility-in-race-and-gender-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-269-passing-flexibility-in-race-and-gender-spring-2009/sp-269s09.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2583, 'Kitchen Chemistry', 'This seminar is designed to be an experimental and hands-on approach to applied chemistry (as seen in cooking).', 'This seminar is designed to be an experimental and hands-on approach to applied chemistry (as seen in cooking). Cooking may be the oldest and most widespread application of chemistry and recipes may be the oldest practical result of chemical research. We shall do some cooking experiments to illustrate some chemical principles, including extraction, denaturation, and phase changes.', 'http://ocw.mit.edu/courses/special-programs/sp-287-kitchen-chemistry-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-287-kitchen-chemistry-spring-2009/sp-287s09.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2584, 'Learning Seminar: Experiments in Education', 'This seminar explores experiments in education and discusses how education and learning might be done, through reading and discussion.', 'This seminar explores experiments in education and discusses how education and learning might be done, through reading and discussion. This seminar is not&nbsp;a survey of experiments in education, but rather, its goal is to determine how learning should happen and what kinds of contexts allow it to happen.', 'http://ocw.mit.edu/courses/special-programs/sp-291-learning-seminar-experiments-in-education-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-291-learning-seminar-experiments-in-education-spring-2003/sp-291s03.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2585, 'Writing Workshop', 'MIT students are challenged daily to solve for x, to complete four problem sets, two papers, and prepare for an exam worth 30% of their grade.', 'MIT students are challenged daily to solve for x, to complete four problem sets, two papers, and prepare for an exam worth 30% of their grade... all in one night. When they do stop to breathe, it''s for a shower or a meal. What does this have to do with creative writing? Everything. Creative writing and MIT go together better than you might imagine.', 'http://ocw.mit.edu/courses/special-programs/sp-292-writing-workshop-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-292-writing-workshop-spring-2008/sp-292s08.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2586, 'Art of Color', 'This seminar introduces, through studio projects, the basic principles regarding the use of color in the visual arts.', 'This seminar introduces, through studio projects, the basic principles regarding the use of color in the visual arts. Students explore a range of topics, including the historical uses of color in the arts, the interactions between colors, and the psychology of color.', 'http://ocw.mit.edu/courses/special-programs/sp-298-art-of-color-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-298-art-of-color-spring-2005/sp-298s05.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2587, 'Ancient Philosophy and Mathematics', 'Western philosophy and theoretical mathematics were born together, and the cross-fertilization of ideas in the two disciplines was continuously acknowledged throughout antiquity.', 'Western philosophy and theoretical mathematics were born together, and the cross-fertilization of ideas in the two disciplines was continuously acknowledged throughout antiquity. In this course, we read works of ancient Greek philosophy and mathematics, and investigate the way in which ideas of definition, reason, argument and proof, rationality and irrationality, number, quality and quantity, truth, and even the idea of an idea were shaped by the interplay of philosophic and mathematical inquiry.', 'http://ocw.mit.edu/courses/special-programs/sp-2h3-ancient-philosophy-and-mathematics-fall-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-2h3-ancient-philosophy-and-mathematics-fall-2009/sp-2h3f09.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2588, 'Prohibition and Permission', 'Explore where the prohibitions and permissions that occur in every day life come from, why they exist, and what gives them force.', 'Explore where the prohibitions and permissions that occur in every day life come from, why they exist, and what gives them force. For example: food&mdash;you are only willing and able to eat a subset of the world''s edible substances. Marriage&mdash;some marriages are prohibited by law or by custom. This course addresses questions of prohibition and permission using psychological sources and literary works from ancient to modern. Texts include works by Shakespeare, Melville, Mary Rowlandson, and Anita Desai. Students give group and individual oral presentations.', 'http://ocw.mit.edu/courses/special-programs/sp-322-prohibition-and-permission-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-322-prohibition-and-permission-spring-2007/sp-322s07.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2589, 'Special Topics in Women & Gender Studies Seminar: Latina Women''s Voices', 'This course will explore the rich diversity of women''s voices and experiences as reflected in writings and films by and about Latina writers, filmmakers, and artists.', 'This course will explore the rich diversity of women''s voices and experiences as reflected in writings and films by and about Latina writers, filmmakers, and artists. Through close readings, class discussions and independently researched student presentations related to each text, we will explore not only the unique, individual voice of the writer, but also the cultural, social and political contexts which inform their narratives. We will also examine the roles that gender, familial ties and social and political preoccupations play in shaping the values of the writers and the nature of the characters encountered in the texts and films.', 'http://ocw.mit.edu/courses/special-programs/sp-400-special-topics-in-women-gender-studies-seminar-latina-womens-voices-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-400-special-topics-in-women-gender-studies-seminar-latina-womens-voices-spring-2010/sp-400s10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2590, 'Introduction to Women''s and Gender Studies', 'This course offers an introduction to Women''s and Gender Studies, an interdisciplinary academic field that asks critical questions about the meaning of gender in society.', 'This course offers an introduction to Women''s and Gender Studies, an interdisciplinary academic field that asks critical questions about the meaning of gender in society. The primary goal of this course is to familiarize students with key issues, questions and debates in Women''s and Gender Studies scholarship, both historical and contemporary. Gender scholarship critically analyzes themes of gendered performance and power in a range of social spheres, such as law, culture, work, medicine and the family.', 'http://ocw.mit.edu/courses/special-programs/sp-401-introduction-to-womens-and-gender-studies-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-401-introduction-to-womens-and-gender-studies-fall-2010/sp-401f10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2591, 'Introduction to Women''s and Gender Studies', 'This course is designed as an introduction to the interdisciplinary field of Women''s and Gender Studies, an academic area of study focused on the ways that sex and gender manifest themselves in social, cultural, and political contexts.', 'This course is designed as an introduction to the interdisciplinary field of Women''s and Gender Studies, an academic area of study focused on the ways that sex and gender manifest themselves in social, cultural, and political contexts. The primary goal of this course is to familiarize students with key issues, questions and debates in Women''s Studies scholarship, both historical and contemporary. This semester you will become acquainted with many of the critical questions and concepts feminist scholars have developed as tools for thinking about gendered experience. In addition, we will study the interconnections among systems of oppression (such as sexism, racism, classism, ethnocentrism, homophobia/heterosexism, transphobia, ableism and others.) In this course you will learn to ''read'' and analyze gender, exploring how it impacts our understanding of the world.', 'http://ocw.mit.edu/courses/special-programs/sp-401-introduction-to-womens-and-gender-studies-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-401-introduction-to-womens-and-gender-studies-spring-2009/sp-401s09.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2592, 'Gender and Media Studies: Women and the Media', 'This course examines representations of race, class, gender, and sexual identity in the media.', 'This course examines representations of race, class, gender, and sexual identity in the media. We will be considering issues of authorship, spectatorship, (audience) and the ways in which various media content (film, television, print journalism, advertising) enables, facilitates, and challenges these social constructions in society. In addition, we will examine how gender and race affects the production of media, and discuss the impact of new media and digital media and how it has transformed access and participation, moving contemporary media users from a traditional position of ''readers'' to ''writers'' and/or commentators. Students will analyze gendered and racialized language and embodiment as it is produced online in blogs and vlogs, avatars, and in the construction of cyberidentities. The course provides an introduction to feminist approaches to media studies by drawing from work in feminist film theory, journalism, cultural studies, gender and politics, and cyberfeminism.', 'http://ocw.mit.edu/courses/special-programs/sp-414-gender-and-media-studies-women-and-the-media-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-414-gender-and-media-studies-women-and-the-media-fall-2008/sp-414f08.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2593, 'Literary Interpretation: Virginia Woolf''s Shakespeare', 'How does one writer use another writer''s work?&nbsp;Does it matter if one author has been dead 300 years? What difference does it make if she''s a groundbreaking twentieth-century feminist and the writer she values has come to epitomize the English literary tradition? How can a novelist borrow from plays and poems? By reading Virginia Woolf''s major novels and essays in juxtaposition with some of the Shakespeare plays that (depending on one''s interpretation) haunt, enrich, and/or shape her writing, we will try to answer these questions and raise others.', 'How does one writer use another writer''s work?&nbsp;Does it matter if one author has been dead 300 years? What difference does it make if she''s a groundbreaking twentieth-century feminist and the writer she values has come to epitomize the English literary tradition? How can a novelist borrow from plays and poems? By reading Virginia Woolf''s major novels and essays in juxtaposition with some of the Shakespeare plays that (depending on one''s interpretation) haunt, enrich, and/or shape her writing, we will try to answer these questions and raise others. Readings in literary criticism, women''s studies, and other literary texts will complement our focus on the relationship--across time, media, and gender--between Shakespeare and Woolf.&nbsp;As a seminar, we will work to become more astute readers of literature within its historical, artistic, and political contexts, and consider how literature both reflects and contributes to these societal frameworks.&nbsp;Central texts will include Shakespeare''s Othello, Antony and Cleopatra, Cymbeline, and The Winter''s Tale, and Woolf''s A Room of One''s Own, Mrs. Dalloway, Orlando, To the Lighthouse, The Waves, and Between the Acts. This subject is an advanced seminar in both the Literature and the Women''s Studies Program.', 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-virginia-woolfs-shakespeare-spring-2001', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-virginia-woolfs-shakespeare-spring-2001/21l-701s01.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2594, 'Gender and Representation of Asian Women', 'This course explores stereotypes associated with Asian women in colonial, nationalist, state-authoritarian, and global/diasporic narratives about gender and power.', 'This course explores stereotypes associated with Asian women in colonial, nationalist, state-authoritarian, and global/diasporic narratives about gender and power. Students will read ethnography, cultural studies, and history, and view films to examine the politics and circumstances that create and perpetuate the representation of Asian women as dragon ladies, lotus blossoms, despotic tyrants, desexualized servants, and docile subordinates. Students are introduced to the debates about Orientalism, gender, and power.', 'http://ocw.mit.edu/courses/anthropology/21a-470j-gender-and-representation-of-asian-women-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-470j-gender-and-representation-of-asian-women-spring-2010/21a-470js10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2595, 'Gender, Sexuality, and Society', 'This course seeks to examine how people experience gender - what it means to be a man or a woman - and sexuality in a variety of historical and cultural contexts.', 'This course seeks to examine how people experience gender - what it means to be a man or a woman - and sexuality in a variety of historical and cultural contexts. We will explore how gender and sexuality relate to other categories of social identity and difference, such as race and ethnicity, economic and social standing, urban or rural life, etc. One goal of the class is to learn how to critically assess media and other popular representations of gender roles and stereotypes. Another is to gain a greater sense of the diversity of human social practices and beliefs in the United States and around the world.', 'http://ocw.mit.edu/courses/anthropology/21a-231j-gender-sexuality-and-society-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-231j-gender-sexuality-and-society-spring-2006/21a-231js06.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2596, 'The Contemporary American Family', 'We begin by considering briefly the evolution of the family, its cross-cultural variability, and its history in the West.', 'We begin by considering briefly the evolution of the family, its cross-cultural variability, and its history in the West. We next examine how the family is currently defined in the U.S., discussing different views about what families should look like.&#160;Class and ethnic variability and the effects of changing gender roles are discussed in this section. We next look at sexuality, traditional and non-traditional marriage, parenting, divorce, family violence, family economics, poverty, and family policy.&#160;Controversial issues dealt with include day care, welfare policy, and the ''Family Values'' debate.', 'http://ocw.mit.edu/courses/anthropology/21a-230j-the-contemporary-american-family-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-230j-the-contemporary-american-family-spring-2004/21a-230js04.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2597, 'Gender, Power, and International Development', 'After decades of efforts to promote development, why is there so much poverty in the world? What are some of the root causes of inequality world-wide and why do poverty, economic transformations and development policies often have different consequences for women and men? This course explores these issues while also examining the history of development itself, its underlying assumptions, and its range of supporters and critics.', 'After decades of efforts to promote development, why is there so much poverty in the world? What are some of the root causes of inequality world-wide and why do poverty, economic transformations and development policies often have different consequences for women and men? This course explores these issues while also examining the history of development itself, its underlying assumptions, and its range of supporters and critics. It considers the various meanings given to development by women and men, primarily as residents of particular regions, but also as aid workers, policy makers and government officials. In considering how development projects and policies are experienced in daily life in urban and rural areas in Africa, Latin America, Asia and Melanesia, this course asks what are the underlying political, economic, social, and gender dynamics that make ''development'' an ongoing problem world-wide.', 'http://ocw.mit.edu/courses/anthropology/21a-338j-gender-power-and-international-development-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-338j-gender-power-and-international-development-fall-2003/21a-338jf03.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2598, 'International Women''s Voices', 'International Women&#8217;s Voices has several objectives.', 'International Women&#8217;s Voices has several objectives. It introduces students to a variety of works by contemporary women writers from Asia, Africa, the Middle East, Latin America and North America. The emphasis is on non-western writers. The readings are chosen to encourage students to think about how each author&#8217;s work reflects a distinct cultural heritage and to what extent, if any, we can identify a female voice that transcends national cultures. In lectures and readings distributed in class, students learn about the history and culture of each of the countries these authors represent. The way in which colonialism, religion, nation formation and language influence each writer is a major concern of this course. In addition, students examine the patterns of socialization of women in patriarchal cultures, and how, in the imaginary world, authors resolve or understand the relationship of the characters to love, work, identity, sex roles, marriage, and politics.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-022j-international-womens-voices-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-022j-international-womens-voices-spring-2004/21f-022js04.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2599, 'Popular Culture and Narrative: Literature, Comics, and Culture', 'In this course, we will investigate popular culture and narrative by focusing on the relationship between literary texts and comics.', 'In this course, we will investigate popular culture and narrative by focusing on the relationship between literary texts and comics. Several questions shape the syllabus and provide a framework for approaching the course materials: How do familiar aspects of comics trace their origins to literary texts and broader cultural concerns? How have classic comics gone on to influence literary fiction? In what ways do contemporary graphic narratives bring a new kind of seriousness of purpose to comics, blurring what''s left of the boundaries between the highbrow and the lowbrow? Readings and materials for the course range from the nineteenth century to the present, and include novels, short stories, essays, older and newer comics, and some older and newer films. Expectations include diligent reading, active participation, occasional discussion leading, and two papers.', 'http://ocw.mit.edu/courses/literature/21l-430-popular-culture-and-narrative-literature-comics-and-culture-fall-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-430-popular-culture-and-narrative-literature-comics-and-culture-fall-2010/21l-430f10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2600, 'Popular Narrative: Masterminds', 'Our purpose is to consider some of the most elaborate and thoughtful efforts to define and delineate ''all-mastering,'' and to consider some of the delineations of ''all-mastering the intellect'' in various guises - from magicians to master spies to detectives to scientists (mad and otherwise).', 'Our purpose is to consider some of the most elaborate and thoughtful efforts to define and delineate ''all-mastering,'' and to consider some of the delineations of ''all-mastering the intellect'' in various guises - from magicians to master spies to detectives to scientists (mad and otherwise). The major written work of the term will be an ongoing reading journal, which you will circulate to your classmates using an e-mail mailing list. The use of that list is fundamental - it is my intention to generate a sort of ongoing cyberconversation.', 'http://ocw.mit.edu/courses/literature/21l-430-popular-narrative-masterminds-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-430-popular-narrative-masterminds-fall-2004/21l-430f04.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2601, 'Literary Interpretation: Literature and Urban Experience', 'Alienation, overcrowding, sensory overload, homelessness, criminality, violence, loneliness, sprawl, blight.', 'Alienation, overcrowding, sensory overload, homelessness, criminality, violence, loneliness, sprawl, blight. How have the realities of city living influenced literature''s formal and thematic techniques? How useful is it to think of literature as its own kind of ''map'' of urban space? Are cities too grand, heterogeneous, and shifting to be captured by writers? In this seminar we will seek answers to these questions in key city literature, and in theoretical works that endeavor to understand the culture of cities.', 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-literature-and-urban-experience-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-701-literary-interpretation-literature-and-urban-experience-spring-2009/21l-701s09.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2602, 'Major Authors: Rewriting Genesis: "Paradise Lost" and Twentieth-Century Fantasy', 'What does the Genesis story of creation and temptation tell us about gender, about heterosexuality, and about the origins of evil? What is the nature of God, and how can we account for that nature in a cosmos where evil exists? When is rebellion justified, and when is authority legitimate? These are some of the key questions that engaged the poet John Milton, and that continue to engage readers of his work.', 'What does the Genesis story of creation and temptation tell us about gender, about heterosexuality, and about the origins of evil? What is the nature of God, and how can we account for that nature in a cosmos where evil exists? When is rebellion justified, and when is authority legitimate? These are some of the key questions that engaged the poet John Milton, and that continue to engage readers of his work.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-rewriting-genesis-paradise-lost-and-twentieth-century-fantasy-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-rewriting-genesis-paradise-lost-and-twentieth-century-fantasy-spring-2009/21l-705s09.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2603, 'Major Authors: John Milton', 'In 1667, John Milton published what he intended both as the crowning achievement of a poetic career and a justification of God''s ways to man: an epic poem which retold and reimagined the Biblical story of creation, temptation, and original sin.', 'In 1667, John Milton published what he intended both as the crowning achievement of a poetic career and a justification of God''s ways to man: an epic poem which retold and reimagined the Biblical story of creation, temptation, and original sin. Even in a hostile political climate, Paradise Lost was almost immediately recognized as a classic, and one fate of a classic is to be rewritten, both by admirers and by antagonists. In this seminar, we will read Paradise Lost alongside works of 20th century fantasy and science fiction which rethink both Milton''s text and its source. Students should come to the seminar having read Paradise Lost straight through at least once; this can be accomplished by taking the IAP subject, Reading Paradise Lost (21L.995), or independently. Twentieth century authors will include C. S. Lewis (Perelandra, The Lion, the Witch and the Wardrobe) and Philip Pullman (His Dark Materials), as well as assorted criticism. Each week, one class meeting will focus on Milton, and the other on one of the modern novels.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-john-milton-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-john-milton-spring-2008/21l-705s08.JPG', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2604, 'Major Authors: Melville and Morrison', 'This seminar provides intensive study of texts by two American authors (Herman Melville, 1819-1891, and Toni Morrison, 1931-) who, using lyrical, radically innovative prose, explore in different ways epic notions of American identity.', 'This seminar provides intensive study of texts by two American authors (Herman Melville, 1819-1891, and Toni Morrison, 1931-) who, using lyrical, radically innovative prose, explore in different ways epic notions of American identity. Focusing on Melville''s Typee (1846), Moby-Dick (1851), and The Confidence-Man (1857) and Morrison''s Sula (1973), Beloved (1987), Jazz (1992), and Paradise (1998), the class will address their common concerns with issues of gender, race, language, and nationhood. Be prepared to read deeply (i.e. a small number of texts with considerable care), to draw on a variety of sources in different media, and to employ them in creative research, writing, and multimedia projects.', 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-melville-and-morrison-fall-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-705-major-authors-melville-and-morrison-fall-2003/21l-705f03.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2605, 'Medieval Literature: Medieval Women Writers', 'This survey provides a general introduction to medieval European literature (from Late Antiquity to the Fifteenth Century) from the perspective of women writers from a variety of cultures, social backgrounds, and historical timeperiods.', 'This survey provides a general introduction to medieval European literature (from Late Antiquity to the Fifteenth Century) from the perspective of women writers from a variety of cultures, social backgrounds, and historical timeperiods.&#160;Though much of the class will be devoted to exploring the evolution of a new literary tradition by and for women from its earliest emergence in the West, wider historical and cultural movements will also be addressed: the Fall of the Roman Empire, the growth of religious communities, the shift from orality to literacy, the culture of chivalry and courtly love, the emergence of scholasticism and universities, changes in devotional practices, the persecution of heretics, the rise of nationalism and class consciousness.&#160;Authors will include some of the most famous women of the period: Hildegard of Bingen, Heloise of Paris, Marie de France, Christine de Pizan, Joan of Arc, Margery Kempe, along with many interesting and intriguing though lesser known figures.', 'http://ocw.mit.edu/courses/literature/21l-460-medieval-literature-medieval-women-writers-spring-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-460-medieval-literature-medieval-women-writers-spring-2004/21l-460s04.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2606, 'American Authors: American Women Authors', 'This subject, cross-listed in Literature and Women''s Studies, examines a range of American women authors from the seventeenth century to the present.', 'This subject, cross-listed in Literature and Women''s Studies, examines a range of American women authors from the seventeenth century to the present.&nbsp;It aims to introduce a number of literary genres and styles- the captivity narrative, slave novel, sensational, sentimental, realistic, and postmodern fiction- and also to address significant historical events in American women''s history: Puritanism, the American Revolution, industrialization and urbanization in the nineteenth century, the Harlem Renaissance, World War II, the 60s civil rights movements.&nbsp;A primary focus will be themes studied and understood through the lens of gender: war, violence, and sexual exploitation (Keller, Rowlandson, Rowson); the relationship between women and religion (Rowlandson, Rowson, Stowe); labor, poverty, and working conditions for women (Fern, Davis, Wharton); captivity and slavery (Rowlandson, Jacobs); class struggle (Fern, Davis, Wharton, Larsen); race and identity (Keller, Jacobs, Larsen, Morrison); feminist revisions of history (Stowe, Morrison, Keller); and the myth of the fallen woman (take your pick).&nbsp;Essays and in-class reports will focus more particularly on specific writers and themes and will stress the skills of close reading, annotation, research, and uses of multimedia where appropriate.', 'http://ocw.mit.edu/courses/literature/21l-512-american-authors-american-women-authors-spring-2003', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/literature/21l-512-american-authors-american-women-authors-spring-2003/21l-512s03.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2607, 'Writing About Race: Narratives of Multiraciality', 'In this course we will read essays, novels, memoirs, and graphic texts, and view documentary and experimental films and videos which explore race from the standpoint of the multiracial.', 'In this course we will read essays, novels, memoirs, and graphic texts, and view documentary and experimental films and videos which explore race from the standpoint of the multiracial. Examining the varied work of multiracial authors and filmmakers such as Danzy Senna, Ruth Ozeki, Kip Fulbeck, James McBride and others, we will focus not on how multiracial people are seen or imagined by the dominant culture, but instead on how they represent themselves. How do these authors approach issues of family, community, nation, language and history? What can their work tell us about the complex interconnections between race, gender, class, sexuality, and citizenship? Is there a relationship between their experiences of multiraciality and a willingness to experiment with form and genre? In addressing these and other questions, we will endeavor to think and write more critically and creatively about race as a social category and a lived experience.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-narratives-of-multiraciality-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-narratives-of-multiraciality-fall-2008/21w-742jf08.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2608, 'Writing About Race', 'In The Souls of Black Folk (1903), the great cultural critic W.', 'In The Souls of Black Folk (1903), the great cultural critic W. E. B. Du Bois wrote that ''...the problem of the Twentieth Century is the problem of the color line.'' A century after Du Bois penned those words, most Americans would agree that at the beginning of the twenty-first century, the color line remains one of our most pressing social issues. In this course, we will explore the terrain of race in America by reading the works of writers of color and others concerned with the issue of race, by viewing films that address racial issues, and by writing to explore how the fictions and facts of race condition all our lives, social and civic, private and public. We will consider the complex question of racial identity, test the givens of history by uncovering histories that have been more elusive or more thoroughly suppressed, and explore how writing and reading can both reflect and challenge racial categories, hierarchies, and perceptions. We will read the work of such writers as Suzan-Lori Parks, Toni Morrison, Sandra Cisneros, James Baldwin, Louise Erdrich, Amy Tan, Chang-Rae Lee, Jhumpa Lahiri, and William Faulkner, among others, as we consider the story of race in its peculiarly American dimensions. We will also view films such as Skins, The Long Walk Home, and Crash. Both the reading and the writing of members of the class will be the focus of class discussion and workshops. Students explore race and ethnicity in personal essays, pieces of cultural criticism or analysis, or (with permission of instructor) fiction.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-742j-writing-about-race-spring-2007/21w-742js07.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2609, 'Advanced Essay Workshop', 'This course is a workshop for students with some experience in writing essays, nonfiction prose.', 'This course is a workshop for students with some experience in writing essays, nonfiction prose. Our focus will be negotiating and representing identities grounded in gender, race, class, nationality, sexuality, and other categories of identity, either our own or others'', in prose that is expository, exploratory, investigative, persuasive, lyrical, or incantatory. We will read nonfiction prose works by a wide array of writers who have used language to negotiate and represent aspects of identity and the ways the different determinants of identity intersect, compete, and cooperate.', 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-745-advanced-essay-workshop-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/writing-and-humanistic-studies/21w-745-advanced-essay-workshop-spring-2008/21w-745s08.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2610, 'Theater and Cultural Diversity in the U.S.', 'This course explores contemporary American theatrical expression as it may be organized around issues of gender and cultural identity.', 'This course explores contemporary American theatrical expression as it may be organized around issues of gender and cultural identity. This exploration will include the analysis of performances, scripts, and video documentation, as well as the invention of original documents of theatrical expression. Class lectures and discussions will analyze samples of Native American, Chicano, African American, and Asian American theater, taking into consideration the historical and political context for the creation of these works. Performance exercises will help students identify theatrical forms and techniques used by these theaters, and how these techniques contribute to the overall goals of specific theatrical expressions.', 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-621-theater-and-cultural-diversity-in-the-u-s-spring-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/music-and-theater-arts/21m-621-theater-and-cultural-diversity-in-the-u-s-spring-2008/21m-621s08.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2611, 'Feminist Political Thought', 'In this course we will examine the development of feminist theory over time.', 'In this course we will examine the development of feminist theory over time. Some subjects we will examine in detail include suffrage and equality; radical feminism; psychoanalysis and feminism; theories of power; sexuality and gender; embodied knowledge; pornography; identities and global feminism; militarism; and the welfare state. Throughout the course we will analyze different ways of looking at power and political culture in modern societies, issues of race and class, poverty and welfare, sexuality and morality.', 'http://ocw.mit.edu/courses/special-programs/sp-601j-feminist-political-thought-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-601j-feminist-political-thought-spring-2010/sp-601js10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2612, 'Race and Gender in Asian America', 'In this seminar we will examine various issues related to the intersection of race and gender in Asian America, starting with the nineteenth century, but focusing on contemporary issues.', 'In this seminar we will examine various issues related to the intersection of race and gender in Asian America, starting with the nineteenth century, but focusing on contemporary issues. Topics to be covered may include racial and gender discourse, the stereotyping of Asian American women and men in the media, Asian American masculinity, Asian American feminisms and their relation to mainstream American feminism, the debate between feminism and ethnic nationalism, gay and lesbian identity, class and labor issues, domestic violence, interracial dating and marriage, and multiracial identity.', 'http://ocw.mit.edu/courses/history/21h-153j-race-and-gender-in-asian-america-spring-2006', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-153j-race-and-gender-in-asian-america-spring-2006/21h-153js06.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2613, 'Cultural Performances of Asia', 'This course examines cultural performances of Asia, including both traditional and contemporary forms, in a variety of genres.', 'This course examines cultural performances of Asia, including both traditional and contemporary forms, in a variety of genres. Students will explore the communicative power of performances with attention to the ways performers, media, cultural settings, and audiences interact. The representation of cultural difference is considered and how it is altered through processes of globalization. Performances are viewed live when possible, but the course also relies on video, audio, and online materials as necessary. There are no prerequisites for this course and it is taught in English.', 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-067j-cultural-performances-of-asia-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/foreign-languages-and-literatures/21f-067j-cultural-performances-of-asia-fall-2005/21f-067jf05.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2614, 'The Economic History of Work and Family', 'This course will explore the relation of women and men in both pre-industrial and modern societies to the changing map of public and private (household) work spaces, examining how that map affected their opportunities for both productive activity and the consumption of goods and leisure.', 'This course will explore the relation of women and men in both pre-industrial and modern societies to the changing map of public and private (household) work spaces, examining how that map affected their opportunities for both productive activity and the consumption of goods and leisure. The reproductive strategies of women, either in conjunction with or in opposition to their families, will be the third major theme of the course. We will consider how a place and an ideal of the &quot;domestic&quot; arose in the early modern west, to what extent it was effective in limiting the economic position of women, and how it has been challenged, and with what success, in the post-industrial period. Finally, we will consider some of the policy implications for contemporary societies as they respond to changes in the composition of the paid work force, as well as to radical changes in their national demographic profiles. Although most of the material for the course will focus on western Europe since the Middle Ages and on the United States, we will also consider how these issues have played themselves out in non-western cultures.', 'http://ocw.mit.edu/courses/history/21h-927j-the-economic-history-of-work-and-family-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/history/21h-927j-the-economic-history-of-work-and-family-spring-2005/21h-927js05.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2615, 'Medicine, Religion and Politics in Africa and the African Diaspora', 'This course provides an exploration of colonial and postcolonial clashes between theories of healing and embodiment in the African world and those of western bio-medicine.', 'This course provides an exploration of colonial and postcolonial clashes between theories of healing and embodiment in the African world and those of western bio-medicine. It examines how Afro-Atlantic religious traditions have challenged western conceptions of illness, healing, and the body and have also offered alternative notions of morality, rationality, kinship, gender, and sexuality. It also analyzes whether contemporary western bio-medical interventions reinforce colonial or imperial power in the effort to promote global health in Africa and the African diaspora.', 'http://ocw.mit.edu/courses/anthropology/21a-460j-medicine-religion-and-politics-in-africa-and-the-african-diaspora-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-460j-medicine-religion-and-politics-in-africa-and-the-african-diaspora-spring-2005/21a-460js05.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2616, 'Dilemmas in Bio-Medical Ethics: Playing God or Doing Good?', 'This course is an introduction to the cross-cultural study of bio-medical ethics.', 'This course is an introduction to the cross-cultural study of bio-medical ethics. It examines moral foundations of the science and practice of western bio-medicine through case studies of abortion, contraception, cloning, organ transplantation, and other issues. It also evaluates challenges that new medical technologies pose to the practice and availability of medical services around the globe, and to cross-cultural ideas of kinship and personhood. It discusses critiques of the bio-medical tradition from anthropological, feminist, legal, religious, and cross-cultural theorists.', 'http://ocw.mit.edu/courses/anthropology/21a-216j-dilemmas-in-bio-medical-ethics-playing-god-or-doing-good-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/anthropology/21a-216j-dilemmas-in-bio-medical-ethics-playing-god-or-doing-good-spring-2005/21a-216js05.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2617, 'Recreate Experiments from History: Inform the Future from the Past: Galileo', '2010 marks the 400th anniversary of Galileo''s astonishing sightings of features on the moon, stars, and moons around Jupiter that no one had seen before.', '2010 marks the 400th anniversary of Galileo''s astonishing sightings of features on the moon, stars, and moons around Jupiter that no one had seen before. Recreate these new ways of seeing and exploring from the materials and techniques Galileo had on hand, while you reflect on the times and works of Galileo. What was it like to improvise new ways of seeing and exploring from the materials and techniques on hand? What do we notice? What surprises us? How can we relate to past experience and ideas? What are we curious to research? How does our experimenting grow into our learning? Let your own curiosity drive your explorations.', 'http://ocw.mit.edu/courses/special-programs/sp-713-recreate-experiments-from-history-inform-the-future-from-the-past-galileo-january-iap-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-713-recreate-experiments-from-history-inform-the-future-from-the-past-galileo-january-iap-2010/sp-713iap10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2618, 'NextLab I: Designing Mobile Technologies for the Next Billion Users', 'Can you make a cellphone change the world? NextLab is a hands-on year-long design course in which students research, develop and deploy mobile technologies for the next billion mobile users in developing countries.', 'Can you make a cellphone change the world? NextLab is a hands-on year-long design course in which students research, develop and deploy mobile technologies for the next billion mobile users in developing countries. Guided by real-world needs as observed by local partners, students work in multidisciplinary teams on term-long projects, closely collaborating with NGOs and communities at the local level, field practitioners, and experts in relevant fields. Students are expected to leverage technical ingenuity in both mobile and internet technologies together with social insight in order to address social challenges in areas such as health, microfinance, entrepreneurship, education, and civic activism. Students with technically and socially viable prototypes may obtain funding for travel to their target communities, in order to obtain the first-hand feedback necessary to prepare their technologies for full fledged deployment into the real world (subject to guidelines and limitations).', 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-965-nextlab-i-designing-mobile-technologies-for-the-next-billion-users-fall-2008', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/media-arts-and-sciences/mas-965-nextlab-i-designing-mobile-technologies-for-the-next-billion-users-fall-2008/mas-965f08.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2619, 'D-Lab II: Design', 'D-Lab: Design addresses problems faced by undeserved communities with a focus on design, experimentation, and prototyping processes.', 'D-Lab: Design addresses problems faced by undeserved communities with a focus on design, experimentation, and prototyping processes. Particular attention is placed on constraints faced when designing for developing countries. Multidisciplinary teams work on semester-long projects in collaboration with community partners, field practitioners, and experts in relevant fields. Topics covered include design for affordability, design for manufacture, sustainability, and strategies for working effectively with community partners and customers. Students may continue projects begun in SP.721/11.025J/11.472 D-Lab Development.', 'http://ocw.mit.edu/courses/special-programs/sp-722j-d-lab-ii-design-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-722j-d-lab-ii-design-spring-2010/sp-722js10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2620, 'D-Lab: Disseminating Innovations for the Common Good', 'in the trilogy of D-Lab courses, D-Lab III focuses on disseminating innovations among underserved communities, especially in developing countries.', 'in the trilogy of D-Lab courses, D-Lab III focuses on disseminating innovations among underserved communities, especially in developing countries.&nbsp;Students acquire skills related to building partnerships and piloting, financing, implementing, and scaling-up a selected innovation for the common good. The course is structured around MIT and outside competitions. Teams develop an idea, project or (social) business plan that is ''ready to roll'' by term''s end. Course includes an on-line forum discussion board, student-led case studies and a final proposal or business plan for realizing your dream innovation.', 'http://ocw.mit.edu/courses/special-programs/sp-723-d-lab-disseminating-innovations-for-the-common-good-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-723-d-lab-disseminating-innovations-for-the-common-good-spring-2007/sp-723s07.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2621, 'Prototypes to Products', 'For students and teams who have started a sustainable-development project in D-Lab (SP.', 'For students and teams who have started a sustainable-development project in D-Lab (SP.776), Product Engineering Processes (2.009), or elsewhere, this class provides a setting to continue developing projects for field implementation. Topics covered include prototyping techniques, materials selection, design-for-manufacturing, field-testing, and project management. All classwork will directly relate to the students'' projects, and the instructor will consult on the projects during weekly lab time. There are no exams. Teams are encouraged to enroll together.', 'http://ocw.mit.edu/courses/special-programs/sp-724-prototypes-to-products-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-724-prototypes-to-products-fall-2005/sp-724f05.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2622, 'Special Topics at Edgerton Center:Developing World Prosthetics', 'D-Lab World Prosthetics is a collaboration between the Massachusetts Institute of Technology and the Jaipur Foot Organization to improve the design, manufacture, and distribution of rehabilitation devices in the developing world.', 'D-Lab World Prosthetics is a collaboration between the Massachusetts Institute of Technology and the Jaipur Foot Organization to improve the design, manufacture, and distribution of rehabilitation devices in the developing world. The course welcomes individuals interested in physical rehabilitation to work on multidisciplinary teams of students with bioengineering, mechanical engineering, material science, and medical or pre-medical backgrounds. Students will learn about the basics of human walking, different types of gait disabilities, as well as the technologies that seek to address those disabilities. Patient perspectives and current research areas are presented. Lecture topics focus on lower-limb disabilities, including polio and above-knee and below-knee amputation, and will cover both developed and developing world techniques for overcoming these disabilities. Students form teams to design and prototype low-cost orthotic and prosthetic devices, and present their work at the end of the course.', 'http://ocw.mit.edu/courses/special-programs/sp-733-special-topics-at-edgerton-center-developing-world-prosthetics-spring-2010', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-733-special-topics-at-edgerton-center-developing-world-prosthetics-spring-2010/sp-733s10.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2623, 'Practical Electronics', 'You can build a wide range of practical electronic devices if you understand a few basic electronics concepts and follow some simple rules.', 'You can build a wide range of practical electronic devices if you understand a few basic electronics concepts and follow some simple rules. These devices include light-activated and sound-activated toys and appliances, remote controls, timers and clocks, and motorized devices. The subject begins with an overview of the fundamental concepts, followed by a series of laboratory exercises that demonstrate the basic rules, and a final project.', 'http://ocw.mit.edu/courses/special-programs/sp-764-practical-electronics-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-764-practical-electronics-fall-2004/sp-764f04.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2624, 'Photovoltaic Solar Energy Systems', 'This class will study the behavior of photovoltaic solar energy systems, focusing on the behavior of ''stand-alone'' systems.', 'This class will study the behavior of photovoltaic solar energy systems, focusing on the behavior of ''stand-alone'' systems. The design of stand-alone photovoltaic systems will be covered. This will include estimation of costs and benefits, taking into account any available government subsidies. Introduction to the hardware elements and their behavior will be included.', 'http://ocw.mit.edu/courses/special-programs/sp-769-photovoltaic-solar-energy-systems-fall-2004', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-769-photovoltaic-solar-energy-systems-fall-2004/sp-769f04.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2625, 'D-Lab: Energy', 'D-Lab: Energy offers a hands-on, project-based approach that engages students in understanding and addressing the applications of small-scale, sustainable energy technology in developing countries where compact, robust, low-cost systems for generating power are required.', 'D-Lab: Energy offers a hands-on, project-based approach that engages students in understanding and addressing the applications of small-scale, sustainable energy technology in developing countries where compact, robust, low-cost systems for generating power are required. Projects may include micro-hydro, solar, or wind turbine generators along with theoretical analysis, design, prototype construction, evaluation and implementation. Students will have the opportunity both to travel to Nicaragua during spring break to identify and implement projects. D-Lab: Energy is part of MIT''s D-Lab program, which fosters the development of appropriate technologies and sustainable solutions within the framework of international development.This course is an elective subject in MIT&rsquo;s undergraduate Energy Studies Minor. This Institute-wide program complements the deep expertise obtained in any major with a broad understanding of the interlinked realms of science, technology, and social sciences as they relate to energy and associated environmental challenges.', 'http://ocw.mit.edu/courses/special-programs/sp-775-d-lab-energy-spring-2011', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-775-d-lab-energy-spring-2011/sp-775s11.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2626, 'Design for Demining', 'Humanitarian Demining is the process of detecting, removing and disposing of landmines.', 'Humanitarian Demining is the process of detecting, removing and disposing of landmines. Millions of landmines are buried in more than 80 countries resulting in more than 10,000 civilian victims every year. MIT Design for Demining is a design course that spans the entire product design and development process from identification of needs and idea generation to prototyping and blast testing to manufacture and deployment. Technical, business and customer aspects are addressed. Students learn about demining while they design, develop and deliver devices to aid the demining community. Past students have invented or improved hand tools, protective gear, safety equipment, educational graphics and teaching materials. Some tools designed in previous years are in use worldwide in the thousands. Course work is informed by a class field trip to a U.S. Army base for demining training and guest expert speakers.', 'http://ocw.mit.edu/courses/special-programs/sp-776-design-for-demining-spring-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-776-design-for-demining-spring-2007/sp-776s07.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2627, 'Water Jet Technologies', 'In this Public Service Design Seminar (PSDS), we will design and build products with developmentally disabled students at the Protestant Guild Learning Center in Waltham, MA.', 'In this Public Service Design Seminar (PSDS), we will design and build products with developmentally disabled students at the Protestant Guild Learning Center in Waltham, MA. The class will work closely with community clients to make sure that what is developed is helpful and functional. These products will be built using the Hobby Shop equipment, the water jet machine in particular. Over the course of the seminar, this class will teach students how to use the OMAX&reg; Jet Machining Center commonly called the water jet and associated OMAX&reg;&nbsp;software. The product development process will also be detailed in depth: determining customer needs, concept development, prototyping, design, and manufacturing.', 'http://ocw.mit.edu/courses/special-programs/sp-777-water-jet-technologies-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-777-water-jet-technologies-spring-2005/sp-777s05.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2628, 'Gender and Race, Work, and Public Policy', 'This course provides an analytic framework for understanding the roles that gender and race play in defining the work worlds of women and men in our society, including ways in which gender intersects with race and class.', 'This course provides an analytic framework for understanding the roles that gender and race play in defining the work worlds of women and men in our society, including ways in which gender intersects with race and class. The course examines specific workplace-related policies through a gender/race lens, including welfare policy, comparable worth, affirmative action, parental leave policy, child care policy and working time policies. Students are required to investigate ways in which these policies address gender and racial inequities, and think critically about mechanisms for change.', 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-237-gender-and-race-work-and-public-policy-spring-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/urban-studies-and-planning/11-237-gender-and-race-work-and-public-policy-spring-2005/11-237s05.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2629, 'Studies in Women''s Life Narratives: Feminist Inquiry', 'Feminist Inquiry starts with questions: What is feminism? What is feminist scholarship? Is feminist scholarship inherently interdisciplinary? Must feminist work interrogate disciplinarity? Must feminists collaborate? Our aim is to promote the development of feminist theory and methods by providing a forum for sharing, assessing, discussing and debating strategies used by feminist scholars to study topics such as gender and the body; sexualities; color and whiteness; migration, colonialism, and indigeneity.', 'Feminist Inquiry starts with questions: What is feminism? What is feminist scholarship? Is feminist scholarship inherently interdisciplinary? Must feminist work interrogate disciplinarity? Must feminists collaborate? Our aim is to promote the development of feminist theory and methods by providing a forum for sharing, assessing, discussing and debating strategies used by feminist scholars to study topics such as gender and the body; sexualities; color and whiteness; migration, colonialism, and indigeneity.', 'http://ocw.mit.edu/courses/special-programs/sp-691-studies-in-womens-life-narratives-feminist-inquiry-spring-2009', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-691-studies-in-womens-life-narratives-feminist-inquiry-spring-2009/sp-691s09.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2630, 'Studies in Women''s Life Narratives: Interrogating Marriage: Case Studies in American Law and Culture', 'Is marriage a patriarchal institution? Much feminist scholarship has characterized it that way, but now in the context of the recent Massachusetts Supreme Court decision legalizing gay marriage, the meaning of marriage itself demands serious re-examination.', 'Is marriage a patriarchal institution? Much feminist scholarship has characterized it that way, but now in the context of the recent Massachusetts Supreme Court decision legalizing gay marriage, the meaning of marriage itself demands serious re-examination. This course will discuss history, literature, film, and legal scholarship, making use of cross-cultural, sociological, anthropological, and many other theoretical approaches to the marriage question from 1630 to the present. As it turns out, sex, marriage, and the family have never been stable institutions; to the contrary, they have continued to function as flash points for the very social and cultural questions that are central to gender studies scholarship.', 'http://ocw.mit.edu/courses/special-programs/sp-691-studies-in-womens-life-narratives-interrogating-marriage-case-studies-in-american-law-and-culture-fall-2007', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-691-studies-in-womens-life-narratives-interrogating-marriage-case-studies-in-american-law-and-culture-fall-2007/sp-691f07.jpg', 'Special Programs', 'MIT');
INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`) VALUES(2631, 'Issues of Representation: Women, Representation, and Music in Selected Folk Traditions of the British Isles and North America', 'This subject investigates the special relation of women to several musical folk traditions in the British Isles and North America.', 'This subject investigates the special relation of women to several musical folk traditions in the British Isles and North America. Throughout, we will be examining the implications of gender in the creation, transmission, and performance of music. Because virtually all societies operate to some extent on a gendered division of labor (and of expressive roles) the music of these societies is marked by the gendering of musical repertoires, traditions of instrumentation, performance settings, and styles. This seminar will examine the gendered dimensions of the music - the song texts, the performance styles, processes of dissemination (collection, literary representation) and issues of historiography - with respect to selected traditions within the folk musics of North America and the British Isles, with the aim of analyzing the special contributions of women to these traditions. In addition to telling stories about women''s musical lives, and studying elements of female identity and subjectivity in song texts and music, we will investigate the ways in which women''s work and women''s cultural roles have affected the folk traditions of these several countries.', 'http://ocw.mit.edu/courses/special-programs/sp-694-issues-of-representation-women-representation-and-music-in-selected-folk-traditions-of-the-british-isles-and-north-america-fall-2005', 'N/A', '2013-04-22', 0, 'http://ocw.mit.edu/courses/special-programs/sp-694-issues-of-representation-women-representation-and-music-in-selected-folk-traditions-of-the-british-isles-and-north-america-fall-2005/sp-694f05.jpg', 'Special Programs', 'MIT');
